/*****************************************************************************
*
* FUNCTION      : Read_Tiff_Image
*
* ARGUMENTS     : IMAGE *Image; char *name;
*
* MODIFIED ARGS : Image
*
* RETURN VALUE  : none
*
* AUTHOR        : Alexander Enzmann
*
* DESCRIPTION
*
*   Reads a TIFF image into an RGB image buffer
*
* CHANGES
*
* New - 6/2000
*
******************************************************************************/

void Read_Tiff_Image(IMAGE *Image, char *name)
{
	unsigned int width, height;
	char *filename = Locate_Filename(name, POV_File_Image_TIFF, 1);

    long LineSize;
    uint16 BitsPerSample, PhotometricInterpretation;
	uint16 SamplePerPixel, Orientation;
	uint32 RowsPerStrip;
    int row, nrow, i, j, l;
    TIFF* tif;
	int result = 0;

	if (filename == NULL)
		Error("Cannot read TIFF image.");

	// Rather than have libTIFF complain about tags it doesn't understand,
	// we just suppress all the warnings.
	TIFFSetWarningHandler(SuppressTIFFWarnings);
	TIFFSetErrorHandler(SuppressTIFFWarnings);

	// Open and do initial processing
	tif = TIFFClientOpen(filename, "r", Tiff_Open(filename),
	                     Tiff_Read, Tiff_Write, Tiff_Seek, Tiff_Close,
	                     Tiff_Size, Tiff_Map, Tiff_Unmap);
	if (!tif)
		return;

	// Get basic information about the image
	int ExtraSamples, ExtraSampleInfo;
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);  
	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);
	TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &RowsPerStrip);   
	TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &PhotometricInterpretation);
	TIFFGetField(tif, TIFFTAG_ORIENTATION, &Orientation);
    TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLESPERPIXEL, &SamplePerPixel);
    TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &ExtraSamples, &ExtraSampleInfo);

	Image->iwidth = width;
	Image->iheight = height;
	Image->width = (DBL)width;
	Image->height = (DBL)height;

    LineSize = TIFFScanlineSize(tif);
	assert(SamplePerPixel == (int)(LineSize / width));
    // SamplePerPixel = (int)(LineSize / width);

#if 0
	// For now we are ignoring the orientation of the image...
	switch (Orientation)
	{
		case ORIENTATION_TOPLEFT:
			break;
		case ORIENTATION_TOPRIGHT:
			break;
		case ORIENTATION_BOTRIGHT:
			break;
		case ORIENTATION_BOTLEFT:
			break;
		case ORIENTATION_LEFTTOP:
			break;
		case ORIENTATION_RIGHTTOP:
			break;
		case ORIENTATION_RIGHTBOT:
			break;
		case ORIENTATION_LEFTBOT:
			break;
		default:
			break;
	}
#endif

	//PhotometricInterpretation = 2 image is RGB
	//PhotometricInterpretation = 3 image have a color palette              
	if (PhotometricInterpretation == PHOTOMETRIC_PALETTE)
	{
		uint16 *red, *green, *blue;
		int16 i;
		int Palette16Bits;
		IMAGE_COLOUR *cmap;

		//load the palette
		Image->data.rgb8_lines = NULL;
		int cmap_len = (1 << BitsPerSample);
		Image->Colour_Map_Size = cmap_len;

		cmap = (IMAGE_COLOUR *)POV_MALLOC(cmap_len*sizeof(IMAGE_COLOUR), "TIFF image color map");
		Image->Colour_Map = cmap;

		TIFFGetField(tif, TIFFTAG_COLORMAP, &red, &green, &blue); 

		// Is the palette 16 or 8 bits ?
		if (checkcmap(cmap_len, red, green, blue) == 16) 
			Palette16Bits = true;
		else
			Palette16Bits = false;

		// Read the palette
		for (i=0,j=0;i<cmap_len;i++)
		{
			if (Palette16Bits)
			{
				cmap[i].Red   = CVT(red[i]);
				cmap[i].Green = CVT(green[i]);
				cmap[i].Blue  = CVT(blue[i]);
			}
			else
			{
				cmap[i].Red   = red[i];
				cmap[i].Green = green[i];
				cmap[i].Blue  = blue[i];
			}
			// I may be mistaken, but it appears that alpha/opacity information doesn't
			// appear in a Paletted Tiff image.  Well - if it does, it's not as easy to
			// get at as RGB.
			cmap[i].Filter   = 0;
			cmap[i].Transmit = 0;
		}

		Image->data.map_lines = (unsigned char **)POV_MALLOC(height * sizeof(unsigned char *), "TIFF image");
		unsigned char *buf = (unsigned char *)POV_MALLOC(sizeof(unsigned char) * TIFFStripSize(tif), "TIFF row");

		//read the tiff lines and save them in the image
		//with RGB mode, we have to change the order of the 3 samples RGB <=> BGR
		for (row=0;row<height;row+=RowsPerStrip)
		{     
			nrow = (row + (int)RowsPerStrip > height ? height - row : RowsPerStrip);
			TIFFReadEncodedStrip(tif, TIFFComputeStrip(tif, row, 0), buf, nrow * LineSize);
			for (l=0;l<nrow;l++)
			{
				Image->data.map_lines[row+l] = (unsigned char *)POV_MALLOC(width, "TIFF Image line");
				POV_MEMCPY(Image->data.map_lines[row+l], &buf[l*LineSize], (int)width); 
			}
		}

		POV_FREE(buf);
	}
	else
	{
		// Allocate the row buffers for the image
		Image->Colour_Map_Size = 0;
		Image->Colour_Map = NULL;
		Image->data.rgb8_lines = (IMAGE8_LINE *)POV_MALLOC(height * sizeof(IMAGE8_LINE), "TIFF image");
		uint32 *buf = (uint32 *)POV_MALLOC(sizeof(uint32) * width * height, "TIIF image data");

		TIFFReadRGBAImage(tif, width, height, buf, 0);
		uint32 abgr, *tbuf = buf;
		for (i=height-1;i>=0;i--)
		{
			Image->data.rgb8_lines[i].blue   = (unsigned char *)POV_MALLOC(width, "TIFF Image line");
			Image->data.rgb8_lines[i].green  = (unsigned char *)POV_MALLOC(width, "TIFF Image line");
			Image->data.rgb8_lines[i].red    = (unsigned char *)POV_MALLOC(width, "TIFF Image line");
			Image->data.rgb8_lines[i].transm = (unsigned char *)POV_MALLOC(width, "TIFF Image line");
			for (j=0,l=0;j<width;j++)
			{
				abgr = *tbuf++;
				Image->data.rgb8_lines[i].blue[j]   = (unsigned char)TIFFGetB(abgr);
				Image->data.rgb8_lines[i].green[j]  = (unsigned char)TIFFGetG(abgr);
				Image->data.rgb8_lines[i].red[j]    = (unsigned char)TIFFGetR(abgr);
				Image->data.rgb8_lines[i].transm[j] = 255 - (unsigned char)TIFFGetA(abgr);
			}
		}
        POV_FREE(buf);
	}

	TIFFClose(tif);
}

bool wxTIFFHandler::LoadFile( wxImage *image, wxInputStream& stream, bool verbose, int index )
{
    if (index == -1)
        index = 0;

    image->Destroy();

    TIFF *tif = TIFFwxOpen( stream, "image", "r" );

    if (!tif)
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Error loading image.") );
        }

        return false;
    }

    if (!TIFFSetDirectory( tif, (tdir_t)index ))
    {
        if (verbose)
        {
            wxLogError( _("Invalid TIFF image index.") );
        }

        TIFFClose( tif );

        return false;
    }

    uint32 w, h;
    uint32 *raster;

    TIFFGetField( tif, TIFFTAG_IMAGEWIDTH, &w );
    TIFFGetField( tif, TIFFTAG_IMAGELENGTH, &h );

    uint16 samplesPerPixel = 0;
    (void) TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);

    uint16 bitsPerSample = 0;
    (void) TIFFGetFieldDefaulted(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);

    uint16 extraSamples;
    uint16* samplesInfo;
    TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES,
                          &extraSamples, &samplesInfo);

    uint16 photometric;
    if (!TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric))
    {
        photometric = PHOTOMETRIC_MINISWHITE;
    }
    const bool hasAlpha = (extraSamples >= 1
        && ((samplesInfo[0] == EXTRASAMPLE_UNSPECIFIED)
            || samplesInfo[0] == EXTRASAMPLE_ASSOCALPHA
            || samplesInfo[0] == EXTRASAMPLE_UNASSALPHA))
        || (extraSamples == 0 && samplesPerPixel == 4
            && photometric == PHOTOMETRIC_RGB);

    // guard against integer overflow during multiplication which could result
    // in allocating a too small buffer and then overflowing it
    const double bytesNeeded = (double)w * (double)h * sizeof(uint32);
    if ( bytesNeeded >= wxUINT32_MAX )
    {
        if ( verbose )
        {
            wxLogError( _("TIFF: Image size is abnormally big.") );
        }

        TIFFClose(tif);

        return false;
    }

    raster = (uint32*) _TIFFmalloc( (uint32)bytesNeeded );

    if (!raster)
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Couldn't allocate memory.") );
        }

        TIFFClose( tif );

        return false;
    }

    image->Create( (int)w, (int)h );
    if (!image->IsOk())
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Couldn't allocate memory.") );
        }

        _TIFFfree( raster );
        TIFFClose( tif );

        return false;
    }

    if ( hasAlpha )
        image->SetAlpha();

    uint16 planarConfig = PLANARCONFIG_CONTIG;
    (void) TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarConfig);

    bool ok = true;
    char msg[1024] = "";
    if
    (
        (planarConfig == PLANARCONFIG_CONTIG && samplesPerPixel == 2
            && extraSamples == 1)
        &&
        (
            ( !TIFFRGBAImageOK(tif, msg) )
            || (bitsPerSample == 8)
        )
    )
    {
        const bool isGreyScale = (bitsPerSample == 8);
        unsigned char *buf = (unsigned char *)_TIFFmalloc(TIFFScanlineSize(tif));
        uint32 pos = 0;
        const bool minIsWhite = (photometric == PHOTOMETRIC_MINISWHITE);
        const int minValue =  minIsWhite ? 255 : 0;
        const int maxValue = 255 - minValue;

        /*
        Decode to ABGR format as that is what the code, that converts to
        wxImage, later on expects (normally TIFFReadRGBAImageOriented is
        used to decode which uses an ABGR layout).
        */
        for (uint32 y = 0; y < h; ++y)
        {
            if (TIFFReadScanline(tif, buf, y, 0) != 1)
            {
                ok = false;
                break;
            }

            if (isGreyScale)
            {
                for (uint32 x = 0; x < w; ++x)
                {
                    uint8 val = minIsWhite ? 255 - buf[x*2] : buf[x*2];
                    uint8 alpha = minIsWhite ? 255 - buf[x*2+1] : buf[x*2+1];
                    raster[pos] = val + (val << 8) + (val << 16)
                        + (alpha << 24);
                    pos++;
                }
            }
            else
            {
                for (uint32 x = 0; x < w; ++x)
                {
                    int mask = buf[x*2/8] << ((x*2)%8);

                    uint8 val = mask & 128 ? maxValue : minValue;
                    raster[pos] = val + (val << 8) + (val << 16)
                        + ((mask & 64 ? maxValue : minValue) << 24);
                    pos++;
                }
            }
        }

        _TIFFfree(buf);
    }
    else
    {
        ok = TIFFReadRGBAImageOriented( tif, w, h, raster,
            ORIENTATION_TOPLEFT, 0 ) != 0;
    }


    if (!ok)
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Error reading image.") );
        }

        _TIFFfree( raster );
        image->Destroy();
        TIFFClose( tif );

        return false;
    }

    unsigned char *ptr = image->GetData();

    unsigned char *alpha = image->GetAlpha();

    uint32 pos = 0;

    for (uint32 i = 0; i < h; i++)
    {
        for (uint32 j = 0; j < w; j++)
        {
            *(ptr++) = (unsigned char)TIFFGetR(raster[pos]);
            *(ptr++) = (unsigned char)TIFFGetG(raster[pos]);
            *(ptr++) = (unsigned char)TIFFGetB(raster[pos]);
            if ( hasAlpha )
                *(alpha++) = (unsigned char)TIFFGetA(raster[pos]);

            pos++;
        }
    }


    image->SetOption(wxIMAGE_OPTION_TIFF_PHOTOMETRIC, photometric);

    uint16 compression;
    /*
    Copy some baseline TIFF tags which helps when re-saving a TIFF
    to be similar to the original image.
    */
    if (samplesPerPixel)
    {
        image->SetOption(wxIMAGE_OPTION_TIFF_SAMPLESPERPIXEL, samplesPerPixel);
    }

    if (bitsPerSample)
    {
        image->SetOption(wxIMAGE_OPTION_TIFF_BITSPERSAMPLE, bitsPerSample);
    }

    if ( TIFFGetFieldDefaulted(tif, TIFFTAG_COMPRESSION, &compression) )
    {
        image->SetOption(wxIMAGE_OPTION_TIFF_COMPRESSION, compression);
    }

    // Set the resolution unit.
    wxImageResolution resUnit = wxIMAGE_RESOLUTION_NONE;
    uint16 tiffRes;
    if ( TIFFGetFieldDefaulted(tif, TIFFTAG_RESOLUTIONUNIT, &tiffRes) )
    {
        switch (tiffRes)
        {
            default:
                wxLogWarning(_("Unknown TIFF resolution unit %d ignored"),
                    tiffRes);
                wxFALLTHROUGH;

            case RESUNIT_NONE:
                resUnit = wxIMAGE_RESOLUTION_NONE;
                break;

            case RESUNIT_INCH:
                resUnit = wxIMAGE_RESOLUTION_INCHES;
                break;

            case RESUNIT_CENTIMETER:
                resUnit = wxIMAGE_RESOLUTION_CM;
                break;
        }
    }

    image->SetOption(wxIMAGE_OPTION_RESOLUTIONUNIT, resUnit);

    /*
    Set the image resolution if it's available. Resolution tag is not
    dependent on RESOLUTIONUNIT != RESUNIT_NONE (according to TIFF spec).
    */
    float resX, resY;

    if ( TIFFGetField(tif, TIFFTAG_XRESOLUTION, &resX) )
    {
        /*
        Use a string value to not lose precision.
        rounding to int as cm and then converting to inch may
        result in whole integer rounding error, eg. 201 instead of 200 dpi.
        If an app wants an int, GetOptionInt will convert and round down.
        */
        image->SetOption(wxIMAGE_OPTION_RESOLUTIONX,
            wxString::FromCDouble((double) resX));
    }

    if ( TIFFGetField(tif, TIFFTAG_YRESOLUTION, &resY) )
    {
        image->SetOption(wxIMAGE_OPTION_RESOLUTIONY,
            wxString::FromCDouble((double) resY));
    }

    _TIFFfree( raster );

    TIFFClose( tif );

    return true;
}

int ImageIO::getTIFFSampleFormat (const Glib::ustring &fname, IIOSampleFormat &sFormat, IIOSampleArrangement &sArrangement)
{
#ifdef _WIN32
    wchar_t *wfilename = (wchar_t*)g_utf8_to_utf16 (fname.c_str(), -1, NULL, NULL, NULL);
    TIFF* in = TIFFOpenW (wfilename, "r");
    g_free (wfilename);
#else
    TIFF* in = TIFFOpen(fname.c_str(), "r");
#endif

    if (in == nullptr) {
        return IMIO_CANNOTREADFILE;
    }

    std::uint16_t bitspersample = 0, samplesperpixel = 0, sampleformat = 0;
    int hasTag = TIFFGetField(in, TIFFTAG_BITSPERSAMPLE, &bitspersample);
    hasTag &= TIFFGetField(in, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);

    if (!hasTag) {
        // These are needed
        TIFFClose(in);
        sFormat = IIOSF_UNKNOWN;
        return IMIO_VARIANTNOTSUPPORTED;
    }

    if (!TIFFGetField(in, TIFFTAG_SAMPLEFORMAT, &sampleformat)) {
        /*
         * WARNING: This is a dirty hack!
         * We assume that files which doesn't contain the TIFFTAG_SAMPLEFORMAT tag
         * (which is the case with uncompressed TIFFs produced by RT!) are RGB files,
         * but that may be not true.   --- Hombre
         */
        sampleformat = SAMPLEFORMAT_UINT;
    } else if (sampleformat == SAMPLEFORMAT_VOID) {
        // according to https://www.awaresystems.be/imaging/tiff/tifftags/sampleformat.html
        // we assume SAMPLEFORMAT_UINT if SAMPLEFORMAT_VOID is set
        sampleformat = SAMPLEFORMAT_UINT;
    }

    std::uint16_t config;
    TIFFGetField(in, TIFFTAG_PLANARCONFIG, &config);

    if (config == PLANARCONFIG_CONTIG) {
        sArrangement = IIOSA_CHUNKY;
    } else {
        sFormat = IIOSF_UNKNOWN;
        sArrangement = IIOSA_UNKNOWN;
        TIFFClose(in);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    std::uint16_t photometric;

    if (!TIFFGetField(in, TIFFTAG_PHOTOMETRIC, &photometric)) {
        TIFFClose(in);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    std::uint16_t compression;

    if (photometric == PHOTOMETRIC_LOGLUV)
        if (!TIFFGetField(in, TIFFTAG_COMPRESSION, &compression)) {
            compression = COMPRESSION_NONE;
        }

    TIFFClose(in);

    if (photometric == PHOTOMETRIC_RGB || photometric == PHOTOMETRIC_MINISBLACK) {
        if ((samplesperpixel == 1 || samplesperpixel == 3 || samplesperpixel == 4) && sampleformat == SAMPLEFORMAT_UINT) {
            if (bitspersample == 8) {
                sFormat = IIOSF_UNSIGNED_CHAR;
                return IMIO_SUCCESS;
            }

            if (bitspersample == 16) {
                sFormat = IIOSF_UNSIGNED_SHORT;
                return IMIO_SUCCESS;
            }
        } else if ((samplesperpixel == 3 || samplesperpixel == 4) && sampleformat == SAMPLEFORMAT_IEEEFP) {
            if (bitspersample==16) {
                sFormat = IIOSF_FLOAT16;
                return IMIO_SUCCESS;
            }
            if (bitspersample == 24) {
                sFormat = IIOSF_FLOAT24;
                return IMIO_SUCCESS;
            }
            if (bitspersample == 32) {
                sFormat = IIOSF_FLOAT32;
                return IMIO_SUCCESS;
            }
        }
    } else if ((samplesperpixel == 3 || samplesperpixel == 4) && photometric == PHOTOMETRIC_LOGLUV) {
        if (compression == COMPRESSION_SGILOG24) {
            sFormat = IIOSF_LOGLUV24;
            return IMIO_SUCCESS;
        } else if (compression == COMPRESSION_SGILOG) {
            sFormat = IIOSF_LOGLUV32;
            return IMIO_SUCCESS;
        }
    }

    return IMIO_VARIANTNOTSUPPORTED;
}

int ImageIO::loadTIFF (const Glib::ustring &fname)
{

    static MyMutex thumbMutex;
    MyMutex::MyLock lock(thumbMutex);

    if(!options.serializeTiffRead) {
        lock.release();
    }

#ifdef _WIN32
    wchar_t *wfilename = (wchar_t*)g_utf8_to_utf16 (fname.c_str(), -1, NULL, NULL, NULL);
    TIFF* in = TIFFOpenW (wfilename, "r");
    g_free (wfilename);
#else
    TIFF* in = TIFFOpen(fname.c_str(), "r");
#endif

    if (in == nullptr) {
        return IMIO_CANNOTREADFILE;
    }

    if (pl) {
        pl->setProgressStr ("PROGRESSBAR_LOADTIFF");
        pl->setProgress (0.0);
    }

    int width, height;
    TIFFGetField(in, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(in, TIFFTAG_IMAGELENGTH, &height);

    std::uint16_t bitspersample, samplesperpixel;
    int hasTag = TIFFGetField(in, TIFFTAG_BITSPERSAMPLE, &bitspersample);
    hasTag &= TIFFGetField(in, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);

    if (!hasTag) {
        // These are needed
        TIFFClose(in);
        fprintf(stderr, "Error 1 loading %s\n", fname.c_str());
        fflush(stderr);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    std::uint16_t config;
    TIFFGetField(in, TIFFTAG_PLANARCONFIG, &config);

    if (config != PLANARCONFIG_CONTIG) {
        TIFFClose(in);
        fprintf(stderr, "Error 2 loading %s\n", fname.c_str());
        fflush(stderr);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    if (sampleFormat & (IIOSF_LOGLUV24 | IIOSF_LOGLUV32)) {
        TIFFSetField(in, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT);
    }

    /*
     * We could use the min/max values set in TIFFTAG_SMINSAMPLEVALUE and
     * TIFFTAG_SMAXSAMPLEVALUE, but for now, we normalize the image to the
     * effective minimum and maximum values
     */
    if (settings->verbose) {
        printf("Information of \"%s\":\n", fname.c_str());
        std::uint16_t tiffDefaultScale, tiffBaselineExposure, tiffLinearResponseLimit;
        if (TIFFGetField(in, TIFFTAG_DEFAULTSCALE, &tiffDefaultScale)) {
            printf("   DefaultScale: %d\n", tiffDefaultScale);
        }
        else
            printf("   No DefaultScale value!\n");
        if (TIFFGetField(in, TIFFTAG_BASELINEEXPOSURE, &tiffBaselineExposure)) {
            printf("   BaselineExposure: %d\n", tiffBaselineExposure);
        }
        else
            printf("   No BaselineExposure value!\n");
        if (TIFFGetField(in, TIFFTAG_LINEARRESPONSELIMIT, &tiffLinearResponseLimit)) {
            printf("   LinearResponseLimit: %d\n", tiffLinearResponseLimit);
        }
        else
            printf("   No LinearResponseLimit value!\n");

        std::uint16_t tiffMinValue, tiffMaxValue;
        if (TIFFGetField(in, TIFFTAG_SMINSAMPLEVALUE, &tiffMinValue)) {
            printf("   MinValue: %d\n", tiffMinValue);
        }
        else
            printf("   No minimum value!\n");
        if (TIFFGetField(in, TIFFTAG_SMAXSAMPLEVALUE, &tiffMaxValue)) {
            printf("   MaxValue: %d\n\n", tiffMaxValue);
        }
        else
            printf("   No maximum value!\n\n");
        printf("   Those values are not taken into account, the image data are normalized to a [0;1] range\n\n");
    }

    char* profdata;
    deleteLoadedProfileData();

    if (TIFFGetField(in, TIFFTAG_ICCPROFILE, &loadedProfileLength, &profdata)) {
        embProfile = cmsOpenProfileFromMem (profdata, loadedProfileLength);
        loadedProfileData = new char [loadedProfileLength];
        memcpy (loadedProfileData, profdata, loadedProfileLength);
    } else {
        embProfile = nullptr;
    }

    allocate (width, height);

    std::unique_ptr<unsigned char[]> linebuffer(new unsigned char[TIFFScanlineSize(in) * (samplesperpixel == 1 ? 3 : 1)]);

    for (int row = 0; row < height; row++) {
        if (TIFFReadScanline(in, linebuffer.get(), row, 0) < 0) {
            TIFFClose(in);
            fprintf(stderr, "Error 3 loading %s\n", fname.c_str());
            fflush(stderr);
            return IMIO_READERROR;
        }

        if (samplesperpixel > 3) {
            for (int i = 0; i < width; i++) {
                memmove(linebuffer.get() + i * 3 * bitspersample / 8, linebuffer.get() + i * samplesperpixel * bitspersample / 8, 3 * bitspersample / 8);
            }
        }
        else if (samplesperpixel == 1) {
            const size_t bytes = bitspersample / 8;
            for (int i = width - 1; i >= 0; --i) {
                const unsigned char* const src = linebuffer.get() + i * bytes;
                unsigned char* const dest = linebuffer.get() + i * 3 * bytes;
                memcpy(dest + 2 * bytes, src, bytes);
                memcpy(dest + 1 * bytes, src, bytes);
                memcpy(dest + 0 * bytes, src, bytes);
            }
        }

        setScanline (row, linebuffer.get(), bitspersample);

        if (pl && !(row % 100)) {
            pl->setProgress ((double)(row + 1) / height);
        }
    }

    TIFFClose(in);

    if (pl) {
        pl->setProgressStr ("PROGRESSBAR_READY");
        pl->setProgress (1.0);
    }

    return IMIO_SUCCESS;
}

static int
readTiffNumScenes(TIFF* tiff, const std::string& filepath)
{
  int numscenes = 1;

  char* imagedescription = nullptr;
  // metadata is in ImageDescription of first IFD in the file.
  if (TIFFGetField(tiff, TIFFTAG_IMAGEDESCRIPTION, &imagedescription) != 1) {
    imagedescription = nullptr;
    LOG_WARNING << "Failed to read imagedescription of TIFF: '" << filepath << "';  interpreting as single channel.";
  }

  std::string simagedescription = trim(imagedescription ? imagedescription : "");

  // check for plain tiff with ImageJ imagedescription:
  if (startsWith(simagedescription, "ImageJ=")) {
    numscenes = 1;
  } else if (startsWith(simagedescription, "{\"shape\":")) {
    numscenes = 1;
  } else if ((startsWith(simagedescription, "<?xml version") || startsWith(simagedescription, "<OME xmlns")) &&
             endsWith(simagedescription, "OME>")) {
    // convert c to xml doc.  if this fails then we don't have an ome tif.
    pugi_agave::xml_document omexml;
    pugi_agave::xml_parse_result parseOk = omexml.load_string(simagedescription.c_str());
    if (!parseOk) {
      LOG_ERROR << "Bad OME xml metadata content";
      return false;
    }

    pugi_agave::xml_node omenode = omexml.child("OME");

    // count how many <Image> tags and that is our number of scenes.
    numscenes = 0;
    pugi_agave::xml_node imageEl;
    pugi_agave::xml_node pixelsEl;
    for (pugi_agave::xml_node imagenode : omenode.children("Image")) {
      numscenes++;
    }
  } else {
    numscenes = 1;
  }

  return numscenes;
}

bool
readTiffDimensions(TIFF* tiff, const std::string filepath, VolumeDimensions& dims, uint32_t scene)
{
  char* imagedescription = nullptr;
  // metadata is in ImageDescription of first IFD in the file.
  if (TIFFGetField(tiff, TIFFTAG_IMAGEDESCRIPTION, &imagedescription) != 1) {
    imagedescription = nullptr;
    LOG_WARNING << "Failed to read imagedescription of TIFF: '" << filepath << "';  interpreting as single channel.";
  }

  // Temporary variables
  uint32_t width, height;
  //  tsize_t scanlength;

  // Read dimensions of image
  if (TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width) != 1) {
    LOG_ERROR << "Failed to read width of TIFF: '" << filepath << "'";
    return false;
  }
  if (TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height) != 1) {
    LOG_ERROR << "Failed to read height of TIFF: '" << filepath << "'";
    return false;
  }

  uint32_t bpp = 0;
  if (TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bpp) != 1) {
    LOG_ERROR << "Failed to read bpp of TIFF: '" << filepath << "'";
    return false;
  }

  uint16_t sampleFormat = SAMPLEFORMAT_UINT;
  if (TIFFGetField(tiff, TIFFTAG_SAMPLEFORMAT, &sampleFormat) != 1) {
    LOG_WARNING << "Failed to read sampleformat of TIFF: '" << filepath << "'";
  }
  if (sampleFormat != SAMPLEFORMAT_UINT && sampleFormat != SAMPLEFORMAT_IEEEFP && sampleFormat != SAMPLEFORMAT_INT) {
    LOG_ERROR << "Unsupported tiff SAMPLEFORMAT " << sampleFormat << " for '" << filepath << "'";
    return false;
  }

  uint32_t sizeT = 1;
  uint32_t sizeX = width;
  uint32_t sizeY = height;
  uint32_t sizeZ = 1;
  uint32_t sizeC = 1;
  float physicalSizeX = 1.0f;
  float physicalSizeY = 1.0f;
  float physicalSizeZ = 1.0f;
  std::string units = "units";
  // see if we can glean xy resolution from the tiff tags
  float tiffXResolution = 1.0f;
  if (TIFFGetField(tiff, TIFFTAG_XRESOLUTION, &tiffXResolution) == 1) {
    physicalSizeX = 1.0f / tiffXResolution;
  }
  float tiffYResolution = 1.0f;
  if (TIFFGetField(tiff, TIFFTAG_YRESOLUTION, &tiffYResolution) == 1) {
    physicalSizeY = 1.0f / tiffYResolution;
  }

  std::vector<std::string> channelNames;
  std::string dimensionOrder = "XYCZT";

  std::string simagedescription = trim(imagedescription ? imagedescription : "");

  // check for plain tiff with ImageJ imagedescription:
  if (startsWith(simagedescription, "ImageJ=")) {
    // "ImageJ=\nhyperstack=true\nimages=7900\nchannels=1\nslices=50\nframes=158"
    // "ImageJ=1.52i\nimages=126\nchannels=2\nslices=63\nhyperstack=true\nmode=composite\nunit=
    //      micron\nfinterval=299.2315368652344\nspacing=0.2245383462882669\nloop=false\nmin=9768.0\nmax=
    //        14591.0\n"
    std::map<std::string, std::string> imagejmetadata = splitToNameValuePairs(simagedescription);
    if (imagejmetadata.empty()) {
      LOG_ERROR << "Unexpected bad or empty TIFF ImageJ metadata";
      return false;
    }

    auto iter = imagejmetadata.find("channels");
    if (iter != imagejmetadata.end()) {
      sizeC = std::stoi((*iter).second);
    } else {
      LOG_WARNING << "Failed to read number of channels of ImageJ TIFF: '" << filepath << "'";
    }

    iter = imagejmetadata.find("slices");
    if (iter != imagejmetadata.end()) {
      sizeZ = std::stoi((*iter).second);
    } else {
      LOG_WARNING << "Failed to read number of slices of ImageJ TIFF: '" << filepath << "'";
    }

    iter = imagejmetadata.find("frames");
    if (iter != imagejmetadata.end()) {
      sizeT = std::stoi((*iter).second);
    } else {
      LOG_WARNING << "Failed to read number of frames of ImageJ TIFF: '" << filepath << "'";
    }

    iter = imagejmetadata.find("spacing");
    if (iter != imagejmetadata.end()) {
      try {
        physicalSizeZ = std::stof((*iter).second);
        if (physicalSizeZ < 0.0f) {
          physicalSizeZ = -physicalSizeZ;
        }
      } catch (...) {
        LOG_WARNING << "Failed to read spacing of ImageJ TIFF: '" << filepath << "'";
        physicalSizeZ = 1.0f;
      }
    }

    iter = imagejmetadata.find("unit");
    if (iter != imagejmetadata.end()) {
      units = (*iter).second;
    } else {
      LOG_WARNING << "Failed to read spatial units of ImageJ TIFF: '" << filepath << "'";
    }

    for (uint32_t i = 0; i < sizeC; ++i) {
      channelNames.push_back(std::to_string(i));
    }
  } else if (startsWith(simagedescription, "SCIFIO=")) {
    // SCIFIO=0.41.0
    // axes=X,Y,Unknown
    // lengths=725,694,906
    // scales=1.0,1.0,1.0
    // units=null,null,null
    // bitsPerPixel=8
    // images=906
    // channels=1
    // slices=1
    // frames=1
    // hyperstack=true
    // mode=composite
    // unit=null

    std::map<std::string, std::string> scifiometadata = splitToNameValuePairs(simagedescription);
    if (scifiometadata.empty()) {
      LOG_ERROR << "Unexpected bad or empty TIFF SCIFIO metadata";
      return false;
    }

    std::vector<std::string> axes;
    auto iter = scifiometadata.find("axes");
    if (iter != scifiometadata.end()) {
      split((*iter).second, ',', axes);
    } else {
      LOG_WARNING << "Failed to read axes of SCIFIO TIFF: '" << filepath << "'";
    }
    std::vector<std::string> scales;
    iter = scifiometadata.find("scales");
    if (iter != scifiometadata.end()) {
      split((*iter).second, ',', scales);
    } else {
      LOG_WARNING << "Failed to read scales of SCIFIO TIFF: '" << filepath << "'";
    }
    std::vector<std::string> lengths;
    iter = scifiometadata.find("lengths");
    if (iter != scifiometadata.end()) {
      split((*iter).second, ',', lengths);
    } else {
      LOG_WARNING << "Failed to read lengths of SCIFIO TIFF: '" << filepath << "'";
    }
    std::vector<std::string> unitsArray;
    iter = scifiometadata.find("units");
    if (iter != scifiometadata.end()) {
      split((*iter).second, ',', unitsArray);
      // just take the first one for now?
      units = unitsArray[0];
      if (units == "null") {
        units = "units";
      }
    } else {
      LOG_WARNING << "Failed to read units of SCIFIO TIFF: '" << filepath << "'";
    }

    if (lengths.size() != unitsArray.size() || scales.size() != axes.size() || scales.size() != unitsArray.size()) {
      LOG_ERROR << "SCIFIO TIFF metadata has inconsistent counts of lengths,units,axes,scales";
    }

    for (size_t i = 0; i < axes.size(); ++i) {
      std::string axis = axes[i];
      double scale = std::stod(scales[i]);
      std::string unit = unitsArray[i];
      int length = std::stoi(lengths[i]);
      if (axis == "X") {
        // check consistency.
        if (sizeX != length) {
          LOG_WARNING << "Inconsistent X size " << length << " in SCIFIO TIFF: '" << filepath << "'";
        }
        physicalSizeX = scale;
      } else if (axis == "Y") {
        if (sizeY != length) {
          LOG_WARNING << "Inconsistent Y size " << length << " in SCIFIO TIFF: '" << filepath << "'";
        }
        physicalSizeY = scale;
      } else if (axis == "Z") {
        sizeZ = length;
        physicalSizeZ = scale;
      } else if (axis == "Channel") {
        sizeC = length;
      } else if (axis == "Time") {
        sizeT = length;
      } else if (axis == "Unknown" && axes.size() == 3) {
        // make a guess and use the last axis as Z
        sizeZ = length;
        physicalSizeZ = scale;
      }
    }
    iter = scifiometadata.find("channels");
    if (iter != scifiometadata.end()) {
      int nch = std::stoi((*iter).second);
      if (sizeC != nch) {
        LOG_WARNING << "Inconsistent number of channels " << nch << " in SCIFIO TIFF: '" << filepath << "'";
      }
    } else {
      LOG_WARNING << "Failed to read number of channels of SCIFIO TIFF: '" << filepath << "'";
    }

    for (uint32_t i = 0; i < sizeC; ++i) {
      channelNames.push_back(std::to_string(i));
    }

  } else if (startsWith(simagedescription, "{\"shape\":")) {
    // expect a 4d shape array of C,Z,Y,X or 5d T,C,Z,Y,X
    size_t firstBracket = simagedescription.find('[');
    size_t lastBracket = simagedescription.rfind(']');
    std::string shape = simagedescription.substr(firstBracket + 1, lastBracket - firstBracket - 1);
    LOG_INFO << shape;
    std::vector<std::string> shapelist;
    split(shape, ',', shapelist);
    if ((shapelist.size() != 4) && (shapelist.size() != 5)) {
      LOG_ERROR << "Expected shape to be 4D or 5D TIFF: '" << filepath << "'";
      return false;
    }
    dimensionOrder = "XYZCT";
    bool hasT = (shapelist.size() == 5);
    int shapeIndex = 0;
    if (hasT) {
      sizeT = std::stoi(shapelist[shapeIndex++]);
    }
    sizeC = std::stoi(shapelist[shapeIndex++]);
    sizeZ = std::stoi(shapelist[shapeIndex++]);
    sizeY = std::stoi(shapelist[shapeIndex++]);
    sizeX = std::stoi(shapelist[shapeIndex++]);
    for (uint32_t i = 0; i < sizeC; ++i) {
      channelNames.push_back(std::to_string(i));
    }

  } else if ((startsWith(simagedescription, "<?xml version") || startsWith(simagedescription, "<OME xmlns")) &&
             endsWith(simagedescription, "OME>")) {
    // convert c to xml doc.  if this fails then we don't have an ome tif.
    pugi_agave::xml_document omexml;
    pugi_agave::xml_parse_result parseOk = omexml.load_string(simagedescription.c_str());
    if (!parseOk) {
      LOG_ERROR << "Bad OME xml metadata content";
      return false;
    }

    pugi_agave::xml_node omenode = omexml.child("OME");

    auto binaryonlynode = omenode.child("BinaryOnly");
    if (binaryonlynode) {
      // try to get metadata from the MetadataFile attribute
      std::string metadatafile = binaryonlynode.attribute("MetadataFile").as_string("");
      if (metadatafile.empty()) {
        LOG_ERROR << "BinaryOnly OME TIFFs is missing a MetadataFile attribute: '" << filepath << "'";
        return false;
      }
      // try to load the metadata file.
      // try prefixing the metadatafile with the directory of the tiff.
      std::filesystem::path tiffpath(filepath);
      std::filesystem::path tiffdir = tiffpath.parent_path();
      std::string metadatapath = (tiffdir / metadatafile).string();

      pugi_agave::xml_document metadataxml;
      pugi_agave::xml_parse_result parseOk = metadataxml.load_file(metadatapath.c_str());
      if (!parseOk) {
        LOG_ERROR << "Failed to load metadata file: '" << metadatafile << "' for OME TIFF: '" << filepath << "'";
        return false;
      }
      omenode = metadataxml.child("OME");
      // then continue with the metadata in the metadata file.
    }

    // extract some necessary info from the xml:

    // count how many <Image> tags and that is our number of scenes.
    uint32_t numScenes = 0;
    pugi_agave::xml_node imageEl;
    pugi_agave::xml_node pixelsEl;
    auto imagenodes = omenode.children("Image");
    numScenes = std::distance(imagenodes.begin(), imagenodes.end());
    if (scene >= numScenes) {
      LOG_ERROR << "Requested invalid scene index " << scene << " in OME TIFF; returning scene 0";
      scene = 0;
    }
    auto imageElIterator = imagenodes.begin();
    for (uint32_t i = 0; i < scene; ++i) {
      ++imageElIterator;
    }
    imageEl = *imageElIterator;
    pixelsEl = imageEl.child("Pixels");
    // for (pugi_agave::xml_node imagenode : imagenodes) {
    //   // get the Image and Pixels element of scene
    //   if (numScenes == scene) {
    //     imageEl = imagenode;
    //     pixelsEl = imagenode.child("Pixels");
    //   }
    // }

    if (!imageEl || !pixelsEl) {
      LOG_ERROR << "No <Pixels> element in ome xml for scene " << scene;
      return false;
    }

    // skipping "complex", "double-complex", and "bit".
    std::map<std::string, uint32_t> mapPixelTypeBPP = { { "uint8", 8 },  { "uint16", 16 }, { "uint32", 32 },
                                                        { "int8", 8 },   { "int16", 16 },  { "int32", 32 },
                                                        { "float", 32 }, { "double", 64 } };

    std::string pixelType = pixelsEl.attribute("Type").as_string("uint16");
    std::transform(
      pixelType.begin(), pixelType.end(), pixelType.begin(), [](unsigned char c) { return std::tolower(c); });
    LOG_INFO << "pixel type: " << pixelType;
    bpp = mapPixelTypeBPP[pixelType];
    if (bpp != 32 && bpp != 16 && bpp != 8) {
      LOG_ERROR << "Image must be 8 or 16-bit integer, or 32-bit float typed";
      return false;
    }
    sizeX = requireUint32Attr(pixelsEl, "SizeX", 0);
    sizeY = requireUint32Attr(pixelsEl, "SizeY", 0);
    sizeZ = requireUint32Attr(pixelsEl, "SizeZ", 0);
    sizeC = requireUint32Attr(pixelsEl, "SizeC", 0);
    sizeT = requireUint32Attr(pixelsEl, "SizeT", 0);
    // one of : "XYZCT", "XYZTC","XYCTZ","XYCZT","XYTCZ","XYTZC"
    dimensionOrder = pixelsEl.attribute("DimensionOrder").as_string(dimensionOrder.c_str());
    physicalSizeX = requireFloatAttr(pixelsEl, "PhysicalSizeX", 1.0f);
    physicalSizeY = requireFloatAttr(pixelsEl, "PhysicalSizeY", 1.0f);
    physicalSizeZ = requireFloatAttr(pixelsEl, "PhysicalSizeZ", 1.0f);
    std::string physicalSizeXunit = pixelsEl.attribute("PhysicalSizeXUnit").as_string("");
    std::string physicalSizeYunit = pixelsEl.attribute("PhysicalSizeYUnit").as_string("");
    std::string physicalSizeZunit = pixelsEl.attribute("PhysicalSizeZUnit").as_string("");
    units = physicalSizeXunit;
    if (physicalSizeYunit != units || physicalSizeZunit != units) {
      LOG_WARNING << "Inconsistent physical size units in OME TIFF: '" << filepath << "' " << physicalSizeXunit << " "
                  << physicalSizeYunit << " " << physicalSizeZunit;
    }
    // this is the documented ome-xml default.
    if (units == "") {
      units = "um";
    }

    // find channel names
    int i = 0;
    for (pugi_agave::xml_node node : pixelsEl.children("Channel")) {
      std::string chid = node.attribute("ID").value();
      std::string chname = node.attribute("Name").value();
      if (!chname.empty()) {
        channelNames.push_back(chname);
      } else if (!chid.empty()) {
        channelNames.push_back(chid);
      } else {
        channelNames.push_back(std::to_string(i));
      }
      i++;
    }
  } else {
    // unrecognized string / no metadata.
    // walk the file and count the directories and assume that is Z
    // sizeZ was initialized to 1.
    sizeZ = 0;
    while (TIFFSetDirectory(tiff, sizeZ)) {
      sizeZ++;
    };
    channelNames.push_back("0");
  }

  assert(sizeX == width);
  assert(sizeY == height);

  // allocate the destination buffer!!!!
  assert(sizeT >= 1);
  assert(sizeC >= 1);
  assert(sizeX >= 1);
  assert(sizeY >= 1);
  assert(sizeZ >= 1);

  dims.sizeX = sizeX;
  dims.sizeY = sizeY;
  dims.sizeZ = sizeZ;
  dims.sizeC = sizeC;
  dims.sizeT = sizeT;
  dims.dimensionOrder = dimensionOrder;
  dims.physicalSizeX = physicalSizeX;
  dims.physicalSizeY = physicalSizeY;
  dims.physicalSizeZ = physicalSizeZ;
  dims.spatialUnits = VolumeDimensions::sanitizeUnitsString(units);
  dims.bitsPerPixel = bpp;
  dims.channelNames = channelNames;
  dims.sampleFormat = sampleFormat;

  dims.log();

  return dims.validate();
}

std::shared_ptr<ImageXYZC>
FileReaderTIFF::loadFromFile(const LoadSpec& loadSpec)
{
  std::string filepath = loadSpec.filepath;
  uint32_t time = loadSpec.time;
  uint32_t scene = loadSpec.scene;
  VolumeDimensions outDims;

  std::shared_ptr<ImageXYZC> emptyimage;

  auto tStart = std::chrono::high_resolution_clock::now();

  // Loads tiff file
  ScopedTiffReader tiffreader(filepath);
  TIFF* tiff = tiffreader.reader();
  if (!tiff) {
    return emptyimage;
  }

  VolumeDimensions dims;
  bool dims_ok = readTiffDimensions(tiff, filepath, dims, scene);
  if (!dims_ok) {
    return emptyimage;
  }

  if (time > (int32_t)(dims.sizeT - 1)) {
    LOG_ERROR << "Time " << time << " exceeds time samples in file: " << dims.sizeT;
    return emptyimage;
  }

  LOG_DEBUG << "Reading " << (TIFFIsTiled(tiff) ? "tiled" : "stripped") << " tiff...";

  uint32_t rowsPerStrip = 0;
  if (TIFFGetField(tiff, TIFFTAG_ROWSPERSTRIP, &rowsPerStrip)) {
    LOG_DEBUG << "ROWSPERSTRIP: " << rowsPerStrip;
    uint32_t StripsPerImage = ((dims.sizeY + rowsPerStrip - 1) / rowsPerStrip);
    LOG_DEBUG << "Strips per image: " << StripsPerImage;
  }
  uint32_t samplesPerPixel = 0;
  if (TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel)) {
    LOG_DEBUG << "SamplesPerPixel: " << samplesPerPixel;
  }
  if (samplesPerPixel != 1) {
    LOG_WARNING << "" << samplesPerPixel
                << " samples per pixel is not supported in tiff. Attempting to ignore and use 1 sample";
    samplesPerPixel = 1;
    // return emptyimage;
  }

  uint32_t planarConfig = 0;
  if (TIFFGetField(tiff, TIFFTAG_PLANARCONFIG, &planarConfig)) {
    LOG_DEBUG << "PlanarConfig: " << (planarConfig == 1 ? "PLANARCONFIG_CONTIG" : "PLANARCONFIG_SEPARATE");
  }

  uint32_t nch = loadSpec.channels.empty() ? dims.sizeC : loadSpec.channels.size();

  size_t planesize_bytes = dims.sizeX * dims.sizeY * (ImageXYZC::IN_MEMORY_BPP / 8);
  size_t channelsize_bytes = planesize_bytes * dims.sizeZ;
  uint8_t* data = new uint8_t[channelsize_bytes * nch];
  memset(data, 0, channelsize_bytes * nch);
  // stash it here in case of early exit, it will be deleted
  std::unique_ptr<uint8_t[]> smartPtr(data);

  uint8_t* destptr = data;

  // still assuming 1 sample per pixel (scalar data) here.
  size_t rawPlanesize = dims.sizeX * dims.sizeY * (dims.bitsPerPixel / 8);
  // allocate temp data for one channel
  uint8_t* channelRawMem = new uint8_t[dims.sizeZ * rawPlanesize];
  memset(channelRawMem, 0, dims.sizeZ * rawPlanesize);

  // stash it here in case of early exit, it will be deleted
  std::unique_ptr<uint8_t[]> smartPtrTemp(channelRawMem);

  // now ready to read channels one by one.
  for (uint32_t channel = 0; channel < nch; ++channel) {
    uint32_t channelToLoad = channel;
    if (!loadSpec.channels.empty()) {
      channelToLoad = loadSpec.channels[channel];
    }

    // read entire channel into its native size
    for (uint32_t slice = 0; slice < dims.sizeZ; ++slice) {
      uint32_t planeIndex = dims.getPlaneIndex(slice, channelToLoad, time);
      destptr = channelRawMem + slice * rawPlanesize;
      if (!readTiffPlane(tiff, planeIndex, dims, destptr)) {
        return emptyimage;
      }
    }

    // convert to our internal format (IN_MEMORY_BPP)
    if (!convertChannelData(data + channel * channelsize_bytes, channelRawMem, dims)) {
      return emptyimage;
    }
  }

  auto tEnd = std::chrono::high_resolution_clock::now();
  std::chrono::duration<double> elapsed = tEnd - tStart;
  LOG_DEBUG << "TIFF loaded in " << (elapsed.count() * 1000.0) << "ms";

  auto tStartImage = std::chrono::high_resolution_clock::now();

  // TODO: convert data to uint16_t pixels if not already.
  // we can release the smartPtr because ImageXYZC will now own the raw data memory
  ImageXYZC* im = new ImageXYZC(dims.sizeX,
                                dims.sizeY,
                                dims.sizeZ,
                                nch,
                                ImageXYZC::IN_MEMORY_BPP, // dims.bitsPerPixel,
                                smartPtr.release(),
                                dims.physicalSizeX,
                                dims.physicalSizeY,
                                dims.physicalSizeZ,
                                dims.spatialUnits);

  std::vector<std::string> channelNames = dims.getChannelNames(loadSpec.channels);
  im->setChannelNames(channelNames);

  tEnd = std::chrono::high_resolution_clock::now();
  elapsed = tEnd - tStartImage;
  LOG_DEBUG << "ImageXYZC prepared in " << (elapsed.count() * 1000.0) << "ms";

  elapsed = tEnd - tStart;
  LOG_DEBUG << "Loaded " << filepath << " in " << (elapsed.count() * 1000.0) << "ms";

  std::shared_ptr<ImageXYZC> sharedImage(im);
  outDims = dims;

  return sharedImage;
}

