							"unzip " + zipfull + " -d " + folder); // TODO just exec
				}
			}

			if (!std::filesystem::exists(tifname) && !std::filesystem::exists(zipfull)) {
				zipfile = "gebco_2023_geotiff.zip";
				zipfull = folder + "/" + zipfile;
				tifname = folder + "/" + "gebco_2023_" + name + ".tif";
				if (multi_http_to_file(zipfile,
							{"https://www.bodc.ac.uk/data/open_download/gebco/gebco_2023_tid/geotiff/"},
							zipfull)) {
					exec_to_string(
							"unzip " + zipfull + " -d " + folder); // TODO just exec
				}
			}
		} else {
			std::cerr
					<< "Want " << tifname << " from "
					<< "https://www.bodc.ac.uk/data/open_download/gebco/gebco_2023_sub_ice_topo/geotiff/"
					<< " or "
					<< "https://www.bodc.ac.uk/data/open_download/gebco/gebco_2023_tid/geotiff/"
					<< " in " << porting::path_cache + DIR_DELIM + "earth" << "\n";
		}

		//DUMP(tifname, std::filesystem::exists(tifname));

		if (!std::filesystem::exists(tifname)) {
			tifname = folder + "/" + "gebco_2023_" + name + ".tif";
		}
		//DUMP("loadtifname", tifname, std::filesystem::exists(tifname));
		if (std::filesystem::exists(tifname)) {
			DUMP("open tif", tifname, std::filesystem::file_size(tifname));
			if (const auto tif = TIFFOpen(tifname.c_str(), "r"); tif) {
				uint32_t w = 0;
				uint32_t h = 0;
				TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
				TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
				if (!w || !h) {
					return false;
				}

				const size_t npixels = (w + 1) * (h + 1);
				//DUMP("tiff size", w, h, npixels);
				heights.resize(npixels);

				//w = TIFFScanlineSize(tif) >> 1;
				const tdata_t buf = _TIFFmalloc(TIFFScanlineSize(tif));
				for (uint32_t row = 0; row < h; ++row) {
					TIFFReadScanline(tif, buf, row, 0);

#if HGT_DEBUG
					if (!(row % 10000))
						DUMP(row, TIFFNumberOfStrips(tif), TIFFStripSize(tif),
								((uint8_t *)buf)[0], ((uint8_t *)buf)[2],
								((uint8_t *)buf)[3], ((uint8_t *)buf)[4]);
#endif

					int16_t height;
					for (uint32_t i = 0; i < w; ++i) {
						height = (((uint8_t *)buf)[i << 1]) |
								 (((uint8_t *)buf)[(i << 1) + 1] << 8);
						if (height == -32768 || height == 31727) {
							height = 0;
						}
						const auto dest = i + row * (w + 1);
#if HGT_DEBUG
						if (!(i % 10000) && !(row % 10000))
							DUMP("fill", i, w, h, row, dest, height, //height2,
									((uint8_t *)buf)[(i << 1)],
									((uint8_t *)buf)[(i << 1) + 1],
									((uint8_t *)buf)[(i << 1) + 2],
									((uint8_t *)buf)[(i << 1) + 3]);
#endif

						heights[dest] = height;
					}
					const auto dest = w + row * (w + 1);
					heights[dest] = height; // hack for  interpolation x+1 get
											//DUMP("xhck", w, dest, height);
				}

				// hack for interpolation y+1 get
				for (uint32_t i = 0; i <= w; ++i) {
					const auto src = i + (h - 1) * (w + 1);
					const auto dest = i + h * (w + 1);
					//DUMP("yhck", i, src, dest, heights[src]);
					heights[dest] = heights[src];
				}

				_TIFFfree(buf);
				TIFFClose(tif);

				lat_loaded = lat_dec;
				lon_loaded = lon_dec;

				side_length_x = w;
				side_length_y = h;

				seconds_per_px_x =
						tile_deg_x * 3600 / ((float)side_length_x - side_length_x_extra);
				seconds_per_px_y = tile_deg_y * 3600 / ((float)side_length_y);

				pixel_per_deg_x = (ll_t)side_length_x / tile_deg_x;
				pixel_per_deg_y = (ll_t)side_length_y / tile_deg_y;

#if HGT_DEBUG
				DUMP("tif ok", seconds_per_px_x, side_length_x, side_length_y,
						seconds_per_px_x, seconds_per_px_y);
				DUMP("loadok", (long)this, heights.size(), lat_loaded, lon_loaded,
						tifname, seconds_per_px_x, get(lat_dec, lon_dec));
				DUMP("testread", read(0, 0), read(0, side_length_x - 1),
						read(side_length_y - 1, side_length_x - 1),
						read(side_length_y - 1, 0));
#endif

				return true;
			}
		}
	}

#endif

	// DUMP("load not ok", (long long)this, heights.size(), lat_loaded, lon_loaded, seconds_per_px_x, get(lat_dec, lon_dec));
	return false;
}

void tif_in::open(const char* filename)
  {
    tiff_handle = NULL;
    if ((tiff_handle = TIFFOpen(filename, "r")) == NULL)
      OJPH_ERROR(0x03000091, "Unable to open file %s", filename);
    fname = filename;

    ui32 tiff_width = 0;
    ui32 tiff_height = 0; 
    TIFFGetField(tiff_handle, TIFFTAG_IMAGEWIDTH, &tiff_width);
    TIFFGetField(tiff_handle, TIFFTAG_IMAGELENGTH, &tiff_height);

    ui16 tiff_bits_per_sample = 0;
    ui16 tiff_samples_per_pixel = 0;
    TIFFGetField(tiff_handle, TIFFTAG_BITSPERSAMPLE, &tiff_bits_per_sample);
    TIFFGetField(tiff_handle, TIFFTAG_SAMPLESPERPIXEL, &tiff_samples_per_pixel);
    // some TIFs have tiff_samples_per_pixel=0 when it is a single channel 
    // image - set to 1
    tiff_samples_per_pixel = 
      (tiff_samples_per_pixel < 1) ? 1 : tiff_samples_per_pixel;

    ui16 tiff_planar_configuration = 0;
    ui16 tiff_photometric = 0;
    TIFFGetField(tiff_handle, TIFFTAG_PLANARCONFIG, &tiff_planar_configuration);
    TIFFGetField(tiff_handle, TIFFTAG_PHOTOMETRIC, &tiff_photometric);

    planar_configuration = tiff_planar_configuration;

    ui16 tiff_compression = 0;
    ui32 tiff_rows_per_strip = 0;
    TIFFGetField(tiff_handle, TIFFTAG_COMPRESSION, &tiff_compression);
    TIFFGetField(tiff_handle, TIFFTAG_ROWSPERSTRIP, &tiff_rows_per_strip);

    if (tiff_planar_configuration == PLANARCONFIG_SEPARATE)
    {
      bytes_per_line = tiff_samples_per_pixel * TIFFScanlineSize64(tiff_handle);
    }
    else
    {
      bytes_per_line = TIFFScanlineSize64(tiff_handle);
    }
    // allocate linebuffer to hold a line of image data
    line_buffer = malloc(bytes_per_line);
    if (NULL == line_buffer)
      OJPH_ERROR(0x03000092, "Unable to allocate %d bytes for line_buffer[] "
        "for file %s", bytes_per_line, filename);

    cur_line = 0;

    // Error on known incompatilbe input formats
    if( tiff_bits_per_sample != 8 && tiff_bits_per_sample != 16 )
    {
      OJPH_ERROR(0x03000093, "\nTIFF IO is currently limited"
        " to files with TIFFTAG_BITSPERSAMPLE=8 and TIFFTAG_BITSPERSAMPLE=16 \n"
        "input file = %s has TIFFTAG_BITSPERSAMPLE=%d", 
        filename, tiff_bits_per_sample);
    }

    if( TIFFIsTiled( tiff_handle ) )
    {
      OJPH_ERROR(0x03000094, "\nTIFF IO is currently limited to TIF files "
        "without tiles. \nInput file %s has been detected as tiled", filename);
    }

    if(PHOTOMETRIC_RGB != tiff_photometric && 
       PHOTOMETRIC_MINISBLACK != tiff_photometric )
    {
      OJPH_ERROR(0x03000095, "\nTIFF IO is currently limited to "
        "TIFFTAG_PHOTOMETRIC=PHOTOMETRIC_MINISBLACK=%d and "
        "PHOTOMETRIC_RGB=%d. \nInput file %s has been detected "
        "TIFFTAG_PHOTOMETRIC=%d", 
      PHOTOMETRIC_MINISBLACK, PHOTOMETRIC_RGB, filename, tiff_photometric);
    }

    if( tiff_samples_per_pixel > 4 )
    {
      OJPH_ERROR(0x03000096, "\nTIFF IO is currently limited to "
        "TIFFTAG_SAMPLESPERPIXEL=4 \nInput file %s has been detected with "
        "TIFFTAG_SAMPLESPERPIXEL=%d",
        filename, tiff_samples_per_pixel);
    }

    // set number of components based on tiff_samples_per_pixel
    width = tiff_width;
    height = tiff_height;
    num_comps = tiff_samples_per_pixel;
    bytes_per_sample = (tiff_bits_per_sample + 7) / 8;
    for (ui32 comp_num = 0; comp_num < num_comps; comp_num++)
      bit_depth[comp_num] = tiff_bits_per_sample;

    // allocate intermediate linebuffers to hold a line of a single component 
    // of image data
    if (tiff_planar_configuration == PLANARCONFIG_SEPARATE && 
        bytes_per_sample == 1)
    {
      line_buffer_for_planar_support_uint8 = 
        (uint8_t*)calloc(width, sizeof(uint8_t));
      if (NULL == line_buffer_for_planar_support_uint8)
        OJPH_ERROR(0x03000097, "Unable to allocate %d bytes for "
          "line_buffer_for_planar_support_uint8[] for file %s", 
          width * sizeof(uint8_t), filename);
    }
    if (tiff_planar_configuration == PLANARCONFIG_SEPARATE && 
        bytes_per_sample == 2)
    {
      line_buffer_for_planar_support_uint16 = 
        (uint16_t*)calloc(width, sizeof(uint16_t));
      if (NULL == line_buffer_for_planar_support_uint16)
        OJPH_ERROR(0x03000098, "Unable to allocate %d bytes for "
          "line_buffer_for_planar_support_uint16[] for file %s", 
          width * sizeof(uint16_t), filename);
    }
  }

void LavaVu::readVolumeTIFF(const FilePath& fn)
{
#ifdef HAVE_LIBTIFF
  TIFF* tif = TIFFOpen(fn.full.c_str(), "r");
  if (tif)
  {
    unsigned int width, height;
    size_t npixels;
    int channels = 4;
    GLubyte* imageData;
    int count = 0;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    npixels = width * height;
    imageData = (GLubyte*)_TIFFmalloc(npixels * channels * sizeof(GLubyte));
    if (imageData)
    {
      json volss = session.global("volsubsample");
      int d = TIFFNumberOfDirectories(tif);
      int ds = volss[2];
      if (d > 1) std::cout << "TIFF contains " << d << " pages, sub-sampling z " << ds << std::endl;
      do
      {
        if (TIFFReadRGBAImage(tif, width, height, (uint32_t*)imageData, 0))
        {
          //Subsample
          if (count % ds != 0) {count++; continue;}
          readVolumeSlice(fn.base, imageData, width, height, channels, true);
        }
        count++;
      }
      while (TIFFReadDirectory(tif));
      _TIFFfree(imageData);
    }
    TIFFClose(tif);
  }
#else
  abort_program("Require libTIFF to load TIFF images\n");
#endif
}

void ImageLoader::loadTIFF()
{
  newSource();
#ifdef HAVE_LIBTIFF
  TIFF* tif = TIFFOpen(fn.full.c_str(), "r");
