#endif
}

bool CxImageTIF::Decode(CxFile * hFile)
{
	//Comment this line if you need more information on errors
	// TIFFSetErrorHandler(NULL);	//<Patrick Hoffmann>

	//Open file and fill the TIFF structure
	// m_tif = TIFFOpen(imageFileName,"rb");
	TIFF* m_tif = _TIFFOpenEx(hFile, "rb");

	uint32 height=0;
	uint32 width=0;
	uint16 bitspersample=1;
	uint16 samplesperpixel=1;
	uint32 rowsperstrip=(uint32_t)-1;
	uint16 photometric=0;
	uint16 compression=1;
	uint16 orientation=ORIENTATION_TOPLEFT; //<vho>
	uint16 res_unit; //<Trifon>
	uint32 x, y;
	float resolution, offset;
	BOOL isRGB;
	uint8_t *bits;		//pointer to source data
	uint8_t *bits2;	//pointer to destination data

  cx_try
  {
	//check if it's a tiff file
	if (!m_tif)
		cx_throw("Error encountered while opening TIFF file");

	// <Robert Abram> - 12/2002 : get NumFrames directly, instead of looping
	// info.nNumFrames=0;
	// while(TIFFSetDirectory(m_tif,(uint16)info.nNumFrames)) info.nNumFrames++;
	info.nNumFrames = TIFFNumberOfDirectories(m_tif);

	if (!TIFFSetDirectory(m_tif, (uint16)info.nFrame))
		cx_throw("Error: page not present in TIFF file");			

	//get image info
	TIFFGetField(m_tif, TIFFTAG_IMAGEWIDTH, &width);
	TIFFGetField(m_tif, TIFFTAG_IMAGELENGTH, &height);
	TIFFGetField(m_tif, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);
	TIFFGetField(m_tif, TIFFTAG_BITSPERSAMPLE, &bitspersample);
	TIFFGetField(m_tif, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);   
	TIFFGetField(m_tif, TIFFTAG_PHOTOMETRIC, &photometric);
	TIFFGetField(m_tif, TIFFTAG_ORIENTATION, &orientation);

	if (info.nEscape == -1) {
		// Return output dimensions only
		head.biWidth = width;
		head.biHeight = height;
		info.dwType = CXIMAGE_FORMAT_TIF;
		cx_throw("output dimensions returned");
	}

	TIFFGetFieldDefaulted(m_tif, TIFFTAG_RESOLUTIONUNIT, &res_unit);
	if (TIFFGetField(m_tif, TIFFTAG_XRESOLUTION, &resolution))
	{
		if (res_unit == RESUNIT_CENTIMETER) resolution = (float)(resolution*2.54f + 0.5f);
		SetXDPI((int32_t)resolution);
	}
	if (TIFFGetField(m_tif, TIFFTAG_YRESOLUTION, &resolution))
	{
		if (res_unit == RESUNIT_CENTIMETER) resolution = (float)(resolution*2.54f + 0.5f);
		SetYDPI((int32_t)resolution);
	}

	if (TIFFGetField(m_tif, TIFFTAG_XPOSITION, &offset))	info.xOffset = (int32_t)offset;
	if (TIFFGetField(m_tif, TIFFTAG_YPOSITION, &offset))	info.yOffset = (int32_t)offset;

	head.biClrUsed=0;
	info.nBkgndIndex =-1;

	if (rowsperstrip>height){
		rowsperstrip=height;
		TIFFSetField(m_tif, TIFFTAG_ROWSPERSTRIP, rowsperstrip);
	}

	isRGB = /*(bitspersample >= 8) && (VK: it is possible so for RGB to have < 8 bpp!)*/
		(photometric == PHOTOMETRIC_RGB) ||
		(photometric == PHOTOMETRIC_YCBCR) ||
		(photometric == PHOTOMETRIC_SEPARATED) ||
		(photometric == PHOTOMETRIC_LOGL) ||
		(photometric == PHOTOMETRIC_LOGLUV);

	if (isRGB){
		head.biBitCount=24;
	}else{
		if ((photometric==PHOTOMETRIC_MINISBLACK)||(photometric==PHOTOMETRIC_MINISWHITE)||(photometric==PHOTOMETRIC_PALETTE)){
			if	(bitspersample == 1){
				head.biBitCount=1;		//B&W image
				head.biClrUsed =2;
			} else if (bitspersample == 4) {
				head.biBitCount=4;		//16 colors gray scale
				head.biClrUsed =16;
			} else {
				head.biBitCount=8;		//gray scale
				head.biClrUsed =256;
			}
		} else if (bitspersample == 4) {
			head.biBitCount=4;			// 16 colors
			head.biClrUsed=16;
		} else {
			head.biBitCount=8;			//256 colors
			head.biClrUsed=256;
		}

		if ((bitspersample > 8) && (photometric==PHOTOMETRIC_PALETTE))	// + VK + (BIG palette! => convert to RGB)
		{	head.biBitCount=24;
			head.biClrUsed =0;
		}
	}

	if (info.nEscape) cx_throw("Cancelled"); // <vho> - cancel decoding

	Create(width,height,head.biBitCount,CXIMAGE_FORMAT_TIF);	//image creation
	if (!pDib) cx_throw("CxImageTIF can't create image");

#if CXIMAGE_SUPPORT_ALPHA
	if (samplesperpixel==4) AlphaCreate();	//add alpha support for 32bpp tiffs
	if (samplesperpixel==2 && bitspersample==8) AlphaCreate();	//add alpha support for 8bpp + alpha
#endif //CXIMAGE_SUPPORT_ALPHA

	TIFFGetField(m_tif, TIFFTAG_COMPRESSION, &compression);
	SetCodecOption(compression); // <DPR> save original compression type

	if (isRGB) {
		// Read the whole image into one big RGBA buffer using
		// the traditional TIFFReadRGBAImage() API that we trust.
		uint32* raster;		// retrieve RGBA image
		uint32 *row;

		raster = (uint32*)_TIFFmalloc(width * height * sizeof (uint32));
		if (raster == NULL) cx_throw("No space for raster buffer");

		// Read the image in one chunk into an RGBA array
		if(!TIFFReadRGBAImage(m_tif, width, height, raster, 1)) {
				_TIFFfree(raster);
				cx_throw("Corrupted TIFF file!");
		}

		// read the raster lines and save them in the DIB
		// with RGB mode, we have to change the order of the 3 samples RGB
		row = &raster[0];
		bits2 = info.pImage;
		for (y = 0; y < height; y++) {

			if (info.nEscape){ // <vho> - cancel decoding
				_TIFFfree(raster);
				cx_throw("Cancelled");
			}

			bits = bits2;
			for (x = 0; x < width; x++) {
				*bits++ = (uint8_t)TIFFGetB(row[x]);
				*bits++ = (uint8_t)TIFFGetG(row[x]);
				*bits++ = (uint8_t)TIFFGetR(row[x]);
#if CXIMAGE_SUPPORT_ALPHA
				if (samplesperpixel==4) AlphaSet(x,y,(uint8_t)TIFFGetA(row[x]));
#endif //CXIMAGE_SUPPORT_ALPHA
			}
			row += width;
			bits2 += info.dwEffWidth;
		}
		_TIFFfree(raster);
	} else {
		int32_t BIG_palette = (bitspersample > 8) &&	// + VK
						  (photometric==PHOTOMETRIC_PALETTE);		
		if (BIG_palette && (bitspersample > 24))	// + VK
			cx_throw("Too big palette to handle");		// + VK

		RGBQUAD *pal;
		pal=(RGBQUAD*)calloc(BIG_palette ? 1<<bitspersample : 256,sizeof(RGBQUAD)); 
			// ! VK: it coasts nothing but more correct to use 256 as temp palette storage
			// ! VK: but for case of BIG palette it just copied
		if (pal==NULL) cx_throw("Unable to allocate TIFF palette");

		int32_t bpp = bitspersample <= 8 ? bitspersample : 8; // + VK (to use instead of bitspersample for case of > 8)

		// set up the colormap based on photometric	
		switch(photometric) {
			case PHOTOMETRIC_MINISBLACK:	// bitmap and greyscale image types
			case PHOTOMETRIC_MINISWHITE:
				if (bitspersample == 1) {	// Monochrome image
					if (photometric == PHOTOMETRIC_MINISBLACK) {
						pal[1].rgbRed = pal[1].rgbGreen = pal[1].rgbBlue = 255;
					} else {
						pal[0].rgbRed = pal[0].rgbGreen = pal[0].rgbBlue = 255;
					}
				} else {		// need to build the scale for greyscale images
					if (photometric == PHOTOMETRIC_MINISBLACK) {
						for (int32_t i=0; i<(1<<bpp); i++){
							pal[i].rgbRed = pal[i].rgbGreen = pal[i].rgbBlue = (uint8_t)(i*(255/((1<<bpp)-1)));
						}
					} else {
						for (int32_t i=0; i<(1<<bpp); i++){
							pal[i].rgbRed = pal[i].rgbGreen = pal[i].rgbBlue = (uint8_t)(255-i*(255/((1<<bpp)-1)));
						}
					}
				}
				break;
			case PHOTOMETRIC_PALETTE:	// color map indexed
				uint16 *red;
				uint16 *green;
				uint16 *blue;
				TIFFGetField(m_tif, TIFFTAG_COLORMAP, &red, &green, &blue); 

				// Is the palette 16 or 8 bits ?
				BOOL Palette16Bits = /*FALSE*/ BIG_palette;
				if (!BIG_palette) {
					int32_t n= 1<<bpp;
					while (n-- > 0) {
						if (red[n] >= 256 || green[n] >= 256 || blue[n] >= 256) {
							Palette16Bits=TRUE;
							break;
						}
					}
				}

				// load the palette in the DIB
				for (int32_t i = (1 << ( BIG_palette ? bitspersample : bpp )) - 1; i >= 0; i--) {
					if (Palette16Bits) {
						pal[i].rgbRed =(uint8_t) CVT(red[i]);
						pal[i].rgbGreen = (uint8_t) CVT(green[i]);
						pal[i].rgbBlue = (uint8_t) CVT(blue[i]);           
					} else {
						pal[i].rgbRed = (uint8_t) red[i];
						pal[i].rgbGreen = (uint8_t) green[i];
						pal[i].rgbBlue = (uint8_t) blue[i];        
					}
				}
				break;
		}
		if (!BIG_palette) { // + VK (BIG palette is stored until image is ready)
			SetPalette(pal,/*head.biClrUsed*/ 1<<bpp);	//palette assign // * VK
			free(pal); 
			pal = NULL; 
		}

		// read the tiff lines and save them in the DIB
		uint32 nrow;
		uint32 ys;
		int32_t line = CalculateLine(width, bitspersample * samplesperpixel);

		int32_t bitsize = TIFFStripSize(m_tif);
		//verify bitsize: could be wrong if StripByteCounts is missing.
		if (bitsize>(int32_t)(head.biSizeImage*samplesperpixel))
			bitsize = head.biSizeImage*samplesperpixel;
		if (bitsize<(int32_t)(info.dwEffWidth*rowsperstrip))
			bitsize = info.dwEffWidth*rowsperstrip;

		if ((bitspersample > 8) && (bitspersample != 16))	// + VK (for bitspersample == 9..15,17..32..64
			bitsize *= (bitspersample + 7)/8; 

		int32_t tiled_image = TIFFIsTiled(m_tif);
		uint32 tw=0, tl=0;
		uint8_t* tilebuf=NULL;
		if (tiled_image){
			TIFFGetField(m_tif, TIFFTAG_TILEWIDTH, &tw);
			TIFFGetField(m_tif, TIFFTAG_TILELENGTH, &tl);
			rowsperstrip = tl;
			bitsize = TIFFTileSize(m_tif) * (int32_t)(1+width/tw);
			tilebuf = (uint8_t*)malloc(TIFFTileSize(m_tif));
		}

		bits = (uint8_t*)malloc(bitspersample==16? bitsize*2 : bitsize); // * VK
		uint8_t * bits16 = NULL;										  // + VK
		int32_t line16    = 0;											  // + VK

		if (!tiled_image && bitspersample==16) {					  // + VK +
			line16 = line;
			line   = CalculateLine(width, 8 * samplesperpixel);
			bits16 = bits;
			bits   = (uint8_t*)malloc(bitsize);
		}

		if (bits==NULL){
			if (bits16) free(bits16);								  // + VK
			if (pal)	free(pal);									  // + VK
			if (tilebuf)free(tilebuf);								  // + VK	
			cx_throw("CxImageTIF can't allocate memory");
		}

#ifdef FIX_16BPP_DARKIMG // + VK: for each line, store shift count bits used to fix it
		uint8_t* row_shifts = NULL;
		if (bits16) row_shifts = (uint8_t*)malloc(height); 
#endif

		for (ys = 0; ys < height; ys += rowsperstrip) {

			if (info.nEscape){ // <vho> - cancel decoding
				free(bits);
				cx_throw("Cancelled");
			}

			nrow = (ys + rowsperstrip > height ? height - ys : rowsperstrip);

			if (tiled_image){
				uint32 imagew = TIFFScanlineSize(m_tif);
				uint32 tilew  = TIFFTileRowSize(m_tif);
				int32_t iskew = imagew - tilew;
				uint8* bufp = (uint8*) bits;

				uint32 colb = 0;
				for (uint32 col = 0; col < width; col += tw) {
					if (TIFFReadTile(m_tif, tilebuf, col, ys, 0, 0) < 0){
						free(tilebuf);
						free(bits);
						cx_throw("Corrupted tiled TIFF file!");
					}

					if (colb + tw > imagew) {
						uint32 owidth = imagew - colb;
						uint32 oskew = tilew - owidth;
						TileToStrip(bufp + colb, tilebuf, nrow, owidth, oskew + iskew, oskew );
					} else {
						TileToStrip(bufp + colb, tilebuf, nrow, tilew, iskew, 0);
					}
					colb += tilew;
				}

			} else {
				if (TIFFReadEncodedStrip(m_tif, TIFFComputeStrip(m_tif, ys, 0), 
					(bits16? bits16 : bits), nrow * (bits16 ? line16 : line)) == -1) { // * VK

#ifdef NOT_IGNORE_CORRUPTED
					free(bits);
					if (bits16) free(bits16);  // + VK
					cx_throw("Corrupted TIFF file!");
#else
					break;
#endif
				}
			}

			for (y = 0; y < nrow; y++) {
				int32_t offset=(nrow-y-1)*line;
				if ((bitspersample==16) && !BIG_palette) {	// * VK
					int32_t offset16 = (nrow-y-1)*line16;		// + VK
					if (bits16)	{							// + VK +
#ifdef FIX_16BPP_DARKIMG
						int32_t the_shift;
						uint8_t hi_byte, hi_max=0;
						uint32_t xi;
						for (xi=0;xi<(uint32)line;xi++) {
							hi_byte = bits16[xi*2+offset16+1];
							if(hi_byte>hi_max)
								hi_max = hi_byte;
						}
						the_shift = (hi_max == 0) ? 8 : 0;
						if (!the_shift)
							while( ! (hi_max & 0x80) ) {
								the_shift++;
								hi_max <<= 1;
							}
						row_shifts[height-ys-nrow+y] = the_shift;
						the_shift = 8 - the_shift;
						for (xi=0;xi<(uint32)line;xi++) 
							bits[xi+offset]= ((bits16[xi*2+offset16+1]<<8) | bits16[xi*2+offset16]) >> the_shift;
#else
						for (uint32_t xi=0;xi<(uint32)line;xi++) 
							bits[xi+offset]=bits16[xi*2+offset16+1];
#endif
					} else {
						for (uint32_t xi=0;xi<width;xi++)
							bits[xi+offset]=bits[xi*2+offset+1];
							}
				}
				if (samplesperpixel==1) { 
					if (BIG_palette)
						if (bits16) {
							int32_t offset16 = (nrow-y-1)*line16;		// + VK
							MoveBitsPal( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
									 bits16 + offset16, width, bitspersample, pal );
						} else
							MoveBitsPal( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
									 bits + offset, width, bitspersample, pal );
					else if ((bitspersample == head.biBitCount) || 
						(bitspersample == 16))	//simple 8bpp, 4bpp image or 16bpp
						memcpy(info.pImage+info.dwEffWidth*(height-ys-nrow+y),bits+offset,min((unsigned)line, info.dwEffWidth));
					else
						MoveBits( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
								  bits + offset, width, bitspersample );
				} else if (samplesperpixel==2) { //8bpp image with alpha layer
					int32_t xi=0;
					int32_t ii=0;
					int32_t yi=height-ys-nrow+y;
#if CXIMAGE_SUPPORT_ALPHA
					if (!pAlpha) AlphaCreate();			// + VK
#endif //CXIMAGE_SUPPORT_ALPHA
					while (ii<line){
						SetPixelIndex(xi,yi,bits[ii+offset]);
#if CXIMAGE_SUPPORT_ALPHA
						AlphaSet(xi,yi,bits[ii+offset+1]);
#endif //CXIMAGE_SUPPORT_ALPHA
						ii+=2;
						xi++;
						if (xi>=(int32_t)width){
							yi--;
							xi=0;
						}
					}
				} else { //photometric==PHOTOMETRIC_CIELAB
					if (head.biBitCount!=24){ //fix image
						Create(width,height,24,CXIMAGE_FORMAT_TIF);
#if CXIMAGE_SUPPORT_ALPHA
						if (samplesperpixel==4) AlphaCreate();
#endif //CXIMAGE_SUPPORT_ALPHA
					}

					int32_t xi=0;
					uint32 ii=0;
					int32_t yi=height-ys-nrow+y;
					RGBQUAD c;
					int32_t l,a,b,bitsoffset;
					double p,cx,cy,cz,cr,cg,cb;
					while (ii</*line*/width){		// * VK
						bitsoffset = ii*samplesperpixel+offset;
						l=bits[bitsoffset];
						a=bits[bitsoffset+1];
						b=bits[bitsoffset+2];
						if (a>127) a-=256;
						if (b>127) b-=256;
						// lab to xyz
						p = (l/2.55 + 16) / 116.0;
						cx = pow( p + a * 0.002, 3);
						cy = pow( p, 3);
						cz = pow( p - b * 0.005, 3);
						// white point
						cx*=0.95047;
						//cy*=1.000;
						cz*=1.0883;
						// xyz to rgb
						cr =  3.240479 * cx - 1.537150 * cy - 0.498535 * cz;
						cg = -0.969256 * cx + 1.875992 * cy + 0.041556 * cz;
						cb =  0.055648 * cx - 0.204043 * cy + 1.057311 * cz;

						if ( cr > 0.00304 ) cr = 1.055 * pow(cr,0.41667) - 0.055;
							else            cr = 12.92 * cr;
						if ( cg > 0.00304 ) cg = 1.055 * pow(cg,0.41667) - 0.055;
							else            cg = 12.92 * cg;
						if ( cb > 0.00304 ) cb = 1.055 * pow(cb,0.41667) - 0.055;
							else            cb = 12.92 * cb;

						c.rgbRed  =(uint8_t)max(0,min(255,(int32_t)(cr*255)));
						c.rgbGreen=(uint8_t)max(0,min(255,(int32_t)(cg*255)));
						c.rgbBlue =(uint8_t)max(0,min(255,(int32_t)(cb*255)));

						SetPixelColor(xi,yi,c);
#if CXIMAGE_SUPPORT_ALPHA
						if (samplesperpixel==4) AlphaSet(xi,yi,bits[bitsoffset+3]);
#endif //CXIMAGE_SUPPORT_ALPHA
						ii++;
						xi++;
						if (xi>=(int32_t)width){
							yi--;
							xi=0;
						}
					}
				}
			}
		}
		free(bits);
		if (bits16) free(bits16);

#ifdef FIX_16BPP_DARKIMG
		if (row_shifts && (samplesperpixel == 1) && (bitspersample==16) && !BIG_palette) {
			// 1. calculate maximum necessary shift
			int32_t min_row_shift = 8;
			for( y=0; y<height; y++ ) {
				if (min_row_shift > row_shifts[y]) min_row_shift = row_shifts[y];
			}
			// 2. for rows having less shift value, correct such rows:
			for( y=0; y<height; y++ ) {
				if (min_row_shift < row_shifts[y]) {
					int32_t need_shift = row_shifts[y] - min_row_shift;
					uint8_t* data = info.pImage + info.dwEffWidth * y;
					for( x=0; x<width; x++, data++ )
						*data >>= need_shift;
				}
			}
		}
		if (row_shifts)	free( row_shifts );
#endif

		if (tiled_image) free(tilebuf);
		if (pal)		 free(pal);

		switch(orientation){
		case ORIENTATION_TOPRIGHT: /* row 0 top, col 0 rhs */
			Mirror();
			break;
		case ORIENTATION_BOTRIGHT: /* row 0 bottom, col 0 rhs */
			Flip();
			Mirror();
			break;
		case ORIENTATION_BOTLEFT: /* row 0 bottom, col 0 lhs */
			Flip();
			break;
		case ORIENTATION_LEFTTOP: /* row 0 lhs, col 0 top */
			RotateRight();
			Mirror();
			break;
		case ORIENTATION_RIGHTTOP: /* row 0 rhs, col 0 top */
			RotateLeft();
			break;
		case ORIENTATION_RIGHTBOT: /* row 0 rhs, col 0 bottom */
			RotateLeft();
			Mirror();
			break;
		case ORIENTATION_LEFTBOT: /* row 0 lhs, col 0 bottom */
			RotateRight();
			break;
		}

	}
  }

int ReadTiff(const char * filename,
  vector<unsigned char> * ptr,
  int * w,
  int * h,
  int * depth)
{
  TIFF* tiff = TIFFOpen(filename, "r");
  if (!tiff) {
    std::cerr << "Error: Couldn't open " << filename << " fopen returned 0";
    return 0;
  }
  uint16 bps, spp;

  TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, w);
  TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, h);
  TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bps);
  TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &spp);
  *depth = bps * spp / 8;

  ptr->resize((*h)*(*w)*(*depth));

  if (*depth==4) {
    if (ptr != NULL) {
      if (!TIFFReadRGBAImageOriented(tiff, *w, *h, (uint32*)&((*ptr)[0]), ORIENTATION_TOPLEFT, 0)) {
        TIFFClose(tiff);
        return 0;
      }
    }
  } else {
    for (size_t i=0; i<TIFFNumberOfStrips(tiff); ++i) {
      if (TIFFReadEncodedStrip(tiff, i, ((uint8*)&((*ptr)[0]))+i*TIFFStripSize(tiff),(tsize_t)-1) ==
        std::numeric_limits<tsize_t>::max()) {
        TIFFClose(tiff);
        return 0;
      }
    }
  }
  TIFFClose(tiff);
  return 1;
}

bool Read_TIFF_ImageHeader(const char * filename, ImageHeader * imgheader)
{
  bool bStatus = false;

  TIFF* tiff = TIFFOpen(filename, "r");
  if (!tiff) {
    return false;
  }

  if (imgheader)
  {
    TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &imgheader->width);
    TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &imgheader->height);
    bStatus = true;
  }

  TIFFClose(tiff);
  return bStatus;
}

std::unique_ptr<Bitmap> TiffLoader::Load()
{
    uint32_t width, height;
    TIFFGetField( m_tiff, TIFFTAG_IMAGEWIDTH, &width );
    TIFFGetField( m_tiff, TIFFTAG_IMAGELENGTH, &height );

    auto bmp = std::make_unique<Bitmap>( width, height );

    if( TIFFReadRGBAImageOriented( m_tiff, width, height, (uint32_t*)bmp->Data(), ORIENTATION_TOPLEFT ) == 0 )
    {
        return nullptr;
    }

    return bmp;
}

bool Image::initWithTiffData(const unsigned char * data, ssize_t dataLen)
{
#if CC_USE_WIC
    return decodeWithWIC(data, dataLen);
#elif CC_USE_TIFF
    bool ret = false;
    do
    {
        // set the read call back function
        tImageSource imageSource;
        imageSource.data    = data;
        imageSource.size    = dataLen;
        imageSource.offset  = 0;

        TIFF* tif = TIFFClientOpen("file.tif", "r", (thandle_t)&imageSource,
            tiffReadProc, tiffWriteProc,
            tiffSeekProc, tiffCloseProc, tiffSizeProc,
            tiffMapProc,
            tiffUnmapProc);

        CC_BREAK_IF(nullptr == tif);

        uint32 w = 0, h = 0;
        uint16 bitsPerSample = 0, samplePerPixel = 0, planarConfig = 0;
        size_t npixels = 0;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplePerPixel);
        TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarConfig);

        npixels = w * h;

        _renderFormat = Image::PixelFormat::RGBA8888;
        _width = w;
        _height = h;

        _dataLen = npixels * sizeof (uint32);
        _data = static_cast<unsigned char*>(malloc(_dataLen * sizeof(unsigned char)));

        uint32* raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
        if (raster != nullptr)
        {
           if (TIFFReadRGBAImageOriented(tif, w, h, raster, ORIENTATION_TOPLEFT, 0))
           {
                /* the raster data is pre-multiplied by the alpha component
                   after invoking TIFFReadRGBAImageOriented*/
                _hasPremultipliedAlpha = true;

               memcpy(_data, raster, npixels*sizeof (uint32));
           }

          _TIFFfree(raster);
        }


        TIFFClose(tif);

        ret = true;
    } while (0);
    return ret;
#else
    CCLOG("tiff is not enabled, please enable it in ccConfig.h");
    return false;
#endif //CC_USE_TIFF
}

int tImageTIFF::ReadSoftwarePageDuration(TIFF* tiff) const
{
	void* data = nullptr;
	int durationMilliSec = -1;
	int success = TIFFGetField(tiff, TIFFTAG_SOFTWARE, &data);
	if (!success || !data)
		return durationMilliSec;

	tString softwareStr((char*)data);
	tExprReader script(softwareStr, false);
	tExpression tacentView = script.First();
	if (tacentView.GetAtomString() == "TacentLibrary")
	{
		tExpression tacentVers = tacentView.Next();
		tExpression durationEx = tacentVers.Next();
		tExpression durCmd = durationEx.Item0();
		if (durCmd.GetAtomString() == "PageDur")
		{
			tExpression durVal = durationEx.Item1();
			durationMilliSec = durVal.GetAtomInt();
		}
	}

	return durationMilliSec;
}

bool tImageTIFF::Load(const tString& tiffFile)
{
	Clear();

	if (tSystem::tGetFileType(tiffFile) != tSystem::tFileType::TIFF)
		return false;

	if (!tFileExists(tiffFile))
		return false;

	TIFF* tiff = TIFFOpen(tiffFile.Chr(), "rb");
	if (!tiff)
		return false;

	// Create all frames.
	tPixelFormat srcFormat = tPixelFormat::R8G8B8A8;
	do
	{
		int width = 0; int height = 0;
		TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width);
		TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
		if ((width <= 0) || (height <= 0))
			break;

		int numPixels = width*height;
		int durationMilliSeconds = ReadSoftwarePageDuration(tiff);

		uint32* pixels = (uint32*)_TIFFmalloc(numPixels * sizeof(uint32));
		int successCode = TIFFReadRGBAImage(tiff, width, height, pixels, 0);
		if (!successCode)
		{
			_TIFFfree(pixels);
			break;
		}

		tFrame* frame = new tFrame;
		frame->Width = width;
		frame->Height = height;
		frame->Pixels = new tPixel4b[width*height];
		frame->PixelFormatSrc = srcFormat;

		// If duration not set we use a default of 1 second.
		frame->Duration = (durationMilliSeconds >= 0) ? float(durationMilliSeconds)/1000.0f : 1.0f;

		for (int p = 0; p < width*height; p++)
			frame->Pixels[p] = pixels[p];

		_TIFFfree(pixels);
		Frames.Append(frame);
	} while (TIFFReadDirectory(tiff));

	TIFFClose(tiff);
	if (Frames.GetNumItems() == 0)
		return false;

	PixelFormatSrc = srcFormat;
	PixelFormat = tPixelFormat::R8G8B8A8;

	// TIFF files are assumed to be in sRGB.
	ColourProfileSrc = tColourProfile::sRGB;
	ColourProfile = tColourProfile::sRGB;

	return true;
}

grk_image* TIFFFormat::decode(const std::string& filename, grk_cparameters* parameters)
{
   bool found_assocalpha = false;
   size_t alpha_count = 0;
   chroma_subsample_x = 1;
   chroma_subsample_y = 1;
   GRK_COLOR_SPACE color_space = GRK_CLRSPC_UNKNOWN;
   grk_image_comp cmptparm[grk::maxNumPackComponents];
   grk_image* image = nullptr;
   uint16_t tiBps = 0, tiPhoto = 0, tiSf = SAMPLEFORMAT_UINT, tiSpp = 0, tiPC = 0;
   bool hasTiSf = false;
   short tiResUnit = 0;
   float tiXRes = 0, tiYRes = 0;
   uint32_t tiWidth = 0, tiHeight = 0;
   bool is_cinema = GRK_IS_CINEMA(parameters->rsiz);
   bool success = false;
   bool isCIE = false;
   uint16_t compress;
   float *luma = nullptr, *refBlackWhite = nullptr;
   uint16_t *red_orig = nullptr, *green_orig = nullptr, *blue_orig = nullptr;

   tif_ = TIFFOpen(filename.c_str(), "r");
   if(!tif_)
   {
      spdlog::error("TIFFFormat::decode: Failed to open {} for reading", filename);
      return 0;
   }

   if(TIFFIsTiled(tif_))
   {
      spdlog::error("TIFFFormat::decode: tiled TIFF images not supported");
      return 0;
   }

   TIFFGetField(tif_, TIFFTAG_COMPRESSION, &compress);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_IMAGEWIDTH, &tiWidth);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_IMAGELENGTH, &tiHeight);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_BITSPERSAMPLE, &tiBps);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_SAMPLESPERPIXEL, &tiSpp);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_PHOTOMETRIC, &tiPhoto);
   TIFFGetFieldDefaulted(tif_, TIFFTAG_PLANARCONFIG, &tiPC);
   hasTiSf = TIFFGetFieldDefaulted(tif_, TIFFTAG_SAMPLEFORMAT, &tiSf) == 1;

   TIFFGetFieldDefaulted(tif_, TIFFTAG_REFERENCEBLACKWHITE, &refBlackWhite);

   uint32_t w = tiWidth;
   uint32_t h = tiHeight;
   uint16_t numcomps = 0;
   uint32_t icclen = 0;
   uint8_t* iccbuf = nullptr;
   uint8_t* iptc_buf = nullptr;
   uint32_t iptc_len = 0;
   uint8_t* xmp_buf = nullptr;
   uint32_t xmp_len = 0;
   uint16_t* sampleinfo = nullptr;
   uint16_t extrasamples = 0;
   bool hasXRes = false, hasYRes = false, hasResUnit = false;
   bool isSigned = (tiSf == SAMPLEFORMAT_INT);
   bool needSignedPixelReader = isSigned && (tiBps == 8 || tiBps == 16);

   // 1. sanity checks

   // check for supported photometric interpretation
   if(tiPhoto != PHOTOMETRIC_MINISBLACK && tiPhoto != PHOTOMETRIC_MINISWHITE &&
      tiPhoto != PHOTOMETRIC_RGB && tiPhoto != PHOTOMETRIC_ICCLAB &&
      tiPhoto != PHOTOMETRIC_CIELAB && tiPhoto != PHOTOMETRIC_YCBCR &&
      tiPhoto != PHOTOMETRIC_SEPARATED && tiPhoto != PHOTOMETRIC_PALETTE)
   {
      spdlog::error("TIFFFormat::decode: Unsupported color format {}.\n"
                    "Only RGB(A), GRAY(A), CIELAB, YCC, CMYK and PALETTE have been implemented.",
                    getColourFormatString(tiPhoto));
      goto cleanup;
   }
   // check for rec601
   if(tiPhoto == PHOTOMETRIC_YCBCR)
   {
      TIFFGetFieldDefaulted(tif_, TIFFTAG_YCBCRCOEFFICIENTS, &luma);
      for(size_t i = 0; i < 3; ++i)
      {
         if((uint32_t)(luma[i] * 1000.0f + 0.5f) != rec_601_luma[i])
         {
            spdlog::error(
                "TIFFFormat::decode: YCbCr image with unsupported non Rec. 601 colour space;");
            spdlog::error("YCbCrCoefficients: {},{},{}", luma[0], luma[1], luma[2]);
            spdlog::error("Please convert to sRGB before compressing.");
            goto cleanup;
         }
      }
   }
   // check sample format
   if(hasTiSf && tiSf != SAMPLEFORMAT_UINT && tiSf != SAMPLEFORMAT_INT)
   {
      spdlog::error("TIFFFormat::decode: Unsupported sample format: {}.",
                    getSampleFormatString(tiSf));
      goto cleanup;
   }
   if(tiSpp == 0)
   {
      spdlog::error("TIFFFormat::decode: Samples per pixel must be non-zero");
      goto cleanup;
   }
   if(tiBps > 16U || tiBps == 0)
   {
      spdlog::error("TIFFFormat::decode: Unsupported precision {}. Maximum 16 Bits supported.",
                    tiBps);
      goto cleanup;
   }
   if(tiWidth == 0 || tiHeight == 0)
   {
      spdlog::error("TIFFFormat::decode: Width({}) and height({}) must both "
                    "be non-zero",
                    tiWidth, tiHeight);
      goto cleanup;
   }
   TIFFGetFieldDefaulted(tif_, TIFFTAG_EXTRASAMPLES, &extrasamples, &sampleinfo);

   // 2. initialize image components and signed/unsigned
   memset(&cmptparm[0], 0, grk::maxNumPackComponents * sizeof(grk_image_comp));
   if((tiPhoto == PHOTOMETRIC_RGB) && (is_cinema) && (tiBps != 12U))
   {
      spdlog::warn("TIFFFormat::decode: Input image bitdepth is {} bits.", tiBps);
      spdlog::warn("TIF conversion has automatically rescaled to 12-bits");
      spdlog::warn("to comply with cinema profiles.\n");
   }
   else
   {
      is_cinema = 0U;
   }
   numcomps = extrasamples;
   switch(tiPhoto)
   {
      case PHOTOMETRIC_PALETTE:
         if(isSigned)
         {
            spdlog::error("TIFFFormat::decode: Signed palette image not supported");
            goto cleanup;
         }
         color_space = GRK_CLRSPC_SRGB;
         numcomps++;
         break;
      case PHOTOMETRIC_MINISBLACK:
      case PHOTOMETRIC_MINISWHITE:
         color_space = GRK_CLRSPC_GRAY;
         numcomps++;
         break;
      case PHOTOMETRIC_RGB:
         color_space = GRK_CLRSPC_SRGB;
         numcomps = (uint16_t)(numcomps + 3);
         break;
      case PHOTOMETRIC_CIELAB:
      case PHOTOMETRIC_ICCLAB:
         isCIE = true;
         color_space = GRK_CLRSPC_DEFAULT_CIE;
         numcomps = (uint16_t)(numcomps + 3);
         break;
      case PHOTOMETRIC_YCBCR:
         // jpeg library is needed to convert from YCbCr to RGB
         if(compress == COMPRESSION_OJPEG || compress == COMPRESSION_JPEG)
         {
            spdlog::error("TIFFFormat::decode: YCbCr image with JPEG compression"
                          " is not supported");
            goto cleanup;
         }
         else if(compress == COMPRESSION_PACKBITS)
         {
            spdlog::error("TIFFFormat::decode: YCbCr image with PACKBITS compression"
                          " is not supported");
            goto cleanup;
         }
         color_space = GRK_CLRSPC_SYCC;
         numcomps = (uint16_t)(numcomps + 3);
         TIFFGetFieldDefaulted(tif_, TIFFTAG_YCBCRSUBSAMPLING, &chroma_subsample_x,
                               &chroma_subsample_y);
         if(chroma_subsample_x == 0 || chroma_subsample_y == 0)
         {
            spdlog::error("TIFFFormat::decode: chroma subsampling factors must be positive.");
            goto cleanup;
         }
         if(chroma_subsample_x > 255 || chroma_subsample_y > 255)
         {
            spdlog::error(
                "TIFFFormat::decode: chroma subsampling factors must each be less than 256.");
            goto cleanup;
         }
         if(chroma_subsample_x != 1 || chroma_subsample_y != 1)
         {
            if(isSigned)
            {
               spdlog::error("TIFFFormat::decode: chroma subsampling {},{} with signed data "
                             "is not supported",
                             chroma_subsample_x, chroma_subsample_y);
               goto cleanup;
            }
            if(numcomps != 3)
            {
               spdlog::error("TIFFFormat::decode: chroma subsampling {},{} with alpha "
                             "channel(s) not supported",
                             chroma_subsample_x, chroma_subsample_y);
               goto cleanup;
            }
         }
         break;
      case PHOTOMETRIC_SEPARATED:
         color_space = GRK_CLRSPC_CMYK;
         numcomps = (uint16_t)(numcomps + 4);
         break;
      default:
         spdlog::error("TIFFFormat::decode: Unsupported colour space {}.", tiPhoto);
         goto cleanup;
         break;
   }
   if(tiPhoto == PHOTOMETRIC_CIELAB)
   {
      if(hasTiSf && (tiSf != SAMPLEFORMAT_INT))
         spdlog::warn("TIFFFormat::decode: Input image is in CIE colour space"
                      " but sample format is unsigned int. Forcing to signed int");
      isSigned = true;
   }
   else if(tiPhoto == PHOTOMETRIC_ICCLAB)
   {
      if(hasTiSf && (tiSf != SAMPLEFORMAT_UINT))
         spdlog::warn("TIFFFormat::decode: Input image is in ICC CIE colour"
                      " space but sample format is signed int. Forcing to unsigned int");
      isSigned = false;
   }

   if(isSigned)
   {
      if(tiPhoto == PHOTOMETRIC_MINISWHITE)
         spdlog::error("TIFFFormat::decode: signed image with "
                       "MINISWHITE format is not fully supported");
      if(tiBps != 4 && tiBps != 8 && tiBps != 10 && tiBps != 12 && tiBps != 16)
      {
         spdlog::error("TIFFFormat::decode: signed image with bit"
                       " depth {} is not supported",
                       tiBps);
         goto cleanup;
      }
   }
   if(numcomps > grk::maxNumPackComponents)
   {
      spdlog::error("TIFFFormat::decode: number of components "
                    "{} must be <= %u",
                    numcomps, grk::maxNumPackComponents);
      goto cleanup;
   }

   // 4. create image
   for(uint32_t j = 0; j < numcomps; j++)
   {
      auto img_comp = cmptparm + j;
      img_comp->prec = (uint8_t)tiBps;
      bool chroma = (j == 1 || j == 2);
      img_comp->dx = chroma ? (uint8_t)chroma_subsample_x : 1;
      img_comp->dy = chroma ? (uint8_t)chroma_subsample_y : 1;
      img_comp->w = grk::ceildiv<uint32_t>(w, img_comp->dx);
      img_comp->h = grk::ceildiv<uint32_t>(h, img_comp->dy);
   }
   image = grk_image_new(numcomps, &cmptparm[0], color_space, true);
   if(!image)
      goto cleanup;

   /* set image offset and reference grid */
   image->x0 = parameters->image_offset_x0;
   image->x1 = image->x0 + (w - 1) * 1 + 1;
   if(image->x1 <= image->x0)
   {
      spdlog::error("TIFFFormat::decode: Bad value for image->x1({}) vs. "
                    "image->x0({}).",
                    image->x1, image->x0);
      goto cleanup;
   }
   image->y0 = parameters->image_offset_y0;
   image->y1 = image->y0 + (h - 1) * 1 + 1;
   if(image->y1 <= image->y0)
   {
      spdlog::error("TIFFFormat::decode: Bad value for image->y1({}) vs. "
                    "image->y0({}).",
                    image->y1, image->y0);
      goto cleanup;
   }
   if(tiPhoto == PHOTOMETRIC_PALETTE)
   {
      if(!TIFFGetField(tif_, TIFFTAG_COLORMAP, &red_orig, &green_orig, &blue_orig))
      {
         spdlog::error("TIFFFormat::decode: Missing required \"Colormap\" tag");
         goto cleanup;
      }
      uint16_t palette_num_entries = (uint16_t)(1U << tiBps);
      uint8_t num_channels = 3U;
      create_meta(image);
      allocPalette(&image->meta->color, num_channels, (uint16_t)palette_num_entries);
      auto cmap = new _grk_component_mapping_comp[num_channels];
      for(uint8_t i = 0; i < num_channels; ++i)
      {
         cmap[i].component = 0;
         cmap[i].mapping_type = 1;
         cmap[i].palette_column = i;
         image->meta->color.palette->channel_prec[i] = 16;
         image->meta->color.palette->channel_sign[i] = false;
      }
      image->meta->color.palette->component_mapping = cmap;
      auto lut_ptr = image->meta->color.palette->lut;
      for(uint16_t i = 0; i < palette_num_entries; i++)
      {
         *lut_ptr++ = red_orig[i];
         *lut_ptr++ = green_orig[i];
         *lut_ptr++ = blue_orig[i];
      }
   }
   for(uint32_t j = 0; j < numcomps; j++)
   {
      // handle non-colour channel
      uint16_t numColourChannels = (uint16_t)(numcomps - extrasamples);
      auto comp = image->comps + j;

      if(extrasamples > 0 && j >= numColourChannels)
      {
         comp->type = GRK_CHANNEL_TYPE_UNSPECIFIED;
         comp->association = GRK_CHANNEL_ASSOC_UNASSOCIATED;
         auto alphaType = sampleinfo[j - numColourChannels];
         if(alphaType == EXTRASAMPLE_ASSOCALPHA)
         {
            if(found_assocalpha)
               spdlog::warn("TIFFFormat::decode: Found more than one associated alpha channel");
            alpha_count++;
            comp->type = GRK_CHANNEL_TYPE_PREMULTIPLIED_OPACITY;
            found_assocalpha = true;
         }
         else if(alphaType == EXTRASAMPLE_UNASSALPHA)
         {
            alpha_count++;
            comp->type = GRK_CHANNEL_TYPE_OPACITY;
         }
         else
         {
            // some older mono or RGB images may have alpha channel
            // stored as EXTRASAMPLE_UNSPECIFIED
            if((color_space == GRK_CLRSPC_GRAY && numcomps == 2) ||
               (color_space == GRK_CLRSPC_SRGB && numcomps == 4))
            {
               alpha_count++;
               comp->type = GRK_CHANNEL_TYPE_OPACITY;
            }
         }
      }
      if(comp->type == GRK_CHANNEL_TYPE_OPACITY ||
         comp->type == GRK_CHANNEL_TYPE_PREMULTIPLIED_OPACITY)
      {
         switch(alpha_count)
         {
            case 1:
               comp->association = GRK_CHANNEL_ASSOC_WHOLE_IMAGE;
               break;
            case 2:
               comp->association = GRK_CHANNEL_ASSOC_UNASSOCIATED;
               break;
            default:
               comp->type = GRK_CHANNEL_TYPE_UNSPECIFIED;
               comp->association = GRK_CHANNEL_ASSOC_UNASSOCIATED;
               break;
         }
      }
      comp->sgnd = isSigned;
   }

   if(needSignedPixelReader && isFinalOutputSubsampled(image))
   {
      spdlog::error("TIFF: subsampling not supported for signed 8 and 16 bit images");
      goto cleanup;
   }

   // 5. extract capture resolution
   hasXRes = TIFFGetFieldDefaulted(tif_, TIFFTAG_XRESOLUTION, &tiXRes) == 1;
   hasYRes = TIFFGetFieldDefaulted(tif_, TIFFTAG_YRESOLUTION, &tiYRes) == 1;
   hasResUnit = TIFFGetFieldDefaulted(tif_, TIFFTAG_RESOLUTIONUNIT, &tiResUnit) == 1;
   if(hasXRes && hasYRes && hasResUnit && tiResUnit != RESUNIT_NONE)
   {
      set_resolution(parameters->capture_resolution_from_file, tiXRes, tiYRes, tiResUnit);
      parameters->write_capture_resolution_from_file = true;
   }
   // 6. extract embedded ICC profile (with sanity check on binary size of profile)
   // note: we ignore ICC profile for CIE images as JPEG 2000 can't signal both
   // CIE and ICC
   if(!isCIE)
   {
      if((TIFFGetFieldDefaulted(tif_, TIFFTAG_ICCPROFILE, &icclen, &iccbuf) == 1) && icclen > 0 &&
         icclen < grk::maxICCProfileBufferLen)
         copy_icc(image, iccbuf, icclen);
   }
   // 7. extract IPTC meta-data
   if(TIFFGetFieldDefaulted(tif_, TIFFTAG_RICHTIFFIPTC, &iptc_len, &iptc_buf) == 1)
   {
      if(TIFFIsByteSwapped(tif_))
         TIFFSwabArrayOfLong((uint32_t*)iptc_buf, iptc_len);
      // since TIFFTAG_RICHTIFFIPTC is of type TIFF_LONG, we must multiply
      // by 4 to get the length in bytes
      create_meta(image);
      image->meta->iptc_len = iptc_len * 4;
      image->meta->iptc_buf = new uint8_t[iptc_len];
      memcpy(image->meta->iptc_buf, iptc_buf, iptc_len);
   }
   // 8. extract XML meta-data
   if(TIFFGetFieldDefaulted(tif_, TIFFTAG_XMLPACKET, &xmp_len, &xmp_buf) == 1)
   {
      create_meta(image);
      image->meta->xmp_len = xmp_len;
      image->meta->xmp_buf = new uint8_t[xmp_len];
      memcpy(image->meta->xmp_buf, xmp_buf, xmp_len);
   }
   // 9. read pixel data
   if(needSignedPixelReader)
   {
      if(tiBps == 8)
         success = readTiffPixelsSigned<int8_t>(tif_, image->comps, numcomps, tiSpp, tiPC);
      else
         success = readTiffPixelsSigned<int16_t>(tif_, image->comps, numcomps, tiSpp, tiPC);
   }
   else
   {
      success = readTiffPixels(tif_, image->comps, numcomps, tiSpp, tiPC, tiPhoto,
                               chroma_subsample_x, chroma_subsample_y);
   }
cleanup:
   if(tif_)
      TIFFClose(tif_);
   tif_ = nullptr;
   if(success)
   {
      if(is_cinema)
      {
         for(uint32_t j = 0; j < numcomps; ++j)
            scaleComponent(image->comps + j, 12);
      }
      return image;
   }
   if(image)
      grk_object_unref(&image->obj);

   return nullptr;
}

HANDLE TIFFRGBA2DIB(TIFFDibImage *dib, uint32_t *raster)
{
    void *pDIB = 0;
    TIFFRGBAImage *img = &dib->tif;

    uint32_t imageLength;
    uint32_t imageWidth;
    uint16_t BitsPerSample;
    uint16_t SamplePerPixel;
    uint32_t RowsPerStrip;
    uint16_t PhotometricInterpretation;

    BITMAPINFOHEADER bi;
    int dwDIBSize;

    TIFFGetField(img->tif, TIFFTAG_IMAGEWIDTH, &imageWidth);
    TIFFGetField(img->tif, TIFFTAG_IMAGELENGTH, &imageLength);
    TIFFGetField(img->tif, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);
    TIFFGetField(img->tif, TIFFTAG_ROWSPERSTRIP, &RowsPerStrip);
    TIFFGetField(img->tif, TIFFTAG_SAMPLESPERPIXEL, &SamplePerPixel);
    TIFFGetField(img->tif, TIFFTAG_PHOTOMETRIC, &PhotometricInterpretation);

    if (BitsPerSample == 1 && SamplePerPixel == 1 && dib->dibinstalled)
    { // bilevel
        bi.biSize = sizeof(BITMAPINFOHEADER);
        bi.biWidth = imageWidth;
        bi.biHeight = imageLength;
        bi.biPlanes = 1; // always
        bi.biBitCount = 1;
        bi.biCompression = BI_RGB;
        bi.biSizeImage = WIDTHBYTES(bi.biWidth * bi.biBitCount) * bi.biHeight;
        bi.biXPelsPerMeter = 0;
        bi.biYPelsPerMeter = 0;
        bi.biClrUsed = 0;      //  must be zero for RGB compression (none)
        bi.biClrImportant = 0; // always

        // Get the size of the DIB
        dwDIBSize = GetDIBSize(&bi);

        // Allocate for the BITMAPINFO structure and the color table.
        pDIB = GlobalAllocPtr(GHND, dwDIBSize);
        if (pDIB == 0)
        {
            return (NULL);
        }

        // Copy the header info
        *((BITMAPINFOHEADER *)pDIB) = bi;

        // Get a pointer to the color table
        RGBQUAD *pRgbq = (RGBQUAD *)((LPSTR)pDIB + sizeof(BITMAPINFOHEADER));

        pRgbq[0].rgbRed = 0;
        pRgbq[0].rgbBlue = 0;
        pRgbq[0].rgbGreen = 0;
        pRgbq[0].rgbReserved = 0;
        pRgbq[1].rgbRed = 255;
        pRgbq[1].rgbBlue = 255;
        pRgbq[1].rgbGreen = 255;
        pRgbq[1].rgbReserved = 255;

        // Pointers to the bits
        // PVOID pbiBits = (LPSTR)pRgbq + bi.biClrUsed * sizeof(RGBQUAD);
        //
        // In the BITMAPINFOHEADER documentation, it appears that
        // there should be no color table for 32 bit images, but
        // experience shows that the image is off by 3 words if it
        // is not included.  So here it is.
        PVOID pbiBits = GetDIBImagePtr(
            (BITMAPINFOHEADER *)pDIB); //(LPSTR)pRgbq + 3 * sizeof(RGBQUAD);

        int sizeWords = bi.biSizeImage / 4;
        RGBQUAD *rgbDib = (RGBQUAD *)pbiBits;
        long *rgbTif = (long *)raster;

        _TIFFmemcpy(pbiBits, raster, bi.biSizeImage);
    }

    //  For now just always default to the RGB 32 bit form. // save as 32 bit
    //  for simplicity
    else if (true /*BitsPerSample == 8 && SamplePerPixel == 3*/)
    { // 24 bit color

        bi.biSize = sizeof(BITMAPINFOHEADER);
        bi.biWidth = imageWidth;
        bi.biHeight = imageLength;
        bi.biPlanes = 1; // always
        bi.biBitCount = 32;
        bi.biCompression = BI_RGB;
        bi.biSizeImage = WIDTHBYTES(bi.biWidth * bi.biBitCount) * bi.biHeight;
        bi.biXPelsPerMeter = 0;
        bi.biYPelsPerMeter = 0;
        bi.biClrUsed = 0;      //  must be zero for RGB compression (none)
        bi.biClrImportant = 0; // always

        // Get the size of the DIB
        dwDIBSize = GetDIBSize(&bi);

        // Allocate for the BITMAPINFO structure and the color table.
        pDIB = GlobalAllocPtr(GHND, dwDIBSize);
        if (pDIB == 0)
        {
            return (NULL);
        }

        // Copy the header info
        *((BITMAPINFOHEADER *)pDIB) = bi;

        // Get a pointer to the color table
        RGBQUAD *pRgbq = (RGBQUAD *)((LPSTR)pDIB + sizeof(BITMAPINFOHEADER));

        // Pointers to the bits
        // PVOID pbiBits = (LPSTR)pRgbq + bi.biClrUsed * sizeof(RGBQUAD);
        //
        // In the BITMAPINFOHEADER documentation, it appears that
        // there should be no color table for 32 bit images, but
        // experience shows that the image is off by 3 words if it
        // is not included.  So here it is.
        PVOID pbiBits = (LPSTR)pRgbq + 3 * sizeof(RGBQUAD);

        int sizeWords = bi.biSizeImage / 4;
        RGBQUAD *rgbDib = (RGBQUAD *)pbiBits;
        long *rgbTif = (long *)raster;

        // Swap the byte order while copying
        for (int i = 0; i < sizeWords; ++i)
        {
            rgbDib[i].rgbRed = TIFFGetR(rgbTif[i]);
            rgbDib[i].rgbBlue = TIFFGetB(rgbTif[i]);
            rgbDib[i].rgbGreen = TIFFGetG(rgbTif[i]);
            rgbDib[i].rgbReserved = 0;
        }
    }

    return pDIB;
}

CPLErr GTIFWktFromMemBufEx( int nSize, unsigned char *pabyBuffer,
                            char **ppszWKT, double *padfGeoTransform,
                            int *pnGCPCount, GDAL_GCP **ppasGCPList,
                            int *pbPixelIsPoint )

{
    bool    bPixelIsPoint = false;
    int     bPointGeoIgnore = FALSE;
    short nRasterType;
    char szFilename[100];

    sprintf( szFilename, "/vsimem/wkt_from_mem_buf_%ld.tif",
             (long) CPLGetPID() );

/* -------------------------------------------------------------------- */
/*      Make sure we have hooked CSVFilename().                         */
/* -------------------------------------------------------------------- */
    LibgeotiffOneTimeInit();

/* -------------------------------------------------------------------- */
/*      Create a memory file from the buffer.                           */
/* -------------------------------------------------------------------- */
    VSILFILE *fp = VSIFileFromMemBuffer( szFilename, pabyBuffer, nSize, FALSE );
    if( fp == NULL )
        return CE_Failure;
    VSIFCloseL( fp );

/* -------------------------------------------------------------------- */
/*      Initialize access to the memory geotiff structure.              */
/* -------------------------------------------------------------------- */
    TIFF        *hTIFF;
    hTIFF = VSI_TIFFOpen( szFilename, "rc" );

    if( hTIFF == NULL )
    {
        CPLError( CE_Failure, CPLE_AppDefined,
                  "TIFF/GeoTIFF structure is corrupt." );
        VSIUnlink( szFilename );
        return CE_Failure;
    }

/* -------------------------------------------------------------------- */
/*      Get the projection definition.                                  */
/* -------------------------------------------------------------------- */
    GTIF 	*hGTIF;
    GTIFDefn    *psGTIFDefn;

    hGTIF = GTIFNew(hTIFF);

    if( hGTIF != NULL && GTIFKeyGet(hGTIF, GTRasterTypeGeoKey, &nRasterType,
                0, 1 ) == 1
        && nRasterType == (short) RasterPixelIsPoint )
    {
        bPixelIsPoint = true;
        bPointGeoIgnore =
            CSLTestBoolean( CPLGetConfigOption("GTIFF_POINT_GEO_IGNORE",
                                            "FALSE") );
    }
    if( pbPixelIsPoint )
        *pbPixelIsPoint = bPixelIsPoint;

#if LIBGEOTIFF_VERSION >= 1410
    psGTIFDefn = GTIFAllocDefn();
#else
    psGTIFDefn = (GTIFDefn *) CPLCalloc(1,sizeof(GTIFDefn));
#endif


    if( hGTIF != NULL && GTIFGetDefn( hGTIF, psGTIFDefn ) )
        *ppszWKT = GTIFGetOGISDefn( hGTIF, psGTIFDefn );
    else
        *ppszWKT = NULL;

    if( hGTIF )
        GTIFFree( hGTIF );

#if LIBGEOTIFF_VERSION >= 1410
    GTIFFreeDefn(psGTIFDefn);
#else
    CPLFree(psGTIFDefn);
#endif

/* -------------------------------------------------------------------- */
/*      Get geotransform or tiepoints.                                  */
/* -------------------------------------------------------------------- */
    double	*padfTiePoints, *padfScale, *padfMatrix;
    int16	nCount;

    padfGeoTransform[0] = 0.0;
    padfGeoTransform[1] = 1.0;
    padfGeoTransform[2] = 0.0;
    padfGeoTransform[3] = 0.0;
    padfGeoTransform[4] = 0.0;
    padfGeoTransform[5] = 1.0;

    *pnGCPCount = 0;
    *ppasGCPList = NULL;

    if( TIFFGetField(hTIFF,TIFFTAG_GEOPIXELSCALE,&nCount,&padfScale )
        && nCount >= 2 )
    {
        padfGeoTransform[1] = padfScale[0];
        padfGeoTransform[5] = - ABS(padfScale[1]);

        if( TIFFGetField(hTIFF,TIFFTAG_GEOTIEPOINTS,&nCount,&padfTiePoints )
            && nCount >= 6 )
        {
            padfGeoTransform[0] =
                padfTiePoints[3] - padfTiePoints[0] * padfGeoTransform[1];
            padfGeoTransform[3] =
                padfTiePoints[4] - padfTiePoints[1] * padfGeoTransform[5];

            // adjust for pixel is point in transform
            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                padfGeoTransform[0] -= (padfGeoTransform[1] * 0.5 + padfGeoTransform[2] * 0.5);
                padfGeoTransform[3] -= (padfGeoTransform[4] * 0.5 + padfGeoTransform[5] * 0.5);
            }
        }
    }

    else if( TIFFGetField(hTIFF,TIFFTAG_GEOTIEPOINTS,&nCount,&padfTiePoints )
             && nCount >= 6 )
    {
        *pnGCPCount = nCount / 6;
        *ppasGCPList = (GDAL_GCP *) CPLCalloc(sizeof(GDAL_GCP),*pnGCPCount);

        for( int iGCP = 0; iGCP < *pnGCPCount; iGCP++ )
        {
            char	szID[32];
            GDAL_GCP	*psGCP = *ppasGCPList + iGCP;

            sprintf( szID, "%d", iGCP+1 );
            psGCP->pszId = CPLStrdup( szID );
            psGCP->pszInfo = CPLStrdup("");
            psGCP->dfGCPPixel = padfTiePoints[iGCP*6+0];
            psGCP->dfGCPLine = padfTiePoints[iGCP*6+1];
            psGCP->dfGCPX = padfTiePoints[iGCP*6+3];
            psGCP->dfGCPY = padfTiePoints[iGCP*6+4];
            psGCP->dfGCPZ = padfTiePoints[iGCP*6+5];
        }
    }

    else if( TIFFGetField(hTIFF,TIFFTAG_GEOTRANSMATRIX,&nCount,&padfMatrix )
             && nCount == 16 )
    {
        padfGeoTransform[0] = padfMatrix[3];
        padfGeoTransform[1] = padfMatrix[0];
        padfGeoTransform[2] = padfMatrix[1];
        padfGeoTransform[3] = padfMatrix[7];
        padfGeoTransform[4] = padfMatrix[4];
        padfGeoTransform[5] = padfMatrix[5];
    }

/* -------------------------------------------------------------------- */
/*      Cleanup.                                                        */
/* -------------------------------------------------------------------- */
    XTIFFClose( hTIFF );

    VSIUnlink( szFilename );

    if( *ppszWKT == NULL )
        return CE_Failure;
    else
        return CE_None;
}

void CqTiffDirHandle::fillHeaderRequiredAttrs(CqTexFileHeader& header) const
{
	// Fill header with general metadata which won't affect the details of the
	// pixel memory layout.
	header.setWidth(tiffTagValue<uint32>(TIFFTAG_IMAGEWIDTH));
	header.setHeight(tiffTagValue<uint32>(TIFFTAG_IMAGELENGTH));
	if(TIFFIsTiled(tiffPtr()))
	{
		header.set<Attr::TileInfo>( SqTileInfo(
					tiffTagValue<uint32>(TIFFTAG_TILEWIDTH),
					tiffTagValue<uint32>(TIFFTAG_TILELENGTH)) );
	}
	// Get the compression type.
	header.set<Attr::Compression>(
			tiffCompressionNameFromTag(tiffTagValue<uint16>(TIFFTAG_COMPRESSION)) );
	// Compute pixel aspect ratio
	TqFloat xRes = 0;
	TqFloat yRes = 0;
	if(TIFFGetField(tiffPtr(), TIFFTAG_XRESOLUTION, &xRes)
			&& TIFFGetField(tiffPtr(), TIFFTAG_YRESOLUTION, &yRes))
	{
		// yRes/xRes should be the correct quantity corresponding to the
		// pixelAspectRatio used in OpenEXR.
		header.set<Attr::PixelAspectRatio>(yRes/xRes);
	}
	else
	{
		header.set<Attr::PixelAspectRatio>(1.0f);
	}
}

void addAttributeToHeader(ttag_t tag, CqTexFileHeader& header,
		const CqTiffDirHandle& dirHandle)
{
	Ttiff temp;
	if(TIFFGetField(dirHandle.tiffPtr(), tag, &temp))
		header.set<Tattr>(attrTypeFromTiff<Tattr, Ttiff>(temp));
}

void addWrapModesToHeader(CqTexFileHeader& header, const CqTiffDirHandle& dirHandle)
{
	char* wrapModesStr = 0;
	if(TIFFGetField(dirHandle.tiffPtr(), TIFFTAG_PIXAR_WRAPMODES, &wrapModesStr))
	{
		std::istringstream iss(wrapModesStr);
		SqWrapModes modes;
		iss >> modes.sWrap >> modes.tWrap;
		header.set<Attr::WrapModes>(modes);
	}
}

void CqTiffDirHandle::fillHeaderOptionalAttrs(CqTexFileHeader& header) const
{
	// Add various descriptive strings to the header if they exist
	addAttributeToHeader<Attr::Software,char*>(TIFFTAG_SOFTWARE, header, *this);
	addAttributeToHeader<Attr::HostName,char*>(TIFFTAG_HOSTCOMPUTER, header, *this);
	addAttributeToHeader<Attr::Description,char*>(TIFFTAG_IMAGEDESCRIPTION, header, *this);
	addAttributeToHeader<Attr::DateTime,char*>(TIFFTAG_DATETIME, header, *this);
	addAttributeToHeader<Attr::TextureFormat,const char*>(TIFFTAG_PIXAR_TEXTUREFORMAT, header, *this);

	// Add texturemap-specific stuff to the header if it exists.
	addWrapModesToHeader(header, *this);

	// Add some matrix attributes
	addAttributeToHeader<Attr::WorldToScreenMatrix,float*>(
			TIFFTAG_PIXAR_MATRIX_WORLDTOSCREEN, header, *this);
	addAttributeToHeader<Attr::WorldToCameraMatrix,float*>(
			TIFFTAG_PIXAR_MATRIX_WORLDTOCAMERA, header, *this);
	// Add cotan of the field of view.
	addAttributeToHeader<Attr::FieldOfViewCot,float>(
			TIFFTAG_PIXAR_FOVCOT, header, *this);

	// Retrieve tags relevant to the display window
	// The origin of the image is apparently given in resolution units, but
	// here we want to interpret it as the number of pixels from the top left
	// of the image, hence the lfloor.
	uint32 fullWidth = header.width();
	uint32 fullHeight = header.height();
	float xPos = 0;
	float yPos = 0;
	if( TIFFGetField(tiffPtr(), TIFFTAG_PIXAR_IMAGEFULLWIDTH, &fullWidth)
		| TIFFGetField(tiffPtr(), TIFFTAG_PIXAR_IMAGEFULLLENGTH, &fullHeight)
		| TIFFGetField(tiffPtr(), TIFFTAG_XPOSITION, &xPos)
		| TIFFGetField(tiffPtr(), TIFFTAG_YPOSITION, &yPos)
		// bitwise OR used since we don't want shortcut evaluation
		)
	{
		header.set<Attr::DisplayWindow>( SqImageRegion(fullWidth, fullHeight,
					lfloor(xPos), lfloor(yPos)) );
	}
}

IqTextureMapOld* CqTextureMapOld::GetShadowMap( const CqString& strName )
{
	QGetRenderContext() ->Stats().IncTextureMisses( 3 );

	//TqUlong hash = CqString::hash(strName.c_str());

	// First search the texture map cache
	for ( std::vector<CqTextureMapOld*>::iterator i = m_TextureMap_Cache.begin(); i != m_TextureMap_Cache.end(); i++ )
	{
		if ( (*i)->getName() == strName )
		{
			if ( ( *i ) ->Type() == MapType_Shadow )
			{
				QGetRenderContext() ->Stats().IncTextureHits( 1, 3 );
				return ( *i );
			} else
			{
				return NULL;
			}
		}
	}

	QGetRenderContext() ->Stats().IncTextureHits( 0, 3 );

	// If we got here, it doesn't exist yet, so we must create and load it.
	CqShadowMapOld* pNew = new CqShadowMapOld( strName );
	m_TextureMap_Cache.push_back( pNew );
	pNew->Open();

	TqPchar ptexfmt;
	if ( pNew->m_pImage == 0 ||
	        TIFFGetField( pNew->m_pImage, TIFFTAG_PIXAR_TEXTUREFORMAT, &ptexfmt ) != 1 ||
	        strcmp( ptexfmt, SHADOWMAP_HEADER ) != 0 )
	{
		static bool done = false;
		if (!done)
		{
			Aqsis::log() << error << "Map \"" << strName.c_str() << "\" is not a valid shadow map, use RiMakeShadow" << std::endl;
			done = true;
		}
		pNew->SetInvalid();
	}
   	else 
   	{
		pNew->ReadMatrices();
   	}
	return ( pNew );
}

void CqShadowMapOld::ReadMatrices()
{
	// Read the transform matrices.
	TqFloat*	WToC;
	TqFloat*	WToS;
	CqMatrix	matWToC, matWToS;

	// Set the number of shadow maps initially to 0.
	m_NumberOfMaps = 0;

	CqMatrix matCToW;
	QGetRenderContextI() ->matSpaceToSpace( "camera", "world", NULL, NULL, QGetRenderContextI()->Time(), matCToW );

	TqDouble minz;

	while(1)
	{
		TqInt reta = TIFFGetField( m_pImage, TIFFTAG_PIXAR_MATRIX_WORLDTOCAMERA, &WToC );
		TqInt retb = TIFFGetField( m_pImage, TIFFTAG_PIXAR_MATRIX_WORLDTOSCREEN, &WToS );
		if ( !reta || !retb )
			SetInvalid();
		else
		{
			TqInt r, c;
			for ( r = 0; r < 4; r++ )
			{
				for ( c = 0; c < 4; c++ )
				{
					matWToC[ r ][ c ] = WToC[ ( r * 4 ) + c ];
					matWToS[ r ][ c ] = WToS[ ( r * 4 ) + c ];
				}
			}
		}
		// Set the matrixes to general, not Identity as default.
		matWToC.SetfIdentity( false );
		matWToS.SetfIdentity( false );

		matWToC *= matCToW;
		matWToS *= matCToW;

		// Generate normal conversion matrices to save time.
		CqMatrix matITTCToL = matWToC;
		matITTCToL[ 3 ][ 0 ] = matITTCToL[ 3 ][ 1 ] = matITTCToL[ 3 ][ 2 ] = matITTCToL[ 0 ][ 3 ] = matITTCToL[ 1 ][ 3 ] = matITTCToL[ 2 ][ 3 ] = 0.0;
		matITTCToL[ 3 ][ 3 ] = 1.0;
		matITTCToL.Inverse();
		matITTCToL.Transpose();

		m_WorldToCameraMatrices.push_back( matWToC );
		m_WorldToScreenMatrices.push_back( matWToS );
		m_ITTCameraToLightMatrices.push_back( matITTCToL );

		if (TIFFGetField( m_pImage, TIFFTAG_SMINSAMPLEVALUE, &minz ))
		{
			m_MinZ.push_back( minz );
		}

		m_NumberOfMaps++;	// Increment the number of maps.

		if( TIFFReadDirectory( m_pImage ) == 0 )
			break;
	}
}

bool CqTextureMapOld::CreateMIPMAP(bool fProtectBuffers)
{
	if ( m_pImage != 0 )
	{
		// Check if the format is normal scanline, otherwise we are unable to MIPMAP it yet.
		uint32 tsx;
		TqInt ret = TIFFGetField( m_pImage, TIFFTAG_TILEWIDTH, &tsx );
		if( ret )
		{
			Aqsis::log() << error << "Cannot MIPMAP a tiled image \"" << m_strName.c_str() << "\"" << std::endl;
			return( false );
		}

		// Read the whole image into a buffer.
		TqUint directory = 0;
		CqTextureMapBuffer* buffer = GetBuffer( 0, 0, directory++, fProtectBuffers );
		CqImageDownsampler sampler(m_swidth, m_twidth, m_FilterFunc, m_smode, m_tmode);
		while(buffer->Width() > 1 && buffer->Height() > 1)
		{
			buffer = sampler.downsample(buffer, *this, directory, fProtectBuffers);
			m_apMipMaps[directory%256].push_back(buffer);
			m_apLast[directory%256] = buffer;
			directory++;
		}
	}
	return( true );
}

CqTextureMapBuffer* CqTextureMapOld::GetBuffer( TqUlong s, TqUlong t, TqInt directory, bool fProt )
{
	QGetRenderContext() ->Stats().IncTextureMisses( 4 );

	CqTextureMapBuffer *lastcache = m_apLast[directory%256];

	if (lastcache && lastcache->IsValid(s, t, directory))
	{
		QGetRenderContext() ->Stats().IncTextureHits( 0, 4 );
		return lastcache;
	}

	// Search already cached segments first.
	std::list<CqTextureMapBuffer*>::iterator i = m_apMipMaps[directory%256].begin(); 
	std::list<CqTextureMapBuffer*>::iterator e = m_apMipMaps[directory%256].end(); 
	for ( ; i != e; i++ )
	{
		if ( ( *i ) ->IsValid( s, t, directory ) )
		{
			QGetRenderContext() ->Stats().IncTextureHits( 1, 4 );
			CqTextureMapBuffer* pbuffer = *i;
			m_apLast[directory%256] = pbuffer;
			return ( pbuffer );
		}
	}

	// If we got here, segment is not currently loaded, so load the correct segement and store it in the cache.
	CqTextureMapBuffer* pTMB = 0;

	if ( !m_pImage )
	{
		boost::filesystem::path imagePath = QGetRenderContext()->poptCurrent()
			->findRiFileNothrow(m_strName, "texture");
		if ( imagePath.empty() )
		{
			Aqsis::log() << error << "Cannot open texture file \"" << m_strName.c_str() << "\"" << std::endl;
			return pTMB;
		}

		// Now open it as a tiff file.
		m_pImage = TIFFOpen( native(imagePath).c_str(), "r" );
	}

	if ( m_pImage )
	{
		uint32 tsx, tsy;
		TqInt ret = TIFFGetField( m_pImage, TIFFTAG_TILEWIDTH, &tsx );
		TIFFGetField( m_pImage, TIFFTAG_TILELENGTH, &tsy );
		// If a tiled image, read the appropriate tile.
		if ( ret )
		{
			// Work out the coordinates of this tile.
			TqUlong ox = ( s / tsx ) * tsx;
			TqUlong oy = ( t / tsy ) * tsy;
			pTMB = CreateBuffer( ox, oy, tsx, tsy, directory, fProt );

			TIFFSetDirectory( m_pImage, directory );

			void* pData = pTMB->pVoidBufferData();
			TIFFReadTile( m_pImage, pData, s, t, 0, 0 );
		}
		else
		{
			// Create a storage buffer
			pTMB = CreateBuffer( 0, 0, m_XRes, m_YRes, directory, true );

			TIFFSetDirectory( m_pImage, directory );
			void* pdata = pTMB->pVoidBufferData();
			TqUint i;
			for ( i = 0; i < m_YRes; i++ )
			{
				TIFFReadScanline( m_pImage, pdata, i );
				pdata = reinterpret_cast<void*>( reinterpret_cast<char*>( pdata ) + m_XRes * pTMB->ElemSize() );
			}
		}
		// Put this segment on the top of the list, so that next time it is found first. This
		// allows us to take advantage of likely spatial coherence during shading.
		m_apMipMaps[directory%256].push_front( pTMB );
		m_apLast[directory%256] = pTMB;
	}
	return ( pTMB );
}

void CqTextureMapOld::Open()
{
	m_IsValid = false;

	// Find the file required.
	boost::filesystem::path imagePath = QGetRenderContext()->poptCurrent()
		->findRiFileNothrow(m_strName, "texture");
	if ( imagePath.empty() )
	{
		Aqsis::log() << error << "Cannot open texture file \"" << m_strName.c_str() << "\"" << std::endl;
		return ;
	}
	m_pImage = TIFFOpen(native(imagePath).c_str(), "r" );

	if ( m_pImage )
	{
		Aqsis::log() << info << "TextureMapOld: \"" << imagePath << "\" is open" << std::endl;
		TqPchar pFormat = 0;
		TqPchar pModes = 0;

		TIFFGetField( m_pImage, TIFFTAG_IMAGEWIDTH, &m_XRes );
		TIFFGetField( m_pImage, TIFFTAG_IMAGELENGTH, &m_YRes );

		uint16 planarconfig;
		TIFFGetField( m_pImage, TIFFTAG_PLANARCONFIG, &planarconfig );
		m_PlanarConfig = planarconfig;
		uint16 samplesperpixel = 1;
		TIFFGetField( m_pImage, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel );
		m_SamplesPerPixel = samplesperpixel;
		uint16 sampleformat;
		TIFFGetFieldDefaulted( m_pImage, TIFFTAG_SAMPLEFORMAT, &sampleformat );
		m_SampleFormat = sampleformat;

		uint16 bitspersample;
		TIFFGetFieldDefaulted( m_pImage, TIFFTAG_BITSPERSAMPLE, &bitspersample );
		m_BitsPerSample = bitspersample;

		TIFFGetField( m_pImage, TIFFTAG_PIXAR_TEXTUREFORMAT, &pFormat );
		TIFFGetField( m_pImage, TIFFTAG_PIXAR_WRAPMODES, &pModes );

		// Resize the temporary storage values to the new depth.
		m_pixel_variance.resize( m_SamplesPerPixel );
		m_pixel_sublevel.resize( m_SamplesPerPixel );
		m_accum_color.resize( m_SamplesPerPixel );

		/* Aqsis supports a slighty different scheme for MipMap tiff file;
		 * its filtering is kept as a string in 
		 * Texture Wrap Modes: "periodic periodic box 1.000000 1.000000"
		 * where AIR, 3Delight, BMRT, RDC use very basic texture wrap mode description eg.
		 * Texture Wrap Modes: "black,black"
		 * therefore I initialized the value for filtering to be black, black, box, 1.0, 1.0
		 * 
		 */
		if ( pModes )
		{
			Interpreted( pModes );
		}
		uint32 tsx;

		/* First tests; is it stored using tiles ? */
		TqInt bMipMap = TIFFGetField( m_pImage, TIFFTAG_TILEWIDTH, &tsx );
		bMipMap &= TIFFGetField( m_pImage, TIFFTAG_TILELENGTH, &tsx );

		/* Second test; is it containing enough directories for us */
		TqInt minRes = min(m_XRes, m_YRes );
		TqInt directory = static_cast<TqInt>(fastlog2(static_cast<TqFloat> (minRes)));
		if (TIFFSetDirectory(m_pImage, directory - 1) == false)
		   bMipMap &= TIFFSetDirectory(m_pImage, directory - 2);


		TIFFSetDirectory(m_pImage, 0 );


		/* Support for 3delight, AIR, BMRT, RDC, PIXIE MipMap files.
		 * Aqsis is not bound to have exact multiples of 2 on height, length.
		 * The Format of 3Delight, AIR, BMRT and RDC is more "Plain Texture"/MipMap.
		 * What is preventing us to load their files was the format description file as 
		 * MipMap differ from our format description not the way they store their information.
		 * A better way is to ask the direct question if if the image is stored as MipMap via
		 * TIFFTAG_TILEWIDTH, TIFFTAG_TILELENGTH and checking if the texture contains enough 
		 * directory/pages.
		 */

		if ( bMipMap )
		{
			m_Format = TexFormat_MIPMAP;
			m_IsValid = true;
		}
		else
		{
			m_Format = TexFormat_Plain;
			m_IsValid = true;
		}
	}
	m_Directory = 0;
	for (TqInt k=0; k < 256; k++)
	{
		m_apLast[k] = NULL;
		m_apMipMaps[k].resize(0);
	}
	m_apFlat.resize(0);
}

IqTextureMapOld* CqTextureMapOld::GetLatLongMap( const CqString& strName )
{
	QGetRenderContext() ->Stats().IncTextureMisses( 2 );

	TqUlong hash = CqString::hash(strName.c_str());

	// First search the texture map cache
	for ( std::vector<CqTextureMapOld*>::iterator i = m_TextureMap_Cache.begin(); i != m_TextureMap_Cache.end(); i++ )
	{
		if ( ( *i ) ->m_hash == hash )
		{
			if ( ( *i ) ->Type() == MapType_LatLong )
			{
				QGetRenderContext() ->Stats().IncTextureHits( 1, 2 );
				return ( *i );
			} else
			{
				return NULL;
			}
		}
	}

	QGetRenderContext() ->Stats().IncTextureHits( 0, 2 );

	// If we got here, it doesn't exist yet, so we must create and load it.
	CqTextureMapOld* pNew = new CqLatLongMapOld( strName );
	m_TextureMap_Cache.push_back( pNew );
	pNew->Open();

	TqPchar ptexfmt;

	// Invalid only if this is not a LatLong Env. map file
	if ( pNew->m_pImage == 0 ||
	        TIFFGetField( pNew->m_pImage, TIFFTAG_PIXAR_TEXTUREFORMAT, &ptexfmt ) != 1 ||
	        strcmp( ptexfmt, LATLONG_HEADER ) != 0 )
	{
		static bool done = false;
		if (!done)
		{
			Aqsis::log() << error << "Map \"" << strName.c_str() << "\" is not an environment map, use RiMakeLatLongEnvironment" << std::endl;
			done = true;
		}

		pNew->SetInvalid();
   	}
	return ( pNew );
}

IqTextureMapOld* CqTextureMapOld::GetEnvironmentMap( const CqString& strName )
{
	QGetRenderContext() ->Stats().IncTextureMisses( 1 );

	TqUlong hash = CqString::hash(strName.c_str());

	// First search the texture map cache
	for ( std::vector<CqTextureMapOld*>::iterator i = m_TextureMap_Cache.begin(); i != m_TextureMap_Cache.end(); i++ )
	{
		if ( ( *i ) ->m_hash == hash )
		{
			if ( ( *i ) ->Type() == MapType_Environment )
			{
				QGetRenderContext() ->Stats().IncTextureHits( 1, 1 );
				return ( *i );
			} else
			{
				return NULL;
			}
		}
	}

	QGetRenderContext() ->Stats().IncTextureHits( 0, 1 );

	// If we got here, it doesn't exist yet, so we must create and load it.
	CqTextureMapOld* pNew = new CqEnvironmentMapOld( strName );
	m_TextureMap_Cache.push_back( pNew );
	pNew->Open();

	TqPchar ptexfmt = 0;

	// Invalid if the m_pImage is not there or it is not cube or latlong env. map file
	if ( pNew->m_pImage == 0 ||
	        TIFFGetField( pNew->m_pImage, TIFFTAG_PIXAR_TEXTUREFORMAT, &ptexfmt ) != 1 ||
	        ( strcmp( ptexfmt, CUBEENVMAP_HEADER ) != 0 ) && ( strcmp( ptexfmt, LATLONG_HEADER ) != 0 ) )
	{
		static bool done = false;
		if (!done)
		{
			Aqsis::log() << error << "Map \"" << strName.c_str() << "\" is not an environment map, use RiMakeCubeFaceEnvironment" << std::endl;
			done = true;
		}
		pNew->SetInvalid();
		delete pNew;
		pNew = NULL;

	}
	else
	{
		TqFloat fov;
		if (TIFFGetField( pNew->m_pImage, TIFFTAG_PIXAR_FOVCOT, &fov) == 1)
			((CqEnvironmentMapOld *)pNew)->SetFov(fov);
		else
			((CqEnvironmentMapOld *)pNew)->SetFov(1.0);
	}

	// remove from the list a LatLong env. map since in shadeops.cpp we will cope with it.
	if ( ptexfmt && strcmp( ptexfmt, LATLONG_HEADER ) == 0 )
	{
		pNew->SetInvalid();
		delete pNew;
		pNew = NULL;
   	} 

	return ( pNew );
}

std::vector<Pixel> load_tiff(const char *path, std::uint32_t &image_width, std::uint32_t &image_height, std::size_t &image_size) {
        TIFF *image_tiff = TIFFOpen(path, "r");
        if(!image_tiff) {
            eprintf_error("Cannot open %s", path);
            exit(EXIT_FAILURE);
        }
        TIFFGetField(image_tiff, TIFFTAG_IMAGEWIDTH, &image_width);
        TIFFGetField(image_tiff, TIFFTAG_IMAGELENGTH, &image_height);

        // Force associated alpha if we have alpha so alpha doesn't get multiplied in TIFFReadRGBAImageOriented
        std::uint16_t count;
        std::uint16_t *attributes;
        int defined = TIFFGetField(image_tiff, TIFFTAG_EXTRASAMPLES, &count, &attributes);
        if(defined && count == 1) {
            if(*attributes == EXTRASAMPLE_UNASSALPHA) {
                std::uint16_t new_value = EXTRASAMPLE_ASSOCALPHA;
                TIFFSetField(image_tiff, TIFFTAG_EXTRASAMPLES, 1, &new_value);
            }
        }

        // Read it all
        image_size = image_width * image_height * sizeof(Invader::Pixel);
        auto image_pixels = std::vector<Invader::Pixel>(image_size);
        TIFFReadRGBAImageOriented(image_tiff, image_width, image_height, reinterpret_cast<std::uint32_t *>(image_pixels.data()), ORIENTATION_TOPLEFT);

        // Close the TIFF
        TIFFClose(image_tiff);

        // Swap red and blue channels
        for(std::size_t i = 0; i < image_size / 4; i++) {
            Invader::Pixel swapped = image_pixels[i];
            swapped.red = image_pixels[i].blue;
            swapped.blue = image_pixels[i].red;
            image_pixels[i] = swapped;
        }

        return image_pixels;
    }

TiffFileReader::TiffFileReader(const char* filepath)
  {
    _tiff = TIFFOpen(filepath, "r");
    if (!_tiff)
      throw std::runtime_error{fmt::format("Failed to open file {}", filepath)};

    TIFFGetField(_tiff, TIFFTAG_IMAGEWIDTH, &_width);
    TIFFGetField(_tiff, TIFFTAG_IMAGELENGTH, &_height);
  }

void TPTImage::loadImageInfo( int seq, int ang )
{
  tdir_t current_dir;
  int count = 0;
  uint16_t colour, samplesperpixel, bitspersample, sampleformat;
  double *sminvalue = NULL, *smaxvalue = NULL;
  double scale;
  unsigned int tw, th, w, h;
  string filename;
  const char *tmp = NULL;

  currentX = seq;
  currentY = ang;

  // Get various essential image parameters
  TIFFGetField( tiff, TIFFTAG_IMAGEWIDTH, &w );
  TIFFGetField( tiff, TIFFTAG_IMAGELENGTH, &h );
  TIFFGetField( tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel );
  TIFFGetField( tiff, TIFFTAG_BITSPERSAMPLE, &bitspersample );
  TIFFGetField( tiff, TIFFTAG_PHOTOMETRIC, &colour );
  TIFFGetField( tiff, TIFFTAG_SAMPLEFORMAT, &sampleformat );
  TIFFGetField( tiff, TIFFTAG_XRESOLUTION, &dpi_x );
  TIFFGetField( tiff, TIFFTAG_YRESOLUTION, &dpi_y );
  TIFFGetField( tiff, TIFFTAG_RESOLUTIONUNIT, &dpi_units );

  // If image is untiled, set tile sizes to zero
  if( TIFFGetField( tiff, TIFFTAG_TILEWIDTH, &tw ) == 0 ) tw = 0;
  if( TIFFGetField( tiff, TIFFTAG_TILEWIDTH, &th ) == 0 ) th = 0;

  // Units for libtiff are 1=unknown, 2=DPI and 3=pixels/cm, whereas we want 0=unknown, 1=DPI and 2=pixels/cm
  dpi_units--;

  // We have to do this conversion explicitly to avoid problems on Mac OS X
  channels = (unsigned int) samplesperpixel;
  bpc = (unsigned int) bitspersample;
  sampleType = (sampleformat==3) ? SampleType::FLOATINGPOINT : SampleType::FIXEDPOINT;

  // Check for the no. of resolutions in the pyramidal image
  current_dir = TIFFCurrentDirectory( tiff );

  // In order to get our list of image sizes, make sure we start in the first TIFF directory
  if( current_dir != 0 ){
    if( !TIFFSetDirectory( tiff, 0 ) ) throw file_error( "TPTImage :: TIFFSetDirectory() failed" );
  }


  // Empty any existing list of available resolution sizes
  image_widths.clear();
  image_heights.clear();
  tile_widths.clear();
  tile_heights.clear();

  // Store the list of image dimensions available, starting with the full resolution
  image_widths.push_back( w );
  image_heights.push_back( h );
  tile_widths.push_back( tw );
  tile_heights.push_back( th );

  // Add this to our list of valid resolutions
  resolution_ids.push_back( 0 );

  // Sub-resolutions can either be stored within the SubIFDs of a top-level IFD or in separate top-level IFDs.
  // Check first for sub-resolution levels stored within SubIFDs (as used by OME-TIFF).
  // In these files, the full resolution image is stored in the first IFD and subsequent
  // resolutions are stored in SubIFDs
  loadSubIFDs();
  subifd_ifd = 0;

  if( subifds.size() > 0 ){
    // Start from 1 as the top-level IFD already holds the full resolution image
    for( unsigned int n = 1; n<subifds.size(); n++ ){
      if( TIFFSetSubDirectory( tiff, subifds[n] ) ){
	uint32_t stype;
	// Only use valid reduced image subfile types
	if( (TIFFGetField( tiff, TIFFTAG_SUBFILETYPE, &stype ) == 1) && (stype == 0x01) ){

	  // Store exact image size for each resolution level
	  TIFFGetField( tiff, TIFFTAG_IMAGEWIDTH, &w );
	  TIFFGetField( tiff, TIFFTAG_IMAGELENGTH, &h );
	  image_widths.push_back( w );
	  image_heights.push_back( h );

	  // Tile sizes can vary between resolutions
	  TIFFGetField( tiff, TIFFTAG_TILEWIDTH, &tw );
	  TIFFGetField( tiff, TIFFTAG_TILELENGTH, &th );
	  tile_widths.push_back( tw );
	  tile_heights.push_back( th );

	  count++;
	}
      }
    }

    // If there are valid SubIFDs, tag this image appropriately and check whether we have a stack of images
    if( count > 0 ){
      pyramid = SUBIFD;
      loadStackInfo();
    }

    // Reset to first TIFF directory
    if( !TIFFSetDirectory( tiff, 0 ) ) throw file_error( "TPTImage :: TIFFSetDirectory() failed" );
  }

  // If there are no SubIFD resolutions, look for them in the main sequence of IFD TIFF directories
  if( pyramid == NORMAL ){
    for( count = 0; TIFFReadDirectory( tiff ); count++ ){

      // Only use tiled IFD directories
      if( ( TIFFGetField( tiff, TIFFTAG_TILEWIDTH, &tw ) == 1 ) &&
	  ( TIFFGetField( tiff, TIFFTAG_TILELENGTH, &th ) == 1 ) ){

	// Tile sizes can vary between resolutions
	tile_widths.push_back( tw );
	tile_heights.push_back( th );

	// Store exact image size for each resolution level
	TIFFGetField( tiff, TIFFTAG_IMAGEWIDTH, &w );
	TIFFGetField( tiff, TIFFTAG_IMAGELENGTH, &h );
	image_widths.push_back( w );
	image_heights.push_back( h );

	// Add this index to our list of valid resolutions
	resolution_ids.push_back( count+1 );
      }

    }

    // Check whether this is in fact a stack from an image too small to have SubIFD resolutions
    if( (image_widths.size() > 0) && (image_widths[0] == image_widths[1]) && (image_heights[0] == image_heights[1]) ){
      loadStackInfo();
      if( stack.size() > 0 ){
	// Remove duplicate sizes
	image_widths.resize(1);
	image_heights.resize(1);
	tile_widths.resize(1);
	tile_heights.resize(1);
	count = 0;
      }
    }
  }


  // Total number of available resolutions
  numResolutions = image_widths.size();


  // Reset the TIFF directory to where it was
  if( !TIFFSetDirectory( tiff, current_dir ) ) throw file_error( "TPTImage :: TIFFSetDirectory() failed" );


  // Handle various colour spaces
  if( colour == PHOTOMETRIC_CIELAB ) colorspace = ColorSpace::CIELAB;
  else if( colour == PHOTOMETRIC_MINISBLACK ){
    colorspace = (bpc==1)? ColorSpace::BINARY : ColorSpace::GREYSCALE;
  }
  else if( colour == PHOTOMETRIC_PALETTE ){
    // Watch out for colourmapped images. These are stored as 1 sample per pixel,
    // but are decoded to 3 channels by libtiff, so declare them as sRGB
    colorspace = ColorSpace::sRGB;
    channels = 3;
  }
  else if( colour == PHOTOMETRIC_YCBCR ){
    // JPEG encoded tiles can be subsampled YCbCr encoded. Ask to decode these to RGB
    TIFFSetField( tiff, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB );
    colorspace = ColorSpace::sRGB;
  }
  else colorspace = ColorSpace::sRGB;


  // Get the max and min values for our data (important for float data)
  // First initialize default values to zero in case
  double *default_min = new double[channels];
  double *default_max = new double[channels];
  for( unsigned int k=0; k<channels; k++ ){
    default_min[k] = 0.0;
    default_max[k] = 0.0;
  }

  // These max and min values can either be single values per image, or as from libtiff > 4.0.2
  // per channel (see: http://www.asmail.be/msg0055458208.html)
#ifdef TIFFTAG_PERSAMPLE

  TIFFSetField( tiff, TIFFTAG_PERSAMPLE, PERSAMPLE_MULTI ); // Need to activate per sample mode
  TIFFGetField( tiff, TIFFTAG_SMINSAMPLEVALUE, &sminvalue );
  TIFFGetField( tiff, TIFFTAG_SMAXSAMPLEVALUE, &smaxvalue );

  if( !sminvalue ) sminvalue = default_min;
  if( !smaxvalue ) smaxvalue = default_max;

#else
  // Set defaults
  sminvalue = default_min;
  smaxvalue = default_max;

  // Add the single min/max header value to each channel if tag exists
  double minmax;
  if( TIFFGetField( tiff, TIFFTAG_SMINSAMPLEVALUE, &minmax ) == 1 ){
    for( unsigned int k=0; k<channels; k++ ) sminvalue[k] = minmax;
  }
  if( TIFFGetField( tiff, TIFFTAG_SMAXSAMPLEVALUE, &minmax ) == 1 ){
    for( unsigned int k=0; k<channels; k++ ) smaxvalue[k] = minmax;
  }
#endif

  // Make sure our min and max arrays are empty
  min.clear();
  max.clear();

  for( unsigned int i=0; i<channels; i++ ){
    // Set our max to the full bit range if max not set in header
    if( smaxvalue[i] == 0 ){
      if( bpc <= 8 ) smaxvalue[i] = 255.0;
      else if( bpc == 12 ) smaxvalue[i] = 4095.0;
      else if( bpc == 16 ) smaxvalue[i] = 65535.0;
      else if( bpc == 32 && sampleType == SampleType::FIXEDPOINT ) smaxvalue[i] = 4294967295.0;
      else if( bpc == 32 && sampleType == SampleType::FLOATINGPOINT ) smaxvalue[i] = 1.0;  // Set dummy value for float
    }
    min.push_back( (float)sminvalue[i] );
    max.push_back( (float)smaxvalue[i] );
  }
  // Don't forget to delete our allocated arrays
  delete[] default_min;
  delete[] default_max;

  // Also get some basic metadata
  if( TIFFGetField( tiff, TIFFTAG_ARTIST, &tmp ) ) metadata.insert( {"creator",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_COPYRIGHT, &tmp ) ) metadata.insert( {"rights",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_DATETIME, &tmp ) ) metadata.insert( {"date",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_IMAGEDESCRIPTION, &tmp ) ) metadata.insert( {"description",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_DOCUMENTNAME, &tmp ) ) metadata.insert( {"title",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_PAGENAME, &tmp ) ) metadata.insert( {"pagename",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_SOFTWARE, &tmp ) ) metadata.insert( {"software",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_MAKE, &tmp ) ) metadata.insert( {"make",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_MODEL, &tmp ) ) metadata.insert( {"model",tmp} );
  if( TIFFGetField( tiff, TIFFTAG_XMLPACKET, &count, &tmp ) ) metadata.insert( {"xmp",string(tmp,count)} );
  if( TIFFGetField( tiff, TIFFTAG_ICCPROFILE, &count, &tmp ) ) metadata.insert( {"icc",string(tmp,count)} );
  if( TIFFGetField( tiff, TIFFTAG_STONITS, &scale ) ){
    char buffer[32];
    snprintf( buffer, sizeof(buffer), "%g", scale );
    metadata.insert( {"scale",buffer} );
  }
}

RawTile TPTImage::getTile( int x, int y, unsigned int res, int layers, unsigned int tile, ImageEncoding requested_encoding )
{
  uint32_t im_width, im_height, tw, th, ntlx, ntly;
  uint32_t rem_x, rem_y;
  uint16_t colour, planar, compression;
  string filename;


  // Check the resolution exists
  if( res > numResolutions ){
    ostringstream error;
    error << "TPTImage :: Asked for non-existent resolution: " << res;
    throw file_error( error.str() );
  }


  // If we are currently working on a different sequence number, then
  //  close and reload the image.
  if( stack.empty() && ( (currentX != x) || (currentY != y) ) ){
    closeImage();
  }


  // Open the TIFF if it's not already open
  if( !tiff ){
    filename = getFileName( x, y );
    if( ( tiff = TIFFOpen( filename.c_str(), mode ) ) == NULL ){
      throw file_error( "TPTImage :: TIFFOpen() failed for:" + filename );
    }
  }


  // Reload our image information in case the tile size etc is different - no need to do this for image stacks
  if( stack.empty() && ( (currentX != x) || (currentY != y) ) ){
    loadImageInfo( x, y );
  }


  // The IIP protocol defines the first resolution as the smallest, so we need to invert
  //  the requested resolution as our TIFF images are stored with the largest resolution first
  int vipsres = ( numResolutions - 1 ) - res;


  // Check in which directory we currently are
  tdir_t cd = TIFFCurrentDirectory( tiff );

  // Handle SubIFD-based resolution levels
  if( pyramid == SUBIFD ){

    // If we have an image stack within our TIFF, change to the appropriate directory
    if( (int)cd != x ){
      if( !TIFFSetDirectory( tiff, x ) ){
	ostringstream error;
	error << "TPTImage :: TIFFSetDirectory() failed for stack " << x;
	throw file_error( error.str() );
      }
      cd = x;
    }

    // Reload our SubIFD list if necessary
    if( subifds.empty() || x != (int)subifd_ifd ){
      loadSubIFDs();
      subifd_ifd = cd;
    }

    // Change to the appropriate SubIFD directory if necessary
    if( (vipsres < (int)subifds.size()) && (subifds[vipsres] > 0) ){
      if( !TIFFSetSubDirectory( tiff, subifds[vipsres] ) ){
	ostringstream error;
	error << "TPTImage :: TIFFSetSubDirectory() failed for SubIFD offset " << subifds[vipsres];
	throw file_error( error.str() );
      }
    }
  }
  // If TIFF pyramid is a "classic" image pyramid with sub-resolutions within successive IFDs, just move to the appropriate directory
  else {
    if( vipsres != (int)cd ){
      if( !TIFFSetDirectory( tiff, resolution_ids[vipsres] ) ){
	ostringstream error;
	error << "TPTImage :: TIFFSetDirectory() failed for resolution " << vipsres;
	throw file_error( error.str() );
      }
    }
  }


  // Check that a valid tile number was given
  if( tile >= TIFFNumberOfTiles( tiff ) ) {
    ostringstream tile_no;
    tile_no << "TPTImage :: Asked for non-existent tile: " << tile;
    throw file_error( tile_no.str() );
  }


  // Get the size of this tile, the size of the current resolution,
  //   the number of samples and the colorspace.
  //  TIFFGetField( tiff, TIFFTAG_TILEWIDTH, &tw );
  //  TIFFGetField( tiff, TIFFTAG_TILELENGTH, &th );
  TIFFGetField( tiff, TIFFTAG_IMAGEWIDTH, &im_width );
  TIFFGetField( tiff, TIFFTAG_IMAGELENGTH, &im_height );
  TIFFGetField( tiff, TIFFTAG_PHOTOMETRIC, &colour );
  TIFFGetField( tiff, TIFFTAG_SAMPLESPERPIXEL, &channels );
  TIFFGetField( tiff, TIFFTAG_BITSPERSAMPLE, &bpc );
  TIFFGetField( tiff, TIFFTAG_PLANARCONFIG, &planar );
  TIFFGetField( tiff, TIFFTAG_COMPRESSION, &compression );

  // Get tile size for this resolution - make sure it is tiled
  tw = tile_widths[vipsres];
  th = tile_heights[vipsres];

  if( (tw == 0) || (th == 0) ){
    throw file_error( "TPTImage :: Requested resolution is not tiled" );
  }

  // Total number of pixels in tile
  size_t np = tw * th;

  // Get the width and height for last row and column tiles
  rem_x = im_width % tw;
  rem_y = im_height % th;


  // Calculate the number of tiles in each direction
  ntlx = (im_width / tw) + (rem_x == 0 ? 0 : 1);
  ntly = (im_height / th) + (rem_y == 0 ? 0 : 1);


  // Alter the tile size if it's in the last column
  if( ( tile % ntlx == ntlx - 1 ) && ( rem_x != 0 ) ) {
    tw = rem_x;
  }


  // Alter the tile size if it's in the bottom row
  if( ( tile / ntlx == ntly - 1 ) && rem_y != 0 ) {
    th = rem_y;
  }


  // Handle various colour spaces
  if( colour == PHOTOMETRIC_CIELAB ) colorspace = ColorSpace::CIELAB;
  else if( colour == PHOTOMETRIC_MINISBLACK ){
    colorspace = (bpc==1)? ColorSpace::BINARY : ColorSpace::GREYSCALE;
  }
  else if( colour == PHOTOMETRIC_PALETTE ){
    // Watch out for colourmapped images. There are stored as 1 sample per pixel,
    // but are decoded to 3 channels by libtiff, so declare them as sRGB
    colorspace = ColorSpace::GREYSCALE;
    channels = 1;
  }
  else if( colour == PHOTOMETRIC_YCBCR ){
    // JPEG encoded tiles can be subsampled YCbCr encoded. Ask to decode these to RGB
    TIFFSetField( tiff, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB );
    colorspace = ColorSpace::sRGB;
  }
  else colorspace = ColorSpace::sRGB;


  // Initialize our RawTile object
  RawTile rawtile( tile, res, x, y, tile_widths[vipsres], tile_heights[vipsres], channels, bpc );
  rawtile.filename = getImagePath();
  rawtile.timestamp = timestamp;
  rawtile.sampleType = sampleType;


  // If codec pass-through is disabled or the tile needs reprocessing (cropping or change of bit depth),
  //  make sure we decode the tile to raw pixel format
  if( (IIPImage::codec_passthrough == false) ||
      (tw != tile_widths[vipsres] || th != tile_heights[vipsres]) ||
      (bpc==1 && channels==1) ){
    requested_encoding = ImageEncoding::RAW;
  }

  // Also disable pass-through for TIFF-JPEGs where YCbCr sub-sampling has been defined with an RGB color space - these
  // require the full JFIF format rather than the abreviated JPEG within the TIFF
  if( requested_encoding == ImageEncoding::JPEG && compression == COMPRESSION_JPEG ){
    uint16_t subsampling[2];
    if( (TIFFGetField( tiff, TIFFTAG_YCBCRSUBSAMPLING, &subsampling[0], &subsampling[1] ) != 0) && (colour == PHOTOMETRIC_RGB) ){
      if( IIPImage::logging ) logfile << "TPTImage :: Sub-sampled RGB JPEG-encoded TIFF: tile decoded to RAW" << endl;
      requested_encoding = ImageEncoding::RAW;
    }
  }


  // Calculate maximum amount of memory required for the tile when decoded as raw pixels values
  tsize_t bytes = TIFFTileSize( tiff );


  // If we are in pass-through mode, we need to know the exact number of bytes the tile
  // takes up as this may sometimes be larger than the raw data size
  if( ( requested_encoding == ImageEncoding::JPEG && compression == COMPRESSION_JPEG )
#ifdef HAVE_WEB
      || ( requested_encoding == ImageEncoding::WEBP && compression == COMPRESSION_WEBP )
#endif
      ){
    // Get actual number of bytes for this specific tile from the TILEBYTECOUNTS tag
    // which returns an array of all tile sizes in current IFD
    uint64_t *bytecounts;
    if( !TIFFGetField( tiff, TIFFTAG_TILEBYTECOUNTS, &bytecounts ) ){
      ostringstream error;
      error << "TPTImage :: Unable to get byte count for tile " << tile;
      throw file_error( error.str() );
    }
    bytes = bytecounts[tile];
    if( IIPImage::logging ) logfile << "TPTImage :: Byte count for compressed tile: " << bytes << endl;
  }


  // Get raw pre-encoded tile if our request matches the tile encoding - only currently makes sense for JPEG and WEBP
  if( requested_encoding == ImageEncoding::JPEG && compression == COMPRESSION_JPEG ){

    /* TIFF JPEG uses the JPEGTABLES field to store quantization and Huffman tables with image data stored
       separately in each tile.
       The JPEGTABLES tag data begins with a JPEG SOI marker (0XFF,0xD8), followed by the tables themselves
       and ending with an EOI marker (0xFF,0xD9).
       The tile data consists of image data preceeded by an SOI marker.
       To reconstruct a full JPEG image, the table and tile data need to be concatenated, but with the final
       EOI marker from the table tag and the initial SOI marker from the tile data removed
    */

    unsigned char* jpeg_tables;
    uint16_t count = 0;

    if( ( TIFFGetField( tiff, TIFFTAG_JPEGTABLES, &count, &jpeg_tables ) != 0 ) && ( count > 4 ) ){

      // Allocate enough space for compressed tile data + JPEG tables
      rawtile.allocate( bytes + count - 4 );

      // Store last 2 bytes of the JPEG table data itself for use later - skip over the final 2 byte EOI marker
      unsigned char table_end[2];
      table_end[0] = ((unsigned char*)jpeg_tables)[count-4];
      table_end[1] = ((unsigned char*)jpeg_tables)[count-3];

      // Copy tables to our RawTile buffer - ignore the final 2 byte EOI marker
      memcpy( rawtile.data, jpeg_tables, count-2 );

      // Note starting position before we add image data - rewind by extra 2 bytes as we want to temporarily
      // overwrite the end of the table data with the SOI which preceeds the image data in the tile stream
      int pos = count - 4;

      int length = TIFFReadRawTile( tiff, (ttile_t) tile, (tdata_t) &(((unsigned char*)rawtile.data)[pos]), bytes );
      if( length == -1 ){
	throw file_error( "TPTImage :: TIFFReadRawTile() failed for JPEG-encoded tile for " + getFileName( x, y ) );
      }

      // Overwrite superfluous SOI marker from tile with previously saved end of JPEG tables
      ((unsigned char*)rawtile.data)[pos]=table_end[0];
      ((unsigned char*)rawtile.data)[pos+1]=table_end[1];

      rawtile.dataLength = pos + length;
      rawtile.compressionType = ImageEncoding::JPEG;
    }
    else{
      // Throw error if no JPEG tables present
      throw file_error( "TPTImage :: Empty TIFFTAG_JPEGTABLES tag for JPEG-encoded tile for " + getFileName( x, y ) );
    }
  }

#ifdef COMPRESSION_WEBP
  else if( requested_encoding == ImageEncoding::WEBP && compression == COMPRESSION_WEBP ){

    // Allocate buffer
    rawtile.allocate( bytes );

    // Copy compressed tile directly into buffer
    int length = TIFFReadRawTile( tiff, (ttile_t) tile, (tdata_t) rawtile.data, bytes );
    if( length == -1 ){
      throw file_error( "TPTImage :: TIFFReadRawTile() failed for WebP-encoded tile for " + getFileName( x, y ) );
    }
    rawtile.dataLength = length;
    rawtile.compressionType = ImageEncoding::WEBP;
  }
#endif

  // Decode the tile into raw pixel data - dump data directly into RawTile buffer
  else{

    // Allocate buffer
    rawtile.allocate( bytes );

    // Copy decoded tile into buffer as raw pixel values
    int length = TIFFReadEncodedTile( tiff, (ttile_t) tile, (tdata_t) rawtile.data, bytes );
    if( length == -1 ){
      throw file_error( "TPTImage :: TIFFReadEncodedTile() failed for " + getFileName( x, y ) );
    }
    rawtile.dataLength = length;
    rawtile.quality = 100;
    rawtile.compressionType = ImageEncoding::RAW;
  }

void TPTImage::loadSubIFDs()
{
  uint16_t n_subifd;
  toff_t *subifd;
  subifds.clear();
  if( TIFFGetField( tiff, TIFFTAG_SUBIFD, &n_subifd, &subifd ) == 1 ){
    if( n_subifd > 0 ){
      subifds.push_back(0);
      for( int n = 0; n<n_subifd; n++ ) subifds.push_back( subifd[n] );
    }
  }
}

void TPTImage::loadStackInfo()
{
  double scale;
  const char *tmp = NULL;

  // Reset to first TIFF directory
  if( !TIFFSetDirectory( tiff, 0 ) ) throw file_error( "TPTImage :: TIFFSetDirectory() failed" );

  // Start from 1 as horizontalAnglesList is initialized with 0 by default
  int n = 1;

  // Loop through our IFDs and get the name and scaling factor for each
  do {
    uint32_t stype;

    // Stack layers should really be in multi-page type sub file types
    if( (TIFFGetField( tiff, TIFFTAG_SUBFILETYPE, &stype ) == 1) && (stype == 0x02) ){
      Stack s;
      horizontalAnglesList.push_back(n++);
      if( TIFFGetField( tiff, TIFFTAG_DOCUMENTNAME, &tmp ) ) s.name = string(tmp);
      if( TIFFGetField( tiff, TIFFTAG_STONITS, &scale ) ) s.scale = (float) scale;
      stack.push_back( s );
    }
  } while( TIFFReadDirectory(tiff) );

  // Need to remove last item from stack list
  if( horizontalAnglesList.size() > 1 ) horizontalAnglesList.pop_back();
}

void Watermark::init()
{
  if( _image.length() > 0 ){

    TIFF *tiff_watermark;
    if( ( tiff_watermark = TIFFOpen( _image.c_str(), "r" ) ) ){

      TIFFGetField( tiff_watermark, TIFFTAG_IMAGEWIDTH, &_width );
      TIFFGetField( tiff_watermark, TIFFTAG_IMAGELENGTH, &_height );
      TIFFGetField( tiff_watermark, TIFFTAG_BITSPERSAMPLE, &_bpc );

      uint32_t *buffer = new uint32_t[_width*_height];

      if( TIFFReadRGBAImageOriented( tiff_watermark, _width, _height, buffer, ORIENTATION_TOPLEFT ) == 0 ){
	delete[] buffer;
	TIFFClose( tiff_watermark );
	return;
      }

      // Set our number of channels to 3 as TIFFReadRGBAImage always outputs an 8bit colour image
      _channels = 3;

      // Set up the memory storage
      _watermark = new unsigned char[_width*_height*_channels];
      memset( _watermark, 0, (size_t) _width*_height*_channels );

      // Load the data into our buffers
      for( uint32_t i=0; i<_width*_height; i++ ){
	uint32_t rgba = buffer[i];
	unsigned char r,g,b;
	float a;
	// Extract the RGBA values
	r = (unsigned char) TIFFGetR(rgba);
	g = (unsigned char) TIFFGetG(rgba);
	b = (unsigned char) TIFFGetB(rgba);
	a = (float) TIFFGetA(rgba) / 255;
	_watermark[i*3] = r * _opacity * a;
	_watermark[i*3 + 1] = g * _opacity * a;
	_watermark[i*3 + 2] = b * _opacity * a;
      }

      delete[] buffer;
      TIFFClose( tiff_watermark );
      _isSet = true;
    }

  }

}

EStatusCode TIFFImageHandler::ReadTopLevelTiffInformation()
{
	EStatusCode status = PDFHummus::eSuccess;
	tdir_t directorycount = TIFFNumberOfDirectories(mT2p->input);
	tdir_t i=0;
	uint16 pagen=0;
	uint16 paged=0;
	uint16 xuint16=0;

	do
	{
		// allocate tiff pages
		mT2p->tiff_pages = (T2P_PAGE*) _TIFFmalloc(directorycount * sizeof(T2P_PAGE));
		if(!mT2p->tiff_pages)
		{
			TRACE_LOG2("Can't allocate %u bytes of memory for tiff_pages array, %s",
						directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}
		_TIFFmemset( mT2p->tiff_pages, 0x00, directorycount * sizeof(T2P_PAGE));

		// allocate tile array, one for each page
		mT2p->tiff_tiles = (T2P_TILES*) _TIFFmalloc(directorycount * sizeof(T2P_TILES));
		if(!mT2p->tiff_tiles)
		{
			TRACE_LOG2("Can't allocate %u bytes of memory for tiff_tiles array, %s",
						directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}
		_TIFFmemset( mT2p->tiff_tiles, 0x00, directorycount * sizeof(T2P_TILES));

		for(i=0;i<directorycount;i++)
		{
			bool isPage = false,isPage2 = false;
			uint32 subfiletype = 0;

			if(!TIFFSetDirectory(mT2p->input, i)){
				TRACE_LOG2("Can't set directory %u of input file %s",
							directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
				status = PDFHummus::eFailure;
				break;
			}			

			if(TIFFGetField(mT2p->input, TIFFTAG_PAGENUMBER, &pagen, &paged))
			{
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_number =
					((pagen>paged) && (paged != 0)) ? paged : pagen;
				isPage2 = true;
			} 
			else if(TIFFGetField(mT2p->input, TIFFTAG_SUBFILETYPE, &subfiletype))
			{
				isPage = ((subfiletype & FILETYPE_PAGE) != 0) || (subfiletype == 0);
			} 
			else if(TIFFGetField(mT2p->input, TIFFTAG_OSUBFILETYPE, &subfiletype))
			{
				isPage =	(subfiletype == OFILETYPE_IMAGE) || 
							(subfiletype == OFILETYPE_PAGE)	 || 
							(subfiletype == 0);
			}
			else
			{
				isPage = true;
				isPage2 = true;
			}

			if(isPage)
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_number=mT2p->tiff_pagecount;
			if(isPage || isPage2)
			{
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_directory=i;
				if(TIFFIsTiled(mT2p->input))
				{
					mT2p->tiff_pages[mT2p->tiff_pagecount].page_tilecount = 
						TIFFNumberOfTiles(mT2p->input);
				}
			}
			mT2p->tiff_pagecount++;
		}
		if(status != PDFHummus::eSuccess)
			break;

		qsort((void*) mT2p->tiff_pages, mT2p->tiff_pagecount,sizeof(T2P_PAGE), t2p_cmp_t2p_page);

		for(i=0;i<mT2p->tiff_pagecount;i++)
		{
			TIFFSetDirectory(mT2p->input, mT2p->tiff_pages[i].page_directory );
			if((TIFFGetField(mT2p->input, TIFFTAG_PHOTOMETRIC, &xuint16)
						&& (xuint16==PHOTOMETRIC_PALETTE))
			   || TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16)) 
			{
				mT2p->tiff_pages[i].page_extra++;
			}
			if (TIFFGetField(mT2p->input, TIFFTAG_TRANSFERFUNCTION,
									 &(mT2p->tiff_transferfunction[0]),
									 &(mT2p->tiff_transferfunction[1]),
									 &(mT2p->tiff_transferfunction[2]))) 
			{
				if(mT2p->tiff_transferfunction[1] !=
				   mT2p->tiff_transferfunction[0]) 
				{
					mT2p->tiff_transferfunctioncount = 3;
					mT2p->tiff_pages[i].page_extra += 4;
				} 
				else 
				{
					mT2p->tiff_transferfunctioncount = 1;
					mT2p->tiff_pages[i].page_extra += 2;
				}
			} 
			else 
			{
				mT2p->tiff_transferfunctioncount=0;
			}
			if(TIFFGetField(
				mT2p->input, 
				TIFFTAG_ICCPROFILE, 
				&(mT2p->tiff_iccprofilelength), 
				&(mT2p->tiff_iccprofile)) != 0)
			{
				mT2p->tiff_pages[i].page_extra++;
			}
			mT2p->tiff_tiles[i].tiles_tilecount = mT2p->tiff_pages[i].page_tilecount;
			if( (TIFFGetField(mT2p->input, TIFFTAG_PLANARCONFIG, &xuint16) != 0)
				&& (xuint16 == PLANARCONFIG_SEPARATE ) )
			{
					TIFFGetField(mT2p->input, TIFFTAG_SAMPLESPERPIXEL, &xuint16);
					mT2p->tiff_tiles[i].tiles_tilecount/= xuint16;
			}
			if( mT2p->tiff_tiles[i].tiles_tilecount > 0)
			{
				TIFFGetField(mT2p->input, 
					TIFFTAG_TILEWIDTH, 
					&(mT2p->tiff_tiles[i].tiles_tilewidth));
				TIFFGetField(mT2p->input, 
					TIFFTAG_TILELENGTH, 
					&(mT2p->tiff_tiles[i].tiles_tilelength) );
				mT2p->tiff_tiles[i].tiles_tiles = 
				(T2P_TILE*) _TIFFmalloc(
					mT2p->tiff_tiles[i].tiles_tilecount 
					* sizeof(T2P_TILE) );
				if(!mT2p->tiff_tiles[i].tiles_tiles)
				{

					TRACE_LOG2("Can't allocate %u bytes of memory for tiles, %s",
								mT2p->tiff_tiles[i].tiles_tilecount * sizeof(T2P_TILE),
								mT2p->inputFilePath.c_str());
					status = PDFHummus::eFailure;
					break;
				}
			}		
		}

	}while(false);
	return status;
}

EStatusCode TIFFImageHandler::ReadTIFFPageInformation() //t2p_read_tiff_data
{
	EStatusCode status = PDFHummus::eSuccess;
	uint16 xuint16;
	uint16* xuint16p;
	float* xfloatp;

	mT2p->pdf_transcode = T2P_TRANSCODE_ENCODE;
	TIFFSetDirectory(mT2p->input, mT2p->tiff_pages[mT2p->pdf_page].page_directory);
	TIFFGetField(mT2p->input, TIFFTAG_IMAGEWIDTH, &(mT2p->tiff_width));

	do
	{
		TIFFGetField(mT2p->input, TIFFTAG_IMAGEWIDTH, &(mT2p->tiff_width));
		if(0 == mT2p->tiff_width)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with zero width",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}

		TIFFGetField(mT2p->input, TIFFTAG_IMAGELENGTH, &(mT2p->tiff_length));
		if(0 == mT2p->tiff_length)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with zero length",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}

        if(TIFFGetField(mT2p->input, TIFFTAG_COMPRESSION, &mT2p->tiff_compression) == 0)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with no compression tag",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;

        }

        if( TIFFIsCODECConfigured(mT2p->tiff_compression) == 0)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with compression type %u:  not configured", 
				mT2p->inputFilePath.c_str(),
				mT2p->tiff_compression);				
			status = PDFHummus::eFailure;
			break;
		}

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_BITSPERSAMPLE, &(mT2p->tiff_bitspersample));
		switch(mT2p->tiff_bitspersample)
		{
			case 1:
			case 2:
			case 4:
			case 8:
				break;
			case 0:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation: Image %s has 0 bits per sample, assuming 1", 
					mT2p->inputFilePath.c_str());				
				mT2p->tiff_bitspersample = 1;
				break;
			default:
				TRACE_LOG2(
					"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with %u bits per sample", 
					mT2p->inputFilePath.c_str(),
					mT2p->tiff_bitspersample);				
				status = PDFHummus::eFailure;
				break;
		}
		if(status != PDFHummus::eSuccess)
			break;

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_SAMPLESPERPIXEL, &(mT2p->tiff_samplesperpixel));
		if(mT2p->tiff_samplesperpixel>4)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with %u samples per pixel", 
				mT2p->inputFilePath.c_str(),
				mT2p->tiff_samplesperpixel);				
			status = PDFHummus::eFailure;
			break;
		}

		if(mT2p->tiff_samplesperpixel==0)
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation: Image %s has 0 samples per pixel, assuming 1", 
				mT2p->inputFilePath.c_str());	
			mT2p->tiff_samplesperpixel=1;
		}

		if(TIFFGetField(mT2p->input, TIFFTAG_SAMPLEFORMAT, &xuint16) != 0 )
		{
			switch(xuint16)
			{
				case 0:
				case 1:
				case 4:
					break;
				default:
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with sample format %u", 
						mT2p->inputFilePath.c_str(),
						xuint16);				
					status = PDFHummus::eFailure;
					break;
			}
		}
		if(status != PDFHummus::eSuccess)
				break;

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_FILLORDER, &(mT2p->tiff_fillorder));
        if(TIFFGetField(mT2p->input, TIFFTAG_PHOTOMETRIC, &(mT2p->tiff_photometric)) == 0)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with no photometric interpretation tag",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
        }

		switch(mT2p->tiff_photometric)
		{
			case PHOTOMETRIC_MINISWHITE:
			case PHOTOMETRIC_MINISBLACK: 
				if (mT2p->tiff_bitspersample==1){
					mT2p->pdf_colorspace=T2P_CS_BILEVEL;
					if(mT2p->tiff_photometric==PHOTOMETRIC_MINISWHITE){
						mT2p->pdf_switchdecode ^= 1;
					}
				} else {
					mT2p->pdf_colorspace=T2P_CS_GRAY;
					if(mT2p->tiff_photometric==PHOTOMETRIC_MINISWHITE){
						mT2p->pdf_switchdecode ^= 1;
					} 
				}
				break;
			case PHOTOMETRIC_RGB: 
				mT2p->pdf_colorspace=T2P_CS_RGB;
				if(mT2p->tiff_samplesperpixel == 3)
				{
					break;
				}
				if(TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16))
				{
					if(xuint16==1)
						ReadPhotometricPalette();
				}
				if(mT2p->tiff_samplesperpixel > 3) 
				{
					if(mT2p->tiff_samplesperpixel == 4) 
					{
						mT2p->pdf_colorspace = T2P_CS_RGB;
						if(TIFFGetField(mT2p->input,
								TIFFTAG_EXTRASAMPLES,
								&xuint16, &xuint16p)
						   && xuint16 == 1) 
						{
							if(xuint16p[0] == EXTRASAMPLE_ASSOCALPHA)
							{
								mT2p->pdf_sample=T2P_SAMPLE_RGBAA_TO_RGB;
								break;
							}
							if(xuint16p[0] == EXTRASAMPLE_UNASSALPHA)
							{
								mT2p->pdf_sample=T2P_SAMPLE_RGBA_TO_RGB;
								break;
							}
							TRACE_LOG1(
								"TIFFImageHandler::ReadTIFFPageInformation, RGB image %s has 4 samples per pixel, assuming RGBA",
								mT2p->inputFilePath.c_str());
							break;
						}
						mT2p->pdf_colorspace=T2P_CS_CMYK;
						mT2p->pdf_switchdecode ^= 1;
						TRACE_LOG1(
							"TIFFImageHandler::ReadTIFFPageInformation, RGB image %s has 4 samples per pixel, assuming CMYK",
							mT2p->inputFilePath.c_str());
						break;
					} 
					else 
					{
						TRACE_LOG2(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for RGB image %s with %u samples per pixel",
							mT2p->inputFilePath.c_str(),
							mT2p->tiff_samplesperpixel
							);
						status = PDFHummus::eFailure;
						break;
					}
				} else {
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for RGB image %s with %u samples per pixel",
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_samplesperpixel
						);
					status = PDFHummus::eFailure;
					break;
				}
			case PHOTOMETRIC_PALETTE: 
				status = ReadPhotometricPalette();
				break;
			case PHOTOMETRIC_SEPARATED:
				if(TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16))
				{
					if(xuint16==1)
						ReadPhotometricPaletteCMYK();
				}
				if( TIFFGetField(mT2p->input, TIFFTAG_INKSET, &xuint16) )
				{
					if(xuint16 != INKSET_CMYK)
					{
						TRACE_LOG1(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for %s because its inkset is not CMYK",
							mT2p->inputFilePath.c_str()
							);
						status = PDFHummus::eFailure;
						break;
					}
				}
				if(mT2p->tiff_samplesperpixel==4)
				{
					mT2p->pdf_colorspace=T2P_CS_CMYK;
				} 
				else 
				{
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for %s because it has %u samples per pixel",
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_samplesperpixel
						);
					status = PDFHummus::eFailure;
					break;
				}
				break;
			case PHOTOMETRIC_YCBCR:
				mT2p->pdf_colorspace=T2P_CS_RGB;
				if(mT2p->tiff_samplesperpixel==1)
				{
					mT2p->pdf_colorspace=T2P_CS_GRAY;
					mT2p->tiff_photometric=PHOTOMETRIC_MINISBLACK;
					break;
				}
				mT2p->pdf_sample=T2P_SAMPLE_YCBCR_TO_RGB;
				break;
			case PHOTOMETRIC_CIELAB:
				mT2p->pdf_labrange[0]= -127;
				mT2p->pdf_labrange[1]= 127;
				mT2p->pdf_labrange[2]= -127;
				mT2p->pdf_labrange[3]= 127;
				mT2p->pdf_sample=T2P_SAMPLE_LAB_SIGNED_TO_UNSIGNED;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_ICCLAB:
				mT2p->pdf_labrange[0]= 0;
				mT2p->pdf_labrange[1]= 255;
				mT2p->pdf_labrange[2]= 0;
				mT2p->pdf_labrange[3]= 255;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_ITULAB:
				mT2p->pdf_labrange[0]=-85;
				mT2p->pdf_labrange[1]=85;
				mT2p->pdf_labrange[2]=-75;
				mT2p->pdf_labrange[3]=124;
				mT2p->pdf_sample=T2P_SAMPLE_LAB_SIGNED_TO_UNSIGNED;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_LOGL:
			case PHOTOMETRIC_LOGLUV:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with photometric interpretation LogL/LogLuv",
					mT2p->inputFilePath.c_str()
					);
				status = PDFHummus::eFailure;
				break;
			default:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with photometric interpretation %u",
					mT2p->inputFilePath.c_str()
					);
				status = PDFHummus::eFailure;
				break;
		}
		if(status != PDFHummus::eSuccess)
			break;

		if(TIFFGetField(mT2p->input, TIFFTAG_PLANARCONFIG, &(mT2p->tiff_planar)))
		{
			switch(mT2p->tiff_planar)
			{
				case 0:
					TRACE_LOG1(
						"TIFFImageHandler::ReadTIFFPageInformation, Image %s has planar configuration 0, assuming 1", 
						mT2p->inputFilePath.c_str());
					mT2p->tiff_planar=PLANARCONFIG_CONTIG;
				case PLANARCONFIG_CONTIG:
					break;
				case PLANARCONFIG_SEPARATE:
					mT2p->pdf_sample=T2P_SAMPLE_PLANAR_SEPARATE_TO_CONTIG;
					if(mT2p->tiff_bitspersample!=8)
					{
						TRACE_LOG2(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with separated planar configuration and %u bits per sample", 
							mT2p->inputFilePath.c_str(),
							mT2p->tiff_bitspersample);
						status = PDFHummus::eFailure;
						break;
					}
					break;
				default:
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with planar configuration %u", 
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_planar);
					status = PDFHummus::eFailure;
					break;
			}
			if(status != PDFHummus::eSuccess)
				break;
		}

        TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_ORIENTATION,
                              &(mT2p->tiff_orientation));
        if(mT2p->tiff_orientation>8)
		{
                TRACE_LOG2("TIFFImageHandler::ReadTIFFPageInformation, Image %s has orientation %u, assuming 0",
                            mT2p->inputFilePath.c_str(), 
							mT2p->tiff_orientation);
                mT2p->tiff_orientation=0;
        }

        if(TIFFGetField(mT2p->input, TIFFTAG_XRESOLUTION, &(mT2p->tiff_xres) ) == 0)
		{
                mT2p->tiff_xres=0.0;
        }
        if(TIFFGetField(mT2p->input, TIFFTAG_YRESOLUTION, &(mT2p->tiff_yres) ) == 0)
		{
                mT2p->tiff_yres=0.0;
        }
		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_RESOLUTIONUNIT,&(mT2p->tiff_resunit));
		if(mT2p->tiff_resunit == RESUNIT_CENTIMETER) 
		{
			mT2p->tiff_xres *= 2.54F;
			mT2p->tiff_yres *= 2.54F;
		} 
		else if (mT2p->tiff_resunit != RESUNIT_INCH && mT2p->pdf_centimeters != 0) 
		{
			mT2p->tiff_xres *= 2.54F;
			mT2p->tiff_yres *= 2.54F;
		}

		ComposePDFPage();

		mT2p->pdf_transcode = T2P_TRANSCODE_ENCODE;
		if(mT2p->pdf_nopassthrough==0)
		{
			if(mT2p->tiff_compression==COMPRESSION_CCITTFAX4)
			{
				if(TIFFIsTiled(mT2p->input) || (TIFFNumberOfStrips(mT2p->input)==1) )
				{
					mT2p->pdf_transcode = T2P_TRANSCODE_RAW;
					mT2p->pdf_compression=T2P_COMPRESS_G4;
				}
			}
			if(mT2p->tiff_compression== COMPRESSION_ADOBE_DEFLATE 
				|| mT2p->tiff_compression==COMPRESSION_DEFLATE)
			{
				if(TIFFIsTiled(mT2p->input) || (TIFFNumberOfStrips(mT2p->input)==1) )
				{
					mT2p->pdf_transcode = T2P_TRANSCODE_RAW;
					mT2p->pdf_compression=T2P_COMPRESS_ZIP;
				}
			}
		}

		if(mT2p->pdf_transcode!=T2P_TRANSCODE_RAW)
		{
			mT2p->pdf_compression = mT2p->pdf_defaultcompression;
		}

		if(mT2p->pdf_sample & T2P_SAMPLE_REALIZE_PALETTE)
		{
			if(mT2p->pdf_colorspace & T2P_CS_CMYK)
			{
				mT2p->tiff_samplesperpixel=4;
				mT2p->tiff_photometric=PHOTOMETRIC_SEPARATED;
			} 
			else 
			{
				mT2p->tiff_samplesperpixel=3;
				mT2p->tiff_photometric=PHOTOMETRIC_RGB;
			}
		}

		if (TIFFGetField(mT2p->input, TIFFTAG_TRANSFERFUNCTION,
				 &(mT2p->tiff_transferfunction[0]),
				 &(mT2p->tiff_transferfunction[1]),
				 &(mT2p->tiff_transferfunction[2]))) 
		{
			if(mT2p->tiff_transferfunction[1] !=
			   mT2p->tiff_transferfunction[0]) 
			{
				mT2p->tiff_transferfunctioncount=3;
			} 
			else 
			{
				mT2p->tiff_transferfunctioncount=1;
			}
		} 
		else 
		{
			mT2p->tiff_transferfunctioncount=0;
		}
		if(TIFFGetField(mT2p->input, TIFFTAG_WHITEPOINT, &xfloatp)!=0)
		{
			mT2p->tiff_whitechromaticities[0]=xfloatp[0];
			mT2p->tiff_whitechromaticities[1]=xfloatp[1];
			if(mT2p->pdf_colorspace & T2P_CS_GRAY)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALGRAY);
			}
			if(mT2p->pdf_colorspace & T2P_CS_RGB)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALRGB);
			}
		}
		if(TIFFGetField(mT2p->input, TIFFTAG_PRIMARYCHROMATICITIES, &xfloatp)!=0)
		{
			mT2p->tiff_primarychromaticities[0]=xfloatp[0];
			mT2p->tiff_primarychromaticities[1]=xfloatp[1];
			mT2p->tiff_primarychromaticities[2]=xfloatp[2];
			mT2p->tiff_primarychromaticities[3]=xfloatp[3];
			mT2p->tiff_primarychromaticities[4]=xfloatp[4];
			mT2p->tiff_primarychromaticities[5]=xfloatp[5];
			if(mT2p->pdf_colorspace & T2P_CS_RGB)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALRGB);
			}
		}
		if(mT2p->pdf_colorspace & T2P_CS_LAB)
		{
			if(TIFFGetField(mT2p->input, TIFFTAG_WHITEPOINT, &xfloatp) != 0)
			{
				mT2p->tiff_whitechromaticities[0]=xfloatp[0];
				mT2p->tiff_whitechromaticities[1]=xfloatp[1];
			} 
			else 
			{
				mT2p->tiff_whitechromaticities[0]=0.3457F; /* 0.3127F; */
				mT2p->tiff_whitechromaticities[1]=0.3585F; /* 0.3290F; */
			}
		}
		if(TIFFGetField(mT2p->input, 
			TIFFTAG_ICCPROFILE, 
			&(mT2p->tiff_iccprofilelength), 
			&(mT2p->tiff_iccprofile))!=0){
			mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_ICCBASED);
		} 
		else 
		{
			mT2p->tiff_iccprofilelength=0;
			mT2p->tiff_iccprofile=NULL;
		}

		if( mT2p->tiff_bitspersample==1 &&
			mT2p->tiff_samplesperpixel==1)
		{
			mT2p->pdf_compression = T2P_COMPRESS_G4;
		}
	}while(false);

	return status;
}

EStatusCode TIFFImageHandler::ReadPhotometricPalette()
{
	EStatusCode status = PDFHummus::eSuccess;
	uint16* r;
	uint16* g;
	uint16* b;

	do
	{
		if(mT2p->tiff_samplesperpixel!=1)
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation, No support for palettized image %s with not one sample per pixel",
				mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}
		mT2p->pdf_colorspace= (t2p_cs_t)(T2P_CS_RGB | T2P_CS_PALETTE);
		mT2p->pdf_palettesize=0x0001<<mT2p->tiff_bitspersample;
		if(!TIFFGetField(mT2p->input, TIFFTAG_COLORMAP, &r, &g, &b))
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation, Palettized image %s has no color map",
				mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		} 
		if(mT2p->pdf_palette != NULL)
		{
			_TIFFfree(mT2p->pdf_palette);
			mT2p->pdf_palette=NULL;
		}
		mT2p->pdf_palette = (unsigned char*)
			_TIFFmalloc(mT2p->pdf_palettesize*3);
		if(mT2p->pdf_palette==NULL)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation, Can't allocate %u bytes of memory for t2p_read_tiff_image, %s",
				mT2p->pdf_palettesize,
				mT2p->inputFilePath.c_str()
				);
			status = PDFHummus::eFailure;
			break;
		}
		for(int i=0;i<mT2p->pdf_palettesize;i++)
		{
			mT2p->pdf_palette[(i*3)]  = (unsigned char) (r[i]>>8);
			mT2p->pdf_palette[(i*3)+1]= (unsigned char) (g[i]>>8);
			mT2p->pdf_palette[(i*3)+2]= (unsigned char) (b[i]>>8);
		}
		mT2p->pdf_palettesize *= 3;
	}while(false);

	return status;
}

EStatusCode TIFFImageHandler::ReadPhotometricPaletteCMYK()
{
	EStatusCode status = PDFHummus::eSuccess;
	uint16* r;
	uint16* g;
	uint16* b;
	uint16* a;

	do
	{
		if(mT2p->tiff_samplesperpixel!=1)
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation, No support for palettized CMYK image %s with not one sample per pixel",
				mT2p->inputFilePath.c_str()
				);
			status = PDFHummus::eFailure;
			break;
		}
		mT2p->pdf_colorspace=t2p_cs_t(T2P_CS_CMYK | T2P_CS_PALETTE);
		mT2p->pdf_palettesize=0x0001<<mT2p->tiff_bitspersample;
		if(!TIFFGetField(mT2p->input, TIFFTAG_COLORMAP, &r, &g, &b, &a))
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation, Palettized image %s has no color map",
				mT2p->inputFilePath.c_str()
				);
			status = PDFHummus::eFailure;
			break;
		} 
		if(mT2p->pdf_palette != NULL)
		{
			_TIFFfree(mT2p->pdf_palette);
			mT2p->pdf_palette=NULL;
		}
		mT2p->pdf_palette = (unsigned char*) 
			_TIFFmalloc(mT2p->pdf_palettesize*4);
		if(mT2p->pdf_palette==NULL)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation, Can't allocate %u bytes of memory for t2p_read_tiff_image, %s",
				mT2p->pdf_palettesize, 
				mT2p->inputFilePath.c_str()
				);
			status = PDFHummus::eFailure;
			break;
		}
		for(int i=0;i<mT2p->pdf_palettesize;i++)
		{
			mT2p->pdf_palette[(i*4)]  = (unsigned char) (r[i]>>8);
			mT2p->pdf_palette[(i*4)+1]= (unsigned char) (g[i]>>8);
			mT2p->pdf_palette[(i*4)+2]= (unsigned char) (b[i]>>8);
			mT2p->pdf_palette[(i*4)+3]= (unsigned char) (a[i]>>8);
		}
		mT2p->pdf_palettesize *= 4;
	}while(false);

	return status;
}

void TIFFImageHandler::CalculateTiffTileSize(int inTileIndex)
{
	uint16 edge=0;

	edge |= (uint16)TileIsRightEdge(inTileIndex);
	edge |= (uint16)TileIsBottomEdge(inTileIndex);

	if(mT2p->pdf_transcode==T2P_TRANSCODE_RAW)
	{
		if(edge)
		{
			mT2p->tiff_datasize=TIFFTileSize(mT2p->input);
		} else 
		{
			// 	TIFFTAG_TILEBYTECOUNTS changed in tiff 4.0.0;

			tsize_t_compat* tbc = NULL;
			TIFFGetField(mT2p->input, TIFFTAG_TILEBYTECOUNTS, &tbc);
			mT2p->tiff_datasize=static_cast<tsize_t>(tbc[inTileIndex]);
		}
	}
	else
	{
		mT2p->tiff_datasize=TIFFTileSize(mT2p->input);
		if(mT2p->tiff_planar==PLANARCONFIG_SEPARATE)
			mT2p->tiff_datasize*= mT2p->tiff_samplesperpixel;
	}
}

void TIFFImageHandler::CalculateTiffSizeNoTiles()
{
	if(mT2p->pdf_transcode == T2P_TRANSCODE_RAW && 
		(mT2p->pdf_compression == T2P_COMPRESS_G4 || mT2p->pdf_compression == T2P_COMPRESS_ZIP))
	{
		// TIFFTAG_STRIPBYTECOUNTS size changed in tiff 4.0.0
		tsize_t_compat * sbc = NULL;
		TIFFGetField(mT2p->input, TIFFTAG_STRIPBYTECOUNTS, &sbc);
		mT2p->tiff_datasize = static_cast<tsize_t>(sbc[0]);
	}
	else
	{
		mT2p->tiff_datasize=TIFFScanlineSize(mT2p->input) * mT2p->tiff_length;
		if(mT2p->tiff_planar==PLANARCONFIG_SEPARATE)
			mT2p->tiff_datasize*= mT2p->tiff_samplesperpixel;
	}
}

bool FilmScan::SourceTIFF(const std::string filename)
{
	// verify the file is a TIFF and get the width and height from the header
	InStream img;

	TIFF *tif;

	if((tif = TIFFOpen(filename.c_str(), "r")) == NULL)
	{
		QString msg;
		int err;

		msg += QString("FilmScan: Cannot open ");
		msg += QString(filename.c_str());
		msg += QString("\n");
		if((err=errno)!=0) msg += QString(strerror(err));
		throw AeoException(msg);
	}

	// just grab the width and height from the header
	uint32 w, h;
	if(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w) != 1 ||
			TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid image size specification in TIFF.");
	}

	TIFFClose(tif);

	this->width = w;
	this->height = h;
	this->srcFormat = SOURCE_TIFF;

	this->TimeCode = "00:00:00:00";
	SourceIdentifyImageSet(filename);

	return true;
}

unsigned char *ReadFrameTIFF_ImageData(const char *fn, unsigned char *buf,
		int &width, int &height, bool &endian,
		GLenum &pix_fmt, int &num_components)
{
	TIFF* tif = TIFFOpen(fn, "r");

	if(tif == NULL)
	{
		QString msg;
		msg += "ReadFrameTIFF: Cannot open ";
		msg += fn;
		msg += "\n";
		if(errno)
			msg += strerror(errno);
		throw AeoException(msg);
	}

	if(TIFFIsTiled(tif))
	{
		TIFFClose(tif);
		throw AeoException("Tiled TIFF not supported.");
	}

	uint16 bitsPerSample[3];
	uint16 bitDepth;
	uint16 numChannels;
	uint32 imageHeight;
	uint32 imageWidth;
	uint16 planarConfig;
	uint16 sampleFormat(0);
	uint16 photometric;

	if(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &numChannels) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid TIFF: no SAMPLESPERPIXEL tag");
	}

	if(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric) != 1)
		photometric = (numChannels==1)?PHOTOMETRIC_MINISBLACK:PHOTOMETRIC_RGB;
	else if(!(numChannels==1 ||
			(numChannels==3 && photometric == PHOTOMETRIC_RGB)))
	{
		TIFFClose(tif);
		throw AeoException("TIF must be either RGB or grayscale.");
	}

	if(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &imageWidth) != 1 ||
			TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &imageHeight) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid TIFF image size");
	}

	// libtiff docs say this returns 1 value, but other sources say it
	// returns <numChannels> values, so just to be safe, we allow it to
	// return up to 3 values and just use the first one.
	if(TIFFGetFieldDefaulted(tif, TIFFTAG_BITSPERSAMPLE, bitsPerSample) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid TIFF: no bitsPerSample tag");
	}

	bitDepth = bitsPerSample[0];

	if(bitDepth != 8 && bitDepth != 16)
	{
		TIFFClose(tif);
		throw AeoException("TIFF must be 8-bit or 16-bit unsigned int.");
	}

	if(TIFFGetFieldDefaulted(tif, TIFFTAG_PLANARCONFIG, &planarConfig) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid TIFF: no planarConfig tag");
	}

	if(TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat) != 1)
	{
		TIFFClose(tif);
		throw AeoException("Invalid TIFF: no sampleFormat (e.g., uint) tag.");
	}

	if(sampleFormat != SAMPLEFORMAT_UINT)
	{
		TIFFClose(tif);
		throw AeoException("TIFF pixel datatype is not unsigned int.");
	}

	tdata_t tbuf;
	uint32 row;

	tbuf = _TIFFmalloc(TIFFScanlineSize(tif));
	if((unsigned char *)tbuf == NULL)
	{
		TIFFClose(tif);
		throw AeoException("Out of memory: TIFF scanline buffer.");
	}

	if(buf == NULL)
	{
		buf = new unsigned char[
				imageWidth * imageHeight * numChannels * (bitDepth/8u)];
		if(buf==NULL)
		{
			_TIFFfree(tbuf);
			TIFFClose(tif);
			throw AeoException("Out of memory: TIFF image buffer.");
		}
	}

	switch(planarConfig)
	{
	case PLANARCONFIG_CONTIG:
		for (row = 0; row < imageHeight; row++)
		{
			if(TIFFReadScanline(tif, tbuf, row) != 1)
			{
				_TIFFfree(tbuf);
				TIFFClose(tif);
				throw AeoException("TIFF I/O Error.");
			}
			memcpy(buf+row*imageWidth*numChannels*(bitDepth/8u), tbuf,
					imageWidth*numChannels*(bitDepth/8u));
		}
		break;
	case PLANARCONFIG_SEPARATE:
		uint16 s, col;

		for (s = 0; s < numChannels; s++)
		{
			for (row = 0; row < imageHeight; row++)
			{
				if(TIFFReadScanline(tif, tbuf, row, s) != 1)
				{
					_TIFFfree(tbuf);
					TIFFClose(tif);
					throw AeoException("TIFF I/O Error.");
				}
				for(col = 0; col < imageWidth; col++)
				{
					memcpy(buf+(bitDepth/8u)*(s+numChannels*(col+imageWidth)),
							tbuf, (bitDepth/8u));
				}
			}
		}
		break;
	default:
		_TIFFfree(tbuf);
		TIFFClose(tif);
		throw AeoException("Invalid Planar Config in TIFF.");
	}

	_TIFFfree(tbuf);
	TIFFClose(tif);

	width = imageWidth;
	height = imageHeight;
	num_components = numChannels;

	if(bitDepth==8) pix_fmt = GL_UNSIGNED_BYTE;
	else pix_fmt = GL_UNSIGNED_SHORT;

	endian = false; // libtiff automatically converts to native endianness

	return buf;
}

bool
tiffns::tiff_to_png(const char *tiffname, FILE *pngfp)
{
    bool bigendian = test_bigendian();

    TIFF *tif = TIFFOpen(tiffname, "r");
    if (!tif) {
        err_printf("Failed to open %s.", tiffname);
        return (false);
    }

    png_struct *png_ptr = png_create_write_struct(PNG_LIBPNG_VER_STRING,
        &jmpbuf_struct, error_handler, 0);
    if (!png_ptr) {
        TIFFClose(tif);
        return (false);
    }

    png_info *info_ptr = png_create_info_struct(png_ptr);
    if (!info_ptr) {
        png_destroy_write_struct(&png_ptr, 0);
        TIFFClose(tif);
        return (false);
    }

    if (setjmp(jmpbuf_struct.jmpbuf)) {
        png_destroy_write_struct(&png_ptr, 0);
        TIFFClose(tif);
        return (false);
    }

    png_init_io(png_ptr, pngfp);

#ifdef DEBUG
    if (verbose) {
        int byteswapped = TIFFIsByteSwapped(tif);

        fprintf(stderr, "tiff_to_png:  ");
        TIFFPrintDirectory(tif, stderr, TIFFPRINT_NONE);
        fprintf(stderr, "tiff_to_png:  byte order = %s\n",
            ((bigendian && byteswapped) || (!bigendian && !byteswapped))?
            "little-endian (Intel)" : "big-endian (Motorola)");
        fprintf(stderr, "tiff_to_png:  this machine is %s-endian\n",
            bigendian? "big" : "little");
    }
#endif

    unsigned short photometric;
    if (!TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric)) {
        err_printf("TIFF photometric could not be retrieved (%s)", tiffname);
        png_destroy_write_struct(&png_ptr, &info_ptr);
        TIFFClose(tif);
        return (false);
    }
    unsigned short bps;
    if (!TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bps))
        bps = 1;
    unsigned short spp;
    if (!TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp))
        spp = 1;
    unsigned short planar;
    if (!TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planar))
        planar = 1;

    unsigned short tiled = TIFFIsTiled(tif);

    int cols, rows;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &cols);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &rows);
    png_uint_32 width = cols;

    bool have_res = false;
    float xres, yres;
    png_uint_32 res_x=0, res_y=0;
    int unit_type = 0;
    if (TIFFGetField(tif, TIFFTAG_XRESOLUTION, &xres) &&
            TIFFGetField(tif, TIFFTAG_YRESOLUTION, &yres) && 
            (xres != 0.0) && (yres != 0.0)) {
        uint16_t resunit;
        have_res = true;
#ifdef DEBUG
        if (verbose) {
            float ratio = xres / yres;
            fprintf(stderr,
                "tiff_to_png:  aspect ratio (hor/vert) = %g (%g / %g)\n",
                ratio, xres, yres);
            if (0.95 < ratio && ratio < 1.05)
                fprintf(stderr, "tiff2png:  near-unity aspect ratio\n");
            else if (1.90 < ratio && ratio < 2.10)
                fprintf(stderr, "tiff2png:  near-2X aspect ratio\n");
            else
                fprintf(stderr, "tiff2png:  non-square, non-2X pixels\n");
        }
#endif

        if (!TIFFGetField(tif, TIFFTAG_RESOLUTIONUNIT, &resunit))
            resunit = RESUNIT_INCH;

        // Convert from TIFF data (floats) to PNG data (unsigned longs).
        switch (resunit) {
        case RESUNIT_CENTIMETER:
            res_x = (png_uint_32)(100.0*xres + 0.5);
            res_y = (png_uint_32)(100.0*yres + 0.5);
            unit_type = PNG_RESOLUTION_METER;
            break;
        case RESUNIT_INCH:
            res_x = (png_uint_32)(39.37*xres + 0.5);
            res_y = (png_uint_32)(39.37*yres + 0.5);
            unit_type = PNG_RESOLUTION_METER;
            break;
        case RESUNIT_NONE:
        default:
            res_x = (png_uint_32)(100.0*xres + 0.5);
            res_y = (png_uint_32)(100.0*yres + 0.5);
            unit_type = PNG_RESOLUTION_UNKNOWN;
            break;
        }
    }

#ifdef DEBUG
    if (verbose) {
        fprintf(stderr, "tiff_to_png:  %dx%dx%d image\n", cols, rows,
            bps * spp);
        fprintf(stderr, "tiff_to_png:  %d bit%s/sample, %d sample%s/pixel\n",
            bps, bps == 1? "" : "s", spp, spp == 1? "" : "s");
    }
#endif

    // Detect tiff filetype.

    int maxval = (1 << bps) - 1;
#ifdef DEBUG
    if (verbose)
        fprintf(stderr, "tiff_to_png:  maxval=%d\n", maxval);
#endif

    int color_type = -1;
    int bit_depth = 0;
    int colors = 0;
    png_color palette[MAXCOLORS];
    unsigned short tiff_compression_method;

    switch (photometric) {
    case PHOTOMETRIC_MINISWHITE:
    case PHOTOMETRIC_MINISBLACK:
#ifdef DEBUG
        if (verbose) {
            fprintf(stderr,
                "tiff_to_png:  %d graylevels (min = %s)\n", maxval + 1,
                photometric == PHOTOMETRIC_MINISBLACK? "black" : "white");
        }
#endif
        if (spp == 1) {
            // no alpha
            color_type = PNG_COLOR_TYPE_GRAY;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr, "tiff_to_png:  color type = grayscale\n");
#endif
            bit_depth = bps;
        }
        else {
            // must be alpha
            color_type = PNG_COLOR_TYPE_GRAY_ALPHA;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr,
                    "tiff_to_png:  color type = grayscale + alpha\n");
#endif
            if (bps <= 8)
                bit_depth = 8;
            else
                bit_depth = bps;
        }
        break;

    case PHOTOMETRIC_PALETTE:
        {
            int palette_8bit; // Set iff all color values in TIFF palette
                              // are < 256.

            color_type = PNG_COLOR_TYPE_PALETTE;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr, "tiff_to_png:  color type = paletted\n");
#endif

            unsigned short *redcolormap;
            unsigned short *greencolormap;
            unsigned short *bluecolormap;
            if (!TIFFGetField(tif, TIFFTAG_COLORMAP, &redcolormap,
                    &greencolormap, &bluecolormap)) {
                err_printf("Cannot retrieve TIFF colormaps (%s)\n", tiffname);
                png_destroy_write_struct(&png_ptr, &info_ptr);
                TIFFClose(tif);
                return (false);
            }
            colors = maxval + 1;
            if (colors > MAXCOLORS) {
                err_printf("Palette too large (%d colors) (%s)\n",
                    colors, tiffname);
                png_destroy_write_struct(&png_ptr, &info_ptr);
                TIFFClose(tif);
                return (false);
            }
            // Max PNG palette-size is 8 bits, you could convert to
            // full-color.
            if (bps >= 8) 
                bit_depth = 8;
            else
                bit_depth = bps;

            // PLTE chunk
            // TIFF palettes contain 16-bit shorts, while PNG palettes
            // are 8-bit.  Some broken (??) software puts 8-bit values
            // in the shorts, which would make the palette come out
            // all zeros, which isn't good.  We check...

            palette_8bit = 1;
            for (int i = 0 ; i < colors ; i++) {
                if (redcolormap[i] > 255 || greencolormap[i] > 255 ||
                         bluecolormap[i] > 255) {
                     palette_8bit = 0;
                     break;
                }
            } 
#ifdef DEBUG
            if (palette_8bit && verbose)
                fprintf(stderr,
                    "tiff_to_png warning:  assuming 8-bit palette values.\n");
#endif

            for (int i = 0 ; i < colors ; i++) {
                if (palette_8bit) {
                    palette[i].red   = (png_byte)redcolormap[i];
                    palette[i].green = (png_byte)greencolormap[i];
                    palette[i].blue  = (png_byte)bluecolormap[i];
                }
                else {
                    palette[i].red   = (png_byte)(redcolormap[i] >> 8);
                    palette[i].green = (png_byte)(greencolormap[i] >> 8);
                    palette[i].blue  = (png_byte)(bluecolormap[i] >> 8);
                }
            }
            break;
        }

    case PHOTOMETRIC_YCBCR:
        TIFFGetField(tif, TIFFTAG_COMPRESSION, &tiff_compression_method);
        if (tiff_compression_method == COMPRESSION_JPEG &&
                planar == PLANARCONFIG_CONTIG) {
            // Can rely on libjpeg to convert to RGB.
            TIFFSetField(tif, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
            photometric = PHOTOMETRIC_RGB;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr, "tiff2png:  original color type = YCbCr "
                    "with JPEG compression.\n");
#endif
        }
        else {
            err_printf(
                "Don't know how to handle PHOTOMETRIC_YCBCR with "
                "compression %d\n"
                "  (%sJPEG) and planar config %d (%scontiguous)\n"
                "  (%s)\n", tiff_compression_method,
                tiff_compression_method == COMPRESSION_JPEG? "" : "not ",
                planar, planar == PLANARCONFIG_CONTIG? "" : "not ", tiffname);
            png_destroy_write_struct(&png_ptr, &info_ptr);
            TIFFClose(tif);
            return (false);
        }
        // fall thru... 

    case PHOTOMETRIC_RGB:
        if (spp == 3) {
            color_type = PNG_COLOR_TYPE_RGB;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr, "tiff_to_png:  color type = truecolor\n");
#endif
        }
        else {
            color_type = PNG_COLOR_TYPE_RGB_ALPHA;
#ifdef DEBUG
            if (verbose)
                fprintf(stderr,
                    "tiff_to_png:  color type = truecolor + alpha\n");
#endif
        }
        if (bps <= 8)
            bit_depth = 8;
        else
            bit_depth = bps;
        break;

    case PHOTOMETRIC_LOGL:
    case PHOTOMETRIC_LOGLUV:
        TIFFGetField(tif, TIFFTAG_COMPRESSION, &tiff_compression_method);
        if (tiff_compression_method != COMPRESSION_SGILOG &&
                tiff_compression_method != COMPRESSION_SGILOG24) {
            err_printf("Don't know how to handle PHOTOMETRIC_LOGL%s with\n"
                "  compression %d (not SGILOG) (%s)\n",
                photometric == PHOTOMETRIC_LOGLUV? "UV" : "",
                tiff_compression_method, tiffname);
            png_destroy_write_struct(&png_ptr, &info_ptr);
            TIFFClose(tif);
            return (false);
        }
        // rely on library to convert to RGB/greyscale.
#ifdef LIBTIFF_HAS_16BIT_INTEGER_FORMAT
        if (bps > 8) {
            // SGILOGDATAFMT_16BIT converts to a floating-point
            // luminance value; U,V are left as such. 
            // SGILOGDATAFMT_16BIT_INT doesn't exist.

            TIFFSetField(tif, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_16BIT_INT);
            bit_depth = bps = 16;
        }
        else
#endif
        {
            // SGILOGDATAFMT_8BIT converts to normal grayscale or RGB format.
            TIFFSetField(tif, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_8BIT);
            bit_depth = bps = 8;
        }
        if (photometric == PHOTOMETRIC_LOGL) {
            photometric = PHOTOMETRIC_MINISBLACK;
            color_type = PNG_COLOR_TYPE_GRAY;
#ifdef DEBUG
            if (verbose) {
                fprintf(stderr, "tiff_to_png:  original color type = "
                    "logL with SGILOG compression.\n");
                fprintf(stderr, "tiff_to_png:  color type = grayscale.\n");
            }
#endif
        }
        else {
            photometric = PHOTOMETRIC_RGB;
            color_type = PNG_COLOR_TYPE_RGB;
#ifdef DEBUG
            if (verbose) {
                fprintf(stderr, "tiff_to_png:  original color type = "
                    "logLUV with SGILOG compression.\n");
                fprintf(stderr, "tiff_to_png:  color type = truecolor.\n");
            }
#endif
        }
        break;

    case PHOTOMETRIC_MASK:
    case PHOTOMETRIC_SEPARATED:
    case PHOTOMETRIC_CIELAB:
    case PHOTOMETRIC_DEPTH:
        err_printf("Don't know how to handle %s (%s)\n",
            photometric == PHOTOMETRIC_MASK?      "PHOTOMETRIC_MASK" :
            photometric == PHOTOMETRIC_SEPARATED? "PHOTOMETRIC_SEPARATED" :
            photometric == PHOTOMETRIC_CIELAB?    "PHOTOMETRIC_CIELAB" :
            photometric == PHOTOMETRIC_DEPTH?     "PHOTOMETRIC_DEPTH" :
                                                  "unknown photometric",
            tiffname);
        png_destroy_write_struct(&png_ptr, &info_ptr);
        TIFFClose(tif);
        return (false);

    default:
        err_printf("Unknown photometric (%d) (%s)\n",
            photometric, tiffname);
        png_destroy_write_struct(&png_ptr, &info_ptr);
        TIFFClose(tif);
        return (false);
    }
    int tiff_color_type = color_type;

#ifdef DEBUG
    if (verbose)
        fprintf(stderr, "tiff_to_png:  bit depth = %d\n", bit_depth);
#endif

    // Put parameter info in png-chunks.

    png_set_IHDR(png_ptr, info_ptr, width, rows, bit_depth, color_type,
        PNG_INTERLACE_NONE, PNG_COMPRESSION_TYPE_DEFAULT,
        PNG_FILTER_TYPE_DEFAULT);

//    if (png_compression_level != -1)
//        png_set_compression_level(png_ptr, png_compression_level);

    if (color_type == PNG_COLOR_TYPE_PALETTE)
        png_set_PLTE(png_ptr, info_ptr, palette, colors);

    // gAMA chunk
//    if (gamma != -1.0) {
#ifdef DEBUG
//        if (verbose)
//            fprintf(stderr, "tiff_to_png:  gamma = %f\n", gamma);
#endif
//        png_set_gAMA(png_ptr, info_ptr, gamma);
//    }

    // pHYs chunk
    if (have_res)
        png_set_pHYs(png_ptr, info_ptr, res_x, res_y, unit_type);

    png_write_info(png_ptr, info_ptr);
    png_set_packing(png_ptr);

    // Allocate space for one line (or row of tiles) of TIFF image.

    unsigned char *tiffline = 0;
    unsigned char *tifftile = 0;
    unsigned char *tiffstrip = 0;
    size_t tilesz = 0L;
    int num_tilesX = 0;
    uint32_t tile_width = 0, tile_height = 0;

    if (!tiled) {
        // strip-based TIFF
        if (planar == 1) {
            // contiguous picture
            tiffline = (unsigned char*)malloc(TIFFScanlineSize(tif));
        }
        else {
            // separated planes
            tiffline = (unsigned char*)malloc(TIFFScanlineSize(tif) * spp);
        }
    }
    else {
        // Allocate space for one "row" of tiles.

        TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tile_width);
        TIFFGetField(tif, TIFFTAG_TILELENGTH, &tile_height);

        num_tilesX = (width+tile_width-1)/tile_width;

        if (planar == 1) {
            tilesz = TIFFTileSize(tif);
            tifftile = new unsigned char[tilesz];
            size_t stripsz = (tile_width*num_tilesX) * tile_height * spp;
            tiffstrip = new unsigned char[stripsz];
            tiffline = tiffstrip;
            // Just set the line to the top of the strip, we'll move it
            // through below.
        }
        else {
            err_printf(
                "Can't handle tiled separated-plane TIFF format (%s).\n",
                tiffname);
            png_destroy_write_struct (&png_ptr, &info_ptr);
            TIFFClose(tif);
            return (false);
        }
    }

    if (!tiffline) {
        err_printf("Can't allocate memory for TIFF scanline buffer (%s).\n",
            tiffname);
        png_destroy_write_struct(&png_ptr, &info_ptr);
        TIFFClose(tif);
        if (tiled && planar == 1)
            delete [] tifftile;
        return (false);
    }

    if (planar != 1) {
        // In case we must combine more planes into one.
        tiffstrip = new unsigned char[TIFFScanlineSize(tif)];
        if (!tiffstrip) {
            err_printf("Can't allocate memory for TIFF strip buffer (%s).\n",
                tiffname);
            png_destroy_write_struct(&png_ptr, &info_ptr);
            TIFFClose(tif);
            free(tiffline);
            return (false);
        }
    }

    // Allocate space for one line of PNG image.
    // max: 3 color channels plus one alpha channel, 16 bit => 8 bytes/pixel

    png_byte *pngline = new unsigned char[cols * 8];
    for (int pass = 0 ; pass < png_set_interlace_handling(png_ptr); pass++) {
        for (int row = 0; row < rows; row++) {
            if (planar == 1) {
                // contiguous picture
                if (!tiled) {
                    if (TIFFReadScanline(tif, tiffline, row, 0) < 0) {
                        err_printf("Bad data read on line %d (%s).\n",
                            row, tiffname);
                        png_destroy_write_struct(&png_ptr, &info_ptr);
                        TIFFClose(tif);
                        free(tiffline);
                        return (false);
                    }
                }
                else {
                    // tiled
                    int col, ok=1, r;
                    int tileno;
                    // Read in one row of tiles and hand out the data
                    // one scanline at a time so the code below
                    // doesn't need to change.

                    // Is it time for a new strip?
                    if ((row % tile_height) == 0) {
                        for (col = 0; ok && col < num_tilesX; col += 1 ) {
                            tileno = col+(row/tile_height)*num_tilesX;
                            // Read the tile into an RGB array.
                            if (!TIFFReadEncodedTile(tif, tileno, tifftile,
                                    tilesz)) {
                                ok = 0;
                                break;
                            }

                            // Copy this tile into the row buffer.
                            for (r = 0; r < (int) tile_height; r++) {
                                void *dest = tiffstrip + (r * tile_width *
                                    num_tilesX * spp) +
                                    (col * tile_width * spp);
                                void *src  = tifftile + (r * tile_width * spp);
                                memcpy(dest, src, (tile_width * spp));
                            }
                        }
                        tiffline = tiffstrip; // Set tileline to top of strip.
                    }
                    else {
                        tiffline = tiffstrip + ((row % tile_height) *
                            ((tile_width * num_tilesX) * spp));
                    }
                }
            }
            else {
                // Separated planes, then combine more strips into one line.
                unsigned short s;

                // XXX:  this assumes strips; are separated-plane
                // tiles possible?

                unsigned char *p_line = tiffline;
                for (int n = 0; n < (cols/8 * bps*spp); n++)
                    *p_line++ = '\0';

                for (s = 0; s < spp; s++) {
                    unsigned char *p_strip = tiffstrip;
                    int getbitsleft = 8;
                    p_line = tiffline;
                    int putbitsleft = 8;

                    if (TIFFReadScanline(tif, tiffstrip, row, s) < 0) {
                        err_printf("Bad data read on line %d (%s).\n",
                            row, tiffname);
                        png_destroy_write_struct(&png_ptr, &info_ptr);
                        TIFFClose(tif);
                        delete [] tiffline;
                        delete [] tiffstrip;
                        return (false);
                    }

                    p_strip = (unsigned char *)tiffstrip;
                    unsigned char sample = '\0';
                    for (int i = 0 ; i < s ; i++)
                        PUT_LINE_SAMPLE
                    for (int n = 0; n < cols; n++) {
                        GET_STRIP_SAMPLE
                        PUT_LINE_SAMPLE
                        sample = '\0';
                        for (int i = 0 ; i < (spp-1) ; i++)
                            PUT_LINE_SAMPLE
                    }
                }
            }

            unsigned char *p_line = tiffline;
            int bitsleft = 8;
            png_byte *p_png = pngline;

            // Convert from tiff-line to png-line.

            switch (tiff_color_type) {
            case PNG_COLOR_TYPE_GRAY:       // we know spp == 1
                for (int col = cols; col > 0; --col) {
                    switch (bps) {
                    case 16:
#ifdef INVERT_MINISWHITE
                        if (photometric == PHOTOMETRIC_MINISWHITE) {
                            unsigned char sample;
                            int sample16;
                            if (bigendian) {
                                // same as PNG order
                                GET_LINE_SAMPLE
                                sample16 = sample;
                                sample16 <<= 8;
                                GET_LINE_SAMPLE
                                sample16 |= sample;
                            }
                            else {
                                // reverse of PNG
                                GET_LINE_SAMPLE
                                sample16 = sample;
                                GET_LINE_SAMPLE
                                sample16 |= (((int)sample) << 8);
                            }
                            sample16 = maxval - sample16;
                            *p_png++ = (unsigned char)((sample16 >> 8) & 0xff);
                            *p_png++ = (unsigned char)(sample16 & 0xff);
                        }
                        else // not PHOTOMETRIC_MINISWHITE
#endif
                        {
                            unsigned char sample;
                            if (bigendian) {
                                GET_LINE_SAMPLE
                                *p_png++ = sample;
                                GET_LINE_SAMPLE
                                *p_png++ = sample;
                            }
                            else {
                                GET_LINE_SAMPLE
                                p_png[1] = sample;
                                GET_LINE_SAMPLE
                                *p_png = sample;
                                p_png += 2;
                            }
                        }
                        break;

                    case 8:
                    case 4:
                    case 2:
                    case 1:
                        {
                            unsigned char sample;
                            GET_LINE_SAMPLE
#ifdef INVERT_MINISWHITE
                            if (photometric == PHOTOMETRIC_MINISWHITE)
                                sample = maxval - sample;
#endif
                            *p_png++ = sample;
                        }
                        break;

                    }
                }
                break;

            case PNG_COLOR_TYPE_GRAY_ALPHA:
                for (int col = 0; col < cols; col++) {
                    for (int i = 0 ; i < spp ; i++) {
                        switch (bps) {
                        case 16:
#ifdef INVERT_MINISWHITE
                            if (photometric == PHOTOMETRIC_MINISWHITE &&
                                    i == 0) {
                                unsigned char sample;
                                int sample16;
                                if (bigendian) {
                                    GET_LINE_SAMPLE
                                    sample16 = (sample << 8);
                                    GET_LINE_SAMPLE
                                    sample16 |= sample;
                                }
                                else {
                                    GET_LINE_SAMPLE
                                    sample16 = sample;
                                    GET_LINE_SAMPLE
                                    sample16 |= (((int)sample) << 8);
                                }
                                sample16 = maxval - sample16;
                                *p_png++ = (unsigned char)(
                                    (sample16 >> 8) & 0xff);
                                *p_png++ = (unsigned char)(sample16 & 0xff);
                            }
                            else
#endif
                            {
                                unsigned char sample;
                                if (bigendian) {
                                    GET_LINE_SAMPLE
                                    *p_png++ = sample;
                                    GET_LINE_SAMPLE
                                    *p_png++ = sample;
                                }
                                else {
                                    GET_LINE_SAMPLE
                                    p_png[1] = sample;
                                    GET_LINE_SAMPLE
                                    *p_png = sample;
                                    p_png += 2;
                                }
                            }
                            break;

                        case 8:
                            {
                                unsigned char sample;
                                GET_LINE_SAMPLE
#ifdef INVERT_MINISWHITE
                                if (photometric == PHOTOMETRIC_MINISWHITE &&
                                        i == 0)
                                    sample = maxval - sample;
#endif
                                *p_png++ = sample;
                            }
                            break;

                        case 4:
                            {
                                unsigned char sample;
                                GET_LINE_SAMPLE
#ifdef INVERT_MINISWHITE
                                if (photometric == PHOTOMETRIC_MINISWHITE &&
                                        i == 0)
                                    sample = maxval - sample;
#endif
                                *p_png++ = sample * 17;   /* was 16 */
                            }
                            break;

                        case 2:
                            {
                                unsigned char sample;
                                GET_LINE_SAMPLE
#ifdef INVERT_MINISWHITE
                                if (photometric == PHOTOMETRIC_MINISWHITE &&
                                        i == 0)
                                    sample = maxval - sample;
#endif
                                *p_png++ = sample * 85;   /* was 64 */
                            }
                            break;

                        case 1:
                            {
                                unsigned char sample;
                                GET_LINE_SAMPLE
#ifdef INVERT_MINISWHITE
                                if (photometric == PHOTOMETRIC_MINISWHITE &&
                                        i == 0)
                                    sample = maxval - sample;
#endif
                                *p_png++ = sample * 255;  /* was 128...oops */
                            }
                            break;

                        }
                    }
                }
                break;

            case PNG_COLOR_TYPE_RGB:
            case PNG_COLOR_TYPE_RGB_ALPHA:
                for (int col = 0; col < cols; col++) {
                    // Process for red, green and blue (and when
                    // applicable alpha).

                    for (int i = 0 ; i < spp ; i++) {
                        switch (bps) {
                        case 16:
                            // XXX:  do we need INVERT_MINISWHITE
                            // support here, too, or is that only for
                            // grayscale?

                            unsigned char sample;
                            if (bigendian) {
                                GET_LINE_SAMPLE
                                *p_png++ = sample;
                                GET_LINE_SAMPLE
                                *p_png++ = sample;
                            }
                            else {
                                GET_LINE_SAMPLE
                                p_png[1] = sample;
                                GET_LINE_SAMPLE
                                *p_png = sample;
                                p_png += 2;
                            }
                            break;

                        case 8:
                            GET_LINE_SAMPLE
                            *p_png++ = sample;
                            break;

                        case 4:
                            GET_LINE_SAMPLE
                            *p_png++ = sample * 17;
                            break;

                        case 2:
                            GET_LINE_SAMPLE
                            *p_png++ = sample * 85;
                            break;

                        case 1:
                            GET_LINE_SAMPLE
                            *p_png++ = sample * 255;
                           break;

                        }
                    }
                }
                break;

            case PNG_COLOR_TYPE_PALETTE:
                for (int col = 0; col < cols; col++) {
                    unsigned char sample;
                    GET_LINE_SAMPLE
                    *p_png++ = sample;
                }
                break;

            default:
                err_printf("Unknown photometric (%d) (%s).\n",
                    photometric, tiffname);
                png_destroy_write_struct(&png_ptr, &info_ptr);
                TIFFClose(tif);
                delete [] tiffline;
                if (tiled && planar == 1)
                    delete [] tifftile;
                else if (planar != 1)
                    delete [] tiffstrip;
                return (false);

            }
            png_write_row(png_ptr, pngline);
        }
    }

    TIFFClose(tif);

    png_write_end(png_ptr, info_ptr);

    png_destroy_write_struct(&png_ptr, &info_ptr);

    free(tiffline);
    if (tiled && planar == 1)
        delete [] tifftile;
    else if (planar != 1)
        delete [] tiffstrip;
    return (true);
}

auto ReadHeader(lt::TIFF* tif)
{
    // Get metadata
    TIFFHeader hdr;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &hdr.width);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &hdr.height);
    TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &hdr.type);
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &hdr.depth);
    TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &hdr.channels);
    TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &hdr.config);
    TIFFGetField(tif, TIFFTAG_COMPRESSION, &hdr.compression);
    TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &hdr.rowsPerStrip);
    TIFFGetField(tif, TIFFTAG_STRIPOFFSETS, &hdr.stripOffsets);
    hdr.bigEndian = lt::TIFFIsBigEndian(tif) != 0;
    return hdr;
}

File(
                    const std::string& fileName,
                    const dtk::InMemoryFile* memory)
                {
                    if (memory)
                    {
                        _memory.p = memory->p;
                        _memory.start = memory->p;
                        _memory.end = memory->p + memory->size;
                        _tiff.p = TIFFClientOpen(
                            fileName.c_str(),
                            "r",
                            &_memory,
                            tiffMemoryRead,
                            tiffMemoryWrite,
                            tiffMemorySeek,
                            tiffMemoryClose,
                            tiffMemorySize,
                            nullptr,
                            nullptr);
                    }
                    else
                    {
#if defined(_WINDOWS)
                        _tiff.p = TIFFOpenW(dtk::toWide(fileName).c_str(), "r");
#else // _WINDOWS
                        _tiff.p = TIFFOpen(fileName.c_str(), "r");
#endif // _WINDOWS
                    }
                    if (!_tiff.p)
                    {
                        throw std::runtime_error(dtk::Format("{0}: Cannot open").arg(fileName));
                    }

                    uint32_t  tiffWidth = 0;
                    uint32_t  tiffHeight = 0;
                    uint16_t  tiffPhotometric = 0;
                    uint16_t  tiffSamples = 0;
                    uint16_t  tiffSampleDepth = 0;
                    uint16_t  tiffSampleFormat = 0;
                    uint16_t* tiffExtraSamples = nullptr;
                    uint16_t  tiffExtraSamplesSize = 0;
                    uint16_t  tiffOrient = 0;
                    uint16_t  tiffCompression = 0;
                    uint16_t  tiffPlanarConfig = 0;
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_IMAGEWIDTH, &tiffWidth);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_IMAGELENGTH, &tiffHeight);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_PHOTOMETRIC, &tiffPhotometric);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_SAMPLESPERPIXEL, &tiffSamples);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_BITSPERSAMPLE, &tiffSampleDepth);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_SAMPLEFORMAT, &tiffSampleFormat);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_EXTRASAMPLES, &tiffExtraSamplesSize, &tiffExtraSamples);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_ORIENTATION, &tiffOrient);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_COMPRESSION, &tiffCompression);
                    TIFFGetFieldDefaulted(_tiff.p, TIFFTAG_PLANARCONFIG, &tiffPlanarConfig);
                    _planar = PLANARCONFIG_SEPARATE == tiffPlanarConfig;
                    _samples = tiffSamples;
                    _sampleDepth = tiffSampleDepth;
                    _scanlineSize = tiffWidth * tiffSamples * tiffSampleDepth / 8;

                    dtk::ImageType pixelType = dtk::ImageType::None;
                    switch (tiffPhotometric)
                    {
                    case PHOTOMETRIC_MINISWHITE:
                    case PHOTOMETRIC_MINISBLACK:
                    case PHOTOMETRIC_RGB:
                        if (32 == tiffSampleDepth && tiffSampleFormat != SAMPLEFORMAT_IEEEFP)
                            break;
                        if (SAMPLEFORMAT_IEEEFP == tiffSampleFormat)
                        {
                            pixelType = io::getFloatType(tiffSamples, tiffSampleDepth);
                        }
                        else
                        {
                            pixelType = io::getIntType(tiffSamples, tiffSampleDepth);
                        }
                        break;
                    }
                    if (dtk::ImageType::None == pixelType)
                    {
                        throw std::runtime_error(dtk::Format("{0}: Cannot open").arg(fileName));
                    }

                    dtk::ImageInfo imageInfo(tiffWidth, tiffHeight, pixelType);
                    imageInfo.layout.mirror.y = true;
                    _info.video.push_back(imageInfo);

                    char* tag = 0;
                    if (TIFFGetField(_tiff.p, TIFFTAG_ARTIST, &tag))
                    {
                        if (tag)
                        {
                            _info.tags["Creator"] = tag;
                        }
                    }
                    if (TIFFGetField(_tiff.p, TIFFTAG_IMAGEDESCRIPTION, &tag))
                    {
                        if (tag)
                        {
                            _info.tags["Description"] = tag;
                        }
                    }
                    if (TIFFGetField(_tiff.p, TIFFTAG_COPYRIGHT, &tag))
                    {
                        if (tag)
                        {
                            _info.tags["Copyright"] = tag;
                        }
                    }
                    if (TIFFGetField(_tiff.p, TIFFTAG_DATETIME, &tag))
                    {
                        if (tag)
                        {
                            _info.tags["Time"] = tag;
                        }
                    }
                }

HANDLE TIFFRGBA2DIB(TIFFDibImage* dib, uint32* raster)
{
    void*   pDIB = 0;
    TIFFRGBAImage* img = &dib->tif;

    uint32 imageLength;
    uint32 imageWidth;
    uint16 BitsPerSample;
    uint16 SamplePerPixel;
    uint32 RowsPerStrip;
    uint16 PhotometricInterpretation;

    BITMAPINFOHEADER   bi;
    int                dwDIBSize ;

    TIFFGetField(img->tif, TIFFTAG_IMAGEWIDTH, &imageWidth);
    TIFFGetField(img->tif, TIFFTAG_IMAGELENGTH, &imageLength);
    TIFFGetField(img->tif, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);
    TIFFGetField(img->tif, TIFFTAG_ROWSPERSTRIP, &RowsPerStrip);
    TIFFGetField(img->tif, TIFFTAG_SAMPLESPERPIXEL, &SamplePerPixel);
    TIFFGetField(img->tif, TIFFTAG_PHOTOMETRIC, &PhotometricInterpretation);

    if ( BitsPerSample == 1 && SamplePerPixel == 1 && dib->dibinstalled ) {   // bilevel
        bi.biSize           = sizeof(BITMAPINFOHEADER);
        bi.biWidth          = imageWidth;
        bi.biHeight         = imageLength;
        bi.biPlanes         = 1;  // always
        bi.biBitCount       = 1;
        bi.biCompression    = BI_RGB;
        bi.biSizeImage      = WIDTHBYTES(bi.biWidth * bi.biBitCount) * bi.biHeight;
        bi.biXPelsPerMeter  = 0;
        bi.biYPelsPerMeter  = 0;
        bi.biClrUsed        = 0;  //  must be zero for RGB compression (none)
        bi.biClrImportant   = 0;  // always

        // Get the size of the DIB
        dwDIBSize = GetDIBSize( &bi );

        // Allocate for the BITMAPINFO structure and the color table.
        pDIB = GlobalAllocPtr( GHND, dwDIBSize );
        if (pDIB == 0) {
            return( NULL );
        }

        // Copy the header info
        *((BITMAPINFOHEADER*)pDIB) = bi;

        // Get a pointer to the color table
        RGBQUAD   *pRgbq = (RGBQUAD   *)((LPSTR)pDIB + sizeof(BITMAPINFOHEADER));

        pRgbq[0].rgbRed      = 0;
        pRgbq[0].rgbBlue     = 0;
        pRgbq[0].rgbGreen    = 0;
        pRgbq[0].rgbReserved = 0;
        pRgbq[1].rgbRed      = 255;
        pRgbq[1].rgbBlue     = 255;
        pRgbq[1].rgbGreen    = 255;
        pRgbq[1].rgbReserved = 255;

        // Pointers to the bits
        //PVOID pbiBits = (LPSTR)pRgbq + bi.biClrUsed * sizeof(RGBQUAD);
        //
        // In the BITMAPINFOHEADER documentation, it appears that
        // there should be no color table for 32 bit images, but
        // experience shows that the image is off by 3 words if it
        // is not included.  So here it is.
        PVOID pbiBits = GetDIBImagePtr((BITMAPINFOHEADER*)pDIB);  //(LPSTR)pRgbq + 3 * sizeof(RGBQUAD);

        int       sizeWords = bi.biSizeImage/4;
        RGBQUAD*  rgbDib = (RGBQUAD*)pbiBits;
        long*     rgbTif = (long*)raster;

        _TIFFmemcpy(pbiBits, raster, bi.biSizeImage);
    }

        //  For now just always default to the RGB 32 bit form.                                                       // save as 32 bit for simplicity
    else if ( true /*BitsPerSample == 8 && SamplePerPixel == 3*/ ) {   // 24 bit color

        bi.biSize           = sizeof(BITMAPINFOHEADER);
        bi.biWidth          = imageWidth;
        bi.biHeight         = imageLength;
        bi.biPlanes         = 1;  // always
        bi.biBitCount       = 32;
        bi.biCompression    = BI_RGB;
        bi.biSizeImage      = WIDTHBYTES(bi.biWidth * bi.biBitCount) * bi.biHeight;
        bi.biXPelsPerMeter  = 0;
        bi.biYPelsPerMeter  = 0;
        bi.biClrUsed        = 0;  //  must be zero for RGB compression (none)
        bi.biClrImportant   = 0;  // always

        // Get the size of the DIB
        dwDIBSize = GetDIBSize( &bi );

        // Allocate for the BITMAPINFO structure and the color table.
        pDIB = GlobalAllocPtr( GHND, dwDIBSize );
        if (pDIB == 0) {
            return( NULL );
        }

        // Copy the header info
        *((BITMAPINFOHEADER*)pDIB) = bi;

        // Get a pointer to the color table
        RGBQUAD   *pRgbq = (RGBQUAD   *)((LPSTR)pDIB + sizeof(BITMAPINFOHEADER));

        // Pointers to the bits
        //PVOID pbiBits = (LPSTR)pRgbq + bi.biClrUsed * sizeof(RGBQUAD);
        //
        // In the BITMAPINFOHEADER documentation, it appears that
        // there should be no color table for 32 bit images, but
        // experience shows that the image is off by 3 words if it
        // is not included.  So here it is.
        PVOID pbiBits = (LPSTR)pRgbq + 3 * sizeof(RGBQUAD);

        int       sizeWords = bi.biSizeImage/4;
        RGBQUAD*  rgbDib = (RGBQUAD*)pbiBits;
        long*     rgbTif = (long*)raster;

        // Swap the byte order while copying
        for ( int i = 0 ; i < sizeWords ; ++i )
        {
            rgbDib[i].rgbRed   = TIFFGetR(rgbTif[i]);
            rgbDib[i].rgbBlue  = TIFFGetB(rgbTif[i]);
            rgbDib[i].rgbGreen = TIFFGetG(rgbTif[i]);
            rgbDib[i].rgbReserved = 0;
        }
    }

    return pDIB;
}

void LibTiffReader::open(std::string filename) {
    if(this->_is_open)
        this->recycle();

    this->_tiff = TIFFOpen(filename.c_str(), "r");
    if(! this->_tiff)
        throw std::runtime_error("[LibTiffReader] Failed to access file: " + filename);
    this->_is_open = true;

    uint16_t channels, depth;
    TIFFGetField(this->_tiff, TIFFTAG_IMAGEWIDTH, &(this->_width));
    TIFFGetField(this->_tiff, TIFFTAG_IMAGELENGTH, &(this->_height));
    TIFFGetField(this->_tiff, TIFFTAG_SAMPLESPERPIXEL, &channels);
    TIFFGetField(this->_tiff, TIFFTAG_BITSPERSAMPLE, &depth);
    this->_channels = channels;
    this->_depth = depth;

    if(channels == 1) {
        uint16_t planar_config = 0xffff;
        TIFFGetField(this->_tiff, TIFFTAG_PLANARCONFIG, &planar_config);
        if(planar_config != PLANARCONFIG_CONTIG)
            throw std::runtime_error("[LibTiffReader] Only 'Chunky' planar config format is supported: " + filename);
    }

    if( 
        _width <= 0 ||
        _height <= 0 ||
        _channels <= 0 ||
        !(_depth == 8 || _depth == 16)
    ) {
        this->recycle();
        throw std::runtime_error("[LibTiffReader] Invalid image dimensions.");
    }
}

cv::Mat LibTiffReader::getCrop(uint32_t left, uint32_t top, uint32_t w, uint32_t h) {
    if( ! this->_is_open )
        throw std::runtime_error("[LibTiffReader] No file opened.");

    int right = left + w;
    int bottom = top + h;

    bool lt_valid = (left >= 0) && (top >= 0) && (left < _width) && (top < _height);
    bool rb_valid = (right > 0) && (bottom > 0) && (right <= _width) && (bottom <= _height);
    if( ! (lt_valid && rb_valid) )
        throw std::runtime_error("[LibTiffReader] Invalid coordinates.");

    int sample_byte_size = this->_depth / 8;

    int cv_depth = this->getCVMatType();
    cv::Mat cropped(h, w, CV_MAKETYPE(cv_depth, _channels));


    uint32_t rows_per_strip = 0;
    TIFFGetField(this->_tiff, TIFFTAG_ROWSPERSTRIP, &rows_per_strip);
    if(rows_per_strip != 1)
        throw std::runtime_error("[LibTiffReader] This tiff was not saved with one row per strip.");


    uint32_t strip_size = TIFFStripSize(this->_tiff);
    if(strip_size != _width * _channels * sample_byte_size)
        throw std::runtime_error("[LibTiffReader] Initial strip size assumption failed.");


	void* row_data = _TIFFmalloc(strip_size);
    if( ! row_data )
        throw std::runtime_error("[LibTiffReader] Memory error.");


	uint32_t row_index;
	uint32_t row_size;
    uint32_t total_strips = TIFFNumberOfStrips(this->_tiff);
    if( total_strips != this->_height)
        throw std::runtime_error("[LibTiffReader] Strip count assumption failed.");


    void* crop_row = (char*)row_data + (left * _channels * sample_byte_size);
    int crop_row_size = w * _channels * sample_byte_size;
    void* mat_row = cropped.data;
	for (row_index = top; row_index < bottom; row_index++) {

		row_size = TIFFReadEncodedStrip(this->_tiff, row_index, row_data, strip_size);

        if(row_size != strip_size) {
            _TIFFfree(row_data);
            if(row_size < 0)
                throw std::runtime_error("[LibTiffReader] Failed to get row #" + std::to_string(row_index));
            else
                throw std::runtime_error("[LibTiffReader] Actual strip size assumption failed.");
        }

        memcpy(mat_row, crop_row, crop_row_size);
        mat_row = (char*)mat_row + crop_row_size;
    }

	_TIFFfree(row_data);

    return cropped;
}

std::string LibTiffReader::getColorSpaceString() {
    if( ! this->_is_open )
        throw std::runtime_error("[LibTiffReader] No file opened.");

    char* artist_tag = 0;
    TIFFGetField(this->_tiff, TIFFTAG_ARTIST, &artist_tag);
    if(!artist_tag)
        throw std::runtime_error("[LibTiffReader] Matrix does not exist.");

    std::string space;
    try {
        jsoncons::json custom_tag = jsoncons::json::parse(artist_tag);
        space = custom_tag["ColorSpace"].as<std::string>();
    }
    catch (const std::exception& e) {
        throw std::runtime_error("[LibTiffReader] "
            + std::string(e.what()) + " Invalid BTRGB Artist tag.\n");
    }

    return space;
}

cv::Mat LibTiffReader::getConversionMatrix(std::string key) {
    if( ! this->_is_open )
        throw std::runtime_error("[LibTiffReader] No file opened.");

    char* artist_tag;
    if( ! TIFFGetField(this->_tiff, TIFFTAG_ARTIST, &artist_tag) )
        throw std::runtime_error("[LibTiffReader] Image does not have artist tag.");

    cv::Mat m;

    try {
        jsoncons::json custom_tag = jsoncons::json::parse(artist_tag);
        m = this->_extractMat(custom_tag[key]);
    }
    catch (const std::exception& e) {
        throw std::runtime_error("[LibTiffReader] "
            + std::string(e.what()) + " Invalid BTRGB Artist tag.\n");
    }

    return m;
}

std::unordered_map<std::string, cv::Mat> LibTiffReader::getConversionMatrices() {
    if( ! this->_is_open )
        throw std::runtime_error("[LibTiffReader] No file opened.");

    std::unordered_map<std::string, cv::Mat> matrices;

    char* artist_tag;
    if( ! TIFFGetField(this->_tiff, TIFFTAG_ARTIST, &artist_tag) )
        throw std::runtime_error("[LibTiffReader] Image does not have artist tag.");

    try {
        jsoncons::json custom_tag = jsoncons::json::parse(artist_tag);
        for (const auto& tagged_item: custom_tag.object_range()) {
            auto tag = tagged_item.value();
            auto name = tagged_item.key();

            if(!tag.is_object() || !tag.contains("cv_type"))
                continue;

            if(matrices.contains(name))
                continue;

            matrices[name] = this->_extractMat(tag);
        }

    }
    catch (const std::exception& e) {
        throw std::runtime_error("[LibTiffReader] "
            + std::string(e.what()) + " Invalid BTRGB Artist tag.\n");
    }

    return matrices;
}

void LibTiffReader::getColorProfile(uint32_t* size, void** data) {
    if( ! this->_is_open )
        throw std::runtime_error("[LibTiffReader] No file opened.");

    if( ! TIFFGetField(this->_tiff, TIFFTAG_ICCPROFILE, size, data) )
        throw std::runtime_error("[LibTiffReader] File does not have color profile.");
}

void TiffIO::init()
{
	ENTERFUNC;
	if (initialized) {
		return;
	}
	initialized = true;

	bool is_new_file = false;
	FILE *tmp_in = sfopen(filename, rw_mode, &is_new_file, true);
	if (!tmp_in) {
		throw ImageReadException(filename, "open TIFF");
	}

	if( !is_new_file ) {
		char buf[64];
		if (fread(buf, sizeof(buf), 1, tmp_in) != 1) {
			throw ImageReadException(filename, "first block");
		}

		if (!is_valid(&buf)) {
			throw ImageReadException(filename, "invalid TIFF");
		}

		if (buf[0] == TIFF_BIG_ENDIAN) {
			is_big_endian = true;
		}
		else {
			is_big_endian = false;
		}
	}

	fclose(tmp_in);
	tmp_in = 0;

	TIFFSetWarningHandler(0);

	if( rw_mode == ImageIO::READ_ONLY ) {
		tiff_file = TIFFOpen(filename.c_str(), "r");

		if (!tiff_file) {
			throw ImageReadException(filename, "open TIFF");
		}

		TIFFGetField(tiff_file, TIFFTAG_BITSPERSAMPLE, &bitspersample);

		if (bitspersample != CHAR_BIT &&
				bitspersample != (CHAR_BIT * sizeof(short)) &&
				bitspersample != (CHAR_BIT * sizeof(float)) ) {
			char desc[256];
			sprintf(desc, "invalid %d bits. only %d-bit and %d-bit TIFF are supported",
					bitspersample, CHAR_BIT, (int)(CHAR_BIT * sizeof(short)));
			throw ImageReadException(filename, desc);
		}
	}
	else {
		tiff_file = TIFFOpen(filename.c_str(), "w");
		if (!tiff_file) {
			throw ImageReadException(filename, "open TIFF");
		}
	}

	EXITFUNC;
}

int TiffIO::read_header(Dict & dict, int image_index, const Region * area, bool)
{
	ENTERFUNC;

	init();

	//single image format, index can only be zero
	if(image_index == -1) {
		image_index = 0;
	}

	if(image_index != 0) {
		throw ImageReadException(filename, "no stack allowed for MRC image. For take 2D slice out of 3D image, read the 3D image first, then use get_clip().");
	}

	int nx = 0;
	int ny = 0;
	TIFFGetField(tiff_file, TIFFTAG_IMAGEWIDTH, &nx);
	TIFFGetField(tiff_file, TIFFTAG_IMAGELENGTH, &ny);

	check_region(area, IntSize(nx, ny));

	float min = 0;
	float max = 0;
	TIFFDataType data_type = TIFF_NOTYPE;
	float resolution_x = 0;
	float resolution_y = 0;

	TIFFGetField(tiff_file, TIFFTAG_MINSAMPLEVALUE, &min);
	TIFFGetField(tiff_file, TIFFTAG_MAXSAMPLEVALUE, &max);

	TIFFGetField(tiff_file, TIFFTAG_PHOTOMETRIC, &photometric);

	TIFFGetField(tiff_file, TIFFTAG_SAMPLEFORMAT, &data_type);
	TIFFGetField(tiff_file, TIFFTAG_XRESOLUTION, &resolution_x);
	TIFFGetField(tiff_file, TIFFTAG_YRESOLUTION, &resolution_y);

	int xlen = 0, ylen = 0;
	EMUtil::get_region_dims(area, nx, &xlen, ny, &ylen);

	dict["nx"] = xlen;
	dict["ny"] = ylen;
	dict["nz"] = 1;

	dict["minimum"] = min;
	dict["maximum"] = max;

	if (bitspersample == CHAR_BIT) {
		dict["datatype"] = EMUtil::EM_UCHAR;
	}
	else if (bitspersample == sizeof(unsigned short) * CHAR_BIT) {
		dict["datatype"] = EMUtil::EM_USHORT;
	}
	else if (bitspersample == sizeof(float) * CHAR_BIT) {
		dict["datatype"] = EMUtil::EM_FLOAT;
	}

	dict["TIFF.bitspersample"] = bitspersample;
	dict["TIFF.resolution_x"] = resolution_x;
	dict["TIFF.resolution_y"] = resolution_y;
	EXITFUNC;
	return 0;
}

int TiffIO::read_data(float *rdata, int image_index, const Region * area, bool)
{
	ENTERFUNC;

	//single image format, index can only be zero
	image_index = 0;
	check_read_access(image_index, rdata);

	int nx = 0;
	int ny = 0;
	TIFFGetField(tiff_file, TIFFTAG_IMAGEWIDTH, &nx);
	TIFFGetField(tiff_file, TIFFTAG_IMAGELENGTH, &ny);

	int err = 0;

	/* for grey scale image, use TIFFReadEncodedStrip() and TIFFReadEncodedTile()
	 * because the reading of strip image is twice time faster than TIFFReadRGBAImage -Grant*/
	if(photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK) {
		unsigned char *cdata;	//buffer for strip or tile

		if(TIFFIsTiled(tiff_file)) {
			tsize_t tileSize = TIFFTileSize(tiff_file);
			tsize_t tileMax = TIFFNumberOfTiles(tiff_file);
			tsize_t tileCount;

			uint32 tileWidth, tileLength;
			TIFFGetField(tiff_file, TIFFTAG_TILEWIDTH, &tileWidth);
			TIFFGetField(tiff_file, TIFFTAG_TILELENGTH, &tileLength);

			if((cdata=(unsigned char*)_TIFFmalloc(tileSize))==NULL){
				fprintf(stderr,"Error: Could not allocate enough memory\n");
				return(-1);
			}

			int tilePerLine = nx/tileWidth + 1;
			int NX, NY;	//(NX, NY) is the cordinates of tile
			int xpos, ypos; //(xpos, ypos) is the actual coordinates of pixel (j,i) in image

			for(tileCount=0; tileCount<tileMax; tileCount++) {
				if(TIFFReadEncodedTile(tiff_file, tileCount, cdata, tileSize) == -1) {
					fprintf(stderr,"Error reading tiled image\n");return(-1);
				}
				else {
					NX = tileCount%tilePerLine;
					NY = tileCount/tilePerLine;
					uint32 i, j;
					for(i=0; i<tileLength; i++) {
						for(j=0; j<tileWidth; j++) {
							xpos = NX*tileWidth + j;
							ypos = NY*tileLength + i;

							if(bitspersample == CHAR_BIT) {
								if(xpos<nx && ypos<ny) {	//discard those pixel in tile which is out of actual image's boundary
									photometric == PHOTOMETRIC_MINISWHITE ?
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = -(float) ((unsigned char*)cdata)[i*tileWidth+j] :
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = (float) ((unsigned char*)cdata)[i*tileWidth+j];
								}
							}
							else if(bitspersample == sizeof(unsigned short) * CHAR_BIT) {
								if(xpos<nx && ypos<ny) {	//discard those pixel in tile which is out of actual image's boundary
									photometric == PHOTOMETRIC_MINISWHITE ?
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = -(float) ((unsigned short*)cdata)[i*tileWidth+j] :
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = (float) ((unsigned short*)cdata)[i*tileWidth+j];
								}
							}
							else if (bitspersample == sizeof(float) * CHAR_BIT) {
								photometric == PHOTOMETRIC_MINISWHITE ?
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = -((float*)cdata)[i*tileWidth+j] :
										rdata[nx*(ny-1)-(ypos*nx)+xpos] = ((float*)cdata)[i*tileWidth+j];
							}
							else {
								fprintf(stderr,"BAILING OUT:Allow only 8- or 16-bits image\n");
								return(-1);
							}
						}
					}
				}
			}

		}
		else {
			check_region(area, IntSize(nx, ny));
			int xlen = 0, ylen = 0, x0 = 0, y0 = 0;
			EMUtil::get_region_dims(area, nx, &xlen, ny, &ylen);
			EMUtil::get_region_origins(area, &x0, &y0);

			int strip_size = TIFFStripSize(tiff_file);
			uint32 num_strips = TIFFNumberOfStrips(tiff_file);

			if((cdata = static_cast < unsigned char *>(_TIFFmalloc(strip_size)))==NULL) {
				fprintf(stderr,"Error: Could not allocate enough memory\n");
				return(-1);
			}

			int k = 0;
			int num_read = 0;
			int mode_size = bitspersample / CHAR_BIT;
			int total_rows = 0;

			for (uint32 i = 0; i < num_strips; i++) {
				if ((num_read = TIFFReadEncodedStrip(tiff_file, i, cdata, strip_size)) == -1) {
					LOGERR("reading stripped TiFF image '%s' failed", filename.c_str());
					err = 1;
					break;
				}

				int nitems = num_read / mode_size;
				int nrows = nitems / nx;
				total_rows += nrows;

				int y_start = 0;
				int y_end = nrows;

				if (area) {
					if (total_rows >= y0 && total_rows < y0 + nrows) {
						y_start = nrows - (total_rows - y0);
					}
					else if (total_rows >= (y0 + ylen) && total_rows < (y0 + ylen + nrows)) {
						y_end = y0 + ylen - total_rows + nrows;
					}
					else if (total_rows >= (y0 + ylen + nrows)) {
						break;
					}
				}

				for (int l = y_start; l < y_end; l++) {
					for (int j = x0; j < x0 + xlen; j++) {
						if (bitspersample == CHAR_BIT) {
							photometric == PHOTOMETRIC_MINISWHITE ?
								rdata[k] = -(float) ((unsigned char*)cdata)[l * nx + j] :
								rdata[k] = (float) ((unsigned char*)cdata)[l * nx + j];
						}
						else if (bitspersample == sizeof(unsigned short) * CHAR_BIT) {
							photometric == PHOTOMETRIC_MINISWHITE ?
								rdata[k] = -(float)((unsigned short*)cdata)[l * nx + j] :
								rdata[k] = (float)((unsigned short*)cdata)[l * nx + j];
						}
						else if (bitspersample == sizeof(float) * CHAR_BIT) {
							photometric == PHOTOMETRIC_MINISWHITE ?
								rdata[k] = -((float*)cdata)[l * nx + j] :
								rdata[k] = ((float*)cdata)[l * nx + j];
						}
						k++;
					}
				}
			}
			Util::flip_image(rdata, xlen, ylen);
		}
		_TIFFfree(cdata);
	}
	else {	//process color image, convert to greyscale
		size_t npixels = nx * ny;
		uint32 * raster = (uint32*) _TIFFmalloc(npixels * sizeof(uint32));
		if(raster != NULL) {
			if(TIFFReadRGBAImage(tiff_file, nx, ny, raster, 0)) {
				int abgr = 0;	//raw ABGR pixel value
				int red=0, green=0, blue=0;
				for (int i=0; i<nx; ++i) {
					for (int j=0; j<ny; ++j) {
						abgr 	= raster[j+ny*i];
						red 	= TIFFGetR(abgr);
						green 	= TIFFGetG(abgr);
						blue 	= TIFFGetB(abgr);
						rdata[j+ny*i] = static_cast<float>(red*RED+green*GREEN+blue*BLUE);
					}
				}
				_TIFFfree(raster);
			}
		}
	}

	EXITFUNC;
	return err;
}

