void readTiff(QImageIO* iio)
{
  QImage image;
  int depth;
  int i;
  float x_resolution, y_resolution;
  int y;
  TIFF *tiff;
  Q_UINT16 extra;
  Q_UINT16* extra_types;
  Q_UINT16 compress_tag;
  Q_UINT32 height,width;
  Q_UINT16 bits_per_sample;
  Q_UINT16 interlace;
  Q_UINT16 max_sample_value;
  Q_UINT16 min_sample_value;
  Q_UINT16 photometric;
  Q_UINT16 samples_per_pixel;
  Q_UINT16 units;
  bool color;
  bool has_alpha = false;
  bool min_is_white = false;
  tiff=TIFFOpen(QFile::encodeName(iio->fileName()),"r");
  if(tiff == (TIFF *) 0L)
  {
    iio->setStatus(1);
    return;
  }
  TIFFGetField(tiff,TIFFTAG_IMAGEWIDTH,&width);
  TIFFGetField(tiff,TIFFTAG_IMAGELENGTH,&height);
  TIFFGetFieldDefaulted(tiff,TIFFTAG_BITSPERSAMPLE,&bits_per_sample);
  TIFFGetFieldDefaulted(tiff,TIFFTAG_PHOTOMETRIC,&photometric);
  TIFFGetField(tiff,TIFFTAG_SAMPLESPERPIXEL,&samples_per_pixel);
  TIFFGetField(tiff,TIFFTAG_EXTRASAMPLES,&extra,&extra_types);
  TIFFGetFieldDefaulted(tiff,TIFFTAG_PLANARCONFIG,&interlace);

  if(extra>0 && (extra_types[0] == EXTRASAMPLE_ASSOCALPHA))
  {
    has_alpha = true;
  }
  //The formats we can read. If the tiff has been written by QuiteInsane,
  //then we can read it this way. For other formats, we fall back to
  //RGBA - at least for the moment.
  if((!TIFFIsTiled(tiff)) &&
     (interlace == PLANARCONFIG_CONTIG) &&
     ((bits_per_sample == 1)   ||
      (bits_per_sample == 8)   ||
      (bits_per_sample == 16)) &&
     ((photometric == PHOTOMETRIC_MINISWHITE) ||
      (photometric == PHOTOMETRIC_MINISBLACK) ||
      (photometric == PHOTOMETRIC_RGB)        ||
      (photometric == PHOTOMETRIC_PALETTE)))
  {
    if((bits_per_sample == 8) || (bits_per_sample == 16))
    {
      if(photometric == PHOTOMETRIC_RGB)
      {
        color = true;
        depth = 32;
        if(!image.create(width,height,depth))
        {
          iio->setStatus(1);
          return;
        }
        image.setAlphaBuffer(has_alpha);
      }
      else
      {
        depth = 8;
        if(!image.create(width,height,depth))
        {
          iio->setStatus(1);
          return;
        }
        image.setNumColors(256);
        image.setAlphaBuffer(has_alpha);
        if(photometric == PHOTOMETRIC_PALETTE)
        {
          color = true;
          Q_UINT16* blue_colormap;
          Q_UINT16* green_colormap;
          Q_UINT16* red_colormap;

          TIFFGetField(tiff,TIFFTAG_COLORMAP,&red_colormap,&green_colormap,
                       &blue_colormap);
          for(i=0; i < 256; i++)
          {
            image.setColor(i,qRgb(red_colormap[i]/257,
                                  green_colormap[i]/257,
                                  blue_colormap[i]/257));

          }
        }
        else if(photometric == PHOTOMETRIC_MINISWHITE)
        {
          color = false; //grayscale
          min_is_white = true;
        }
        else
        {
          color = false;
        }
      }
    }
    else if(bits_per_sample == 1) //lineart, B&W
    {
      depth = 1;
      if(!image.create(width,height,depth,2,QImage::BigEndian))
      {
        iio->setStatus(1);
        return;
      }
    }
    if(depth == 1)
    {
      image.setColor(0,qRgb(255,255,255));
      image.setColor(1,qRgb(0,0,0));
    }
    TIFFGetFieldDefaulted(tiff,TIFFTAG_RESOLUTIONUNIT,&units);
    TIFFGetFieldDefaulted(tiff,TIFFTAG_XRESOLUTION,&x_resolution);
    TIFFGetFieldDefaulted(tiff,TIFFTAG_YRESOLUTION,&y_resolution);
    if(units == 1)
    {
      image.setDotsPerMeterX(0);
      image.setDotsPerMeterY(0);
    }
    else if(units == 2)
    {
      image.setDotsPerMeterX(int(x_resolution*100.0/2.54));
      image.setDotsPerMeterY(int(y_resolution*100.0/2.54));
    }
    else if(units == 3)
    {
      image.setDotsPerMeterX(int(x_resolution*100.0));
      image.setDotsPerMeterY(int(y_resolution*100.0));
    }
    TIFFGetFieldDefaulted(tiff,TIFFTAG_COMPRESSION,&compress_tag);
    TIFFGetFieldDefaulted(tiff,TIFFTAG_MINSAMPLEVALUE,&min_sample_value);
    TIFFGetFieldDefaulted(tiff,TIFFTAG_MAXSAMPLEVALUE,&max_sample_value);
    TIFFGetFieldDefaulted(tiff,TIFFTAG_SAMPLESPERPIXEL,&samples_per_pixel);

    Q_UINT8* line;
    line = 0;
    if(depth == 32)
    {
      if(bits_per_sample == 16)
      {
        if(has_alpha)
          line = new Q_UINT8 [width*8];
        else
          line = new Q_UINT8 [width*6];
      }
      else
      {
        if(has_alpha)
          line = new Q_UINT8 [width*4];
        else
          line = new Q_UINT8 [width*3];
      }
    }
    if(depth == 8)
    {
      if(bits_per_sample == 16)
        line = new Q_UINT8 [width*2];
      else
        line = new Q_UINT8 [width];
    }
    if(depth == 1)
      line = new Q_UINT8 [TIFFScanlineSize(tiff)];

    if(line)
    {
      for(y=0; y < (int) image.height(); y++)
      {
        if(TIFFReadScanline(tiff,(unsigned char*)line,y,0) == -1)
        {
          delete [] line;
          TIFFClose(tiff);
          iio->setStatus(1);
          return;
        }
        if(depth == 32)
        {
          Q_UINT16* line16;
          line16 = (Q_UINT16*) line;
          int Qt::red,Qt::green,Qt::blue,alpha;
          for(i=0;i<image.width();i++)
          {
            if(bits_per_sample == 16)
            {
              if(has_alpha)
              {
                Qt::red = int(line16[i*4])/257;
                Qt::green = int(line16[i*4+1])/257;
                Qt::blue = int(line16[i*4+2])/257;
                alpha = int(line16[i*4+3]/257);
                if(alpha > 0)
                {
                  Qt::red = (Qt::red*255)/alpha;
                  Qt::green = (Qt::green*255)/alpha;
                  Qt::blue = (Qt::blue*255)/alpha;
                }
                image.setPixel(i,y,qRgba(Qt::red,Qt::green,Qt::blue,alpha));
              }
              else
                image.setPixel(i,y,qRgb(int(line16[i*3]),
                                        int(line16[i*3+1]),
                                        int(line16[i*3+2])));
            }
            else
            {
              if(has_alpha)
              {
                Qt::red = int(line[i*4]);
                Qt::green = int(line[i*4+1]);
                Qt::blue = int(line[i*4+2]);
                alpha = int(line[i*4+3]);
                if(alpha > 0)
                {
                  Qt::red = (Qt::red*255)/alpha;
                  Qt::green = (Qt::green*255)/alpha;
                  Qt::blue = (Qt::blue*255)/alpha;
                }
                image.setPixel(i,y,qRgba(Qt::red,Qt::green,Qt::blue,alpha));
              }
              else
                image.setPixel(i,y,qRgb(int(line[i*3]),
                                        int(line[i*3+1]),
                                        int(line[i*3+2])));
            }
          }
        }
        if(depth == 8)
        {
          if(bits_per_sample == 16)
          {
            Q_UINT16* line16;
            line16 = (Q_UINT16*) line;
            for(i=0;i<image.width();i++)
            {
              int Qt::gray = line16[i]/257;
              if(color)
              {
                image.setPixel(i,y,Qt::gray);
              }
              else
              {
                if(min_is_white)
                {
                  Qt::gray = 255 - Qt::gray;
                  image.setPixel(i,y,Qt::gray);
                  image.setColor(Qt::gray,qRgb(Qt::gray,Qt::gray,Qt::gray));
                }
                else
                {
                  image.setPixel(i,y,Qt::gray);
                  image.setColor(Qt::gray,qRgb(Qt::gray,Qt::gray,Qt::gray));
                }
              }
            }
          }
          else if(bits_per_sample == 8)
          {
            for(i=0;i<image.width();i++)
            {
              if(color)
              {
                image.setPixel(i,y,line[i]);
              }
              else
              {
                image.setPixel(i,y,line[i]);
                if(min_is_white)
                {
                  image.setPixel(i,y,line[i]);
                  image.setColor(line[i],qRgb(int(line[i]^0xff),
                                              int(line[i]^0xff),
                                              int(line[i]^0xff)));
                }
                else
                {
                  image.setPixel(i,y,line[i]);
                  image.setColor(line[i],qRgb(int(line[i]),
                                              int(line[i]),
                                              int(line[i])));
                }
              }
            }
          }
        }
        if(depth == 1)
        {
          memcpy(image.scanLine(y),line,TIFFScanlineSize(tiff));
        }
      }
      delete [] line;
      TIFFClose(tiff);
    }
    else //error
    {
      TIFFClose(tiff);
      iio->setStatus(1);
      return;
    }
  }
//otherwise, try to read an RGBA image
  else
  {
    unsigned int i;
    unsigned int i2;
    unsigned char* idat;
    uint32 w, h;
    size_t npixels;
    uint32* raster;
    TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &w);
    TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &h);
    npixels = w * h;
    raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
    if(raster)
    {
      if(TIFFReadRGBAImage(tiff, w, h, raster, 0))
      {
        if(!image.create(w,h,32))
        {
          iio->setStatus(1);
          _TIFFfree(raster);
          return;
        }
        image.setAlphaBuffer(true);
        //we finaly can transfer the data to our image
        idat = (unsigned char*)raster;
        for(i=0;i<h;i++)
        {
          for(i2=0;i2<w;i2++)
          {
            image.setPixel(i2,i,qRgba((int)*(idat),
                                      (int)*(idat+1),
                                      (int)*(idat+2),
                                      (int)*(idat+3)));
            idat += 4;
          }
        }
      }
      _TIFFfree(raster);
      image = image.mirror(false,true);
    }
    else //couldn't allocate raster
    {
      TIFFClose(tiff);
      iio->setStatus(1);
      return;
    }
    TIFFClose(tiff);
  }
  iio->setImage(image);
  iio->setStatus(0);
}

int imageIO::readTiff(){
  TIFF * image;
  uint width, height, depth, planesize;
  uint offset = 0;
  int unit;

  // Open the TIFF image
  image = TIFFOpen(filename.c_str(), "r");

  // Find the width and height of the image
  TIFFGetField(image, TIFFTAG_IMAGEWIDTH, &width);
  TIFFGetField(image, TIFFTAG_IMAGELENGTH, &height);
  depth = getTiffCount();

  img.height = height;
  img.width  = width;
  img.depth  = depth;
  img.image_stack = (uchar *) malloc (height * width * depth * sizeof(uchar));

  if (img.image_stack == NULL){
    cerr << "Error, could not allocate enough memory for image stack.\n";
    return 0;
  }

  TIFFGetField(image, TIFFTAG_RESOLUTIONUNIT, &unit);
  TIFFGetField(image, TIFFTAG_XRESOLUTION, &img.xy_dist);

  if (unit == RESUNIT_CENTIMETER){
    /*
     * divide by 10,000 to convert from centimeters to microns
     * take inverse to get microns/pixel
     */
    img.xy_dist = (1 / (img.xy_dist / 10000));
  }else if (unit == RESUNIT_INCH){
    // divide by 24,500 to convert from inches to microns 
    img.xy_dist = (1 / (img.xy_dist / 25400));
  }

  planesize = height * width;

  if (image != NULL) {

    uint bufsize= TIFFStripSize(image);
    uchar * buf = (uchar *) _TIFFmalloc(bufsize);
    uint s, ns  = TIFFNumberOfStrips(image);
    uint *bytecounts;

    do {

      TIFFGetField(image, TIFFTAG_STRIPBYTECOUNTS, &bytecounts);
      for (s = 0; s < ns; s++) {

	// check to see if we need to resize the buffer
	if (bytecounts[s] != (uint)bufsize){
	  buf = (uchar *)_TIFFrealloc(buf, bytecounts[s]);
	  if (!buf){
	    return 0;
	  }
	  bufsize = bytecounts[s];
	}

	// try to read the strip
	if (TIFFReadRawStrip(image, s, buf, bytecounts[s]) < 0){
	  // if we encounter an error, free the buffer and return
	  _TIFFfree(buf);
	  return 0;
	}else{
	  memcpy(&img.image_stack[offset], buf, (bufsize * sizeof(uchar)));
	  offset += bufsize;
	}
      }

    } while (TIFFReadDirectory(image));
    _TIFFfree(buf);
  }

  /*
    // DEBUG
    tstrip_t strip;
    uint32* bc;

    TIFFGetField(image, TIFFTAG_STRIPBYTECOUNTS, &bc);


    int cnt = 0;


    do {
      cout << "strip byte counts: " << bc[cnt++] << endl;

      // Read the image into the memory buffer
      if(TIFFReadRGBAStrip(image, 0, raster) == 0){
	fprintf(stderr, "Could not read image\n");
	return 0;
      }

      for (i = 0; i < planesize; ++i){
	img.image_stack[offset + i] = ((uchar) TIFFGetR(raster[i]));
      }
      offset += planesize;

      cout << "raster[" << offset - 1 << "] = " << TIFFGetR(raster[offset - 1]) << endl;      
      printf("img[%i] = %i\n", offset - 1, img.image_stack[offset - 1]);

    } while (TIFFReadDirectory(image));

    free(raster);
    (void) TIFFClose(image);
  }
  */
  return 1;
}

int TMOImage::ReadHeader(TIFF *pFile)
{
	unsigned short iCompression = 0; // compression type
	unsigned short iOrientation = 1; // visual orientation (TIFF spec.)

	if (!TIFFGetField(pFile, TIFFTAG_COMPRESSION, &iCompression))
	{
		iCompression = COMPRESSION_NONE;
	}

	TIFFGetField(pFile, TIFFTAG_XRESOLUTION, &fXres);
	TIFFGetField(pFile, TIFFTAG_YRESOLUTION, &fYres);

	TIFFGetFieldDefaulted(pFile, TIFFTAG_ORIENTATION, &iOrientation);

	if (!TIFFGetFieldDefaulted(pFile, TIFFTAG_PHOTOMETRIC, &iPhotometric))
		throw TMO_EFILE_PARSE;

	switch (iPhotometric)
	{
	case PHOTOMETRIC_LOGLUV:
		TIFFSetField(pFile, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT);
		TIFFSetField(pFile, TIFFTAG_SGILOGENCODE, SGILOGENCODE_RANDITHER);
		break;
	case PHOTOMETRIC_LOGL:
		TIFFSetField(pFile, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT);
		TIFFSetField(pFile, TIFFTAG_SGILOGENCODE, SGILOGENCODE_NODITHER);
		break;
	case PHOTOMETRIC_RGB:
		break;
	default:
		throw TMO_EFILE_PARSE;
	}

	if (!TIFFGetField(pFile, TIFFTAG_BITSPERSAMPLE, &iBitsPerSample))
	{
		iBitsPerSample = 8;
	}

	if (!TIFFGetField(pFile, TIFFTAG_IMAGEWIDTH, &iWidth) ||
		!TIFFGetField(pFile, TIFFTAG_IMAGELENGTH, &iHeight))
		throw TMO_EFILE_PARSE;

	if (!TIFFGetField(pFile, TIFFTAG_STONITS, &dStonits))
		dStonits = 1.;

	if (pData)
		delete[] pData;
	pData = new double[3 * iWidth * iHeight];

	return 0;
}

void TiffDirectory::_getTiffField_ui32(TIFF *tiff, ttag_t tag,
int64_t *val) const {
  uint32_t normalint = 0;
  if (1 == TIFFGetField(tiff, tag, &normalint)) {
      *val = static_cast<int64_t>(normalint);
  } else {
    *val = -1;
  }
}

void TiffDirectory::_getTiffField_ui16(TIFF *tiff, ttag_t tag,
int64_t *val) const {
  uint16_t normalint = 0;
  if (1 == TIFFGetField(tiff, tag, &normalint)) {
      *val = static_cast<int64_t>(normalint);
  } else {
    *val = -1;
  }
}

void TiffDirectory::_getTiffField_f(TIFF *tiff, ttag_t tag,
double *val) const {
  float flt;
  if (1 == TIFFGetField(tiff, tag, &flt)) {
      *val = static_cast<double>(flt);
  } else {
    *val = -1.0;
  }
}

void TiffDirectory::_getTiffField_str(TIFF *tiff, ttag_t tag,
std::string *val) const {
  char *str;
  int result = TIFFGetField(tiff, tag, &str);
  if (result == 1) {
    *val = str;
  } else {
    *val = "";
  }
}

void TiffDirectory::_getTiffField_jpegTables(TIFF *tiff,
int64_t* jpegTableDataSize, std::unique_ptr<uint8_t[]> *jpegTableData) const {
  uint16_t size = 0;
  void *tableData;
  *jpegTableDataSize = -1;
  *jpegTableData = nullptr;
  int result = TIFFGetField(tiff, TIFFTAG_JPEGTABLES, &size, &tableData);
  if (result == 1) {
    *jpegTableDataSize = static_cast<int64_t>(size);
    if (*jpegTableDataSize > 0) {
      *jpegTableData = std::make_unique<uint8_t[]>(size);
      memcpy((*jpegTableData).get(), tableData, size);
    }
  }
}

bool TiffDirectory::_hasICCProfile(TIFF *tiff) const {
  uint32_t temp;
  void *ptr;
  return (1 == TIFFGetField(tiff, TIFFTAG_ICCPROFILE, &temp, &ptr));
}

bool isLightZoneLayeredTIFF( TIFF *tiff ) {
    LC_TIFFFieldValue value[2];
    if ( !TIFFGetField( tiff, TIFFTAG_SOFTWARE, &value[0] ) )
        return false;
    if ( ::strncmp( value[0].cp, AppName, ::strlen( AppName ) ) )
        return false;
    if ( !TIFFGetField( tiff, TIFFTAG_PAGENUMBER, &value[0], &value[1] ) )
        return false;
    return value[1].u16 == 2;
}

JNIEXPORT jbyteArray JNICALL LCTIFFReader_METHOD(getICCProfileData)
    ( JNIEnv *env, jobject jLCTIFFReader )
{
    uint32_t profileSize;
    void *profileData;
    int const result = TIFFGetField(
        getNativePtr( env, jLCTIFFReader ), TIFFTAG_ICCPROFILE,
        &profileSize, &profileData
    );
    if ( !result )
        return NULL;
    jbyteArray jProfileData = (jbyteArray)env->NewByteArray( profileSize );
    jarray_to_c<jbyte> cProfileData( env, jProfileData );
    ::memcpy( cProfileData, profileData, profileSize );
    return jProfileData;
}

JNIEXPORT jint JNICALL LCTIFFReader_METHOD(getIntField)
    ( JNIEnv *env, jobject jLCTIFFReader, jint tagID )
{
    TIFF *const tiff = getNativePtr( env, jLCTIFFReader );
    LC_TIFFFieldValue value, unused;
    int result;
    switch ( tagID ) {
        case TIFFTAG_EXTRASAMPLES:
            //
            // Special case for those tags that have extra values we're not
            // interested in.
            //
            result = TIFFGetField( tiff, tagID, &value, &unused );
            break;
        default:
            result = TIFFGetField( tiff, tagID, &value );
    }
    if ( !result )
        return -1;
    switch ( tagID ) {

        case TIFFTAG_BADFAXLINES:
        case TIFFTAG_CONSECUTIVEBADFAXLINES:
        case TIFFTAG_GROUP3OPTIONS:
        case TIFFTAG_GROUP4OPTIONS:
        case TIFFTAG_IMAGEDEPTH:
        case TIFFTAG_IMAGELENGTH:
        case TIFFTAG_IMAGEWIDTH:
        case TIFFTAG_ROWSPERSTRIP:
        case TIFFTAG_SUBFILETYPE:
        case TIFFTAG_TILEDEPTH:
        case TIFFTAG_TILELENGTH:
        case TIFFTAG_TILEWIDTH:
            return value.u32;

        case TIFFTAG_BITSPERSAMPLE:
        case TIFFTAG_CLEANFAXDATA:
        case TIFFTAG_COMPRESSION:
        case TIFFTAG_DATATYPE:
        case TIFFTAG_FILLORDER:
        case TIFFTAG_INKSET:
        case TIFFTAG_MATTEING:
        case TIFFTAG_MAXSAMPLEVALUE:
        case TIFFTAG_MINSAMPLEVALUE:
        case TIFFTAG_ORIENTATION:
        case TIFFTAG_PHOTOMETRIC:
        case TIFFTAG_PLANARCONFIG:
        case TIFFTAG_PREDICTOR:
        case TIFFTAG_RESOLUTIONUNIT:
        case TIFFTAG_SAMPLEFORMAT:
        case TIFFTAG_SAMPLESPERPIXEL:
        case TIFFTAG_THRESHHOLDING:
        case TIFFTAG_YCBCRPOSITIONING:
        case TIFFTAG_EXTRASAMPLES:
            return value.u16;

        default:
            LC_throwIllegalArgumentException( env, "unsupported tagID" );
            return -1;
    }
}

JNIEXPORT jint JNICALL LCTIFFReader_METHOD(getIntField2)
    ( JNIEnv *env, jobject jLCTIFFReader, jint tagID, jboolean getSecond )
{
    TIFF *const tiff = getNativePtr( env, jLCTIFFReader );
    LC_TIFFFieldValue value[2];

    switch ( tagID ) {

        case TIFFTAG_PAGENUMBER:
            return  TIFFGetField( tiff, tagID, &value[0], &value[1] ) ?
                    value[ getSecond ].u16 : -1;

        default:
            LC_throwIllegalArgumentException( env, "unsupported tagID" );
            return -1;
    }
}

JNIEXPORT jstring JNICALL LCTIFFReader_METHOD(getStringField)
    ( JNIEnv *env, jobject jLCTIFFReader, jint tagID )
{
    TIFF *const tiff = getNativePtr( env, jLCTIFFReader );
    LC_TIFFFieldValue value;
    switch ( tagID ) {

        case TIFFTAG_ARTIST:
        case TIFFTAG_COPYRIGHT:
        case TIFFTAG_DATETIME:
        case TIFFTAG_DOCUMENTNAME:
        case TIFFTAG_HOSTCOMPUTER:
        case TIFFTAG_IMAGEDESCRIPTION:
        case TIFFTAG_INKNAMES:
        case TIFFTAG_MAKE:
        case TIFFTAG_MODEL:
        case TIFFTAG_PAGENAME:
        case TIFFTAG_SOFTWARE:
        case TIFFTAG_TARGETPRINTER:
            return  TIFFGetField( tiff, tagID, &value ) ?
                    env->NewStringUTF( value.cp ) : NULL;

        default:
            LC_throwIllegalArgumentException( env, "unsupported tagID" );
            return NULL;
    }
}

bool ZLWin32ImageManager::tiffConvert(const std::string &stringData, ZLWin32ImageData &data, bool &result) const {
	result = false;
	TIFFReader reader(stringData);
	TIFF *tiff = TIFFClientOpen("ZLWin32ImageManager", "rM", &reader, TIFFReader::read, TIFFReader::write, TIFFReader::seek, TIFFReader::close, TIFFReader::size, TIFFReader::map, TIFFReader::unmap);
	if (tiff == 0) {
		return false;
	}

	int width, height;
	if (!TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width) ||
			!TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height)) {
		TIFFClose(tiff);
		return false;
	}

	data.init(width, height, true, 0);

	result = TIFFReadRGBAImage(tiff, width, height, (uint32*)data.myArray, 1) != 0;
	data.bgr2rgb();

	TIFFClose(tiff);
	return true;
}

Image *Read (IStream *file, const ImageReadOptions& options)
{
    int                   nrow;
    int                   result = 0;
    long                  LineSize;
    TIFF                  *tif;
    Image                 *image ;
    uint16                BitsPerSample;
    uint16                BytesPerSample = 1;
    uint16                PhotometricInterpretation;
    uint16                SamplePerPixel;
    uint16                Orientation;
    uint16                ExtraSamples;
    uint16*               ExtraSampleInfo;
    uint32                RowsPerStrip;
    unsigned int          width;
    unsigned int          height;

    // TODO - TIFF files probably have some gamma info in them by default, but we're currently ignorant about that.
    // Until that is fixed, use whatever the user has chosen as default.
    GammaCurvePtr gamma;
    if (options.gammacorrect && options.defaultGamma)
        gamma = TranscodingGammaCurve::Get(options.workingGamma, options.defaultGamma);

    // Rather than have libTIFF complain about tags it doesn't understand,
    // we just suppress all the warnings.
    TIFFSetWarningHandler(SuppressTIFFWarnings);
    TIFFSetErrorHandler(SuppressTIFFWarnings);

    // Open and do initial processing
    tif = TIFFClientOpen("Dummy File Name", "r", file,
        Tiff_Read, Tiff_Write, Tiff_Seek, Tiff_Close,
        Tiff_Size, Tiff_Map, Tiff_Unmap);
    if (!tif)
        return nullptr;

    // Get basic information about the image
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);
    TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &RowsPerStrip);
    TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &PhotometricInterpretation);
    TIFFGetField(tif, TIFFTAG_ORIENTATION, &Orientation);
    TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLESPERPIXEL, &SamplePerPixel);
    TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &ExtraSamples, &ExtraSampleInfo);

    // [CLi] TIFF provides alpha mode information in the TIFFTAG_EXTRASAMPLES field, so that's the preferred mode to use
    // for the image container unless the user overrides (e.g. to handle a non-compliant file).
    // If TIFFTAG_EXTRASAMPLES information is absent or inconclusive, we presume associated (= premultiplied) alpha.
    bool premul = true;
    if ((ExtraSamples > 0) && (ExtraSampleInfo[0] == EXTRASAMPLE_UNASSALPHA))
        premul = false;
    if (options.premultipliedOverride)
        premul = options.premultiplied;

    // don't support more than 16 bits per sample
    if (BitsPerSample == 16)
    {
        BytesPerSample = 2 ;
        options.warnings.push_back ("Warning: reading 16 bits/sample TIFF file; components crunched to 8");
    }

    LineSize = TIFFScanlineSize(tif);
    POV_IMAGE_ASSERT(SamplePerPixel == (int) (LineSize / width) / BytesPerSample);
    // SamplePerPixel = (int)(LineSize / width);

#if 0
    // For now we are ignoring the orientation of the image...
    switch (Orientation)
    {
    case ORIENTATION_TOPLEFT:
        break;
    case ORIENTATION_TOPRIGHT:
        break;
    case ORIENTATION_BOTRIGHT:
        break;
    case ORIENTATION_BOTLEFT:
        break;
    case ORIENTATION_LEFTTOP:
        break;
    case ORIENTATION_RIGHTTOP:
        break;
    case ORIENTATION_RIGHTBOT:
        break;
    case ORIENTATION_LEFTBOT:
        break;
    default:
        break;
    }
#endif

    //PhotometricInterpretation = 2 image is RGB
    //PhotometricInterpretation = 3 image have a color palette
    if ((PhotometricInterpretation == PHOTOMETRIC_PALETTE) && (TIFFIsTiled(tif) == 0))
    {
        uint16 *red, *green, *blue;

        //load the palette
        int cmap_len = (1 << BitsPerSample);

        TIFFGetField(tif, TIFFTAG_COLORMAP, &red, &green, &blue);

        std::vector<Image::RGBMapEntry> colormap;
        Image::RGBMapEntry entry;

        // I may be mistaken, but it appears that alpha/opacity information doesn't
        // appear in a Paletted Tiff image.  Well - if it does, it's not as easy to
        // get at as RGB.
        // Read the palette
        // Is the palette 16 or 8 bits ?
        if (checkcmap(cmap_len, red, green, blue) == 16)
        {
            for (int i=0;i<cmap_len;i++)
            {
                entry.red   = IntDecode(gamma, red[i],   65535);
                entry.green = IntDecode(gamma, green[i], 65535);
                entry.blue  = IntDecode(gamma, blue[i],  65535);
                colormap.push_back (entry);
            }
        }
        else
        {
            for (int i=0;i<cmap_len;i++)
            {
                entry.red   = IntDecode(gamma, red[i],   255);
                entry.green = IntDecode(gamma, green[i], 255);
                entry.blue  = IntDecode(gamma, blue[i],  255);
                colormap.push_back (entry);
            }
        }

        ImageDataType imagetype = options.itype;
        if (imagetype == ImageDataType::Undefined)
            imagetype = ImageDataType::Colour_Map;
        image = Image::Create (width, height, imagetype, colormap) ;
        image->SetPremultiplied(premul); // specify whether the color map data has premultiplied alpha

        std::unique_ptr<unsigned char[]> buf (new unsigned char [TIFFStripSize(tif)]);

        //read the tiff lines and save them in the image
        //with RGB mode, we have to change the order of the 3 samples RGB <=> BGR
        for (int row=0;row<height;row+=RowsPerStrip)
        {
            nrow = (row + (int)RowsPerStrip > height ? height - row : RowsPerStrip);
            TIFFReadEncodedStrip(tif, TIFFComputeStrip(tif, row, 0), buf.get(), nrow * LineSize);
            for (int l = 0, offset = 0; l < nrow ; l++, offset += LineSize)
                for (int x = 0 ; x < width ; x++)
                    image->SetIndexedValue (x, row+l, buf[offset+x]) ;
        }
    }
    else
    {
        // Allocate the row buffers for the image
        std::unique_ptr<uint32[]> buf (new uint32 [width * height]) ;

        ImageDataType imagetype = options.itype;
        if (imagetype == ImageDataType::Undefined)
            imagetype = Image::GetImageDataType(8, 3, true, gamma);
        image = Image::Create (width, height, imagetype) ;
        image->SetPremultiplied(premul); // set desired storage mode regarding alpha premultiplication
        image->TryDeferDecoding(gamma, 255); // try to have gamma adjustment being deferred until image evaluation.

        TIFFReadRGBAImage(tif, width, height, buf.get(), 0);
        uint32 abgr, *tbuf = buf.get();
        for (int i=height-1;i>=0;i--)
        {
            for (int j=0;j<width;j++)
            {
                abgr = *tbuf++;
                unsigned int b = (unsigned char)TIFFGetB(abgr);
                unsigned int g = (unsigned char)TIFFGetG(abgr);
                unsigned int r = (unsigned char)TIFFGetR(abgr);
                unsigned int a = (unsigned char)TIFFGetA(abgr);
                SetEncodedRGBAValue(image, j, i, gamma, 255, r, g, b, a, premul) ;
            }
        }
    }

    TIFFClose(tif);

    return (image) ;
}

void ImLoadImageToMat(const char* path, ImMat& mat, bool gray)
{
    auto file_suffix = ImGuiHelper::path_filename_suffix(path);
    if (file_suffix.compare(".tiff") == 0 || file_suffix.compare(".TIFF") == 0)
    {
#if IMGUI_TIFF
        TIFF* tif = TIFFOpen(path, "r");
        if (tif)
        {
            uint32_t w, h, c, imagelength;
            uint16_t depth, config;
            TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
            TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
            TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &c);
            TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &depth);
            TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &imagelength);
            TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);
            mat.create_type(w, h, c, depth == 8 ? IM_DT_INT8 : depth == 16 ? IM_DT_INT16 : IM_DT_FLOAT32);
            if (config == PLANARCONFIG_CONTIG)
            {
                mat.elempack = c;
                for (int row = 0; row < imagelength; row++)
                {
                    void* mat_row = (unsigned char*)mat.data + (size_t)mat.w * row * mat.elemsize * mat.elempack;
                    TIFFReadScanline(tif, mat_row, row);
                }
            }
            else if (config == PLANARCONFIG_SEPARATE)
            {
                // TODO::Dicky
            }
            TIFFClose(tif);
        }
#endif
        return;
    }

    int width = 0, height = 0, component = 0;
    if (auto data = stbi_load(path, &width, &height, &component, gray ? 1 : 4))
    {
        ImMat tmp;
        tmp.create_type(width, height, gray ? 1 : 4, data, IM_DT_INT8);
        tmp.elempack = gray ? 1 : 4;
        mat = tmp.clone();
        stbi_image_free(data);
    }
    else if (auto data = stbi_load_16(path, &width, &height, &component, gray ? 1 : 4))
    {
        ImMat tmp;
        tmp.create_type(width, height, gray ? 1 : 4, data, IM_DT_INT16);
        tmp.elempack = gray ? 1 : 4;
        mat = tmp.clone();
        stbi_image_free(data);
    }
}

EStatusCode TIFFImageHandler::ReadTopLevelTiffInformation()
{
	EStatusCode status = PDFHummus::eSuccess;
	tdir_t directorycount = TIFFNumberOfDirectories(mT2p->input);
	tdir_t i=0;
	uint16_t pagen=0;
	uint16_t paged=0;
	uint16_t xuint16_t=0;

	do
	{
		// allocate tiff pages
		mT2p->tiff_pages = (T2P_PAGE*) _TIFFmalloc(directorycount * sizeof(T2P_PAGE));
		if(!mT2p->tiff_pages)
		{
			TRACE_LOG2("Can't allocate %u bytes of memory for tiff_pages array, %s",
						directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}
		_TIFFmemset( mT2p->tiff_pages, 0x00, directorycount * sizeof(T2P_PAGE));

		// allocate tile array, one for each page
		mT2p->tiff_tiles = (T2P_TILES*) _TIFFmalloc(directorycount * sizeof(T2P_TILES));
		if(!mT2p->tiff_tiles)
		{
			TRACE_LOG2("Can't allocate %u bytes of memory for tiff_tiles array, %s",
						directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}
		_TIFFmemset( mT2p->tiff_tiles, 0x00, directorycount * sizeof(T2P_TILES));

		for(i=0;i<directorycount;i++)
		{
			bool isPage = false,isPage2 = false;
			uint32_t subfiletype = 0;

			if(!TIFFSetDirectory(mT2p->input, i)){
				TRACE_LOG2("Can't set directory %u of input file %s",
							directorycount * sizeof(T2P_PAGE),mT2p->inputFilePath.c_str());
				status = PDFHummus::eFailure;
				break;
			}			

			if(TIFFGetField(mT2p->input, TIFFTAG_PAGENUMBER, &pagen, &paged))
			{
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_number =
					((pagen>paged) && (paged != 0)) ? paged : pagen;
				isPage2 = true;
			} 
			else if(TIFFGetField(mT2p->input, TIFFTAG_SUBFILETYPE, &subfiletype))
			{
				isPage = ((subfiletype & FILETYPE_PAGE) != 0) || (subfiletype == 0);
			} 
			else if(TIFFGetField(mT2p->input, TIFFTAG_OSUBFILETYPE, &subfiletype))
			{
				isPage =	(subfiletype == OFILETYPE_IMAGE) || 
							(subfiletype == OFILETYPE_PAGE)	 || 
							(subfiletype == 0);
			}
			else
			{
				isPage = true;
				isPage2 = true;
			}

			if(isPage)
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_number=mT2p->tiff_pagecount;
			if(isPage || isPage2)
			{
				mT2p->tiff_pages[mT2p->tiff_pagecount].page_directory=i;
				if(TIFFIsTiled(mT2p->input))
				{
					mT2p->tiff_pages[mT2p->tiff_pagecount].page_tilecount = 
						TIFFNumberOfTiles(mT2p->input);
				}
			}
			mT2p->tiff_pagecount++;
		}
		if(status != PDFHummus::eSuccess)
			break;

		qsort((void*) mT2p->tiff_pages, mT2p->tiff_pagecount,sizeof(T2P_PAGE), t2p_cmp_t2p_page);

		for(i=0;i<mT2p->tiff_pagecount;i++)
		{
			TIFFSetDirectory(mT2p->input, mT2p->tiff_pages[i].page_directory );
			if((TIFFGetField(mT2p->input, TIFFTAG_PHOTOMETRIC, &xuint16_t)
						&& (xuint16_t==PHOTOMETRIC_PALETTE))
			   || TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16_t)) 
			{
				mT2p->tiff_pages[i].page_extra++;
			}
			if (TIFFGetField(mT2p->input, TIFFTAG_TRANSFERFUNCTION,
									 &(mT2p->tiff_transferfunction[0]),
									 &(mT2p->tiff_transferfunction[1]),
									 &(mT2p->tiff_transferfunction[2]))) 
			{
				if(mT2p->tiff_transferfunction[1] !=
				   mT2p->tiff_transferfunction[0]) 
				{
					mT2p->tiff_transferfunctioncount = 3;
					mT2p->tiff_pages[i].page_extra += 4;
				} 
				else 
				{
					mT2p->tiff_transferfunctioncount = 1;
					mT2p->tiff_pages[i].page_extra += 2;
				}
			} 
			else 
			{
				mT2p->tiff_transferfunctioncount=0;
			}
			if(TIFFGetField(
				mT2p->input, 
				TIFFTAG_ICCPROFILE, 
				&(mT2p->tiff_iccprofilelength), 
				&(mT2p->tiff_iccprofile)) != 0)
			{
				mT2p->tiff_pages[i].page_extra++;
			}
			mT2p->tiff_tiles[i].tiles_tilecount = mT2p->tiff_pages[i].page_tilecount;
			if( (TIFFGetField(mT2p->input, TIFFTAG_PLANARCONFIG, &xuint16_t) != 0)
				&& (xuint16_t == PLANARCONFIG_SEPARATE ) )
			{
					TIFFGetField(mT2p->input, TIFFTAG_SAMPLESPERPIXEL, &xuint16_t);
					mT2p->tiff_tiles[i].tiles_tilecount/= xuint16_t;
			}
			if( mT2p->tiff_tiles[i].tiles_tilecount > 0)
			{
				TIFFGetField(mT2p->input, 
					TIFFTAG_TILEWIDTH, 
					&(mT2p->tiff_tiles[i].tiles_tilewidth));
				TIFFGetField(mT2p->input, 
					TIFFTAG_TILELENGTH, 
					&(mT2p->tiff_tiles[i].tiles_tilelength) );
				mT2p->tiff_tiles[i].tiles_tiles = 
				(T2P_TILE*) _TIFFmalloc(
					mT2p->tiff_tiles[i].tiles_tilecount 
					* sizeof(T2P_TILE) );
				if(!mT2p->tiff_tiles[i].tiles_tiles)
				{

					TRACE_LOG2("Can't allocate %u bytes of memory for tiles, %s",
								mT2p->tiff_tiles[i].tiles_tilecount * sizeof(T2P_TILE),
								mT2p->inputFilePath.c_str());
					status = PDFHummus::eFailure;
					break;
				}
			}		
		}

	}while(false);
	return status;
}

EStatusCode TIFFImageHandler::ReadTIFFPageInformation() //t2p_read_tiff_data
{
	EStatusCode status = PDFHummus::eSuccess;
	uint16_t xuint16_t;
	uint16_t* xuint16_tp;
	float* xfloatp;

	mT2p->pdf_transcode = T2P_TRANSCODE_ENCODE;
	TIFFSetDirectory(mT2p->input, mT2p->tiff_pages[mT2p->pdf_page].page_directory);
	TIFFGetField(mT2p->input, TIFFTAG_IMAGEWIDTH, &(mT2p->tiff_width));

	do
	{
		TIFFGetField(mT2p->input, TIFFTAG_IMAGEWIDTH, &(mT2p->tiff_width));
		if(0 == mT2p->tiff_width)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with zero width",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}

		TIFFGetField(mT2p->input, TIFFTAG_IMAGELENGTH, &(mT2p->tiff_length));
		if(0 == mT2p->tiff_length)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with zero length",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
		}

        if(TIFFGetField(mT2p->input, TIFFTAG_COMPRESSION, &mT2p->tiff_compression) == 0)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with no compression tag",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;

        }

        if( TIFFIsCODECConfigured(mT2p->tiff_compression) == 0)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with compression type %u:  not configured", 
				mT2p->inputFilePath.c_str(),
				mT2p->tiff_compression);				
			status = PDFHummus::eFailure;
			break;
		}

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_BITSPERSAMPLE, &(mT2p->tiff_bitspersample));
		switch(mT2p->tiff_bitspersample)
		{
			case 1:
			case 2:
			case 4:
			case 8:
				break;
			case 0:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation: Image %s has 0 bits per sample, assuming 1", 
					mT2p->inputFilePath.c_str());				
				mT2p->tiff_bitspersample = 1;
				break;
			default:
				TRACE_LOG2(
					"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with %u bits per sample", 
					mT2p->inputFilePath.c_str(),
					mT2p->tiff_bitspersample);				
				status = PDFHummus::eFailure;
				break;
		}
		if(status != PDFHummus::eSuccess)
			break;

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_SAMPLESPERPIXEL, &(mT2p->tiff_samplesperpixel));
		if(mT2p->tiff_samplesperpixel>4)
		{
			TRACE_LOG2(
				"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with %u samples per pixel", 
				mT2p->inputFilePath.c_str(),
				mT2p->tiff_samplesperpixel);				
			status = PDFHummus::eFailure;
			break;
		}

		if(mT2p->tiff_samplesperpixel==0)
		{
			TRACE_LOG1(
				"TIFFImageHandler::ReadTIFFPageInformation: Image %s has 0 samples per pixel, assuming 1", 
				mT2p->inputFilePath.c_str());	
			mT2p->tiff_samplesperpixel=1;
		}

		if(TIFFGetField(mT2p->input, TIFFTAG_SAMPLEFORMAT, &xuint16_t) != 0 )
		{
			switch(xuint16_t)
			{
				case 0:
				case 1:
				case 4:
					break;
				default:
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation: No support for %s with sample format %u", 
						mT2p->inputFilePath.c_str(),
						xuint16_t);				
					status = PDFHummus::eFailure;
					break;
			}
		}
		if(status != PDFHummus::eSuccess)
				break;

		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_FILLORDER, &(mT2p->tiff_fillorder));
        if(TIFFGetField(mT2p->input, TIFFTAG_PHOTOMETRIC, &(mT2p->tiff_photometric)) == 0)
		{
			TRACE_LOG1("TIFFImageHandler::ReadTIFFPageInformation: No support for %s with no photometric interpretation tag",
						mT2p->inputFilePath.c_str());
			status = PDFHummus::eFailure;
			break;
        }

		switch(mT2p->tiff_photometric)
		{
			case PHOTOMETRIC_MINISWHITE:
			case PHOTOMETRIC_MINISBLACK: 
				if (mT2p->tiff_bitspersample==1){
					mT2p->pdf_colorspace=T2P_CS_BILEVEL;
					if(mT2p->tiff_photometric==PHOTOMETRIC_MINISWHITE){
						mT2p->pdf_switchdecode ^= 1;
					}
				} else {
					mT2p->pdf_colorspace=T2P_CS_GRAY;
					if(mT2p->tiff_photometric==PHOTOMETRIC_MINISWHITE){
						mT2p->pdf_switchdecode ^= 1;
					} 
				}
				break;
			case PHOTOMETRIC_RGB: 
				mT2p->pdf_colorspace=T2P_CS_RGB;
				if(mT2p->tiff_samplesperpixel == 3)
				{
					break;
				}
				if(TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16_t))
				{
					if(xuint16_t==1)
						ReadPhotometricPalette();
				}
				if(mT2p->tiff_samplesperpixel > 3) 
				{
					if(mT2p->tiff_samplesperpixel == 4) 
					{
						mT2p->pdf_colorspace = T2P_CS_RGB;
						if(TIFFGetField(mT2p->input,
								TIFFTAG_EXTRASAMPLES,
								&xuint16_t, &xuint16_tp)
						   && xuint16_t == 1) 
						{
							if(xuint16_tp[0] == EXTRASAMPLE_ASSOCALPHA)
							{
								mT2p->pdf_sample=T2P_SAMPLE_RGBAA_TO_RGB;
								break;
							}
							if(xuint16_tp[0] == EXTRASAMPLE_UNASSALPHA)
							{
								mT2p->pdf_sample=T2P_SAMPLE_RGBA_TO_RGB;
								break;
							}
							TRACE_LOG1(
								"TIFFImageHandler::ReadTIFFPageInformation, RGB image %s has 4 samples per pixel, assuming RGBA",
								mT2p->inputFilePath.c_str());
							break;
						}
						mT2p->pdf_colorspace=T2P_CS_CMYK;
						mT2p->pdf_switchdecode ^= 1;
						TRACE_LOG1(
							"TIFFImageHandler::ReadTIFFPageInformation, RGB image %s has 4 samples per pixel, assuming CMYK",
							mT2p->inputFilePath.c_str());
						break;
					} 
					else 
					{
						TRACE_LOG2(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for RGB image %s with %u samples per pixel",
							mT2p->inputFilePath.c_str(),
							mT2p->tiff_samplesperpixel
							);
						status = PDFHummus::eFailure;
						break;
					}
				} else {
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for RGB image %s with %u samples per pixel",
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_samplesperpixel
						);
					status = PDFHummus::eFailure;
					break;
				}
			case PHOTOMETRIC_PALETTE: 
				status = ReadPhotometricPalette();
				break;
			case PHOTOMETRIC_SEPARATED:
				if(TIFFGetField(mT2p->input, TIFFTAG_INDEXED, &xuint16_t))
				{
					if(xuint16_t==1)
						ReadPhotometricPaletteCMYK();
				}
				if( TIFFGetField(mT2p->input, TIFFTAG_INKSET, &xuint16_t) )
				{
					if(xuint16_t != INKSET_CMYK)
					{
						TRACE_LOG1(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for %s because its inkset is not CMYK",
							mT2p->inputFilePath.c_str()
							);
						status = PDFHummus::eFailure;
						break;
					}
				}
				if(mT2p->tiff_samplesperpixel==4)
				{
					mT2p->pdf_colorspace=T2P_CS_CMYK;
				} 
				else 
				{
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for %s because it has %u samples per pixel",
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_samplesperpixel
						);
					status = PDFHummus::eFailure;
					break;
				}
				break;
			case PHOTOMETRIC_YCBCR:
				mT2p->pdf_colorspace=T2P_CS_RGB;
				if(mT2p->tiff_samplesperpixel==1)
				{
					mT2p->pdf_colorspace=T2P_CS_GRAY;
					mT2p->tiff_photometric=PHOTOMETRIC_MINISBLACK;
					break;
				}
				mT2p->pdf_sample=T2P_SAMPLE_YCBCR_TO_RGB;
				break;
			case PHOTOMETRIC_CIELAB:
				mT2p->pdf_labrange[0]= -127;
				mT2p->pdf_labrange[1]= 127;
				mT2p->pdf_labrange[2]= -127;
				mT2p->pdf_labrange[3]= 127;
				mT2p->pdf_sample=T2P_SAMPLE_LAB_SIGNED_TO_UNSIGNED;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_ICCLAB:
				mT2p->pdf_labrange[0]= 0;
				mT2p->pdf_labrange[1]= 255;
				mT2p->pdf_labrange[2]= 0;
				mT2p->pdf_labrange[3]= 255;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_ITULAB:
				mT2p->pdf_labrange[0]=-85;
				mT2p->pdf_labrange[1]=85;
				mT2p->pdf_labrange[2]=-75;
				mT2p->pdf_labrange[3]=124;
				mT2p->pdf_sample=T2P_SAMPLE_LAB_SIGNED_TO_UNSIGNED;
				mT2p->pdf_colorspace=T2P_CS_LAB;
				break;
			case PHOTOMETRIC_LOGL:
			case PHOTOMETRIC_LOGLUV:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with photometric interpretation LogL/LogLuv",
					mT2p->inputFilePath.c_str()
					);
				status = PDFHummus::eFailure;
				break;
			default:
				TRACE_LOG1(
					"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with photometric interpretation %u",
					mT2p->inputFilePath.c_str()
					);
				status = PDFHummus::eFailure;
				break;
		}
		if(status != PDFHummus::eSuccess)
			break;

		if(TIFFGetField(mT2p->input, TIFFTAG_PLANARCONFIG, &(mT2p->tiff_planar)))
		{
			switch(mT2p->tiff_planar)
			{
				case 0:
					TRACE_LOG1(
						"TIFFImageHandler::ReadTIFFPageInformation, Image %s has planar configuration 0, assuming 1", 
						mT2p->inputFilePath.c_str());
					mT2p->tiff_planar=PLANARCONFIG_CONTIG;
				case PLANARCONFIG_CONTIG:
					break;
				case PLANARCONFIG_SEPARATE:
					mT2p->pdf_sample=T2P_SAMPLE_PLANAR_SEPARATE_TO_CONTIG;
					if(mT2p->tiff_bitspersample!=8)
					{
						TRACE_LOG2(
							"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with separated planar configuration and %u bits per sample", 
							mT2p->inputFilePath.c_str(),
							mT2p->tiff_bitspersample);
						status = PDFHummus::eFailure;
						break;
					}
					break;
				default:
					TRACE_LOG2(
						"TIFFImageHandler::ReadTIFFPageInformation, No support for %s with planar configuration %u", 
						mT2p->inputFilePath.c_str(),
						mT2p->tiff_planar);
					status = PDFHummus::eFailure;
					break;
			}
			if(status != PDFHummus::eSuccess)
				break;
		}

        TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_ORIENTATION,
                              &(mT2p->tiff_orientation));
        if(mT2p->tiff_orientation>8)
		{
                TRACE_LOG2("TIFFImageHandler::ReadTIFFPageInformation, Image %s has orientation %u, assuming 0",
                            mT2p->inputFilePath.c_str(), 
							mT2p->tiff_orientation);
                mT2p->tiff_orientation=0;
        }

        if(TIFFGetField(mT2p->input, TIFFTAG_XRESOLUTION, &(mT2p->tiff_xres) ) == 0)
		{
                mT2p->tiff_xres=0.0;
        }
        if(TIFFGetField(mT2p->input, TIFFTAG_YRESOLUTION, &(mT2p->tiff_yres) ) == 0)
		{
                mT2p->tiff_yres=0.0;
        }
		TIFFGetFieldDefaulted(mT2p->input, TIFFTAG_RESOLUTIONUNIT,&(mT2p->tiff_resunit));
		if(mT2p->tiff_resunit == RESUNIT_CENTIMETER) 
		{
			mT2p->tiff_xres *= 2.54F;
			mT2p->tiff_yres *= 2.54F;
		} 
		else if (mT2p->tiff_resunit != RESUNIT_INCH && mT2p->pdf_centimeters != 0) 
		{
			mT2p->tiff_xres *= 2.54F;
			mT2p->tiff_yres *= 2.54F;
		}

		ComposePDFPage();

		mT2p->pdf_transcode = T2P_TRANSCODE_ENCODE;
		if(mT2p->pdf_nopassthrough==0)
		{
			if(mT2p->tiff_compression==COMPRESSION_CCITTFAX4)
			{
				if(TIFFIsTiled(mT2p->input) || (TIFFNumberOfStrips(mT2p->input)==1) )
				{
					mT2p->pdf_transcode = T2P_TRANSCODE_RAW;
					mT2p->pdf_compression=T2P_COMPRESS_G4;
				}
			}
			if(mT2p->tiff_compression== COMPRESSION_ADOBE_DEFLATE 
				|| mT2p->tiff_compression==COMPRESSION_DEFLATE)
			{
				if(TIFFIsTiled(mT2p->input) || (TIFFNumberOfStrips(mT2p->input)==1) )
				{
					mT2p->pdf_transcode = T2P_TRANSCODE_RAW;
					mT2p->pdf_compression=T2P_COMPRESS_ZIP;
				}
			}
		}

		if(mT2p->pdf_transcode!=T2P_TRANSCODE_RAW)
		{
			mT2p->pdf_compression = mT2p->pdf_defaultcompression;
		}

		if(mT2p->pdf_sample & T2P_SAMPLE_REALIZE_PALETTE)
		{
			if(mT2p->pdf_colorspace & T2P_CS_CMYK)
			{
				mT2p->tiff_samplesperpixel=4;
				mT2p->tiff_photometric=PHOTOMETRIC_SEPARATED;
			} 
			else 
			{
				mT2p->tiff_samplesperpixel=3;
				mT2p->tiff_photometric=PHOTOMETRIC_RGB;
			}
		}

		if (TIFFGetField(mT2p->input, TIFFTAG_TRANSFERFUNCTION,
				 &(mT2p->tiff_transferfunction[0]),
				 &(mT2p->tiff_transferfunction[1]),
				 &(mT2p->tiff_transferfunction[2]))) 
		{
			if(mT2p->tiff_transferfunction[1] !=
			   mT2p->tiff_transferfunction[0]) 
			{
				mT2p->tiff_transferfunctioncount=3;
			} 
			else 
			{
				mT2p->tiff_transferfunctioncount=1;
			}
		} 
		else 
		{
			mT2p->tiff_transferfunctioncount=0;
		}
		if(TIFFGetField(mT2p->input, TIFFTAG_WHITEPOINT, &xfloatp)!=0)
		{
			mT2p->tiff_whitechromaticities[0]=xfloatp[0];
			mT2p->tiff_whitechromaticities[1]=xfloatp[1];
			if(mT2p->pdf_colorspace & T2P_CS_GRAY)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALGRAY);
			}
			if(mT2p->pdf_colorspace & T2P_CS_RGB)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALRGB);
			}
		}
		if(TIFFGetField(mT2p->input, TIFFTAG_PRIMARYCHROMATICITIES, &xfloatp)!=0)
		{
			mT2p->tiff_primarychromaticities[0]=xfloatp[0];
			mT2p->tiff_primarychromaticities[1]=xfloatp[1];
			mT2p->tiff_primarychromaticities[2]=xfloatp[2];
			mT2p->tiff_primarychromaticities[3]=xfloatp[3];
			mT2p->tiff_primarychromaticities[4]=xfloatp[4];
			mT2p->tiff_primarychromaticities[5]=xfloatp[5];
			if(mT2p->pdf_colorspace & T2P_CS_RGB)
			{
				mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_CALRGB);
			}
		}
		if(mT2p->pdf_colorspace & T2P_CS_LAB)
		{
			if(TIFFGetField(mT2p->input, TIFFTAG_WHITEPOINT, &xfloatp) != 0)
			{
				mT2p->tiff_whitechromaticities[0]=xfloatp[0];
				mT2p->tiff_whitechromaticities[1]=xfloatp[1];
			} 
			else 
			{
				mT2p->tiff_whitechromaticities[0]=0.3457F; /* 0.3127F; */
				mT2p->tiff_whitechromaticities[1]=0.3585F; /* 0.3290F; */
			}
		}
		if(TIFFGetField(mT2p->input, 
			TIFFTAG_ICCPROFILE, 
			&(mT2p->tiff_iccprofilelength), 
			&(mT2p->tiff_iccprofile))!=0){
			mT2p->pdf_colorspace = (t2p_cs_t)(mT2p->pdf_colorspace | T2P_CS_ICCBASED);
		} 
		else 
		{
			mT2p->tiff_iccprofilelength=0;
			mT2p->tiff_iccprofile=NULL;
		}

		if( mT2p->tiff_bitspersample==1 &&
			mT2p->tiff_samplesperpixel==1)
		{
			mT2p->pdf_compression = T2P_COMPRESS_G4;
		}
	}while(false);

	return status;
}

void TIFFImageHandler::CalculateTiffTileSize(int inTileIndex)
{
	uint16_t edge=0;

	edge |= (uint16_t)TileIsRightEdge(inTileIndex);
	edge |= (uint16_t)TileIsBottomEdge(inTileIndex);

	if(mT2p->pdf_transcode==T2P_TRANSCODE_RAW)
	{
		if(edge)
		{
			mT2p->tiff_datasize=TIFFTileSize(mT2p->input);
		} else 
		{
			// 	TIFFTAG_TILEBYTECOUNTS changed in tiff 4.0.0;

			tsize_t_compat* tbc = NULL;
			TIFFGetField(mT2p->input, TIFFTAG_TILEBYTECOUNTS, &tbc);
			mT2p->tiff_datasize=static_cast<tsize_t>(tbc[inTileIndex]);
		}
	}
	else
	{
		mT2p->tiff_datasize=TIFFTileSize(mT2p->input);
		if(mT2p->tiff_planar==PLANARCONFIG_SEPARATE)
			mT2p->tiff_datasize*= mT2p->tiff_samplesperpixel;
	}
}

bool tiffToQImage( const char * data, int size, QImage & image )
{
  QByteArray arr = QByteArray::fromRawData( data, size );
  QBuffer buf;
  buf.setData( arr );
  buf.open( QIODevice::ReadOnly );

  TIFF *const tiff = TIFFClientOpen( "foo",
                                     "r",
                                     &buf,
                                     tiffReadProc,
                                     tiffWriteProc,
                                     tiffSeekProc,
                                     tiffCloseProc,
                                     tiffSizeProc,
                                     tiffMapProc,
                                     tiffUnmapProc );
  if( !tiff )
    return false;

  Uint32 width, height;
  if( !TIFFGetField( tiff, TIFFTAG_IMAGEWIDTH, &width )
      || !TIFFGetField( tiff, TIFFTAG_IMAGELENGTH, &height ) )
  {
    TIFFClose( tiff );
    return false;
  }

  Uint16 bitPerSample;
  if( !TIFFGetField( tiff, TIFFTAG_BITSPERSAMPLE, &bitPerSample ) )
    bitPerSample = 1;

  Uint16 samplesPerPixel; // they may be e.g. grayscale with 2 samples per pixel
  if( !TIFFGetField( tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel ) )
    samplesPerPixel = 1;

  if( bitPerSample == 1 && samplesPerPixel == 1 )
  {
    QImage tiffImage( width, height, QImage::Format_Mono );

    QVector<QRgb> colortable( 2 );
    colortable[0] = 0xffffffff;
    colortable[1] = 0xff000000;
    tiffImage.setColorTable( colortable );

    for ( Uint32 y = 0; y < height; ++y )
    {
      if( TIFFReadScanline( tiff, tiffImage.scanLine( y ), y, 0 ) < 0 )
      {
        TIFFClose( tiff );
        return false;
      }
    }

    image = tiffImage;
    TIFFClose( tiff );
    return true;
  }

  TIFFClose( tiff );
  return false;
}

ImageHeaders
load_tiff_file_headers (std::string const& filename)
{
    ImageHeaders headers;
    TIFFSetWarningHandler(nullptr);
    TIFFSetErrorHandler(tiff_error_handler);

    TIFF* tif = TIFFOpen(filename.c_str(), "r");
    if (tif == nullptr)
        throw util::FileException(filename, "TIFF file format not recognized");

    try
    {
        uint32_t width, height;
        uint16_t channels, bits, sampleFormat;
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &channels);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits);
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        headers.width = width;
        headers.height = height;
        headers.channels = channels;
        headers.type = IMAGE_TYPE_UNKNOWN;

        if (bits == 8) {
            switch(sampleFormat) {
                case SAMPLEFORMAT_UINT:
                    headers.type = IMAGE_TYPE_UINT8;
                    break;
                case SAMPLEFORMAT_INT:
                    headers.type = IMAGE_TYPE_SINT8;
                    break;
                case SAMPLEFORMAT_IEEEFP:
                    headers.type = IMAGE_TYPE_FLOAT;
                    break;
                default:
                    break;
            }
        } else if (bits == 16) {
            switch(sampleFormat) {
                case SAMPLEFORMAT_UINT:
                    headers.type = IMAGE_TYPE_UINT16;
                    break;
                case SAMPLEFORMAT_INT:
                    headers.type = IMAGE_TYPE_SINT16;
                    break;
                case SAMPLEFORMAT_IEEEFP:
                    headers.type = IMAGE_TYPE_FLOAT;
                    break;
                default:
                    break;
            }
        } else if (bits == 32) {
            switch(sampleFormat) {
                case SAMPLEFORMAT_UINT:
                    headers.type = IMAGE_TYPE_UINT32;
                    break;
                case SAMPLEFORMAT_INT:
                    headers.type = IMAGE_TYPE_SINT32;
                    break;
                case SAMPLEFORMAT_IEEEFP:
                    headers.type = IMAGE_TYPE_FLOAT;
                    break;
                default:
                    break;
            }
        } else if (bits == 64) {
            switch(sampleFormat) {
                case SAMPLEFORMAT_UINT:
                    headers.type = IMAGE_TYPE_UINT64;
                    break;
                case SAMPLEFORMAT_INT:
                    headers.type = IMAGE_TYPE_SINT64;
                    break;
                case SAMPLEFORMAT_IEEEFP:
                    headers.type = IMAGE_TYPE_FLOAT;
                    break;
                default:
                    break;
            }
        }

        if (headers.type == IMAGE_TYPE_UNKNOWN){
            throw util::Exception("TIFF file has unsupported bits and/or sample format.");
        }

        TIFFClose(tif);
        return headers;
    }
    catch (std::exception& e)
    {
        TIFFClose(tif);
        throw;
    }
}

ByteImage::Ptr
load_tiff_file (std::string const& filename)
{
    TIFFSetWarningHandler(nullptr);
    TIFFSetErrorHandler(tiff_error_handler);

    TIFF* tif = TIFFOpen(filename.c_str(), "r");
    if (!tif)
        throw util::Exception("TIFF file format not recognized");

    try
    {
        /* Read width and height from TIFF and create MVE image. */
        uint32_t width, height;
        uint16_t channels, bits;
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &channels);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits);
        if (bits != 8)
            throw util::Exception("Expected 8 bit TIFF file");
        ByteImage::Ptr image = ByteImage::create(width, height, channels);

        /* Scanline based TIFF reading. */
        uint32_t rowstride = TIFFScanlineSize(tif);
        ByteImage::ImageData& data = image->get_data();
        for (uint32_t row = 0; row < height; row++)
        {
            tdata_t row_pointer = &data[row * rowstride];
            TIFFReadScanline(tif, row_pointer, row);
        }

        TIFFClose(tif);
        return image;
    }
    catch (std::exception& e)
    {
        TIFFClose(tif);
        throw;
    }
}

