int R2Image::
ReadTIFFFile(const char *filename)
{
#ifdef RN_USE_TIFF
  // Open file
  TIFF* tif = TIFFOpen(filename, "r");
  if (!tif) {
    RNFail("Unable to open TIFF file %s\n", filename);
    return 0;
  }

  // Get image dimensions
  uint32 w, h;
  TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
  TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
  size_t npixels = w * h;

  // Allocate buffer for data
  uint32* raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
  if (!raster) {
    RNFail("Unable to allocate data for TIFF file %s\n", filename);
    return 0;
  }

  // Read data into buffer
  if (!TIFFReadRGBAImage(tif, w, h, raster, 0)) {
    RNFail("Unable to read TIFF file %s\n", filename);
    return 0;
  }

  // Initialize R2Image data
  width = w;
  height = h;
  ncomponents = 3;
  rowsize = ncomponents * width;
  if ((rowsize % 4) != 0) rowsize = (rowsize / 4 + 1) * 4;
  int nbytes = rowsize * height;
  pixels = new unsigned char [nbytes];
  if (!pixels) {
    RNFail("Unable to allocate memory for TIFF file %s", filename);
    return 0;
  }

  // Fill R2Image pixel data 
  uint32 *rasterp = raster;
  for (int j = 0; j < height; j++) {
    unsigned char *p = &pixels[j*rowsize];
    for (int i = 0; i < width; i++) {
      uint32 pixel = *(rasterp++);
      *(p++) = pixel & 0xFF;
      *(p++) = (pixel >> 8) & 0xFF;
      *(p++) = (pixel >> 16) & 0xFF;
    }
  }

  // Free data
  _TIFFfree(raster);

  // Close file
  TIFFClose(tif);

  // Return success
  return 1;
#else
  RNFail("TIFF not supported");
  return 0;
#endif
}

void tiff_dims (TIFF *tiff_handle, std::vector<long> &fdims)
    {
        assert(fdims.size() == 2);
        uint32_t image_length, image_width;
        if (! TIFFGetField(tiff_handle, TIFFTAG_IMAGELENGTH, &image_length))
            throw std::runtime_error("unable to get image length");
        if (! image_length)
            throw std::runtime_error("dimension along column is not positive");
        if (! TIFFGetField(tiff_handle, TIFFTAG_IMAGEWIDTH, &image_width))
            throw std::runtime_error("unable to get image width");
        if (! image_width)
            throw std::runtime_error("dimension along row is not positive");
        fdims[0] = image_length;
        fdims[1] = image_width;
    }

void moench_read_data (unsigned int tid,
                           f_type * destination,
                           const std::string &path,
                           const std::vector<mwSize> &mdims,
                           const std::vector<long> &im_ctr)
    {
        /*
        std::vector<long> fdims{0, 0};
        std::vector<float> fbuf(0);
        {
            TIFF *tiff_handle = TIFFOpen(path.c_str(), "r");
            if (! tiff_handle)
                throw std::runtime_error(std::string("unable to open file ") + path);
            try {
                tiff_dims(tiff_handle, fdims);
                long nelems = fdims[0] * fdims[1];
                fbuf.resize(nelems);
                tmsize_t res, sz = fdims[1] * sizeof(float);
                for (uint32_t strip=0; strip<fdims[0]; strip++) {
                    float *pos = &fbuf[fdims[1] * strip];
                    res = TIFFReadRawStrip(tiff_handle, strip, pos, sz);
                    if (res != sz)
                        throw std::runtime_error("unable to read tiff data");
                }
            } catch (...) {
                TIFFClose(tiff_handle);
                throw;
            }
            TIFFClose(tiff_handle);
        }
        */
        std::vector<long> fdims{0, 0};
        std::vector<f_type> fbuf(0);
        {
            TIFF *tiff_handle = TIFFOpen(path.c_str(), "r");
            if (! tiff_handle)
                throw std::runtime_error(std::string("unable to open file ") + path);
            try {
                tiff_dims(tiff_handle, fdims);
                long nelems = fdims[0] * fdims[1];
                fbuf.resize(nelems);
                std::uint16_t bps;
                if (TIFFGetField(tiff_handle, TIFFTAG_BITSPERSAMPLE, &bps) != 1)
                    throw std::runtime_error(std::string("unable to read number of bits per sample for file ") + path);
                std::uint16_t format;
                if (TIFFGetFieldDefaulted(tiff_handle, TIFFTAG_SAMPLEFORMAT, &format) != 1)
                    throw std::runtime_error(std::string("unable to read sample format for file") + path);
                tstrip_t num_strips = TIFFNumberOfStrips(tiff_handle);
                tsize_t strip_sz = TIFFStripSize(tiff_handle);
                std::vector<char> cbuf(strip_sz);
                switch (format) {
                case 1:
                    switch (bps) {
                    case 8:
                        tiff_read<std::uint8_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    case 16:
                        tiff_read<std::uint16_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    case 32:
                        tiff_read<std::uint32_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    default:
                        throw std::runtime_error(std::string("unsupported number of bits per unsigned integer sample in file ") + path);
                    }
                    break;
                case 2:
                    switch (bps) {
                    case 8:
                        tiff_read<std::int8_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    case 16:
                        tiff_read<std::int16_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    case 32:
                        tiff_read<std::int32_t, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    default:
                        throw std::runtime_error(std::string("unsupported number of bits per integer sample in file ") + path);
                    }
                    break;
                case 3:
                    switch (bps) {
                    case 32:
                        tiff_read<float, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    case 64:
                        tiff_read<double, f_type>(tiff_handle, cbuf, fbuf, num_strips); break;
                    default:
                        throw std::runtime_error(std::string("unsupported number of bits per ieee sample in file ") + path);
                    }
                    break;
                default:
                    throw std::runtime_error(std::string("unsupported sample format in file ") + path);
                }
            } catch (...) {
                TIFFClose(tiff_handle);
                throw;
            }
            TIFFClose(tiff_handle);
        }
        std::vector<long> fstride{0, 0};
        std::vector<long> mstride{0, 0};
        std::vector<long> count{0, 0};
        adapt_dimensions(fdims, mdims, im_ctr, fstride, mstride, count);
        DEBUG {
            LOCK(mutex_cout);
            OUT << thread_id << ":     fstride=[" << fstride[0] << ',' << fstride[1] << "], mstride=[" << mstride[0] << ',' << mstride[1] << "], count=[" << count[0] << ',' << count[1] <<']' << std::endl;
        }

bool CxImageTIF::Decode(CxFile * hFile)
{
  //Comment this line if you need more information on errors
  // TIFFSetErrorHandler(NULL);  //<Patrick Hoffmann>

  //Open file and fill the TIFF structure
  // m_tif = TIFFOpen(imageFileName,"rb");
  TIFF* m_tif = _TIFFOpenEx(hFile, "rb");

  uint32 height=0;
  uint32 width=0;
  uint16 bitspersample=1;
  uint16 samplesperpixel=1;
  uint32 rowsperstrip=(DWORD)-1;
  uint16 photometric=0;
  uint16 compression=1;
  uint16 orientation=ORIENTATION_TOPLEFT; //<vho>
  uint16 res_unit; //<Trifon>
  uint32 x, y;
  float resolution, offset;
  BOOL isRGB;
  BYTE *bits;    //pointer to source data
  BYTE *bits2;  //pointer to destination data

  cx_try
  {
  //check if it's a tiff file
  if (!m_tif)
    cx_throw("Error encountered while opening TIFF file");

  // <Robert Abram> - 12/2002 : get NumFrames directly, instead of looping
  // info.nNumFrames=0;
  // while(TIFFSetDirectory(m_tif,(uint16)info.nNumFrames)) info.nNumFrames++;
  info.nNumFrames = TIFFNumberOfDirectories(m_tif);

  if (!TIFFSetDirectory(m_tif, (uint16)info.nFrame))
    cx_throw("Error: page not present in TIFF file");      

  //get image info
  TIFFGetField(m_tif, TIFFTAG_IMAGEWIDTH, &width);
  TIFFGetField(m_tif, TIFFTAG_IMAGELENGTH, &height);
  TIFFGetField(m_tif, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);
  TIFFGetField(m_tif, TIFFTAG_BITSPERSAMPLE, &bitspersample);
  TIFFGetField(m_tif, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);   
  TIFFGetField(m_tif, TIFFTAG_PHOTOMETRIC, &photometric);
  TIFFGetField(m_tif, TIFFTAG_ORIENTATION, &orientation);

  if (info.nEscape == -1) {
    // Return output dimensions only
    head.biWidth = width;
    head.biHeight = height;
    info.dwType = CXIMAGE_FORMAT_TIF;
    cx_throw("output dimensions returned");
  }

  TIFFGetFieldDefaulted(m_tif, TIFFTAG_RESOLUTIONUNIT, &res_unit);
  if (TIFFGetField(m_tif, TIFFTAG_XRESOLUTION, &resolution))
  {
    if (res_unit == RESUNIT_CENTIMETER) resolution = (float)(resolution*2.54f + 0.5f);
    SetXDPI((long)resolution);
  }
  if (TIFFGetField(m_tif, TIFFTAG_YRESOLUTION, &resolution))
  {
    if (res_unit == RESUNIT_CENTIMETER) resolution = (float)(resolution*2.54f + 0.5f);
    SetYDPI((long)resolution);
  }

  if (TIFFGetField(m_tif, TIFFTAG_XPOSITION, &offset))  info.xOffset = (long)offset;
  if (TIFFGetField(m_tif, TIFFTAG_YPOSITION, &offset))  info.yOffset = (long)offset;

  head.biClrUsed=0;
  info.nBkgndIndex =-1;

  if (rowsperstrip>height){
    rowsperstrip=height;
    TIFFSetField(m_tif, TIFFTAG_ROWSPERSTRIP, rowsperstrip);
  }

  isRGB = /*(bitspersample >= 8) && (VK: it is possible so for RGB to have < 8 bpp!)*/
    (photometric == PHOTOMETRIC_RGB) ||
    (photometric == PHOTOMETRIC_YCBCR) ||
    (photometric == PHOTOMETRIC_SEPARATED) ||
    (photometric == PHOTOMETRIC_LOGL) ||
    (photometric == PHOTOMETRIC_LOGLUV);

  if (isRGB){
    head.biBitCount=24;
  }else{
    if ((photometric==PHOTOMETRIC_MINISBLACK)||(photometric==PHOTOMETRIC_MINISWHITE)||(photometric==PHOTOMETRIC_PALETTE)){
      if  (bitspersample == 1){
        head.biBitCount=1;    //B&W image
        head.biClrUsed =2;
      } else if (bitspersample == 4) {
        head.biBitCount=4;    //16 colors gray scale
        head.biClrUsed =16;
      } else {
        head.biBitCount=8;    //gray scale
        head.biClrUsed =256;
      }
    } else if (bitspersample == 4) {
      head.biBitCount=4;      // 16 colors
      head.biClrUsed=16;
    } else {
      head.biBitCount=8;      //256 colors
      head.biClrUsed=256;
    }

    if ((bitspersample > 8) && (photometric==PHOTOMETRIC_PALETTE))  // + VK + (BIG palette! => convert to RGB)
    {  head.biBitCount=24;
      head.biClrUsed =0;
    }
  }

  if (info.nEscape) cx_throw("Cancelled"); // <vho> - cancel decoding

  Create(width,height,head.biBitCount,CXIMAGE_FORMAT_TIF);  //image creation
  if (!pDib) cx_throw("CxImageTIF can't create image");

#if CXIMAGE_SUPPORT_ALPHA
  if (samplesperpixel==4) AlphaCreate();  //add alpha support for 32bpp tiffs
  if (samplesperpixel==2 && bitspersample==8) AlphaCreate();  //add alpha support for 8bpp + alpha
#endif //CXIMAGE_SUPPORT_ALPHA

  TIFFGetField(m_tif, TIFFTAG_COMPRESSION, &compression);
  SetCodecOption(compression); // <DPR> save original compression type

  if (isRGB) {
    // Read the whole image into one big RGBA buffer using
    // the traditional TIFFReadRGBAImage() API that we trust.
    uint32* raster;    // retrieve RGBA image
    uint32 *row;

    raster = (uint32*)_TIFFmalloc(width * height * sizeof (uint32));
    if (raster == NULL) cx_throw("No space for raster buffer");

    // Read the image in one chunk into an RGBA array
    if(!TIFFReadRGBAImage(m_tif, width, height, raster, 1)) {
        _TIFFfree(raster);
        cx_throw("Corrupted TIFF file!");
    }

    // read the raster lines and save them in the DIB
    // with RGB mode, we have to change the order of the 3 samples RGB
    row = &raster[0];
    bits2 = info.pImage;
    for (y = 0; y < height; y++) {

      if (info.nEscape){ // <vho> - cancel decoding
        _TIFFfree(raster);
        cx_throw("Cancelled");
      }

      bits = bits2;
      for (x = 0; x < width; x++) {
        *bits++ = (BYTE)TIFFGetB(row[x]);
        *bits++ = (BYTE)TIFFGetG(row[x]);
        *bits++ = (BYTE)TIFFGetR(row[x]);
#if CXIMAGE_SUPPORT_ALPHA
        if (samplesperpixel==4) AlphaSet(x,y,(BYTE)TIFFGetA(row[x]));
#endif //CXIMAGE_SUPPORT_ALPHA
      }
      row += width;
      bits2 += info.dwEffWidth;
    }
    _TIFFfree(raster);
  } else {
    int BIG_palette = (bitspersample > 8) &&  // + VK
              (photometric==PHOTOMETRIC_PALETTE);    
    if (BIG_palette && (bitspersample > 24))  // + VK
      cx_throw("Too big palette to handle");    // + VK

    RGBQUAD *pal;
    pal=(RGBQUAD*)calloc(BIG_palette ? 1<<bitspersample : 256,sizeof(RGBQUAD)); 
      // ! VK: it coasts nothing but more correct to use 256 as temp palette storage
      // ! VK: but for case of BIG palette it just copied
    if (pal==NULL) cx_throw("Unable to allocate TIFF palette");

    int bpp = bitspersample <= 8 ? bitspersample : 8; // + VK (to use instead of bitspersample for case of > 8)

    // set up the colormap based on photometric  
    switch(photometric) {
      case PHOTOMETRIC_MINISBLACK:  // bitmap and greyscale image types
      case PHOTOMETRIC_MINISWHITE:
        if (bitspersample == 1) {  // Monochrome image
          if (photometric == PHOTOMETRIC_MINISBLACK) {
            pal[1].rgbRed = pal[1].rgbGreen = pal[1].rgbBlue = 255;
          } else {
            pal[0].rgbRed = pal[0].rgbGreen = pal[0].rgbBlue = 255;
          }
        } else {    // need to build the scale for greyscale images
          if (photometric == PHOTOMETRIC_MINISBLACK) {
            for (int i=0; i<(1<<bpp); i++){
              pal[i].rgbRed = pal[i].rgbGreen = pal[i].rgbBlue = (BYTE)(i*(255/((1<<bpp)-1)));
            }
          } else {
            for (int i=0; i<(1<<bpp); i++){
              pal[i].rgbRed = pal[i].rgbGreen = pal[i].rgbBlue = (BYTE)(255-i*(255/((1<<bpp)-1)));
            }
          }
        }
        break;
      case PHOTOMETRIC_PALETTE:  // color map indexed
        uint16 *red;
        uint16 *green;
        uint16 *blue;
        TIFFGetField(m_tif, TIFFTAG_COLORMAP, &red, &green, &blue); 

        // Is the palette 16 or 8 bits ?
        BOOL Palette16Bits = /*FALSE*/ BIG_palette;
        if (!BIG_palette) {
          int n= 1<<bpp;
          while (n-- > 0) {
            if (red[n] >= 256 || green[n] >= 256 || blue[n] >= 256) {
              Palette16Bits=TRUE;
              break;
            }
          }
        }

        // load the palette in the DIB
        for (int i = (1 << ( BIG_palette ? bitspersample : bpp )) - 1; i >= 0; i--) {
          if (Palette16Bits) {
            pal[i].rgbRed =(BYTE) CVT(red[i]);
            pal[i].rgbGreen = (BYTE) CVT(green[i]);
            pal[i].rgbBlue = (BYTE) CVT(blue[i]);           
          } else {
            pal[i].rgbRed = (BYTE) red[i];
            pal[i].rgbGreen = (BYTE) green[i];
            pal[i].rgbBlue = (BYTE) blue[i];        
          }
        }
        break;
    }
    if (!BIG_palette) { // + VK (BIG palette is stored until image is ready)
      SetPalette(pal,/*head.biClrUsed*/ 1<<bpp);  //palette assign // * VK
      free(pal); 
      pal = NULL; 
    }

    // read the tiff lines and save them in the DIB
    uint32 nrow;
    uint32 ys;
    int line = CalculateLine(width, bitspersample * samplesperpixel);

    long bitsize = TIFFStripSize(m_tif);
    //verify bitsize: could be wrong if StripByteCounts is missing.
    if (bitsize<(long)(head.biSizeImage*samplesperpixel))
      bitsize = head.biSizeImage*samplesperpixel;

    if ((bitspersample > 8) && (bitspersample != 16))  // + VK (for bitspersample == 9..15,17..32..64
      bitsize *= (bitspersample + 7)/8; 

    int tiled_image = TIFFIsTiled(m_tif);
    uint32 tw=0, tl=0;
    BYTE* tilebuf=NULL;
    if (tiled_image){
      TIFFGetField(m_tif, TIFFTAG_TILEWIDTH, &tw);
      TIFFGetField(m_tif, TIFFTAG_TILELENGTH, &tl);
      rowsperstrip = tl;
      bitsize = TIFFTileSize(m_tif) * (int)(1+width/tw);
      tilebuf = (BYTE*)malloc(TIFFTileSize(m_tif));
    }

    bits = (BYTE*)malloc(bitspersample==16? bitsize*2 : bitsize); // * VK
    BYTE * bits16 = NULL;                      // + VK
    int line16    = 0;                        // + VK

    if (!tiled_image && bitspersample==16) {            // + VK +
      line16 = line;
      line   = CalculateLine(width, 8 * samplesperpixel);
      bits16 = bits;
      bits   = (BYTE*)malloc(bitsize);
    }

    if (bits==NULL){
      if (bits16) free(bits16);                  // + VK
      if (pal)  free(pal);                    // + VK
      if (tilebuf)free(tilebuf);                  // + VK  
      cx_throw("CxImageTIF can't allocate memory");
    }

#ifdef FIX_16BPP_DARKIMG // + VK: for each line, store shift count bits used to fix it
    BYTE* row_shifts = NULL;
    if (bits16) row_shifts = (BYTE*)malloc(height); 
#endif

    for (ys = 0; ys < height; ys += rowsperstrip) {

      if (info.nEscape){ // <vho> - cancel decoding
        free(bits);
        cx_throw("Cancelled");
      }

      nrow = (ys + rowsperstrip > height ? height - ys : rowsperstrip);

      if (tiled_image){
        uint32 imagew = TIFFScanlineSize(m_tif);
        uint32 tilew  = TIFFTileRowSize(m_tif);
        int iskew = imagew - tilew;
        uint8* bufp = (uint8*) bits;

        uint32 colb = 0;
        for (uint32 col = 0; col < width; col += tw) {
          if (TIFFReadTile(m_tif, tilebuf, col, ys, 0, 0) < 0){
            free(tilebuf);
            free(bits);
            cx_throw("Corrupted tiled TIFF file!");
          }

          if (colb + tw > imagew) {
            uint32 owidth = imagew - colb;
            uint32 oskew = tilew - owidth;
            TileToStrip(bufp + colb, tilebuf, nrow, owidth, oskew + iskew, oskew );
          } else {
            TileToStrip(bufp + colb, tilebuf, nrow, tilew, iskew, 0);
          }
          colb += tilew;
        }

      } else {
        if (TIFFReadEncodedStrip(m_tif, TIFFComputeStrip(m_tif, ys, 0), 
          (bits16? bits16 : bits), nrow * (bits16 ? line16 : line)) == -1) { // * VK

#ifdef NOT_IGNORE_CORRUPTED
          free(bits);
          if (bits16) free(bits16);  // + VK
          cx_throw("Corrupted TIFF file!");
#else
          break;
#endif
        }
      }

      for (y = 0; y < nrow; y++) {
        long offset=(nrow-y-1)*line;
        if ((bitspersample==16) && !BIG_palette) {  // * VK
          long offset16 = (nrow-y-1)*line16;    // + VK
          if (bits16)  {              // + VK +
#ifdef FIX_16BPP_DARKIMG
            int the_shift;
            BYTE hi_byte, hi_max=0;
            DWORD xi;
            for (xi=0;xi<(uint32)line;xi++) {
              hi_byte = bits16[xi*2+offset16+1];
              if(hi_byte>hi_max)
                hi_max = hi_byte;
            }
            the_shift = (hi_max == 0) ? 8 : 0;
            if (!the_shift)
              while( ! (hi_max & 0x80) ) {
                the_shift++;
                hi_max <<= 1;
              }
            row_shifts[height-ys-nrow+y] = the_shift;
            the_shift = 8 - the_shift;
            for (xi=0;xi<(uint32)line;xi++) 
              bits[xi+offset]= ((bits16[xi*2+offset16+1]<<8) | bits16[xi*2+offset16]) >> the_shift;
#else
            for (DWORD xi=0;xi<(uint32)line;xi++) 
              bits[xi+offset]=bits16[xi*2+offset16+1];
#endif
          } else {
            for (DWORD xi=0;xi<width;xi++)
              bits[xi+offset]=bits[xi*2+offset+1];
              }
        }
        if (samplesperpixel==1) { 
          if (BIG_palette)
            if (bits16) {
              long offset16 = (nrow-y-1)*line16;    // + VK
              MoveBitsPal( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
                   bits16 + offset16, width, bitspersample, pal );
            } else
              MoveBitsPal( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
                   bits + offset, width, bitspersample, pal );
          else if ((bitspersample == head.biBitCount) || 
            (bitspersample == 16))  //simple 8bpp, 4bpp image or 16bpp
            memcpy(info.pImage+info.dwEffWidth*(height-ys-nrow+y),bits+offset,info.dwEffWidth);
          else
            MoveBits( info.pImage + info.dwEffWidth * (height-ys-nrow+y),
                  bits + offset, width, bitspersample );
        } else if (samplesperpixel==2) { //8bpp image with alpha layer
          int xi=0;
          int ii=0;
          int yi=height-ys-nrow+y;
#if CXIMAGE_SUPPORT_ALPHA
          if (!pAlpha) AlphaCreate();      // + VK
#endif //CXIMAGE_SUPPORT_ALPHA
          while (ii<line){
            SetPixelIndex(xi,yi,bits[ii+offset]);
#if CXIMAGE_SUPPORT_ALPHA
            AlphaSet(xi,yi,bits[ii+offset+1]);
#endif //CXIMAGE_SUPPORT_ALPHA
            ii+=2;
            xi++;
            if (xi>=(int)width){
              yi--;
              xi=0;
            }
          }
        } else { //photometric==PHOTOMETRIC_CIELAB
          if (head.biBitCount!=24){ //fix image
            Create(width,height,24,CXIMAGE_FORMAT_TIF);
#if CXIMAGE_SUPPORT_ALPHA
            if (samplesperpixel==4) AlphaCreate();
#endif //CXIMAGE_SUPPORT_ALPHA
          }

          int xi=0;
          uint32 ii=0;
          int yi=height-ys-nrow+y;
          RGBQUAD c;
          int l,a,b,bitsoffset;
          double p,cx,cy,cz,cr,cg,cb;
          while (ii</*line*/width){    // * VK
            bitsoffset = ii*samplesperpixel+offset;
            l=bits[bitsoffset];
            a=bits[bitsoffset+1];
            b=bits[bitsoffset+2];
            if (a>127) a-=256;
            if (b>127) b-=256;
            // lab to xyz
            p = (l/2.55 + 16) / 116.0;
            cx = pow( p + a * 0.002, 3);
            cy = pow( p, 3);
            cz = pow( p - b * 0.005, 3);
            // white point
            cx*=0.95047;
            //cy*=1.000;
            cz*=1.0883;
            // xyz to rgb
            cr =  3.240479 * cx - 1.537150 * cy - 0.498535 * cz;
            cg = -0.969256 * cx + 1.875992 * cy + 0.041556 * cz;
            cb =  0.055648 * cx - 0.204043 * cy + 1.057311 * cz;

            if ( cr > 0.00304 ) cr = 1.055 * pow(cr,0.41667) - 0.055;
              else            cr = 12.92 * cr;
            if ( cg > 0.00304 ) cg = 1.055 * pow(cg,0.41667) - 0.055;
              else            cg = 12.92 * cg;
            if ( cb > 0.00304 ) cb = 1.055 * pow(cb,0.41667) - 0.055;
              else            cb = 12.92 * cb;

            c.rgbRed  =(BYTE)max(0,min(255,(int)(cr*255)));
            c.rgbGreen=(BYTE)max(0,min(255,(int)(cg*255)));
            c.rgbBlue =(BYTE)max(0,min(255,(int)(cb*255)));

            SetPixelColor(xi,yi,c);
#if CXIMAGE_SUPPORT_ALPHA
            if (samplesperpixel==4) AlphaSet(xi,yi,bits[bitsoffset+3]);
#endif //CXIMAGE_SUPPORT_ALPHA
            ii++;
            xi++;
            if (xi>=(int)width){
              yi--;
              xi=0;
            }
          }
        }
      }
    }
    free(bits);
    if (bits16) free(bits16);

#ifdef FIX_16BPP_DARKIMG
    if (row_shifts && (samplesperpixel == 1) && (bitspersample==16) && !BIG_palette) {
      // 1. calculate maximum necessary shift
      int min_row_shift = 8;
      for( y=0; y<height; y++ ) {
        if (min_row_shift > row_shifts[y]) min_row_shift = row_shifts[y];
      }
      // 2. for rows having less shift value, correct such rows:
      for( y=0; y<height; y++ ) {
        if (min_row_shift < row_shifts[y]) {
          int need_shift = row_shifts[y] - min_row_shift;
          BYTE* data = info.pImage + info.dwEffWidth * y;
          for( x=0; x<width; x++, data++ )
            *data >>= need_shift;
        }
      }
    }
    if (row_shifts)  free( row_shifts );
#endif

    if (tiled_image) free(tilebuf);
    if (pal)     free(pal);

    switch(orientation){
    case ORIENTATION_TOPRIGHT: /* row 0 top, col 0 rhs */
      Mirror();
      break;
    case ORIENTATION_BOTRIGHT: /* row 0 bottom, col 0 rhs */
      Flip();
      Mirror();
      break;
    case ORIENTATION_BOTLEFT: /* row 0 bottom, col 0 lhs */
      Flip();
      break;
    case ORIENTATION_LEFTTOP: /* row 0 lhs, col 0 top */
      RotateRight();
      Mirror();
      break;
    case ORIENTATION_RIGHTTOP: /* row 0 rhs, col 0 top */
      RotateLeft();
      break;
    case ORIENTATION_RIGHTBOT: /* row 0 rhs, col 0 bottom */
      RotateLeft();
      Mirror();
      break;
    case ORIENTATION_LEFTBOT: /* row 0 lhs, col 0 bottom */
      RotateRight();
      break;
    }

  }
  }

int image::read_tiff(const std::string &filename) {
  TIFF *tiff_handle;
  size_t bytes_per_line;
  uint16_t planar_configuration;
  if ((tiff_handle = TIFFOpen(filename.c_str(), "r")) == nullptr) {
    printf("ERROR: File %s is not found.\n", filename.c_str());
    return EXIT_FAILURE;
  }

  uint32_t tiff_width  = 0;
  uint32_t tiff_height = 0;
  TIFFGetField(tiff_handle, TIFFTAG_IMAGEWIDTH, &tiff_width);
  TIFFGetField(tiff_handle, TIFFTAG_IMAGELENGTH, &tiff_height);

  uint16_t tiff_bits_per_sample   = 0;
  uint16_t tiff_samples_per_pixel = 0;
  TIFFGetField(tiff_handle, TIFFTAG_BITSPERSAMPLE, &tiff_bits_per_sample);
  TIFFGetField(tiff_handle, TIFFTAG_SAMPLESPERPIXEL, &tiff_samples_per_pixel);
  // some TIFs have tiff_samples_per_pixel=0 when it is a single channel
  // image - set to 1
  tiff_samples_per_pixel = (tiff_samples_per_pixel < 1) ? 1 : tiff_samples_per_pixel;

  uint16_t tiff_planar_configuration = 0;
  uint16_t tiff_photometric          = 0;
  TIFFGetField(tiff_handle, TIFFTAG_PLANARCONFIG, &tiff_planar_configuration);
  TIFFGetField(tiff_handle, TIFFTAG_PHOTOMETRIC, &tiff_photometric);

  planar_configuration = tiff_planar_configuration;

  uint16_t tiff_compression    = 0;
  uint32_t tiff_rows_per_strip = 0;
  TIFFGetField(tiff_handle, TIFFTAG_COMPRESSION, &tiff_compression);
  TIFFGetField(tiff_handle, TIFFTAG_ROWSPERSTRIP, &tiff_rows_per_strip);

  if (tiff_planar_configuration == PLANARCONFIG_SEPARATE) {
    bytes_per_line = tiff_samples_per_pixel * TIFFScanlineSize64(tiff_handle);
  } else {
    bytes_per_line = TIFFScanlineSize64(tiff_handle);
  }
  // Error on known incompatilbe input formats
  if (tiff_bits_per_sample != 8 && tiff_bits_per_sample != 16) {
    printf(
        "ERROR: TIFF IO is currently limited to file limited"
        " to files with TIFFTAG_BITSPERSAMPLE=8 and TIFFTAG_BITSPERSAMPLE=16 \n"
        "input file = %s has TIFFTAG_BITSPERSAMPLE=%d\n",
        filename.c_str(), tiff_bits_per_sample);
    TIFFClose(tiff_handle);
    return EXIT_FAILURE;
  }
  if (TIFFIsTiled(tiff_handle)) {
    printf(
        "ERROR: TIFF IO is currently limited to TIF files"
        "without tiles. \nInput file %s has been detected as tiled\n",
        filename.c_str());
    TIFFClose(tiff_handle);
    return EXIT_FAILURE;
  }
  if (PHOTOMETRIC_RGB != tiff_photometric && PHOTOMETRIC_MINISBLACK != tiff_photometric) {
    printf(
        "ERROR: TIFF IO is currently limited to "
        "TIFFTAG_PHOTOMETRIC=PHOTOMETRIC_MINISBLACK=%d and "
        "PHOTOMETRIC_RGB=%d. \nInput file %s has been detected "
        "TIFFTAG_PHOTOMETRIC=%d\n",
        PHOTOMETRIC_MINISBLACK, PHOTOMETRIC_RGB, filename.c_str(), tiff_photometric);
    TIFFClose(tiff_handle);
    return EXIT_FAILURE;
  }
  this->num_components    = tiff_samples_per_pixel;
  uint16_t num_iterations = this->num_components;
  // setting bit-depth for components
  this->width  = tiff_width;
  this->height = tiff_height;
  for (int i = 0; i < num_iterations; ++i) {
    this->component_width.push_back(width);
    this->component_height.push_back(height);
    this->bits_per_pixel.push_back(static_cast<uint8_t>(tiff_bits_per_sample));
    this->is_signed.push_back(false);
  }

  const uint32_t byte_per_sample = (tiff_bits_per_sample + 8U - 1U) / 8U;
  const uint32_t component_gap   = num_iterations * byte_per_sample;
  // const uint32_t line_width = component_gap * tiff_width;
  std::unique_ptr<uint8_t[]> line_buf = MAKE_UNIQUE<uint8_t[]>(bytes_per_line);

  // allocate memory once
  if (this->buf == nullptr) {
    this->buf = MAKE_UNIQUE<std::unique_ptr<int32_t[]>[]>(this->num_components);
  }
  for (size_t i = 0; i < this->num_components; ++i) {
    this->buf[i] =
        MAKE_UNIQUE<int32_t[]>(static_cast<size_t>(this->component_width[i]) * this->component_height[i]);
  }

  if (PLANARCONFIG_SEPARATE == planar_configuration) {
    // PLANAR read
  } else if (PLANARCONFIG_CONTIG == planar_configuration) {
    // NORMAL read
    for (uint32_t i = 0; i < tiff_height; ++i) {
      if (TIFFReadScanline(tiff_handle, line_buf.get(), i) < 0) {
        printf("ERROR: not enough samples in the given pnm file.\n");
        TIFFClose(tiff_handle);
        return EXIT_FAILURE;
      }

      for (size_t c = 0; c < num_iterations; ++c) {
        uint8_t *src;
        int32_t *dst;
        src = &line_buf[c * byte_per_sample];
        dst = &this->buf[c][static_cast<size_t>(i) * tiff_width];

        switch (byte_per_sample) {
          case 1:
            for (size_t j = 0; j < tiff_width; ++j) {
              *dst = *src;
              dst++;
              src += component_gap;
            }
            break;
          case 2:
            for (size_t j = 0; j < tiff_width; ++j) {
              // suppose little-endian
              *dst = src[0] | (src[1] << 8);
              // *dst >>= 16 - tiff_bits_per_sample; // for future use
              dst++;
              src += component_gap;
            }
            break;
          default:
            printf("ERROR: bit-depth over 16 is not supported.\n");
            TIFFClose(tiff_handle);
            return EXIT_FAILURE;
            break;
        }
      }
    }
  }
  return EXIT_SUCCESS;
}

bool Tiff16Source::init()
{
  //_image = Tiff16();
  if (_z) {
    delete [] _z;
    _z = 0L;
  }

  iv->clear();
  im->clear();

  TIFF *tif=TIFFOpen(_filename.toAscii(), "r");
  if (tif) {
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &_width);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &_height);

    _z = new unsigned short[_width*_height];

    char *buf = (char *)_z;

    int linesize = TIFFScanlineSize(tif);
    for (int i = 0; i < _height; i++) {
      TIFFReadScanline(tif, &buf[i * linesize], i, 0);
    }
  } else {
    _width = _height = 0;
  }

  iv->init();
  im->init();
  registerChange();
  return true;
}

int Tiff16SourcePlugin::understands(QSettings *cfg, const QString& filename) const {
  Q_UNUSED(cfg)

  quint16 spp, bpp, is_tiled;

  if (filename.toLower().endsWith(".tiff") ||
      filename.toLower().endsWith(".tif")) {

    TIFF *tif=TIFFOpen(filename.toAscii(), "r");

    if (tif) {
      TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp);
      TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp);
      is_tiled = TIFFIsTiled(tif);

      TIFFClose(tif);


      if ((bpp == 16) && (spp = 1) && (is_tiled == 0)) {
          return 91;
      }
    }
  }
  return 0;
}

static FloatImage * loadFloatTIFF(const char * fileName, Stream & s)
{
    nvCheck(!s.isError());

    TIFF * tif = TIFFOpen(fileName, "r");
    //TIFF * tif = TIFFClientOpen(fileName, "r", &s, tiffReadWriteProc, tiffReadWriteProc, tiffSeekProc, tiffCloseProc, tiffSizeProc, tiffMapFileProc, tiffUnmapFileProc);

    if (!tif)
    {
        nvDebug("Can't open '%s' for reading\n", fileName);
        return NULL;
    }

    ::uint16 spp, bpp, format;
    ::uint32 width, height;
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp);
    TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp);
    TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &format);

    if (bpp != 8 && bpp != 16 && bpp != 32) {
        nvDebug("Can't load '%s', only 1 sample per pixel supported\n", fileName);
        TIFFClose(tif);
        return NULL;
    }

    AutoPtr<FloatImage> fimage(new FloatImage());
    fimage->allocate(spp, width, height);

    int linesize = TIFFScanlineSize(tif);
    tdata_t buf = malloc<uint8>(linesize);

    for (uint y = 0; y < height; y++)
    {
        TIFFReadScanline(tif, buf, y, 0);

	for (uint c=0; c<spp; c++ )
	{
	    float * dst = fimage->scanline(y, c);

	    for(uint x = 0; x < width; x++)
	    {
		if (bpp == 8)
		{
			dst[x] = float(((::uint8 *)buf)[x*spp+c]) / float(0xFF);
		}
		else if (bpp == 16)
		{
			dst[x] = float(((::uint16 *)buf)[x*spp+c]) / float(0xFFFF);
		}
		else if (bpp == 32)
		{
		    if (format==SAMPLEFORMAT_IEEEFP)
		    {
			dst[x] = float(((float *)buf)[x*spp+c]);
		    }
		    else
		    {
			dst[x] = float(((::uint32 *)buf)[x*spp+c] >> 8) / float(0xFFFFFF);
		    }
		}
	    }
	}
    }

    free(buf);

    TIFFClose(tif);

    return fimage.release();
}

BiometricEvaluation::Image::TIFF::TIFF(
    const uint8_t *data,
    const uint64_t size,
    const std::string &identifier,
    const statusCallback_t &statusCallback) :
    Image(
    data,
    size,
    CompressionAlgorithm::TIFF,
    identifier,
    statusCallback)
{
	if (!isTIFF(data, size))
		throw BE::Error::StrategyError("Not a TIFF image");

	TIFFSetWarningHandlerExt(BE_TIFFWarningHandler);
	TIFFSetErrorHandlerExt(BE_TIFFErrorHandler);

	std::unique_ptr<::TIFF, void(*)(::TIFF*)> tiff(
	    static_cast<::TIFF*>(this->getDecompressionStream()), TIFFClose);

	uint16_t colorType{};
	TIFFGetField(tiff.get(), TIFFTAG_PHOTOMETRIC, &colorType);
	if ((colorType != PHOTOMETRIC_MINISBLACK) &&
	    (colorType != PHOTOMETRIC_RGB))
		throw BE::Error::NotImplemented("Unsupported TIFF colortype: " +
		    std::to_string(colorType));

	uint32_t width{}, height{};
	if (TIFFGetField(tiff.get(), TIFFTAG_IMAGEWIDTH, &width) != 1)
		throw BE::Error::StrategyError("Could not read width");
	if (TIFFGetField(tiff.get(), TIFFTAG_IMAGELENGTH, &height) != 1)
		throw BE::Error::StrategyError("Could not read height");
	this->setDimensions({width, height});

	uint16_t bitsPerSample{};
	if (TIFFGetFieldDefaulted(tiff.get(), TIFFTAG_BITSPERSAMPLE,
	    &bitsPerSample) != 1)
		throw BE::Error::StrategyError("Could not read bits per "
		    "sample");
	this->setBitDepth(bitsPerSample);

	uint16_t samplesPerPixel;
	if (TIFFGetFieldDefaulted(tiff.get(), TIFFTAG_SAMPLESPERPIXEL,
	    &samplesPerPixel) != 1)
		throw BE::Error::StrategyError("Could not read samples per "
		    "pixel");
	this->setColorDepth(samplesPerPixel * bitsPerSample);

	if ((samplesPerPixel == 1) || (samplesPerPixel == 3))
		this->setHasAlphaChannel(false);
	else {
		uint16_t extraSamples{};
		if (TIFFGetFieldDefaulted(tiff.get(), TIFFTAG_EXTRASAMPLES,
		    &extraSamples) != 1)
			throw BE::Error::StrategyError("Could not read extra "
			    "samples");
		if (extraSamples == EXTRASAMPLE_ASSOCALPHA)
			this->setHasAlphaChannel(true);
		else
			throw BE::Error::NotImplemented("Unusual color depth, "
			    "and unsure what do to with extra samples");
	}

	float xRes{}, yRes{};
	if (TIFFGetField(tiff.get(), TIFFTAG_XRESOLUTION, &xRes) != 1)
		xRes = 72;
	if (TIFFGetField(tiff.get(), TIFFTAG_YRESOLUTION, &yRes) != 1)
		yRes = 72;

	/* RGBRGBRGB vs RRRGGGBBB when retrieving scanlines */
	uint16_t planarConfig{};
	if (TIFFGetField(tiff.get(), TIFFTAG_PLANARCONFIG, &planarConfig) != 1)
		throw BE::Error::StrategyError("Could not read planar "
		    "configuration");
	if (planarConfig != PLANARCONFIG_CONTIG)
		throw BE::Error::NotImplemented("TIFF images separated by "
		    "component are not yet supported");

	uint16_t rawResUnits{};
	if (TIFFGetFieldDefaulted(tiff.get(), TIFFTAG_RESOLUTIONUNIT,
	    &rawResUnits) != 1)
		throw BE::Error::StrategyError("Could not read resolution "
		    "units");
	BE::Image::Resolution::Units resUnits{BE::Image::Resolution::Units::NA};
	switch (rawResUnits) {
	case RESUNIT_INCH:
		resUnits = BE::Image::Resolution::Units::PPI;
		break;
	case RESUNIT_CENTIMETER:
		resUnits = BE::Image::Resolution::Units::PPCM;
		break;
	default:
		resUnits = BE::Image::Resolution::Units::NA;
		break;
	}
	this->setResolution({xRes, yRes, resUnits});
}

void PdfImage::LoadFromTiffHandle(void* hInHandle) {

    TIFF* hInTiffHandle = (TIFF*)hInHandle;

    int32 row, width, height;
    uint16 samplesPerPixel, bitsPerSample;
    uint16* sampleInfo;
    uint16 extraSamples;
    uint16 planarConfig, photoMetric, orientation;
    int32 resolutionUnit;

    TIFFGetField(hInTiffHandle,	   TIFFTAG_IMAGEWIDTH,		&width);
    TIFFGetField(hInTiffHandle,	   TIFFTAG_IMAGELENGTH,		&height);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_BITSPERSAMPLE,	&bitsPerSample);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_SAMPLESPERPIXEL,     &samplesPerPixel);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_PLANARCONFIG,	&planarConfig);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_PHOTOMETRIC,		&photoMetric);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_EXTRASAMPLES,	&extraSamples, &sampleInfo);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_ORIENTATION,		&orientation);

    resolutionUnit = 0;
    float resX;
    float resY;
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_XRESOLUTION,		&resX);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_YRESOLUTION,		&resY);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_RESOLUTIONUNIT,	&resolutionUnit);

    int colorChannels = samplesPerPixel - extraSamples;

    int bitsPixel = bitsPerSample * samplesPerPixel;

    // TODO: implement special cases
    if( TIFFIsTiled(hInTiffHandle) )
    {
        TIFFClose(hInTiffHandle);
        PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
    }

    if ( planarConfig != PLANARCONFIG_CONTIG && colorChannels != 1 )
    {
        TIFFClose(hInTiffHandle);
        PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
    }

    if ( orientation != ORIENTATION_TOPLEFT )
    {
        TIFFClose(hInTiffHandle);
        PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
    }

    switch(photoMetric)
    {
        case PHOTOMETRIC_MINISBLACK:
        {
            if( bitsPixel == 1 )
            {
                PdfArray decode;
                decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(0) ) );
                decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(1) ) );
                this->GetObject()->GetDictionary().AddKey( PdfName("Decode"), decode );
                this->GetObject()->GetDictionary().AddKey( PdfName("ImageMask"), PdfVariant( true ) );
                this->GetObject()->GetDictionary().RemoveKey( PdfName("ColorSpace") );
            }
            else if ( bitsPixel == 8  ||  bitsPixel == 16)
                SetImageColorSpace(ePdfColorSpace_DeviceGray);
            else
            {
                TIFFClose(hInTiffHandle);
                PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
            }
        }
            break;

        case PHOTOMETRIC_MINISWHITE:
        {
            if( bitsPixel == 1 )
            {
                PdfArray decode;
                decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(1) ) );
                decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(0) ) );
                this->GetObject()->GetDictionary().AddKey( PdfName("Decode"), decode );
                this->GetObject()->GetDictionary().AddKey( PdfName("ImageMask"), PdfVariant( true ) );
                this->GetObject()->GetDictionary().RemoveKey( PdfName("ColorSpace") );
            }
            else if ( bitsPixel == 8  ||  bitsPixel == 16)
                SetImageColorSpace(ePdfColorSpace_DeviceGray);
            else
            {
                TIFFClose(hInTiffHandle);
                PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
            }
        }
            break;

        case PHOTOMETRIC_RGB:
            if ( bitsPixel != 24 )
            {
                TIFFClose(hInTiffHandle);
                PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
            }
            SetImageColorSpace(ePdfColorSpace_DeviceRGB);
            break;

        case PHOTOMETRIC_SEPARATED:
            if( bitsPixel != 32)
            {
                TIFFClose(hInTiffHandle);
                PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
            }
            SetImageColorSpace(ePdfColorSpace_DeviceCMYK);
            break;

        case PHOTOMETRIC_PALETTE:
        {
            int numColors = (1 << bitsPixel);

            PdfArray decode;
            decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(0) ) );
            decode.insert( decode.end(), PdfVariant( static_cast<pdf_int64>(numColors-1) ) );
            this->GetObject()->GetDictionary().AddKey( PdfName("Decode"), decode );

            uint16 * rgbRed;
            uint16 * rgbGreen;
            uint16 * rgbBlue;
            TIFFGetField(hInTiffHandle, TIFFTAG_COLORMAP, &rgbRed, &rgbGreen, &rgbBlue);

            char *datap = new char[numColors*3];

            for ( int clr = 0; clr < numColors; clr++ )
            {
                datap[3*clr+0] = rgbRed[clr]/257;
                datap[3*clr+1] = rgbGreen[clr]/257;
                datap[3*clr+2] = rgbBlue[clr]/257;
            }
            PdfMemoryInputStream stream( datap, numColors*3 );

            // Create a colorspace object
            PdfObject* pIdxObject = this->GetObject()->GetOwner()->CreateObject();
            pIdxObject->GetStream()->Set( &stream );

            // Add the colorspace to our image
            PdfArray array;
            array.push_back( PdfName("Indexed") );
            array.push_back( PdfName("DeviceRGB") );
            array.push_back( static_cast<pdf_int64>(numColors-1) );
            array.push_back( pIdxObject->Reference() );
            this->GetObject()->GetDictionary().AddKey( PdfName("ColorSpace"), array );

            delete[] datap;
        }
            break;

        default:
            TIFFClose(hInTiffHandle);
            PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
            break;
    }

    int32 scanlineSize = TIFFScanlineSize(hInTiffHandle);
    long bufferSize = scanlineSize * height;
    char *buffer = new char[bufferSize];
    if( !buffer )
    {
        TIFFClose(hInTiffHandle);
        PODOFO_RAISE_ERROR( ePdfError_OutOfMemory );
    }

    for(row = 0; row < height; row++)
    {
        if(TIFFReadScanline(hInTiffHandle,
                            &buffer[row * scanlineSize],
                            row) == (-1))
        {
            TIFFClose(hInTiffHandle);
            PODOFO_RAISE_ERROR( ePdfError_UnsupportedImageFormat );
        }
    }

    PdfMemoryInputStream stream(buffer, bufferSize);

    SetImageData(static_cast<unsigned int>(width),
                 static_cast<unsigned int>(height),
                 static_cast<unsigned int>(bitsPerSample),
                 &stream);

    delete[] buffer;

    TIFFClose(hInTiffHandle);
}

static bool ReadTIFF(const char *name, float *&rgba, int &xRes, int &yRes,
		     bool &hasAlpha)
{
    // Try to open TIFF file
    TIFF *tiff = TIFFOpen(name, "r");
    if (!tiff) {
	fprintf(stderr, "Unable to open TIFF %s", name);
	return false;
    }
    // Get basic information from TIFF header
    short int nSamples;
    int xSize, ySize;
    TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &xSize);
    TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &ySize);
    TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &nSamples);
    if (nSamples != 3 && nSamples != 4) {
	fprintf(stderr, "Sorry, only handle 3 and 4 sample TIFFs...\n");
	return false;
    }
    hasAlpha = (nSamples == 4);
    xRes = xSize;
    yRes = ySize;

    // Make sure this is a TIFF we can read
    short int bitsPerSample, sampleFormat = SAMPLEFORMAT_UINT;
    if (!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample)) {
	fprintf(stderr, "TIFFRead: bits per sample not set in TIFF");
	TIFFClose(tiff);
	return false;
    }

    if (!TIFFGetField(tiff, TIFFTAG_SAMPLEFORMAT, &sampleFormat)) {
	if (bitsPerSample == 32)
	    sampleFormat = SAMPLEFORMAT_IEEEFP;
	else
	    sampleFormat = SAMPLEFORMAT_UINT;
    }

    if (bitsPerSample == 32) {
	if (sampleFormat != SAMPLEFORMAT_IEEEFP) {
	    fprintf(stderr, "TIFFRead: 32 bit TIFF not stored in floating point format");
	    TIFFClose(tiff);
	    return false;
	}
    }
    else {
	if (bitsPerSample != 8 && bitsPerSample != 32) {
	    fprintf(stderr, "TIFFRead: only 8 and 32 bits per sample supported");
	    TIFFClose(tiff);
	    return false;
	}
	if (sampleFormat != SAMPLEFORMAT_UINT) {
	    fprintf(stderr, "TIFFRead: 8 bit TIFFs must be stored as unsigned ints");
	    TIFFClose(tiff);
	    return false;
	}
    }

    int bytesPerSample = bitsPerSample / 8;
    if (nSamples * xRes * bytesPerSample != TIFFScanlineSize(tiff)) {
	fprintf(stderr, "TIFFRead: RGB not interleaved in TIFF %s", name);
	TIFFClose(tiff);
	return false;
    }

    // Allocate space for [[pixels]] and buffers
    rgba = new float[nSamples * xRes * yRes];
    float *p = rgba;
    unsigned char *ubuf = NULL;
    if (bitsPerSample == 8) ubuf = new unsigned char[nSamples * xRes];

    for (int y = 0; y < yRes; ++y) {
	if (ubuf) {
	    // Read 8-bit TIFF scanline
	    if (TIFFReadScanline(tiff, ubuf, y, 1) == -1) {
		TIFFClose(tiff);
		return false;
	    }
	    for (int x = 0; x < nSamples*xRes; ++x)
		*p++ = ubuf[x] * INV_255;
	}
	else {
	    // Read floating point TIFF scanline
	    if (TIFFReadScanline(tiff, p, y, 1) == -1) {
		TIFFClose(tiff);
		return false;
	    }
	    p += nSamples * xRes;
	}
    }

    delete[] ubuf;
    TIFFClose(tiff);
    return rgba;
}

TiffReader::TiffInfo::TiffInfo(TiffHandle const& tif, TiffHeader const& header)
    :   width(0),
        height(0),
        bits_per_sample(1),
        samples_per_pixel(1),
        sample_format(SAMPLEFORMAT_UINT),
        photometric(PHOTOMETRIC_MINISBLACK),
        host_big_endian(QSysInfo::ByteOrder == QSysInfo::BigEndian),
        file_big_endian(header.signature() == TiffHeader::TIFF_BIG_ENDIAN)
{
    uint16 compression = 1;
    TIFFGetField(tif.handle(), TIFFTAG_COMPRESSION, &compression);
    switch (compression) {
    case COMPRESSION_CCITTFAX3:
    case COMPRESSION_CCITTFAX4:
    case COMPRESSION_CCITTRLE:
    case COMPRESSION_CCITTRLEW:
        photometric = PHOTOMETRIC_MINISWHITE;
    }

    TIFFGetField(tif.handle(), TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif.handle(), TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif.handle(), TIFFTAG_BITSPERSAMPLE, &bits_per_sample);
    TIFFGetField(tif.handle(), TIFFTAG_SAMPLESPERPIXEL, &samples_per_pixel);
    TIFFGetField(tif.handle(), TIFFTAG_SAMPLEFORMAT, &sample_format);
    TIFFGetField(tif.handle(), TIFFTAG_PHOTOMETRIC, &photometric);
}

ImageMetadata
TiffReader::currentPageMetadata(TiffHandle const& tif)
{
    uint32 width = 0, height = 0;
    float xres = 0, yres = 0;
    uint16 res_unit = 0;
    TIFFGetField(tif.handle(), TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif.handle(), TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif.handle(), TIFFTAG_XRESOLUTION, &xres);
    TIFFGetField(tif.handle(), TIFFTAG_YRESOLUTION, &yres);
    TIFFGetFieldDefaulted(tif.handle(), TIFFTAG_RESOLUTIONUNIT, &res_unit);
    return ImageMetadata(QSize(width, height), getDpi(xres, yres, res_unit));
}

QImage
TiffReader::extractBinaryOrIndexed8Image(
    TiffHandle const& tif, TiffInfo const& info)
{
    QImage::Format format = QImage::Format_Indexed8;
    if (info.bits_per_sample == 1) {
        // Because we specify B option when opening, we can
        // always use Format_Mono, and not Format_MonoLSB.
        format = QImage::Format_Mono;
    }

    QImage image(info.width, info.height, format);
    if (image.isNull()) {
        throw std::bad_alloc();
    }

    int const num_colors = 1 << info.bits_per_sample;
    image.setColorCount(num_colors);

    if (info.photometric == PHOTOMETRIC_PALETTE) {
        uint16* pr = 0;
        uint16* pg = 0;
        uint16* pb = 0;
        TIFFGetField(tif.handle(), TIFFTAG_COLORMAP, &pr, &pg, &pb);
        if (!pr || !pg || !pb) {
            return QImage();
        }
        if (info.host_big_endian != info.file_big_endian) {
            TIFFSwabArrayOfShort(pr, num_colors);
            TIFFSwabArrayOfShort(pg, num_colors);
            TIFFSwabArrayOfShort(pb, num_colors);
        }
        double const f = 255.0 / 65535.0;
        for (int i = 0; i < num_colors; ++i) {
            uint32 const r = (uint32)(pr[i] * f + 0.5);
            uint32 const g = (uint32)(pg[i] * f + 0.5);
            uint32 const b = (uint32)(pb[i] * f + 0.5);
            uint32 const a = 0xFF000000;
            image.setColor(i, a | (r << 16) | (g << 8) | b);
        }
    } else if (info.photometric == PHOTOMETRIC_MINISBLACK) {
        double const f = 255.0 / (num_colors - 1);
        for (int i = 0; i < num_colors; ++i) {
            int const gray = (int)(i * f + 0.5);
            image.setColor(i, qRgb(gray, gray, gray));
        }
    } else if (info.photometric == PHOTOMETRIC_MINISWHITE) {
        double const f = 255.0 / (num_colors - 1);
        int c = num_colors - 1;
        for (int i = 0; i < num_colors; ++i, --c) {
            int const gray = (int)(c * f + 0.5);
            image.setColor(i, qRgb(gray, gray, gray));
        }
    } else {
        return QImage();
    }

    if (info.bits_per_sample == 1 || info.bits_per_sample == 8) {
        readLines(tif, image);
    } else {
        readAndUnpackLines(tif, info, image);
    }

    return image;
}

GraphicsBuffer load_image_to_graphics_buffer_tiff(GraphicsDevice device, const char* texturePath, uint32_t& width, uint32_t& height, TextureFormat& textureFormat)
{
	// Set the error callback
	TIFFSetErrorHandler(&tif_read_error);

	// Open the file
	TIFF* tif = TIFFOpen(texturePath, "r");
	assert_msg(tif != nullptr, "Could not find tiff file.");

	// Read the resource's dimensions
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);

	// Number of channels per pixel
	uint16_t channelCount, bitsPerChannel, channelFormat;
	TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &channelCount);
	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerChannel);
	uint16_t channelSize = bitsPerChannel / 8;
	TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &channelFormat);
	if (channelSize == 1 && channelCount == 3)
		channelFormat = SAMPLEFORMAT_UINT;

	// Get the output format
	uint16_t actualChannelCount;
	get_adequate_format(channelSize, channelCount, channelFormat, textureFormat, actualChannelCount);

	// Allocate the upload texture
	const uint16_t pixelSize = actualChannelCount * channelSize;
	const uint32_t imageSize = width * height * pixelSize;
	GraphicsBuffer textureBuffer = d3d12::graphics_resources::create_graphics_buffer(device, imageSize, pixelSize, GraphicsBufferType::Upload);
	char* bufferCPU = d3d12::graphics_resources::allocate_cpu_buffer(textureBuffer);

	// Read the image
	tsize_t lineSize = TIFFScanlineSize(tif);

	assert(lineSize == width * (channelSize * channelCount));

	// Allocate a buffer for line reading
	char* image = (char*)malloc(width * channelSize * channelCount);
	for (uint32_t channelIdx = 0; channelIdx < channelCount; ++channelIdx)
	{
		// For each line
		for (uint32_t y = 0; y < height; y++)
		{
			TIFFReadScanline(tif, image, y, channelIdx);
			for (uint32_t x = 0; x < width; x++)
			{
				uint32_t inputOffset = channelSize * channelCount * x;
				uint32_t outputOffset = pixelSize * (x + y * width);
				memcpy(bufferCPU + outputOffset, image + inputOffset, channelSize * channelCount);
			}
		}
	}

	// free the buffer
	free(image);

	// Release the CPU view
	d3d12::graphics_resources::release_cpu_buffer(textureBuffer);

	// Close the tiff file
	TIFFClose(tif);

	// Return the created graphics buffer
	return textureBuffer;
}

tiffcp(TIFF* in, TIFF* out)
{
	uint16 bitspersample, samplesperpixel = 1;
uint16 input_compression, input_photometric = PHOTOMETRIC_MINISBLACK;
copyFunc cf;
uint32 width, length;
struct cpTag* p;

CopyField(TIFFTAG_IMAGEWIDTH, width);
CopyField(TIFFTAG_IMAGELENGTH, length);
CopyField(TIFFTAG_BITSPERSAMPLE, bitspersample);
CopyField(TIFFTAG_SAMPLESPERPIXEL, samplesperpixel);
if (compression != (uint16)-1)
TIFFSetField(out, TIFFTAG_COMPRESSION, compression);
else
CopyField(TIFFTAG_COMPRESSION, compression);
TIFFGetFieldDefaulted(in, TIFFTAG_COMPRESSION, &input_compression);
TIFFGetFieldDefaulted(in, TIFFTAG_PHOTOMETRIC, &input_photometric);
if (input_compression == COMPRESSION_JPEG) {
/* Force conversion to RGB */
TIFFSetField(in, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
} else if (input_photometric == PHOTOMETRIC_YCBCR) {
/* Otherwise, can't handle subsampled input */
uint16 subsamplinghor,subsamplingver;

TIFFGetFieldDefaulted(in, TIFFTAG_YCBCRSUBSAMPLING,
&subsamplinghor, &subsamplingver);
if (subsamplinghor!=1 || subsamplingver!=1) {
fprintf(stderr, "tiffcp: %s: Can't copy/convert subsampled image.\n",
TIFFFileName(in));
return FALSE;
}
}
if (compression == COMPRESSION_JPEG) {
if (input_photometric == PHOTOMETRIC_RGB &&
jpegcolormode == JPEGCOLORMODE_RGB)
TIFFSetField(out, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_YCBCR);
else
TIFFSetField(out, TIFFTAG_PHOTOMETRIC, input_photometric);
}
else if (compression == COMPRESSION_SGILOG
|| compression == COMPRESSION_SGILOG24)
TIFFSetField(out, TIFFTAG_PHOTOMETRIC,
samplesperpixel == 1 ?
PHOTOMETRIC_LOGL : PHOTOMETRIC_LOGLUV);
else if (input_compression == COMPRESSION_JPEG &&
samplesperpixel == 3 ) {
/* RGB conversion was forced above
hence the output will be of the same type */
TIFFSetField(out, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_RGB);
}
else
CopyTag(TIFFTAG_PHOTOMETRIC, 1, TIFF_SHORT);
if (fillorder != 0)
TIFFSetField(out, TIFFTAG_FILLORDER, fillorder);
else
CopyTag(TIFFTAG_FILLORDER, 1, TIFF_SHORT);
/*
* Will copy `Orientation' tag from input image
*/
TIFFGetFieldDefaulted(in, TIFFTAG_ORIENTATION, &orientation);
switch (orientation) {
case ORIENTATION_BOTRIGHT:
case ORIENTATION_RIGHTBOT:	/* XXX */
TIFFWarning(TIFFFileName(in), "using bottom-left orientation");
orientation = ORIENTATION_BOTLEFT;
/* fall thru... */
case ORIENTATION_LEFTBOT:	/* XXX */
case ORIENTATION_BOTLEFT:
break;
case ORIENTATION_TOPRIGHT:
case ORIENTATION_RIGHTTOP:	/* XXX */
default:
TIFFWarning(TIFFFileName(in), "using top-left orientation");
orientation = ORIENTATION_TOPLEFT;
/* fall thru... */
case ORIENTATION_LEFTTOP:	/* XXX */
case ORIENTATION_TOPLEFT:
break;
}
TIFFSetField(out, TIFFTAG_ORIENTATION, orientation);
/*
* Choose tiles/strip for the output image according to
* the command line arguments (-tiles, -strips) and the
* structure of the input image.
*/
if (outtiled == -1)
outtiled = TIFFIsTiled(in);
if (outtiled) {
/*
* Setup output file's tile width&height.  If either
* is not specified, use either the value from the
* input image or, if nothing is defined, use the
* library default.
*/
if (tilewidth == (uint32) -1)
TIFFGetField(in, TIFFTAG_TILEWIDTH, &tilewidth);
if (tilelength == (uint32) -1)
TIFFGetField(in, TIFFTAG_TILELENGTH, &tilelength);
TIFFDefaultTileSize(out, &tilewidth, &tilelength);
TIFFSetField(out, TIFFTAG_TILEWIDTH, tilewidth);
TIFFSetField(out, TIFFTAG_TILELENGTH, tilelength);
} else {
/*
* RowsPerStrip is left unspecified: use either the
* value from the input image or, if nothing is defined,
* use the library default.
*/
if (rowsperstrip == (uint32) 0) {
if (!TIFFGetField(in, TIFFTAG_ROWSPERSTRIP,
&rowsperstrip)) {
rowsperstrip =
TIFFDefaultStripSize(out, rowsperstrip);
}
if (rowsperstrip > length && rowsperstrip != (uint32)-1)
rowsperstrip = length;
}
else if (rowsperstrip == (uint32) -1)
rowsperstrip = length;
TIFFSetField(out, TIFFTAG_ROWSPERSTRIP, rowsperstrip);
}
if (config != (uint16) -1)
TIFFSetField(out, TIFFTAG_PLANARCONFIG, config);
else
CopyField(TIFFTAG_PLANARCONFIG, config);
if (samplesperpixel <= 4)
CopyTag(TIFFTAG_TRANSFERFUNCTION, 4, TIFF_SHORT);
CopyTag(TIFFTAG_COLORMAP, 4, TIFF_SHORT);
/* SMinSampleValue & SMaxSampleValue */
switch (compression) {
case COMPRESSION_JPEG:
TIFFSetField(out, TIFFTAG_JPEGQUALITY, quality);
TIFFSetField(out, TIFFTAG_JPEGCOLORMODE, jpegcolormode);
break;
case COMPRESSION_JBIG:
CopyTag(TIFFTAG_FAXRECVPARAMS, 1, TIFF_LONG);
CopyTag(TIFFTAG_FAXRECVTIME, 1, TIFF_LONG);
CopyTag(TIFFTAG_FAXSUBADDRESS, 1, TIFF_ASCII);
CopyTag(TIFFTAG_FAXDCS, 1, TIFF_ASCII);
break;
case COMPRESSION_LZW:
case COMPRESSION_ADOBE_DEFLATE:
case COMPRESSION_DEFLATE:
case COMPRESSION_LZMA:
if (predictor != (uint16)-1)
TIFFSetField(out, TIFFTAG_PREDICTOR, predictor);
else
CopyField(TIFFTAG_PREDICTOR, predictor);
if (preset != -1) {
if (compression == COMPRESSION_ADOBE_DEFLATE
|| compression == COMPRESSION_DEFLATE)
TIFFSetField(out, TIFFTAG_ZIPQUALITY, preset);
else if (compression == COMPRESSION_LZMA)
TIFFSetField(out, TIFFTAG_LZMAPRESET, preset);
}
break;
case COMPRESSION_CCITTFAX3:
case COMPRESSION_CCITTFAX4:
if (compression == COMPRESSION_CCITTFAX3) {
if (g3opts != (uint32) -1)
TIFFSetField(out, TIFFTAG_GROUP3OPTIONS,
g3opts);
else
CopyField(TIFFTAG_GROUP3OPTIONS, g3opts);
} else
CopyTag(TIFFTAG_GROUP4OPTIONS, 1, TIFF_LONG);
CopyTag(TIFFTAG_BADFAXLINES, 1, TIFF_LONG);
CopyTag(TIFFTAG_CLEANFAXDATA, 1, TIFF_LONG);
CopyTag(TIFFTAG_CONSECUTIVEBADFAXLINES, 1, TIFF_LONG);
CopyTag(TIFFTAG_FAXRECVPARAMS, 1, TIFF_LONG);
CopyTag(TIFFTAG_FAXRECVTIME, 1, TIFF_LONG);
CopyTag(TIFFTAG_FAXSUBADDRESS, 1, TIFF_ASCII);
break;
}
{
uint32 len32;
void** data;
if (TIFFGetField(in, TIFFTAG_ICCPROFILE, &len32, &data))
TIFFSetField(out, TIFFTAG_ICCPROFILE, len32, data);
}
{
uint16 ninks;
const char* inknames;
if (TIFFGetField(in, TIFFTAG_NUMBEROFINKS, &ninks)) {
TIFFSetField(out, TIFFTAG_NUMBEROFINKS, ninks);
if (TIFFGetField(in, TIFFTAG_INKNAMES, &inknames)) {
int inknameslen = strlen(inknames) + 1;
const char* cp = inknames;
while (ninks > 1) {
cp = strchr(cp, '\0');
cp++;
inknameslen += (strlen(cp) + 1);
ninks--;
}
TIFFSetField(out, TIFFTAG_INKNAMES, inknameslen, inknames);
}
}
}
{
unsigned short pg0, pg1;

if (pageInSeq == 1) {
if (pageNum < 0) /* only one input file */ {
if (TIFFGetField(in, TIFFTAG_PAGENUMBER, &pg0, &pg1))
TIFFSetField(out, TIFFTAG_PAGENUMBER, pg0, pg1);
} else
TIFFSetField(out, TIFFTAG_PAGENUMBER, pageNum++, 0);

} else {
if (TIFFGetField(in, TIFFTAG_PAGENUMBER, &pg0, &pg1)) {
if (pageNum < 0) /* only one input file */
TIFFSetField(out, TIFFTAG_PAGENUMBER, pg0, pg1);
else
TIFFSetField(out, TIFFTAG_PAGENUMBER, pageNum++, 0);
}
}
}

for (p = tags; p < &tags[NTAGS]; p++)
CopyTag(p->tag, p->count, p->type);

cf = pickCopyFunc(in, out, bitspersample, samplesperpixel);
return (cf ? (*cf)(in, out, length, width, samplesperpixel) : FALSE);
}

loadImage(TIFF* in, struct image_data *image, struct dump_opts *dump, unsigned char **read_ptr)
{
uint32   i;
float    xres = 0.0, yres = 0.0;
  uint16   nstrips = 0, ntiles = 0, planar = 0;
uint16   bps = 0, spp = 0, res_unit = 0;
uint16   orientation = 0;
uint16   input_compression = 0, input_photometric = 0;
uint16   subsampling_horiz, subsampling_vert;
uint32   width = 0, length = 0;
uint32   stsize = 0, tlsize = 0, buffsize = 0, scanlinesize = 0;
uint32   tw = 0, tl = 0;       /* Tile width and length */
uint32   tile_rowsize = 0;
unsigned char *read_buff = NULL;
unsigned char *new_buff  = NULL;
int      readunit = 0;
static   uint32  prev_readsize = 0;

TIFFGetFieldDefaulted(in, TIFFTAG_BITSPERSAMPLE, &bps);
TIFFGetFieldDefaulted(in, TIFFTAG_SAMPLESPERPIXEL, &spp);
TIFFGetFieldDefaulted(in, TIFFTAG_PLANARCONFIG, &planar);
TIFFGetFieldDefaulted(in, TIFFTAG_ORIENTATION, &orientation);
if (! TIFFGetFieldDefaulted(in, TIFFTAG_PHOTOMETRIC, &input_photometric))
TIFFError("loadImage","Image lacks Photometric interpreation tag");
if (! TIFFGetField(in, TIFFTAG_IMAGEWIDTH,  &width))
TIFFError("loadimage","Image lacks image width tag");
if(! TIFFGetField(in, TIFFTAG_IMAGELENGTH, &length))
TIFFError("loadimage","Image lacks image length tag");
TIFFGetFieldDefaulted(in, TIFFTAG_XRESOLUTION, &xres);
TIFFGetFieldDefaulted(in, TIFFTAG_YRESOLUTION, &yres);
if (!TIFFGetFieldDefaulted(in, TIFFTAG_RESOLUTIONUNIT, &res_unit))
res_unit = RESUNIT_INCH;
if (!TIFFGetField(in, TIFFTAG_COMPRESSION, &input_compression))
input_compression = COMPRESSION_NONE;

#ifdef DEBUG2
char compressionid[16];

switch (input_compression)
{
case COMPRESSION_NONE:	/* 1  dump mode */
strcpy (compressionid, "None/dump");
break;
case COMPRESSION_CCITTRLE:	  /* 2 CCITT modified Huffman RLE */
strcpy (compressionid, "Huffman RLE");
break;
case COMPRESSION_CCITTFAX3:	  /* 3 CCITT Group 3 fax encoding */
strcpy (compressionid, "Group3 Fax");
break;
case COMPRESSION_CCITTFAX4:	  /* 4 CCITT Group 4 fax encoding */
strcpy (compressionid, "Group4 Fax");
break;
case COMPRESSION_LZW:	  /* 5 Lempel-Ziv  & Welch */
strcpy (compressionid, "LZW");
break;
case COMPRESSION_OJPEG:	  /* 6 !6.0 JPEG */
strcpy (compressionid, "Old Jpeg");
break;
case COMPRESSION_JPEG:	  /* 7 %JPEG DCT compression */
strcpy (compressionid, "New Jpeg");
break;
case COMPRESSION_NEXT:	  /* 32766 NeXT 2-bit RLE */
strcpy (compressionid, "Next RLE");
break;
case COMPRESSION_CCITTRLEW:   /* 32771 #1 w/ word alignment */
strcpy (compressionid, "CITTRLEW");
break;
case COMPRESSION_PACKBITS:	  /* 32773 Macintosh RLE */
strcpy (compressionid, "Mac Packbits");
break;
case COMPRESSION_THUNDERSCAN: /* 32809 ThunderScan RLE */
strcpy (compressionid, "Thunderscan");
break;
case COMPRESSION_IT8CTPAD:	  /* 32895 IT8 CT w/padding */
strcpy (compressionid, "IT8 padded");
break;
case COMPRESSION_IT8LW:	  /* 32896 IT8 Linework RLE */
strcpy (compressionid, "IT8 RLE");
break;
case COMPRESSION_IT8MP:	  /* 32897 IT8 Monochrome picture */
strcpy (compressionid, "IT8 mono");
break;
case COMPRESSION_IT8BL:	  /* 32898 IT8 Binary line art */
strcpy (compressionid, "IT8 lineart");
break;
case COMPRESSION_PIXARFILM:	  /* 32908 Pixar companded 10bit LZW */
strcpy (compressionid, "Pixar 10 bit");
break;
case COMPRESSION_PIXARLOG:	  /* 32909 Pixar companded 11bit ZIP */
strcpy (compressionid, "Pixar 11bit");
break;
case COMPRESSION_DEFLATE:	  /* 32946 Deflate compression */
strcpy (compressionid, "Deflate");
break;
case COMPRESSION_ADOBE_DEFLATE: /* 8 Deflate compression */
strcpy (compressionid, "Adobe deflate");
break;
default:
strcpy (compressionid, "None/unknown");
break;
}
TIFFError("loadImage", "Input compression %s", compressionid);
#endif

scanlinesize = TIFFScanlineSize(in);
image->bps = bps;
image->spp = spp;
image->planar = planar;
image->width = width;
image->length = length;
image->xres = xres;
image->yres = yres;
image->res_unit = res_unit;
image->compression = input_compression;
image->photometric = input_photometric;
#ifdef DEBUG2
char photometricid[12];

switch (input_photometric)
{
case PHOTOMETRIC_MINISWHITE:
strcpy (photometricid, "MinIsWhite");
break;
case PHOTOMETRIC_MINISBLACK:
strcpy (photometricid, "MinIsBlack");
break;
case PHOTOMETRIC_RGB:
strcpy (photometricid, "RGB");
break;
case PHOTOMETRIC_PALETTE:
strcpy (photometricid, "Palette");
break;
case PHOTOMETRIC_MASK:
strcpy (photometricid, "Mask");
break;
case PHOTOMETRIC_SEPARATED:
strcpy (photometricid, "Separated");
break;
case PHOTOMETRIC_YCBCR:
strcpy (photometricid, "YCBCR");
break;
case PHOTOMETRIC_CIELAB:
strcpy (photometricid, "CIELab");
break;
case PHOTOMETRIC_ICCLAB:
strcpy (photometricid, "ICCLab");
break;
case PHOTOMETRIC_ITULAB:
strcpy (photometricid, "ITULab");
break;
case PHOTOMETRIC_LOGL:
strcpy (photometricid, "LogL");
break;
case PHOTOMETRIC_LOGLUV:
strcpy (photometricid, "LOGLuv");
break;
default:
strcpy (photometricid, "Unknown");
break;
}
TIFFError("loadImage", "Input photometric interpretation %s", photometricid);

#endif
image->orientation = orientation;
switch (orientation)
{
case 0:
case ORIENTATION_TOPLEFT:
image->adjustments = 0;
break;
case ORIENTATION_TOPRIGHT:
image->adjustments = MIRROR_HORIZ;
break;
case ORIENTATION_BOTRIGHT:
image->adjustments = ROTATECW_180;
break;
case ORIENTATION_BOTLEFT:
image->adjustments = MIRROR_VERT;
break;
case ORIENTATION_LEFTTOP:
image->adjustments = MIRROR_VERT | ROTATECW_90;
break;
case ORIENTATION_RIGHTTOP:
image->adjustments = ROTATECW_90;
break;
case ORIENTATION_RIGHTBOT:
image->adjustments = MIRROR_VERT | ROTATECW_270;
break;
case ORIENTATION_LEFTBOT:
image->adjustments = ROTATECW_270;
break;
default:
image->adjustments = 0;
image->orientation = ORIENTATION_TOPLEFT;
}

if ((bps == 0) || (spp == 0))
{
TIFFError("loadImage", "Invalid samples per pixel (%d) or bits per sample (%d)",
spp, bps);
return (-1);
}

if (TIFFIsTiled(in))
{
readunit = TILE;
tlsize = TIFFTileSize(in);
ntiles = TIFFNumberOfTiles(in);
TIFFGetField(in, TIFFTAG_TILEWIDTH, &tw);
TIFFGetField(in, TIFFTAG_TILELENGTH, &tl);

tile_rowsize  = TIFFTileRowSize(in);
if (ntiles == 0 || tlsize == 0 || tile_rowsize == 0)
{
TIFFError("loadImage", "File appears to be tiled, but the number of tiles, tile size, or tile rowsize is zero.");
exit(-1);
}
buffsize = tlsize * ntiles;
if (tlsize != (buffsize / ntiles))
{
TIFFError("loadImage", "Integer overflow when calculating buffer size");
exit(-1);
}

if (buffsize < (uint32)(ntiles * tl * tile_rowsize))
{
buffsize = ntiles * tl * tile_rowsize;
if (ntiles != (buffsize / tl / tile_rowsize))
{
TIFFError("loadImage", "Integer overflow when calculating buffer size");
exit(-1);
}

#ifdef DEBUG2
TIFFError("loadImage",
"Tilesize %u is too small, using ntiles * tilelength * tilerowsize %lu",
tlsize, (unsigned long)buffsize);
#endif
}

if (dump->infile != NULL)
dump_info (dump->infile, dump->format, "",
"Tilesize: %u, Number of Tiles: %u, Tile row size: %u",
tlsize, ntiles, tile_rowsize);
}
else
{
uint32 buffsize_check;
readunit = STRIP;
TIFFGetFieldDefaulted(in, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);
stsize = TIFFStripSize(in);
nstrips = TIFFNumberOfStrips(in);
if (nstrips == 0 || stsize == 0)
{
TIFFError("loadImage", "File appears to be striped, but the number of stipes or stripe size is zero.");
exit(-1);
}

buffsize = stsize * nstrips;
if (stsize != (buffsize / nstrips))
{
TIFFError("loadImage", "Integer overflow when calculating buffer size");
exit(-1);
}
buffsize_check = ((length * width * spp * bps) + 7);
if (length != ((buffsize_check - 7) / width / spp / bps))
{
TIFFError("loadImage", "Integer overflow detected.");
exit(-1);
}
if (buffsize < (uint32) (((length * width * spp * bps) + 7) / 8))
{
buffsize =  ((length * width * spp * bps) + 7) / 8;
#ifdef DEBUG2
TIFFError("loadImage",
"Stripsize %u is too small, using imagelength * width * spp * bps / 8 = %lu",
stsize, (unsigned long)buffsize);
#endif
}

if (dump->infile != NULL)
dump_info (dump->infile, dump->format, "",
"Stripsize: %u, Number of Strips: %u, Rows per Strip: %u, Scanline size: %u",
stsize, nstrips, rowsperstrip, scanlinesize);
}

if (input_compression == COMPRESSION_JPEG)
{  /* Force conversion to RGB */
jpegcolormode = JPEGCOLORMODE_RGB;
TIFFSetField(in, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
}
/* The clause up to the read statement is taken from Tom Lane's tiffcp patch */
else
{   /* Otherwise, can't handle subsampled input */
if (input_photometric == PHOTOMETRIC_YCBCR)
{
TIFFGetFieldDefaulted(in, TIFFTAG_YCBCRSUBSAMPLING,
&subsampling_horiz, &subsampling_vert);
if (subsampling_horiz != 1 || subsampling_vert != 1)
{
TIFFError("loadImage",
"Can't copy/convert subsampled image with subsampling %d horiz %d vert",
subsampling_horiz, subsampling_vert);
return (-1);
}
}
}

read_buff = *read_ptr;
/* +3 : add a few guard bytes since reverseSamples16bits() can read a bit */
/* outside buffer */
if (!read_buff)
read_buff = (unsigned char *)_TIFFmalloc(buffsize+3);
else
{
if (prev_readsize < buffsize)
{
new_buff = _TIFFrealloc(read_buff, buffsize+3);
if (!new_buff)
{
free (read_buff);
read_buff = (unsigned char *)_TIFFmalloc(buffsize+3);
}
else
read_buff = new_buff;
}
}
if (!read_buff)
{
TIFFError("loadImage", "Unable to allocate/reallocate read buffer");
return (-1);
}

read_buff[buffsize] = 0;
read_buff[buffsize+1] = 0;
read_buff[buffsize+2] = 0;

prev_readsize = buffsize;
*read_ptr = read_buff;

/* N.B. The read functions used copy separate plane data into a buffer as interleaved
* samples rather than separate planes so the same logic works to extract regions
* regardless of the way the data are organized in the input file.
*/
switch (readunit) {
case STRIP:
if (planar == PLANARCONFIG_CONTIG)
{
if (!(readContigStripsIntoBuffer(in, read_buff)))
{
TIFFError("loadImage", "Unable to read contiguous strips into buffer");
return (-1);
}
}
else
{
if (!(readSeparateStripsIntoBuffer(in, read_buff, length, width, spp, dump)))
{
TIFFError("loadImage", "Unable to read separate strips into buffer");
return (-1);
}
}
break;

case TILE:
if (planar == PLANARCONFIG_CONTIG)
{
if (!(readContigTilesIntoBuffer(in, read_buff, length, width, tw, tl, spp, bps)))
{
TIFFError("loadImage", "Unable to read contiguous tiles into buffer");
return (-1);
}
}
else
{
if (!(readSeparateTilesIntoBuffer(in, read_buff, length, width, tw, tl, spp, bps)))
{
TIFFError("loadImage", "Unable to read separate tiles into buffer");
return (-1);
}
}
break;
default: TIFFError("loadImage", "Unsupported image file format");
return (-1);
break;
}
if ((dump->infile != NULL) && (dump->level == 2))
{
dump_info  (dump->infile, dump->format, "loadImage",
"Image width %d, length %d, Raw image data, %4d bytes",
width, length,  buffsize);
dump_info  (dump->infile, dump->format, "",
"Bits per sample %d, Samples per pixel %d", bps, spp);

for (i = 0; i < length; i++)
dump_buffer(dump->infile, dump->format, 1, scanlinesize,
i, read_buff + (i * scanlinesize));
}
return (0);
}

int main(int argc, char **argv)
{
  if (argc < 2) {
    std::cout << "Needs input.tif\n";
    return EXIT_FAILURE;
  }

  TIFF* tif = TIFFOpen(argv[1], "r");
  if (tif) {
    tdata_t buf;
    tstrip_t strip;

    // TODO: count is N(SamplesPerPixel)
    uint16_t sampleformat;
    if (!TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleformat)) {
      sampleformat = 1;
    }

    uint16_t bitspersample;
    if (!TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitspersample)) {
      std::cout << "TIFFTAG_BITSPERSAMPLE not found\n";
      return -1;
    }

    std::cout << "bitspersample = " << bitspersample << "\n";
    std::cout << "sampleformat = " << sampleformat << "\n";

    std::cout << "strip size: " << TIFFStripSize(tif) << "\n";

    buf = _TIFFmalloc(TIFFStripSize(tif));
  	for (strip = 0; strip < TIFFNumberOfStrips(tif); strip++) {
		  TIFFReadEncodedStrip(tif, strip, buf, (tsize_t) -1);
      if (sampleformat == 3) { // IEEEFP
        assert(bitspersample == 32); // TODO: 64bit float
        const float *pixels = reinterpret_cast<const float *>(buf);
        const size_t num_pixels = TIFFStripSize(tif) / sizeof(float);
        for (size_t i = 0; i < num_pixels; i++) {
          std::cout << "val[" << i << "] = " << pixels[i] << "\n";
        }
      } else if (sampleformat == 1) { // uint
        if (bitspersample == 16) {
          const uint16_t *pixels = reinterpret_cast<const uint16_t *>(buf);
          const size_t num_pixels = TIFFStripSize(tif) / sizeof(uint16_t);
          for (size_t i = 0; i < num_pixels; i++) {
            std::cout << "val[" << i << "] = " << pixels[i] << "\n";
          }
        }
      }
    }
	  _TIFFfree(buf);
	  TIFFClose(tif);
  }

  return EXIT_SUCCESS;
}

void XTiffImage::loadTIFF(const std::string& utf8Path) {
    TIFFSetWarningHandler(onTiffWarning);
    TIFFSetErrorHandler(onTiffError);
    auto path = platform::UTF8ToACP(utf8Path);
    tif = XTIFFOpen(path.c_str(), "r");

    if (!tif) {
        throw std::runtime_error("Couldn't open TIFF");
    }

    if (!TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &fullWidth)) {
        XTIFFClose(tif);
        throw std::runtime_error("TIFF has no width");
    }

    if (!TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &fullHeight)) {
        XTIFFClose(tif);
        throw std::runtime_error("TIFF has no height");
    }
}

void Compute(OpKernelContext* context) override {
    const Tensor* input_tensor;
    OP_REQUIRES_OK(context, context->input("input", &input_tensor));

    std::istringstream input_stream(input_tensor->scalar<tstring>()(),
                                    std::ios_base::in | std::ios_base::binary);

    std::unique_ptr<TIFF, void (*)(TIFF*)> tiff(
        XTIFFStreamOpen("memory", &input_stream), [](TIFF* p) {
          if (p != nullptr) {
            XTIFFClose(p);
          }
        });
    OP_REQUIRES(context, (tiff.get() != nullptr),
                errors::InvalidArgument("unable to open TIFF from memory"));

    std::vector<TensorShape> shape;
    std::vector<DataType> dtype;
    do {
      unsigned int height, width;
      TIFFGetField(tiff.get(), TIFFTAG_IMAGELENGTH, &height);
      TIFFGetField(tiff.get(), TIFFTAG_IMAGEWIDTH, &width);

      unsigned short channels;
      TIFFGetField(tiff.get(), TIFFTAG_SAMPLESPERPIXEL, &channels);

      shape.push_back(
          TensorShape({static_cast<int64>(height), static_cast<int64>(width),
                       static_cast<int64>(channels)}));

      unsigned short format, bits;
      if (!TIFFGetField(tiff.get(), TIFFTAG_SAMPLEFORMAT, &format)) {
        // If format is not defined, then we assume format is SAMPLEFORMAT_UINT
        format = SAMPLEFORMAT_UINT;
      }
      TIFFGetField(tiff.get(), TIFFTAG_BITSPERSAMPLE, &bits);
      DataType pixel_dtype;
      switch (format) {
        case SAMPLEFORMAT_UINT:
          switch (bits) {
            case 8:
              pixel_dtype = DT_UINT8;
              break;
            case 16:
              pixel_dtype = DT_UINT16;
              break;
            default:
              OP_REQUIRES(context, false,
                          errors::InvalidArgument("unsupported bits ", bits,
                                                  " for uint"));
          }
          break;
        case SAMPLEFORMAT_INT:
          switch (bits) {
            default:
              OP_REQUIRES(context, false,
                          errors::InvalidArgument("unsupported bits ", bits,
                                                  " for int"));
          }
          break;
        case SAMPLEFORMAT_IEEEFP:
          switch (bits) {
            case 16:
              pixel_dtype = DT_HALF;
              break;
            default:
              OP_REQUIRES(context, false,
                          errors::InvalidArgument("unsupported bits ", bits,
                                                  " for fp"));
          }
          break;
        default:
          OP_REQUIRES(context, false,
                      errors::InvalidArgument("unsupported format ", format));
      }
      dtype.push_back(pixel_dtype);

      // GeoTIFF specifi information
      // std::unique_ptr<GTIF, void(*)(GTIF*)> gtif(GTIFNew(tiff.get()),
      // [](GTIF* p) { if (p != nullptr) { GTIFFree(p); } });
      // OP_REQUIRES(context, (gtif.get() != nullptr),
      // errors::InvalidArgument("unable to read GeoTIFF information"));
      // GTIFPrint(gtif,0,0);
    } while (TIFFReadDirectory(tiff.get()));

    Tensor* shape_tensor = nullptr;
    OP_REQUIRES_OK(context,
                   context->allocate_output(
                       0, TensorShape({static_cast<int64>(shape.size()), 3}),
                       &shape_tensor));
    for (size_t i = 0; i < shape.size(); i++) {
      shape_tensor->flat<int64>()(i * 3) = shape[i].dim_size(0);
      shape_tensor->flat<int64>()(i * 3 + 1) = shape[i].dim_size(1);
      shape_tensor->flat<int64>()(i * 3 + 2) = shape[i].dim_size(2);
    }
    Tensor* dtype_tensor = nullptr;
    OP_REQUIRES_OK(
        context,
        context->allocate_output(
            1, TensorShape({static_cast<int64>(dtype.size())}), &dtype_tensor));
    for (size_t i = 0; i < dtype.size(); i++) {
      dtype_tensor->flat<int64>()(i) = dtype[i];
    }
  }

void Compute(OpKernelContext* context) override {
    const Tensor* input_tensor;
    OP_REQUIRES_OK(context, context->input("input", &input_tensor));

    const Tensor* index_tensor;
    OP_REQUIRES_OK(context, context->input("index", &index_tensor));

    std::istringstream input_stream(input_tensor->scalar<tstring>()(),
                                    std::ios_base::in | std::ios_base::binary);

    std::unique_ptr<TIFF, void (*)(TIFF*)> tiff(
        XTIFFStreamOpen("memory", &input_stream), [](TIFF* p) {
          if (p != nullptr) {
            XTIFFClose(p);
          }
        });
    OP_REQUIRES(context, (tiff.get() != nullptr),
                errors::InvalidArgument("unable to open TIFF from memory"));

    int status = TIFFSetDirectory(tiff.get(), index_tensor->scalar<int64>()());
    OP_REQUIRES(context, (status),
                errors::InvalidArgument("unable to set TIFF directory to ",
                                        index_tensor->scalar<int64>()()));
    unsigned int height, width;
    TIFFGetField(tiff.get(), TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tiff.get(), TIFFTAG_IMAGEWIDTH, &width);

    Tensor* image_tensor = nullptr;
    OP_REQUIRES_OK(context,
                   context->allocate_output(
                       0,
                       TensorShape({static_cast<int64>(height),
                                    static_cast<int64>(width), channels_}),
                       &image_tensor));

    uint32* raster =
        reinterpret_cast<uint32*>(image_tensor->flat<uint8>().data());
    OP_REQUIRES(context,
                (TIFFReadRGBAImageOriented(tiff.get(), width, height, raster,
                                           ORIENTATION_TOPLEFT, 0)),
                errors::InvalidArgument("unable to read directory: ",
                                        index_tensor->scalar<int64>()()));
  }

bool ossimGeoTiff::readTags( TIFF *tiff, ossim_uint32 entryIdx, bool ownTiffPtrFlag)
{
   std::lock_guard<std::mutex> lock(theMutex);

   if (!tiff)
   {
      return false;
   }

   if (!TIFFSetDirectory(tiff, (ossim_uint16)entryIdx))
   {
      return false;
   }

   GTIF *gtif = GTIFNew(tiff);
   if (!gtif)
   {
      return false;
   }

   if (theTiffPtr)
   {
      XTIFFClose(theTiffPtr);
   }
   theTiffPtr = tiff;

   if (thePrivateDefinitions->m_defs)
   {
      delete thePrivateDefinitions->m_defs;
      ;
   }
   thePrivateDefinitions->m_defs = new GTIFDefn;
   GTIFGetDefn(gtif, thePrivateDefinitions->m_defs);
   ossim_uint32 idx = 0;
   theGeoKeysPresentFlag = true;
   if (traceDebug())
   {
      ossimNotify(ossimNotifyLevel_DEBUG)
          << "ossimGeoTiff::readTags: Raw Geotiff Tags are\n";
      GTIFPrint(gtif, 0, 0);
   }
   if (TIFFGetField(theTiffPtr,
                    TIFFTAG_IMAGELENGTH,
                    &theLength))
   {
   }
   if (TIFFGetField(theTiffPtr,
                    TIFFTAG_IMAGEWIDTH,
                    &theWidth))
   {
   }
   theScaleFactor = 0.0;
   theModelType = thePrivateDefinitions->m_defs->Model;
   theGcsCode = thePrivateDefinitions->m_defs->GCS;
   thePcsCode = thePrivateDefinitions->m_defs->PCS;
   theDatumCode = thePrivateDefinitions->m_defs->Datum;
   theAngularUnits = thePrivateDefinitions->m_defs->UOMAngle;
   theLinearUnitsCode = thePrivateDefinitions->m_defs->UOMLength;

   if (theAngularUnits == ANGULAR_DMS_HEMISPHERE || theAngularUnits == 9122)
   {
      //---
      // Hack for bug, where the libgeotiff funtion GTIFGetDefn sets the angular units
      // incorrectly to ANGULAR_DMS_HEMISPHERE:
      if (traceDebug())
      {
         ossimNotify(ossimNotifyLevel_WARN)
             << " WARNING ossimGeoTiff::addImageGeometry:"
             << "The angular units (key 2054) is set to ANGULAR_DMS_HEMISPHERE!"
             << "\nAssuming \"Angular_Degree\"..." << std::endl;
      }
      theAngularUnits = ANGULAR_DEGREE;
   }

#if 0
   ossim_uint16 modelType;
   if(GTIFKeyGet(gtif, GTModelTypeGeoKey, &modelType, 0, 1))
   {
      theModelType = (ModelType)modelType;
   }
#endif
   if (GTIFKeyGet(gtif, GTRasterTypeGeoKey, &theRasterType, 0, 1))
   {
   }

#if 0
   if(GTIFKeyGet(gtif, GeographicTypeGeoKey, &theGcsCode, 0, 1))
   {
   }
   if(GTIFKeyGet(gtif, GeogGeodeticDatumGeoKey, &theDatumCode, 0, 1))
   {
   }
   if(GTIFKeyGet(gtif, GeogAngularUnitsGeoKey, &theAngularUnits, 0, 1))
   {
   }
#endif

   if (GTIFKeyGet(gtif, ProjectedCSTypeGeoKey, &thePcsCode, 0, 1))
      parsePcsCode();

   //---
   // ESH 2/2008 -- Handle geotiff's with state plane coordinate systems produced by ERDAS.
   // They use the citation filed to specify the geometry (complete HACK by Erdas)
   //---
   else
   {
      const int CITATION_STRING_SIZE = 512;
      char citationStr[CITATION_STRING_SIZE];
      if (GTIFKeyGet(gtif, GTCitationGeoKey, &citationStr,
                     0, CITATION_STRING_SIZE))
      {
         ossimString gTCitation = citationStr; // key 1026

         // Extract state plane string from the citation key
         ossimString projStrTemp =
             gTCitation.afterRegExp("Projection Name = ");

         ossimString projStr = projStrTemp.beforeRegExp("\n");
         if (projStr.empty() == false)
         {
            ossimEpsgProjectionFactory *f = ossimEpsgProjectionFactory::instance();
            ossimProjection *proj = f->createProjection(projStr);
            ossimMapProjection *map_proj = PTR_CAST(ossimMapProjection, proj);
            parseProjection(map_proj);
         }
      } // End of "if(GTIFKeyGet(gtif, GTCitationGeoKey..."
   }

   char *buf = 0;
   theOriginLon = ossim::nan();
   theOriginLat = ossim::nan();
   if (GTIFKeyGet(gtif, PCSCitationGeoKey, &buf, 0, 1))
   {
      thePcsCitation = ossimString(buf);
   }
   GTIFKeyGet(gtif, ProjCoordTransGeoKey, &theCoorTransGeoCode, 0, 1);
   for (idx = 0; idx < (ossim_uint32)thePrivateDefinitions->m_defs->nParms; ++idx)
   {
      switch (thePrivateDefinitions->m_defs->ProjParmId[idx])
      {
      case ProjStdParallel1GeoKey:
      {
         theStdPar1 = thePrivateDefinitions->m_defs->ProjParm[idx];
         break;
      }
      case ProjStdParallel2GeoKey:
      {
         theStdPar2 = thePrivateDefinitions->m_defs->ProjParm[idx];
         break;
      }
      case ProjOriginLongGeoKey:
      {
         theOriginLon = thePrivateDefinitions->m_defs->ProjParm[idx];
         break;
      }
      case ProjOriginLatGeoKey:
      {
         theOriginLat = thePrivateDefinitions->m_defs->ProjParm[idx];
