bool	ExtractGeoTiff(DEMGeo& inMap, const char * inFileName, int post_style, int no_geo_needed)
{
	int result = -1;
	double	corners[8];
	TIFF * tif;
	TIFFErrorHandler	warnH = TIFFSetWarningHandler(IgnoreTiffWarnings);
	TIFFErrorHandler	errH = TIFFSetErrorHandler(IgnoreTiffErrs);
	StTiffMemFile	tiffMem(inFileName);
	if (tiffMem.file == NULL) goto bail;

	printf("Trying file: %s\n", inFileName);
	tif = XTIFFClientOpen(inFileName, "r", &tiffMem,
	    MemTIFFReadWriteProc, MemTIFFReadWriteProc,
	    MemTIFFSeekProc, MemTIFFCloseProc,
	    MemTIFFSizeProc,
	    MemTIFFMapFileProc, MemTIFFUnmapFileProc);
	printf("Opened TIF file.\n");
//    TIFF* tif = TIFFOpen(inFileName, "r");

    if (tif == NULL) goto bail;

	if (!FetchTIFFCornersWithTIFF(tif, corners, post_style))
	{
		if(no_geo_needed)
		{
			printf("TIFF has no corners - using default.\n");
			inMap.mWest = -180;
			inMap.mSouth = -90;
			inMap.mEast = 180;
			inMap.mNorth = 90;
			inMap.mPost = 1;
		}
		else
		{
			printf("Could not read GeoTiff projection data.\n");
			return false;
		}
	}

	inMap.mWest = corners[0];
	inMap.mSouth = corners[1];
	inMap.mEast = corners[6];
	inMap.mNorth = corners[7];
	inMap.mPost = (post_style == dem_want_Post);

	printf("Corners: %.12lf,%.12lf   %.12lf,%.12lf   %.12lf,%.12lf   %.12lf,%.12lf\n",
		corners[0], corners[1], corners[2], corners[3], corners[4], corners[5], corners[6], corners[7]);

	uint32 w, h;
	uint16 cc;
	uint16 d;
	uint16 format;

	format = SAMPLEFORMAT_UINT;	// sample format is NOT mandatory - unsigned int is the default if not present!


	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
	TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &cc);
	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &d);
	TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &format);
	printf("Image is: %dx%d, samples: %d, depth: %d, format: %d\n", w, h, cc, d, format);

	inMap.resize(w,h);

	if(TIFFIsTiled(tif))
	{
		uint32	tw, th;
		TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tw);
		TIFFGetField(tif, TIFFTAG_TILELENGTH, &th);
		tdata_t buf = _TIFFmalloc(TIFFTileSize(tif));
		for (int y = 0; y < h; y += th)
		for (int x = 0; x < w; x += tw)
		{
			result = TIFFReadTile(tif, buf, x, y, 0, 0);
			if (result == -1) { printf("Tiff error in read.\n"); break; }

			int ux = min(tw,w-x);
			int uy = min(th,h-y);

			switch(format) {
			case SAMPLEFORMAT_UINT:
				switch(d) {
				case 8:
					copy_tile<unsigned char>((const unsigned char *) buf, x,y,ux,uy, inMap);
					break;
				case 16:
					copy_tile<unsigned short>((const unsigned short *) buf, x,y,ux,uy, inMap);
					break;
				case 32:
					copy_tile<unsigned int>((const unsigned int *) buf, x,y,ux,uy, inMap);
					break;
				default:
					printf("TIFF error: unsupported unsigned int sample depth: %d\n", d);
					goto bail;
				}
				break;
			case SAMPLEFORMAT_INT:
				switch(d) {
				case 8:
					copy_tile<char>((const char *) buf, x,y,ux,uy, inMap);
					break;
				case 16:
					copy_tile<short>((const short *) buf, x,y,ux,uy, inMap);
					break;
				case 32:
					copy_tile<int>((const int *) buf, x,y,ux,uy, inMap);
					break;
				default:
					printf("TIFF error: unsupported signed int sample depth: %d\n", d);
					goto bail;
				}
				break;
			case SAMPLEFORMAT_IEEEFP:
				switch(d) {
				case 32:
					copy_tile<float>((const float *) buf, x,y,ux,uy, inMap);
					break;
				case 64:
					copy_tile<double>((const double *) buf, x,y,ux,uy, inMap);
					break;
				default:
					printf("TIFF error: unsupported floating point sample depth: %d\n", d);
					goto bail;
				}
				break;
			default:
				printf("TIFF error: unsupported pixel format %d\n", format);
				break;			
			}
		}

		_TIFFfree(buf);	

		TIFFClose(tif);

		TIFFSetWarningHandler(warnH);
		TIFFSetErrorHandler(errH);
		return result != -1;
	}
	else
	{
		tsize_t line_size = TIFFScanlineSize(tif);
		tdata_t aline = _TIFFmalloc(line_size);

		int cs = TIFFCurrentStrip(tif);
		int nos = TIFFNumberOfStrips(tif);
		int cr = TIFFCurrentRow  (tif);

		for (int y = 0; y < h; ++y)
		{
			result = TIFFReadScanline(tif, aline, y, 0);
			if (result == -1) { printf("Tiff error in read.\n"); break; }

			switch(format) {
			case SAMPLEFORMAT_UINT:
				switch(d) {
				case 8:
					copy_scanline<unsigned char>((const unsigned char *) aline, y, inMap);
					break;
				case 16:
					copy_scanline<unsigned short>((const unsigned short *) aline, y, inMap);
					break;
				case 32:
					copy_scanline<unsigned int>((const unsigned int *) aline, y, inMap);
					break;
				default:
					printf("TIFF error: unsupported unsigned int sample depth: %d\n", d);
					goto bail;
				}
				break;
			case SAMPLEFORMAT_INT:
				switch(d) {
				case 8:
					copy_scanline<char>((const char *) aline, y, inMap);
					break;
				case 16:
					copy_scanline<short>((const short *) aline, y, inMap);
					break;
				case 32:
					copy_scanline<int>((const int *) aline, y, inMap);
					break;
				default:
					printf("TIFF error: unsupported signed int sample depth: %d\n", d);
					goto bail;
				}
				break;
			case SAMPLEFORMAT_IEEEFP:
				switch(d) {
				case 32:
					copy_scanline<float>((const float *) aline, y, inMap);
					break;
				case 64:
					copy_scanline<double>((const double *) aline, y, inMap);
					break;
				default:
					printf("TIFF error: unsupported floating point sample depth: %d\n", d);
					goto bail;
				}
				break;
			default:
				printf("TIFF error: unsupported pixel format %d\n", format);
				break;			
			}

		}
		_TIFFfree(aline);


		TIFFClose(tif);

		TIFFSetWarningHandler(warnH);
		TIFFSetErrorHandler(errH);
		return result != -1;
	}
bail:
	TIFFSetWarningHandler(warnH);
	TIFFSetErrorHandler(errH);
	return false;

}

bool	FetchTIFFCornersWithTIFF(TIFF * tiffFile, double corners[8], int& post_pos, gcp_t * gcp)
{
	bool retVal = false;
#if USE_TIF
	GTIF * gtif = GTIFNew(tiffFile);
	if (gtif)
	{
		GTIFPrint(gtif, GTIFPrintFunc, 0);

		GTIFDefn 	defn;
        if( GTIFGetDefn( gtif, &defn ) )
        {
#if WED
			GTIFPrintDefn(&defn, gLogFile ? gLogFile : stdout);
#endif
			LOG_MSG("PROJ.4 Definition: %s\n", GTIFGetProj4Defn(&defn));
        	int xs, ys;
            TIFFGetField( tiffFile, TIFFTAG_IMAGEWIDTH, &xs );
            TIFFGetField( tiffFile, TIFFTAG_IMAGELENGTH, &ys );
			double xsize(xs), ysize(ys);

			uint16 pixel_type;
			double dx(0.0), dy(0.0);

			if (GTIFKeyGet(gtif,GTRasterTypeGeoKey, &pixel_type, 0, 1) != 1)
				pixel_type = RasterPixelIsArea;

			// If we are a 'point sampled' file, the upper right edge _IS_ the last pixels!  Thus
			// passing in the number of pixels induces an off-by-one.  Cut the size by one to fix this.
			if(pixel_type == RasterPixelIsPoint)
			{
				xsize -= 1.0;
				ysize -= 1.0;
			}

			if(pixel_type == RasterPixelIsArea && post_pos == dem_want_Post)
			{
				// This is an area-pixel DEM, but we are going to reinterpret it via pixel centers.
				// This will INSET the corners of the pixels by 1/2 pixel to the sample centers.
				dx=0.5;
				dy=0.5;
			}

			if(pixel_type == RasterPixelIsPoint && post_pos == dem_want_Area)
			{
				// This is a center post sampled image, but we are going to treat it as area.  Each
				// pixel "sticks out" a bit in its coverage, so extend.
				dx=-0.5;
				dy=-0.5;
			}

			if(post_pos == dem_want_File)
				post_pos = (pixel_type==RasterPixelIsPoint) ? dem_want_Post : dem_want_Area;

        	if (TransformTiffCorner(gtif, &defn,	   dx, ysize-dy, corners[0], corners[1]) &&
	        	TransformTiffCorner(gtif, &defn, xsize-dx, ysize-dy, corners[2], corners[3]) &&
	        	TransformTiffCorner(gtif, &defn,	   dx,		 dy, corners[4], corners[5]) &&
	        	TransformTiffCorner(gtif, &defn, xsize-dx,		 dy, corners[6], corners[7]))
	        {
	        	retVal = true;
	        }

			if (gcp)
			{
				gcp->pts.clear();
				gcp->size_x = 1;
				gcp->size_y = 1;
				if (xsize > 1536 || ysize > 1536)  // calculate control points for map warping/projection, if texture has high resolution
				{
					gcp->size_x = intlim(roundf((double) xsize / 1024.0), 2, 10) + 1;
					gcp->size_y = intlim(roundf((double) ysize / 1024.0), 2, 10) + 1;
					for (int y = 0; y < gcp->size_y; y++)
						for (int x = 0; x < gcp->size_x; x++)
						{
							double lon, lat;
							if (TransformTiffCorner(gtif, &defn, dx + x * xsize / (gcp->size_x - 1), ysize - y * ysize / (gcp->size_y - 1) - dy, lon, lat))
								gcp->pts.push_back(Point2(lon, lat));
						}
				}
			}
		}
		GTIFFree(gtif);
	}
#endif
	return retVal;
}

static	bool	FetchTIFFCorners(const char * inFileName, double corners[8])
{
	bool retVal = false;
	TIFF * tiffFile = XTIFFOpen(inFileName, "r");
	if (tiffFile)
	{
		GTIF * gtif = GTIFNew(tiffFile);
		if (gtif)
		{
			GTIFDefn 	defn;
	        if( GTIFGetDefn( gtif, &defn ) )
	        {
	        	int xsize, ysize;
	            TIFFGetField( tiffFile, TIFFTAG_IMAGEWIDTH, &xsize );
	            TIFFGetField( tiffFile, TIFFTAG_IMAGELENGTH, &ysize );

	        	if (TransformTiffCorner(gtif, &defn, 0,     ysize, corners[0], corners[1]) &&
		        	TransformTiffCorner(gtif, &defn, xsize, ysize, corners[2], corners[3]) &&
		        	TransformTiffCorner(gtif, &defn, 0,     0,     corners[4], corners[5]) &&
		        	TransformTiffCorner(gtif, &defn, xsize, 9,     corners[6], corners[7]))
		        {
		        	retVal = true;
		        }
			}
			GTIFFree(gtif);
		}

		XTIFFClose(tiffFile);
	}
	return retVal;
}

int		CreateBitmapFromTIF(const char * inFilePath, struct ImageInfo * outImageInfo)
{
	int result = -1;
	TIFFErrorHandler	errH = TIFFSetWarningHandler(IgnoreTiffWarnings);
	TIFFErrorHandler	errH2= TIFFSetErrorHandler(IgnoreTiffWarnings);
#if SUPPORT_UNICODE
    TIFF* tif = TIFFOpenW(convert_str_to_utf16(inFilePath).c_str(), "r");
#else
	FILE_case_correct_path path(inFilePath);  // If earth.wed.xml with case-incorrect .tif references are exchanged, this helps on Linux
    TIFF* tif = TIFFOpen(path, "r");
#endif
    if (tif == NULL) goto bail;

	uint32 w, h;
	uint16 cc;
	size_t npixels;
	uint32* raster;


	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
	TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &cc);

	npixels = w * h;
	raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
	if (raster != NULL) {
	    if (TIFFReadRGBAImage(tif, w, h, raster, 0)) {

			outImageInfo->data = (unsigned char *) malloc(npixels * 4);
			outImageInfo->width = w;
			outImageInfo->height = h;
			outImageInfo->channels = 4;
			outImageInfo->pad = 0;
			int	count = outImageInfo->width * outImageInfo->height;
			unsigned char * d = outImageInfo->data;
			unsigned char * s = (unsigned char *) raster;
			while (count--)
			{
#if BIG
				d[0] = s[1];	// B
				d[1] = s[2];	// G
				d[2] = s[3];	// R
				d[3] = s[0];	// A
#elif LIL
				d[0] = s[2];	// B
				d[1] = s[1];	// G
				d[2] = s[0];	// R
				d[3] = s[3];	// A
#else
	#error PLATFORM NOT DEFINED
#endif
				s += 4;
				d += 4;
			}
			result = 0;
	    }
	    _TIFFfree(raster);
	}
	TIFFClose(tif);
	TIFFSetWarningHandler(errH);
    return result;
bail:
	TIFFSetWarningHandler(errH);
	return -1;
}

bool	WED_ExtractGeoTiff(dem_info_t& inMap, const char* inFileName, int post_style)
{
	TIFF * tif;
#if SUPPORT_UNICODE
	XTIFFInitialize();
	tif = TIFFOpenW(convert_str_to_utf16(inFileName).c_str(), "r");
#else
	tif = XTIFFOpen(inFileName, "r");
#endif
	if (tif)
	{
		double	corners[8];
		if (FetchTIFFCornersWithTIFF(tif, corners, post_style))
	{

		// this assumes geopgrahic, not projected coordinates ...
		inMap.mWest = corners[0];
		inMap.mSouth = corners[1];
		inMap.mEast = corners[6];
		inMap.mNorth = corners[7];
		inMap.mPost = (post_style == dem_want_Post);

		uint32 w, h;
		uint16 cc;
		uint16 d;
		uint16 format = SAMPLEFORMAT_UINT;	// sample format is NOT mandatory - unsigned int is the default if not present!

		TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
		TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
		TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &cc);
		TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &d);
		TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &format);
//		printf("Image is: %dx%d, samples: %d, depth: %d, format: %d\n", w, h, cc, d, format);

		inMap.resize(w, h);

		if (TIFFIsTiled(tif))
		{
			uint32	tw, th;
			TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tw);
			TIFFGetField(tif, TIFFTAG_TILELENGTH, &th);
			vector<char> buf;
			buf.resize(TIFFTileSize(tif));
			for (int y = 0; y < h; y += th)
				for (int x = 0; x < w; x += tw)
				{
					if (TIFFReadTile(tif, buf.data(), x, y, 0, 0) == -1)
						goto bail;

					int ux = min(tw, w - x);
					int uy = min(th, h - y);
					int pad = tw - ux;

					switch (format) 
					{
					case SAMPLEFORMAT_UINT:
						switch (d) 
						{
						case 8:  copy_tile<unsigned char >((unsigned char*)  buf.data(), x, y, ux, uy, pad, inMap); break;
						case 16: copy_tile<unsigned short>((unsigned short*) buf.data(), x, y, ux, uy, pad, inMap); break;
						case 32: copy_tile<unsigned int  >((unsigned int*)   buf.data(), x, y, ux, uy, pad, inMap); break;
						default: goto bail;
						}
						break;
					case SAMPLEFORMAT_INT:
						switch (d) 
						{
						case 8:  copy_tile<char >((char* ) buf.data(), x, y, ux, uy, pad, inMap); break;
						case 16: copy_tile<short>((short*) buf.data(), x, y, ux, uy, pad, inMap); break;
						case 32: copy_tile<int  >((int*  ) buf.data(), x, y, ux, uy, pad, inMap); break;
						default: goto bail;
						}
						break;
					case SAMPLEFORMAT_IEEEFP:
						switch (d) 
						{
						case 32: copy_tile<float >((float* ) buf.data(), x, y, ux, uy, pad, inMap); break;
						case 64: copy_tile<double>((double*) buf.data(), x, y, ux, uy, pad, inMap); break;
						default: goto bail;
						}
						break;
					default: goto bail;
					}
				}
			XTIFFClose(tif);
			return true;
		}
		else
		{
			tsize_t line_size = TIFFScanlineSize(tif);
			vector<char> aline;
			aline.resize(line_size);

			int cs = TIFFCurrentStrip(tif);
			int nos = TIFFNumberOfStrips(tif);
			int cr = TIFFCurrentRow(tif);

			for (int y = 0; y < h; ++y)
			{
				if (TIFFReadScanline(tif, aline.data(), y, 0) == -1)
					goto bail;

				switch (format) 
				{
				case SAMPLEFORMAT_UINT:
					switch (d) 
					{
					case 8:  copy_scanline<unsigned char >((unsigned char* )aline.data(), y, inMap); break;
					case 16: copy_scanline<unsigned short>((unsigned short*)aline.data(), y, inMap); break;
					case 32: copy_scanline<unsigned int  >((unsigned int*  )aline.data(), y, inMap); break;
					default: goto bail;
					}
					break;
				case SAMPLEFORMAT_INT:
					switch (d) 
					{
					case 8:	 copy_scanline<char >((char* )aline.data(), y, inMap); break;
					case 16: copy_scanline<short>((short*)aline.data(), y, inMap); break;
					case 32: copy_scanline<int  >((int*  )aline.data(), y, inMap); break;
					default: goto bail;
					}
					break;
				case SAMPLEFORMAT_IEEEFP:
					switch (d) 
					{
					case 32: copy_scanline<float >((float* )aline.data(), y, inMap); break;
					case 64: copy_scanline<double>((double*)aline.data(), y, inMap); break;
					default: goto bail;
					}
					break;
				default: break;
				}
			}
			XTIFFClose(tif);
			return true;
		}

	}
	bail:
		XTIFFClose(tif);
		LOG_MSG("E/Dem Error reading DEM %s\n", inFileName);
	}
	else
		LOG_MSG("E/Dem Error opening DEM %s\n", inFileName);
	return false;
}

FloatImage * nv::ImageIO::loadFloatTIFF(const char * fileName, Stream & s)
{
	nvCheck(!s.isError());
	
	TIFF * tif = TIFFOpen(fileName, "r");
	//TIFF * tif = TIFFClientOpen(fileName, "r", &s, tiffReadWriteProc, tiffReadWriteProc, tiffSeekProc, tiffCloseProc, tiffSizeProc, tiffMapFileProc, tiffUnmapFileProc);
	
	if (!tif)
	{
		nvDebug("Can't open '%s' for reading\n", fileName);
		return NULL;
	}
	
	::uint16 spp, bpp, format;
	::uint32 width, height;
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp);
	TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp);
	TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &format);
	
	if (bpp != 8 && bpp != 16 && bpp != 32) {
		nvDebug("Can't load '%s', only 1 sample per pixel supported\n", fileName);
		TIFFClose(tif);
		return NULL;
	}
	
	AutoPtr<FloatImage> fimage(new FloatImage());
	fimage->allocate(spp, width, height);
	
	int linesize = TIFFScanlineSize(tif);
	tdata_t buf = (::uint8 *)nv::mem::malloc(linesize);
	
	for (uint y = 0; y < height; y++) 
	{
		TIFFReadScanline(tif, buf, y, 0);

		for (uint c=0; c<spp; c++ ) 
		{
			float * dst = fimage->scanline(y, c);

			for(uint x = 0; x < width; x++) 
			{
				if (bpp == 8)
				{
					dst[x] = float(((::uint8 *)buf)[x*spp+c]) / float(0xFF);
				}
				else if (bpp == 16)
				{
					dst[x] = float(((::uint16 *)buf)[x*spp+c]) / float(0xFFFF);
				}
				else if (bpp == 32)
				{
					if (format==SAMPLEFORMAT_IEEEFP)
					{
						dst[x] = float(((float *)buf)[x*spp+c]);
					}
					else
					{
						dst[x] = float(((::uint32 *)buf)[x*spp+c] >> 8) / float(0xFFFFFF);
					}

				}

			}
		}
	}

	nv::mem::free(buf);
	
	TIFFClose(tif);
	
	return fimage.release();
}

bool CLoadEerHeader::DoIt(TIFF* pTiff, int iEerSampling)
{
	m_iEerSampling = iEerSampling;
	memset(m_aiCamSize, 0, sizeof(m_aiCamSize));
	memset(m_aiFrmSize, 0, sizeof(m_aiFrmSize));
	//------------------------------------------
	TIFFGetField(pTiff, TIFFTAG_IMAGEWIDTH, &m_aiCamSize[0]);
	TIFFGetField(pTiff, TIFFTAG_IMAGELENGTH, &m_aiCamSize[1]);
	m_iNumFrames = TIFFNumberOfDirectories(pTiff);
	TIFFGetField(pTiff, TIFFTAG_COMPRESSION, &m_usCompression);
	if(m_usCompression == 65000) m_iNumBits = 8;
	else if(m_usCompression == 65001) m_iNumBits = 7;
	else m_iNumBits = -1;
	//-------------------
	bool bHasError = mCheckError();
	if(bHasError) return false;
	//-------------------------
	int iFact = 1;
	if(m_iEerSampling == 2) iFact = 2;
	else if(m_iEerSampling == 3) iFact = 4;
	m_aiFrmSize[0] = m_aiCamSize[0] * iFact;
	m_aiFrmSize[1] = m_aiCamSize[1] * iFact;
	return true;
}

bool CLoadTiffHeader::mReadImageSize(void)
{
	uint32_t uiWidth = 0;
	uint32_t uiLength = 0;
	int iRetX = TIFFGetField(m_pTiff, TIFFTAG_IMAGEWIDTH, &uiWidth);
        int iRetY = TIFFGetField(m_pTiff, TIFFTAG_IMAGELENGTH, &uiLength);
	if(iRetX == 0 || iRetY == 0) return false;
	m_aiImgSize[0] = uiWidth;
	m_aiImgSize[1] = uiLength;
	m_aiImgSize[2] = TIFFNumberOfDirectories(m_pTiff);
	return true;
}

bool CLoadTiffHeader::mReadMode(void)
{
        short sNumBits = 0;
	short sFormat = 0;
	TIFFGetField(m_pTiff, TIFFTAG_BITSPERSAMPLE, &sNumBits);
	bool bFormat = TIFFGetField(m_pTiff, TIFFTAG_SAMPLEFORMAT, &sFormat);
	//-------------------------------------------------------------------
	if(sNumBits == 8)
	{	m_iMode = Mrc::eMrcUChar;
		return true;
	}
        else if(sNumBits == 16)
	{	if(bFormat && sFormat == SAMPLEFORMAT_INT)
		{	m_iMode = Mrc::eMrcShort;
		}
                else
		{	m_iMode = Mrc::eMrcUShort;
		}
		return true;
	}
        else if(bFormat && sFormat == SAMPLEFORMAT_IEEEFP)
        {       m_iMode = Mrc::eMrcFloat;
		return true;
        }
	return false;
}

void CLoadTiffHeader::mReadPixelSize(void)
{
	float fResX = 0.0f;
	float fResY = 0.0f;
	short sResUnit = 0;
	bool bResX = TIFFGetField(m_pTiff, TIFFTAG_XRESOLUTION, &fResX);
	bool bResY = TIFFGetField(m_pTiff, TIFFTAG_YRESOLUTION, &fResY);
	TIFFGetField(m_pTiff, TIFFTAG_RESOLUTIONUNIT, &sResUnit);
	//-------------------------------------------------------
	if(bResX) 
	{	m_fPixelSize = (float)(fResX / 2.54e8);
	}
	else if(bResY)
	{	m_fPixelSize = (float)(fResY / 2.54e8);
	}
	else
	{	m_fPixelSize = 0.0f;
	} 
}

bool CLoadTiffHeader::mReadRowsPerStrip(void)
{
	uint32_t uiRowsPerStrip = 0;
	int iRetVal = TIFFGetField
        (  m_pTiff,
           TIFFTAG_ROWSPERSTRIP,
           &uiRowsPerStrip
        );
	if(iRetVal == 0) return false;
	//----------------------------
	m_aiTileSize[0] = m_aiImgSize[0];
	m_aiTileSize[1] = uiRowsPerStrip;
	m_aiNumTiles[0] = 1;
        m_aiNumTiles[1] = m_aiImgSize[1] / uiRowsPerStrip;
	if((m_aiNumTiles[1] * m_aiTileSize[1]) < m_aiImgSize[1])
	{	m_aiNumTiles[1] += 1;
	}
	return true;
}

bool CLoadTiffHeader::mReadTileSize(void)
{
	uint32_t uiWidth = 0;
	uint32_t uiLength = 0;
	int iRetX = TIFFGetField(m_pTiff, TIFFTAG_TILEWIDTH, &uiWidth);
	int iRetY = TIFFGetField(m_pTiff, TIFFTAG_TILELENGTH, &uiLength);
	if(iRetX == 0 || iRetY == 0) return false;
	//----------------------------------------
	m_aiTileSize[0] = uiWidth;
	m_aiTileSize[1] = uiLength;
	m_aiNumTiles[0] = m_aiImgSize[0] / m_aiTileSize[0];
	m_aiNumTiles[1] = m_aiImgSize[1] / m_aiTileSize[1];
	if((m_aiNumTiles[0] * m_aiTileSize[0]) < m_aiImgSize[0])
	{	m_aiNumTiles[0] += 1;
	}
	if((m_aiNumTiles[1] * m_aiTileSize[1]) < m_aiImgSize[1])
	{	m_aiNumTiles[1] += 1;
	}
	return true;
}

int metno::GeoTiff::read_diana(const std::string& infile, unsigned char* image[], int nchan, int /*chan*/[], dihead& ginfo)
{
  METLIBS_LOG_TIME();

  ginfo.noofcl = 0;
  ginfo.zsize =1;

  const int pal = head_diana(infile, ginfo);
  METLIBS_LOG_DEBUG(LOGVAL(pal));
  if (pal == -1)
    return -1;

  if (nchan == 0)
    return 1;

  std::unique_ptr<TIFF, CloseTIFF> in(TIFFOpen(infile.c_str(), "rc"));
  if (!in) {
    METLIBS_LOG_ERROR("TIFFOpen failed, probably not a TIFF file: '" << infile << "'");
    return -1;
  }

  tsample_t samplesperpixel;
  TIFFGetField(in.get(), TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);

  const int size = ginfo.xsize * ginfo.ysize;

  uint32  count;
  void    *data;
  // TIFFTAG_GDAL_METADATA 42112 defined in some projets
  // see https://www.awaresystems.be/imaging/tiff/tifftags/gdal_metadata.html
  if (samplesperpixel == 1 && TIFFGetField(in.get(), /*GDAL_METADATA*/ 42112, &count, &data)) {
    // this is an xml document, we just search for text and hope that
    // scale is after '... role="scale">'
    if (const char* t = strstr((char *)data, "scale")) {
      ginfo.AIr = atof(t+7);
    }
    // ... and offset after '... role="offset">'
    if (const char* t = strstr((char *)data, "offset")) {
      ginfo.BIr = atof(t + 8);
    }

    // Why not use BIr and AIr ?
    std::ostringstream oss;
    oss << "T=(" << ginfo.BIr << ")+(" << ginfo.AIr << ")*C";
    ginfo.cal_ir = oss.str();
  }
  METLIBS_LOG_DEBUG(LOGVAL(ginfo.projection.getProj4Definition()) << LOGVAL(size)
                    << LOGVAL(ginfo.xsize) << LOGVAL(ginfo.ysize) << LOGVAL(ginfo.zsize)
                    << LOGVAL(samplesperpixel) << LOGVAL(ginfo.time));
  /*
   * Memory allocated for image data in this function (*image) is freed
   * in function main process.
   */
  if (ginfo.zsize > MAXCHANNELS) {
    METLIBS_LOG_ERROR("ginfo.zsize > MAXCHANNELS!");
    return(-1);
  }

  // RGBA buffer
  image[0] = (unsigned char *) malloc((size)*4);
  memset(image[0], 0, size*4);

  std::string file = infile.substr(infile.rfind("/") + 1);
  ImageCache* mImageCache = ImageCache::getInstance();

  if (!mImageCache->getFromCache(file, (uint8_t*)image[0])) {
    if (!TIFFReadRGBAImageOriented(in.get(), ginfo.xsize, ginfo.ysize, (uint32*)image[0])) {
      METLIBS_LOG_ERROR("TIFFReadRGBAImageOriented (ORIENTATION_BOTLEFT) failed: size " <<  ginfo.xsize << "," << ginfo.ysize);
    }
    // GDAL_NODATA, see https://www.awaresystems.be/imaging/tiff/tifftags/gdal_nodata.html
    // Used by the GDAL library, contains an ASCII encoded nodata or background pixel value.
    if (samplesperpixel == 1 && TIFFGetField(in.get(), /* GDAL_NODATA */ 42113, &count, &data) && count > 0) {
      const char* ascii = (const char*)data;
      const unsigned int nodata = atoi(ascii);
      const uint32_t rgba_nodata = 0xFF << 24 | nodata << 16 | nodata << 8 | nodata;
      for (int i=0; i<size; ++i) {
        uint32_t* rgba = (uint32_t*)(&image[0][i*4]);
        if (*rgba == rgba_nodata)
          *rgba = 0;
      }
    }
    mImageCache->putInCache(file, (uint8_t*)image[0], size*4);
  }
  return(pal);
}

int metno::GeoTiff::head_diana(const std::string& infile, dihead &ginfo)
{
  METLIBS_LOG_TIME();

  ginfo.noofcl = 0;

  std::unique_ptr<TIFF, CloseXTIFF> in(XTIFFOpen(infile.c_str(), "rc"));
  if (!in) {
    METLIBS_LOG_ERROR("XTIFFOpen failed, probably not a TIFF file: '" << infile << "'");
    return -1;
  }

  // test whether this is a color palette image
  short pmi; // PhotometricInterpretation, 3: Baseline palette-color; 2: Baseline RGB
  if (!TIFFGetField(in.get(), TIFFTAG_PHOTOMETRIC, &pmi)) {
    METLIBS_LOG_ERROR("no TIFFTAG_PHOTOMETRIC in '" << infile << "'");
    return -1;
  }
  if (pmi == PHOTOMETRIC_PALETTE) { // see tiff.h for the constants
    unsigned short int *red, *green, *blue;
    if (!TIFFGetField(in.get(), TIFFTAG_COLORMAP, &red, &green, &blue)) {
      METLIBS_LOG_ERROR("no TIFFTAG_COLORMAP in '" << infile << "'");
      return -1;
    }

    for (int i=0; i<256; i++) {
      ginfo.cmap[0][i] = red[i];
      ginfo.cmap[1][i] = green[i];
      ginfo.cmap[2][i] = blue[i];
    }
  }
  // Rest is added by Niklas, for making greyscales of Storm single channel images...
  else if (pmi == PHOTOMETRIC_MINISWHITE) {
    for (int i=0; i<256; i++) {
      ginfo.cmap[0][i] = (255-i)*256;
      ginfo.cmap[1][i] = (255-i)*256;
      ginfo.cmap[2][i] = (255-i)*256;
    }
  } else if (pmi == PHOTOMETRIC_MINISBLACK) {
    for (int i=0; i<256; i++) {
      ginfo.cmap[0][i] = i*256;
      ginfo.cmap[1][i] = i*256;
      ginfo.cmap[2][i] = i*256;
    }
  }

  char* datetime = 0;
  if (TIFFGetField(in.get(), TIFFTAG_DATETIME, &datetime) && datetime) {
    METLIBS_LOG_DEBUG(LOGVAL(datetime));

    int year, month, day, hour, minute, sec;
    if (sscanf(datetime, "%4d-%2d-%2d %2d:%2d:%2d", &year, &month, &day, &hour, &minute, &sec) != 6) {
      if (sscanf(datetime, "%4d:%2d:%2d %2d:%2d:%2d", &year, &month, &day, &hour, &minute, &sec) != 6) {
         METLIBS_LOG_WARN("Invalid time in TIFFTAG_DATETIME '" << datetime << "'");
      }
    }
    if (year == 0)
      year = 2000;
    if (hour == 24) {
      hour = 0;
      ginfo.time = miutil::miTime(year, month, day, hour, minute, 0);
      ginfo.time.addDay(1);
    } else {
      ginfo.time = miutil::miTime(year, month, day, hour, minute, 0);
    }
    METLIBS_LOG_DEBUG(LOGVAL(ginfo.time));
  } else {
    METLIBS_LOG_WARN("TIFFTAG_DATETIME error");
  }

  std::unique_ptr<GTIF, FreeGTIFF> gtifin(GTIFNew(in.get()));
  if (!gtifin) {
    METLIBS_LOG_ERROR("could not init GTIF object");
    return -1;
  }

  double x_0, y_0, x_scale, y_scale;

  // Geospecific Tags
  uint32 transmatrix_size = 0;
  double* transmatrix = 0;
  const bool have_transmatrix = (TIFFGetField(in.get(), GTIFF_TRANSMATRIX, &transmatrix_size, &transmatrix) == 1) && (transmatrix_size == 16) && transmatrix;
  if (have_transmatrix) {
    x_scale = transmatrix[0];
    x_0 = transmatrix[3];
    y_scale = transmatrix[5];
    y_0 = transmatrix[7];
    if (transmatrix[1] != 0 || transmatrix[1] != 0 || transmatrix[4] != 0 || transmatrix[6] != 0) {
      METLIBS_LOG_WARN("only the linear part of GTIFF_TRANSMATRIX is supported in '" << infile << "'");
    }
  } else { // !have_transmatrix
    uint32 tiepointsize = 0;
    double* tiepoints = 0; //[6];
    const bool have_tiepoints = (TIFFGetField(in.get(), TIFFTAG_GEOTIEPOINTS, &tiepointsize, &tiepoints) == 1) && (tiepointsize >= 6) && tiepoints;

    uint32 pixscalesize = 0;
    double* pixscale = 0; //[3];
    const bool have_pixelscale = (TIFFGetField(in.get(), TIFFTAG_GEOPIXELSCALE, &pixscalesize, &pixscale) == 1) && (pixscalesize == 3) && pixscale;

    if (METLIBS_LOG_DEBUG_ENABLED()) {
      METLIBS_LOG_DEBUG("tiepointsize: " << tiepointsize);
      if (tiepoints) {
        for (uint32 i = 0; i < tiepointsize; i++)
          METLIBS_LOG_DEBUG("tiepoints[" << i << "]=" << tiepoints[i]);
      }
      METLIBS_LOG_DEBUG("pixscalesize: " << pixscalesize);
      if (pixscale) {
        for (uint32 i = 0; i < pixscalesize; i++)
          METLIBS_LOG_DEBUG("pixscale[" << i << "]=" << pixscale[i]);
      }
    }

    if (!(have_tiepoints && have_pixelscale)) {
      METLIBS_LOG_ERROR("neither GTIFF_TRANSMATRIX nor TIFFTAG_GEOTIEPOINTS+TIFFTAG_GEOPIXELSCALE in '" << infile << "'");
      return -1;
    }

    x_scale = pixscale[0];
    y_scale = -pixscale[1];
    x_0 = tiepoints[3];
    y_0 = tiepoints[4];
    METLIBS_LOG_DEBUG(LOGVAL(x_scale) << LOGVAL(y_scale) << LOGVAL(x_0) << LOGVAL(y_0));
  } // !have_transmatrix

  /* Coordinate Transformation Codes */
  short linearUnitsCode;
  if (!GTIFKeyGet(gtifin.get(), ProjLinearUnitsGeoKey, &linearUnitsCode, 0, 1)) {
    linearUnitsCode = 32767;
  }
  double unit_scale_factor = 1;

  unsigned short modeltype;
  if (!GTIFKeyGet(gtifin.get(), GTModelTypeGeoKey, &modeltype, 0, 1)) {
    METLIBS_LOG_ERROR("getting GTModelType from file");
    return -1;
  }

  if (modeltype == 32767) {
    // User defined WKT, use gdalsrsrinfo to convert to proj4
    int cit_size;
    int cit_length = GTIFKeyInfo(gtifin.get(), PCSCitationGeoKey, &cit_size, NULL);
    if (cit_length <= 0) {
      METLIBS_LOG_ERROR("Missing PCSCitationGeoKey");
      return -1;
    }
    std::unique_ptr<char[]> citation(new char[cit_length]);
    GTIFKeyGet(gtifin.get(), PCSCitationGeoKey, citation.get(), 0, cit_length);
    std::string PCSCitation(citation.get());

    miutil::replace(PCSCitation, "ESRI PE String = ", "");
    ginfo.projection.setFromWKT(PCSCitation);

  } else if (modeltype == ModelTypeGeographic) {
    /* Geographic latitude-longitude System */
    /* Assume mercartor for now */

    double GeogSemiMajorAxis = 0, GeogSemiMinorAxis = 0, GeogInvFlattening = 0;
    if (!GTIFKeyGet(gtifin.get(), GeogSemiMajorAxisGeoKey, &GeogSemiMajorAxis, 0, 1)) {
      METLIBS_LOG_INFO("No GeogSemiMajorAxisGeoKey in geotiff, set to 6.37814e+06");
      GeogSemiMajorAxis = 6.37814e6;
    }
    unsigned short GeogAngularUnits, GeogEllipsoid, GeographicType;
    if (!GTIFKeyGet(gtifin.get(), GeogAngularUnitsGeoKey, &GeogAngularUnits, 0, 1)) {
      GeogAngularUnits = Angular_Degree;
    }
    if (!GTIFKeyGet(gtifin.get(), GeogEllipsoidGeoKey, &GeogEllipsoid, 0, 1)) {
      GeogEllipsoid = 32767;
    }
    if (!GTIFKeyGet(gtifin.get(), GeogSemiMinorAxisGeoKey, &GeogSemiMinorAxis, 0, 1)) {
      GeogSemiMinorAxis = 6356752.314;
    }
    if (!GTIFKeyGet(gtifin.get(), GeogInvFlatteningGeoKey, &GeogInvFlattening, 0, 1)) {
      GeogInvFlattening = 298.257;
    }
    if (!GTIFKeyGet(gtifin.get(), GeographicTypeGeoKey, &GeographicType, 0, 1)) {
      GeographicType = 32767; // Default value
    }

    int cit_size;
    int cit_length = GTIFKeyInfo(gtifin.get(), GeogCitationGeoKey, &cit_size, NULL);
    std::string GTCitation;
    if (cit_length > 0) {
      std::unique_ptr<char[]> citation(new char[cit_length]);
      GTIFKeyGet(gtifin.get(), GeogCitationGeoKey, citation.get(), 0, cit_length);
      GTCitation = std::string(citation.get());
    }

    ginfo.projection = Projection("+proj=lonlat +ellps=WGS84 +towgs84=0,0,0 +no_defs");
    switch (GeogAngularUnits) { // see http://geotiff.maptools.org/spec/geotiff6.html#6.3.1.4
    case Angular_Degree:
      unit_scale_factor = 1;
      break;
    case Angular_Radian:
      unit_scale_factor = 180 / M_PI;
      break;
    case Angular_Arc_Minute:
      unit_scale_factor = 1 / 60;
      break;
    case Angular_Arc_Second:
      unit_scale_factor = 1 / 3600;
      break;
    case Angular_Gon:
      unit_scale_factor = 0.9;
      break;
    default:
      METLIBS_LOG_WARN("GeogAngularUnits = " << GeogAngularUnits << " are not supported");
    }

    if (METLIBS_LOG_DEBUG_ENABLED()) {
      METLIBS_LOG_DEBUG("GTIFKeyGet: GTModelTypeGeoKey = " << modeltype);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeographicType = " << GeographicType);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeogCitation = " << GTCitation);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeogAngularUnits = " << GeogAngularUnits);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeogSemiMajorAxis = " << GeogSemiMajorAxis);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeogSemiMinorAxis = " << GeogSemiMinorAxis);
      METLIBS_LOG_DEBUG("GTIFKeyGet: GeogInvFlattening = " << GeogInvFlattening);
    }

  } else if (modeltype == ModelTypeProjected) {
    unsigned short ProjectedCSType = 0;
    if (!GTIFKeyGet(gtifin.get(), ProjectedCSTypeGeoKey, &ProjectedCSType, 0, 1)) {
      METLIBS_LOG_ERROR("geotiff key ProjectedCSTypeGeoKey could not be read");
      return -1;
    }
    std::ostringstream proj4;
    if (ProjectedCSType >= 1024 && ProjectedCSType <= 32766) {
      // EPSG code; note: this range is for geotiff 1.1, in geotiff 1.0 the range was 20000 to 32766
      proj4 << "+init=epsg:" << ProjectedCSType;
    } else /*if (ProjectedCSType == 32767)*/ {
      unsigned short ProjCoordTrans = 0;
      if (!GTIFKeyGet(gtifin.get(), ProjCoordTransGeoKey, &ProjCoordTrans, 0, 1)) {
        METLIBS_LOG_ERROR("geotiff key ProjectionGeoKey could not be read");
        return -1;
      }
      if (ProjCoordTrans == CT_PolarStereographic) {
        // see http://geotiff.maptools.org/proj_list/polar_stereographic.html
        double ProjNatOriginLat, ProjScaleAtNatOrigin = 1, ProjStraightVertPoleLong;
        GTIFKeyGet(gtifin.get(), ProjNatOriginLatGeoKey, &ProjNatOriginLat, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjScaleAtNatOriginGeoKey, &ProjScaleAtNatOrigin, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjStraightVertPoleLongGeoKey, &ProjStraightVertPoleLong, 0, 1);
        // clang-format off
        proj4 << "+proj=stere"
              << " +lat_ts=" << ProjNatOriginLat
              << " +lat_0=" << (ProjNatOriginLat < 0 ? "-" : "") << "90"
              << " +lon_0=" << ProjStraightVertPoleLong
              << " +ellps=WGS84"
              << " +units=m"
              << " +k_0=" << ProjScaleAtNatOrigin;
        // clang-format on
      } else if (ProjCoordTrans == CT_Mercator) {
        // see http://geotiff.maptools.org/proj_list/mercator_1sp.html
        double ProjNatOriginLong, ProjNatOriginLat, ProjScaleAtNatOrigin;
        GTIFKeyGet(gtifin.get(), ProjNatOriginLongGeoKey, &ProjNatOriginLong, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjNatOriginLatGeoKey, &ProjNatOriginLat, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjScaleAtNatOriginGeoKey, &ProjScaleAtNatOrigin, 0, 1);
        // clang-format off
        proj4 << "+proj=merc"
              << " +lon_0=" << ProjNatOriginLong
              << " +lat_0=" << ProjNatOriginLat
              << " +k_0=" << ProjScaleAtNatOrigin;
        // clang-format on
      } else if (ProjCoordTrans == CT_Equirectangular) {
        // see http://geotiff.maptools.org/proj_list/equirectangular.html
        double ProjCenterLong, ProjCenterLat, ProjStdParallel1;
        GTIFKeyGet(gtifin.get(), ProjCenterLongGeoKey, &ProjCenterLong, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjCenterLatGeoKey, &ProjCenterLat, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjStdParallel1GeoKey, &ProjStdParallel1, 0, 1);
        // clang-format off
        proj4 << "+proj=eqc"
              << " +lat_ts=" << ProjCenterLat
              << " +lon_0=" << ProjCenterLong;
        // clang-format on
      } else if (ProjCoordTrans == CT_LambertConfConic_2SP) {
        // see http://geotiff.maptools.org/proj_list/lambert_conic_conformal_2sp.html
        double ProjStdParallel1 = 63, ProjStdParallel2 = 63, ProjFalseOriginLat = 63, ProjFalseOriginLong = 15;
        GTIFKeyGet(gtifin.get(), ProjStdParallel1GeoKey, &ProjStdParallel1, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjStdParallel2GeoKey, &ProjStdParallel2, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjFalseOriginLatGeoKey, &ProjFalseOriginLat, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjFalseOriginLongGeoKey, &ProjFalseOriginLong, 0, 1);
        // clang-format off
        proj4 << "+proj=lcc"
              << " +lat_1=" << ProjStdParallel1     // latitude of first standard parallel
              << " +lat_2=" << ProjStdParallel2     // latitude of second standard parallel
              << " +lat_0=" << ProjFalseOriginLat   // latitude of false origin
              << " +lon_0=" << ProjFalseOriginLong; // longitude of false origin
        // clang-format on
      } else if (ProjCoordTrans == CT_HotineObliqueMercatorAzimuthCenter || ProjCoordTrans == CT_ObliqueMercator_Hotine) {
        // see http://geotiff.maptools.org/proj_list/oblique_mercator.html
        // and http://geotiff.maptools.org/proj_list/hotine_oblique_mercator.html
        double ProjCenterLat = 63, ProjCenterLon = 15, ProjScaleAtCenter = 1;
        GTIFKeyGet(gtifin.get(), ProjCenterLatGeoKey, &ProjCenterLat, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjCenterLongGeoKey, &ProjCenterLon, 0, 1);
        GTIFKeyGet(gtifin.get(), ProjScaleAtCenterGeoKey, &ProjScaleAtCenter, 0, 1);
        double ProjAzimuthAngle = 63, ProjRectifiedGridAngle = 0;
        const bool have_alpha = (GTIFKeyGet(gtifin.get(), ProjAzimuthAngleGeoKey, &ProjAzimuthAngle, 0, 1) > 0);
        const bool have_gamma = (GTIFKeyGet(gtifin.get(), ProjRectifiedGridAngleGeoKey, &ProjRectifiedGridAngle, 0, 1) > 0);

        double GeogSemiMajorAxis = 6370997, GeogSemiMinorAxis = GeogSemiMajorAxis, GeogInvFlattening = 298;
        bool have_semiminor_axis = false;
        if (GTIFKeyGet(gtifin.get(), GeogSemiMajorAxisGeoKey, &GeogSemiMajorAxis, 0, 1)) {
          METLIBS_LOG_DEBUG(LOGVAL(GeogSemiMajorAxis));
        }
        if (GTIFKeyGet(gtifin.get(), GeogSemiMinorAxisGeoKey, &GeogSemiMinorAxis, 0, 1)) {
          METLIBS_LOG_DEBUG(LOGVAL(GeogSemiMinorAxis));
          have_semiminor_axis = true;
        } else if (GTIFKeyGet(gtifin.get(), GeogInvFlatteningGeoKey, &GeogInvFlattening, 0, 1)) {
          METLIBS_LOG_DEBUG(LOGVAL(GeogInvFlattening));
        }

        // clang-format off
        proj4 << "+proj=omerc"
              << " +lat_0=" << ProjCenterLat
              << " +lonc=" << ProjCenterLon;
        // clang-format on
        if (have_alpha)
          proj4 << " +alpha=" << ProjAzimuthAngle;
        if (have_gamma)
          proj4 << " +gamma=" << ProjRectifiedGridAngle;
        // clang-format off
        proj4 << " +k_0=" << ProjScaleAtCenter
              << " +a=" << GeogSemiMajorAxis
              << " +units=m +no_defs";
        // clang-format on
        if (have_semiminor_axis)
          proj4 << " +b=" << GeogSemiMinorAxis;
        else
          proj4 << " +rf=" << GeogInvFlattening;
      } else {
        METLIBS_LOG_ERROR("Projection CT " << ProjCoordTrans << " not yet supported");
        return -1;
      }

      double ProjFalseEasting = 0, ProjFalseNorthing = 0;
      GTIFKeyGet(gtifin.get(), ProjFalseEastingGeoKey, &ProjFalseEasting, 0, 1);
      GTIFKeyGet(gtifin.get(), ProjFalseNorthingGeoKey, &ProjFalseNorthing, 0, 1);
      if (ProjFalseEasting != 0)
        proj4 << " +x_0=" << ProjFalseEasting;
      if (ProjFalseNorthing != 0)
        proj4 << " +y_0=" << ProjFalseNorthing;
    }
    ginfo.projection = Projection(proj4.str());
  } else {
    METLIBS_LOG_ERROR("Grid type not supported, GTModelType = " << modeltype);
    return -1;
  }

  ginfo.Bx = x_0 * unit_scale_factor;
  ginfo.By = y_0 * unit_scale_factor;
  ginfo.Ax = x_scale * unit_scale_factor;
  ginfo.Ay = y_scale * unit_scale_factor;

  if (!TIFFGetField(in.get(), TIFFTAG_IMAGEWIDTH, &ginfo.xsize)) {
    METLIBS_LOG_DEBUG("No TIFFTAG_IMAGEWIDTH");
  }
  if (!TIFFGetField(in.get(), TIFFTAG_IMAGELENGTH, &ginfo.ysize)) {
    METLIBS_LOG_DEBUG("No TIFFTAG_IMAGELENGTH");
  }

  int tilesAcross = 1, tilesDown = 1;

  unsigned int tileWidth;
  if (!TIFFGetField(in.get(), TIFFTAG_TILEWIDTH, &tileWidth)) {
    METLIBS_LOG_DEBUG("No TIFFTAG_TILEWIDTH");
    tileWidth = 0;
  }
  unsigned int tileLength;
  if (!TIFFGetField(in.get(), TIFFTAG_TILELENGTH, &tileLength)) {
    METLIBS_LOG_DEBUG("No TIFFTAG_TILELENGTH");
    tileLength = 0;
  }

  if (tileWidth != 0 && tileLength != 0) {
    tilesAcross = (ginfo.xsize + (tileWidth - 1)) / tileWidth;
    tilesDown = (ginfo.ysize + (tileLength - 1)) / tileLength;
    if (tilesAcross * tileWidth > ginfo.xsize)
      ginfo.xsize = tilesAcross * tileWidth;
    if (tilesDown * tileLength > ginfo.ysize)
      ginfo.ysize = tilesDown * tileLength;
  }

  METLIBS_LOG_DEBUG(LOGVAL(ginfo.Ax) << LOGVAL(ginfo.Ay) << LOGVAL(ginfo.Bx) << LOGVAL(ginfo.By) << LOGVAL(ginfo.projection.getProj4Definition())
                                     << LOGVAL(tileWidth) << LOGVAL(tileLength) << LOGVAL(tilesAcross) << LOGVAL(tilesDown));

  if (pmi == PHOTOMETRIC_PALETTE)
    return 2;
  else
    return 0;
}

int satimg::MITIFF_read_diana(const std::string& infile, unsigned char* image[], int nchan, const int chan[], dihead& ginfo)
{
  ginfo.noofcl = 0;

  const int pal = MITIFF_head_diana(infile, ginfo);
  if (pal == -1)
    return -1;

  if (nchan == 0) {
    return 1;
  }

  std::unique_ptr<TIFF, CloseTIFF> in(TIFFOpen(infile.c_str(), "rc"));
  if (!in) {
    METLIBS_LOG_ERROR("TIFFOpen failed, probably not a TIFF file: '" << infile << "'");
    return -1;
  }

  // Read image data into matrix.
  TIFFGetField(in.get(), TIFFTAG_IMAGEWIDTH, &ginfo.xsize);
  TIFFGetField(in.get(), TIFFTAG_IMAGELENGTH, &ginfo.ysize);
  const int size = ginfo.xsize * ginfo.ysize;

  /*
   * Memory allocated for image data in this function (*image) is freed
   * in function main process.
   */
  if (ginfo.zsize > MAXCHANNELS) {
    METLIBS_LOG_ERROR("NOT ENOUGH POINTERS AVAILABLE TO HOLD DATA!");
    return -1;
  }

  for (int i = 0; i < nchan; i++) {
    if (i != 0 || chan[0] != 0) { /*TIFFsetDirectory chrashes if chan[0]=0,why??*/
      if (!TIFFSetDirectory(in.get(), chan[i]))
        return -1;
    }
    image[i] = new unsigned char[size + 1];
    if (!image[i])
      return -1;

    int compression = COMPRESSION_NONE;
    if (!TIFFGetField(in.get(), TIFFTAG_COMPRESSION, &compression))
      compression = COMPRESSION_NONE;

    int status;
    if (compression == COMPRESSION_NONE)
      status = TIFFReadRawStrip(in.get(), 0, image[i], size);
    else
      status = TIFFReadEncodedStrip(in.get(), 0, image[i], size);
    if (status == -1)
      return -1;
  }

  return pal;
}

int satimg::MITIFF_head_diana(const std::string& infile, dihead& ginfo)
{
  METLIBS_LOG_SCOPE();
  std::unique_ptr<TIFF, CloseTIFF> in(TIFFOpen(infile.c_str(), "rc"));
  if (!in) {
    METLIBS_LOG_ERROR("TIFFOpen failed, probably not a TIFF file: '" << infile << "'");
    return -1;
  }

  const std::string fieldname[] = {"Satellite:",
                                   "Date and Time:",
                                   "SatDir:",
                                   "Channels:",
                                   "In this file:",
                                   "Xsize:",
                                   "Ysize:",
                                   "Map projection:",
                                   "Proj string:",
                                   "TrueLat:",
                                   "GridRot:",
                                   "Xunit:",
                                   "Yunit:",
                                   "NPX:",
                                   "NPY:",
                                   "Ax:",
                                   "Ay:",
                                   "Bx:",
                                   "By:",
                                   "Calibration VIS:",
                                   "Calibration IR:",
                                   "Table_calibration:",
                                   "COLOR INFO:",
                                   "NWP INFO:"};
  const size_t FIELDS = sizeof(fieldname) / sizeof(fieldname[0]);

  float gridRot = 60, trueLat = 0;
  ginfo.noofcl = 0;

  // Test whether this is a color palette image or not. pmi==3 => color palette
  short pmi;
  if (TIFFGetField(in.get(), TIFFTAG_PHOTOMETRIC, &pmi) && pmi == PHOTOMETRIC_PALETTE) {
    unsigned short int *red, *green, *blue;
    if (!TIFFGetField(in.get(), TIFFTAG_COLORMAP, &red, &green, &blue)) {
      return 2;
    }
    for (int i = 0; i < 256; i++) {
      ginfo.cmap[0][i] = red[i];
      ginfo.cmap[1][i] = green[i];
      ginfo.cmap[2][i] = blue[i];
    }
  }

  char* description = 0;
  if (!TIFFGetField(in.get(), TIFFTAG_IMAGEDESCRIPTION, &description) || !description)
    return -1;
  std::string desc_str(description);

  // read all common fields
  while (true) {
    // Find key word
    size_t i = 0;
    size_t nn = desc_str.npos;
    while (nn == desc_str.npos && i < FIELDS) {
      nn = desc_str.find(fieldname[i]);
      i++;
    }
    if (i == FIELDS)
      break; // no key word found
    i--;
    nn += fieldname[i].size();
    desc_str = desc_str.substr(nn, desc_str.size() - nn + 1);
    // find next key word
    size_t j = 0;
    size_t mm = std::string::npos;
    while (mm == std::string::npos && j < FIELDS) {
      mm = desc_str.find(fieldname[j]);
      j++;
    }
    std::string value;
    if (j < FIELDS + 1)
      value = desc_str.substr(0, mm);
    else
      value = desc_str;

    miutil::trim(value);
    if (fillhead_diana(value, fieldname[i], ginfo, gridRot, trueLat) != 0)
      return -1;
  }

  // If the mitiff image contains no proj string, it is probably transformed to +R=6371000
  // and adjusted to fit nwp-data and maps.
  // These adjustments require no conversion between +R=6371000 and ellps=WGS84,
  // and therefore no +datum or +towgs84 are given.
  if (!ginfo.projection.isDefined()) {
    std::ostringstream proj4;
    proj4 << "+proj=stere";
    proj4 << " +lon_0=" << gridRot;
    proj4 << " +lat_ts=" << trueLat;
    proj4 << " +lat_0=90";
    proj4 << " +R=6371000";
    proj4 << " +units=km";
    ginfo.projection.setProj4Definition(proj4.str());
    ginfo.By -= ginfo.Ay * ginfo.ysize;
  } else {
    double x_0, y_0;
    std::string proj4 = ginfo.projection.getProj4Definition();
    if (proj4_value(proj4, "+x_0=", x_0, true))
      ginfo.Bx = x_0 / -1000;
    if (proj4_value(proj4, "+y_0=", y_0, true))
      ginfo.By = y_0 / -1000;
    ginfo.projection.setProj4Definition(proj4);
  }

  return (pmi == PHOTOMETRIC_PALETTE) ? 2 : 0;
}

uint8 ReadGeotiff_bits(char *filename)
{
    TIFF *tif;
    uint8 bits;

    tif = XTIFFOpen(filename, "r");
    if (tif)
    {
        size_t value = 0;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &value);

        if(value == 16)
            bits = 12;
        else
            bits = value;

        XTIFFClose(tif);
    }
    return bits;
}

CSize ReadGeotiff_info(const char *filename, double *minX, double *maxY, double *grid_size)
{
    TIFF *tif;
    CSize image_size;

    tif = XTIFFOpen(filename, "r");
    if (tif)
    {
        uint16 count = 0;
        double *data = 0;

        TIFFGetField(tif, TIFFTAG_GEOTIEPOINTS, &count, &data);
        if (minX != NULL) *minX = data[3];
        if (maxY != NULL) *maxY = data[4];

        TIFFGetField(tif, TIFFTAG_GEOPIXELSCALE, &count, &data);
        if (grid_size != NULL) *grid_size = data[0];

        size_t value = 0;
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &value);
        image_size.width = value;
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &value);
        image_size.height = value;

        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &value);

        if(value == 16)
            value = 12;
       XTIFFClose(tif);
    }
    return image_size;
}

CSize ReadGeotiff_info_dxy(char *filename, double *minX, double *maxY, double *grid_size_dx, double *grid_size_dy)
{
    TIFF *tif;
    CSize image_size;

    tif = XTIFFOpen(filename, "r");
    if (tif)
    {
        uint16 count = 0;
        double *data = 0;

        TIFFGetField(tif, TIFFTAG_GEOTIEPOINTS, &count, &data);
        if (minX != NULL) *minX = data[3];
        if (maxY != NULL) *maxY = data[4];

        TIFFGetField(tif, TIFFTAG_GEOPIXELSCALE, &count, &data);
        if (grid_size_dx != NULL) *grid_size_dx = data[0];
        if (grid_size_dy != NULL) *grid_size_dy = data[1];

        size_t value = 0;
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &value);
        image_size.width = value;
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &value);
        image_size.height = value;

        XTIFFClose(tif);
    }
    return image_size;
}

bool Image::initWithTiffData(const unsigned char * data, ssize_t dataLen)
{
#if CC_USE_TIFF
    bool ret = false;
    do 
    {
        // set the read call back function
        tImageSource imageSource;
        imageSource.data    = data;
        imageSource.size    = dataLen;
        imageSource.offset  = 0;

        TIFF* tif = TIFFClientOpen("file.tif", "r", (thandle_t)&imageSource, 
            tiffReadProc, tiffWriteProc,
            tiffSeekProc, tiffCloseProc, tiffSizeProc,
            tiffMapProc,
            tiffUnmapProc);

        CC_BREAK_IF(nullptr == tif);

        uint32 w = 0, h = 0;
        uint16 bitsPerSample = 0, samplePerPixel = 0, planarConfig = 0;
        size_t npixels = 0;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplePerPixel);
        TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarConfig);

        npixels = w * h;

        _renderFormat = Texture2D::PixelFormat::RGBA8888;
        _width = w;
        _height = h;

        _dataLen = npixels * sizeof (uint32);
        _data = static_cast<unsigned char*>(malloc(_dataLen * sizeof(unsigned char)));

        uint32* raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
        if (raster != nullptr) 
        {
           if (TIFFReadRGBAImageOriented(tif, w, h, raster, ORIENTATION_TOPLEFT, 0))
           {
                /* the raster data is pre-multiplied by the alpha component 
                   after invoking TIFFReadRGBAImageOriented*/
                _hasPremultipliedAlpha = true;

               memcpy(_data, raster, npixels*sizeof (uint32));
           }

          _TIFFfree(raster);
        }


        TIFFClose(tif);

        ret = true;
    } while (0);
    return ret;
#else
    CCLOG("tiff is not enabled, please enalbe it in ccConfig.h");
    return false;
#endif
}

bool TIFFTReadUInt16(TIFF* tif, uint16_t** image, uint16_t* width, uint16_t* height, char* errormessage, unsigned int /*sample*/) {
    uint16_t samplesperpixel=1;
    uint16_t bitspersample=16;
    uint16_t sampleformat = SAMPLEFORMAT_UINT;
    uint32_t nx=0;
    uint32_t ny=0;
    uint16_t* tiffbuf = NULL;
    uint16_t* imagebuf=NULL;
    uint32_t row = 0;
    uint32_t rowsperstrip = 0;
    unsigned int rr=0;
    unsigned int cc=0;
    bool ok=true;

    TIFFGetField(tif,TIFFTAG_IMAGEWIDTH,&nx);
    TIFFGetField(tif,TIFFTAG_IMAGELENGTH,&ny);
    TIFFGetField(tif,TIFFTAG_SAMPLESPERPIXEL,&samplesperpixel);
    TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleformat);
    TIFFGetFieldDefaulted(tif,TIFFTAG_BITSPERSAMPLE,&bitspersample);


    if (sampleformat!=SAMPLEFORMAT_UINT) {
        if (errormessage) sprintf(errormessage, "can not read sample format other than uint");
        return false;
    }

    if ((bitspersample!=16)) {
        if (errormessage) sprintf(errormessage, "can not read sample with %d bits depth", bitspersample);
        return false;
    }

    if ((*image==NULL)||(nx!=*width)||(ny!=*height)) {
        *image=(uint16_t*)realloc(*image, nx*ny*sizeof(uint16_t));
        *width=nx;
        *height=ny;
    }

    imagebuf=*image;
    if (imagebuf==NULL) {
        if (errormessage) sprintf(errormessage, "error allocating memory (%.1f kBytes)", (float)(nx*ny*sizeof(uint16_t))/1024.0);
        return false;
    }

    tiffbuf = (uint16_t*)_TIFFmalloc(TIFFStripSize(tif));
    if (tiffbuf) {
        row = 0;
        rowsperstrip = 0;
        TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);
        for (row = 0; ok&&(row<ny); row+= rowsperstrip) {
            uint32_t nrow =  ( (row+rowsperstrip>ny)?(ny-row):rowsperstrip);
            tstrip_t strip = TIFFComputeStrip(tif, row, 0);
            if ((TIFFReadEncodedStrip(tif,strip,tiffbuf,-1))<0) {
                ok=false;
                if (errormessage) sprintf(errormessage, "invalid strip");
                break;
            }
            uint16_t* ptr = tiffbuf;
            int iii=0;
            for (rr = 0;rr<nrow; ++rr) {
                for (cc = 0; cc<nx; ++cc) {
                    uint16_t t=(uint16_t)*(ptr++);
                    iii++;
                    imagebuf[cc+(row+rr)*nx]=t;
                }
            }
        }
    } else {
        if (errormessage) sprintf(errormessage, "error allocating memory buffer for TIFF strip");
        return false;
    }
    if (tiffbuf) _TIFFfree(tiffbuf);

    return ok;
}

void TEST_AGAINST_LIBTIFF(const std::string& filename, std::vector<TestResult>& test_results) {
#ifdef TINYTIFF_TEST_LIBTIFF
    HighResTimer timer, timer1;
    bool ok=false;
    std::cout<<"\n\nreading '"<<std::string(filename)<<"' with tinytiff and libtiff and checking read contents ... filesize = "<<bytestostr(get_filesize(filename.c_str()))<<"\n";
    test_results.emplace_back();
    test_results.back().name=std::string("TEST_AGAINST_LIBTIFF(")+std::string(filename)+std::string(")");
    test_results.back().success=ok=false;
    test_results.back().numImages=0;
    try {
        timer.start();
        std::cout<<"    libTIFF: opening file with     [duration: "<<floattounitstr(double(timer.get_time())/1.0e6, "s")<<" ]\n";
        TIFF* ltiff=TIFFOpen(filename.c_str(), "r");
        if (!ltiff) {
            TESTFAIL("libTIFF ERROR: reading (not existent, not accessible or no TIFF file)", test_results.back())
        } else {
            timer.start();
            uint32_t libTIFF_frames=TIFFCountDirectories(ltiff);
            double duration=timer.get_time();
            std::cout<<"    libTIFF: frames: "<<libTIFF_frames<<"     [duration: "<<duration<<" us  =  "<<floattounitstr(duration/1.0e6, "s")<<" ]\n";
            std::cout<<"    TinyTIFF: opening file with     [duration: "<<floattounitstr(double(timer.get_time())/1.0e6, "s")<<" ]\n";
            TinyTIFFReaderFile* tiffr=TinyTIFFReader_open(filename.c_str());
            if (!tiffr) {
                TESTFAIL("TinyTIFF ERROR: reading (not existent, not accessible or no TIFF file)", test_results.back())
                ok=false;
            } else if (TinyTIFFReader_wasError(tiffr)) {
                TESTFAIL("TinyTIFF ERROR: "<<TinyTIFFReader_getLastError(tiffr)<<"", test_results.back())
                ok=false;
            } else {
                timer.start();
                uint32_t tinyTIFF_frames=TinyTIFFReader_countFrames(tiffr);
                duration=timer.get_time();
                std::cout<<"    TinyTIFF: frames: "<<tinyTIFF_frames<<"     [duration: "<<duration<<" us  =  "<<floattounitstr(duration/1.0e6, "s")<<" ]\n";
                if (TinyTIFFReader_wasError(tiffr)) {
                    TESTFAIL("TinyTIFF ERROR: "<<TinyTIFFReader_getLastError(tiffr)<<"", test_results.back())
                    ok=false;
                } else {
                    if (libTIFF_frames!=tinyTIFF_frames) {
                        TESTFAIL("number of frames, read with TinyTIFF ("<<tinyTIFF_frames<<") and libTIFF ("<<libTIFF_frames<<") are not equal!", test_results.back())
                        ok=false;
                    } else {
                        timer.start();
                        size_t frame=0;
                        ok=true;
                        do {
                            std::cout<<"   frame: "<<frame<<std::endl;
                            uint32_t tinyTIFF_width=TinyTIFFReader_getWidth(tiffr);
                            uint32_t tinyTIFF_height=TinyTIFFReader_getHeight(tiffr);
                            uint16_t tinyTIFF_bitspersample=TinyTIFFReader_getBitsPerSample(tiffr, 0);
                            uint16_t tinyTIFF_samplesperpixel=TinyTIFFReader_getSamplesPerPixel(tiffr);
                            const char* tinyTIFF_imagedesc=TinyTIFFReader_getImageDescription(tiffr);
                            std::cout<<"     tinyTIFF: width="<<tinyTIFF_width<<", height="<<tinyTIFF_height<<", bitspersample="<<tinyTIFF_bitspersample<<", samplesperpixel="<<tinyTIFF_samplesperpixel<<"\n";
                            if (frame==0) {
                                const std::string desc=std::to_string(tinyTIFF_width)+"x"+std::to_string(tinyTIFF_height)+"pix/"+std::to_string(sizeof(TIMAGESAMPLETYPE)*8)+"bit/"+std::to_string(tinyTIFF_samplesperpixel)+"ch/"+std::to_string(tinyTIFF_frames)+"frames";
                                test_results.back().name=std::string("TEST_AGAINST_LIBTIFF(")+desc+", "+std::string(filename)+std::string(")");
                            }

                            uint32_t libTIFF_width=0;
                            uint32_t libTIFF_height=0;
                            uint32_t libTIFF_bitspersample=0;
                            uint32_t libTIFF_samplesperpixel=0;
                            char*libtiff_imagedesc;
                            TIFFGetField(ltiff,TIFFTAG_SAMPLESPERPIXEL,&libTIFF_samplesperpixel);
                            TIFFGetField(ltiff,TIFFTAG_BITSPERSAMPLE,&libTIFF_bitspersample);
                            TIFFGetField(ltiff,TIFFTAG_IMAGEWIDTH,&libTIFF_width);
                            TIFFGetField(ltiff,TIFFTAG_IMAGELENGTH,&libTIFF_height);
                            TIFFGetField(ltiff,TIFFTAG_IMAGEDESCRIPTION,&libtiff_imagedesc);
                            TIFFPrintDirectory(ltiff, stdout,TIFFPRINT_STRIPS|TIFFPRINT_COLORMAP);
                            std::cout<<"     libTIFF:  width="<<libTIFF_width<<", height="<<libTIFF_height<<", bitspersample="<<libTIFF_bitspersample<<", samplesperpixel="<<libTIFF_samplesperpixel<<"\n";

                            if (tinyTIFF_width!=libTIFF_width) {
