                                ok=false;
                                break;
                            }
                            if (tinyTIFF_samplesperpixel!=libTIFF_samplesperpixel) {
                                TESTFAIL("in frame "<<frame<<": TinyTIFF and libTIFF read different samplesperpixels (TinyTIFF: "<<tinyTIFF_samplesperpixel<<" != libTIFF: "<<libTIFF_samplesperpixel<<")", test_results.back())
                                ok=false;
                                break;
                            }
                            if (tinyTIFF_imagedesc==nullptr || libtiff_imagedesc==nullptr || strcmp(tinyTIFF_imagedesc, libtiff_imagedesc)!=0) {
                                std::cout<<"       WARNING in frame "<<frame<<": TinyTIFF and libTIFF read different descriptions (TinyTIFF: '"<<tinyTIFF_imagedesc<<"' != libTIFF: '"<<libtiff_imagedesc<<"')\n";
                                //ok=false;
                                //break;
                            }
                            if (ok) {
                                for (size_t sample=0; sample<libTIFF_samplesperpixel; sample++) {
                                    std::vector<TIMAGESAMPLETYPE> tinyTIFF_data(tinyTIFF_width*tinyTIFF_height*sizeof(TIMAGESAMPLETYPE)*2, 0);
                                    TinyTIFFReader_getSampleData(tiffr,tinyTIFF_data.data(), sample);
                                    if (TinyTIFFReader_wasError(tiffr)) {
                                        TESTFAIL("TinyTIFF ERROR: reading frame "<<frame<<", sample "<<sample<<":"<<TinyTIFFReader_getLastError(tiffr)<<"", test_results.back())
                                        ok=false;
                                        break;
                                    }
                                    std::vector<TIMAGESAMPLETYPE> libTIFF_data(libTIFF_width*libTIFF_height*sizeof(TIMAGESAMPLETYPE)*2, 0);
                                    if (!TIFFReadFrame(ltiff, libTIFF_data.data(), sample)) {
                                        TESTFAIL("libTIFF ERROR: reading frame "<<frame<<", sample "<<sample<<"", test_results.back())
                                        ok=false;
                                        break;
                                    }
                                    for (size_t i=0; i<libTIFF_data.size(); i++) {
                                        if (i<=8) std::cout<<"          f"<<frame<<"s"<<sample<<"i"<<i<<": tiny="<<std::dec<<static_cast<typename atleast_int<TIMAGESAMPLETYPE>::TPrint>(tinyTIFF_data[i])<<" lib="<<static_cast<typename atleast_int<TIMAGESAMPLETYPE>::TPrint>(libTIFF_data[i])<<"\n";
                                        if (libTIFF_data[i]!=tinyTIFF_data[i]) {
                                            TESTFAIL("in frame "<<frame<<", sample "<<sample<<": TinyTIFF and libTIFF read different sample values (I="<<i<<": TinyTIFF: "<<std::dec<<static_cast<typename atleast_int<TIMAGESAMPLETYPE>::TPrint>(tinyTIFF_data[i])<<" != libTIFF: "<<std::dec<<static_cast<typename atleast_int<TIMAGESAMPLETYPE>::TPrint>(libTIFF_data[i])<<")", test_results.back())
                                            ok=false;
                                            break;
                                        }
                                    }
                                    if (!ok) {
                                        SAVE_TIFF(std::string(std::string(filename)+".tinytiff.error_sample"+std::to_string(sample)+".tif").c_str(), tinyTIFF_data.data(), tinyTIFF_width, tinyTIFF_height);
                                        SAVE_TIFF(std::string(std::string(filename)+".libtiff.error_sample"+std::to_string(sample)+".tif").c_str(), libTIFF_data.data(), tinyTIFF_width, tinyTIFF_height);
                                    }
                                }
                            }


                            frame++;
                        } while (ok && TIFFReadDirectory(ltiff) && TinyTIFFReader_readNext(tiffr));

                        test_results.back().success=ok;
                        test_results.back().numImages=frame;
                        duration=timer.get_time();
                        test_results.back().duration_ms=duration/1.0e3;
                        std::cout<<"    read and checked all "<<frame<<" frames: "<<((ok)?std::string("SUCCESS"):std::string("ERROR"))<<"     [duration: "<<duration<<" us  =  "<<floattounitstr(duration/1.0e6, "s")<<" ]\n";
                    }
                }
            }
        }
    } catch(...) {
        ok=false;
        std::cout<<"       CRASH While reading file\n";
    }

    test_results.back().success=ok;
    if (ok) std::cout<<"  => SUCCESS\n";
    else std::cout<<"  => NOT CORRECTLY READ\n";
#endif
}

bool libtiffTestRead(const char* filename, const T* writteneven, const T* writtenodd, uint32_t width, uint32_t height, uint16_t samples=1, uint32_t frames_expected=0, TinyTIFFSampleLayout inputOrg=TinyTIFF_Chunky, std::string* description_out=nullptr)  {
    bool ok=true;
#ifdef TINYTIFF_TEST_LIBTIFF
    TIFF* tif = TIFFOpen(filename, "r");
    T* data=(T*)malloc(width*height*sizeof(T));
    if (tif) {
        uint32_t frame=0;
        do {
            uint32_t nx,ny;
            uint16_t ns,bs;
            TIFFGetField(tif,TIFFTAG_IMAGEWIDTH,&nx);
            TIFFGetField(tif,TIFFTAG_IMAGELENGTH,&ny);
            TIFFGetField(tif,TIFFTAG_SAMPLESPERPIXEL,&ns);
            TIFFGetField(tif,TIFFTAG_BITSPERSAMPLE,&bs);
            char* val=NULL;
            TIFFGetField(tif,TIFFTAG_IMAGEDESCRIPTION,&val);
            if (val) {
                std::cout<<"    ImageDescription("<<strlen(val)<<"):\n"<<val<<"\n";
                if (description_out) *description_out=val;
            } else {
                if (description_out) description_out->clear();
            }
            TIFFPrintDirectory(tif, stdout);
            if (nx==width && ny==height && ns==samples && bs==sizeof(T)*8) {
                size_t errcnt=0;
                size_t pixcnt=0;
                for (uint16_t samp=0; samp<samples; samp++) {
                    if (TIFFReadFrame(tif, data, samp)) {
                        ok=true;
                        const T* written=writteneven;
                        if (writtenodd && frame%2==1) written=writtenodd;
                        if (inputOrg==TinyTIFF_Chunky) {
                            for (uint32_t i=0; i<width*height; i++) {
                                if (data[i]!=written[i*samples+samp]) {
                                    ok=false;
                                    errcnt++;
                                    if (errcnt<50) std::cout<<" -- READ-ERROR: pixel-value differs in frame "<<frame<<" for pixel ("<<i%width<<","<<i/width<<"): file: "<<static_cast<typename atleast_int<T>::TPrint>(data[i])<<" expected: "<<static_cast<typename atleast_int<T>::TPrint>(written[i*samples+samp])<<"\n";
                                    if (errcnt==50) std::cout<<" -- READ-ERROR: ...\n";
                                } else {
                                    pixcnt++;
                                }
                            }
                        } else {
                            for (uint32_t i=0; i<width*height; i++) {
                                if (data[i]!=written[i+samp*width*height]) {
                                    ok=false;
                                    errcnt++;
                                    if (errcnt<50) std::cout<<" -- READ-ERROR: pixel-value differs in frame "<<frame<<" for pixel ("<<i%width<<","<<i/width<<"): file: "<<static_cast<typename atleast_int<T>::TPrint>(data[i])<<" expected: "<<static_cast<typename atleast_int<T>::TPrint>(written[i+samp*width*height])<<"\n";
                                    if (errcnt==50) std::cout<<" -- READ-ERROR: ...\n";
                                } else {
                                    pixcnt++;
                                }
                            }
                        }
                        if (!ok) {
                            std::cout<<" -- TEST READ WITH LIBTIFF: READ WRONG DATA for "<<errcnt<<" pixels in frame "<<frame<<"!!!\n";
                        }
                    } else {
                        std::cout<<" -- TEST READ WITH LIBTIFF: COULD NOT READ FRAME "<<frame<<"!\n";
                        ok=false;
                    }
                }
                if (ok) {
                    std::cout<<" -- TEST READ WITH LIBTIFF: SUCCESS FOR FRAME "<<frame<<"! All "<<pixcnt<<" pixels&samples as expected!\n";
                }
            } else {
                std::cout<<" -- TEST READ WITH LIBTIFF: FRAME SIZE OF FRAME "<<frame<<" DOES NOT MATCH (width: file:"<<nx<<"/expected:"<<width<<",   height: file:"<<ny<<"/expected:"<<height<<",   samples: file:"<<ns<<"/expected:"<<samples<<",   bitspersample: file:"<<bs<<"/expected:"<<(sizeof(T)*8)<<")!\n";
                ok=false;
            }
            frame++;
        } while (ok && TIFFReadDirectory(tif));
        if (frames_expected>0 && frames_expected!=frame) {
            std::cout<<" -- ERROR IN TEST READ WITH LIBTIFF: number of frames ("<<frame<<")does not match expected number of frames ("<<frames_expected<<")\n";
            ok=false;
        }
        TIFFClose(tif);
    } else {
        std::cout<<" -- TEST READ WITH LIBTIFF: COULD NOT OPEN FILE!\n";
        ok=false;
    }
    free(data);
#endif
    return ok;
}

__declspec(dllexport) void ReadEERCombinedFrame(const char* path, int firstFrameInclusive, int lastFrameExclusive, int eer_upsampling, float* h_result)
{
	if (eer_upsampling < 1 || eer_upsampling > 3)
		throw("EERRenderer::read: eer_upsampling must be 1, 2 or 3.");

	// First of all, check the file size
	FILE* fh = fopen(path, "r");
	if (fh == NULL)
		throw std::runtime_error("Failed to open file");

	fseek(fh, 0, SEEK_END);
	long long file_size = ftell(fh);
	fseek(fh, 0, SEEK_SET);
	fclose(fh);

	int nframes = 0;
	bool is_7bit = false;

	// Try reading as TIFF; this handle is kept open
	TIFF* ftiff = TIFFOpen(path, "r");

	if (ftiff == NULL)
	{
		throw std::runtime_error("Legacy mode not implemented");
	}
	else
	{
		// Check width & size
		int width, height;
		uint16_t compression = 0;
		TIFFGetField(ftiff, TIFFTAG_IMAGEWIDTH, &width);
		TIFFGetField(ftiff, TIFFTAG_IMAGELENGTH, &height);
		TIFFGetField(ftiff, TIFFTAG_COMPRESSION, &compression);

		// TIA can write an EER file whose first page is a sum and compressoin == 1.
		// This is not supported (yet). EPU never writes such movies.
		if (compression == TIFF_COMPRESSION_EER8bit)
			is_7bit = false;
		else if (compression == TIFF_COMPRESSION_EER7bit)
			is_7bit = true;
		else
			throw std::runtime_error("Unknown compression scheme for EER");

		if (width != EER_IMAGE_WIDTH || height != EER_IMAGE_HEIGHT)
			throw std::runtime_error("Currently we support only 4096x4096 pixel EER movies.");

		// Find the number of frames
		nframes = TIFFNumberOfDirectories(ftiff);
	}

	std::vector<long long> frame_starts, frame_sizes;
	unsigned char* buf;

	{
		frame_starts.resize(nframes, 0);
		frame_sizes.resize(nframes, 0);
		buf = (unsigned char*)malloc(file_size); // This is big enough
		if (buf == NULL)
			throw std::runtime_error("Failed to allocate buffer");
		long long pos = 0;

		// Read everything
		for (int frame = firstFrameInclusive; frame < lastFrameExclusive; frame++)
		{
			TIFFSetDirectory(ftiff, frame);
			const int nstrips = TIFFNumberOfStrips(ftiff);
			frame_starts[frame] = pos;

			for (int strip = 0; strip < nstrips; strip++)
			{
				const int strip_size = TIFFRawStripSize(ftiff, strip);
				if (pos + strip_size >= file_size)
					throw std::runtime_error("EER: buffer overflow when reading raw strips.");

				TIFFReadRawStrip(ftiff, strip, buf + pos, strip_size);
				pos += strip_size;
				frame_sizes[frame] += strip_size;
			}
		}

		TIFFClose(ftiff);
	}

	{
		long long total_n_electron = 0;

		long long supersize = 4096 << (eer_upsampling - 1);

		std::vector<unsigned int> positions;
		std::vector<unsigned char> symbols;
		memset(h_result, 0, supersize * supersize * sizeof(float));

		for (int iframe = firstFrameInclusive; iframe < lastFrameExclusive; iframe++)
		{
			long long pos = frame_starts[iframe];
			unsigned int n_pix = 0, n_electron = 0;
			const int max_electrons = frame_sizes[iframe] * 2; // at 4 bits per electron (very permissive bound!)
			if (positions.size() < max_electrons)
			{
				positions.resize(max_electrons);
				symbols.resize(max_electrons);
			}

			if (is_7bit)
			{
				unsigned int bit_pos = 0; // 4 K * 4 K * 11 bit << 2 ** 32
				unsigned char p, s;

				while (true)
				{
					// Fetch 32 bits and unpack up to 2 chunks of 7 + 4 bits.
					// This is faster than unpack 7 and 4 bits sequentially.
					// Since the size of buf is larger than the actual size by the TIFF header size,
					// it is always safe to read ahead.

					long long first_byte = pos + (bit_pos >> 3);
					const unsigned int bit_offset_in_first_byte = bit_pos & 7; // 7 = 00000111 (same as % 8)
					const unsigned int chunk = (*(unsigned int*)(buf + first_byte)) >> bit_offset_in_first_byte;

					p = (unsigned char)(chunk & 127); // 127 = 01111111
					bit_pos += 7; // TODO: we can remove this for further speed.
					n_pix += p;
					if (n_pix == EER_IMAGE_PIXELS) break;
					if (p == 127) continue; // this should be rare.

					s = (unsigned char)((chunk >> 7) & 15) ^ 0x0A; // 15 = 00001111; See below for 0x0A
					bit_pos += 4;
					positions[n_electron] = n_pix;
					symbols[n_electron] = s;
					n_electron++;
					n_pix++;

					p = (unsigned char)((chunk >> 11) & 127); // 127 = 01111111
					bit_pos += 7;
					n_pix += p;
					if (n_pix == EER_IMAGE_PIXELS) break;
					if (p == 127) continue;

					s = (unsigned char)((chunk >> 18) & 15) ^ 0x0A; // 15 = 00001111; See below for 0x0A
					bit_pos += 4;
					positions[n_electron] = n_pix;
					symbols[n_electron] = s;
					n_electron++;
					n_pix++;
				}
			}
			else
			{
				// unpack every two symbols = 12 bit * 2 = 24 bit = 3 byte
				// high <- |bbbbBBBB|BBBBaaaa|AAAAAAAA| -> low
				// With SIMD intrinsics at the SSSE3 level, we can unpack 10 symbols (120 bits) simultaneously.
				unsigned char p1, p2, s1, s2;

				const long long pos_limit = frame_starts[iframe] + frame_sizes[iframe];
				// Because there is a footer, it is safe to go beyond the limit by two bytes.
				while (pos < pos_limit)
				{
					// symbol is bit tricky. 0000YyXx; Y and X must be flipped.
					p1 = buf[pos];
					s1 = (buf[pos + 1] & 0x0F) ^ 0x0A; // 0x0F = 00001111, 0x0A = 00001010

					p2 = (buf[pos + 1] >> 4) | (buf[pos + 2] << 4);
					s2 = (buf[pos + 2] >> 4) ^ 0x0A;

					// Note the order. Add p before checking the size and placing a new electron.
					n_pix += p1;
					if (n_pix == EER_IMAGE_PIXELS) break;
					if (p1 < 255)
					{
						positions[n_electron] = n_pix;
						symbols[n_electron] = s1;
						n_electron++;
						n_pix++;
					}

					n_pix += p2;
					if (n_pix == EER_IMAGE_PIXELS) break;
					if (p2 < 255)
					{
						positions[n_electron] = n_pix;
						symbols[n_electron] = s2;
						n_electron++;
						n_pix++;
					}

					pos += 3;
				}
			}

			if (n_pix != EER_IMAGE_PIXELS)
				throw std::runtime_error("Number of pixels is not right.");

			if (eer_upsampling == 3)
				render16K(h_result, positions, symbols, n_electron);
			else if (eer_upsampling == 2)
				render8K(h_result, positions, symbols, n_electron);
			else if (eer_upsampling == 1)
				render4K(h_result, positions, symbols, n_electron);
			else
				throw std::runtime_error("Invalid EER upsamle");

			total_n_electron += n_electron;
		}

		free(buf);
	}
}

__declspec(dllexport) void ReadTIFF(const char* path, int layer, bool flipy, float* h_result)
{
	TIFF* ftiff = TIFFOpen(path, "r");

	// libtiff's types
	uint32 width, length;

	if (TIFFGetField(ftiff, TIFFTAG_IMAGEWIDTH, &width) != 1 ||
		TIFFGetField(ftiff, TIFFTAG_IMAGELENGTH, &length) != 1)
	{
		throw std::runtime_error("The input TIFF file does not have the width or height field.");
	}

	int3 dims = make_int3(width, length, TIFFNumberOfDirectories(ftiff));

	if (layer >= dims.z)
		throw std::runtime_error("Requested layer exceeds the stack size.");

	if (layer >= 0)
		dims.z = 1;

	uint16 sampleFormat, bitsPerSample;
	TIFFGetFieldDefaulted(ftiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
	TIFFGetFieldDefaulted(ftiff, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

	TIFFSetDirectory(ftiff, 0);

	relion::DataType datatype;

	if (bitsPerSample == 8 && sampleFormat == 1) {
		datatype = relion::UChar;
	}
	else if (bitsPerSample == 16 && sampleFormat == 1) {
		datatype = relion::UShort;
	}
	else if (bitsPerSample == 16 && sampleFormat == 2) {
		datatype = relion::Short;
	}
	else if (bitsPerSample == 32 && sampleFormat == 3) {
		datatype = relion::Float;
	}
	else {
		throw std::runtime_error("Unsupported TIFF format");
	}

	float* h_tempstrip = (float*)malloc(dims.x * sizeof(float));

	//dims.z = 20;

	for (int z = 0; z < dims.z; z++) 
	{
		TIFFSetDirectory(ftiff, layer < 0 ? z : layer);

		float* h_resultlayer = h_result + Elements2(dims) * z;

		tsize_t stripSize = TIFFStripSize(ftiff);
		tstrip_t numberOfStrips = TIFFNumberOfStrips(ftiff);
		tdata_t buf = _TIFFmalloc(stripSize);

		size_t haveread_n = 0;

		for (tstrip_t strip = 0; strip < numberOfStrips; strip++) 
		{
			tsize_t actually_read = TIFFReadEncodedStrip(ftiff, strip, buf, stripSize);
			tsize_t actually_read_n = actually_read * 8 / bitsPerSample;

			tsize_t actually_read_n_8 = actually_read_n / 8 * 8;

			if (datatype == relion::UChar)
			{
				for (tsize_t i = 0; i < actually_read_n_8; i += 8)
				{
					h_resultlayer[haveread_n + i] = (float)((uchar*)buf)[i];
					h_resultlayer[haveread_n + i + 1] = (float)((uchar*)buf)[i + 1];
					h_resultlayer[haveread_n + i + 2] = (float)((uchar*)buf)[i + 2];
					h_resultlayer[haveread_n + i + 3] = (float)((uchar*)buf)[i + 3];
					h_resultlayer[haveread_n + i + 4] = (float)((uchar*)buf)[i + 4];
					h_resultlayer[haveread_n + i + 5] = (float)((uchar*)buf)[i + 5];
					h_resultlayer[haveread_n + i + 6] = (float)((uchar*)buf)[i + 6];
					h_resultlayer[haveread_n + i + 7] = (float)((uchar*)buf)[i + 7];
				}
				for (tsize_t i = actually_read_n_8; i < actually_read_n; i++)
					h_resultlayer[haveread_n + i] = (float)((uchar*)buf)[i];
			}
			else if (datatype == relion::UShort)
				for (tsize_t i = 0; i < actually_read_n; i++)
					h_resultlayer[haveread_n + i] = (float)((ushort*)buf)[i];
			else if (datatype == relion::Short)
				for (tsize_t i = 0; i < actually_read_n; i++)
					h_resultlayer[haveread_n + i] = (float)((short*)buf)[i];
			else if (datatype == relion::Float)
				memcpy(h_resultlayer + haveread_n, buf, actually_read);

			haveread_n += actually_read_n;
		}

		_TIFFfree(buf);

		// Flip the Y axis if requested

		if (flipy)
		{
			for (tsize_t y1 = 0; y1 < dims.y / 2; y1++)
			{
				tsize_t y2 = dims.y - 1 - y1;

				memcpy(h_tempstrip, h_resultlayer + y1 * dims.x, dims.x * sizeof(float));

				memcpy(h_resultlayer + y1 * dims.x, h_resultlayer + y2 * dims.x, dims.x * sizeof(float));

				memcpy(h_resultlayer + y2 * dims.x, h_tempstrip, dims.x * sizeof(float));
			}
		}
	}

	free(h_tempstrip);

	TIFFClose(ftiff);
}

char *loadTiff3D2Metadata ( char * filename, unsigned int &sz0, unsigned int  &sz1, unsigned int  &sz2, unsigned int  &sz3, int &datatype, int &b_swap, void * &fhandle, int &header_len ) {

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_START(TiffLoadMetadata)
    #endif

	uint32 XSIZE;
	uint32 YSIZE;
	uint16 bpp;
	uint16 spp;
	uint16 Cpage;
	uint16 Npages;
    TIFF *input;
    int check;

	//disable warning and error handlers to avoid messages on unrecognized tags
	TIFFSetWarningHandler(0);
	TIFFSetErrorHandler(0);

	input=TIFFOpen(filename,"r");
	if (!input)
    {
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Cannot open the file %s",finName).c_str());
		return ((char *) "Cannot open the file.");
    }

	check=TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &XSIZE);
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Image length of %s undefined\n", finName).c_str());
		return ((char *) "Image width of undefined.");
	}		
    
	check=TIFFGetField(input, TIFFTAG_IMAGELENGTH, &YSIZE);
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Image length of %s undefined\n", finName).c_str());
		return ((char *) "Image length of undefined.");
	}		
    
	check=TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp); 
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Undefined bits per sample in %s \n", finName).c_str());
		return ((char *) "Undefined bits per sample.");
	}

	check=TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp);
	if (!check)
	{
		spp = 1;
		//TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Undefined bits per sample in %s \n", finName).c_str());
		//return ((char *) "Undefined samples per pixel.");
	}

	// 2021-02-10. Giulio. when it is compiled as a Vaa3D plugin does not check the tag TIFFTAG_PAGENUMBER to avoid exceptions on inconsistent files  
	#ifdef _VAA3D_TERAFLY_PLUGIN_MODE
	check = 0;
	#else
	// Onofri
	check=TIFFGetField(input, TIFFTAG_PAGENUMBER, &Cpage, &Npages);
	#endif
	if (check!=1 || Npages==0) { // the tag has not been read correctly
		// Add warning?
		Npages = 0;
		do {
			Npages++;
		} while ( TIFFReadDirectory(input) );
	}

	sz0 = XSIZE;
	sz1 = YSIZE;
	sz2 = Npages;
	sz3 = spp;
	datatype = bpp/8;

	//b_swap = 0;
	b_swap=TIFFIsByteSwapped(input);
	fhandle = (void *) input;
	header_len = -1;

	// the file must non be closed (it is responsibility of the caller)
	//TIFFClose(input);

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_STOP(TiffLoadMetadata, tf::IO, tf::strprintf("successfully loaded metadata from file \"%s\"", filename))
    #endif

	return ((char *) 0);
}

char *appendSlice2Tiff3DFile ( char *filename, int slice, unsigned char *img, unsigned int img_width, unsigned int img_height ) {
    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_START(TiffAppendData)
    #endif

	TIFF *output;
	uint16 spp, bpp, NPages, pg0;
	uint32 rperstrip;
	uint16 cmprssd;
	uint16 photomtrcintrp;

	//disable warning and error handlers to avoid messages on unrecognized tags
	TIFFSetWarningHandler(0);
	TIFFSetErrorHandler(0);

	output=TIFFOpen(filename,"r");
	TIFFGetField(output, TIFFTAG_BITSPERSAMPLE, &bpp); 
	TIFFGetField(output, TIFFTAG_SAMPLESPERPIXEL, &spp);
	TIFFGetField(output, TIFFTAG_PAGENUMBER, &pg0, &NPages);
	TIFFGetField(output, TIFFTAG_ROWSPERSTRIP, &rperstrip);
	TIFFGetField(output, TIFFTAG_COMPRESSION, &cmprssd);
	TIFFGetField(output, TIFFTAG_PHOTOMETRIC, &photomtrcintrp);
	TIFFClose(output);

	iim::sint64 expectedSize = ((iim::sint64) img_width) * ((iim::sint64) img_height) * ((iim::sint64) NPages) * ((iim::sint64) spp) * ((iim::sint64) (bpp/8)); 

	if ( bigtiff || expectedSize > (4*GBSIZE) ) {
		if ( (rowsPerStrip == -1 && (((iim::sint64) img_width) * ((iim::sint64) img_height)) > (4*GBSIZE)) || ((rowsPerStrip * ((iim::sint64) img_width)) > (4*GBSIZE)) )
			// one strip is larger than 4GB
			return ((char *) "Too many rows per strip for this image width.");
		else 
			output = (slice==0)? TIFFOpen(filename,"w8") : TIFFOpen(filename,"a8");
	}
	else
		output = (slice==0)? TIFFOpen(filename,"w") : TIFFOpen(filename,"a");

	TIFFSetDirectory(output,slice); // WARNING: slice must be the first page after the last, otherwise the file can be corrupted

	TIFFSetField(output, TIFFTAG_IMAGEWIDTH, img_width);
	TIFFSetField(output, TIFFTAG_IMAGELENGTH, img_height);
	TIFFSetField(output, TIFFTAG_BITSPERSAMPLE, bpp); 
	TIFFSetField(output, TIFFTAG_SAMPLESPERPIXEL, spp);
	TIFFSetField(output, TIFFTAG_ROWSPERSTRIP, rperstrip);
	TIFFSetField(output, TIFFTAG_ORIENTATION, ORIENTATION_TOPLEFT);
	TIFFSetField(output, TIFFTAG_COMPRESSION, cmprssd);
	TIFFSetField(output, TIFFTAG_PLANARCONFIG,PLANARCONFIG_CONTIG);
	TIFFSetField(output, TIFFTAG_PHOTOMETRIC, photomtrcintrp);	
	// We are writing single page of the multipage file 
	TIFFSetField(output, TIFFTAG_SUBFILETYPE, FILETYPE_PAGE);
	TIFFSetField(output, TIFFTAG_PAGENUMBER, (uint16)slice, NPages); 

	if ( rowsPerStrip == -1 ) 
		TIFFWriteEncodedStrip(output, 0, img, img_width * img_height * spp * (bpp/8));
	else { 
		int check,StripsPerImage,LastStripSize;
		uint32 rps = rperstrip;
		unsigned char *buf = img;

		StripsPerImage =  (img_height + rps - 1) / rps;
		LastStripSize = img_height % rps;
		if (LastStripSize==0)
			LastStripSize=rps;

		for (int i=0; i < StripsPerImage-1; i++){
			TIFFWriteEncodedStrip(output, i, buf, spp * rps * img_width * (bpp/8));
			buf = buf + spp * rps * img_width * (bpp/8);
		}

		TIFFWriteEncodedStrip(output, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
		buf = buf + spp * LastStripSize * img_width * (bpp/8);
	}
	//img +=  img_width * img_height;

	TIFFWriteDirectory(output);

	TIFFClose(output);

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_STOP(TiffAppendData, tf::IO, tf::strprintf("appended slice %d x %d to 3D tiff \"%s\"", img_width, img_height, filename))
    #endif

	return (char *) 0;
}

char *readTiff3DFile2Buffer ( void *fhandler, unsigned char *img, unsigned int img_width, unsigned int img_height, unsigned int first, unsigned int last, 
																		   int b_swap, int downsamplingFactor, int starti, int endi, int startj, int endj ) {
	uint32 rps;
    uint16 spp, bpp, orientation, photo, comp, planar_config;
    int check, StripsPerImage,LastStripSize;
	uint32 XSIZE;
	uint32 YSIZE;

    TIFF *input = (TIFF *) fhandler;

	check=TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp); 
	if (!check)
	{
		return ((char *) "Undefined bits per sample.");
	}

	check=TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp);
	if (!check)
	{
		spp = 1;
		//return ((char *) "Undefined samples per pixel.");
	}

	check=TIFFGetField(input, TIFFTAG_PHOTOMETRIC, &photo);
	if (!check)
	{
		return ((char *) "Cannot determine photometric interpretation.");
	}

	check=TIFFGetField(input, TIFFTAG_COMPRESSION, &comp);
	if (!check)
	{
		return ((char *) "Cannot determine compression technique.");
	}

	check=TIFFGetField(input, TIFFTAG_PLANARCONFIG, &planar_config);
	if (!check)
	{
		return ((char *) "Cannot determine planar configuration.");
	}

	starti = (starti == -1) ? 0 : starti;
	endi   = (endi == -1) ? img_height-1 : endi;
	startj = (startj == -1) ? 0 : startj;
	endj   = (endj == -1) ? img_width-1 : endj;

	check=TIFFIsTiled(input);
	if (check) // file is internally tiled
	{
		uint32 tilewidth;
		uint32 tilelength;
		uint32 tiledepth;
		tsize_t tilenum;
		tsize_t tilesize;
		tsize_t tilenum_width;
		tsize_t tilenum_length;
		ttile_t tile;
		tdata_t data;
		unsigned char *psrc; // pointer in the tile buffer to the top left pixel of the current block to be copied
		unsigned char *pdst; // pointer in the image buffer to the top left pixel of the current block to be filled
		uint32 stride_src;
		uint32 stride_dst;
		int i; // row index in the slice of the top left pixel of the current block to be filled
		int j; // column index in the slice of the top left pixel of the current block to be filled 
		uint32 width; // width of the current block to be filled (in pixels)
		uint32 len; // length of the current block to be filled (in pixels)
		int page;

		// checks
		if ( TIFFGetField(input, TIFFTAG_TILEDEPTH, &tiledepth) )
			return ((char *) "Tiling among slices (z direction) not supported.");
		if ( spp > 1 )
			if ( TIFFGetField(input, TIFFTAG_PLANARCONFIG, &planar_config) )
				if ( planar_config > 1 )
					return ((char *) "Non-interleaved multiple channels not supported with tiling.");

		// tiling is in x,y only
		TIFFGetField(input, TIFFTAG_TILEWIDTH, &tilewidth);
		TIFFGetField(input, TIFFTAG_TILELENGTH, &tilelength);
		tilenum = TIFFNumberOfTiles(input);
		tilesize = TIFFTileSize(input);
		tilenum_width  = (img_width % tilewidth) ? (img_width / tilewidth) + 1 : img_width / tilewidth;
		tilenum_length = (img_height % tilelength) ? (img_height / tilelength) + 1 : img_height / tilelength;

		data = new unsigned char[tilesize];
		stride_src = tilewidth * spp; // width of tile (in bytes)
		stride_dst = (endj - startj + 1) * spp; // width of subregion (in bytes)

		page = 0;
		do {

			psrc = ((unsigned char *)data) + ((starti % tilelength)*tilewidth + (startj % tilewidth)) * spp; // in the first tile skip (starti % tilelength) rows plus (startj % tilewidth) pixels
			pdst = img; // the buffer has the size of the subregion
			len = tilelength - (starti % tilelength); // rows to be copied for the first row of tiles 
			tile = TIFFComputeTile(input,startj,starti,0,0); // index of the first tile to be copied in the current row of tiles
			for ( i=starti; i<=endi; ) {
				width = tilewidth - (startj%tilewidth); // width of the first block to be copied/filled
				for ( j=startj; j<=endj; ) {
					TIFFReadEncodedTile(input,tile,data,(tsize_t) -1); // read tile into tile buffer
					copydata (psrc,stride_src,pdst,stride_dst,(width * spp),len); // copy the block 
					j += width;
					tile++; // index of the next tile in the same row of tiles
					psrc = ((unsigned char *)data) + ((i % tilelength)*tilewidth) * spp; // the block in the next tile begins just after (i % tilelength) rows
					pdst += width * spp; // the block in the image buffer move forward of width pixels
					width = (((tile%tilenum_width) + 1) * tilewidth <= (endj+1)) ? tilewidth : ((endj+1)%tilewidth); // if the next tile in the row is all within the subregion, width is tilewidth otherwise it is shorter
				}
				i += len;
				tile = TIFFComputeTile(input,startj,i,0,0); // index of the first tile to be copied in the current row of tiles
				psrc = ((unsigned char *)data) + ((i % tilelength)*tilewidth + (startj % tilewidth)) * spp; // in the first tile of the next row of tiles skip (i % tilelength) rows plus (startj % tilewidth) pixels
				pdst = img + ((i-starti) * stride_dst); // the block in the image buffer begin after (i-starti) rows
				len = (((tile/tilenum_width) + 1) * tilelength <= (endi+1)) ? tilelength : ((endi+1)%tilelength); // if the next row of tiles is all within the subregion, len is tilelength otherwise it is shorter
			}

			page++;
	
		}while ( page < static_cast<int>(last-first+1) && TIFFReadDirectory(input));

		return (char *) 0;
	}

	// 2018-08-22. Giulio. @CHANGED if TIFFTAG_ROWSPERSTRIP is missing it is assumed that all rows are packed into only one strip
	if ( !TIFFGetField(input, TIFFTAG_ROWSPERSTRIP, &rps) )
		rps = img_height;	
// 	check=TIFFGetField(input, TIFFTAG_ROWSPERSTRIP, &rps);
// 	if (!check)
// 	{
// 		return ((char *) "Undefined rows per strip.");
// 	}

    
	//check=TIFFGetField(input, TIFFTAG_ORIENTATION, &orientation); 
	//if (!check)
	//{
	//	return ((char *) "Image orientation undefined.");
	//}	
    
	StripsPerImage =  (img_height + rps - 1) / rps;
	LastStripSize = img_height % rps;
	if (LastStripSize==0)
		LastStripSize=rps;

	unsigned char *buf = img;
	int page=0;

	if ( downsamplingFactor == 1 ) { // read without downsampling

		if ( starti < 0 || endi >= img_height || startj < 0 || endj >= img_width || starti >= endi || startj >= endj )
		{
			return ((char *) "Wrong substack indices.");
		}

		if ( starti == 0 && endi == (img_height-1) && startj == 0 && endj == (img_width-1) ) { // read whole images from files 

			check=TIFFSetDirectory(input, first);
			if (!check)
			{
				return ((char *) "Cannot open the requested first strip.");
			}

			do{

				for (int i=0; i < StripsPerImage-1; i++){
					if (comp==1) {
						TIFFReadRawStrip(input, i, buf, spp * rps * img_width * (bpp/8));
						buf = buf + spp * rps * img_width * (bpp/8);
					}
					else{
						TIFFReadEncodedStrip(input, i, buf, spp * rps * img_width * (bpp/8));
						buf = buf + spp * rps * img_width * (bpp/8);
					}
				}

				if (comp==1) {
					TIFFReadRawStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
				}
				else{
					TIFFReadEncodedStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
				}
				buf = buf + spp * LastStripSize * img_width * (bpp/8);

				page++;
	
			}while ( page < static_cast<int>(last-first+1) && TIFFReadDirectory(input));//while (TIFFReadDirectory(input));

		}
		else { // read only a subregion of images from files

			check=TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &XSIZE);
			if (!check)
			{
				return ((char *) "Image width of undefined.");
			}		    
			check=TIFFGetField(input, TIFFTAG_IMAGELENGTH, &YSIZE);
			if (!check)
			{
				return ((char *) "Image length of undefined.");
			}

			unsigned char *rowbuf = new unsigned char[spp * rps * XSIZE * (bpp/8)];
			unsigned char *bufptr;

			do{
				check=TIFFSetDirectory(input, first + page);
				if (!check)
				{
					return ((char *) "Cannot open next requested strip.");
				}

				int stripIndex = (starti / rps) - 1; // the strip preceeding the first one
				for (int i=starti; i <= endi; i++) {
					if ( floor((double)i / rps) > stripIndex ) { // read a new strip
						stripIndex = (int)floor((double)i / rps);
						if (comp==1) {
							TIFFReadRawStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
						}
						else{
							TIFFReadEncodedStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
						}
					}
					bufptr = rowbuf + (i % rps) * (spp * XSIZE * (bpp/8));
					if ( bpp == 8 )
						for (int j=0, j1=startj; j<=(endj-startj); j++, j1++) {
							for (int c=0; c<spp; c++) {
								buf[j * spp + c] = bufptr[j1 * spp + c];
							}
						}
					else
						for (int j=0 , j1=startj; j<=(endj-startj); j++, j1++) {
							for (int c=0; c<spp; c++) {
								((uint16 *)buf)[j * spp + c] = ((uint16 *)bufptr)[j1 * spp + c];
							}
						}
					buf = buf + spp * (endj-startj+1) * (bpp/8);
				}

				page++;

			}while ( page < static_cast<int>(last-first+1) );

			delete []rowbuf;

		}

		// input file is assumed to be already open and it is provided as an handler; the file should be closed by caller
		//TIFFClose(input); 

		// 2021. Giulio. The following code seems useless since the condition should be always false
// 		if ( page < static_cast<int>(last-first+1) ){
// 			return ((char *) "Cannot read all the pages.");
// 		}
	}
	else { // read with downsampling

		// preliminary checks
		if ( starti != 0 || endi != (img_height-1) || startj != 0 || endj != (img_width-1) ) { // a subregion has been requested 
			return ((char *) "Subregion extraction not supported with downsampling.");
		}		    
		check=TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &XSIZE);
		if (!check)
		{
			return ((char *) "Image width of undefined.");
		}		    
		check=TIFFGetField(input, TIFFTAG_IMAGELENGTH, &YSIZE);
		if (!check)
		{
			return ((char *) "Image length of undefined.");
		}

		if ( (int)ceil((double)XSIZE/downsamplingFactor) < img_width ) 
		{
			return ((char *) "Requested image width too large.");
		}
		if ( (int)ceil((double)YSIZE/downsamplingFactor) < img_height ) 
		{
			return ((char *) "Requested image height too large.");
		}

		unsigned char *rowbuf = new unsigned char[spp * rps * XSIZE * (bpp/8)];
		unsigned char *bufptr;

		do{
			check=TIFFSetDirectory(input, ((first + page) * downsamplingFactor));
			if (!check)
			{
				return ((char *) "Cannot open next requested strip.");
			}

			int stripIndex = -1; // the strip preceeding the first one
			for (int i=0; i < img_height; i++) {
				if ( floor(i * downsamplingFactor / (double)rps) > stripIndex ) { // read a new strip
					stripIndex = (int)floor(i * downsamplingFactor / (double)rps);
					if (comp==1) {
						TIFFReadRawStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
					}
					else{
						TIFFReadEncodedStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
					}
				}
				bufptr = rowbuf + ((i * downsamplingFactor) % rps) * (spp * XSIZE * (bpp/8));
				if ( bpp == 8 )
					for (int j=0; j<img_width; j++) {
						for (int c=0; c<spp; c++) {
							buf[j * spp + c] = bufptr[j * spp * downsamplingFactor + c];
						}
					}
				else
					for (int j=0; j<img_width; j++) {
						for (int c=0; c<spp; c++) {
							((uint16 *)buf)[j * spp + c] = ((uint16 *)bufptr)[j * spp * downsamplingFactor + c];
						}
					}
				buf = buf + spp * img_width * (bpp/8);
			}

			page++;

		}while ( page < static_cast<int>(last-first+1) );

		delete []rowbuf;
	}
    
	// swap the data bytes if necessary 	
	if (b_swap)
	{
		int i;
		size_t total = img_width * img_height * spp * (last-first+1);
		if (bpp/8 == 2)
		{
			for (i=0;i<total; i++)
			{
                iim::swap2bytes((void *)(img+2*i));
			}
		}
		else if (bpp/8 == 4)
		{
			for (i=0;i<total; i++)
			{
                iim::swap4bytes((void *)(img+4*i));
			}
		}
	}

	return (char *) 0;
}

void 
	iomanager::tiff3D::readMetadata(
	std::string img_path,		// (INPUT)	image filepath
	int & img_width,			// (OUTPUT) image width  (in pixels)
	int & img_height,			// (OUTPUT) image height (in pixels)
	int & img_depth,			// (OUTPUT) image depth (in pixels)
	int & img_bytes_x_chan,	// (OUTPUT) number of bytes per channel
	int & img_chans,			// (OUTPUT) number of channels
	const std::string & params)	// (INPUT)	additional parameters <param1=val, param2=val, ...> 
throw (iom::exception)
{
	/**/iom::debug(iom::LEV3, iom::strprintf("img_path = %s, img_width = %d, img_height = %d, img_depth = %d, img_bytes_x_chan = %d, img_chans = %d, params = \"%s\"",
		img_path.c_str(), img_width, img_height, img_depth,	img_bytes_x_chan, img_chans, params.c_str()).c_str(), __iom__current__function__);

	//disable warning and error handlers to avoid messages on unrecognized tags
	TIFFSetWarningHandler(0);
	TIFFSetErrorHandler(0);

	TIFF* input=TIFFOpen(img_path.c_str(),"r");
	if (!input)
		throw iom::exception(iom::strprintf("unable to open image \"%s\". Possible unsupported format or it isn't an image.\nSupported format is 3DTIFF", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &img_width))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_IMAGEWIDTH' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_IMAGELENGTH, &img_height))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_IMAGELENGTH' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &img_bytes_x_chan))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_BITSPERSAMPLE' from image \"%s\". ", img_path.c_str()), __iom__current__function__);
	img_bytes_x_chan /= 8;

	if (!TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &img_chans)) 
		img_chans = 1;
		//throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_SAMPLESPERPIXEL' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	// Onofri
	uint16 cpage;  // Current page. We do not actually need it.
	uint16 npages; // Number of pages. 
	int PNcheck=TIFFGetField(input, TIFFTAG_PAGENUMBER, &cpage, &npages);
	if (!PNcheck || npages==0) { // the tag has not been read correctly
		//iom::warning(iom::strprintf("unable to determine 'TIFFTAG_PAGENUMBER' from image file \"%s\". ", img_path.c_str()).c_str(),__iom__current__function__);
		img_depth = 0;
		do {
			img_depth++;
		} while (TIFFReadDirectory(input));
	}
	else
		img_depth = npages;

	TIFFClose(input);
}

