	if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_BITSPERSAMPLE' from image \"%s\". ", img_path.c_str()), __iom__current__function__);
	img_bytes_x_chan = bpp/8;

	if (!TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp)) 
		spp = 1;
		//throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_SAMPLESPERPIXEL' from image \"%s\". ", img_path.c_str()), __iom__current__function__);
	img_chans = spp;

	// Onofri
	int img_depth;			// image depth (in pixels)
	uint16 cpage;  // Current page. We do not actually need it.
	uint16 npages; // Number of pages. 
	int PNcheck=TIFFGetField(input, TIFFTAG_PAGENUMBER, &cpage, &npages);
	if (!PNcheck || npages==0) { // the tag has not been read correctly
		//iom::warning(iom::strprintf("unable to determine 'TIFFTAG_PAGENUMBER' from image file \"%s\". ", img_path.c_str()).c_str(),__iom__current__function__);
		img_depth = 0;
		do {
			img_depth++;
		} while (TIFFReadDirectory(input));
	}
	else
		img_depth = npages;

	TIFFClose(input);
	
	// check the exception after closing the file
	if ( img_depth > 1 ) 
		throw iom::exception(iom::strprintf("image \"%s\" has more than one page.\nSupported format is 2DTIFF", img_path.c_str()), __iom__current__function__);
}

bool Import_3DTiff_Workspace::sizeOfDomain() {
    uint32 bitsPerSample = 0;
    uint16 rgb_check;

    TIFF* input_Image;
    TIFFSetWarningHandler(nullptr);
    if((input_Image = TIFFOpen(fileName.c_str(), "r")) == nullptr){
        X=-1;
        return false;
    }


    if(input_Image) {
        TIFFGetField(input_Image, TIFFTAG_IMAGEWIDTH, &X);
        TIFFGetField(input_Image, TIFFTAG_IMAGELENGTH, &Y);
        TIFFGetField(input_Image, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(input_Image, TIFFTAG_SAMPLESPERPIXEL, &rgb_check);
    }

    if(rgb_check == 3){
        std::cout << "Image must be grayscale, input is RGB." << std::endl;
        X=-1;
        return false;
    }

    if(bitsPerSample!=8){
        if(bitsPerSample == 16 || bitsPerSample == 32){
            std::cout << "Image must be 8-bit" << std::endl;
            X=-1;
            return false;
        }
        else {
            bitsPerSample = 8;
        }
    }

    int page=0;
    while(true){
        TIFFSetDirectory(input_Image, page);
        page++;
        if(!TIFFReadDirectory(input_Image)) {
            break;
        }
    }
    Z=page;

    xMin = 0;
    yMin = 0;
    zMin = 0;
    xMax = X-1;
    yMax = Y-1;
    zMax = Z-1;

    TIFFClose(input_Image);

    return true;
}

bool TiffDecoder::readHeader()
{
    bool result = false;

    TIFF* tif = static_cast<TIFF*>(m_tif.get());
    if (!tif)
    {
        // TIFFOpen() mode flags are different to fopen().  A 'b' in mode "rb" has no effect when reading.
        // http://www.remotesensing.org/libtiff/man/TIFFOpen.3tiff.html
        if ( !m_buf.empty() )
        {
            m_buf_pos = 0;
            TiffDecoderBufHelper* buf_helper = new TiffDecoderBufHelper(this->m_buf, this->m_buf_pos);
            tif = TIFFClientOpen( "", "r", reinterpret_cast<thandle_t>(buf_helper), &TiffDecoderBufHelper::read,
                                  &TiffDecoderBufHelper::write, &TiffDecoderBufHelper::seek,
                                  &TiffDecoderBufHelper::close, &TiffDecoderBufHelper::size,
                                  &TiffDecoderBufHelper::map, /*unmap=*/0 );
            if (!tif)
                delete buf_helper;
        }
        else
        {
            tif = TIFFOpen(m_filename.c_str(), "r");
        }
        if (tif)
            m_tif.reset(tif, cv_tiffCloseHandle);
        else
            m_tif.release();
    }

    if (tif)
    {
        uint32 wdth = 0, hght = 0;
        uint16 photometric = 0;

        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &wdth));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &hght));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric));

        {
            bool isGrayScale = photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK;
            uint16 bpp = 8, ncn = isGrayScale ? 1 : 3;
            if (0 == TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp))
            {
                // TIFF bi-level images don't require TIFFTAG_BITSPERSAMPLE tag
                bpp = 1;
            }
            CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &ncn));

            m_width = wdth;
            m_height = hght;
            if (ncn == 3 && photometric == PHOTOMETRIC_LOGLUV)
            {
                m_type = CV_32FC3;
                m_hdr = true;
                return true;
            }
            m_hdr = false;

            if( bpp > 8 &&
               ((photometric > 2) ||
                (ncn != 1 && ncn != 3 && ncn != 4)))
                bpp = 8;

            uint16 sample_format = SAMPLEFORMAT_UINT;
            TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sample_format);
            int wanted_channels = normalizeChannelsNumber(ncn);
            switch (bpp)
            {
            case 1:
            {
                CV_Check((int)sample_format, sample_format == SAMPLEFORMAT_UINT || sample_format == SAMPLEFORMAT_INT, "");
                int depth = sample_format == SAMPLEFORMAT_INT ? CV_8S : CV_8U;
                m_type = CV_MAKETYPE(depth, !isGrayScale ? wanted_channels : 1);
                result = true;
                break;
            }
            case 4:
                //support 4-bit palette.
                if (photometric == PHOTOMETRIC_PALETTE)
                {
                    CV_Check((int)sample_format, sample_format == SAMPLEFORMAT_UINT || sample_format == SAMPLEFORMAT_INT, "");
                    int depth = sample_format == SAMPLEFORMAT_INT ? CV_8S : CV_8U;
                    m_type = CV_MAKETYPE(depth, 3);
                    result = true;
                }
                else
                    CV_Error(cv::Error::StsError, "bitsperpixel value is 4 should be palette.");
                break;
            case 8:
            {
                //Palette color, the value of the component is used as an index into the red,
                //green and blue curves in the ColorMap field to retrieve an RGB triplet that defines the color.
                CV_Check((int)sample_format, sample_format == SAMPLEFORMAT_UINT || sample_format == SAMPLEFORMAT_INT, "");
                int depth = sample_format == SAMPLEFORMAT_INT ? CV_8S : CV_8U;
                if (photometric == PHOTOMETRIC_PALETTE)
                    m_type = CV_MAKETYPE(depth, 3);
                else
                    m_type = CV_MAKETYPE(depth, !isGrayScale ? wanted_channels : 1);
                result = true;
                break;
            }
            case 10:
            case 12:
            case 14:
            case 16:
            {
                CV_Check((int)sample_format, sample_format == SAMPLEFORMAT_UINT || sample_format == SAMPLEFORMAT_INT, "");
                int depth = sample_format == SAMPLEFORMAT_INT ? CV_16S : CV_16U;
                m_type = CV_MAKETYPE(depth, !isGrayScale ? wanted_channels : 1);
                result = true;
                break;
            }
            case 32:
            {
                CV_Check((int)sample_format, sample_format == SAMPLEFORMAT_IEEEFP || sample_format == SAMPLEFORMAT_INT, "");
                int depth = sample_format == SAMPLEFORMAT_IEEEFP ? CV_32F : CV_32S;
                m_type = CV_MAKETYPE(depth, wanted_channels);
                result = true;
                break;
            }
            case 64:
                CV_CheckEQ((int)sample_format, SAMPLEFORMAT_IEEEFP, "");
                m_type = CV_MAKETYPE(CV_64F, wanted_channels);
                result = true;
                break;
            default:
                CV_Error(cv::Error::StsError, "Invalid bitsperpixel value read from TIFF header! Must be 1, 8, 10, 12, 14, 16, 32 or 64.");
            }
        }
    }

    if( !result )
        close();

    return result;
}

bool  TiffDecoder::readData( Mat& img )
{
    int type = img.type();
    int depth = CV_MAT_DEPTH(type);

    CV_Assert(!m_tif.empty());
    TIFF* tif = (TIFF*)m_tif.get();

    uint16 photometric = (uint16)-1;
    CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric));

    if (m_hdr && depth >= CV_32F)
    {
        CV_TIFF_CHECK_CALL(TIFFSetField(tif, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT));
    }

    bool color = img.channels() > 1;

    CV_CheckType(type, depth == CV_8U || depth == CV_8S || depth == CV_16U || depth == CV_16S || depth == CV_32S || depth == CV_32F || depth == CV_64F, "");

    if (m_width && m_height)
    {
        int is_tiled = TIFFIsTiled(tif) != 0;
        bool isGrayScale = photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK;
        uint16 bpp = 8, ncn = isGrayScale ? 1 : 3;
        if (0 == TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp))
        {
            // TIFF bi-level images don't require TIFFTAG_BITSPERSAMPLE tag
            bpp = 1;
        }
        CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &ncn));
        uint16 img_orientation = ORIENTATION_TOPLEFT;
        CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_ORIENTATION, &img_orientation));
        constexpr const int bitsPerByte = 8;
        int dst_bpp = (int)(img.elemSize1() * bitsPerByte);
        bool vert_flip = dst_bpp == 8 &&
                        (img_orientation == ORIENTATION_BOTRIGHT || img_orientation == ORIENTATION_RIGHTBOT ||
                         img_orientation == ORIENTATION_BOTLEFT || img_orientation == ORIENTATION_LEFTBOT);
        int wanted_channels = normalizeChannelsNumber(img.channels());

        if (dst_bpp == 8)
        {
            char errmsg[1024];
            if (!TIFFRGBAImageOK(tif, errmsg))
            {
                CV_LOG_WARNING(NULL, "OpenCV TIFF: TIFFRGBAImageOK: " << errmsg);
                close();
                return false;
            }
        }

        uint32 tile_width0 = m_width, tile_height0 = 0;

        if (is_tiled)
        {
            CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tile_width0));
            CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_TILELENGTH, &tile_height0));
        }
        else
        {
            // optional
            CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &tile_height0));
        }

        {
            if (tile_width0 == 0)
                tile_width0 = m_width;

            if (tile_height0 == 0 ||
                    (!is_tiled && tile_height0 == std::numeric_limits<uint32>::max()) )
                tile_height0 = m_height;

            const int TILE_MAX_WIDTH = (1 << 24);
            const int TILE_MAX_HEIGHT = (1 << 24);
            CV_Assert((int)tile_width0 > 0 && (int)tile_width0 <= TILE_MAX_WIDTH);
            CV_Assert((int)tile_height0 > 0 && (int)tile_height0 <= TILE_MAX_HEIGHT);
            const uint64_t MAX_TILE_SIZE = (CV_BIG_UINT(1) << 30);
            CV_CheckLE((int)ncn, 4, "");
            CV_CheckLE((int)bpp, 64, "");
            CV_Assert(((uint64_t)tile_width0 * tile_height0 * ncn * std::max(1, (int)(bpp / bitsPerByte)) < MAX_TILE_SIZE) && "TIFF tile size is too large: >= 1Gb");

            if (dst_bpp == 8)
            {
                // we will use TIFFReadRGBA* functions, so allocate temporary buffer for 32bit RGBA
                bpp = 8;
                ncn = 4;
            }
            else if (dst_bpp == 32 || dst_bpp == 64)
            {
                CV_Assert(ncn == img.channels());
                CV_TIFF_CHECK_CALL(TIFFSetField(tif, TIFFTAG_SAMPLEFORMAT, SAMPLEFORMAT_IEEEFP));
            }
            const size_t src_buffer_bytes_per_row = divUp(static_cast<size_t>(ncn * tile_width0 * bpp), static_cast<size_t>(bitsPerByte));
            const size_t src_buffer_size = tile_height0 * src_buffer_bytes_per_row;
            const size_t src_buffer_unpacked_bytes_per_row = divUp(static_cast<size_t>(ncn * tile_width0 * dst_bpp), static_cast<size_t>(bitsPerByte));
            const size_t src_buffer_unpacked_size = tile_height0 * src_buffer_unpacked_bytes_per_row;
            const bool needsUnpacking = (bpp < dst_bpp);
            AutoBuffer<uchar> _src_buffer(src_buffer_size);
            uchar* src_buffer = _src_buffer.data();
            AutoBuffer<uchar> _src_buffer_unpacked(needsUnpacking ? src_buffer_unpacked_size : 0);
            uchar* src_buffer_unpacked = needsUnpacking ? _src_buffer_unpacked.data() : nullptr;
            int tileidx = 0;

            for (int y = 0; y < m_height; y += (int)tile_height0)
            {
                int tile_height = std::min((int)tile_height0, m_height - y);

                const int img_y = vert_flip ? m_height - y - tile_height : y;

                for(int x = 0; x < m_width; x += (int)tile_width0, tileidx++)
                {
                    int tile_width = std::min((int)tile_width0, m_width - x);

                    switch (dst_bpp)
                    {
                        case 8:
                        {
                            uchar* bstart = src_buffer;
                            if (!is_tiled)
                            {
                                CV_TIFF_CHECK_CALL(TIFFReadRGBAStrip(tif, y, (uint32*)src_buffer));
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL(TIFFReadRGBATile(tif, x, y, (uint32*)src_buffer));
                                // Tiles fill the buffer from the bottom up
                                bstart += (tile_height0 - tile_height) * tile_width0 * 4;
                            }

                            for (int i = 0; i < tile_height; i++)
                            {
                                if (color)
                                {
                                    if (wanted_channels == 4)
                                    {
                                        icvCvt_BGRA2RGBA_8u_C4R(bstart + i*tile_width0*4, 0,
                                                img.ptr(img_y + tile_height - i - 1, x), 0,
                                                Size(tile_width, 1) );
                                    }
                                    else
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "TIFF-8bpp: BGR/BGRA images are supported only");
                                        icvCvt_BGRA2BGR_8u_C4C3R(bstart + i*tile_width0*4, 0,
                                                img.ptr(img_y + tile_height - i - 1, x), 0,
                                                Size(tile_width, 1), 2);
                                    }
                                }
                                else
                                {
                                    CV_CheckEQ(wanted_channels, 1, "");
                                    icvCvt_BGRA2Gray_8u_C4C1R( bstart + i*tile_width0*4, 0,
                                            img.ptr(img_y + tile_height - i - 1, x), 0,
                                            Size(tile_width, 1), 2);
                                }
                            }
                            break;
                        }

                        case 16:
                        {
                            if (!is_tiled)
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedStrip(tif, tileidx, (uint32*)src_buffer, src_buffer_size) >= 0);
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedTile(tif, tileidx, (uint32*)src_buffer, src_buffer_size) >= 0);
                            }

                            for (int i = 0; i < tile_height; i++)
                            {
                                ushort* buffer16 = (ushort*)(src_buffer+i*src_buffer_bytes_per_row);
                                if (needsUnpacking)
                                {
                                    const uchar* src_packed = src_buffer+i*src_buffer_bytes_per_row;
                                    uchar* dst_unpacked = src_buffer_unpacked+i*src_buffer_unpacked_bytes_per_row;
                                    if (bpp == 10)
                                        _unpack10To16(src_packed, src_packed+src_buffer_bytes_per_row,
                                                      (ushort*)dst_unpacked, (ushort*)(dst_unpacked+src_buffer_unpacked_bytes_per_row),
                                                      ncn * tile_width0);
                                    else if (bpp == 12)
                                        _unpack12To16(src_packed, src_packed+src_buffer_bytes_per_row,
                                                      (ushort*)dst_unpacked, (ushort*)(dst_unpacked+src_buffer_unpacked_bytes_per_row),
                                                      ncn * tile_width0);
                                    else if (bpp == 14)
                                        _unpack14To16(src_packed, src_packed+src_buffer_bytes_per_row,
                                                      (ushort*)dst_unpacked, (ushort*)(dst_unpacked+src_buffer_unpacked_bytes_per_row),
                                                      ncn * tile_width0);
                                    buffer16 = (ushort*)dst_unpacked;
                                }

                                if (color)
                                {
                                    if (ncn == 1)
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "");
                                        icvCvt_Gray2BGR_16u_C1C3R(buffer16, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                    }
                                    else if (ncn == 3)
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "");
                                        icvCvt_RGB2BGR_16u_C3R(buffer16, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                    }
                                    else if (ncn == 4)
                                    {
                                        if (wanted_channels == 4)
                                        {
                                            icvCvt_BGRA2RGBA_16u_C4R(buffer16, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                        }
                                        else
                                        {
                                            CV_CheckEQ(wanted_channels, 3, "TIFF-16bpp: BGR/BGRA images are supported only");
                                            icvCvt_BGRA2BGR_16u_C4C3R(buffer16, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1), 2);
                                        }
                                    }
                                    else
                                    {
                                        CV_Error(Error::StsError, "Not supported");
                                    }
                                }
                                else
                                {
                                    CV_CheckEQ(wanted_channels, 1, "");
                                    if( ncn == 1 )
                                    {
                                        memcpy(img.ptr<ushort>(img_y + i, x),
                                               buffer16,
                                               tile_width*sizeof(ushort));
                                    }
                                    else
                                    {
                                        icvCvt_BGRA2Gray_16u_CnC1R(buffer16, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1), ncn, 2);
                                    }
                                }
                            }
                            break;
                        }

                        case 32:
                        case 64:
                        {
                            if( !is_tiled )
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedStrip(tif, tileidx, src_buffer, src_buffer_size) >= 0);
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedTile(tif, tileidx, src_buffer, src_buffer_size) >= 0);
                            }

                            Mat m_tile(Size(tile_width0, tile_height0), CV_MAKETYPE((dst_bpp == 32) ? (depth == CV_32S ? CV_32S : CV_32F) : CV_64F, ncn), src_buffer);
                            Rect roi_tile(0, 0, tile_width, tile_height);
                            Rect roi_img(x, img_y, tile_width, tile_height);
                            if (!m_hdr && ncn == 3)
                                cvtColor(m_tile(roi_tile), img(roi_img), COLOR_RGB2BGR);
                            else if (!m_hdr && ncn == 4)
                                cvtColor(m_tile(roi_tile), img(roi_img), COLOR_RGBA2BGRA);
                            else
                                m_tile(roi_tile).copyTo(img(roi_img));
                            break;
                        }
                        default:
                        {
                            CV_Assert(0 && "OpenCV TIFF: unsupported depth");
                        }
                    }  // switch (dst_bpp)
                }  // for x
            }  // for y
        }
        if (bpp < dst_bpp)
          img *= (1<<(dst_bpp-bpp));
        fixOrientation(img, img_orientation, dst_bpp);
    }

    if (m_hdr && depth >= CV_32F)
    {
        CV_Assert(photometric == PHOTOMETRIC_LOGLUV);
        cvtColor(img, img, COLOR_XYZ2BGR);
    }
    return true;
}

FXbool fxloadTIF(FXStream& store,FXColor*& data,FXint& width,FXint& height,FXushort& codec){
  tiff_store_handle s_handle;
  FXuval size,s;

  // Null out
  data=nullptr;
  width=0;
  height=0;

  // Set error/warning handlers
  TIFFSetErrorHandler(nullptr);
  TIFFSetWarningHandler(nullptr);

  // Initialize
  s_handle.store=&store;
  s_handle.begin=store.position();
  s_handle.end=store.position();

  FXTRACE((100,"fxloadTIF\n"));

  // Open image
  TIFF* image=TIFFClientOpen("tiff","rm",(thandle_t)&s_handle,tif_read_store,tif_write_store,tif_seek_store,tif_close_store,tif_size_store,nullptr,nullptr);
  if(image){

    // Get sizes
    TIFFGetField(image,TIFFTAG_IMAGEWIDTH,&width);
    TIFFGetField(image,TIFFTAG_IMAGELENGTH,&height);
    TIFFGetField(image,TIFFTAG_COMPRESSION,&codec);

    FXTRACE((100,"fxloadTIF: width=%d height=%d codec=%d\n",width,height,codec));

    // Make room for data
    size=width*height;
    if(allocElms(data,size)){
      if(TIFFReadRGBAImageOriented(image,width,height,data,ORIENTATION_TOPLEFT,0)){
        for(s=0; s<size; s++){
          data[s]=((data[s]&0xff)<<16)|((data[s]&0xff0000)>>16)|(data[s]&0xff00)|(data[s]&0xff000000);
          }
        TIFFClose(image);
        return true;
        }
      freeElms(data);
      }
    TIFFClose(image);
    }
  return false;
  }

FXbool fxloadTIF__(FXStream& store,FXColor*& data,FXint& width,FXint& height,FXushort& codec){
  tiff_store_handle s_handle;
  FXbool result=false;
  TIFF* image;

  // Null out
  data=nullptr;
  width=0;
  height=0;
  codec=0;

  // Set error/warning handlers
  TIFFSetErrorHandler(nullptr);
  TIFFSetWarningHandler(nullptr);

  // Initialize
  s_handle.store=&store;
  s_handle.begin=store.position();
  s_handle.end=store.position();

  FXTRACE((100,"fxloadGEOTIF\n"));

  // Open image
  if((image=TIFFClientOpen("tiff","rm",(thandle_t)&s_handle,tif_read_store,tif_write_store,tif_seek_store,tif_close_store,tif_size_store,nullptr,nullptr))!=nullptr){
    FXushort samples=0;
    FXushort samplebits=0;
    FXushort format=0;
    FXuint   scanlinesize;
    FXuchar *scanline;

    // Get size
    TIFFGetField(image,TIFFTAG_IMAGEWIDTH,&width);
    TIFFGetField(image,TIFFTAG_IMAGELENGTH,&height);
    TIFFGetField(image,TIFFTAG_SAMPLESPERPIXEL,&samples);
    TIFFGetField(image,TIFFTAG_BITSPERSAMPLE,&samplebits);
    TIFFGetField(image,TIFFTAG_SAMPLEFORMAT,&format);

    // We try to remember the codec for later when we save the image back out...
    TIFFGetField(image,TIFFTAG_COMPRESSION,&codec);

    // Get line size (bytes)
    scanlinesize=TIFFScanlineSize(image);

    // Show image configuration
    FXTRACE((100,"width=%d height=%d codec=%u samples=%u samplebits=%u format=%u scanlinesize=%u\n",width,height,codec,samples,samplebits,format,scanlinesize));

    // Supported formats
    if((format==SAMPLEFORMAT_UINT || format==SAMPLEFORMAT_INT || format==SAMPLEFORMAT_IEEEFP) && (samples==1 || samples==3)){

      // Allocate scanline buffer
      if(callocElms(scanline,scanlinesize)){

        // Make room for data
        if(callocElms(data,width*height)){

/*

    FXuint nPlanarConfig=0;
    FXuint nCompressFlag=0;
    FXuint nPhotometric=0;




    TIFFGetField(image,TIFFTAG_PLANARCONFIG,&nPlanarConfig);
    TIFFGetField(image,TIFFTAG_COMPRESSION,&nCompressFlag);
    TIFFGetField(image,TIFFTAG_PHOTOMETRIC,&nPhotometric);

    FXTRACE((100,"nPlanarConfig=%u\n",nPlanarConfig));
    FXTRACE((100,"nCompressFlag=%u\n",nCompressFlag));
    FXTRACE((100,"nPhotometric=%u\n",nPhotometric));

    switch(nSampleFormat){
      case SAMPLEFORMAT_UINT:
        break;
      case SAMPLEFORMAT_INT:
        break;
      case SAMPLEFORMAT_IEEEFP:
        break;
      case SAMPLEFORMAT_VOID:
        break;
      case SAMPLEFORMAT_COMPLEXINT:
        break;
      case SAMPLEFORMAT_COMPLEXIEEEFP:
        break;
      default:
        break;
      }
*/

          // Read lines
          for(FXint y=0; y<height; ++y){
            TIFFReadScanline(image,scanline,y,0);

            if(samples==3){
              if(samplebits==8){
                for(FXint x=0; x<width; ++x){
                  ((FXuchar*)&data[y*width+x])[0]=scanline[3*x+2];        // Blue
                  ((FXuchar*)&data[y*width+x])[1]=scanline[3*x+1];        // Green
                  ((FXuchar*)&data[y*width+x])[2]=scanline[3*x+0];        // Red
                  ((FXuchar*)&data[y*width+x])[3]=255;                    // Alpha
                  }
                }
              else if(samplebits==16){
                for(FXint x=0; x<width; ++x){
                  ((FXuchar*)&data[y*width+x])[0]=((FXushort*)scanline)[3*x+2]/257;
                  ((FXuchar*)&data[y*width+x])[1]=((FXushort*)scanline)[3*x+1]/257;
                  ((FXuchar*)&data[y*width+x])[2]=((FXushort*)scanline)[3*x+0]/257;
                  ((FXuchar*)&data[y*width+x])[3]=255;
                  }
                }
              }
            else{
              if(samplebits==8){
                for(FXint x=0; x<width; ++x){
                  ((FXuchar*)&data[y*width+x])[0]=scanline[x];          // Blue
                  ((FXuchar*)&data[y*width+x])[1]=scanline[x];          // Green
                  ((FXuchar*)&data[y*width+x])[2]=scanline[x];          // Red
                  ((FXuchar*)&data[y*width+x])[3]=255;                  // Alpha
                  }
                }
              else if(samplebits==16){
                for(FXint x=0; x<width; ++x){
                  ((FXuchar*)&data[y*width+x])[0]=((FXushort*)scanline)[x]/257;
                  ((FXuchar*)&data[y*width+x])[1]=((FXushort*)scanline)[x]/257;
                  ((FXuchar*)&data[y*width+x])[2]=((FXushort*)scanline)[x]/257;
                  ((FXuchar*)&data[y*width+x])[3]=255;
                  }
                }
              }
            }

          // Got as far as this if success
          result=true;
          }
        }
      freeElms(scanline);
      }
    TIFFClose(image);
    }
  return result;
  }

bool Image::LoadTIFFImage(Stream::IStream& stream)
{
	bool result = false;
	TIFFStreamManager manager(stream);
	TIFF* tif = manager.Open("Test", "r");
	if (tif != 0)
	{
		uint32 newImageWidth;
		uint32 newImageHeight;
		TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &newImageWidth);
		TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &newImageHeight);

		unsigned int pixelCount = (unsigned int)(newImageWidth * newImageHeight);
		uint32* rawPixelData = new uint32[pixelCount];
		if (TIFFReadRGBAImage(tif, newImageWidth, newImageHeight, rawPixelData, 0) != 0)
		{
			// Apply the new image format
			SetImageFormat(newImageWidth, newImageHeight, PIXELFORMAT_RGBA, DATAFORMAT_8BIT);

			// Decode the image data
			for (unsigned int ypos = 0; ypos < _imageHeight; ++ypos)
			{
				unsigned int writeYPos = ((_imageHeight - 1) - ypos);
				for (unsigned int xpos = 0; xpos < _imageWidth; ++xpos)
				{
					uint32 pixel = rawPixelData[(ypos * _imageWidth) + xpos];
					unsigned char r = (unsigned char)TIFFGetR(pixel);
					unsigned char g = (unsigned char)TIFFGetG(pixel);
					unsigned char b = (unsigned char)TIFFGetB(pixel);
					unsigned char a = (unsigned char)TIFFGetA(pixel);
					WritePixelDataInternal(xpos, writeYPos, 0, r);
					WritePixelDataInternal(xpos, writeYPos, 1, g);
					WritePixelDataInternal(xpos, writeYPos, 2, b);
					WritePixelDataInternal(xpos, writeYPos, 3, a);
				}
			}

			result = true;
		}
		delete[] rawPixelData;
	}

	return result;
}

std::unique_ptr<Image> TifFormat::loadFromFile(const std::string &name, const Image::Optimization &optimization, const ColorSpace &color_space, float gamma)
{
#if defined(_WIN32)
	std::wstring wname = string::utf8ToWutf16Le(name);
	libtiff::TIFF *tif = libtiff::TIFFOpenW(wname.c_str(), "r");	//Windows needs the path in UTF16LE (unicode, UTF16, little endian) so we have to convert the UTF8 path to UTF16
#else
	libtiff::TIFF *tif = libtiff::TIFFOpen(name.c_str(), "r");
#endif
	if(!tif)
	{
		logger_.logError(getFormatName(), ": Cannot open file ", name);
		return nullptr;
	}
	logger_.logInfo(getFormatName(), ": Loading image \"", name, "\"...");
	uint32_t w, h;
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
	auto *tiff_data = static_cast<uint32_t *>(libtiff::_TIFFmalloc(w * h * sizeof(uint32_t)));
	if(!libtiff::TIFFReadRGBAImage(tif, w, h, tiff_data, 0))
	{
		logger_.logError(getFormatName(), ": Error reading TIFF file");
		return nullptr;
	}
	Image::Params image_params;
	image_params.width_ = static_cast<int>(w);
	image_params.height_ = static_cast<int>(h);
	image_params.type_ = Image::getTypeFromSettings(true, grayscale_);
	image_params.image_optimization_ = optimization;
	image_params.filename_ = name;
	auto image = Image::factory(image_params);
	int i = 0;
	for(int y = static_cast<int>(h) - 1; y >= 0; y--)
	{
		for(int x = 0; x < static_cast<int>(w); x++)
		{
			Rgba color;
			color.set((float)TIFFGetR(tiff_data[i]) * inv_max_8_bit_,
					  (float)TIFFGetG(tiff_data[i]) * inv_max_8_bit_,
					  (float)TIFFGetB(tiff_data[i]) * inv_max_8_bit_,
					  (float)TIFFGetA(tiff_data[i]) * inv_max_8_bit_);
			color.linearRgbFromColorSpace(color_space, gamma);
			image->setColor({{x, y}}, color);
			++i;
		}
	}
	libtiff::_TIFFfree(tiff_data);
	libtiff::TIFFClose(tif);
	if(logger_.isVerbose()) logger_.logVerbose(getFormatName(), ": Done.");
	return image;
}

RasterPage* GeoTiffPager::getPage(DataRequest *pOriginalRequest,
      DimensionDescriptor startRow, 
      DimensionDescriptor startColumn, 
      DimensionDescriptor startBand)
{
   if (pOriginalRequest == NULL)
   {
      return NULL;
   }

   if (pOriginalRequest->getWritable())
   {
      return NULL;
   }

   GeoTiffPage* pPage(NULL);

   // ensure only one thread enters this code at a time
   mta::MutexLock lock(mMutex);

   // we wrap all this in a try/catch so we can have one exit point
   // and ensure that the mutex gets unlocked on an error
   try
   {
      unsigned int concurrentRows = pOriginalRequest->getConcurrentRows();
      unsigned int concurrentColumns = pOriginalRequest->getConcurrentColumns();
      unsigned int concurrentBands = pOriginalRequest->getConcurrentBands();

      unsigned int rowNumber = startRow.getOnDiskNumber();
      unsigned int colNumber = startColumn.getOnDiskNumber();
      unsigned int bandNumber = startBand.getOnDiskNumber();

      // make sure the request is valid
      if ((rowNumber >= mRowCount) || ((rowNumber + concurrentRows) > mRowCount) ||
         (colNumber >= mColumnCount) || ((colNumber + concurrentColumns) > mColumnCount) ||
         (bandNumber >= mBandCount) || ((bandNumber + concurrentBands) > mBandCount))
      {
         // Since it is acceptable to increment off the end of the data, do not report an error message
         throw string();
      }

      if ((mInterleave == BSQ) && (concurrentBands != 1))
      {
         throw string("BSQ data can only be accessed one band at a time.");
      }

      /**
       * possibilities for load
       *
       * BIP with strips
       *     with tiles
       * BSQ with strips
       *     with tiles
       **/

      if (TIFFIsTiled(mpTiff) == 0)
      {
         // the data are stored as strips
         tsize_t stripSize(TIFFStripSize(mpTiff)); // columns * bands * rows in a strip (bands=1 for BSQ)
         if (stripSize <= 0)
         {
            throw string("TIFF file error. Strip size <= 0.");
         }

         // Load in a data block and create a GeoTiffPage for the data.
         // we do this reasonably efficiently by loading complete strips even
         // if that means loading more data than needed

         // how many strips should we load?
         // find the strip that our data block begins in
         // then find the strip that our concurrent data ends in
         tstrip_t startStrip(TIFFComputeStrip(mpTiff, rowNumber, static_cast<tsample_t>(bandNumber)));
         tstrip_t endStrip(TIFFComputeStrip(mpTiff, (rowNumber + concurrentRows) - 1, static_cast<tsample_t>(bandNumber)));

         // if this data block is already in the cache, retrieve it...otherwise create a new block
         GeoTiffOnDisk::CacheUnit* pCacheUnit(mBlockCache.getCacheUnit(startStrip, endStrip, stripSize));
         if (pCacheUnit == NULL)
         {
            throw string("Can't create a cache unit");
         }
         if (mInterleave == BIP)
         {
            float rowsPerStrip(static_cast<float>(stripSize / mColumnCount) / static_cast<float>(mBandCount) /
               static_cast<float>(mBytesPerElement));
            if (rowsPerStrip < 1.0)
            {
               pPage = new GeoTiffPage(pCacheUnit, 0 + (rowNumber * mBandCount * mBytesPerElement) +
                                                           (bandNumber * mBytesPerElement),
                                                           0, 0, 0);
            }
            else
            {
               const unsigned int uiRowsPerStrip = static_cast<unsigned int>(rowsPerStrip);
               const unsigned int numRowsOffset = rowNumber % uiRowsPerStrip;
               const unsigned int numRowsAvailable = (uiRowsPerStrip * (endStrip - startStrip + 1)) - numRowsOffset;
               pPage = new GeoTiffPage(pCacheUnit,
                           (numRowsOffset * mColumnCount * mBandCount * mBytesPerElement) +
                           (colNumber * mBandCount * mBytesPerElement) + (bandNumber * mBytesPerElement),
                           numRowsAvailable, 0, 0);
            }
         }
         else if (mInterleave == BSQ)
         {
            float rowsPerStrip(static_cast<float>(stripSize / mColumnCount) / static_cast<float>(mBytesPerElement));
            if (rowsPerStrip < 1.0)
            {
               pPage = new GeoTiffPage(pCacheUnit, 0 + colNumber * mBytesPerElement, 0, 0, 0);
            }
            else
            {
               const unsigned int uiRowsPerStrip = static_cast<unsigned int>(rowsPerStrip);
               const unsigned int numRowsOffset = rowNumber % uiRowsPerStrip;
               const unsigned int numRowsAvailable = (uiRowsPerStrip * (endStrip - startStrip + 1)) - numRowsOffset;
               pPage = new GeoTiffPage(pCacheUnit,
                  (numRowsOffset * mColumnCount * mBytesPerElement) + (colNumber * mBytesPerElement),
                  numRowsAvailable, 0, 0);
            }
         }

         if (pPage == NULL)
         {
            throw string("Can't create a data block");
         }
         if (pCacheUnit->isEmpty())
         {
            // we need to load this block
            char* pData(pCacheUnit->data());
            if (pData == NULL)
            {
               throw string("Data block has no data");
            }
            // read in the strips
            char* pBlockPos(pData);
            for (tstrip_t curStrip = startStrip; curStrip <= endStrip; curStrip++)
            {
               tsize_t bytesRead = TIFFReadEncodedStrip(mpTiff, curStrip, pBlockPos, -1);
               if (bytesRead == -1)
               {
                  throw string("Error reading TIFF data");
               }
               pBlockPos += bytesRead;
            }

            pCacheUnit->setIsEmpty(false);
         }
      }
      else
      {
         // The number of pixels in each tile from left to right
         uint32 tileWidth;
         if (TIFFGetField(mpTiff, TIFFTAG_TILEWIDTH, &tileWidth) == 0 || tileWidth == 0)
         {
            throw string("Cannot determine tileWidth");
         }

         // The number of pixels in each tile from top to bottom
         uint32 tileLength;
         if (TIFFGetField(mpTiff, TIFFTAG_TILELENGTH, &tileLength) == 0 || tileLength == 0)
         {
            throw string("Cannot determine tileLength");
         }

         // The number of bytes in each tile
         const tsize_t tileSize(TIFFTileSize(mpTiff));
         if (tileSize <= 0)
         {
            throw string("Cannot determine tileSize");
         }

         // Compute the first and last tile to load.
         // This should ideally use TIFFComputeTile, but there are problems
         // in that method (as of libtiff 3.8.1) so compute them manually here.

         // The number of tiles from left to right
         const uint32 tilesAcross((mColumnCount + tileWidth - 1) / tileWidth);
         if (tilesAcross == 0)
         {
            throw string("Cannot determine tilesAcross");
         }

         // The number of tiles from top to bottom
         const uint32 tilesDown((mRowCount + tileLength - 1) / tileLength);
         if (tilesDown == 0)
         {
            throw string("Cannot determine tilesDown");
         }

         // The offset to the first tile in the requested band
         // BIP: Tiles contain all bands, so this is 0
         // BSQ: Tiles contain a single band, so this is number of tiles per band * bandNumber
         // This is stated in the TIFF 6.0 spec on page 68 (in the TileOffsets definition)
         const uint32 tileOffset(mInterleave == BIP ? 0 : bandNumber * tilesAcross * tilesDown);

         // The 0-based index of the first desired tile
         const ttile_t startTileIndex(rowNumber / tileLength);

         // The tile containing the first column of the first requested row
         const ttile_t startTile(tileOffset + startTileIndex * tilesAcross);
         if (startTile < 0)
         {
            throw string("Cannot determine startTile");
         }

         // The 0-based index of the last desired tile
         const ttile_t endTileIndex((rowNumber + concurrentRows - 1) / tileLength);

         // The tile containing the last column of the last requested row
         const ttile_t endTile(tileOffset + endTileIndex * tilesAcross + tilesAcross - 1);
         if (endTile < 0)
         {
            throw string("Cannot determine endTile");
         }

         // Retrieve a block from the cache
         GeoTiffOnDisk::CacheUnit* pCacheUnit(mBlockCache.getCacheUnit(startTile, endTile, tileSize));
         if (pCacheUnit == NULL)
         {
            throw string("Cannot create a cache unit");
         }

         // The number of bands in pPage
         const unsigned int bandSkip(mInterleave == BIP ? mBandCount : 1);

         // The number of columns in pPage
         const unsigned int columnSkip(mColumnCount);

         // The offset of the first requested data within pPage
         const size_t offset(mBytesPerElement *
            ((rowNumber % tileLength) * columnSkip * bandSkip + (mInterleave == BSQ ? 0 : bandNumber)));

         // The number of rows in pPage
         const unsigned int rowSkip(tileLength * ((endTile - startTile + 1) / tilesAcross));

         // Create a GeoTiffPage based on the computed values
         pPage = new GeoTiffPage(pCacheUnit, offset, rowSkip, columnSkip, bandSkip);
         if (pCacheUnit->isEmpty())
         {
            // Temporary storage for the working tile
            vector<unsigned char> tileData(tileSize);
            for (ttile_t curTile = startTile, tileNum = 0; curTile <= endTile; ++curTile, ++tileNum)
            {
               if (TIFFReadEncodedTile(mpTiff, curTile, &tileData[0], tileSize) != tileSize)
               {
                  throw string("Error reading TIFF data");
               }

               // The starting address of this tile within pPage
               char* pBlockPos(pCacheUnit->data());

               // Increment by one or more rows of tiles
               pBlockPos += tileSize * tilesAcross * bandSkip * mBytesPerElement * (tileNum / tilesAcross);

               // Increment by one or more tiles within a row
               pBlockPos += tileWidth * bandSkip * mBytesPerElement * (tileNum % tilesAcross);

               // The number of bytes to copy - this might be different for partial tiles (e.g.: at the end of a row)
               size_t numBytesToCopy = tileWidth;
               if ((tileNum + 1) % tilesAcross == 0 && mColumnCount % tileWidth != 0)
               {
                  numBytesToCopy = mColumnCount % tileWidth;
               }

               numBytesToCopy *= bandSkip * mBytesPerElement;
               for (uint32 row = 0; row < tileLength; ++row)
               {
                  const size_t rowOffset = row * mColumnCount * bandSkip * mBytesPerElement;
                  const size_t tileOffset2 = row * tileWidth * bandSkip * mBytesPerElement;
                  memcpy(pBlockPos + rowOffset, &tileData[tileOffset2], numBytesToCopy);
               }
            }

            pCacheUnit->setIsEmpty(false);
         }
      }
   }
   catch (const string& exc)
   {
      delete pPage;
      pPage = NULL;
      if (exc.empty() == false)
      {
         MessageResource pMsg("GeoTIFF Pager Error", "app", "71B84E03-6AF1-4971-9F83-69E3C8BC6BF3");
         pMsg->addProperty("Message", exc);
      }
   }

   return pPage;
}

bool populateStaticTagMetadata(TIFF* pTiffFile, ttag_t tag,
      const uint32 count, DynamicObject* pMetadata, string& message)
   {
      VERIFY(pTiffFile != NULL && count > 0 && pMetadata != NULL);
      const TIFFField* pFieldInfo = TIFFFieldWithTag(pTiffFile, tag);
      VERIFY(pFieldInfo != NULL);

      vector<T> values(count);
      if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), &values[0]) == 0)
      {
         // The tag was not present in the file
         return false;
      }

      // Must call the templated version because some fields are internally inconsistent within libtiff
      // e.g.: TIFFTAG_BITSPERSAMPLE can be TIFF_LONG according to tif_dirinfo.c yet is used as a uint16 in tif_dir.c
      // While it is inefficient to make multiple copies of the vector, this code is only reached by static,
      // non-pointer tags which typically have no more than 2 elements.
      if (setMetadata<T>(TIFFFieldName(pFieldInfo), count, &values[0], pMetadata) == false)
      {
         message += "Error processing " + string(TIFFFieldName(pFieldInfo)) + "\n";
         return false;
      }

      return true;
   }

bool populateStaticTagMetadataArray1D(TIFF* pTiffFile, ttag_t tag,
      uint32 count, DynamicObject* pMetadata, string& message)
   {
      VERIFY(pTiffFile != NULL && pMetadata != NULL);

      const TIFFField* pFieldInfo = TIFFFieldWithTag(pTiffFile, tag);
      VERIFY(pFieldInfo != NULL);

      T* pValue = NULL;
      if (count == 0)
      {
         // Special case
         // The only fields that need this are TIFFTAG_EXTRASAMPLES and TIFFTAG_SUBIFD (tif_dir.c:747, 782)
         // Both of these fields use a uint16 to store the count, so create a temporary uint16
         uint16 count16 = 0;
         if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), &count16, &pValue) == 0)
         {
            // The tag was not present in the file
            return false;
         }

         count = static_cast<uint32>(count16);
      }
      else
      {
         if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), &pValue) == 0)
         {
            // The tag was not present in the file
            return false;
         }
      }

      VERIFY(pValue != NULL && count > 0);

      // Must call the templated version because values must be copied into a vector to be stored in a DataVariant
      if (setMetadata<T>(TIFFFieldName(pFieldInfo), count, pValue, pMetadata) == false)
      {
         message += "Error processing " + string(TIFFFieldName(pFieldInfo)) + "\n";
         return false;
      }

      return true;
   }

bool populateStaticTagMetadataArray2D(TIFF* pTiffFile, ttag_t tag,
      const uint32 count, DynamicObject* pMetadata, string& message)
   {
      VERIFY(pTiffFile != NULL && count != 0 && pMetadata != NULL);
      const TIFFField* pFieldInfo = TIFFFieldWithTag(pTiffFile, tag);
      VERIFY(pFieldInfo != NULL);

      // TIFFTAG_COLORMAP and TIFFTAG_TRANSFERFUNCTION have up to 3 children
      vector<T*> values(3, NULL);
      if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), &values[0], &values[1], &values[2]) == 0)
      {
         // The tag was not present in the file
         return false;
      }

      for (uint32 i = 0; i < values.size() && values[i] != NULL; ++i)
      {
         // Must call the templated version because values must be copied into a vector to be stored in a DataVariant
         if (setMetadata<T>((QString(TIFFFieldName(pFieldInfo)) + QString("-%1").arg(i)).toStdString(),
            count, values[i], pMetadata) == false)
         {
            message += "Error processing " + string(TIFFFieldName(pFieldInfo)) + "\n";
            return false;
         }
      }

      return true;
   }

bool populateCustomTagMetadata(TIFF* pTiffFile, ttag_t tag, DynamicObject* pMetadata, string& message)
   {
      VERIFY(pTiffFile != NULL && pMetadata != NULL);

      const TIFFField* pFieldInfo = TIFFFieldWithTag(pTiffFile, tag);
      VERIFY(pFieldInfo != NULL);

      uint32 count = 0;
      void* pValues = NULL;
      if (TIFFFieldPassCount(pFieldInfo))
      {
         // Special case
         // Use either a uint16* or uint32* based on field_readcount as is done in tif_dir.c:837
         void* pCount = &count;
         uint16 count16 = 0;
         if (TIFFFieldReadCount(pFieldInfo) != TIFF_VARIABLE2)
         {
            pCount = &count16;
         }

         if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), pCount, &pValues) == 0)
         {
            // The tag was not present in the file even though libtiff earlier said it was present
            message += "Unable to retrieve information for " + string(TIFFFieldName(pFieldInfo)) + "\n";
            return false;
         }

         if (TIFFFieldReadCount(pFieldInfo) != TIFF_VARIABLE2)
         {
            count = count16;
         }
      }
      else
      {
         // Special case
         // TIFFTAG_DOTRANGE requires either 2 or N arguments
         // Since it cannot be known how many arguments to send, display a warning
         if (TIFFFieldTag(pFieldInfo) == TIFFTAG_DOTRANGE)
         {
            message += "Custom field " + string(TIFFFieldName(pFieldInfo)) + " is not supported.\n";
            return false;
         }

         // The data types in this switch must match the ones in tif_dir.c:861
         switch (TIFFFieldDataType(pFieldInfo))
         {
            case TIFF_ASCII:
            {
               // Special case
               // Make this appear as a string rather than a vector<char>
               count = 1;
               if (TIFFGetField(pTiffFile, TIFFFieldTag(pFieldInfo), &pValues) == 0)
               {
                  return false;
               }

               break;
            }

            case TIFF_BYTE:   // Fall through
            case TIFF_UNDEFINED:
            {
               return populateStaticTagMetadata<uint8>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_SBYTE:
            {
               return populateStaticTagMetadata<int8>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_SHORT:
            {
               return populateStaticTagMetadata<uint16>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_SSHORT:
            {
               return populateStaticTagMetadata<int16>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_LONG:   // Fall through
            case TIFF_IFD:
            {
               return populateStaticTagMetadata<uint32>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_SLONG:
            {
               return populateStaticTagMetadata<int32>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_RATIONAL:  // Fall through
            case TIFF_SRATIONAL: // Fall through
            case TIFF_FLOAT:
            {
               return populateStaticTagMetadata<float>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            case TIFF_DOUBLE:
            {
               return populateStaticTagMetadata<double>(pTiffFile, tag,
                   TIFFFieldReadCount(pFieldInfo), pMetadata, message);
            }

            default:
            {
               message += "Invalid tag type for custom field " + string(TIFFFieldName(pFieldInfo)) + "\n";
               return false;
            }
         }
      }

      if (setMetadata(pFieldInfo, count, pValues, pMetadata) == false)
      {
         message += "Error processing custom field " + string(TIFFFieldName(pFieldInfo)) + "\n";
         return false;
      }

      return true;
   }

void populateTiffMetadata(TIFF* pTiffFile, DynamicObject* pMetadata, string& message)
   {
      message.clear();
      if (pTiffFile == NULL || pMetadata == NULL)
      {
         message += "Cannot populate metadata.\n";
         return;
      }

      // Static tags - these MUST match those called out in tif_dir.c:657-800
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_SUBFILETYPE, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_IMAGEWIDTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_IMAGELENGTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_BITSPERSAMPLE, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_COMPRESSION, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_PHOTOMETRIC, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_THRESHHOLDING, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_FILLORDER, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_ORIENTATION, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_SAMPLESPERPIXEL, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_ROWSPERSTRIP, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_MINSAMPLEVALUE, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_MAXSAMPLEVALUE, 1, pMetadata, message);
      populateStaticTagMetadata<double>(pTiffFile, TIFFTAG_SMINSAMPLEVALUE, 1, pMetadata, message);
      populateStaticTagMetadata<double>(pTiffFile, TIFFTAG_SMAXSAMPLEVALUE, 1, pMetadata, message);
      populateStaticTagMetadata<float>(pTiffFile, TIFFTAG_XRESOLUTION, 1, pMetadata, message);
      populateStaticTagMetadata<float>(pTiffFile, TIFFTAG_YRESOLUTION, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_PLANARCONFIG, 1, pMetadata, message);
      populateStaticTagMetadata<float>(pTiffFile, TIFFTAG_XPOSITION, 1, pMetadata, message);
      populateStaticTagMetadata<float>(pTiffFile, TIFFTAG_YPOSITION, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_RESOLUTIONUNIT, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_PAGENUMBER, 2, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_HALFTONEHINTS, 2, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_MATTEING, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_TILEWIDTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_TILELENGTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_TILEDEPTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_DATATYPE, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_SAMPLEFORMAT, 1, pMetadata, message);
      populateStaticTagMetadata<uint32>(pTiffFile, TIFFTAG_IMAGEDEPTH, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_YCBCRPOSITIONING, 1, pMetadata, message);
      populateStaticTagMetadata<uint16>(pTiffFile, TIFFTAG_YCBCRSUBSAMPLING, 2, pMetadata, message);

      // More static tags - each of these tags represents a special case in tif_dir.c:657-800

      // Special case
      // Either strips or tiles will be present, but not both
      // In either case, the number is obtained by calling the appropriate TIFFNumberOf function
      if (TIFFIsTiled(pTiffFile) == 0)
      {
         // Strips
         const tstrip_t numStrips(TIFFNumberOfStrips(pTiffFile));
         populateStaticTagMetadataArray1D<uint32>(pTiffFile, TIFFTAG_STRIPOFFSETS, numStrips, pMetadata, message);
         populateStaticTagMetadataArray1D<uint32>(pTiffFile, TIFFTAG_STRIPBYTECOUNTS, numStrips, pMetadata, message);
      }
      else
      {
         // Tiles
         const ttile_t numTiles(TIFFNumberOfTiles(pTiffFile));
         populateStaticTagMetadataArray1D<uint32>(pTiffFile, TIFFTAG_TILEOFFSETS, numTiles, pMetadata, message);
         populateStaticTagMetadataArray1D<uint32>(pTiffFile, TIFFTAG_TILEBYTECOUNTS, numTiles, pMetadata, message);
      }

      // Special case
      // Counts for TIFFTAG_EXTRASAMPLES and TIFFTAG_SUBIFD are obtained by calling TIFFGetField
      populateStaticTagMetadataArray1D<uint16>(pTiffFile, TIFFTAG_EXTRASAMPLES, 0, pMetadata, message);
      populateStaticTagMetadataArray1D<uint32>(pTiffFile, TIFFTAG_SUBIFD, 0, pMetadata, message);

      // Special case
      // TIFFTAG_COLORMAP and TIFFTAG_TRANSFERFUNCTION are uint16** with either 1 or 3 entries
      uint16 bitsPerElement = 0;
      TIFFGetField(pTiffFile, TIFFTAG_BITSPERSAMPLE, &bitsPerElement);
      populateStaticTagMetadataArray2D<uint16>(pTiffFile, TIFFTAG_COLORMAP,
         static_cast<uint32>(1 << bitsPerElement), pMetadata, message);

      populateStaticTagMetadataArray2D<uint16>(pTiffFile, TIFFTAG_TRANSFERFUNCTION, 
         static_cast<uint32>(1 << bitsPerElement), pMetadata, message);

      // Special case
      // TIFFTAG_REFERENCEBLACKWHITE is float* with exactly 6 entries
      populateStaticTagMetadataArray1D<float>(pTiffFile, TIFFTAG_REFERENCEBLACKWHITE, 6, pMetadata, message);

      // Special case
      // TIFFTAG_INKNAMES is a char** to be read in as multiple NULL-terminated strings
      uint16 numberOfInks = 0;
      TIFFGetField(pTiffFile, TIFFTAG_NUMBEROFINKS, &numberOfInks);
      if (numberOfInks > 0)
      {
         if (numberOfInks > 1)
         {
            message += QString("%1 InkNames defined. Only the first will be read.\n").arg(numberOfInks).toStdString();
            numberOfInks = 1;
         }

         populateStaticTagMetadataArray1D<char>(pTiffFile, TIFFTAG_INKNAMES, numberOfInks, pMetadata, message);
      }

      // Iterate over each custom tag, placing it into the metadata
      const int numTags = TIFFGetTagListCount(pTiffFile);
      for (int i = 0; i < numTags; ++i)
      {
         populateCustomTagMetadata(pTiffFile, TIFFGetTagListEntry(pTiffFile, i), pMetadata, message);
      }
   }

bool GeoTIFFImporter::populateDataDescriptor(RasterDataDescriptor* pDescriptor)
{
   if (pDescriptor == NULL)
   {
      return false;
   }

   RasterFileDescriptor* pFileDescriptor = dynamic_cast<RasterFileDescriptor*>
      (pDescriptor->getFileDescriptor());
   if (pFileDescriptor == NULL)
   {
      return false;
   }

   const string& filename = pFileDescriptor->getFilename();
   if (filename.empty() == true)
   {
      return false;
   }

   {
      // Check the first four bytes for TIFF magic number

      //force file to be closed when scope block ends
      FileResource pFile(filename.c_str(), "r");
      if (pFile.get() != NULL)
      {
         const unsigned short tiffBigEndianMagicNumber = 0x4d4d;
         const unsigned short tiffLittleEndianMagicNumber = 0x4949;
         const unsigned short tiffVersionMagicNumber = 42;

         unsigned short fileEndian;
         fread(&fileEndian, sizeof(fileEndian), 1, pFile);

         if ( (fileEndian == tiffBigEndianMagicNumber) || (fileEndian == tiffLittleEndianMagicNumber) )
         {
            unsigned short tiffVersion;
            fread(&tiffVersion, sizeof(tiffVersion), 1, pFile);

            EndianType fileEndianType = (fileEndian == tiffBigEndianMagicNumber ? BIG_ENDIAN_ORDER : LITTLE_ENDIAN_ORDER);
            Endian swapper(fileEndianType);
            swapper.swapBuffer(&tiffVersion, 1);

            if (tiffVersion != tiffVersionMagicNumber)
            {
               return false;
            }
            pFileDescriptor->setEndian(fileEndianType);
         }
         else
         {
            return false;
         }
      }
   }

   TIFF* pTiffFile = XTIFFOpen(filename.c_str(), "r");
   if (pTiffFile == NULL)
   {
      return false;
   }

   // Metadata
   DynamicObject* pMetadata = pDescriptor->getMetadata();
   if (pMetadata != NULL)
   {
      // TIFF tags
      string message;
      populateTiffMetadata(pTiffFile, pDescriptor->getMetadata(), message);
      if (message.empty() == false)
      {
         const string& rasterName = pDescriptor->getName();
         mMetadataMessages[rasterName] = message;
      }

      // ISD metadata
      QuickbirdIsd isd(pMetadata);

      QString isdFilename = isd.getIsdFilename();
      if (isdFilename.isEmpty() == true)
      {
         // An ISD filename has not yet been set, so check for an existing file based on the raster filename
         const FileDescriptor* pFileDescriptor2 = pDescriptor->getFileDescriptor();
         if (pFileDescriptor2 != NULL)
         {
            QFileInfo tiffInfo(QString::fromStdString(pFileDescriptor2->getFilename().getFullPathAndName()));
            QFileInfo isdInfo(tiffInfo.absolutePath() + "/" + tiffInfo.completeBaseName() + ".xml");
            if (isdInfo.exists() == true)
            {
               isdFilename = isdInfo.absoluteFilePath();
            }
         }
      }

      isd.loadIsdMetadata(isdFilename);
   }

   // Check for unsupported palette data
   unsigned short photometric = 0;
   TIFFGetField(pTiffFile, TIFFTAG_PHOTOMETRIC, &photometric);

   if (photometric == PHOTOMETRIC_PALETTE)
   {
      XTIFFClose(pTiffFile);
      return false;
   }

   // Rows
   unsigned int numRows = 0;
   TIFFGetField(pTiffFile, TIFFTAG_IMAGELENGTH, &numRows);

   vector<DimensionDescriptor> rows = RasterUtilities::generateDimensionVector(numRows, true, false, true);
   pDescriptor->setRows(rows);
   pFileDescriptor->setRows(rows);

   // Columns
   unsigned int numColumns = 0;
   TIFFGetField(pTiffFile, TIFFTAG_IMAGEWIDTH, &numColumns);

   vector<DimensionDescriptor> columns = RasterUtilities::generateDimensionVector(numColumns, true, false, true);

   pDescriptor->setColumns(columns);
   pFileDescriptor->setColumns(columns);

   // Bands
   unsigned short numBands = 1;
   TIFFGetField(pTiffFile, TIFFTAG_SAMPLESPERPIXEL, &numBands);

   vector<DimensionDescriptor> bands = RasterUtilities::generateDimensionVector(numBands, true, false, true);

   pDescriptor->setBands(bands);
   pFileDescriptor->setBands(bands);

   // Bits per pixel
   unsigned short bitsPerElement = 0;
   TIFFGetField(pTiffFile, TIFFTAG_BITSPERSAMPLE, &bitsPerElement);

   pFileDescriptor->setBitsPerElement(bitsPerElement);

   // Data type
   unsigned short sampleFormat = SAMPLEFORMAT_VOID;
   TIFFGetField(pTiffFile, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

   EncodingType dataType = INT1UBYTE;

   unsigned int bytesPerElement = bitsPerElement / 8;
   switch (bytesPerElement)
   {
      case 1:
         if (sampleFormat == SAMPLEFORMAT_INT)
         {
            dataType = INT1SBYTE;
         }
         else
         {
            dataType = INT1UBYTE;
         }
         break;

      case 2:
         if (sampleFormat == SAMPLEFORMAT_INT)
         {
            dataType = INT2SBYTES;
         }
         else
         {
            dataType = INT2UBYTES;
         }
         break;

      case 4:
         if (sampleFormat == SAMPLEFORMAT_INT)
         {
            dataType = INT4SBYTES;
         }
         else if (sampleFormat == SAMPLEFORMAT_IEEEFP)
         {
            dataType = FLT4BYTES;
         }
         else
         {
            dataType = INT4UBYTES;
         }
         break;

      case 8:
         dataType = FLT8BYTES;
         break;

      default:
         break;
   }

   pDescriptor->setDataType(dataType);
   pDescriptor->setValidDataTypes(vector<EncodingType>(1, dataType));

   // Interleave format
   unsigned short planarConfig = 0;
   TIFFGetField(pTiffFile, TIFFTAG_PLANARCONFIG, &planarConfig);

   if (planarConfig == PLANARCONFIG_SEPARATE)
   {
      pFileDescriptor->setInterleaveFormat(BSQ);
   }
   else if (planarConfig == PLANARCONFIG_CONTIG)
   {
      pFileDescriptor->setInterleaveFormat(BIP);
   }

   pDescriptor->setInterleaveFormat(BIP);

   // Bad values
   if ((dataType != FLT4BYTES) && (dataType != FLT8COMPLEX) && (dataType != FLT8BYTES))
   {
      vector<int> badValues(1);
      badValues[0] = 0;

      pDescriptor->setBadValues(badValues);
   }

   // Latitude/longitude GCPs
   GTIF* pGeoTiff = GTIFNew(pTiffFile);

   GTIFDefn defn;
   GTIFGetDefn(pGeoTiff, &defn);

   char* pProj4Defn = GTIFGetProj4Defn(&defn);
   if (pProj4Defn != NULL)
   {
      free(pProj4Defn);
      list<GcpPoint> gcps;

      // The pixel coordinate system for GeoTIFF is defined as referring to the
      // top-left corner of the pixel, regardless of whether the format is set
      // as PixelIsArea or PixelIsPoint.  More on the issue can be read at the
      // GeoTIFF FAQ: http://remotesensing.org/geotiff/faq.html#PixelIsPoint

      // Upper left
      double x = 0;
      double y = 0;
      if (GTIFImageToPCS(pGeoTiff, &x, &y) &&
         (defn.Model == ModelTypeGeographic || GTIFProj4ToLatLong(&defn, 1, &x, &y)))
      {
         GcpPoint gcp;
         gcp.mPixel.mX = 0.0;
         gcp.mPixel.mY = 0.0;
         gcp.mCoordinate.mX = y;
         gcp.mCoordinate.mY = x;
         gcps.push_back(gcp);
      }

      // Lower left
      x = 0;
      y = numRows - 1;
      if (GTIFImageToPCS(pGeoTiff, &x, &y) &&
         (defn.Model == ModelTypeGeographic || GTIFProj4ToLatLong(&defn, 1, &x, &y)))
      {
         GcpPoint gcp;
         gcp.mPixel.mX = 0.0;
         gcp.mPixel.mY = numRows - 1.0;
         gcp.mCoordinate.mX = y;
         gcp.mCoordinate.mY = x;
         gcps.push_back(gcp);
      }

      // Upper right
      x = numColumns - 1;
      y = 0;
      if (GTIFImageToPCS(pGeoTiff, &x, &y) &&
         (defn.Model == ModelTypeGeographic || GTIFProj4ToLatLong(&defn, 1, &x, &y)))
      {
         GcpPoint gcp;
         gcp.mPixel.mX = numColumns - 1.0;
         gcp.mPixel.mY = 0.0;
         gcp.mCoordinate.mX = y;
         gcp.mCoordinate.mY = x;
         gcps.push_back(gcp);
      }

      // Lower right
      x = numColumns - 1;
      y = numRows - 1;
      if (GTIFImageToPCS(pGeoTiff, &x, &y) &&
         (defn.Model == ModelTypeGeographic || GTIFProj4ToLatLong(&defn, 1, &x, &y)))
      {
         GcpPoint gcp;
         gcp.mPixel.mX = numColumns - 1.0;
         gcp.mPixel.mY = numRows - 1.0;
         gcp.mCoordinate.mX = y;
         gcp.mCoordinate.mY = x;
         gcps.push_back(gcp);
      }

      // Center
      x = (numColumns - 1) / 2;
      y = (numRows - 1) / 2;
      if (GTIFImageToPCS(pGeoTiff, &x, &y) &&
         (defn.Model == ModelTypeGeographic || GTIFProj4ToLatLong(&defn, 1, &x, &y)))
      {
         GcpPoint gcp;
         // pixels in GeoTIFF refer to top-left corner of the rastered pixel, so floor the center
         gcp.mPixel.mX = floor((numColumns - 1.0) / 2.0);
         gcp.mPixel.mY = floor((numRows - 1.0) / 2.0);
         gcp.mCoordinate.mX = y;
         gcp.mCoordinate.mY = x;
         gcps.push_back(gcp);
      }

      if (gcps.empty() == false)
      {
         pFileDescriptor->setGcps(gcps);
      }
   }

   // Close the TIFF file
   XTIFFClose(pTiffFile);

   return true;
}

bool TIFFImage::initializeType(const std::string& imagePath) {
  std::unique_lock<std::shared_mutex> l(*_openCloseMutex);
  cleanup();

#ifdef _WIN32
  int wchars_num = MultiByteToWideChar(CP_UTF8, 0, imagePath.c_str(), -1, NULL, 0);
  wchar_t* w_imagePath = new wchar_t[wchars_num];
  MultiByteToWideChar(CP_UTF8, 0, imagePath.c_str(), -1, w_imagePath, wchars_num);
  _tiff = TIFFOpenW(w_imagePath, "rm");
  delete[] w_imagePath;
#else
  _tiff = TIFFOpen(imagePath.c_str(), "rm");
#endif

  if (_tiff) {
    const char* img_desc = NULL;

    if (TIFFGetFieldDefaulted(_tiff, TIFFTAG_IMAGEDESCRIPTION, &img_desc)) {
      std::string img_desc_string(img_desc);
      if (img_desc_string.find("DICOM") != std::string::npos || img_desc_string.find("xml") != std::string::npos || img_desc_string.find("XML") != std::string::npos) {
        // Not one of our TIFFs
        cleanup();
        return false;
      }
    }

    if (TIFFIsTiled(_tiff) == 0) {
      cleanup();
      return false;
    }
    unsigned int codec = 0;
    TIFFGetField(_tiff, TIFFTAG_COMPRESSION, &codec);
    if (codec != 33005 && codec != COMPRESSION_DEFLATE && codec != COMPRESSION_JPEG && codec != COMPRESSION_LZW && codec != COMPRESSION_NONE) {
      cleanup();
      return false;
    }

    unsigned int cType = 0, dType = 0, planarconfig = 0, bitsPerSample = 0;
    TIFFGetField(_tiff, TIFFTAG_PHOTOMETRIC, &cType);

    // TIFFTAG_SAMPLEFORMAT is not always present, when it is not the TIFF
    // standard indicates that data should be interpreted as unsigned int
    if (TIFFGetField(_tiff, TIFFTAG_SAMPLEFORMAT, &dType) == 0 && dType == 0) {
      dType = 1;
    }

    TIFFGetField(_tiff, TIFFTAG_SAMPLESPERPIXEL, &_samplesPerPixel);
    TIFFGetField(_tiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
    TIFFGetField(_tiff, TIFFTAG_PLANARCONFIG, &planarconfig);

    if (planarconfig != PLANARCONFIG_CONTIG) {
      cleanup();
    }
    if (dType != SAMPLEFORMAT_IEEEFP && dType != SAMPLEFORMAT_UINT) {
      cleanup();
    }
    if (cType != PHOTOMETRIC_MINISBLACK && cType != PHOTOMETRIC_RGB && cType != PHOTOMETRIC_YCBCR) {
      cleanup();
    }
    if (cType == PHOTOMETRIC_RGB && _samplesPerPixel != 3 && _samplesPerPixel != 4) {
      cleanup();
    }
    if (dType == SAMPLEFORMAT_IEEEFP && bitsPerSample != 32) {
      cleanup();
    }
    if (dType == SAMPLEFORMAT_UINT && bitsPerSample != 32 && bitsPerSample != 16 && bitsPerSample != 8) {
      cleanup();
    }
    if (!_tiff) {
      return false;
    }

    _numberOfLevels = TIFFNumberOfDirectories(_tiff);

    if (_numberOfLevels < 1) {
      cleanup();
      return false;
    }

    // Check if the multi-resolution pyramid is stored nicely
    TIFFSetDirectory(_tiff, 0);
    unsigned int width = 0;
    TIFFGetField(_tiff, TIFFTAG_IMAGEWIDTH, &width);
    unsigned int x = 0, y = 0;
    unsigned int tileW = 0, tileH = 0;
    unsigned int dir = 0;
    for (int level = 0; level < TIFFNumberOfDirectories(_tiff); ++level) {
      TIFFSetDirectory(_tiff, level);
      if (TIFFIsTiled(_tiff) == 1) {
        TIFFGetField(_tiff, TIFFTAG_IMAGEWIDTH, &x);
        TIFFGetField(_tiff, TIFFTAG_IMAGELENGTH, &y);
        TIFFGetField(_tiff, TIFFTAG_TILEWIDTH, &tileW);
        TIFFGetField(_tiff, TIFFTAG_TILELENGTH, &tileH);
        std::vector<unsigned long long> tmp;
        std::vector<unsigned int> levelTileSize;
        tmp.push_back(x);
        tmp.push_back(y);
        levelTileSize.push_back(tileW);
        levelTileSize.push_back(tileH);
        _levelDimensions.push_back(tmp);
        _tileSizesPerLevel.push_back(levelTileSize);
        if (level > 0) {
          if (width > x) {
            width = x;
          }
          else {
            cleanup();
            return false;
          }
        }
      }
      else {
        _numberOfLevels -= 1;
      }
    }

    TIFFSetDirectory(_tiff, 0);
    if (dType == SAMPLEFORMAT_IEEEFP) {
      _dataType = DataType::Float;
    }
    else if (dType == SAMPLEFORMAT_UINT) {
      if (bitsPerSample == 8) {
        _dataType = DataType::UChar;
      }
      else if (bitsPerSample == 16) {
        _dataType = DataType::UInt16;
      }
      else if (bitsPerSample == 32) {
        _dataType = DataType::UInt32;
      }
      else {
        _dataType = DataType::InvalidDataType;
      }
    }

    if (cType == PHOTOMETRIC_MINISBLACK) {
      if (_samplesPerPixel > 1) {
        _colorType = ColorType::Indexed;
      }
      else {
        _colorType = ColorType::Monochrome;
      }
    }
    else if (cType == PHOTOMETRIC_RGB) {
      if (_samplesPerPixel == 3) {
        _colorType = ColorType::RGB;
      }
      else if (_samplesPerPixel == 4) {
        _colorType = ColorType::RGBA;
      }
      else {
        _colorType = ColorType::InvalidColorType;
      }
    }
    else if (cType == PHOTOMETRIC_YCBCR && codec == COMPRESSION_JPEG) {
      if (_samplesPerPixel == 3) {
        _colorType = ColorType::RGB;
      }
      else if (_samplesPerPixel == 4) {
        _colorType = ColorType::RGBA;
      }
      else {
        _colorType = ColorType::InvalidColorType;
      }
    }

    float spacingX;
    float spacingY;
    if (TIFFGetField(_tiff, TIFFTAG_XRESOLUTION, &spacingX) == 1) {
      _spacing.push_back(1. / (spacingX / (10000.)));
    }
    if (TIFFGetField(_tiff, TIFFTAG_YRESOLUTION, &spacingY) == 1) {
      _spacing.push_back(1. / (spacingY / (10000.)));
    }

    TIFFSetField(_tiff, TIFFTAG_PERSAMPLE, PERSAMPLE_MULTI);
    double* min_values;
    if (TIFFGetField(_tiff, TIFFTAG_SMINSAMPLEVALUE, &min_values)) {
      for (unsigned int i = 0; i < _samplesPerPixel; ++i) {
        _minValues.push_back(min_values[i]);
      }
    }
    else {
      for (unsigned int i = 0; i < _samplesPerPixel; ++i) {
        _minValues.push_back(0.);
      }
    }
    double* max_values;
    if (TIFFGetField(_tiff, TIFFTAG_SMAXSAMPLEVALUE, &max_values)) {
      for (unsigned int i = 0; i < _samplesPerPixel; ++i) {
        _maxValues.push_back(max_values[i]);
      }
    }
    else {
      for (unsigned int i = 0; i < _samplesPerPixel; ++i) {
        _maxValues.push_back(255.);
      }
    }
    TIFFSetField(_tiff, TIFFTAG_PERSAMPLE, PERSAMPLE_MERGED);

    _fileType = "tif";
    _isValid = true;
  }
  else {
    cleanup();
    return false;
  }

  if (_dataType == DataType::UInt32) {
    createCache<unsigned int>();
  }
  else if (_dataType == DataType::UInt16) {
    createCache<unsigned short>();
  }
  else if (_dataType == DataType::Float) {
    createCache<float>();
  }
  else if (_dataType == DataType::UChar) {
    createCache<unsigned char>();
  }
  return _isValid;
}

long long TIFFImage::getEncodedTileSize(const long long& startX, const long long& startY, const unsigned int& level) {
  if (_tiff && level < this->_numberOfLevels) {
    long long levelStartX = std::floor(startX / getLevelDownsample(level) + 0.5);
    long long levelStartY = std::floor(startY / getLevelDownsample(level) + 0.5);
    TIFFSetDirectory(_tiff, level);
    unsigned int tileNr = TIFFComputeTile(_tiff, levelStartX, levelStartY, 0, 0);
    unsigned int total_tiles = TIFFNumberOfTiles(_tiff);
    if (tileNr < total_tiles) {
      unsigned long long* tbc = NULL;
      TIFFGetField(_tiff, TIFFTAG_TILEBYTECOUNTS, &tbc);
      unsigned long long k = tbc[tileNr];
      if (k == 0) {
        return -1;
      }
      unsigned int count = 0;
      unsigned char* jpt;
      if (TIFFGetField(_tiff, TIFFTAG_JPEGTABLES, &count, &jpt) != 0) {
        if (count > 4) {
          k = k + count;
          k -= 2; /* don't use EOI of header or SOI of tile */
        }
      }
      return k;
    }
    else {
      return -1;
    }
  }
  else {
    return -1;
  }
}

unsigned char* TIFFImage::readEncodedDataFromImage(const long long& startX, const long long& startY, const unsigned int& level) {
  if (_tiff) {
    unsigned int codec = 0;
    TIFFGetField(_tiff, TIFFTAG_COMPRESSION, &codec);
    if (codec == 7) { // New style JPEG
      long long levelStartX = std::floor(startX / getLevelDownsample(level) + 0.5);
      long long levelStartY = std::floor(startY / getLevelDownsample(level) + 0.5);
      TIFFSetDirectory(_tiff, level);
      unsigned int tileNr = TIFFComputeTile(_tiff, levelStartX, levelStartY, 0, 0);
      if (tileNr < TIFFNumberOfTiles(_tiff)) {
        unsigned char table_end[2];
        unsigned int count = 0;
        unsigned char* jpt;
        float* xfloatp;
        unsigned int endOfBuffer = 0;
        unsigned long long datasize = this->getEncodedTileSize(startX, startY, level);
        if (datasize < 0) {
          return NULL;
        }
        unsigned long long bufferoffset = 0;
        unsigned char* buffer = new unsigned char[datasize];

        if (TIFFGetField(_tiff, TIFFTAG_JPEGTABLES, &count, &jpt) != 0) {
          if (count > 4) {
            int retTIFFReadRawTile;
            /* Ignore EOI marker of JpegTables */
            _TIFFmemcpy(buffer, jpt, count - 2);
            bufferoffset += count - 2;
            /* Store last 2 bytes of the JpegTables */
            table_end[0] = buffer[bufferoffset - 2];
            table_end[1] = buffer[bufferoffset - 1];
            endOfBuffer = bufferoffset;
            bufferoffset -= 2;
            retTIFFReadRawTile = TIFFReadRawTile(
              _tiff,
              tileNr,
              (void*)&(((unsigned char*)buffer)[bufferoffset]),
              -1);
            bufferoffset += retTIFFReadRawTile;
            /* Overwrite SOI marker of image scan with previously */
            /* saved end of JpegTables */
            buffer[endOfBuffer - 2] = table_end[0];
            buffer[endOfBuffer - 1] = table_end[1];
          }
        }
        else {
          TIFFReadRawTile(
            _tiff,
            tileNr,
            (void*)&(((unsigned char*)buffer)[bufferoffset]),
            -1);
        }
        return buffer;
      }
      else {
        return NULL;
      }
    }
    else {
      return NULL;
    }
  }
  else {
    return NULL;
  }
}

T* TIFFImage::FillRequestedRegionFromTIFF(const long long& startX, const long long& startY, const unsigned long long& width,
  const unsigned long long& height, const unsigned int& level, unsigned int nrSamples)
{
  std::shared_lock<std::shared_mutex> l(*_openCloseMutex);
  T* temp = new T[width * height * nrSamples];
  std::fill(temp, temp + width * height * nrSamples, static_cast<T>(0));
  unsigned int tileW = _tileSizesPerLevel[level][0], tileH = _tileSizesPerLevel[level][1], levelH = _levelDimensions[level][1], levelW = _levelDimensions[level][0];

  long long levelStartX = std::floor(startX / getLevelDownsample(level) + 0.5);
  long long levelStartY = std::floor(startY / getLevelDownsample(level) + 0.5);
  long long startTileY = levelStartY - (levelStartY - ((levelStartY / tileH) * tileH));
  long long startTileX = levelStartX - (levelStartX - ((levelStartX / tileW) * tileW));
  long long finalX = levelStartX + width >= levelW ? levelW : levelStartX + width;
  long long finalY = levelStartY + height >= levelH ? levelH : levelStartY + height;

  for (long long iy = startTileY; iy < finalY; iy += tileH) {
    if (iy < 0) {
      continue;
    }
    for (long long ix = startTileX; ix < finalX; ix += tileW) {
      if (ix < 0) {
        continue;
      }

      std::stringstream k;
      k << ix * getLevelDownsample(level) << "-" << iy * getLevelDownsample(level) << "-" << level;
      bool deleteTile = false;
      unsigned int cachedTileSize = 0;
      T* tile = NULL;
      _cacheMutex->lock();
      std::static_pointer_cast<TileCache<T>>(_cache)->get(k.str(), tile, cachedTileSize);
      _cacheMutex->unlock();
      if (!tile) {
        tile = new T[tileW * tileH * getSamplesPerPixel()];
        std::fill(tile, tile + tileW * tileH * getSamplesPerPixel(), static_cast<T>(0.0));
        _cacheMutex->lock();
        TIFFSetDirectory(_tiff, level);
        unsigned int codec = 0;
        TIFFGetField(_tiff, TIFFTAG_COMPRESSION, &codec);
        unsigned int ycbcr = 0;
        TIFFGetField(_tiff, TIFFTAG_PHOTOMETRIC, &ycbcr);
        if (codec == 33005) {
          if (!_jp2000) {
            _jp2000 = new JPEG2000Codec();
          }
          unsigned int byteSize = tileW * tileH * getSamplesPerPixel() * sizeof(T);
          unsigned int rawSize = TIFFReadRawTile(_tiff, TIFFComputeTile(_tiff, ix, iy, 0, 0), tile, byteSize);
          _jp2000->decode((unsigned char*)tile, rawSize, byteSize);
        }
        else {
          if (codec == COMPRESSION_JPEG && ycbcr == PHOTOMETRIC_YCBCR) {
            TIFFSetField(_tiff, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
          }
          TIFFReadTile(_tiff, tile, ix, iy, 0, 0);
        }
        if (std::static_pointer_cast<TileCache<T>>(_cache)->set(k.str(), tile, tileW * tileH * getSamplesPerPixel() * sizeof(T))) {
          deleteTile = true;
        }
        _cacheMutex->unlock();
      }

      long long ixx = (ix - levelStartX);
      long long iyy = (iy - levelStartY);
      long long lyh = levelStartY + height;
      long long lxw = levelStartX + width;
      long long ixw = ixx + tileW;
      long long rowLength = ixw > static_cast<long long>(width) ? (tileW - (ixw - width)) * nrSamples : tileW * nrSamples;
      long long tileDeltaX = 0;
      if (ixx < 0) {
        rowLength += ixx * nrSamples;
        tileDeltaX -= ixx * nrSamples;
        ixx = 0;
      }
      for (unsigned int ty = 0; ty < tileH; ++ty) {
        if ((iyy + ty >= 0) && (ixx >= 0) && (iyy + ty < static_cast<long long>(height)) && lxw > 0) {
          long long idx = (ty + iyy) * width * nrSamples + ixx * nrSamples;
          long long tids = (ty * tileW) * nrSamples;
          std::copy(tile + tids + tileDeltaX, tile + tids + rowLength + tileDeltaX, temp + idx);
        }
      }
      if (deleteTile) {
        delete[] tile;
      }
    }
  }
  return temp;
}

void AperioSVSWriter::writeThumbnail() {
  unsigned long w = 0, h = 0, nrsamples = 0;
  TIFF* lowestResTiff = TIFFOpen(_levelFiles[_levelFiles.size()-1].c_str(), "r");
  TIFFGetField(lowestResTiff, TIFFTAG_IMAGEWIDTH, &w);
  TIFFGetField(lowestResTiff, TIFFTAG_IMAGELENGTH, &h);
  TIFFGetField(lowestResTiff, TIFFTAG_SAMPLESPERPIXEL, &nrsamples);

  setBaseTags(_tiff);
  TIFFSetField(_tiff, TIFFTAG_COMPRESSION, COMPRESSION_JPEG);
  TIFFSetField(_tiff, TIFFTAG_JPEGQUALITY, 70);
  TIFFSetField(_tiff, TIFFTAG_ROWSPERSTRIP, 16);
  TIFFSetField(_tiff, TIFFTAG_IMAGEWIDTH, w);
  TIFFSetField(_tiff, TIFFTAG_IMAGELENGTH, h);
  TIFFSetField(_tiff, TIFFTAG_RESOLUTIONUNIT, RESUNIT_CENTIMETER);
  TIFFSetField(_tiff, TIFFTAG_SUBFILETYPE, FILETYPE_REDUCEDIMAGE);

  unsigned int npixels = w * h * nrsamples;
  T* raster = (T*)_TIFFmalloc(npixels * sizeof(T));
  unsigned int nrTiles = TIFFNumberOfTiles(lowestResTiff);
  unsigned int tileW = 0, tileH = 0;
  TIFFGetField(lowestResTiff, TIFFTAG_TILEWIDTH, &tileW);
  TIFFGetField(lowestResTiff, TIFFTAG_TILELENGTH, &tileH);
  T* tile = (T*)_TIFFmalloc(tileW * tileH * nrsamples * sizeof(T));
  JPEG2000Codec cod;
  for (unsigned int tileY = 0; tileY < h; tileY += tileH) {
    for (unsigned int tileX = 0; tileX < w; tileX += tileW) {
      if (getCompression() == Compression::JPEG2000) {
        unsigned int no = TIFFComputeTile(lowestResTiff, tileX, tileY, 0, 0);
        unsigned int rawSize = TIFFReadRawTile(lowestResTiff, no, tile, tileW*tileH*nrsamples*sizeof(T));
        cod.decode((unsigned char*)tile, rawSize, tileW*tileH*nrsamples*sizeof(T));
      }
      else {
        TIFFReadTile(lowestResTiff, tile, tileX, tileY, 0, 0);
      }
      for (unsigned int y = 0; y < tileH; ++y){
        for (unsigned int x = 0; x < tileW; ++x){
          if ((tileX + x) < w && (tileY + y) < h) {
            for (unsigned int s = 0; s < nrsamples; ++s) {
              (*(raster + (tileY + y) * w * nrsamples + (tileX + x) * nrsamples + s)) = (*(tile + y * tileW * nrsamples + x * nrsamples + s));
            }
          }
        }
      }
    }
  }
  _TIFFfree(tile);
  tile = NULL;
  if (raster != NULL) {
    unsigned int nrOfStrips = TIFFNumberOfStrips(_tiff);
    for (unsigned int i = 0; i < nrOfStrips; ++i) {
      T* strip = (T*)_TIFFmalloc(16 * w * nrsamples * sizeof(T));
      unsigned int startOfStrip = i * 16 * w * nrsamples;
      unsigned int endOfStrip = (i + 1) * 16 * w * nrsamples;
      if (endOfStrip>npixels) {
        endOfStrip = npixels;
      }
      for (unsigned int j = startOfStrip, k = 0; j < endOfStrip; ++j, ++k) {
        T val = *(raster + j);
        *(strip + k) = val;
      }
      TIFFWriteEncodedStrip(_tiff, i, strip, 16 * w * nrsamples * sizeof(T));
      _TIFFfree(strip);
    }
    _TIFFfree(raster);
  }

  TIFFClose(lowestResTiff);
  lowestResTiff = NULL;
  if (TIFFNumberOfDirectories(_tiff) > 1) {
    TIFFSetField(_tiff, TIFFTAG_IMAGEDESCRIPTION, "Aperio Image macro");
  }
  TIFFWriteDirectory(_tiff);
}

int MultiResolutionImageWriter::writePyramidToDisk() {

	//! First get the overall image width and height;
	unsigned long w = 0, h = 0, nrsamples = 0, nrbits = 0;
	// TIFF idiosyncracy, when setting resolution tags one uses doubles,
	// getting them requires floats
	float spacingX = 0, spacingY = 0;
	std::vector<double> spacing;
	TIFFGetField(_tiff, TIFFTAG_IMAGEWIDTH, &w);
	TIFFGetField(_tiff, TIFFTAG_IMAGELENGTH, &h);
	TIFFGetField(_tiff, TIFFTAG_SAMPLESPERPIXEL, &nrsamples);
	TIFFGetField(_tiff, TIFFTAG_BITSPERSAMPLE, &nrbits);
	if (TIFFGetField(_tiff, TIFFTAG_XRESOLUTION, &spacingX) == 1) {
		if (TIFFGetField(_tiff, TIFFTAG_YRESOLUTION, &spacingY) == 1) {
			spacing.push_back(1. / (spacingX / (10000.)));
			spacing.push_back(1. / (spacingY / (10000.)));
		}
	}
	// Determine the amount of pyramid levels
	unsigned int pyramidlevels = 1;
	unsigned int lowestwidth = w;
	if (_maxPyramidLevels < 0) {
		while (lowestwidth > 1024) {
			lowestwidth /= _downsamplePerLevel;
			pyramidlevels += 1;
		}
		if (abs(1024. - lowestwidth) > abs(1024. - lowestwidth * 2)) {
			lowestwidth *= _downsamplePerLevel;
			pyramidlevels -= 1;
		}
	}
	else {
		pyramidlevels = _maxPyramidLevels;
		lowestwidth /= pow(_downsamplePerLevel, pyramidlevels);
	}
	// Setup the image directory for the thumbnail
	unsigned int lowestheight = (unsigned int)(h / pow(_downsamplePerLevel, (double)pyramidlevels));

	// Write temporary image to store previous level (LibTiff does not allow to go back and forth between
	// empty directories
#ifdef WIN32
	size_t found = _fileName.find_last_of("/\\");
#else 
	size_t found = _fileName.find_last_of("/");
#endif
	string tmpPth = _fileName.substr(0, found + 1);
	string fileName = _fileName.substr(found + 1);
	size_t dotLoc = fileName.find_last_of(".");
	string baseName = fileName.substr(0, dotLoc);
	for (unsigned int level = 1; level <= pyramidlevels; ++level) {
		if (_monitor) {
			_monitor->setProgress((_monitor->maximumProgress() / 2.) + (static_cast<float>(level) / static_cast<float>(pyramidlevels))* (_monitor->maximumProgress() / 4.));
		}
		TIFF* prevLevelTiff = _tiff;
		if (level != 1) {
			std::stringstream ssm;
			ssm << tmpPth << "temp" << baseName << "Level" << level - 1 << ".tif";
			prevLevelTiff = TIFFOpen(ssm.str().c_str(), "r");
		}
		std::stringstream ssm;
		ssm << tmpPth << "temp" << baseName << "Level" << level << ".tif";
		TIFF* levelTiff = TIFFOpen(ssm.str().c_str(), "w8");
		_levelFiles.push_back(ssm.str());
		unsigned int levelw = (unsigned int)(w / pow(_downsamplePerLevel, (double)level));
		unsigned int levelh = (unsigned int)(h / pow(_downsamplePerLevel, (double)level));
		unsigned int prevLevelw = (unsigned int)(w / pow(_downsamplePerLevel, (double)level - 1));
		unsigned int prevLevelh = (unsigned int)(h / pow(_downsamplePerLevel, (double)level - 1));
		setTempPyramidTags(levelTiff, levelw, levelh);
		unsigned int nrTilesX = (unsigned int)ceil(float(levelw) / _tileSize);
		unsigned int nrTilesY = (unsigned int)ceil(float(levelh) / _tileSize);
		unsigned int levelTiles = nrTilesX * nrTilesY;
		unsigned int npixels = _tileSize * _tileSize * nrsamples;
		int rowOrg = -_downsamplePerLevel, colOrg = 0;
		for (unsigned int i = 0; i < levelTiles; ++i) {
			if (i % nrTilesX == 0) {
				rowOrg += _downsamplePerLevel;
				colOrg = 0;
			}
			unsigned int xpos = _tileSize * colOrg;
			unsigned int ypos = _tileSize * rowOrg;
			int inpTilesForOutpTile = _downsamplePerLevel * _downsamplePerLevel;
			std::vector<T*> tiles;
			std::vector<bool> tiles_valid;
			for (int inTileNr = 0; inTileNr < inpTilesForOutpTile; ++inTileNr) {
				tiles.push_back((T*)_TIFFmalloc(npixels * sizeof(T)));
				tiles_valid.push_back(false);
			}
			T* outTile = (T*)_TIFFmalloc(npixels * sizeof(T));
			unsigned int size = npixels * sizeof(T);
			if (level == 1 && (getCompression() == Compression::JPEG2000)) {
				for (int inRow = 0; inRow < _downsamplePerLevel; inRow++) {
					for (int inCol = 0; inCol < _downsamplePerLevel; inCol++) {
						if (xpos + inCol * _tileSize >= prevLevelw || ypos + inRow * _tileSize >= prevLevelh) {
							std::fill_n(tiles[inRow * _downsamplePerLevel + inCol], npixels, static_cast<T>(0));
						}
						else {
							int tileNr = TIFFComputeTile(prevLevelTiff, xpos + inCol * _tileSize, ypos + inRow * _tileSize, 0, 0);
							unsigned int outTileSize = _tileSize * _tileSize * nrsamples * (nrbits / 8);
							int rawSize = TIFFReadRawTile(prevLevelTiff, tileNr, tiles[inRow * _downsamplePerLevel + inCol], outTileSize);
							if (rawSize > 0) {
								tiles_valid[inRow * _downsamplePerLevel + inCol] = true;
							}
							else {
								std::fill_n(tiles[inRow * _downsamplePerLevel + inCol], npixels, static_cast<T>(0));
							}
						}
					}
				}
			}
			else {
				for (int inRow = 0; inRow < _downsamplePerLevel; inRow++) {
					for (int inCol = 0; inCol < _downsamplePerLevel; inCol++) {
						if (xpos + inCol * _tileSize >= prevLevelw || ypos + inRow * _tileSize >= prevLevelh) {
							std::fill_n(tiles[inRow * _downsamplePerLevel + inCol], npixels, static_cast<T>(0));
						} else {
							if (TIFFReadTile(prevLevelTiff, tiles[inRow * _downsamplePerLevel + inCol], xpos + inCol * _tileSize, ypos + inRow * _tileSize, 0, 0) < 0) {
								std::fill_n(tiles[inRow * _downsamplePerLevel + inCol], npixels, static_cast<T>(0));
							}
							else {
								tiles_valid[inRow * _downsamplePerLevel + inCol] = true;
							}
						}
					}
				}
			}
			if (std::any_of(tiles_valid.begin(), tiles_valid.end(), [](bool v) { return v; })) {
				std::vector<T*> dsTiles;
				for (auto tile : tiles) {
					dsTiles.push_back(downscaleTile(tile, _tileSize, nrsamples));
				}
				unsigned int dsSize = _tileSize / _downsamplePerLevel;
				for (unsigned int y = 0; y < _tileSize; ++y) {
					for (unsigned int x = 0; x < _tileSize; ++x) {
						for (unsigned int s = 0; s < nrsamples; ++s) {
							unsigned int outIndex = nrsamples * (y * _tileSize + x) + s;
							unsigned int colTile = std::floor(x / dsSize);
							unsigned int rowTile = std::floor(y / dsSize);
							unsigned int inIndex = y * dsSize * nrsamples + x * nrsamples + s;
							T* usedTile = dsTiles[rowTile * _downsamplePerLevel + colTile];
							inIndex = ((y - rowTile * dsSize) * dsSize * nrsamples) + ((x - colTile * dsSize) * nrsamples) + s;
							T val = *(usedTile + inIndex);
							*(outTile + outIndex) = val;
						}
					}
				}
				TIFFWriteEncodedTile(levelTiff, i, outTile, npixels * sizeof(T));
				for (auto tile : dsTiles) {
					_TIFFfree(tile);
				}
			}
			for (auto tile : tiles) {
				_TIFFfree(tile);
			}
			_TIFFfree(outTile);
			colOrg += _downsamplePerLevel;
		}
		if (level != 1) {
			TIFFClose(prevLevelTiff);
		}
		TIFFSetField(_tiff, TIFFTAG_RESOLUTIONUNIT, RESUNIT_CENTIMETER);
		if (!spacing.empty()) {
			spacing[0] *= _downsamplePerLevel;
			spacing[1] *= _downsamplePerLevel;
			double pixPerCmX = (1. / spacing[0]) * 10000;
			double pixPerCmY = (1. / spacing[1]) * 10000;
			TIFFSetField(levelTiff, TIFFTAG_XRESOLUTION, pixPerCmX);
			TIFFSetField(levelTiff, TIFFTAG_YRESOLUTION, pixPerCmY);
		}
		TIFFClose(levelTiff);
	}
	//! Write base directory to disk
	TIFFWriteDirectory(_tiff);
	return 0;
}

int MultiResolutionImageWriter::incorporatePyramid() {
	unsigned long nrsamples = 0;

	//// Now add all the pyramid levels
	for (vector<string>::const_iterator it = _levelFiles.begin(); it != _levelFiles.end(); ++it) {
		if (_monitor) {
			_monitor->setProgress(3 * (_monitor->maximumProgress() / 4.) + ((static_cast<float>(it - _levelFiles.begin()) + 1.0) / static_cast<float>(_levelFiles.size()))* (_monitor->maximumProgress() / 4.));
		}
		TIFF* level = TIFFOpen(it->c_str(), "rm");

		float spacingX = 0, spacingY = 0;
		std::vector<double> spacing;
		if (TIFFGetField(level, TIFFTAG_XRESOLUTION, &spacingX) == 1) {
			if (TIFFGetField(level, TIFFTAG_YRESOLUTION, &spacingY) == 1) {
				spacing.push_back(1. / (spacingX / (10000.)));
				spacing.push_back(1. / (spacingY / (10000.)));
			}
		}

		unsigned int levelw, levelh;
		TIFFGetField(level, TIFFTAG_IMAGEWIDTH, &levelw);
		TIFFGetField(level, TIFFTAG_IMAGELENGTH, &levelh);
		setPyramidTags(_tiff, levelw, levelh);
		TIFFSetField(_tiff, TIFFTAG_SUBFILETYPE, FILETYPE_REDUCEDIMAGE);
		TIFFGetField(level, TIFFTAG_SAMPLESPERPIXEL, &nrsamples);
		writePyramidLevel<T>(level, levelw, levelh, nrsamples);

		setSpacing(spacing);
		TIFFWriteDirectory(_tiff);
		TIFFClose(level);
	}
	return 0;
}

DATA_TYPE GridManager::getGridDataType( const char * cfilename, GRID_TYPE GridType )
{	
	CString filename = cfilename;

	GRID_TYPE grid_type = GridType;
	if( GridType == USE_EXTENSION )
		grid_type = getGridType( filename );
	
	if( grid_type == GEOTIFF_GRID) //added 8/26/05 --ah
	{
		DATA_TYPE type = UNKNOWN_TYPE;
		TIFF *tiff = XTIFFOpen((char *)cfilename, "r"); // TIFF-level descriptor
		if(tiff)
		{
			uint16_t sampleFormat, bitsPerSample;

			TIFFGetField(tiff,TIFFTAG_SAMPLEFORMAT, &sampleFormat);
			TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
			if(sampleFormat == 1)
			{//type is unsigned int
				type = SHORT_TYPE;
			}
			else if(sampleFormat == 2)
			{//type is signed int
				type = LONG_TYPE;
			}
			else if(sampleFormat == 3)
			{//IEEE floating-point value
				if(bitsPerSample == 32)
				{
					type = FLOAT_TYPE;
				}
				else if(bitsPerSample == 64)
				{
					type = DOUBLE_TYPE;
				}
			}
			//TODO: may want to check for other sample formats:
			//4 = unknown, 5 = complex int, 6 = complex float or double
			//0 = undefined -> check bits per sample and samples per pixel
			//to determine size.
			else
			{
				type = UNKNOWN_TYPE;
			}			
		}
		XTIFFClose(tiff);

		return type;
	}
	else if( grid_type == ASCII_GRID )
		return DOUBLE_TYPE;
	else if( grid_type == BINARY_GRID )
	{	
		FILE * in = fopen( filename, "rb" );
		if( !in )
			return INVALID_DATA_TYPE;
		else
		{	long ncols;
			fread( &ncols, sizeof(int),1,in);
			long nrows;
			fread( &nrows, sizeof(int),1,in);
			double dx;
			fread( &dx, sizeof(double),1,in);
			double dy;
			fread( &dy, sizeof(double),1,in);
			double xllcenter;
			fread( &xllcenter, sizeof(double),1,in);
			double yllcenter;
			fread( &yllcenter, sizeof(double),1,in);
			DATA_TYPE data_type;
			fread( &data_type, sizeof(DATA_TYPE),1,in);
			fclose( in );
			return data_type;
		}
	}
	else if( grid_type == SDTS_GRID )
		return DOUBLE_TYPE;
	else if( grid_type == ESRI_GRID )
	{	if( filename.GetLength() <= 0 )
			return INVALID_DATA_TYPE;		
		else
		{	DATA_TYPE data_type = esri_data_type(filename);
			return data_type;
		}
	}
	
	return INVALID_DATA_TYPE;
}

STDMETHODIMP CUtils::IsTiffGrid(BSTR Filename, VARIANT_BOOL* retVal)
{
	AFX_MANAGE_STATE(AfxGetStaticModuleState());
	*retVal = VARIANT_FALSE;

	try
	{
		USES_CONVERSION;
		const char* name = OLE2CA(Filename);
		TIFF* tiff = XTIFFOpen(name, "r"); // TIFF-level descriptor
		if (tiff)
		{
			int w = 0, h = 0;

			tdir_t d = 0;
			TIFFSetDirectory(tiff, d);

			uint32_t SamplesPerPixel = 0;

			TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &w);
			TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &h);
			TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &SamplesPerPixel);

			uint16_t photo = 0;
			// If it's a color-mapped palette, consider it an image --
			// it's probably an image (USGS DLG or USGS Quad Map most commonly)
			TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC, &photo);

			XTIFFClose(tiff);

			if (photo == PHOTOMETRIC_PALETTE) {}
			else if (SamplesPerPixel == 1)
			{
				*retVal = VARIANT_TRUE;
			}
		}
	}
	catch (...)
	{
		CallbackHelper::ErrorMsg("Exception in Utils.IsTiffGrid");
	}
	return S_OK;
}

CML::Pair<CML::FloatImage, CML::Image> CML::loadTiffImage(const uint8_t *str, size_t lenght) {
    std::istringstream input_TIFF_stream(std::string((char *)str, lenght));

//Populate input_TIFF_stream with TIFF image data
//...

    TIFF *tif = TIFFStreamOpen("MemTIFF", &input_TIFF_stream);

    if (!tif) {
        throw std::runtime_error("Can't open the tiff file");
    }

    uint32_t height, width, bitspersample;
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitspersample);

    if (bitspersample == 8) {
        return loadTiffImage<uint8_t, 8>(tif, width, height);
    }
    if (bitspersample == 16) {
        return loadTiffImage<uint16_t, 16>(tif, width, height);
    }
    if (bitspersample == 32) {
        return loadTiffImage<uint32_t, 32>(tif, width, height);
    }
    if (bitspersample == 64) {
        return loadTiffImage<uint64_t, 64>(tif, width, height);
    }

    throw std::runtime_error("Unsupported tiff bit per sample : " + std::to_string(bitspersample));

}

bool getCurrentDirectoryInfo(TIFF* tif, Vec3c& dimensions, ImageDataType& dataType, size_t& pixelSizeBytes,
				coord_t& imageJDepth, uint64_t& imageJOffset,
				string& reason)
			{
				reason = "";

				uint16_t tiffDatatype = 0;
				uint32_t sampleFormat = 0;
				uint32_t tiffWidth = 0;
				uint32_t tiffHeight = 0;
				uint32_t tiffDepth = 0;
				uint16_t samplesPerPixel = 0;
				uint16_t bitsPerSample = 0;
				char* description = 0;

				TIFFGetFieldDefaulted(tif, TIFFTAG_DATATYPE, &tiffDatatype);
				TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);
				TIFFGetFieldDefaulted(tif, TIFFTAG_IMAGEWIDTH, &tiffWidth);
				TIFFGetFieldDefaulted(tif, TIFFTAG_IMAGELENGTH, &tiffHeight);
				TIFFGetFieldDefaulted(tif, TIFFTAG_IMAGEDEPTH, &tiffDepth);
				TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);
				TIFFGetFieldDefaulted(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
				TIFFGetFieldDefaulted(tif, TIFFTAG_IMAGEDESCRIPTION, &description);

				dimensions = Vec3c(tiffWidth, tiffHeight, tiffDepth);
				imageJDepth = dimensions.z;
				imageJOffset = 0;

				// Test if this file is a ImageJ fake tiff.
				if (description)
				{
					string desc = description;
					if (startsWith(desc, "ImageJ="))
					{
						auto start = desc.find("images=");
						if (start != string::npos)
						{
							start += string("images=").length();
							auto end = desc.find("\n", start);
							if (end != string::npos)
							{
								string depth = desc.substr(start, end - start);
								imageJDepth = fromString<uint32_t>(depth);

								uint64_t* stripOffsets = 0;
								int result = TIFFGetField(tif, TIFFTAG_STRIPOFFSETS, &stripOffsets);
								imageJOffset = stripOffsets[0];
							}
						}
					}
				}

				switch (sampleFormat)
				{
				case SAMPLEFORMAT_UINT:
					if (bitsPerSample == 8)
					{
						dataType = ImageDataType::UInt8;
						pixelSizeBytes = 1;
					}
					else if(bitsPerSample == 16)
					{
						dataType = ImageDataType::UInt16;
						pixelSizeBytes = 2;
					}
					else if (bitsPerSample == 32)
					{
						dataType = ImageDataType::UInt32;
						pixelSizeBytes = 4;
					}
					else if (bitsPerSample == 64)
					{
						dataType = ImageDataType::UInt64;
						pixelSizeBytes = 8;
					}
					else
					{
						reason = "Unsupported unsigned integer data type.";
					}
					break;
				case SAMPLEFORMAT_INT:
					reason = "Unsupported signed integer data type.";
					break;
				case SAMPLEFORMAT_IEEEFP:
					if (bitsPerSample == 32)
					{
						dataType = ImageDataType::Float32;
						pixelSizeBytes = 4;
					}
					else
					{
						reason = "Unsupported floating point data type.";
					}
					break;
				case SAMPLEFORMAT_COMPLEXINT:
					reason = "Unsupported complex integer data type.";
					break;
				case SAMPLEFORMAT_COMPLEXIEEEFP:
					if (bitsPerSample == 64)
					{
						dataType = ImageDataType::Complex32;
						pixelSizeBytes = 8;
					}
					else
					{
						reason = "Unsupported complex floating point data type.";
					}
					break;
				case SAMPLEFORMAT_VOID:
					// Unknown data type

					// Check older tag
					switch (tiffDatatype)
					{
					case TIFF_BYTE:
						dataType = ImageDataType::UInt8;
						pixelSizeBytes = 1;
						break;
					case TIFF_SHORT:
						dataType = ImageDataType::UInt16;
						pixelSizeBytes = 2;
						break;
					case TIFF_LONG:
						dataType = ImageDataType::UInt32;
						pixelSizeBytes = 4;
						break;
					case TIFF_LONG8:
						dataType = ImageDataType::UInt64;
						pixelSizeBytes = 8;
						break;
					case TIFF_FLOAT:
						dataType = ImageDataType::Float32;
						pixelSizeBytes = 4;
						break;
					case TIFF_NOTYPE:
						dataType = ImageDataType::Unknown;
						pixelSizeBytes = bitsPerSample / 8;

						//// Guess data type (this is needed to load, e.g., tiff files generated by ImageJ)
						//if (pixelSizeBytes == 1)
						//	dataType = ImageDataType::UInt8;
						//else if (pixelSizeBytes == 2)
						//	dataType = ImageDataType::UInt16;
						//else if (pixelSizeBytes == 4)
						//	dataType = ImageDataType::Float32;
						//else if (pixelSizeBytes == 8)
						//	dataType = ImageDataType::UInt64;

						break;
					default:
						return false;
					}

					break;
				}

				if (samplesPerPixel != 1)
					reason = "Only grayscale images are supported.";

				lastTiffErrorMessage = reason;

				return reason.length() <= 0;
			}

int readTiffRGBA(char const *const filename, VkExtent2D *const size,
                 VkSubresourceLayout *layout, uint8_t *const data)
{
    const uint32_t nchannels = 4;

    TIFF *tif = TIFFOpen(filename, "r");
    if (tif == NULL)
        return 1;

    if (data == NULL) {
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &size->height);
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &size->width);
    } else {
        if (layout == NULL || size == NULL)
            goto cleanup;

        char *const temp = (char *)malloc(nchannels * size->width * size->height);
        if (!TIFFReadRGBAImage(tif, size->width, size->height, (uint32_t *) temp, 0))
            goto cleanup;

        for (size_t x = 0; x < size->width; x++)
            for (size_t y = 0; y < size->height; y++)
                for (size_t ch = 0; ch < nchannels; ch++) {
                    data[ch + x * nchannels + y * layout->rowPitch] =
                        temp[ch + x * nchannels + y * size->width * nchannels];
                }
        free(temp);
    }

    TIFFClose(tif);
    return 0;
cleanup:
    TIFFClose(tif);
    return 1;
}

void TIFFFileImageProvider::progress()
{
    if (!p) {
        p = new TIFFPrivate(this);
        p->tif = TIFFOpen(filename.c_str(), "rm");
        if (!p->tif)
            return onFinish(makeError("cannot read tiff " + filename));

        int r = 0;
        r += TIFFGetField(p->tif, TIFFTAG_IMAGEWIDTH, &p->w);
        r += TIFFGetField(p->tif, TIFFTAG_IMAGELENGTH, &p->h);

        if (r != 2)
            return onFinish(makeError("can not read tiff of unknown size"));

        r = TIFFGetField(p->tif, TIFFTAG_SAMPLESPERPIXEL, &p->spp);
        if (!r)
            p->spp = 1;

        r = TIFFGetField(p->tif, TIFFTAG_BITSPERSAMPLE, &p->bps);
        if (!r)
            p->bps = 1;

        r = TIFFGetField(p->tif, TIFFTAG_SAMPLEFORMAT, &p->fmt);
        if (!r)
            p->fmt = SAMPLEFORMAT_UINT;

        if (p->fmt == SAMPLEFORMAT_COMPLEXINT || p->fmt == SAMPLEFORMAT_COMPLEXIEEEFP) {
            p->spp *= 2;
            p->bps /= 2;
        }
        if (p->fmt == SAMPLEFORMAT_COMPLEXINT)
            p->fmt = SAMPLEFORMAT_INT;
        if (p->fmt == SAMPLEFORMAT_COMPLEXIEEEFP)
            p->fmt = SAMPLEFORMAT_IEEEFP;

        uint16_t planarity;
        r = TIFFGetField(p->tif, TIFFTAG_PLANARCONFIG, &planarity);
        if (r != 1)
            planarity = PLANARCONFIG_CONTIG;
        p->broken = planarity == PLANARCONFIG_SEPARATE;

        uint32_t scanline_size = (p->w * p->spp * p->bps) / 8;
        int rbps = (p->bps / 8) ? (p->bps / 8) : 1;
        p->sls = TIFFScanlineSize(p->tif);
        if ((int)scanline_size != p->sls)
            fprintf(stderr, "scanline_size,sls = %d,%d\n", (int)scanline_size, p->sls);
        //assert((int)scanline_size == sls);
        if (!p->broken)
            assert((int)scanline_size == p->sls);
        assert((int)scanline_size >= p->sls);
        p->data = (float*)malloc(p->w * p->h * p->spp * rbps);
        p->buf = (uint8_t*)_TIFFmalloc(scanline_size);
        p->curh = 0;

        if (TIFFIsTiled(p->tif) || p->fmt != SAMPLEFORMAT_IEEEFP || p->broken || rbps != sizeof(float)) {
#ifdef USE_IIO
            std::shared_ptr<Image> image = load_from_iio(filename);
            if (!image) {
                onFinish(makeError("iio: cannot load image '" + filename + "'"));
            } else {
                onFinish(image);
            }
#else
            onFinish(makeError("cannot load image '" + filename + "'"));
#endif
        }
    } else if (p->curh < p->h) {
        int r = TIFFReadScanline(p->tif, p->buf, p->curh);
        if (r < 0) {
            onFinish(makeError("error reading tiff row " + std::to_string(p->curh)));
        }
        memcpy(p->data + p->curh * p->sls / sizeof(float), p->buf, p->sls);
        p->curh++;
    } else {
        std::shared_ptr<Image> image = std::make_shared<Image>(p->data, p->w, p->h, p->spp);
        onFinish(image);
        p->data = nullptr;
    }
}

ILboolean iLoadTiffInternal()
{
	TIFF	 *tif;
	uint16	 photometric, planarconfig, orientation;
	uint16	 samplesperpixel, bitspersample, *sampleinfo, extrasamples;
	uint32	 w, h, d, linesize, tilewidth, tilelength;
	ILubyte  *pImageData;
	ILuint	 i, ProfileLen, DirCount = 0;
	void	 *Buffer;
	ILimage  *Image, *TempImage;
	ILushort si;
        ILfloat  x_position, x_resolution, y_position, y_resolution;
	//TIFFRGBAImage img;
	//char emsg[1024];

	// to avoid high order bits garbage when used as shorts
	w = h = d = linesize = tilewidth = tilelength = 0;

	if (iCurImage == NULL) {
		ilSetError(IL_ILLEGAL_OPERATION);
		return IL_FALSE;
	}

	TIFFSetWarningHandler (NULL);
	TIFFSetErrorHandler   (NULL);

	//for debugging only
	//TIFFSetWarningHandler(warningHandler);
	//TIFFSetErrorHandler(errorHandler);

	tif = iTIFFOpen((char*)"r");
	if (tif == NULL) {
		ilSetError(IL_COULD_NOT_OPEN_FILE);
		return IL_FALSE;
	}

	do {
		DirCount++;
	} while (TIFFReadDirectory(tif));

	/*
	 if (!ilTexImage(1, 1, 1, 1, IL_RGBA, IL_UNSIGNED_BYTE, NULL)) {
		 TIFFClose(tif);
		 return IL_FALSE;
	}
	Image = iCurImage;
	for (i = 1; i < DirCount; i++) {
		 Image->Next = ilNewImage(1, 1, 1, 1, 1);
		if (Image->Next == NULL) {
			TIFFClose(tif);
			return IL_FALSE;
		}
		Image = Image->Next;
	}
	iCurImage->NumNext = DirCount - 1;
	*/
	Image = NULL;
	for (i = 0; i < DirCount; i++) {
		TIFFSetDirectory(tif, (tdir_t)i);
		TIFFGetField(tif, TIFFTAG_IMAGEWIDTH,  &w);
		TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);

		TIFFGetFieldDefaulted(tif, TIFFTAG_IMAGEDEPTH,		&d); //TODO: d is ignored...
		TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);
		TIFFGetFieldDefaulted(tif, TIFFTAG_BITSPERSAMPLE,	&bitspersample);
		TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES,	&extrasamples, &sampleinfo);
		TIFFGetFieldDefaulted(tif, TIFFTAG_ORIENTATION, 	&orientation);

		linesize = (uint32)TIFFScanlineSize(tif);

		//added 2003-08-31
		//1 bpp tiffs are not neccessarily greyscale, they can
		//have a palette (photometric == 3)...get this information
		TIFFGetFieldDefaulted(tif, TIFFTAG_PHOTOMETRIC,  &photometric);
		TIFFGetFieldDefaulted(tif, TIFFTAG_PLANARCONFIG, &planarconfig);

		//special-case code for frequent data cases that may be read more
		//efficiently than with the TIFFReadRGBAImage() interface.

		//added 2004-05-12
		//Get tile sizes and use TIFFReadRGBAImage() for tiled images for now
		tilewidth = w; tilelength = h;
		TIFFGetFieldDefaulted(tif, TIFFTAG_TILEWIDTH,  &tilewidth);
		TIFFGetFieldDefaulted(tif, TIFFTAG_TILELENGTH, &tilelength);


		if (extrasamples == 0
			&& samplesperpixel == 1  //luminance or palette
			&& (bitspersample == 8 || bitspersample == 1 || bitspersample == 16)
			&& (photometric == PHOTOMETRIC_MINISWHITE
				|| photometric == PHOTOMETRIC_MINISBLACK
				|| photometric == PHOTOMETRIC_PALETTE)
			&& (orientation == ORIENTATION_TOPLEFT || orientation == ORIENTATION_BOTLEFT)
			&& tilewidth == w && tilelength == h
			) {
			ILubyte* strip;
			tsize_t stripsize;
			ILuint y;
			uint32 rowsperstrip, j, linesread;

			//TODO: 1 bit/pixel images should not be stored as 8 bits...
			//(-> add new format)
			if (!Image) {
				int type = IL_UNSIGNED_BYTE;
				if (bitspersample == 16) type = IL_UNSIGNED_SHORT;
				if (!ilTexImage(w, h, 1, 1, IL_LUMINANCE, type, NULL)) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = iCurImage;
			}
			else {
				Image->Next = ilNewImage(w, h, 1, 1, 1);
				if (Image->Next == NULL) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = Image->Next;
			}

			if (photometric == PHOTOMETRIC_PALETTE) { //read palette
				uint16 *red, *green, *blue;
				//ILboolean is16bitpalette = IL_FALSE;
				ILubyte *entry;
				uint32 count = 1 << bitspersample, j;

				TIFFGetField(tif, TIFFTAG_COLORMAP, &red, &green, &blue);

				Image->Format = IL_COLOUR_INDEX;
				Image->Pal.PalSize = (count)*3;
				Image->Pal.PalType = IL_PAL_RGB24;
				Image->Pal.Palette = (ILubyte*)ialloc(Image->Pal.PalSize);
				entry = Image->Pal.Palette;
				for (j = 0; j < count; ++j) {
					entry[0] = (ILubyte)(red[j] >> 8);
					entry[1] = (ILubyte)(green[j] >> 8);
					entry[2] = (ILubyte)(blue[j] >> 8);

					entry += 3;
				}
			}

			TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);
			stripsize = TIFFStripSize(tif);

			strip = (ILubyte*)ialloc(stripsize);

			if (bitspersample == 8 || bitspersample == 16) {
				ILubyte *dat = Image->Data;
				for (y = 0; y < h; y += rowsperstrip) {
					//the last strip may contain less data if the image
					//height is not evenly divisible by rowsperstrip
					if (y + rowsperstrip > h) {
						stripsize = linesize*(h - y);
						linesread = h - y;
					}
					else
						linesread = rowsperstrip;

					if (TIFFReadEncodedStrip(tif, TIFFComputeStrip(tif, y, 0), strip, stripsize) == -1) {
						ilSetError(IL_LIB_TIFF_ERROR);
						ifree(strip);
						TIFFClose(tif);
						return IL_FALSE;
					}

					if (photometric == PHOTOMETRIC_MINISWHITE) { //invert channel
						uint32 k, t2;
						for (j = 0; j < linesread; ++j) {
							t2 = j*linesize;
							//this works for 16bit images as well: the two bytes
							//making up a pixel can be inverted independently
							for (k = 0; k < Image->Bps; ++k)
								dat[k] = ~strip[t2 + k];
							dat += w;
						}
					}
					else
						for(j = 0; j < linesread; ++j)
							memcpy(&Image->Data[(y + j)*Image->Bps], &strip[j*linesize], Image->Bps);
				}
			}
			else if (bitspersample == 1) {
				//TODO: add a native format to devil, so we don't have to
				//unpack the values here
				ILubyte mask, curr, *dat = Image->Data;
				uint32 k, sx, t2;
				for (y = 0; y < h; y += rowsperstrip) {
					//the last strip may contain less data if the image
					//height is not evenly divisible by rowsperstrip
					if (y + rowsperstrip > h) {
						stripsize = linesize*(h - y);
						linesread = h - y;
					}
					else
						linesread = rowsperstrip;

					if (TIFFReadEncodedStrip(tif, TIFFComputeStrip(tif, y, 0), strip, stripsize) == -1) {
						ilSetError(IL_LIB_TIFF_ERROR);
						ifree(strip);
						TIFFClose(tif);
						return IL_FALSE;
					}

					for (j = 0; j < linesread; ++j) {
						k = 0;
						sx = 0;
						t2 = j*linesize;
						while (k < w) {
							curr = strip[t2 + sx];
							if (photometric == PHOTOMETRIC_MINISWHITE)
								curr = ~curr;
							for (mask = 0x80; mask != 0 && k < w; mask >>= 1){
								if((curr & mask) != 0)
									dat[k] = 255;
								else
									dat[k] = 0;
								++k;
							}
							++sx;
						}
						dat += w;
					}
				}
			}

			ifree(strip);

			if(orientation == ORIENTATION_TOPLEFT)
				Image->Origin = IL_ORIGIN_UPPER_LEFT;
			else if(orientation == ORIENTATION_BOTLEFT)
				Image->Origin = IL_ORIGIN_LOWER_LEFT;
		}
		//for 16bit rgb images:
		else if (extrasamples == 0
			&& samplesperpixel == 3
			&& (bitspersample == 8 || bitspersample == 16)
			&& photometric == PHOTOMETRIC_RGB
			&& planarconfig == 1
			&& (orientation == ORIENTATION_TOPLEFT || orientation == ORIENTATION_BOTLEFT)
			&& tilewidth == w && tilelength == h
			) {
			ILubyte *strip, *dat;
			tsize_t stripsize;
			ILuint y;
			uint32 rowsperstrip, j, linesread;

			if (!Image) {
				int type = IL_UNSIGNED_BYTE;
				if (bitspersample == 16) type = IL_UNSIGNED_SHORT;
				if(!ilTexImage(w, h, 1, 3, IL_RGB, type, NULL)) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = iCurImage;
			}
			else {
				Image->Next = ilNewImage(w, h, 1, 1, 1);
				if(Image->Next == NULL) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = Image->Next;
			}

			TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);
			stripsize = TIFFStripSize(tif);

			strip = (ILubyte*)ialloc(stripsize);

			dat = Image->Data;
			for (y = 0; y < h; y += rowsperstrip) {
				//the last strip may contain less data if the image
				//height is not evenly divisible by rowsperstrip
				if (y + rowsperstrip > h) {
					stripsize = linesize*(h - y);
					linesread = h - y;
				}
				else
					linesread = rowsperstrip;

				if (TIFFReadEncodedStrip(tif, TIFFComputeStrip(tif, y, 0), strip, stripsize) == -1) {
					ilSetError(IL_LIB_TIFF_ERROR);
					ifree(strip);
					TIFFClose(tif);
					return IL_FALSE;
				}

				for(j = 0; j < linesread; ++j)
						memcpy(&Image->Data[(y + j)*Image->Bps], &strip[j*linesize], Image->Bps);
			}

			ifree(strip);

			if(orientation == ORIENTATION_TOPLEFT)
				Image->Origin = IL_ORIGIN_UPPER_LEFT;
			else if(orientation == ORIENTATION_BOTLEFT)
				Image->Origin = IL_ORIGIN_LOWER_LEFT;
		}/*
		else if (extrasamples == 0 && samplesperpixel == 3  //rgb
					&& (bitspersample == 8 || bitspersample == 1 || bitspersample == 16)
					&& photometric == PHOTOMETRIC_RGB
					&& (planarconfig == PLANARCONFIG_CONTIG || planarcon
						&& (orientation == ORIENTATION_TOPLEFT || orientation == ORIENTATION_BOTLEFT)
					) {
		}
				*/
		else {
				//not direclty supported format
			if(!Image) {
				if(!ilTexImage(w, h, 1, 4, IL_RGBA, IL_UNSIGNED_BYTE, NULL)) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = iCurImage;
			}
			else {
				Image->Next = ilNewImage(w, h, 1, 4, 1);
				if(Image->Next == NULL) {
					TIFFClose(tif);
					return IL_FALSE;
				}
				Image = Image->Next;
			}

			if (samplesperpixel == 4) {
				TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &extrasamples, &sampleinfo);
				if (!sampleinfo || sampleinfo[0] == EXTRASAMPLE_UNSPECIFIED) {
					si = EXTRASAMPLE_ASSOCALPHA;
					TIFFSetField(tif, TIFFTAG_EXTRASAMPLES, 1, &si);
				}
			}
			/*
			 if (!ilResizeImage(Image, Image->Width, Image->Height, 1, 4, 1)) {
				 TIFFClose(tif);
				 return IL_FALSE;
			 }
			 */
			Image->Format = IL_RGBA;
			Image->Type = IL_UNSIGNED_BYTE;

			// Siigron: added u_long cast to shut up compiler warning
			//2003-08-31: changed flag from 1 (exit on error) to 0 (keep decoding)
			//this lets me view text.tif, but can give crashes with unsupported
			//tiffs...
			//2003-09-04: keep flag 1 for official version for now
			if (!TIFFReadRGBAImage(tif, Image->Width, Image->Height, (uint32*)Image->Data, 0)) {
				TIFFClose(tif);
				ilSetError(IL_LIB_TIFF_ERROR);
				return IL_FALSE;
			}
			Image->Origin = IL_ORIGIN_LOWER_LEFT;  // eiu...dunno if this is right

#ifdef __BIG_ENDIAN__ //TIFFReadRGBAImage reads abgr on big endian, convert to rgba
			EndianSwapData(Image);
#endif

			/*
			 The following switch() should not be needed,
			 because we take care of the special cases that needed
			 these conversions. But since not all special cases
			 are handled right now, keep it :)
			 */
			//TODO: put switch into the loop??
			TempImage = iCurImage;
			iCurImage = Image; //ilConvertImage() converts iCurImage
			switch (samplesperpixel)
			{
				case 1:
					//added 2003-08-31 to keep palettized tiffs colored
					if(photometric != 3)
						ilConvertImage(IL_LUMINANCE, IL_UNSIGNED_BYTE);
					else //strip alpha as tiff supports no alpha palettes
						ilConvertImage(IL_RGB, IL_UNSIGNED_BYTE);
					break;

				case 3:
					//TODO: why the ifdef??
#ifdef __LITTLE_ENDIAN__
					ilConvertImage(IL_RGB, IL_UNSIGNED_BYTE);
#endif			
					break; 

				case 4:
					pImageData = Image->Data;
					//removed on 2003-08-26...why was this here? libtiff should and does
					//take care of these things???
					/*			
					//invert alpha
#ifdef __LITTLE_ENDIAN__
					pImageData += 3;
#endif			
					for (i = Image->Width * Image->Height; i > 0; i--) {
						*pImageData ^= 255;
						pImageData += 4;
					}
					*/
					break;
			}
			iCurImage = TempImage;

		} //else not directly supported format

		if (TIFFGetField(tif, TIFFTAG_ICCPROFILE, &ProfileLen, &Buffer)) {
			if (Image->Profile && Image->ProfileSize)
				ifree(Image->Profile);
			Image->Profile = (ILubyte*)ialloc(ProfileLen);
			if (Image->Profile == NULL) {
				TIFFClose(tif);
				return IL_FALSE;
			}

			memcpy(Image->Profile, Buffer, ProfileLen);
			Image->ProfileSize = ProfileLen;

			//removed on 2003-08-24 as explained in bug 579574 on sourceforge
			//_TIFFfree(Buffer);
		}

                // dangelo: read offset from tiff tags.
                //If nothing is stored in these tags, then this must be an "uncropped" TIFF 
                //file, in which case, the "full size" width/height is the same as the 
                //"cropped" width and height
                //
                // the full size is currently not supported by DevIL
                //if (TIFFGetField(tif, TIFFTAG_PIXAR_IMAGEFULLWIDTH, &(c->full_width)) ==
                //        0)
                //    (c->full_width = c->cropped_width);
                //if (TIFFGetField(tif, TIFFTAG_PIXAR_IMAGEFULLLENGTH, &(c->full_height)) ==
                //        0)
                //    (c->full_height = c->cropped_height);

                if (TIFFGetField(tif, TIFFTAG_XPOSITION, &x_position) == 0)
                    x_position = 0;
                if (TIFFGetField(tif, TIFFTAG_XRESOLUTION, &x_resolution) == 0)
                    x_resolution = 0;
                if (TIFFGetField(tif, TIFFTAG_YPOSITION, &y_position) == 0)
                    y_position = 0;
                if (TIFFGetField(tif, TIFFTAG_YRESOLUTION, &y_resolution) == 0)
                    y_resolution = 0;

                //offset in pixels of "cropped" image from top left corner of 
                //full image (rounded to nearest integer)
                Image->OffX = (uint32) ((x_position * x_resolution) + 0.49);
                Image->OffY = (uint32) ((y_position * y_resolution) + 0.49);


		/*
		 Image = Image->Next;
		 if (Image == NULL)  // Should never happen except when we reach the end, but check anyway.
		 break;
		 */ 
	} //for tiff directories

	TIFFClose(tif);

	return ilFixImage();
}

static Status libtiff_process(TiffDecHandle handle, const TiffDecInput* in, TiffDecOutput* out) {
    tiff_dec_libtiff_t* state = (tiff_dec_libtiff_t*)handle;

    state->data->msg.clear();

    TiffStream stream;
    stream.data = (char*)in->buffer;
    stream.pos = 0;
    stream.size = in->size;

    TIFF* inTiff = TIFFClientOpen("Memory", "r", (thandle_t)&stream, tiff_Read, tiff_Write, tiff_Seek, tiff_Close,
                                  tiff_Size, tiff_Map, tiff_Unmap);

    uint32 width = 0;
    uint32 height = 0;
    uint16 bitsPerSample = 0;
    uint16 nsamples = 0;
    uint16 photometric = 0;
    uint16 subsampling[2] = {0, 0};

    TIFFGetField(inTiff, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(inTiff, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(inTiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
    TIFFGetField(inTiff, TIFFTAG_SAMPLESPERPIXEL, &nsamples);
    TIFFGetField(inTiff, TIFFTAG_PHOTOMETRIC, &photometric);

    if (bitsPerSample != 16) {
        state->data->msg = "Unsupported bit-depth: " + std::to_string(bitsPerSample);
        TIFFClose(inTiff);
        return STATUS_ERROR;
    }

    TiffFormat format;
    if (PHOTOMETRIC_RGB == photometric) {
        format = TIFF_FORMAT_RGB48LE;
    }
    else if (PHOTOMETRIC_YCBCR == photometric) {
        TIFFGetField(inTiff, TIFFTAG_YCBCRSUBSAMPLING, &subsampling[0], &subsampling[1]);
        if (1 == subsampling[0] && 1 == subsampling[1]) {
            format = TIFF_FORMAT_YUV444P16LE;
        }
        else {
            state->data->msg =
                "Unsupported subsampling: " + std::to_string(subsampling[0]) + "," + std::to_string(subsampling[1]);
            TIFFClose(inTiff);
            return STATUS_ERROR;
        }
    }
    else {
        state->data->msg = "Unsupported photometric interpretation: " + std::to_string(photometric);
        TIFFClose(inTiff);
        return STATUS_ERROR;
    }
    const int maxPlaneNum = 4;
    uint64_t buffer_size = width * height * maxPlaneNum;
    if (!state->data->outputBuffer)
        state->data->outputBuffer = new uint16_t[buffer_size];
    if (!state->data->lineBuffer)
        state->data->lineBuffer = new uint16_t[buffer_size];

    uint64_t pixNum = width * height;
    memset(state->data->outputBuffer, 0, buffer_size);
    uint16* plane[maxPlaneNum];
    for (int i = 0; i < maxPlaneNum; i++) {
        plane[i] = &state->data->outputBuffer[i * pixNum];
    }

    out->buffer[0] = (void*)plane[0];
    out->buffer[1] = (void*)plane[1];
    out->buffer[2] = (void*)plane[2];

    uint16_t* line = state->data->lineBuffer;
    for (uint32 row = 0; row < height; row++) {
        if (!TIFFReadScanline(inTiff, line, row)) {
            state->data->msg = "TIFFReadScanline failed";
            TIFFClose(inTiff);
            return STATUS_ERROR;
        }

        for (uint32 i = 0; i < width; i++) {
            for (uint32 s = 0; s < nsamples; s++) {
                *(plane[s]++) = line[(i*nsamples)+s];
            }
        }
    }

    out->width = width;
    out->height = height;
    out->format = format;

    TIFFClose(inTiff);
    return STATUS_OK;
}

JNIEXPORT void JNICALL Java_freed_jni_DngStack_startStack(JNIEnv *env, jobject thiz, jobjectArray filesToStack, jstring outputfile)
{
    int stringCount = (*env).GetArrayLength(filesToStack);
    int width,height, outputcount;
    const char * files[stringCount];
    const char * outfile =(*env).GetStringUTFChars( outputfile, NULL);
    unsigned short tmpPixel, bitdeep,bitdeeptemp;
    unsigned char * rawOutputData;
    unsigned char* cfa= new unsigned char[4];
    unsigned char* cfatmp= new unsigned char[4];
    float* cmat1 = new float[9];
    float * cmat2 = new float[9];
    float * neutMat = new float[3];
    float * fmat1= new float[9];
    float * fmat2= new float[9];
    float * calib1= new float[9];
    float * calib2= new float[9];
    double *noisemat  = new double[6];
    float *blackleveltmp;
    short blacklevel;
    float * tmpmat = new float[9];
    double * tmpdouble = new double[6];
    long * whitelvl;
    long white;

    //short * whitelvltmp;
    //short whitelvl;
    unsigned char * inbuf;
    /*unsigned char * opcodetmp;
    unsigned char * opcode2;
    unsigned char * opcode3;*/
    LOGD("FilesToReadCount: %i", stringCount);
    for (int i=0; i<stringCount; i++) {
        jstring string = (jstring) (*env).GetObjectArrayElement(filesToStack, i);
        files[i] = (*env).GetStringUTFChars( string, NULL);
    }


    TIFF *tif=TIFFOpen(files[0], "rw");
    //read needed dng tags
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    LOGD("GetWidth");
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    LOGD("GetHeight");
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitdeeptemp);
    LOGD("GetBitDeep");
    bitdeep = bitdeeptemp;
    TIFFGetField(tif, TIFFTAG_COLORMATRIX1,9, &tmpmat);
    LOGD("cc1");
    moveToMem(tmpmat, cmat1,9);
    TIFFGetField(tif, TIFFTAG_COLORMATRIX2,9, &tmpmat);
    LOGD("cc2");
    moveToMem(tmpmat, cmat2,9);
    TIFFGetField(tif, TIFFTAG_ASSHOTNEUTRAL,3, &tmpmat);
    LOGD("neutral");
    moveToMem(tmpmat, neutMat,3);
    TIFFGetField(tif, TIFFTAG_FOWARDMATRIX1,9, &tmpmat);
    moveToMem(tmpmat, fmat1,9);
    TIFFGetField(tif, TIFFTAG_FOWARDMATRIX2,9, &tmpmat);
    moveToMem(tmpmat, fmat2,9);
    TIFFGetField(tif, TIFFTAG_CAMERACALIBRATION1,9, &tmpmat);
    moveToMem(tmpmat, calib1,9);
    TIFFGetField(tif, TIFFTAG_CAMERACALIBRATION2,9, &tmpmat);
    moveToMem(tmpmat, calib2,9);
    TIFFGetField(tif, TIFFTAG_NOISEPROFILE,6, &tmpdouble);
    for (int i = 0; i < 6; ++i) {
        noisemat[i] = tmpdouble[i];
    }
    TIFFGetField(tif, TIFFTAG_CFAPATTERN, &cfatmp);
    for (int i = 0; i < 4; ++i) {
        cfa[i] = cfatmp[i];
    }
    LOGD("cfa pattern %c%c&c&c", cfa[0],cfa[1],cfa[2],cfa[3]);

    TIFFGetField(tif, TIFFTAG_WHITELEVEL, &whitelvl);
    LOGD("whitelvl:%i" , whitelvl[0]);
    white = whitelvl[0];
    LOGD("whitelvl:%i" , white);
    //whitelvl = whitelvltmp[0];
    TIFFGetField(tif, TIFFTAG_BLACKLEVEL, &blackleveltmp);
    /*TIFFGetField(tif, TIFFTAG_OPC2, &opcodetmp);
    opcode2 = opcodetmp;
    TIFFGetField(tif, TIFFTAG_OPC3, &opcodetmp);
    opcode3 = opcodetmp;*/
    blacklevel = blackleveltmp[0];

    rawOutputData = new unsigned char[((width*height)*16)/8];

    int scanlinesize = TIFFStripSize(tif);
    inbuf = (unsigned char*)_TIFFmalloc(scanlinesize);
    outputcount = 0;
    for (int row = 0; row < height; row++)
    {
        TIFFReadRawStrip(tif,row, inbuf, scanlinesize);
        if(bitdeep == 10)
        {
            for (int i = 0; i < scanlinesize; i+=5) {
                tmpPixel = (inbuf[i] << 2 | (inbuf[i+1] & 0b11000000) >> 6); //11111111 11
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = ((inbuf[i+1] & 0b00111111 ) << 4 | (inbuf[i+2] & 0b11110000) >> 4); // 222222 2222
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = ((inbuf[i+2]& 0b00001111 ) << 6 | (inbuf[i+3] & 0b11111100) >> 2); // 3333 333333
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = ((inbuf[i+3]& 0b00000011 ) << 8 | inbuf[i+4]); // 44 44444444
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;
            }
        }
        else if(bitdeep == 16)
        {
            for (int i = 0; i < scanlinesize; i+=8) {
                tmpPixel = (inbuf[i] | inbuf[i+1]<<8);
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = (inbuf[i+2] | inbuf[i+3]<<8);
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = (inbuf[i+4] | inbuf[i+5]<<8);
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;

                tmpPixel = (inbuf[i+6] | inbuf[i+7]<<8);
                rawOutputData[outputcount++] = tmpPixel & 0xff;
                rawOutputData[outputcount++] = tmpPixel >>8;
            }
        }
    }
    TIFFClose(tif);

    //read left dngs and merge them
    for (int i = 1; i < stringCount; ++i) {
        TIFF *tif=TIFFOpen(files[i], "rw");
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitdeeptemp);
        bitdeep = bitdeeptemp;
        outputcount = 0;
        for (int row = 0; row < height; row++)
        {
            TIFFReadRawStrip(tif,row, inbuf, scanlinesize);
            if(bitdeep == 10)
            {
                for (int i = 0; i < scanlinesize; i+=5) {
                    tmpPixel = (((inbuf[i] << 2 | (inbuf[i+1] & 0b11000000) >> 6)) + (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2; //11111111 11
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = ((((inbuf[i+1] & 0b00111111 ) << 4 | (inbuf[i+2] & 0b11110000) >> 4)) + (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2; // 222222 2222
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = ((((inbuf[i+2]& 0b00001111 )  | (inbuf[i+3] & 0b11111100) >> 2)) + (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2; // 3333 333333
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = ((((inbuf[i+3]& 0b00000011 ) << 8 | inbuf[i+4])) + (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2; // 44 44444444
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;
                }
            }
            else if(bitdeep == 16)
            {
                for (int i = 0; i < scanlinesize; i+=8) {
                    tmpPixel = (((inbuf[i] | inbuf[i+1]<<8))+ (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2;
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = (((inbuf[i+2] | inbuf[i+3]<<8))+ (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2;
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = (((inbuf[i+4] | inbuf[i+5]<<8))+ (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2;
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;

                    tmpPixel = (((inbuf[i+6] | inbuf[i+7]<<8))+ (rawOutputData[outputcount] | rawOutputData[outputcount+1]<<8))/2;
                    rawOutputData[outputcount++] = tmpPixel & 0xff;
                    rawOutputData[outputcount++] = tmpPixel >>8;
                }
            }
        }
        TIFFClose(tif);
    }
    free(inbuf);
    //create stacked dng
    tif=TIFFOpen(outfile, "w");

    TIFFSetField (tif, TIFFTAG_SUBFILETYPE, 0);
    TIFFSetField(tif, TIFFTAG_IMAGEWIDTH, width);
    TIFFSetField(tif, TIFFTAG_IMAGELENGTH, height);
    TIFFSetField(tif, TIFFTAG_BITSPERSAMPLE, 16);
    TIFFSetField(tif, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_CFA);
    TIFFSetField(tif, TIFFTAG_COMPRESSION, COMPRESSION_NONE);
    TIFFSetField (tif, TIFFTAG_SAMPLESPERPIXEL, 1);
    TIFFSetField(tif, TIFFTAG_PLANARCONFIG, PLANARCONFIG_CONTIG);
    TIFFSetField(tif, TIFFTAG_DNGVERSION, "\001\003\0\0");
    TIFFSetField(tif, TIFFTAG_DNGBACKWARDVERSION, "\001\001\0\0");
    TIFFSetField(tif, TIFFTAG_COLORMATRIX1, 9, cmat1);
    LOGD("colormatrix1");
    TIFFSetField(tif, TIFFTAG_COLORMATRIX2, 9, cmat2);
    TIFFSetField(tif, TIFFTAG_ASSHOTNEUTRAL, 3, neutMat);
    LOGD("neutralMatrix");
    if(fmat1 != NULL)
        TIFFSetField(tif, TIFFTAG_FOWARDMATRIX1, 9,  fmat1);
    if(fmat2 != NULL)
        TIFFSetField(tif, TIFFTAG_FOWARDMATRIX2, 9,  fmat2);
    if(calib1 != NULL)
        TIFFSetField(tif, TIFFTAG_CAMERACALIBRATION1, 9,  calib1);
    if(calib2 != NULL)
        TIFFSetField(tif, TIFFTAG_CAMERACALIBRATION2, 9,  calib2);
    if(noisemat != NULL)
        TIFFSetField(tif, TIFFTAG_NOISEPROFILE, 6,  noisemat);
    TIFFSetField(tif, TIFFTAG_CALIBRATIONILLUMINANT1, 17);
    TIFFSetField(tif, TIFFTAG_CALIBRATIONILLUMINANT2, 21);

    TIFFSetField(tif, TIFFTAG_ORIENTATION, ORIENTATION_TOPLEFT);

    TIFFSetField (tif, TIFFTAG_CFAPATTERN, cfa);
    LOGD("whitelvl:%i" , white);
    TIFFSetField (tif, TIFFTAG_WHITELEVEL,1, &white);

    short CFARepeatPatternDim[] = { 2,2 };
    TIFFSetField (tif, TIFFTAG_CFAREPEATPATTERNDIM, CFARepeatPatternDim);
    int bl = blacklevel;
    float *blacklevelar = new float[4];
    for (int i = 0; i < 4; ++i) {
        blacklevelar[i] = bl;
    }
    TIFFSetField (tif, TIFFTAG_BLACKLEVEL, 4, blacklevelar);
    LOGD("wrote blacklevel");
    TIFFSetField (tif, TIFFTAG_BLACKLEVELREPEATDIM, CFARepeatPatternDim);

    //TODO find out why OPCODE cause ueof ex in ps
    /*if(sizeof(opcode2)>0)
        TIFFSetField(tif,TIFFTAG_OPC2, sizeof(opcode2), opcode2);
    if(sizeof(opcode3)>0)
        TIFFSetField(tif,TIFFTAG_OPC3, sizeof(opcode3), opcode3);*/
    TIFFCheckpointDirectory(tif);

    TIFFWriteRawStrip(tif, 0, rawOutputData, ((width*height)*16)/8);

    TIFFRewriteDirectory(tif);

    //TIFFWriteRawStrip(tif, 0, rawOutputData, width*height);

    TIFFClose(tif);
    delete[] rawOutputData;
}

bool Image::initWithTiffData(const unsigned char * data, ssize_t dataLen)
{
#if CC_USE_TIFF
    bool ret = false;
    do 
    {
        // set the read call back function
        tImageSource imageSource;
        imageSource.data    = data;
        imageSource.size    = dataLen;
        imageSource.offset  = 0;

        TIFF* tif = TIFFClientOpen("file.tif", "r", (thandle_t)&imageSource, 
            tiffReadProc, tiffWriteProc,
            tiffSeekProc, tiffCloseProc, tiffSizeProc,
            tiffMapProc,
            tiffUnmapProc);

        CC_BREAK_IF(nullptr == tif);

        uint32 w = 0, h = 0;
        uint16 bitsPerSample = 0, samplePerPixel = 0, planarConfig = 0;
        size_t npixels = 0;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplePerPixel);
        TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarConfig);

        npixels = w * h;

        _renderFormat = Texture2D::PixelFormat::RGBA8888;
        _width = w;
        _height = h;

        _dataLen = npixels * sizeof (uint32);
        _data = static_cast<unsigned char*>(malloc(_dataLen * sizeof(unsigned char)));

        uint32* raster = (uint32*) _TIFFmalloc(npixels * sizeof (uint32));
        if (raster != nullptr) 
        {
           if (TIFFReadRGBAImageOriented(tif, w, h, raster, ORIENTATION_TOPLEFT, 0))
           {
                /* the raster data is pre-multiplied by the alpha component 
                   after invoking TIFFReadRGBAImageOriented*/
                _hasPremultipliedAlpha = true;

               memcpy(_data, raster, npixels*sizeof (uint32));
           }

          _TIFFfree(raster);
        }


        TIFFClose(tif);

        ret = true;
    } while (0);
    return ret;
#else
    CCLOG("tiff is not enabled");
    return false;
#endif //CC_USE_TIFF
}

sensor_msgs::ImagePtr
loadTiff(const std::string& file)
{
    using	namespace sensor_msgs;

    TIFF* const	tiff = TIFFOpen(file.c_str(), "r");
    if (!tiff)
	throw std::runtime_error("loadTiff(): cannot open file[" + file + ']');

    uint32	width, height;
    uint16	bitsPerSample, samplesPerPixel, photometric;

    if (!TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH,	     &width)		||
	!TIFFGetField(tiff, TIFFTAG_IMAGELENGTH,     &height)		||
	!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE,   &bitsPerSample)	||
	!TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel)	||
	!TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC,     &photometric))
    {
	throw std::runtime_error("loadTiff(): cannot get necessary fields");
    }

    sensor_msgs::ImagePtr	image(new sensor_msgs::Image());
    image->width  = width;
    image->height = height;

    switch (photometric)
    {
      case PHOTOMETRIC_MINISBLACK:
	switch(bitsPerSample)
	{
	  case 8:
	    image->encoding = image_encodings::MONO8;
	    break;
	  case 16:
	    image->encoding = image_encodings::MONO16;
	    break;
	  case 32:
	    switch (samplesPerPixel)
	    {
	      case 1:
		image->encoding = image_encodings::TYPE_32FC1;
		break;
	      case 2:
		image->encoding = image_encodings::TYPE_32FC2;
		break;
	      case 3:
		image->encoding = image_encodings::TYPE_32FC3;
		break;
	      case 4:
		image->encoding = image_encodings::TYPE_32FC4;
		break;
	      default:
		throw std::runtime_error("loadTiff(): unsupported #samples per pixel["
					 + std::to_string(samplesPerPixel)
					 + "] under #bits per sample["
					 + std::to_string(bitsPerSample)
					 + ']');
	    }
	    break;
	  case 64:
	    switch (samplesPerPixel)
	    {
	      case 1:
		image->encoding = image_encodings::TYPE_64FC1;
		break;
	      case 2:
		image->encoding = image_encodings::TYPE_64FC2;
		break;
	      case 3:
		image->encoding = image_encodings::TYPE_64FC3;
		break;
	      case 4:
		image->encoding = image_encodings::TYPE_64FC4;
		break;
	      default:
		throw std::runtime_error("loadTiff(): unsupported #samples per pixel["
					 + std::to_string(samplesPerPixel)
					 + "] under #bits per sample["
					 + std::to_string(bitsPerSample)
					 + ']');
	    }
	    break;
	  default:
	    throw std::runtime_error("loadTiff(): unsupported #bits per sample["
				     + std::to_string(bitsPerSample) + ']');
	}
	break;

      case PHOTOMETRIC_RGB:
	switch(bitsPerSample)
	{
	  case 8:
	    image->encoding = image_encodings::RGB8;
	    break;
	  case 16:
	    image->encoding = image_encodings::RGB16;
	    break;
	  default:
	    throw std::runtime_error("loadTiff(): unsupported #bits per sample["
				     + std::to_string(bitsPerSample) + ']');
	}
	break;

      default:
	throw std::runtime_error("loadTiff(): unsupported photometic["
				 + std::to_string(photometric) + ']');
    }

    image->step		= image->width * bitsPerSample * samplesPerPixel / 8;
    image->is_bigendian = false;
    image->data.resize(image->height * image->step);

    const auto	nBytesPerStrip = TIFFStripSize(tiff);
    const auto	nStrips        = TIFFNumberOfStrips(tiff);

    for (int n = 0, offset = 0; n < nStrips; ++n)
    {
	const auto nBytes = TIFFReadEncodedStrip(tiff, n,
						 image->data.data() + offset,
						 nBytesPerStrip);
	if (nBytes < 0)
	    throw std::runtime_error("loadTiff(): failed to read strip");

	offset += nBytes;
    }

    TIFFClose(tiff);

    return image;
}

int ImageMatrix::LoadTIFF(char *filename) {
	unsigned int h,w,x=0,y=0;
	unsigned short int spp=0,bps=0;
	TIFF *tif = NULL;
	unsigned char *buf8;
	unsigned short *buf16;
	RGBcolor rgb = {0,0,0};
	ImageMatrix R_matrix, G_matrix, B_matrix;
	Moments2 R_stats, G_stats, B_stats;

	TIFFSetWarningHandler(NULL);
	if( (tif = TIFFOpen(filename, "r")) ) {
		source = filename;

		TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
		width = w;
		TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
		height = h;
		TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bps);
		bits=bps;
		if ( ! (bits == 8 || bits == 16) ) return (0); // only 8 and 16-bit images supported.
		TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp);
		if (!spp) spp=1;  /* assume one sample per pixel if nothing is specified */
		// regardless of how the image comes in, the stored mode is HSV
		if (spp == 3) {
			ColorMode = cmHSV;
			// If the bits are > 8, we do the read into doubles so that later
			// we can scale the image to its actual signal range.
			if (bits > 8) {
				R_matrix.ColorMode = cmGRAY;
				R_matrix.allocate (width, height);
				G_matrix.ColorMode = cmGRAY;
				G_matrix.allocate (width, height);
				B_matrix.ColorMode = cmGRAY;
				B_matrix.allocate (width, height);
			}
		} else {
			ColorMode = cmGRAY;
		}
		if ( TIFFNumberOfDirectories(tif) > 1) return(0);   /* get the number of slices (Zs) */

		/* allocate the data */
		allocate (width, height);
		writeablePixels pix_plane = WriteablePixels();
		writeableColors clr_plane = WriteableColors();

		/* read TIFF header and determine image size */
		buf8 = (unsigned char *)_TIFFmalloc(TIFFScanlineSize(tif)*spp);
		buf16 = (unsigned short *)_TIFFmalloc( (tsize_t)sizeof(unsigned short)*TIFFScanlineSize(tif)*spp );
		for (y = 0; y < height; y++) {
			int col;
			if (bits==8) TIFFReadScanline(tif, buf8, y);
			else TIFFReadScanline(tif, buf16, y);
			x=0;col=0;
			while (x<width) {
				unsigned char byte_data;
				unsigned short short_data;
				double val=0;
				int sample_index;
				for (sample_index=0;sample_index<spp;sample_index++) {
					byte_data=buf8[col+sample_index];
					short_data=buf16[col+sample_index];
					if (bits==8) val=(double)byte_data;
					else val=(double)(short_data);
					if (spp==3 && bits > 8) {  /* RGB image */
						if (sample_index==0) R_matrix.WriteablePixels()(y,x) = R_stats.add (val);
						if (sample_index==1) G_matrix.WriteablePixels()(y,x) = G_stats.add (val);
						if (sample_index==2) B_matrix.WriteablePixels()(y,x) = B_stats.add (val);
					} else if (spp == 3) {
						if (sample_index==0) rgb.r = (unsigned char)(R_stats.add (val));
						if (sample_index==1) rgb.g = (unsigned char)(G_stats.add (val));
						if (sample_index==2) rgb.b = (unsigned char)(B_stats.add (val));
					}
				}
				if (spp == 3 && bits == 8) {
					pix_plane (y, x) = stats.add (RGB2GRAY (rgb));
					clr_plane (y, x) = RGB2HSV(rgb);
				} else if (spp == 1) {
					pix_plane (y, x) = stats.add (val);
				}
				x++;
				col+=spp;
			}
		}
		// Do the conversion to unsigned chars based on the input signal range
		// i.e. scale global RGB min-max to 0-255
		if (spp == 3 && bits > 8) {
			//size_t a, num = width*height;
			double RGB_min=0, RGB_max=0, RGB_scale=0;
			R_matrix.finish();
			G_matrix.finish();
			B_matrix.finish();
			// Get the min and max for all 3 channels
			if (R_stats.min() <= G_stats.min() && R_stats.min() <= B_stats.min()) RGB_min = R_stats.min();
			else if (G_stats.min() <= R_stats.min() && G_stats.min() <= B_stats.min()) RGB_min = G_stats.min();
			else if (B_stats.min() <= R_stats.min() && B_stats.min() <= G_stats.min()) RGB_min = B_stats.min();
			if (R_stats.max() >= G_stats.max() && R_stats.max() >= B_stats.max()) RGB_max = R_stats.max();
			else if (G_stats.max() >= R_stats.max() && G_stats.max() >= B_stats.max()) RGB_max = G_stats.max();
			else if (B_stats.max() >= R_stats.max() && B_stats.max() >= G_stats.max()) RGB_max = B_stats.max();
			// Scale the clrData to the global min / max.
			RGB_scale = (255.0/(RGB_max-RGB_min));
			for (y = 0; y < height; y++) {
				for (x = 0; x < width; x++) {
					rgb.r = (unsigned char)( (R_matrix.ReadablePixels()(y,x) - RGB_min) * RGB_scale);
					rgb.g = (unsigned char)( (G_matrix.ReadablePixels()(y,x) - RGB_min) * RGB_scale);
					rgb.b = (unsigned char)( (B_matrix.ReadablePixels()(y,x) - RGB_min) * RGB_scale);
					pix_plane (y, x) = stats.add (RGB2GRAY (rgb));
					clr_plane (y, x) = RGB2HSV(rgb);
				}
			}
		}
		_TIFFfree(buf8);
		_TIFFfree(buf16);
		TIFFClose(tif);

	} else return(0);

	return(1);
}

unsigned char *
simage_tiff_load(std::istream& fin,
                 int& width_ret,
                 int& height_ret,
                 int& numComponents_ret,
                 uint16& bitspersample)
{
    TIFF *in;
    uint16 dataType;
    uint16 samplesperpixel;
    uint16 photometric;
    uint32 w, h;
    uint16 config;
    uint16* red;
    uint16* green;
    uint16* blue;
    unsigned char *inbuf = NULL;
    tsize_t rowsize;
    uint32 row;
    int format;
    unsigned char *buffer;
    int width;
    int height;
    unsigned char *currPtr;

    TIFFSetErrorHandler(tiff_error);
    TIFFSetWarningHandler(tiff_warn);

    in = TIFFClientOpen("inputstream", "r", (thandle_t)&fin,
            libtiffStreamReadProc, //Custom read function
            libtiffStreamWriteProc, //Custom write function
            libtiffStreamSeekProc, //Custom seek function
            libtiffStreamCloseProc, //Custom close function
            libtiffStreamSizeProc, //Custom size function
            libtiffStreamMapProc, //Custom map function
            libtiffStreamUnmapProc); //Custom unmap function

    if (in == NULL)
    {
        tifferror = ERR_OPEN;
        return NULL;
    }
    if (TIFFGetField(in, TIFFTAG_PHOTOMETRIC, &photometric) == 1)
    {
        if (photometric != PHOTOMETRIC_RGB && photometric != PHOTOMETRIC_PALETTE &&
            photometric != PHOTOMETRIC_MINISWHITE &&
            photometric != PHOTOMETRIC_MINISBLACK)
        {
            OSG_NOTICE << "Photometric type "<<photometric<<" not handled; can only handle Grayscale, RGB and Palette images" << std::endl;
            TIFFClose(in);
            tifferror = ERR_UNSUPPORTED;
            return NULL;
        }
    }
    else
    {
        tifferror = ERR_READ;
        TIFFClose(in);
        return NULL;
    }

    if (TIFFGetField(in, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel) == 1)
    {
        if (samplesperpixel != 1 &&
            samplesperpixel != 2 &&
            samplesperpixel != 3 &&
            samplesperpixel != 4)
        {
            OSG_DEBUG << "Bad samples/pixel" << std::endl;
            tifferror = ERR_UNSUPPORTED;
            TIFFClose(in);
            return NULL;
        }
    }
    else
    {
        tifferror = ERR_READ;
        TIFFClose(in);
        return NULL;
    }

    if (TIFFGetField(in, TIFFTAG_BITSPERSAMPLE, &bitspersample) == 1)
    {
         if (bitspersample != 8 && bitspersample != 16 && bitspersample != 32)
        {
            OSG_NOTICE << "can only handle 8, 16 and 32 bit samples" << std::endl;
            TIFFClose(in);
            tifferror = ERR_UNSUPPORTED;
            return NULL;
        }
    }
    else
    {
        tifferror = ERR_READ;
        TIFFClose(in);
        return NULL;
    }

    if (TIFFGetField(in, TIFFTAG_IMAGEWIDTH, &w) != 1 ||
        TIFFGetField(in, TIFFTAG_IMAGELENGTH, &h) != 1 ||
        TIFFGetField(in, TIFFTAG_PLANARCONFIG, &config) != 1)
    {
        TIFFClose(in);
        tifferror = ERR_READ;
        return NULL;
    }


    TIFFGetField(in, TIFFTAG_DATATYPE, &dataType);
    OSG_INFO<<"TIFFTAG_DATATYPE="<<dataType<<std::endl;


    /*
    if (photometric == PHOTOMETRIC_MINISWHITE ||
        photometric == PHOTOMETRIC_MINISBLACK)
        format = 1;
    else
        format = 3;
    */
    // if it has a palette, data returned is 3 byte rgb
    // so set format to 3.
    if (photometric == PHOTOMETRIC_PALETTE)
        format = 3;
    else
        format = samplesperpixel * bitspersample / 8;


    int bytespersample = bitspersample / 8;
    int bytesperpixel = bytespersample * samplesperpixel;

    OSG_INFO<<"format="<<format<<std::endl;
    OSG_INFO<<"bytespersample="<<bytespersample<<std::endl;
    OSG_INFO<<"bytesperpixel="<<bytesperpixel<<std::endl;

    buffer = new unsigned char [w*h*format];

    if (!buffer)
    {
        tifferror = ERR_MEM;
        TIFFClose(in);
        return NULL;
    }

    // initialize memory
    for(unsigned char* ptr=buffer;ptr<buffer+w*h*format;++ptr) *ptr = 0;

    width = w;
    height = h;

    currPtr = buffer + (h-1)*w*format;

    tifferror = ERR_NO_ERROR;

    switch (pack(photometric, config))
    {
        case pack(PHOTOMETRIC_MINISWHITE, PLANARCONFIG_CONTIG):
        case pack(PHOTOMETRIC_MINISBLACK, PLANARCONFIG_CONTIG):
        case pack(PHOTOMETRIC_MINISWHITE, PLANARCONFIG_SEPARATE):
        case pack(PHOTOMETRIC_MINISBLACK, PLANARCONFIG_SEPARATE):
            inbuf = new unsigned char [TIFFScanlineSize(in)];
            for (row = 0; row < h; row++)
            {
                if (TIFFReadScanline(in, inbuf, row, 0) < 0)
                {
                    tifferror = ERR_READ;
                    break;
                }
                invert_row(currPtr, inbuf, samplesperpixel*w, photometric == PHOTOMETRIC_MINISWHITE, bitspersample);
                currPtr -= format*w;
            }
            break;

        case pack(PHOTOMETRIC_PALETTE, PLANARCONFIG_CONTIG):
        case pack(PHOTOMETRIC_PALETTE, PLANARCONFIG_SEPARATE):

            if (TIFFGetField(in, TIFFTAG_COLORMAP, &red, &green, &blue) != 1)
            {
                tifferror = ERR_READ;
                break;
            }

            /* */
            /* Convert 16-bit colormap to 8-bit (unless it looks */
            /* like an old-style 8-bit colormap). */
            /* */
            if (!tifferror && bitspersample!=32 && checkcmap(1<<bitspersample, red, green, blue) == 16)
            {
                int i;
                for (i = (1<<bitspersample)-1; i >= 0; i--)
                {
                    red[i] = CVT(red[i]);
                    green[i] = CVT(green[i]);
                    blue[i] = CVT(blue[i]);
                }
            }

            inbuf = new unsigned char [TIFFScanlineSize(in)];
            for (row = 0; row < h; row++)
            {
                if (TIFFReadScanline(in, inbuf, row, 0) < 0)
                {
                    tifferror = ERR_READ;
                    break;
                }
                remap_row(currPtr, inbuf, w, red, green, blue);
                currPtr -= format*w;
            }
            break;

        case pack(PHOTOMETRIC_RGB, PLANARCONFIG_CONTIG):
            inbuf = new unsigned char [TIFFScanlineSize(in)];
            for (row = 0; row < h; row++)
            {
                if (TIFFReadScanline(in, inbuf, row, 0) < 0)
                {
                    tifferror = ERR_READ;
                    break;
                }
                memcpy(currPtr, inbuf, format*w);
                currPtr -= format*w;
            }
            break;

        case pack(PHOTOMETRIC_RGB, PLANARCONFIG_SEPARATE):
            rowsize = TIFFScanlineSize(in);
            inbuf = new unsigned char [format*rowsize];
            for (row = 0; !tifferror && row < h; row++)
            {
                int s;
                for (s = 0; s < format; s++)
                {
                    if (TIFFReadScanline(in, (tdata_t)(inbuf+s*rowsize), (uint32)row, (tsample_t)s) < 0)
                    {
                        tifferror = ERR_READ; break;
                    }
                }
                if (!tifferror)
                {
                    if (format==3) interleave_row(currPtr, inbuf, inbuf+rowsize, inbuf+2*rowsize, w, format, bitspersample);
                    else if (format==4) interleave_row(currPtr, inbuf, inbuf+rowsize, inbuf+2*rowsize, inbuf+3*rowsize, w, format, bitspersample);
                    currPtr -= format*w;
                }
            }
            break;
        default:
            tifferror = ERR_UNSUPPORTED;
            break;
    }

    if (inbuf) delete [] inbuf;
    TIFFClose(in);

    if (tifferror)
    {
        if (buffer) delete [] buffer;
        return NULL;
    }
    width_ret = width;
    height_ret = height;
    if (photometric == PHOTOMETRIC_PALETTE)
        numComponents_ret = format;
    else
        numComponents_ret = samplesperpixel;

    return buffer;
}

IO::Info Read::_open(const std::string& fileName, File& f)
            {
#if defined(DJV_PLATFORM_WINDOWS)
                f.f = TIFFOpen(fileName.data(), "r");
#else
                f.f = TIFFOpen(fileName.data(), "r");
#endif // DJV_WINDOWS
                if (!f.f)
                {
                    throw System::File::Error(String::Format("{0}: {1}").
                        arg(fileName).
                        arg(_textSystem->getText(DJV_TEXT("error_file_open"))));
                }

                uint32   width            = 0;
                uint32   height           = 0;
                uint16   photometric      = 0;
                uint16   samples          = 0;
                uint16   sampleDepth      = 0;
                uint16   sampleFormat     = 0;
                uint16 * extraSamples     = nullptr;
                uint16   extraSamplesSize = 0;
                uint16   orient           = 0;
                uint16   compression      = 0;
                uint16   channels         = 0;
                TIFFGetFieldDefaulted(f.f, TIFFTAG_IMAGEWIDTH, &width);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_IMAGELENGTH, &height);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_PHOTOMETRIC, &photometric);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_SAMPLESPERPIXEL, &samples);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_BITSPERSAMPLE, &sampleDepth);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_SAMPLEFORMAT, &sampleFormat);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_EXTRASAMPLES, &extraSamplesSize, &extraSamples);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_ORIENTATION, &orient);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_COMPRESSION, &compression);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_PLANARCONFIG, &channels);
                TIFFGetFieldDefaulted(f.f, TIFFTAG_COLORMAP, &f.colormap[0], &f.colormap[1], &f.colormap[2]);

                Image::Type imageType = Image::Type::None;
                switch (photometric)
                {
                case PHOTOMETRIC_PALETTE:
                    imageType = Image::Type::RGB_U8;
                    break;
                case PHOTOMETRIC_MINISWHITE:
                case PHOTOMETRIC_MINISBLACK:
                case PHOTOMETRIC_RGB:
                    if (32 == sampleDepth && sampleFormat != SAMPLEFORMAT_IEEEFP)
                        break;
                    if (SAMPLEFORMAT_IEEEFP == sampleFormat)
                    {
                        imageType = Image::getFloatType(samples, sampleDepth);
                    }
                    else
                    {
                        imageType = Image::getIntType(samples, sampleDepth);
                    }
                    break;
                }
                if (Image::Type::None == imageType)
                {
                    throw System::File::Error(String::Format("{0}: {1}").
                        arg(fileName).
                        arg(_textSystem->getText(DJV_TEXT("error_unsupported_image_type"))));
                }

                Image::Layout layout;
                switch (orient)
                {
                case ORIENTATION_TOPLEFT:  break;
                case ORIENTATION_TOPRIGHT: layout.mirror.x = true; break;
                case ORIENTATION_BOTRIGHT: layout.mirror.x = layout.mirror.y = true; break;
                case ORIENTATION_BOTLEFT:  layout.mirror.y = true; break;
                }

                f.compression = compression != COMPRESSION_NONE;
                f.palette = PHOTOMETRIC_PALETTE == photometric;

                Image::Tags tags;
                char * tag = 0;
                if (TIFFGetField(f.f, TIFFTAG_ARTIST, &tag))
                {
                    if (tag)
                    {
                        tags.set("Creator", tag);
                    }
                }
                if (TIFFGetField(f.f, TIFFTAG_IMAGEDESCRIPTION, &tag))
                {
                    if (tag)
                    {
                        tags.set("Description", tag);
                    }
                }
                if (TIFFGetField(f.f, TIFFTAG_COPYRIGHT, &tag))
                {
                    if (tag)
                    {
                        tags.set("Copyright", tag);
                    }
                }
                if (TIFFGetField(f.f, TIFFTAG_DATETIME, &tag))
                {
                    if (tag)
                    {
                        tags.set("Time", tag);
                    }
                }

                IO::Info info;
                info.fileName = fileName;
                info.videoSpeed = _speed;
                info.videoSequence = _sequence;
                info.video.push_back(Image::Info(width, height, imageType, layout));
                info.tags = tags;
                return info;
            }

bool TiffDecoder::readHeader()
{
    bool result = false;

    TIFF* tif = static_cast<TIFF*>(m_tif.get());
    if (!tif)
    {
        // TIFFOpen() mode flags are different to fopen().  A 'b' in mode "rb" has no effect when reading.
        // http://www.remotesensing.org/libtiff/man/TIFFOpen.3tiff.html
        if ( !m_buf.empty() )
        {
            m_buf_pos = 0;
            TiffDecoderBufHelper* buf_helper = new TiffDecoderBufHelper(this->m_buf, this->m_buf_pos);
            tif = TIFFClientOpen( "", "r", reinterpret_cast<thandle_t>(buf_helper), &TiffDecoderBufHelper::read,
                                  &TiffDecoderBufHelper::write, &TiffDecoderBufHelper::seek,
                                  &TiffDecoderBufHelper::close, &TiffDecoderBufHelper::size,
                                  &TiffDecoderBufHelper::map, /*unmap=*/0 );
            if (!tif)
                delete buf_helper;
        }
        else
        {
            tif = TIFFOpen(m_filename.c_str(), "r");
        }
        if (tif)
            m_tif.reset(tif, cv_tiffCloseHandle);
        else
            m_tif.release();
    }

    if (tif)
    {
        uint32 wdth = 0, hght = 0;
        uint16 photometric = 0;

        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &wdth));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &hght));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric));

        {
            bool isGrayScale = photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK;
            uint16 bpp = 8, ncn = isGrayScale ? 1 : 3;
            CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp));
            CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &ncn));

            m_width = wdth;
            m_height = hght;
            if (ncn == 3 && photometric == PHOTOMETRIC_LOGLUV)
            {
                m_type = CV_32FC3;
                m_hdr = true;
                return true;
            }
            m_hdr = false;

            if( bpp > 8 &&
               ((photometric > 2) ||
                (ncn != 1 && ncn != 3 && ncn != 4)))
                bpp = 8;

            int wanted_channels = normalizeChannelsNumber(ncn);
            switch(bpp)
            {
                case 1:
                    m_type = CV_MAKETYPE(CV_8U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 8:
                    m_type = CV_MAKETYPE(CV_8U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 16:
                    m_type = CV_MAKETYPE(CV_16U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 32:
                    m_type = CV_MAKETYPE(CV_32F, wanted_channels);
                    result = true;
                    break;
                case 64:
                    m_type = CV_MAKETYPE(CV_64F, wanted_channels);
                    result = true;
                    break;
            default:
                CV_Error(cv::Error::StsError, "Invalid bitsperpixel value read from TIFF header! Must be 1, 8, 16, 32 or 64.");
            }
        }
    }

    if( !result )
        close();

    return result;
}

bool  TiffDecoder::readData( Mat& img )
{
    int type = img.type();
    int depth = CV_MAT_DEPTH(type);

    CV_Assert(!m_tif.empty());
    TIFF* tif = (TIFF*)m_tif.get();

    uint16 photometric = (uint16)-1;
    CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric));

    if (m_hdr && depth >= CV_32F)
    {
        CV_TIFF_CHECK_CALL(TIFFSetField(tif, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT));
    }

    bool color = img.channels() > 1;

    CV_CheckType(type, depth == CV_8U || depth == CV_16U || depth == CV_32F || depth == CV_64F, "");

    if (m_width && m_height)
    {
        int is_tiled = TIFFIsTiled(tif) != 0;
        bool isGrayScale = photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK;
        uint16 bpp = 8, ncn = isGrayScale ? 1 : 3;
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp));
        CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &ncn));
        uint16 img_orientation = ORIENTATION_TOPLEFT;
        CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_ORIENTATION, &img_orientation));
        const int bitsPerByte = 8;
        int dst_bpp = (int)(img.elemSize1() * bitsPerByte);
        bool vert_flip = dst_bpp == 8 &&
                        (img_orientation == ORIENTATION_BOTRIGHT || img_orientation == ORIENTATION_RIGHTBOT ||
                         img_orientation == ORIENTATION_BOTLEFT || img_orientation == ORIENTATION_LEFTBOT);
        int wanted_channels = normalizeChannelsNumber(img.channels());

        if (dst_bpp == 8)
        {
            char errmsg[1024];
            if (!TIFFRGBAImageOK(tif, errmsg))
            {
                CV_LOG_WARNING(NULL, "OpenCV TIFF: TIFFRGBAImageOK: " << errmsg);
                close();
                return false;
            }
        }

        uint32 tile_width0 = m_width, tile_height0 = 0;

        if (is_tiled)
        {
            CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tile_width0));
            CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_TILELENGTH, &tile_height0));
        }
        else
        {
            // optional
            CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &tile_height0));
        }

        {
            if (tile_width0 == 0)
                tile_width0 = m_width;

            if (tile_height0 == 0 ||
                    (!is_tiled && tile_height0 == std::numeric_limits<uint32>::max()) )
                tile_height0 = m_height;

            const int TILE_MAX_WIDTH = (1 << 24);
            const int TILE_MAX_HEIGHT = (1 << 24);
            CV_Assert((int)tile_width0 > 0 && (int)tile_width0 <= TILE_MAX_WIDTH);
            CV_Assert((int)tile_height0 > 0 && (int)tile_height0 <= TILE_MAX_HEIGHT);
            const uint64_t MAX_TILE_SIZE = (CV_BIG_UINT(1) << 30);
            CV_CheckLE((int)ncn, 4, "");
            CV_CheckLE((int)bpp, 64, "");
            CV_Assert(((uint64_t)tile_width0 * tile_height0 * ncn * std::max(1, (int)(bpp / bitsPerByte)) < MAX_TILE_SIZE) && "TIFF tile size is too large: >= 1Gb");

            if (dst_bpp == 8)
            {
                // we will use TIFFReadRGBA* functions, so allocate temporary buffer for 32bit RGBA
                bpp = 8;
                ncn = 4;
            }
            else if (dst_bpp == 32 || dst_bpp == 64)
            {
                CV_Assert(ncn == img.channels());
                CV_TIFF_CHECK_CALL(TIFFSetField(tif, TIFFTAG_SAMPLEFORMAT, SAMPLEFORMAT_IEEEFP));
            }
            const size_t buffer_size = (bpp / bitsPerByte) * ncn * tile_height0 * tile_width0;
            AutoBuffer<uchar> _buffer(buffer_size);
            uchar* buffer = _buffer.data();
            ushort* buffer16 = (ushort*)buffer;
            int tileidx = 0;

            for (int y = 0; y < m_height; y += (int)tile_height0)
            {
                int tile_height = std::min((int)tile_height0, m_height - y);

                const int img_y = vert_flip ? m_height - y - tile_height : y;

                for(int x = 0; x < m_width; x += (int)tile_width0, tileidx++)
                {
                    int tile_width = std::min((int)tile_width0, m_width - x);

                    switch (dst_bpp)
                    {
                        case 8:
                        {
                            uchar* bstart = buffer;
                            if (!is_tiled)
                            {
                                CV_TIFF_CHECK_CALL(TIFFReadRGBAStrip(tif, y, (uint32*)buffer));
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL(TIFFReadRGBATile(tif, x, y, (uint32*)buffer));
                                // Tiles fill the buffer from the bottom up
                                bstart += (tile_height0 - tile_height) * tile_width0 * 4;
                            }

                            for (int i = 0; i < tile_height; i++)
                            {
                                if (color)
                                {
                                    if (wanted_channels == 4)
                                    {
                                        icvCvt_BGRA2RGBA_8u_C4R(bstart + i*tile_width0*4, 0,
                                                img.ptr(img_y + tile_height - i - 1, x), 0,
                                                Size(tile_width, 1) );
                                    }
                                    else
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "TIFF-8bpp: BGR/BGRA images are supported only");
                                        icvCvt_BGRA2BGR_8u_C4C3R(bstart + i*tile_width0*4, 0,
                                                img.ptr(img_y + tile_height - i - 1, x), 0,
                                                Size(tile_width, 1), 2);
                                    }
                                }
                                else
                                {
                                    CV_CheckEQ(wanted_channels, 1, "");
                                    icvCvt_BGRA2Gray_8u_C4C1R( bstart + i*tile_width0*4, 0,
                                            img.ptr(img_y + tile_height - i - 1, x), 0,
                                            Size(tile_width, 1), 2);
                                }
                            }
                            break;
                        }

                        case 16:
                        {
                            if (!is_tiled)
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedStrip(tif, tileidx, (uint32*)buffer, buffer_size) >= 0);
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedTile(tif, tileidx, (uint32*)buffer, buffer_size) >= 0);
                            }

                            for (int i = 0; i < tile_height; i++)
                            {
                                if (color)
                                {
                                    if (ncn == 1)
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "");
                                        icvCvt_Gray2BGR_16u_C1C3R(buffer16 + i*tile_width0*ncn, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                    }
                                    else if (ncn == 3)
                                    {
                                        CV_CheckEQ(wanted_channels, 3, "");
                                        icvCvt_RGB2BGR_16u_C3R(buffer16 + i*tile_width0*ncn, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                    }
                                    else if (ncn == 4)
                                    {
                                        if (wanted_channels == 4)
                                        {
                                            icvCvt_BGRA2RGBA_16u_C4R(buffer16 + i*tile_width0*ncn, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1));
                                        }
                                        else
                                        {
                                            CV_CheckEQ(wanted_channels, 3, "TIFF-16bpp: BGR/BGRA images are supported only");
                                            icvCvt_BGRA2BGR_16u_C4C3R(buffer16 + i*tile_width0*ncn, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1), 2);
                                        }
                                    }
                                    else
                                    {
                                        CV_Error(Error::StsError, "Not supported");
                                    }
                                }
                                else
                                {
                                    CV_CheckEQ(wanted_channels, 1, "");
                                    if( ncn == 1 )
                                    {
                                        memcpy(img.ptr<ushort>(img_y + i, x),
                                               buffer16 + i*tile_width0*ncn,
                                               tile_width*sizeof(ushort));
                                    }
                                    else
                                    {
                                        icvCvt_BGRA2Gray_16u_CnC1R(buffer16 + i*tile_width0*ncn, 0,
                                                img.ptr<ushort>(img_y + i, x), 0,
                                                Size(tile_width, 1), ncn, 2);
                                    }
                                }
                            }
                            break;
                        }

                        case 32:
                        case 64:
                        {
                            if( !is_tiled )
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedStrip(tif, tileidx, buffer, buffer_size) >= 0);
                            }
                            else
                            {
                                CV_TIFF_CHECK_CALL((int)TIFFReadEncodedTile(tif, tileidx, buffer, buffer_size) >= 0);
                            }

                            Mat m_tile(Size(tile_width0, tile_height0), CV_MAKETYPE((dst_bpp == 32) ? CV_32F : CV_64F, ncn), buffer);
                            Rect roi_tile(0, 0, tile_width, tile_height);
                            Rect roi_img(x, img_y, tile_width, tile_height);
                            if (!m_hdr && ncn == 3)
                                cvtColor(m_tile(roi_tile), img(roi_img), COLOR_RGB2BGR);
                            else if (!m_hdr && ncn == 4)
                                cvtColor(m_tile(roi_tile), img(roi_img), COLOR_RGBA2BGRA);
                            else
                                m_tile(roi_tile).copyTo(img(roi_img));
                            break;
                        }
                        default:
                        {
                            CV_Assert(0 && "OpenCV TIFF: unsupported depth");
                        }
                    }  // switch (dst_bpp)
                }  // for x
            }  // for y
        }
        fixOrientation(img, img_orientation, dst_bpp);
    }

    if (m_hdr && depth >= CV_32F)
    {
        CV_Assert(photometric == PHOTOMETRIC_LOGLUV);
        cvtColor(img, img, COLOR_XYZ2BGR);
    }
    return true;
}

TerrainImportOp PrepareTerrainImport(
        const ::Assets::ResChar input[], 
        unsigned destNodeDims, unsigned destCellTreeDepth)
    {
        TerrainImportOp result;
        result._sourceDims = UInt2(0, 0);
        result._sourceFile = input;
        result._sourceIsGood = false;
        result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::Float;

        auto ext = XlExtension(input);
        if (ext && (!XlCompareStringI(ext, "hdr") || !XlCompareStringI(ext, "flt"))) {

            result._sourceFormat = TerrainImportOp::SourceFormat::AbsoluteFloats;
            result._sourceHeightRange = Float2(FLT_MAX, -FLT_MAX);

            ::Assets::ResChar inputFile[MaxPath];
            XlCopyString(inputFile, input);
            XlChopExtension(inputFile);
            XlCatString(inputFile, dimof(inputFile), ".hdr");

            size_t fileSize = 0;
            auto block = LoadFileAsMemoryBlock(inputFile, &fileSize);
            if (block.get() && fileSize) {
                std::string configAsString(block.get(), &block[fileSize]);
                std::regex parse("^(\\S+)\\s+(.*)");

                std::vector<int> captureGroups;
                captureGroups.push_back(1);
                captureGroups.push_back(2);

                const std::sregex_token_iterator end;
                std::sregex_token_iterator iter(configAsString.begin(), configAsString.end(), parse, captureGroups);
                for (;iter != end;) {
                    auto paramName = *iter++;
                    auto paramValue = *iter++;

                        //  we ignore many parameters. But we at least need to get ncols & nrows
                        //  These tell us the dimensions of the input data
                    if (!XlCompareStringI(paramName.str().c_str(), "ncols")) { result._sourceDims[0] = XlAtoI32(paramValue.str().c_str()); }
                    if (!XlCompareStringI(paramName.str().c_str(), "nrows")) { result._sourceDims[1] = XlAtoI32(paramValue.str().c_str()); }
                }

                result._sourceIsGood = true;
            } else {
                result._warnings.push_back("Could not open input file");
            }

        } else if (ext && (!XlCompareStringI(ext, "tif") || !XlCompareStringI(ext, "tiff"))) {

            auto oldWarningHandler = TIFFSetWarningHandler(&TIFFWarningHandler);
            auto oldErrorHandler = TIFFSetErrorHandler(&TIFFErrorHandler);
            s_tiffWarningVector = &result._warnings;
            auto autoClose = MakeAutoClose([oldWarningHandler, oldErrorHandler]() 
                {
                    TIFFSetWarningHandler(oldWarningHandler);
                    TIFFSetErrorHandler(oldErrorHandler);
                    s_tiffWarningVector = nullptr;
                });

            auto* tif = TIFFOpen(input, "r");
            if (tif) {
                auto autoClose = MakeAutoClose([tif]() { TIFFClose(tif); });

                TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &result._sourceDims[0]);
                TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &result._sourceDims[1]);

                uint32 bitsPerPixel = 32;
                uint32 sampleFormat = SAMPLEFORMAT_IEEEFP;
                TIFFGetFieldDefaulted(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerPixel);
                TIFFGetFieldDefaulted(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

                switch (sampleFormat) {
                case SAMPLEFORMAT_UINT:
					result._sourceFormat = TerrainImportOp::SourceFormat::Quantized;
                    if (bitsPerPixel == 8)          { result._sourceHeightRange = Float2(0.f, float(0xff)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt8; }
                    else if (bitsPerPixel == 16)    { result._sourceHeightRange = Float2(0.f, float(0xffff)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt16; }
                    else if (bitsPerPixel == 32)    { result._sourceHeightRange = Float2(0.f, float(0xffffffff)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt32; }
                    else                            { result._warnings.push_back("Bad bits per pixel"); return result; }
                    break;

                case SAMPLEFORMAT_INT:
                    result._sourceFormat = TerrainImportOp::SourceFormat::Quantized;
                    if (bitsPerPixel == 8)          { result._sourceHeightRange = Float2(float( INT8_MIN), float( INT8_MAX)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt8; }
                    else if (bitsPerPixel == 16)    { result._sourceHeightRange = Float2(float(INT16_MIN), float(INT16_MAX)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt16; }
                    else if (bitsPerPixel == 32)    { result._sourceHeightRange = Float2(float(INT32_MIN), float(INT32_MAX)); result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::UInt32; }
                    else                            { result._warnings.push_back("Bad bits per pixel"); return result; }
                    break;

                case SAMPLEFORMAT_IEEEFP:
                    result._sourceFormat = TerrainImportOp::SourceFormat::AbsoluteFloats;
                    if (bitsPerPixel != 16 && bitsPerPixel != 32)
                        { result._warnings.push_back("Bad bits per pixel"); return result; }

                    result._importCoverageFormat = (unsigned)ImpliedTyping::TypeCat::Float;     // (todo -- float16 support?)
                    break;

                default:
                    result._warnings.push_back("Unsupported sample format");
                    return result;
                }

                result._sourceIsGood = true;
            } else {
                result._warnings.push_back("Could not open tiff file");
            }

        } else {
            result._warnings.push_back("Unknown input file format");
        }

        result._importMins = UInt2(0, 0);
        result._importMaxs = ClampImportDims(result._sourceDims, destNodeDims, destCellTreeDepth);
        result._importHeightRange = result._sourceHeightRange;
        return result;
    }

bool hasAssociatedAlpha()
    {
        uint16_t extra = EXTRASAMPLE_UNSPECIFIED;
        // WAR to avoid random segfaults when TIFFTAG_EXTRASAMPLES exists.
        // The first 3(?) values get filled with random garbage (libtiff 4.0.6).
        //
        // The documentation for TIFFGetField states there should be two params:
        // TIFFTAG_EXTRASAMPLES  2    uint16*,uint16** count & types array
        //
        // Which is probably for symmetry with TIFFSetField:
        // TIFFTAG_EXTRASAMPLES	 2    uint16,uint16*   count & types array
        //
        // Except when reading the value of the parameter is written in the
        // first variable...
        std::array<int16_t, 10> overflowBuffer;
        TIFFGetField(tif.get(), TIFFTAG_EXTRASAMPLES, &extra, &overflowBuffer);
        return extra == EXTRASAMPLE_ASSOCALPHA;
    }

QSize TiffPyramidReader::getImageSize() const
{
    QSize size;
    TIFFGetField(_impl->tif.get(), TIFFTAG_IMAGEWIDTH, &size.rwidth());
    TIFFGetField(_impl->tif.get(), TIFFTAG_IMAGELENGTH, &size.rheight());
    return size;
}

QSize TiffPyramidReader::getTileSize() const
{
    QSize size;
    TIFFGetField(_impl->tif.get(), TIFFTAG_TILEWIDTH, &size.rwidth());
    TIFFGetField(_impl->tif.get(), TIFFTAG_TILELENGTH, &size.rheight());
    return size;
}

int TiffPyramidReader::getBytesPerPixel() const
{
    int value = 0;
    TIFFGetField(_impl->tif.get(), TIFFTAG_SAMPLESPERPIXEL, &value);
    return value;
}

GTiffRasterBand::GTiffRasterBand( GTiffDataset *poDSIn, int nBandIn ) :
    eBandInterp(GCI_Undefined),
    bHaveOffsetScale(false),
    dfOffset(0.0),
    dfScale(1.0),
    poGDS(poDSIn),
    bNoDataSet(false),
    dfNoDataValue(-9999.0)
{
    poDS = poDSIn;
    nBand = nBandIn;

/* -------------------------------------------------------------------- */
/*      Get the GDAL data type.                                         */
/* -------------------------------------------------------------------- */
    const uint16 nBitsPerSample = poGDS->nBitsPerSample;
    const uint16 nSampleFormat = poGDS->nSampleFormat;

    eDataType = GDT_Unknown;

    if( nBitsPerSample <= 8 )
    {
        eDataType = GDT_Byte;
        if( nSampleFormat == SAMPLEFORMAT_INT )
            oGTiffMDMD.SetMetadataItem( "PIXELTYPE", "SIGNEDBYTE",
                                        "IMAGE_STRUCTURE" );
    }
    else if( nBitsPerSample <= 16 )
    {
        if( nSampleFormat == SAMPLEFORMAT_INT )
            eDataType = GDT_Int16;
        else
            eDataType = GDT_UInt16;
    }
    else if( nBitsPerSample == 32 )
    {
        if( nSampleFormat == SAMPLEFORMAT_COMPLEXINT )
            eDataType = GDT_CInt16;
        else if( nSampleFormat == SAMPLEFORMAT_IEEEFP )
            eDataType = GDT_Float32;
        else if( nSampleFormat == SAMPLEFORMAT_INT )
            eDataType = GDT_Int32;
        else
            eDataType = GDT_UInt32;
    }
    else if( nBitsPerSample == 64 )
    {
        if( nSampleFormat == SAMPLEFORMAT_IEEEFP )
            eDataType = GDT_Float64;
        else if( nSampleFormat == SAMPLEFORMAT_COMPLEXIEEEFP )
            eDataType = GDT_CFloat32;
        else if( nSampleFormat == SAMPLEFORMAT_COMPLEXINT )
            eDataType = GDT_CInt32;
    }
    else if( nBitsPerSample == 128 )
    {
        if( nSampleFormat == SAMPLEFORMAT_COMPLEXIEEEFP )
            eDataType = GDT_CFloat64;
    }

/* -------------------------------------------------------------------- */
/*      Try to work out band color interpretation.                      */
/* -------------------------------------------------------------------- */
    bool bLookForExtraSamples = false;

    if( poGDS->poColorTable != nullptr && nBand == 1 )
    {
        eBandInterp = GCI_PaletteIndex;
    }
    else if( poGDS->nPhotometric == PHOTOMETRIC_RGB
             || (poGDS->nPhotometric == PHOTOMETRIC_YCBCR
                 && poGDS->nCompression == COMPRESSION_JPEG
                 && CPLTestBool( CPLGetConfigOption("CONVERT_YCBCR_TO_RGB",
                                                    "YES") )) )
    {
        if( nBand == 1 )
            eBandInterp = GCI_RedBand;
        else if( nBand == 2 )
            eBandInterp = GCI_GreenBand;
        else if( nBand == 3 )
            eBandInterp = GCI_BlueBand;
        else
            bLookForExtraSamples = true;
    }
    else if( poGDS->nPhotometric == PHOTOMETRIC_YCBCR )
    {
        if( nBand == 1 )
            eBandInterp = GCI_YCbCr_YBand;
        else if( nBand == 2 )
            eBandInterp = GCI_YCbCr_CbBand;
        else if( nBand == 3 )
            eBandInterp = GCI_YCbCr_CrBand;
        else
            bLookForExtraSamples = true;
    }
    else if( poGDS->nPhotometric == PHOTOMETRIC_SEPARATED )
    {
        if( nBand == 1 )
            eBandInterp = GCI_CyanBand;
        else if( nBand == 2 )
            eBandInterp = GCI_MagentaBand;
        else if( nBand == 3 )
            eBandInterp = GCI_YellowBand;
        else if( nBand == 4 )
            eBandInterp = GCI_BlackBand;
        else
            bLookForExtraSamples = true;
    }
    else if( poGDS->nPhotometric == PHOTOMETRIC_MINISBLACK && nBand == 1 )
    {
        eBandInterp = GCI_GrayIndex;
    }
    else
    {
        bLookForExtraSamples = true;
    }

    if( bLookForExtraSamples )
    {
        uint16 *v = nullptr;
        uint16 count = 0;

        if( TIFFGetField( poGDS->hTIFF, TIFFTAG_EXTRASAMPLES, &count, &v ) )
        {
            const int nBaseSamples = poGDS->nSamplesPerPixel - count;
            const int nExpectedBaseSamples =
                (poGDS->nPhotometric == PHOTOMETRIC_MINISBLACK) ? 1 :
                (poGDS->nPhotometric == PHOTOMETRIC_MINISWHITE) ? 1 :
                (poGDS->nPhotometric == PHOTOMETRIC_RGB) ? 3 :
                (poGDS->nPhotometric == PHOTOMETRIC_YCBCR) ? 3 :
                (poGDS->nPhotometric == PHOTOMETRIC_SEPARATED) ? 4 : 0;

            if( nExpectedBaseSamples > 0 &&
                nBand == nExpectedBaseSamples + 1 &&
                nBaseSamples != nExpectedBaseSamples )
            {
                CPLError(CE_Warning, CPLE_AppDefined,
                         "Wrong number of ExtraSamples : %d. %d were expected",
                         count, poGDS->nSamplesPerPixel - nExpectedBaseSamples);
            }

            if( nBand > nBaseSamples
                && nBand-nBaseSamples-1 < count
                && (v[nBand-nBaseSamples-1] == EXTRASAMPLE_ASSOCALPHA
                    || v[nBand-nBaseSamples-1] == EXTRASAMPLE_UNASSALPHA) )
                eBandInterp = GCI_AlphaBand;
            else
                eBandInterp = GCI_Undefined;
        }
        else
        {
            eBandInterp = GCI_Undefined;
        }
    }

/* -------------------------------------------------------------------- */
/*      Establish block size for strip or tiles.                        */
/* -------------------------------------------------------------------- */
    nBlockXSize = poGDS->nBlockXSize;
    nBlockYSize = poGDS->nBlockYSize;
}

int GTiffRasterBand::DirectIO( GDALRWFlag eRWFlag,
                               int nXOff, int nYOff, int nXSize, int nYSize,
                               void * pData, int nBufXSize, int nBufYSize,
                               GDALDataType eBufType,
                               GSpacing nPixelSpace, GSpacing nLineSpace,
                               GDALRasterIOExtraArg* psExtraArg )
{
    const int nDTSizeBits = GDALGetDataTypeSizeBits(eDataType);
    if( !(eRWFlag == GF_Read &&
          poGDS->nCompression == COMPRESSION_NONE &&
          (poGDS->nPhotometric == PHOTOMETRIC_MINISBLACK ||
           poGDS->nPhotometric == PHOTOMETRIC_RGB ||
           poGDS->nPhotometric == PHOTOMETRIC_PALETTE) &&
          poGDS->nBitsPerSample == nDTSizeBits &&
          poGDS->SetDirectory() /* very important to make hTIFF uptodate! */) )
    {
        return -1;
    }

    // Only know how to deal with nearest neighbour in this optimized routine.
    if( (nXSize != nBufXSize || nYSize != nBufYSize) &&
        psExtraArg != nullptr &&
        psExtraArg->eResampleAlg != GRIORA_NearestNeighbour )
    {
        return -1;
    }

#if DEBUG_VERBOSE
    CPLDebug( "GTiff", "DirectIO(%d,%d,%d,%d -> %dx%d)",
              nXOff, nYOff, nXSize, nYSize,
              nBufXSize, nBufYSize );
#endif

    // Make sure that TIFFTAG_STRIPOFFSETS is up-to-date.
    if( poGDS->GetAccess() == GA_Update )
    {
        poGDS->FlushCache();
        VSI_TIFFFlushBufferedWrite( TIFFClientdata( poGDS->hTIFF ) );
    }

    if( TIFFIsTiled( poGDS->hTIFF ) )
    {
        if( poGDS->m_pTempBufferForCommonDirectIO == nullptr )
        {
            const int nDTSize = nDTSizeBits / 8;
            poGDS->m_nTempBufferForCommonDirectIOSize =
                static_cast<size_t>(
                    nBlockXSize * nBlockYSize * nDTSize *
                    (poGDS->nPlanarConfig == PLANARCONFIG_CONTIG ?
                     poGDS->nBands : 1) );

            poGDS->m_pTempBufferForCommonDirectIO =
                static_cast<GByte *>( VSI_MALLOC_VERBOSE(
                    poGDS->m_nTempBufferForCommonDirectIOSize ) );
            if( poGDS->m_pTempBufferForCommonDirectIO == nullptr )
                return CE_Failure;
        }

        VSILFILE* fp = VSI_TIFFGetVSILFile(TIFFClientdata( poGDS->hTIFF ));
        FetchBufferDirectIO oFetcher(fp, poGDS->m_pTempBufferForCommonDirectIO,
                                     poGDS->m_nTempBufferForCommonDirectIOSize);

        return poGDS->CommonDirectIO(
            oFetcher,
            nXOff, nYOff, nXSize, nYSize,
            pData, nBufXSize, nBufYSize,
            eBufType,
            1, &nBand,
            nPixelSpace, nLineSpace,
            0 );
    }

    // Get strip offsets.
    toff_t *panTIFFOffsets = nullptr;
    if( !TIFFGetField( poGDS->hTIFF, TIFFTAG_STRIPOFFSETS, &panTIFFOffsets ) ||
        panTIFFOffsets == nullptr )
    {
        return CE_Failure;
    }

    // Sub-sampling or over-sampling can only be done at last stage.
    int nReqXSize = nXSize;
    // Can do sub-sampling at the extraction stage.
    const int nReqYSize = std::min(nBufYSize, nYSize);
    // TODO(schwehr): Make ppData be GByte**.
    void** ppData = static_cast<void **>(
        VSI_MALLOC_VERBOSE(nReqYSize * sizeof(void*)) );
    vsi_l_offset* panOffsets = static_cast<vsi_l_offset *>(
        VSI_MALLOC_VERBOSE(nReqYSize * sizeof(vsi_l_offset)) );
    size_t* panSizes = static_cast<size_t *>(
        VSI_MALLOC_VERBOSE(nReqYSize * sizeof(size_t)) );
    const int nDTSize = GDALGetDataTypeSizeBytes(eDataType);
    void* pTmpBuffer = nullptr;
    int eErr = CE_None;
    int nContigBands =
        poGDS->nPlanarConfig == PLANARCONFIG_CONTIG ? poGDS->nBands : 1;
    int nSrcPixelSize = nDTSize * nContigBands;

    if( ppData == nullptr || panOffsets == nullptr || panSizes == nullptr )
        eErr = CE_Failure;
    else if( nXSize != nBufXSize || nYSize != nBufYSize ||
             eBufType != eDataType ||
             nPixelSpace != GDALGetDataTypeSizeBytes(eBufType) ||
             nContigBands > 1 )
    {
        // We need a temporary buffer for over-sampling/sub-sampling
        // and/or data type conversion.
        pTmpBuffer = VSI_MALLOC_VERBOSE(nReqXSize * nReqYSize * nSrcPixelSize);
        if( pTmpBuffer == nullptr )
            eErr = CE_Failure;
    }

    // Prepare data extraction.
    const double dfSrcYInc = nYSize / static_cast<double>( nBufYSize );

    for( int iLine = 0; eErr == CE_None && iLine < nReqYSize; ++iLine )
    {
        if( pTmpBuffer == nullptr )
            ppData[iLine] = static_cast<GByte *>(pData) + iLine * nLineSpace;
        else
            ppData[iLine] =
                static_cast<GByte *>(pTmpBuffer) +
                iLine * nReqXSize * nSrcPixelSize;
        int nSrcLine = 0;
        if( nBufYSize < nYSize )  // Sub-sampling in y.
            nSrcLine = nYOff + static_cast<int>((iLine + 0.5) * dfSrcYInc);
        else
            nSrcLine = nYOff + iLine;

        const int nBlockXOff = 0;
        const int nBlockYOff = nSrcLine / nBlockYSize;
        const int nYOffsetInBlock = nSrcLine % nBlockYSize;
        nBlocksPerRow = DIV_ROUND_UP(nRasterXSize, nBlockXSize);
        int nBlockId = nBlockXOff + nBlockYOff * nBlocksPerRow;
        if( poGDS->nPlanarConfig == PLANARCONFIG_SEPARATE )
        {
            nBlockId += (nBand-1) * poGDS->nBlocksPerBand;
        }

        panOffsets[iLine] = panTIFFOffsets[nBlockId];
        if( panOffsets[iLine] == 0 )  // We don't support sparse files.
            eErr = -1;

        panOffsets[iLine] +=
            (nXOff + nYOffsetInBlock * nBlockXSize) * nSrcPixelSize;
        panSizes[iLine] = nReqXSize * nSrcPixelSize;
    }

    // Extract data from the file.
    if( eErr == CE_None )
    {
        VSILFILE* fp = VSI_TIFFGetVSILFile(TIFFClientdata( poGDS->hTIFF ));
        const int nRet =
            VSIFReadMultiRangeL( nReqYSize, ppData, panOffsets, panSizes, fp );
        if( nRet != 0 )
            eErr = CE_Failure;
    }

    // Byte-swap if necessary.
    if( eErr == CE_None && TIFFIsByteSwapped(poGDS->hTIFF) )
    {
        for( int iLine = 0; iLine < nReqYSize; ++iLine )
        {
            if( GDALDataTypeIsComplex(eDataType) )
                GDALSwapWords( ppData[iLine], nDTSize / 2,
                               2 * nReqXSize * nContigBands, nDTSize / 2 );
            else
                GDALSwapWords( ppData[iLine], nDTSize,
                               nReqXSize * nContigBands, nDTSize );
        }
    }

    // Over-sampling/sub-sampling and/or data type conversion.
    const double dfSrcXInc = nXSize / static_cast<double>( nBufXSize );
    if( eErr == CE_None && pTmpBuffer != nullptr )
    {
        for( int iY=0; iY < nBufYSize; ++iY )
        {
            const int iSrcY =
                nBufYSize <= nYSize ?
                iY : static_cast<int>((iY + 0.5) * dfSrcYInc);

            GByte* pabySrcData =
                static_cast<GByte*>(ppData[iSrcY]) +
                (nContigBands > 1 ? (nBand-1) : 0) * nDTSize;
            GByte* pabyDstData =
                static_cast<GByte *>(pData) + iY * nLineSpace;
            if( nBufXSize == nXSize )
            {
                GDALCopyWords( pabySrcData,
                               eDataType,
                               nSrcPixelSize,
                               pabyDstData,
                               eBufType,
                               static_cast<int>(nPixelSpace),
                               nBufXSize );
            }
            else
            {
                if( eDataType == GDT_Byte && eBufType == GDT_Byte )
                {
                    double dfSrcX = 0.5 * dfSrcXInc;
                    for( int iX = 0; iX < nBufXSize; ++iX, dfSrcX += dfSrcXInc )
                    {
                        const int iSrcX = static_cast<int>(dfSrcX);
                        pabyDstData[iX * nPixelSpace] =
                            pabySrcData[iSrcX * nSrcPixelSize];
                    }
                }
                else
                {
                    double dfSrcX = 0.5 * dfSrcXInc;
                    for( int iX = 0; iX < nBufXSize; ++iX, dfSrcX += dfSrcXInc )
                    {
                        const int iSrcX = static_cast<int>(dfSrcX);
                        GDALCopyWords( pabySrcData + iSrcX * nSrcPixelSize,
                                       eDataType, 0,
                                       pabyDstData + iX * nPixelSpace,
                                       eBufType, 0, 1 );
                    }
                }
            }
        }
    }

    // Cleanup.
    CPLFree(pTmpBuffer);
    CPLFree(ppData);
    CPLFree(panOffsets);
    CPLFree(panSizes);

    return eErr;
}

CPLVirtualMem* GTiffRasterBand::GetVirtualMemAutoInternal( GDALRWFlag eRWFlag,
                                                           int *pnPixelSpace,
                                                           GIntBig *pnLineSpace,
                                                           char **papszOptions )
{
    int nLineSize = nBlockXSize * GDALGetDataTypeSizeBytes(eDataType);
    if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
        nLineSize *= poGDS->nBands;

    if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
    {
        // In case of a pixel interleaved file, we save virtual memory space
        // by reusing a base mapping that embraces the whole imagery.
        if( poGDS->pBaseMapping != nullptr )
        {
            // Offset between the base mapping and the requested mapping.
            vsi_l_offset nOffset =
                static_cast<vsi_l_offset>(nBand - 1) *
                GDALGetDataTypeSizeBytes(eDataType);

            GTiffRasterBand** ppoSelf =
                static_cast<GTiffRasterBand** >(
                    CPLCalloc(1, sizeof(GTiffRasterBand*)) );
            *ppoSelf = this;

            CPLVirtualMem* pVMem = CPLVirtualMemDerivedNew(
                poGDS->pBaseMapping,
                nOffset,
                CPLVirtualMemGetSize(poGDS->pBaseMapping) - nOffset,
                GTiffRasterBand::DropReferenceVirtualMem,
                ppoSelf);
            if( pVMem == nullptr )
            {
                CPLFree(ppoSelf);
                return nullptr;
            }

            // Mechanism used so that the memory mapping object can be
            // destroyed after the raster band.
            aSetPSelf.insert(ppoSelf);
            ++poGDS->nRefBaseMapping;
            *pnPixelSpace = GDALGetDataTypeSizeBytes(eDataType);
            if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
                *pnPixelSpace *= poGDS->nBands;
            *pnLineSpace = nLineSize;
            return pVMem;
        }
    }

    if( !poGDS->SetDirectory() )  // Very important to make hTIFF up-to-date.
        return nullptr;
    VSILFILE* fp = VSI_TIFFGetVSILFile(TIFFClientdata( poGDS->hTIFF ));

    vsi_l_offset nLength = static_cast<vsi_l_offset>(nRasterYSize) * nLineSize;

    if( !(CPLIsVirtualMemFileMapAvailable() &&
          VSIFGetNativeFileDescriptorL(fp) != nullptr &&
#if SIZEOF_VOIDP == 4
          nLength == static_cast<size_t>(nLength) &&
#endif
          poGDS->nCompression == COMPRESSION_NONE &&
          (poGDS->nPhotometric == PHOTOMETRIC_MINISBLACK ||
           poGDS->nPhotometric == PHOTOMETRIC_RGB ||
           poGDS->nPhotometric == PHOTOMETRIC_PALETTE) &&
          poGDS->nBitsPerSample == GDALGetDataTypeSizeBits(eDataType) &&
          !TIFFIsTiled( poGDS->hTIFF ) && !TIFFIsByteSwapped(poGDS->hTIFF)) )
    {
        return nullptr;
    }

    // Make sure that TIFFTAG_STRIPOFFSETS is up-to-date.
    if( poGDS->GetAccess() == GA_Update )
    {
        poGDS->FlushCache();
        VSI_TIFFFlushBufferedWrite( TIFFClientdata( poGDS->hTIFF ) );
    }

    // Get strip offsets.
    toff_t *panTIFFOffsets = nullptr;
    if( !TIFFGetField( poGDS->hTIFF, TIFFTAG_STRIPOFFSETS, &panTIFFOffsets ) ||
        panTIFFOffsets == nullptr )
    {
        return nullptr;
    }

    int nBlockSize =
        nBlockXSize * nBlockYSize * GDALGetDataTypeSizeBytes(eDataType);
    if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
        nBlockSize *= poGDS->nBands;

    int nBlocks = poGDS->nBlocksPerBand;
    if( poGDS->nPlanarConfig == PLANARCONFIG_SEPARATE )
        nBlocks *= poGDS->nBands;
    int i = 0;  // Used after for.
    for( ; i < nBlocks; ++i )
    {
        if( panTIFFOffsets[i] != 0 )
            break;
    }
    if( i == nBlocks )
    {
        // All zeroes.
        if( poGDS->eAccess == GA_Update )
        {
            // Initialize the file with empty blocks so that the file has
            // the appropriate size.

            toff_t* panByteCounts = nullptr;
            if( !TIFFGetField( poGDS->hTIFF, TIFFTAG_STRIPBYTECOUNTS,
                               &panByteCounts ) ||
                panByteCounts == nullptr )
            {
                return nullptr;
            }
            if( VSIFSeekL(fp, 0, SEEK_END) != 0 )
                return nullptr;
            vsi_l_offset nBaseOffset = VSIFTellL(fp);

            // Just write one tile with libtiff to put it in appropriate state.
            GByte* pabyData =
                static_cast<GByte*>(VSI_CALLOC_VERBOSE(1, nBlockSize));
            if( pabyData == nullptr )
            {
                return nullptr;
            }
            int ret =
                static_cast<int>(
                    TIFFWriteEncodedStrip( poGDS->hTIFF, 0, pabyData,
                                           nBlockSize ) );
            VSI_TIFFFlushBufferedWrite( TIFFClientdata( poGDS->hTIFF ) );
            VSIFree(pabyData);
            if( ret != nBlockSize )
            {
                return nullptr;
            }
            CPLAssert(panTIFFOffsets[0] == nBaseOffset);
            CPLAssert(panByteCounts[0] == static_cast<toff_t>(nBlockSize));

            // Now simulate the writing of other blocks.
            const vsi_l_offset nDataSize =
                static_cast<vsi_l_offset>(nBlockSize) * nBlocks;
            if( VSIFTruncateL(fp, nBaseOffset + nDataSize) != 0 )
                return nullptr;

            for( i = 1; i < nBlocks; ++i)
            {
                panTIFFOffsets[i] =
                    nBaseOffset + i * static_cast<toff_t>(nBlockSize);
                panByteCounts[i] = nBlockSize;
            }
        }
        else
        {
            CPLDebug( "GTiff", "Sparse files not supported in file mapping" );
            return nullptr;
        }
    }

    GIntBig nBlockSpacing = 0;
    bool bCompatibleSpacing = true;
    toff_t nPrevOffset = 0;
    for( i = 0; i < poGDS->nBlocksPerBand; ++i )
    {
        toff_t nCurOffset = 0;
        if( poGDS->nPlanarConfig == PLANARCONFIG_SEPARATE )
            nCurOffset =
                panTIFFOffsets[poGDS->nBlocksPerBand * (nBand - 1) + i];
        else
            nCurOffset = panTIFFOffsets[i];
        if( nCurOffset == 0 )
        {
            bCompatibleSpacing = false;
            break;
        }
        if( i > 0 )
        {
            const GIntBig nCurSpacing = nCurOffset - nPrevOffset;
            if( i == 1 )
            {
                if( nCurSpacing !=
                    static_cast<GIntBig>(nBlockYSize) * nLineSize )
                {
                    bCompatibleSpacing = false;
                    break;
                }
                nBlockSpacing = nCurSpacing;
            }
            else if( nBlockSpacing != nCurSpacing )
            {
                bCompatibleSpacing = false;
                break;
            }
        }
        nPrevOffset = nCurOffset;
    }

    if( !bCompatibleSpacing )
    {
        return nullptr;
    }

    vsi_l_offset nOffset = 0;
    if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
    {
        CPLAssert( poGDS->pBaseMapping == nullptr );
        nOffset = panTIFFOffsets[0];
    }
    else
    {
        nOffset = panTIFFOffsets[poGDS->nBlocksPerBand * (nBand - 1)];
    }
    CPLVirtualMem* pVMem = CPLVirtualMemFileMapNew(
        fp, nOffset, nLength,
        eRWFlag == GF_Write ? VIRTUALMEM_READWRITE : VIRTUALMEM_READONLY,
        nullptr, nullptr);
    if( pVMem == nullptr )
    {
        return nullptr;
    }

    if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
    {
        // TODO(schwehr): Revisit this block.
        poGDS->pBaseMapping = pVMem;
        pVMem = GetVirtualMemAutoInternal( eRWFlag,
                                           pnPixelSpace,
                                           pnLineSpace,
                                           papszOptions );
        // Drop ref on base mapping.
        CPLVirtualMemFree(poGDS->pBaseMapping);
        if( pVMem == nullptr )
            poGDS->pBaseMapping = nullptr;
    }
    else
    {
        *pnPixelSpace = GDALGetDataTypeSizeBytes(eDataType);
        if( poGDS->nPlanarConfig == PLANARCONFIG_CONTIG )
            *pnPixelSpace *= poGDS->nBands;
        *pnLineSpace = nLineSize;
    }
    return pVMem;
}

CPLErr GTiffDataset::CommonDirectIO(
    FetchBuffer& oFetcher,
    int nXOff, int nYOff, int nXSize, int nYSize,
    void * pData, int nBufXSize, int nBufYSize,
    GDALDataType eBufType,
    int nBandCount, int *panBandMap,
    GSpacing nPixelSpace, GSpacing nLineSpace,
    GSpacing nBandSpace )
{
    const GDALDataType eDataType = GetRasterBand(1)->GetRasterDataType();
    const int nDTSize = GDALGetDataTypeSizeBytes(eDataType);
    const bool bIsComplex = CPL_TO_BOOL(GDALDataTypeIsComplex(eDataType));
    const int nBufDTSize = GDALGetDataTypeSizeBytes(eBufType);

    // Get strip offsets.
    toff_t *panOffsets = nullptr;
    if( !TIFFGetField( hTIFF, (TIFFIsTiled( hTIFF )) ?
                       TIFFTAG_TILEOFFSETS : TIFFTAG_STRIPOFFSETS,
                       &panOffsets ) ||
        panOffsets == nullptr )
    {
        return CE_Failure;
    }

    bool bUseContigImplementation =
        nPlanarConfig == PLANARCONFIG_CONTIG &&
        nBandCount > 1 &&
        nBandSpace == nBufDTSize;
    if( bUseContigImplementation )
    {
        for( int iBand = 0; iBand < nBandCount; ++iBand )
        {
            const int nBand = panBandMap[iBand];
            if( nBand != iBand + 1 )
            {
                bUseContigImplementation = false;
                break;
            }
        }
    }

    const int nBandsPerBlock =
        nPlanarConfig == PLANARCONFIG_SEPARATE ? 1 : nBands;
    const int nBandsPerBlockDTSize = nBandsPerBlock * nDTSize;
    const int nBlocksPerRow = DIV_ROUND_UP(nRasterXSize, nBlockXSize);
    const bool bNoTypeChange = (eDataType == eBufType);
    const bool bNoXResampling = (nXSize == nBufXSize );
    const bool bNoXResamplingNoTypeChange = (bNoTypeChange && bNoXResampling);
    const bool bByteOnly = (bNoTypeChange && nDTSize == 1 );
    const bool bByteNoXResampling = ( bByteOnly && bNoXResamplingNoTypeChange );
    const bool bIsByteSwapped = CPL_TO_BOOL(TIFFIsByteSwapped(hTIFF));
    const double dfSrcXInc = nXSize / static_cast<double>( nBufXSize );
    const double dfSrcYInc = nYSize / static_cast<double>( nBufYSize );

    int bNoDataSetIn = FALSE;
    double dfNoData = GetRasterBand(1)->GetNoDataValue( &bNoDataSetIn );
    GByte abyNoData = 0;
    if( !bNoDataSetIn )
        dfNoData = 0;
    else if( dfNoData >= 0 && dfNoData <= 255 )
        abyNoData = static_cast<GByte>(dfNoData + 0.5);

    if( FetchBuffer::bMinimizeIO &&
             TIFFIsTiled( hTIFF ) && bNoXResampling && (nYSize == nBufYSize ) &&
             nPlanarConfig == PLANARCONFIG_CONTIG && nBandCount > 1 )
    {
        GByte* pabyData = static_cast<GByte *>(pData);
        for( int y = 0; y < nBufYSize; )
        {
            const int nSrcLine = nYOff + y;
            const int nBlockYOff = nSrcLine / nBlockYSize;
            const int nYOffsetInBlock = nSrcLine % nBlockYSize;
            const int nUsedBlockHeight =
                std::min( nBufYSize - y,
                          nBlockYSize - nYOffsetInBlock );

            int nBlockXOff = nXOff / nBlockXSize;
            int nXOffsetInBlock = nXOff % nBlockXSize;
            int nBlockId = nBlockXOff + nBlockYOff * nBlocksPerRow;

            int x = 0;
            while( x < nBufXSize )
            {
                const toff_t nCurOffset = panOffsets[nBlockId];
                const int nUsedBlockWidth =
                    std::min( nBlockXSize - nXOffsetInBlock,
                              nBufXSize - x );

                if( nCurOffset == 0 )
                {
                    REACHED(30);
                    for( int k = 0; k < nUsedBlockHeight; ++k )
                    {
                        GByte* pabyLocalData =
                            pabyData + (y + k) * nLineSpace + x * nPixelSpace;
                        for( int iBand = 0; iBand < nBandCount; ++iBand )
                        {
                            GByte* pabyLocalDataBand =
                                pabyLocalData + iBand * nBandSpace;

                            GDALCopyWords(
                                &dfNoData, GDT_Float64, 0,
                                pabyLocalDataBand, eBufType,
                                static_cast<int>(nPixelSpace),
                                nUsedBlockWidth );
                        }
                    }
                }
                else
                {
                    const int nByteOffsetInBlock =
                        nYOffsetInBlock * nBlockXSize * nBandsPerBlockDTSize;
                    const GByte* pabyLocalSrcDataK0 = oFetcher.FetchBytes(
                            nCurOffset + nByteOffsetInBlock,
                            nBlockXSize *
                            nUsedBlockHeight * nBandsPerBlock,
                            nDTSize, bIsByteSwapped, bIsComplex, nBlockId);
                    if( pabyLocalSrcDataK0 == nullptr )
                        return CE_Failure;

                    for( int k = 0; k < nUsedBlockHeight; ++k )
                    {
                        GByte* pabyLocalData =
                            pabyData + (y + k) * nLineSpace + x * nPixelSpace;
                        const GByte* pabyLocalSrcData =
                            pabyLocalSrcDataK0 +
