static int get_bits_per_sample(TIFF *tif, USHORT *bps) {
  if (!TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, bps)) {
    *bps = 0;
    return FALSE;
  }
  switch (*bps) {
    CASE 1 : __OR 2 : __OR 4 : __OR 8 : __OR 16 : __OR 32 : return TRUE;
  }
  return FALSE;
}

static int get_samples_per_pixel(TIFF *tif, USHORT *spp) {
  if (!TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, spp)) {
    *spp = 0;
    return FALSE;
  }
  switch (*spp) { CASE 1 : __OR 3 : __OR 4 : return TRUE; }
  return FALSE;
}

static int get_image_sizes(TIFF *tif, int *xsize, int *ysize) {
  *ysize = 0;
  if (!TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, xsize)) return FALSE;
  if (!TIFFGetField(tif, TIFFTAG_IMAGELENGTH, ysize)) return FALSE;
  return TRUE;
}

static void get_image_offsets_and_dimensions(TIFF *tfp, int xSBsize,
                                             int ySBsize, int *x0, int *y0,
                                             int *xsize, int *ysize,
                                             double *h_pos, UCHAR *extra_mask,
                                             TBOOL *edu_file) {
  USHORT *window, orientation;
  float xposition, dpi;
  // int i;

  if (!TIFFGetField(tfp, TIFFTAG_TOONZWINDOW, &window)) {
    *x0         = 0;
    *y0         = 0;
    *xsize      = xSBsize;
    *ysize      = ySBsize;
    *extra_mask = 0;

    *edu_file = FALSE;
  } else {
    *x0                     = window[0];
    *y0                     = window[1];
    *xsize                  = window[2];
    *ysize                  = window[3];
    if (*xsize == 0) *xsize = xSBsize + *x0;
    if (*ysize == 0) *ysize = ySBsize + *y0;
    *extra_mask             = Read_with_extra ? window[4] : 0;

    *edu_file = window[TOONZWINDOW_COUNT - 1] & 1;
  }
  if (!TIFFGetField(tfp, TIFFTAG_XPOSITION, &xposition)) xposition = 8.0;
  if (!TIFFGetField(tfp, TIFFTAG_ORIENTATION, &orientation))
    orientation = ORIENTATION_TOPLEFT;
  switch (orientation) {
    CASE ORIENTATION_BOTLEFT
        : __OR ORIENTATION_BOTRIGHT
          : __OR ORIENTATION_TOPLEFT
            : __OR ORIENTATION_TOPRIGHT
              : if (!TIFFGetField(tfp, TIFFTAG_XRESOLUTION, &dpi)) dpi = 0.0;
    CASE ORIENTATION_LEFTBOT
        : __OR ORIENTATION_RIGHTBOT
          : __OR ORIENTATION_LEFTTOP
            : __OR ORIENTATION_RIGHTTOP
              : if (!TIFFGetField(tfp, TIFFTAG_YRESOLUTION, &dpi)) dpi = 0.0;
  }
  *h_pos = (xposition - 8.0) * dpi;
}

static int get_photometric(TIFF *tif, USHORT *pm) {
  if (!TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, pm)) return FALSE;
  return TRUE;
}

static int get_resolutions(TIFF *tif, double *x_dpi, double *y_dpi) {
  float xdpi, ydpi;
  // USHORT resunit;

  if (!TIFFGetField(tif, TIFFTAG_XRESOLUTION, &xdpi) ||
      !TIFFGetField(tif, TIFFTAG_YRESOLUTION, &ydpi)) {
    *x_dpi = *y_dpi = 0.0;
    return FALSE;
  }
  *x_dpi = (double)xdpi;
  *y_dpi = (double)ydpi;
  return TRUE;
}

static int get_orientation(TIFF *tif, int *orientation) {
  if (!TIFFGetField(tif, TIFFTAG_ORIENTATION, &orientation)) return FALSE;
  return TRUE;
}

static int get_rows_per_strip(TIFF *tif, long *rowperstrip) {
  if (!TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &rowperstrip)) return FALSE;
  return TRUE;
}

static int get_compression(TIFF *tif, int *compression) {
  if (!TIFFGetField(tif, TIFFTAG_COMPRESSION, &compression)) return FALSE;
  return TRUE;
}

static int get_tag_software(TIFF *tif, char *tag_software) {
  if (!TIFFGetField(tif, TIFFTAG_SOFTWARE, tag_software)) return FALSE;
  return TRUE;
}

static void get_planarconfig(TIFF *tif, USHORT *planarconfig) {
  TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarconfig);
}

static int get_history(TIFF *tif, char **history) {
  if (!TIFFGetField(tif, TIFFTAG_TOONZHISTORY, history)) return FALSE;
  //*history = strsave(*history);
  return TRUE;
}

static int get_image(TIFF *tf, IMAGE *image) {
  USHORT planarconfig;
  // int i;

  TIFFGetField(tf, TIFFTAG_PLANARCONFIG, &planarconfig);
  if (planarconfig == PLANARCONFIG_SEPARATE) {
    // tmsg_error("separate buffer image not supported yet in .tz(up) files");
    return FALSE;
  }
  switch (image->type) {
    CASE CMAPPED : return get_image_contig_16(tf, image);
    CASE CMAPPED24 : return get_image_contig_24(tf, image);
  DEFAULT:
    abort();
  }
  return FALSE;
}

void TifReader::open(FILE *file) {
  int fd = fileno(file);
#if 0
	m_tiff = TIFFFdOpenNoCloseProc(fd, "", "rb");
#elif defined(_WIN32) && defined(__GNUC__)
  m_tiff = TIFFFdOpen((int)_get_osfhandle(dup(fd)), "", "rb");
#else
  m_tiff = TIFFFdOpen(dup(fd), "", "rb");
#endif
  if (!m_tiff) {
    std::string str("Tiff file closed");
    throw(str);
  }

  uint32 w = 0, h = 0, rps = 0;
  uint16 bps = 0, spp = 0;
  uint32 tileWidth = 0, tileLength = 0;

  // TIFFSetDirectory(m_tiff,1);
  // TIFFGetField(m_tiff, TIFFTAG_PAGENUMBER, &pn);
  // int pn = TIFFNumberOfDirectories(m_tiff);
  // TIFFSetDirectory(m_tiff,1);
  TIFFGetField(m_tiff, TIFFTAG_IMAGEWIDTH, &w);
  TIFFGetField(m_tiff, TIFFTAG_IMAGELENGTH, &h);
  TIFFGetField(m_tiff, TIFFTAG_BITSPERSAMPLE, &bps);
  TIFFGetField(m_tiff, TIFFTAG_SAMPLESPERPIXEL, &spp);
  TIFFGetField(m_tiff, TIFFTAG_ROWSPERSTRIP, &rps);
  // int stripCount = TIFFNumberOfStrips(m_tiff);
  // int tileCount = TIFFNumberOfTiles(m_tiff);
  TIFFGetField(m_tiff, TIFFTAG_TILEWIDTH, &tileWidth);
  TIFFGetField(m_tiff, TIFFTAG_TILELENGTH, &tileLength);
  Tiio::TifWriterProperties *prop = new Tiio::TifWriterProperties();
  m_info.m_properties             = prop;
  uint16 orient                   = Tiio::TOP2BOTTOM;
  if (TIFFGetField(m_tiff, TIFFTAG_ORIENTATION, &orient)) {
    switch (orient) {
    case ORIENTATION_TOPLEFT: /* row 0 top, col 0 lhs */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_TOPLEFT);
      m_rowOrder = Tiio::TOP2BOTTOM;
      break;
    case ORIENTATION_TOPRIGHT: /* row 0 top, col 0 rhs */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_TOPRIGHT);
      m_rowOrder = Tiio::TOP2BOTTOM;
      break;
    case ORIENTATION_LEFTTOP: /* row 0 lhs, col 0 top */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_LEFTTOP);
      m_rowOrder = Tiio::TOP2BOTTOM;
      break;
    case ORIENTATION_RIGHTTOP: /* row 0 rhs, col 0 top */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_RIGHTTOP);
      m_rowOrder = Tiio::TOP2BOTTOM;
      break;

    case ORIENTATION_BOTRIGHT: /* row 0 bottom, col 0 rhs */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_BOTRIGHT);
      m_rowOrder = Tiio::BOTTOM2TOP;
      break;
    case ORIENTATION_BOTLEFT: /* row 0 bottom, col 0 lhs */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_BOTLEFT);
      m_rowOrder = Tiio::BOTTOM2TOP;
      break;
    case ORIENTATION_RIGHTBOT: /* row 0 rhs, col 0 bottom */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_RIGHTBOT);
      m_rowOrder = Tiio::BOTTOM2TOP;
      break;
    case ORIENTATION_LEFTBOT: /* row 0 lhs, col 0 bottom */
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_LEFTBOT);
      m_rowOrder = Tiio::BOTTOM2TOP;
      break;
    default:
      prop->m_orientation.setValue(TNZ_INFO_ORIENT_NONE);
      m_rowOrder = Tiio::TOP2BOTTOM;
      break;
    }
  }

  USHORT compression;
  TIFFGetField(m_tiff, TIFFTAG_COMPRESSION, &compression);
  switch (compression) {
  case COMPRESSION_LZW:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_LZW);
    break;
  case COMPRESSION_PACKBITS:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_PACKBITS);
    break;
  case COMPRESSION_THUNDERSCAN:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_THUNDERSCAN);
    break;
  case COMPRESSION_CCITTFAX3:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_CCITTFAX3);
    break;
  case COMPRESSION_CCITTFAX4:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_CCITTFAX4);
    break;
  case COMPRESSION_CCITTRLE:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_CCITTRLE);
    break;
  case COMPRESSION_JPEG:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_JPEG);
    break;
  case COMPRESSION_OJPEG:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_OJPEG);
    break;
  case COMPRESSION_NONE:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_NONE);
    break;
  case COMPRESSION_SGILOG:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_SGILOG);
    break;
  case COMPRESSION_SGILOG24:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_SGILOG24);
    break;
  case COMPRESSION_ADOBE_DEFLATE:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_ADOBE_DEFLATE);
    break;
  case COMPRESSION_DEFLATE:
    prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_DEFLATE);
    break;
  /*default :
prop->m_compressionType.setValue(TNZ_INFO_COMPRESS_UNKNOWN);
break;*/
  default:
    assert(0);
  }

  float xdpi = 0, ydpi = 0;
  TIFFGetField(m_tiff, TIFFTAG_XRESOLUTION, &xdpi);
  TIFFGetField(m_tiff, TIFFTAG_YRESOLUTION, &ydpi);

  bool swapxy = false;  // orient == ORIENTATION_RIGHTTOP;

  if (swapxy) {
    std::swap(w, h);
    std::swap(xdpi, ydpi);
  }

  m_xdpi        = xdpi;
  m_ydpi        = ydpi;
  m_info.m_lx   = w;
  m_info.m_ly   = h;
  m_info.m_dpix = xdpi;
  m_info.m_dpiy = ydpi;

  m_info.m_samplePerPixel = spp;

  if (bps == 64 && spp == 3) bps = 16;  // immagine con bpp = 192

  uint16 photometric;  // codice di controllo
  TIFFGetField(m_tiff, TIFFTAG_PHOTOMETRIC, &photometric);
  if (photometric == 3 &&
      (bps == 2 || bps == 4))  // immagini con PHOTOMATRIC_PALETTE
    bps = 8;

  if (photometric == 1 && (bps == 12 || bps == 24)) bps = 16;

  if (bps == 6) bps = 4;  // immagini con bps = 6

  if (bps == 10 || bps == 12 ||
      bps == 14)  // immagini con bps = 10 , 12 , 14 , 24 , 32
    bps = 8;
  if (bps == 24 || bps == 32) bps = 16;

  m_info.m_bitsPerSample = bps;

  if (bps == 8) switch (spp) {
    case 1: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L" 8(GREYTONES)");
      break;
    case 3: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"24(RGB)");
      break;
    case 4: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    }
  else if (bps == 16)
    switch (spp) {
    case 1: /* row 0 top, col 0 lhs */
      // prop->m_bitsPerPixel.setValue(L"16(GREYTONES)");
      break;
    case 3: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"48(RGB)");
      break;
    case 4: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"64(RGBM)");
      break;
    }

  else if (bps == 2)
    switch (spp) {
    case 1: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    case 3: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    case 4: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    }

  else if (bps == 1)
    switch (spp) {
    case 1: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L" 1(BW)");
      break;
    case 3: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    case 4: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    }

  else if (bps == 4)
    switch (spp) {
    case 1: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L" 8(GREYTONES)");
      break;
    case 3: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    case 4: /* row 0 top, col 0 lhs */
      prop->m_bitsPerPixel.setValue(L"32(RGBM)");
      break;
    }

  else if (bps == 64 && spp == 3)
    prop->m_bitsPerPixel.setValue(L"64(RGBM)");

  else
    assert(false);

  if (TIFFIsTiled(m_tiff)) {
    m_rowsPerStrip  = tileLength;
    int tilesPerRow = (w + tileWidth - 1) / tileWidth;
    // m_rowLength = tileWidth * tilesPerRow;
    m_rowLength   = m_info.m_lx;
    int pixelSize = bps == 16 ? 8 : 4;
    int stripSize = m_rowsPerStrip * m_rowLength * pixelSize;

    m_tmpRas = TRasterGR8P(stripSize, 1);
    m_tmpRas->lock();

    m_stripBuffer = m_tmpRas->getRawData();
  } else {
    m_rowsPerStrip = rps;
    // if(m_rowsPerStrip<=0) m_rowsPerStrip = 1;			//potrei
    // mettere
    // qualsiasi
    // valore
    // purch sia lo stesso in tif_getimage.c linea 2512
    // if(m_rowsPerStrip==-1) assert(0);

    if (m_rowsPerStrip <= 0) m_rowsPerStrip = m_info.m_ly;

    int stripSize = m_rowsPerStrip * w * 4;  // + 4096;  TIFFStripSize(m_tiff);

    if (bps == 16) stripSize *= 2;

    m_tmpRas = TRasterGR8P(stripSize, 1);
    m_tmpRas->lock();

    m_stripBuffer = m_tmpRas->getRawData();

    m_rowLength = m_info.m_lx;  // w;
  }

  /*
int TIFFTileRowSize(m_tiff);

m_rowsPerStrip = 0;
if(TIFFGetField(m_tiff, TIFFTAG_ROWSPERSTRIP, &rps) )
{
int stripSize = TIFFStripSize(m_tiff);
if(stripSize>0)
 {
 }
}
*/
  if (m_isTzi) {
    USHORT risCount  = 0;
    USHORT *risArray = 0;

    if (TIFFGetField(m_tiff, TIFFTAG_TOONZWINDOW, &risCount, &risArray)) {
      if (m_info.m_lx == risArray[2] &&
          m_info.m_ly ==
              risArray[3])  // se sono diverse, la lettura tif crasha....
      {
        // m_info.m_lx = risArray[2];
        // m_info.m_ly = risArray[3];
        m_info.m_x0 = risArray[0];
        m_info.m_y0 = risArray[1];
      }
      //      USHORT extraMask = risArray[4];
      // bool isEduFile = risArray[TOONZWINDOW_COUNT - 1] & 1;
    } else {
      m_info.m_x0 = 0;
      m_info.m_y0 = 0;
    }
    if (swapxy) {
      std::swap(m_info.m_x0, m_info.m_y0);
      std::swap(m_info.m_lx, m_info.m_ly);
    }
    m_info.m_x1 = m_info.m_x0 + w;
    m_info.m_y1 = m_info.m_y0 + h;
  } else {
    m_info.m_x0 = m_info.m_y0 = 0;
    m_info.m_x1               = m_info.m_x0 + m_info.m_lx - 1;
    m_info.m_y1               = m_info.m_y0 + m_info.m_ly - 1;
  }
}

void PltReader::open(FILE *file) {
  char *data;
  uint32 count;
  int i;
  for (i = 0; i < 256; i++) ComboInkIndex[i] = -1;

  int fd = fileno(file);

  TIFFErrorHandler oldhandler;
  oldhandler = TIFFSetWarningHandler(NULL);
  m_tiff     = TIFFFdOpen(fd, "", "rb");
  TIFFSetWarningHandler(oldhandler);

  if (!m_tiff) return;

  uint32 w = 0, h = 0, rps = 0;
  uint32 tileWidth = 0, tileLength = 0;
  uint16 bps = 0, spp = 0;

  TIFFGetField(m_tiff, TIFFTAG_IMAGEWIDTH, &w);
  TIFFGetField(m_tiff, TIFFTAG_IMAGELENGTH, &h);
  TIFFGetField(m_tiff, TIFFTAG_BITSPERSAMPLE, &bps);
  TIFFGetField(m_tiff, TIFFTAG_SAMPLESPERPIXEL, &spp);
  TIFFGetField(m_tiff, TIFFTAG_ROWSPERSTRIP, &rps);

  float xdpi, ydpi;
  TIFFGetField(m_tiff, TIFFTAG_XRESOLUTION, &xdpi);
  TIFFGetField(m_tiff, TIFFTAG_YRESOLUTION, &ydpi);
  m_info.m_dpix = xdpi;
  m_info.m_dpiy = ydpi;

  TIFFGetField(m_tiff, TIFFTAG_TILEWIDTH, &tileWidth);
  TIFFGetField(m_tiff, TIFFTAG_TILELENGTH, &tileLength);

  uint32 risCount  = 0;
  USHORT *risArray = 0;

  m_info.m_lx = w;
  m_info.m_ly = h;
  m_x         = 0;
  m_y         = 0;
  m_lx        = w;
  m_ly        = h;

  assert(h == 1);
  m_ly = m_info.m_ly = 2;  // per l'infoRow

  if (TIFFGetField(m_tiff, TIFFTAG_TOONZWINDOW, &risCount, &risArray)) {
    m_info.m_x0 = m_x = risArray[0];
    m_info.m_y0 = m_y = risArray[1];
    m_info.m_lx       = risArray[2];
    m_info.m_ly       = risArray[3];
  } else {
    m_info.m_x0 = 0;
    m_info.m_y0 = 0;
  }

  m_info.m_x1 = m_info.m_x0 + w;
  m_info.m_y1 = m_info.m_y0 + h;

  if (TIFFIsTiled(m_tiff)) {
    m_rowsPerStrip  = tileLength;
    int tilesPerRow = (w + tileWidth - 1) / tileWidth;
    m_rowLength     = tileWidth * tilesPerRow;
    int stripSize   = m_rowsPerStrip * m_rowLength * 4;
    m_stripBuffer   = new UCHAR[stripSize];
  } else {
    m_rowsPerStrip = rps;
    int stripSize  = rps * w * 4 + 4096;  // TIFFStripSize(m_tiff);

    m_stripBuffer = new UCHAR[stripSize];
    m_rowLength   = w;
  }

  uint32 paletteCount;
  USHORT *palette;

  TIFFGetField(m_tiff, TIFFTAG_TOONZPALETTE, &paletteCount, &palette);
  assert(paletteCount);

  m_pltType = palette[0];

  m_nColor  = palette[10];
  m_nPencil = palette[11];

  bool isOldCmap13Plt = false;
  if (m_nColor == 128 && m_nPencil == 32) {
    isOldCmap13Plt = true;
  }

  std::string colorNames;
  if (TIFFGetField(m_tiff, TIFFTAG_TOONZCOLORNAMES, &count, &data))
    colorNames = data;

  TREE *names = cdb_decode_all(data, Tcm_24_default_info);

  CDB_TREE_ITEM *item;

  char *pageName;
  int key, sisterIndex;

  int maxCount = m_nColor + m_nPencil;
  m_infoRow.resize(maxCount, TPixel32(0, 0, 0, 0));

  for (i = 0; i < maxCount; i++) {
    item = (CDB_TREE_ITEM *)avl_locate(names, i);
    if (!item) continue;
    decode_group_name(item->group, &pageName, &key, &sisterIndex);

    if (strcmp(pageName, "_UNUSED_PAGE") == 0) continue;
    if (sisterIndex == -1 || i < m_nColor) m_infoRow[i].r = 255;
    if (i < m_nColor) m_infoRow[i].g = 255;

    while (isdigit(item->name[0]))  // in toonz colors cannot begin with digits
      item->name++;

    std::map<int, std::pair<std::string, std::string>>::iterator it;
    if ((it = m_pltNames.find(i)) == m_pltNames.end() ||
        isDefaultName(it->second.second))
      m_pltNames[i] = std::pair<std::string, std::string>(pageName, item->name);

    if (sisterIndex != -1) {
      int comboindex;
      if (isOldCmap13Plt) {
        comboindex = (i < 128) ? (sisterIndex >> 11) + 128 : sisterIndex >> 4;
        if (i == 128)
          ComboInkIndex[0] = 0;
        else if (i > 128)
          ComboInkIndex[i - 128] = comboindex;
      } else {
        comboindex = (i < 256) ? (sisterIndex >> 16) + 256 : sisterIndex >> 8;
        if (i >= 256) ComboInkIndex[i - 256] = comboindex;
      }
      std::map<int, std::pair<std::string, std::string>>::iterator it;
      if ((it = m_pltNames.find(comboindex)) == m_pltNames.end() ||
          isDefaultName(it->second.second)) {
        m_pltNames[comboindex] =
            std::pair<std::string, std::string>(pageName, item->name);
        if (comboindex < m_nColor)
          m_infoRow[comboindex].r = m_infoRow[comboindex].g = 255;
      }
    }
  }

  if (TIFFGetField(m_tiff, TIFFTAG_TOONZHISTORY, &count, &data))
    m_history = data;

  uint16 photometric, planarconfig;

  TIFFGetField(m_tiff, TIFFTAG_PHOTOMETRIC, &photometric);
  TIFFGetField(m_tiff, TIFFTAG_PLANARCONFIG, &planarconfig);

  if (photometric != PHOTOMETRIC_RGB || planarconfig != PLANARCONFIG_CONTIG) {
    // tmsg_error("bad !");
  }
}

