        //FillOrder
        int fillOrder = 0;
        TIFFGetField(image, TIFFTAG_FILLORDER, &fillOrder);
        LOGII("fill Order", fillOrder);
        jclass gOptions_FillOrderClass = env->FindClass("org/beyka/tiffbitmapfactory/FillOrder");
