void image_info::readtif(const char *filename){
#ifdef USE_LIBTIFF
#ifdef _USE_DL_
  if(!have_tiff){
    if(init_tiff(LIBTIFF_SOLIBRARY)){ 
      have_tiff = 1;
    }
  }
  if(!have_tiff){
    printf("tiff library not found\n");
    printf("Please try reading from a supported format\n");
    return;
  }
#endif  
  TIFF* tif = TIFFOpen(filename, "r");
  uint16 spp;
  TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &spp);
  TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
  TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
  if(spp!=1&&spp!=3&&spp!=4){
    uint32 tileWidth, tileLength;
    uint32 *bc, *bc2;
    bool istiled = true, isstripped = true;
    int success = TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tileWidth);
    if(!success)  istiled = false;
    success = TIFFGetField(tif, TIFFTAG_TILELENGTH, &tileLength);
    if(!success)  istiled = false;
    success = TIFFGetField(tif, TIFFTAG_STRIPBYTECOUNTS, &bc);
    if(!success)  isstripped = false;
    uint32 stripsize = bc[0];
    success = TIFFGetField(tif,  TIFFTAG_TILEBYTECOUNTS, &bc2);
    if(!success)  isstripped = false;
    uint32 tilesize = bc2[0];
    if(istiled&&isstripped) {
      printf("Seems to be tiled and stripped in readtiff.\n");
      printf("Unlikley!!, quitting ...\n");
      return;
    }
    if(istiled) {
      pixels = new unsigned char[width*height*spp];
      colourspace = spp;
      if(spp==2){
        colourspace_type = IMAGEINFO_MONOA;
      }else{
        printf("Unsupported samples per pixel in readtiff: %d\n",spp);
        delete [] pixels;
        return;
      }
      /*
      printf("Samples per pixel size of %d tiled tiff files is currently not supported by CCP4MG\n",spp);
      printf("Please convert to another tiff format, eg rgb or rgb+alpha\n");
      printf("The following is developer info to help rectify the problem\n");
      printf("Scanline size:%d\n",TIFFScanlineSize(tif));
      printf("Number of tiles:%d\n",TIFFNumberOfTiles(tif));
      printf("Number of strips:%d\n",TIFFNumberOfStrips(tif));
      printf("Strip size:%d\n",TIFFStripSize(tif));
      printf("Strip size from TIFFGetField:%d\n",stripsize);
      printf("Tile size from TIFFGetField:%d\n",tilesize);
      printf("Tile width:%d\n",tileWidth);
      printf("Tile length:%d\n",tileLength);
      printf("Seems to be tiled\n");
      */
      uint32 imageWidth, imageLength;
      uint32 tileWidth, tileLength;
      uint32 x, y;
      tdata_t buf;
      uint16 config;

      TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &imageWidth);
      TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &imageLength);
      TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tileWidth);
      TIFFGetField(tif, TIFFTAG_TILELENGTH, &tileLength);
      TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);
      buf = _TIFFmalloc(TIFFTileSize(tif));
      if (config == PLANARCONFIG_CONTIG) {
        for (y = 0; y < imageLength; y += tileLength){
          for (x = 0; x < imageWidth; x += tileWidth){
            TIFFReadTile(tif, buf, x, y, 0, 0);
            for(unsigned jj=0;jj<tileLength;jj++){
              for(unsigned ii=0;ii<tileWidth*spp;ii++){
                pixels[(y+jj)*width*spp+x+ii] = ((unsigned char *)buf)[jj*tileWidth*spp+ii];
              }
            }
          }
        }
      } else if (config == PLANARCONFIG_SEPARATE) {
        uint16 s;
        for (s = 0; s < spp; s++){
          for (y = 0; y < imageLength; y += tileLength){
            for (x = 0; x < imageWidth; x += tileWidth){
              TIFFReadTile(tif, buf, x, y, 0, s);
              for(unsigned jj=0;jj<tileLength;jj++){
                for(unsigned ii=0;ii<tileWidth;ii++){
                  pixels[(y+jj)*width*spp+x+ii+s] = ((unsigned char *)buf)[jj*tileWidth+ii];
                }
              }
            }
          }
        }
      }
      _TIFFfree(buf);
    }
    if(isstripped) {
      pixels = new unsigned char[width*height*spp];
      colourspace = spp;
      if(spp==2){
        colourspace_type = IMAGEINFO_MONOA;
      }else{
        printf("Unsupported samples per pixel in readtiff: %d\n",spp);
        delete [] pixels;
        return;
      }
      uint32 imagelength;
      tdata_t buf;
      uint32 row;
      uint16 config;

      TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &imagelength);
      TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);
      buf = _TIFFmalloc(TIFFScanlineSize(tif));
      if (config == PLANARCONFIG_CONTIG) {
        for (row = 0; row < imagelength; row++){
          TIFFReadScanline(tif, buf, row,0);
          /* Just possible this may ignore a colourmap, not sure */
          memcpy(pixels+row*TIFFScanlineSize(tif),(unsigned char *)buf,sizeof(unsigned char)*TIFFScanlineSize(tif));
        }
      } else if (config == PLANARCONFIG_SEPARATE) {
        uint16 s;
        for (s = 0; s < spp; s++){
          for (row = 0; row < imagelength; row++){
            TIFFReadScanline(tif, buf, row, s);
            for(int ii=0;ii<width;ii++){
              pixels[spp*ii+s+width*row*spp] = ((unsigned char *)buf)[ii];
            }
          }
        }
      }
      _TIFFfree(buf);
    }
  } else {
    uint32 *raster;
    raster = (uint32*) _TIFFmalloc(width*height*sizeof(uint32));
    if(!raster){
      printf("Error reading tif image\n");
      return;
    }
    TIFFReadRGBAImage(tif, width, height, raster, 0);
    pixels = new unsigned char[width*height*4];
    colourspace = 4;
    colourspace_type = IMAGEINFO_RGBA;
    int ii=0;
    for(int i=height-1;i>=0;i--){
      for(int j=0;j<width;j++){
         uint32 pix = raster[i*width+j];
         unsigned char a = (pix>>24)&255;
         unsigned char b = (pix>>16)&255;
         unsigned char g = (pix>>8)&255;
         unsigned char r = pix&255;
         pixels[ii++] = r;
         pixels[ii++] = g;
         pixels[ii++] = b;
         pixels[ii++] = a;
      }
    }
    _TIFFfree(raster);
  }
  TIFFClose(tif);
#else
    printf("No tiff support compiled into this program\n");
    printf("Please install libtiff (if not already done) and\n");
    printf("rebuild adding -DUSE_LIBTIFF to C_DEFINES\n");
    printf("and CXX_DEFINES. Or convert input file to a\n");
    printf("supported format.\n");
#endif  
}

bool QTiffHandler::read(QImage *image)
{
   if (!canRead()) {
      return false;
   }

   TIFF *const tiff = TIFFClientOpen("foo",
         "r",
         this,
         qtiffReadProc,
         qtiffWriteProc,
         qtiffSeekProc,
         qtiffCloseProc,
         qtiffSizeProc,
         qtiffMapProc,
         qtiffUnmapProc);

   if (!tiff) {
      return false;
   }

   uint32_t width;
   uint32_t height;
   uint16_t photometric;

   if (!TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width)
      || !TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height)
      || !TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC, &photometric)) {
      TIFFClose(tiff);
      return false;
   }

   // BitsPerSample defaults to 1 according to the TIFF spec.
   uint16_t bitPerSample;
   if (!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitPerSample)) {
      bitPerSample = 1;
   }

   uint16_t samplesPerPixel; // they may be e.g. grayscale with 2 samples per pixel
   if (!TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel)) {
      samplesPerPixel = 1;
   }

   bool grayscale = photometric == PHOTOMETRIC_MINISBLACK || photometric == PHOTOMETRIC_MINISWHITE;
   if (grayscale && bitPerSample == 1 && samplesPerPixel == 1) {
      if (image->size() != QSize(width, height) || image->format() != QImage::Format_Mono) {
         *image = QImage(width, height, QImage::Format_Mono);
      }
      QVector<QRgb> colortable(2);
      if (photometric == PHOTOMETRIC_MINISBLACK) {
         colortable[0] = 0xff000000;
         colortable[1] = 0xffffffff;
      } else {
         colortable[0] = 0xffffffff;
         colortable[1] = 0xff000000;
      }
      image->setColorTable(colortable);

      if (!image->isNull()) {
         for (uint32_t y = 0; y < height; ++y) {
            if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
               TIFFClose(tiff);
               return false;
            }
         }
      }
   } else {
      if ((grayscale || photometric == PHOTOMETRIC_PALETTE) && bitPerSample == 8 && samplesPerPixel == 1) {
         if (image->size() != QSize(width, height) || image->format() != QImage::Format_Indexed8) {
            *image = QImage(width, height, QImage::Format_Indexed8);
         }
         if (!image->isNull()) {
            const uint16_t tableSize = 256;
            QVector<QRgb> qtColorTable(tableSize);

            if (grayscale) {
               for (int i = 0; i < tableSize; ++i) {
                  const int c = (photometric == PHOTOMETRIC_MINISBLACK) ? i : (255 - i);
                  qtColorTable[i] = qRgb(c, c, c);
               }

            } else {
               // create the color table
               uint16_t *redTable   = nullptr;
               uint16_t *greenTable = nullptr;
               uint16_t *blueTable  = nullptr;

               if (!TIFFGetField(tiff, TIFFTAG_COLORMAP, &redTable, &greenTable, &blueTable)) {
                  TIFFClose(tiff);
                  return false;
               }
               if (!redTable || !greenTable || !blueTable) {
                  TIFFClose(tiff);
                  return false;
               }

               for (int i = 0; i < tableSize ; ++i) {
                  const int red = redTable[i] / 257;
                  const int green = greenTable[i] / 257;
                  const int blue = blueTable[i] / 257;
                  qtColorTable[i] = qRgb(red, green, blue);
               }
            }

            image->setColorTable(qtColorTable);
            for (uint32_t y = 0; y < height; ++y) {
               if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                  TIFFClose(tiff);
                  return false;
               }
            }

            // free redTable, greenTable and greenTable done by libtiff
         }
      } else {
         if (image->size() != QSize(width, height) || image->format() != QImage::Format_ARGB32) {
            *image = QImage(width, height, QImage::Format_ARGB32);
         }
         if (!image->isNull()) {
            const int stopOnError = 1;
            if (TIFFReadRGBAImageOriented(tiff, width, height, reinterpret_cast<uint32_t *>(image->bits()), ORIENTATION_TOPLEFT,
                  stopOnError)) {
               for (uint32_t y = 0; y < height; ++y) {
                  convert32BitOrder(image->scanLine(y), width);
               }
            } else {
               TIFFClose(tiff);
               return false;
            }
         }
      }
   }

   if (image->isNull()) {
      TIFFClose(tiff);
      return false;
   }

   float resX = 0;
   float resY = 0;
   uint16_t resUnit;

   if (!TIFFGetField(tiff, TIFFTAG_RESOLUTIONUNIT, &resUnit)) {
      resUnit = RESUNIT_INCH;
   }

   if (TIFFGetField(tiff, TIFFTAG_XRESOLUTION, &resX)
      && TIFFGetField(tiff, TIFFTAG_YRESOLUTION, &resY)) {
      switch (resUnit) {
         case RESUNIT_CENTIMETER:
            image->setDotsPerMeterX(qRound(resX * 100));
            image->setDotsPerMeterY(qRound(resY * 100));
            break;
         case RESUNIT_INCH:
            image->setDotsPerMeterX(qRound(resX * (100 / 2.54)));
            image->setDotsPerMeterY(qRound(resY * (100 / 2.54)));
            break;
         default:
            // do nothing as defaults have already
            // been set within the QImage class
            break;
      }
   }

   // rotate the image if the orientation is defined in the file
   uint16_t orientationTag;
   if (TIFFGetField(tiff, TIFFTAG_ORIENTATION, &orientationTag)) {
      if (image->format() == QImage::Format_ARGB32) {
         // TIFFReadRGBAImageOriented() flip the image but does not rotate them
         switch (orientationTag) {
            case 5:
               rotate_right_mirror_horizontal(image);
               break;
            case 6:
               rotate_right_mirror_vertical(image);
               break;
            case 7:
               rotate_right_mirror_horizontal(image);
               break;
            case 8:
               rotate_right_mirror_vertical(image);
               break;
         }
      } else {
         switch (orientationTag) {
            case 1: // default orientation
               break;
            case 2: // mirror horizontal
               *image = image->mirrored(true, false);
               break;
            case 3: // mirror both
               *image = image->mirrored(true, true);
               break;
            case 4: // mirror vertical
               *image = image->mirrored(false, true);
               break;
            case 5: { // rotate right mirror horizontal
               QMatrix transformation;
               transformation.rotate(90);
               *image = image->transformed(transformation);
               *image = image->mirrored(true, false);
               break;
            }
            case 6: { // rotate right
               QMatrix transformation;
               transformation.rotate(90);
               *image = image->transformed(transformation);
               break;
            }
            case 7: { // rotate right, mirror vertical
               QMatrix transformation;
               transformation.rotate(90);
               *image = image->transformed(transformation);
               *image = image->mirrored(false, true);
               break;
            }
            case 8: { // rotate left
               QMatrix transformation;
               transformation.rotate(270);
               *image = image->transformed(transformation);
               break;
            }
         }
      }
   }


   TIFFClose(tiff);
   return true;
}

QVariant QTiffHandler::option(ImageOption option)
{
   if (option == Size && canRead()) {
      QSize imageSize;
      qint64 pos = device()->pos();
      TIFF *tiff = TIFFClientOpen("foo",
            "r",
            const_cast<QTiffHandler *>(this),
            qtiffReadProc,
            qtiffWriteProc,
            qtiffSeekProc,
            qtiffCloseProc,
            qtiffSizeProc,
            qtiffMapProc,
            qtiffUnmapProc);

      if (tiff) {
         uint32_t width = 0;
         uint32_t height = 0;
         TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width);
         TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
         imageSize = QSize(width, height);
         TIFFClose(tiff);
      }

      device()->seek(pos);
      if (imageSize.isValid()) {
         return imageSize;
      }

   } else if (option == CompressionRatio) {
      return compression;
   } else if (option == ImageFormat) {
      return QImage::Format_ARGB32;
   }

   return QVariant();
}

std::shared_ptr<PrimitiveObject> readTiffFile(std::string const &path, int type = 0) {
    TIFF* tif = TIFFOpen(path.c_str(), "r");
    if (!tif) {
        throw std::runtime_error("tiff read fail");
    }
    auto img = std::make_shared<PrimitiveObject>();

    uint32 width, height;
    uint16_t samplesPerPixel, bitsPerSample;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
    img->userData().set2("isImage", 1);
    img->userData().set2("w", (int)width);
    img->userData().set2("h", (int)height);
    img->userData().set2("samplesPerPixel", (int)samplesPerPixel);
    img->userData().set2("bitsPerSample", (int)bitsPerSample);
    if (bitsPerSample != 32) {
        throw std::runtime_error("tiff read fail");
    }

    std::vector<uint8_t> data_;
    uint32_t rowSize = TIFFScanlineSize(tif);
    img->userData().set2("rowSize", (int)rowSize);
    data_.resize(rowSize * height);

    for (int32_t row = 0; row < height; ++row) {
        TIFFReadScanline(tif, &data_[row * rowSize], row);
    }
    TIFFClose(tif);

    img->resize(width * height);
    if (samplesPerPixel == 4) {
        vec4f *ptr = (vec4f*)data_.data();
        auto &alpha = img->verts.add_attr<float>("alpha");
        auto &uv = img->verts.add_attr<zeno::vec2f>("uv");
        for (auto i = 0; i < height; i++) {
            for (auto j = 0; j < width; j++) {
                vec4f rgba = ptr[i * width + j];
                img->verts[i * width + j] =  { rgba[0], rgba[1], rgba[2] };
                alpha[i * width + j] =  rgba[3];
                uv[i * width + j] = vec2f(j,i);
            }
        }
    }
    else if (samplesPerPixel == 3) {
        vec3f *ptr = (vec3f*)data_.data();
        auto &uv = img->verts.add_attr<zeno::vec2f>("uv");
        for (auto i = 0; i < height; i++) {
            for (auto j = 0; j < width; j++) {
                vec3f rgb = ptr[i * width + j];
                img->verts[i * width + j] = rgb;
                uv[i * width + j] = vec2f(j,i);
            }
        }
    }
    else if (samplesPerPixel == 1) {
        auto &uv = img->verts.add_attr<zeno::vec2f>("uv");
        for (auto i = 0; i < height; i++) {
            for (auto j = 0; j < width; j++) {
                float r;
                if(type == 0)
                  r = ((int *)data_.data())[i*width + j];
                else
                  r = ((float *)data_.data())[i*width + j];
                img->verts[i * width + j] = {r, r, r};
                uv[i * width + j] = vec2f(j,i);
            }
        }
    }

    // Process image data here...
    return img;
}

bool EerFile::ReadLogicalDimensionsFromDisk(bool check_only_the_first_image) {
    MyDebugAssertTrue(tif != NULL, "File must be open");
    MyDebugAssertTrue(fh != NULL, "File must be open: %s", filename.GetFullPath( ));
    MyDebugAssertFalse(number_of_eer_frames_per_image == 0, "Number of EER frames per image has not yet been set. Cannot work out logical dimensions.");
    /*
	 * Since the file was already open, EerOpen has already read in the first dictionary
	 * and it must be valid, else we would have returned an error at open-time already
	 */
    int  set_dir_ret  = TIFFSetDirectory(tif, 0);
    bool return_value = (set_dir_ret == 1);
    int  dircount     = 1;

    uint32 original_x = 0;
    uint16 compression;

    // Work out logical dimensions of the frames (x,y)
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &logical_dimension_x);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &logical_dimension_y);
    frame_size_bits = logical_dimension_x * logical_dimension_y;

    // Work out RLE compression scheme
    TIFFGetField(tif, TIFFTAG_COMPRESSION, &compression);
    if ( compression == 65000 )
        bits_per_rle = 8;
    else if ( compression == 65001 )
        bits_per_rle = 7;
    else {
        MyPrintWithDetails("Warning:: Unknown Compression in EER tif file, assuming 7-bit (65001)");
        bits_per_rle = 7;
    }

    if ( ! check_only_the_first_image ) {
        // Work out the number of frames
        while ( TIFFSetDirectory(tif, number_of_eer_frames) != 0 )
            number_of_eer_frames++;

        // Work out the number of images
        number_of_images = number_of_eer_frames / number_of_eer_frames_per_image;

        // Work out the total file size
        fseek(fh, 0, SEEK_END);
        file_size_bytes = ftell(fh);
        fseek(fh, 0, SEEK_SET); //go back to the beginning of the file
    }

    return return_value;
}

bool TiffFile::ReadLogicalDimensionsFromDisk(bool check_only_the_first_image) {
    MyDebugAssertTrue(tif != NULL, "File must be open");

    /*
	 * Since the file was already open, TiffOpen has already read in the first dictionary
	 * and it must be valid, else we would have returned an error at open-time already
	 */
    int  set_dir_ret  = TIFFSetDirectory(tif, 0);
    bool return_value = (set_dir_ret == 1);
    int  dircount     = 1;

    uint32 original_x = 0;
    uint32 current_x  = 0;
    uint32 current_y  = 0;

    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &current_x);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &current_y);

    // Serial EM has the option to write out 4-bit compressed tif.  They way that this is done is to do it as an 8-bit with
    // half the x dimension. If the file has specific dimensions, it is assumed to be 4-bit.  I've copied the check directly
    // from David Mastronarde. - see function comments for sizeCanBe4BitK2SuperRes

    if ( sizeCanBe4BitK2SuperRes(current_x, current_y) == 1 ) {
        logical_dimension_x                     = current_x * 2;
        logical_dimension_y                     = current_y;
        this_is_in_mastronarde_4bit_hack_format = true;
    }
    else {
        logical_dimension_x                     = current_x;
        logical_dimension_y                     = current_y;
        this_is_in_mastronarde_4bit_hack_format = false;
    }

    original_x = current_x;

    if ( ! check_only_the_first_image ) {
        const bool check_dimensions_of_every_image = true;
        if ( check_dimensions_of_every_image ) {
            // Loop through all the TIFF directories and check they all have the same x,y dimensions
            // This takes ages when importing TIFFs
            while ( ! TIFFLastDirectory(tif) ) {
                // The current directory is not the last one, so let's go to the next
                dircount++;
                set_dir_ret = TIFFSetDirectory(tif, dircount - 1);

                if ( set_dir_ret != 1 ) {
                    MyPrintfRed("Warning: Image %i of file %s seems to be corrupted\n", dircount, filename.GetFullName( ));
                    return_value = false;
                    dircount--;
                    break;
                }
                else {
                    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &current_x);
                    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &current_y);

                    if ( current_x != original_x || logical_dimension_y != current_y ) {
                        MyPrintfRed("Warning: Image %i of file %s has dimensions %i,%i, whereas previous images had dimensions %i,%i\n", dircount, filename.GetFullName( ), current_x, current_y, original_x, logical_dimension_y);
                        return_value = false;
                        dircount--;
                        break;
                    }
                }
            }
            // we return the number of valid images
            number_of_images = dircount;
        }
        else {
            // This doesnt' seem to be significantly faster than the other way to do it.
            number_of_images = TIFFNumberOfDirectories(tif);
        }
    }
    else {
        // We only checked the first image, so we don't know how many images are in the file
        number_of_images = -1;
    }

    return return_value;
}

void TiffFile::ReadSlicesFromDisk(int start_slice, int end_slice, float* output_array) {
    MyDebugAssertTrue(tif != NULL, "File must be open");
    MyDebugAssertTrue(start_slice > 0 && end_slice >= start_slice && end_slice <= number_of_images, "Bad start or end slice number");

    unsigned int bits_per_sample   = 0;
    unsigned int samples_per_pixel = 0;
    unsigned int sample_format     = 0;
    unsigned int rows_per_strip    = 0;

    long output_counter;

    tstrip_t strip_counter;
    tmsize_t number_of_bytes_placed_in_buffer;

    for ( unsigned int directory_counter = start_slice - 1; directory_counter < end_slice; directory_counter++ ) {

        TIFFSetDirectory(tif, directory_counter);

        // We don't support tiles, only strips
        if ( TIFFIsTiled(tif) ) {
            MyPrintfRed("Error. Cannot read tiled TIF files. Filename = %s, Directory # %i. Number of tiles per image = %i\n", filename.GetFullPath( ), directory_counter, TIFFNumberOfTiles(tif));
        }

        // Get bit depth etc
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits_per_sample);
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samples_per_pixel);
        if ( samples_per_pixel != 1 ) {
            MyPrintfRed("Error. Unsupported number of samples per pixel: %i. Filename = %s, Directory # %i\n", samples_per_pixel, filename.GetFullPath( ), directory_counter);
        }
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sample_format);

        // How many rows per strip?
        TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &rows_per_strip);

        // Copy & cast data from these rows into the output array
        switch ( sample_format ) {
            case SAMPLEFORMAT_UINT:
                switch ( bits_per_sample ) {
                    case 8: {
                        uint8* buf = new uint8[TIFFStripSize(tif)];

                        // Serial EM has the option to write out 4-bit compressed tif.  They way that this is done is to do it as an 8-bit with
                        // half the x dimension. If the file has specific dimensions, it is assumed to be 4-bit.  I've copied the check directly
                        // from David Mastronarde. - see function comments for sizeCanBe4BitK2SuperRes

                        if ( this_is_in_mastronarde_4bit_hack_format == true ) // this is 4-bit
                        {

                            uint8 hi_4bits;
                            uint8 low_4bits;

                            strip_counter = 0;

                            number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                            //wxPrintf("%i %i %i\n",int(number_of_bytes_placed_in_buffer),int(rows_per_strip), int(rows_per_strip * ReturnXSize()));
                            if ( strip_counter < TIFFNumberOfStrips(tif) - 1 )
                                MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ), "Unexpected number of bytes in uint8 buffer");

                            for ( strip_counter = 0; strip_counter < TIFFNumberOfStrips(tif); strip_counter++ ) {

                                number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                                if ( strip_counter < TIFFNumberOfStrips(tif) - 1 )
                                    MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ) / 2, "Unexpected number of bytes in uint8 buffer");

                                output_counter = strip_counter * rows_per_strip * ReturnXSize( ) + ((directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( ));

                                for ( long counter = 0; counter < number_of_bytes_placed_in_buffer; counter++ ) {
                                    low_4bits = buf[counter] & 0x0F;
                                    hi_4bits  = (buf[counter] >> 4) & 0x0F;

                                    output_array[output_counter] = float(low_4bits);
                                    output_counter++;
                                    output_array[output_counter] = float(hi_4bits);
                                    output_counter++;
                                }
                            }
                        }
                        else // not 4-bit
                        {
                            for ( strip_counter = 0; strip_counter < TIFFNumberOfStrips(tif); strip_counter++ ) {

                                number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                                if ( strip_counter < TIFFNumberOfStrips(tif) - 1 )
                                    MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ), "Unexpected number of bytes in uint8 buffer");

                                output_counter = strip_counter * rows_per_strip * ReturnXSize( ) + ((directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( ));
                                for ( long counter = 0; counter < number_of_bytes_placed_in_buffer; counter++ ) {
                                    output_array[output_counter] = buf[counter];
                                    output_counter++;
                                }
                            }
                        }

                        delete[] buf;
                    } break;
                    case 16: {
                        uint16* buf = new uint16[TIFFStripSize(tif) / 2];

                        for ( strip_counter = 0; strip_counter < TIFFNumberOfStrips(tif); strip_counter++ ) {
                            number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                            MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ) * 2, "Unexpected number of bytes in uint16 buffer");

                            output_counter = strip_counter * rows_per_strip * ReturnXSize( ) + ((directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( ));
                            for ( long counter = 0; counter < number_of_bytes_placed_in_buffer / 2; counter++ ) {
                                output_array[output_counter] = buf[counter];
                                output_counter++;
                            }
                        }
                        delete[] buf;
                    } break;
                    default:
                        MyPrintfRed("Error. Unsupported uint bit depth: %i. Filename = %s, Directory # %i\n", bits_per_sample, filename.GetFullPath( ), directory_counter);
                        break;
                }
                break;
            case SAMPLEFORMAT_INT:
                switch ( bits_per_sample ) {
                    case 16: {
                        int16* buf = new int16[TIFFStripSize(tif) / 2];

                        for ( strip_counter = 0; strip_counter < TIFFNumberOfStrips(tif); strip_counter++ ) {
                            number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                            MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ) * 2, "Unexpected number of bytes in uint16 buffer");

                            output_counter = strip_counter * rows_per_strip * ReturnXSize( ) + ((directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( ));
                            for ( long counter = 0; counter < number_of_bytes_placed_in_buffer / 2; counter++ ) {
                                output_array[output_counter] = buf[counter];
                                output_counter++;
                            }
                        }
                        delete[] buf;
                    } break;
                    default:
                        MyPrintfRed("Error. Unsupported int bit depth: %i. Filename = %s, Directory # %i\n", bits_per_sample, filename.GetFullPath( ), directory_counter);
                        break;
                }
                break;
            case SAMPLEFORMAT_IEEEFP: {
                switch ( bits_per_sample ) {
                    case 32: {
                        float* buf = new float[TIFFStripSize(tif) / 4];

                        for ( strip_counter = 0; strip_counter < TIFFNumberOfStrips(tif); strip_counter++ ) {
                            number_of_bytes_placed_in_buffer = TIFFReadEncodedStrip(tif, strip_counter, (char*)buf, (tsize_t)-1);
                            MyDebugAssertTrue(number_of_bytes_placed_in_buffer == rows_per_strip * ReturnXSize( ) * 4, "Unexpected number of bytes in float buffer");

                            output_counter = strip_counter * rows_per_strip * ReturnXSize( ) + ((directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( ));
                            for ( long counter = 0; counter < number_of_bytes_placed_in_buffer / 4; counter++ ) {
                                output_array[output_counter] = buf[counter];
                                output_counter++;
                            }
                        }
                        delete[] buf;
                    } break;
                    default:
                        MyPrintfRed("Error. Unsupported float bit depth: %i. Filename = %s, Directory # %i\n", bits_per_sample, filename.GetFullPath( ), directory_counter);
                        break;
                }
                break;
            } break;
            default:
                MyPrintfRed("Error. Unsupported sample format: %i. Filename = %s, Directory # %i\n", sample_format, filename.GetFullPath( ), directory_counter);
                break;
        }

        // Annoyingly, we need to swap the order of the lines to be "compatible" with MRC files etc
        {
            float* temp_line                      = new float[ReturnXSize( )];
            long   address_of_start_of_slice      = (directory_counter - start_slice + 1) * ReturnXSize( ) * ReturnYSize( );
            long   address_of_start_of_line       = address_of_start_of_slice;
            long   address_of_start_of_other_line = address_of_start_of_slice + (ReturnYSize( ) - 1) * ReturnXSize( );
            long   counter;

            for ( long line_counter = 0; line_counter < ReturnYSize( ) / 2; line_counter++ ) {
                // Copy current line to a buffer
                for ( counter = 0; counter < ReturnXSize( ); counter++ ) {
                    temp_line[counter] = output_array[counter + address_of_start_of_line];
                }
                // Copy other line to current line
                for ( counter = 0; counter < ReturnXSize( ); counter++ ) {
                    output_array[counter + address_of_start_of_line] = output_array[counter + address_of_start_of_other_line];
                }
                // Copy line from buffer to other line
                for ( counter = 0; counter < ReturnXSize( ); counter++ ) {
                    output_array[counter + address_of_start_of_other_line] = temp_line[counter];
                }
                // Get ready for next iteration
                address_of_start_of_line += ReturnXSize( );
                address_of_start_of_other_line -= ReturnXSize( );
            }

            delete[] temp_line;
        }

    } // end of loop over slices
}

bool TIFRaster::Data::Create()
{
	TIFFSetErrorHandler(NULL);
	TIFFSetErrorHandlerExt(NULL);
	TIFFSetWarningHandler(NULL);
	TIFFSetWarningHandlerExt(NULL);

	tiff = TIFFStreamOpen("tiff@" + Format64((intptr_t)this), "r", &stream);
	if(!tiff)
		return false;

	int count = TIFFNumberOfDirectories(tiff);
	if(count <= 0)
		return false;
	for(int i = 0; i < count; i++) {
		Page& page = pages.Add();
		TIFFSetDirectory(tiff, i);
		TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &page.width);
		TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &page.height);
		float xres, yres;
		TIFFGetFieldDefaulted(tiff, TIFFTAG_XRESOLUTION, &xres);
		TIFFGetFieldDefaulted(tiff, TIFFTAG_YRESOLUTION, &yres);
		uint16 resunit;
		TIFFGetFieldDefaulted(tiff, TIFFTAG_RESOLUTIONUNIT, &resunit);
		TIFFGetFieldDefaulted(tiff, TIFFTAG_BITSPERSAMPLE, &page.bits_per_sample);
		TIFFGetFieldDefaulted(tiff, TIFFTAG_SAMPLESPERPIXEL, &page.samples_per_pixel);
		TIFFGetFieldDefaulted(tiff, TIFFTAG_PHOTOMETRIC, &page.photometric);
		TIFFGetFieldDefaulted(tiff, TIFFTAG_ORIENTATION, &page.orientation);
		double dots_per_unit = (resunit == RESUNIT_INCH ? 600.0 : resunit == RESUNIT_CENTIMETER
			? 600.0 / 2.54 : 0);
		page.dot_size.cx = (xres ? fround(page.width * dots_per_unit / xres) : 0);
		page.dot_size.cy = (yres ? fround(page.height * dots_per_unit / yres) : 0);
		page.alpha = false;
		uint16 extrasamples, *sampletypes;
		TIFFGetFieldDefaulted(tiff, TIFFTAG_EXTRASAMPLES, &extrasamples, &sampletypes);
		for(int e = 0; e < extrasamples; e++)
			if(sampletypes[e] == EXTRASAMPLE_ASSOCALPHA) {
				page.alpha = true;
				break;
			}
		
		if(i == 0) {
			const word TIFFTAG_GEOPIXELSCALE = 33550,
			           TIFFTAG_GEOTIEPOINTS = 33922,
			           TIFFTAG_GEOTRANSMATRIX = 34264,
			           TIFFTAG_GEOKEYDIRECTORY = 34735,
			           TIFFTAG_GEODOUBLEPARAMS = 34736,
			           TIFFTAG_GEOASCIIPARAMS = 34737;

			word count = 0;
			word *geokeys = nullptr;
			bool pixel_is_area = true; // Default is true, usually only DTM/DEM use 'RasterPixelIsPoint' and generally include the GeoKey in such case

			int dpc = 0;
			double *doubleparams = 0;

			int apc = 0;
			char *asciiparams = 0;

			TIFFGetField(tiff, TIFFTAG_GEODOUBLEPARAMS, &dpc, &doubleparams);
			TIFFGetField(tiff, TIFFTAG_GEOASCIIPARAMS, &apc, &asciiparams);

			if(TIFFGetField(tiff, TIFFTAG_GEOKEYDIRECTORY, &count, &geokeys) &&
				count >= 4 && (count % 4) == 0 && geokeys[0] == 1)
				for(int i = 4; i + 3 < count; i += 4) {
					const word GTRasterTypeGeoKey = 1025,
					           RasterPixelIsArea = 1,
					           GeographicTypeGeoKey = 2048,
					           ProjectedCSTypeGeoKey = 3072;

					word value = geokeys[i + 3];

					switch(geokeys[i]) {
					case GTRasterTypeGeoKey:
						pixel_is_area = value == RasterPixelIsArea;
						break;
					case GeographicTypeGeoKey:
					case ProjectedCSTypeGeoKey:
						attr.GetAdd("epsg") = value;
						break;
					}

					switch(geokeys[i+1]){ // by type
						case TIFFTAG_GEOASCIIPARAMS:
							if(geokeys[i+3]<=apc) attr.GetAdd(Format("GK%d",geokeys[i])) = Value(String(&asciiparams[geokeys[i+3]],geokeys[i+2]));
							break;
						case TIFFTAG_GEODOUBLEPARAMS:
							if(geokeys[i+3]<=dpc) attr.GetAdd(Format("GK%d",geokeys[i])) = Value(doubleparams[geokeys[i+3]]);
							break;
						default:
							attr.GetAdd(Format("GK%d",geokeys[i])) = Value(geokeys[i+3]);
							break;
					}
				}

			double geomatrix[6];
	        geomatrix[0] = 0.0;
	        geomatrix[1] = 1.0;
	        geomatrix[2] = 0.0;
	        geomatrix[3] = 0.0;
	        geomatrix[4] = 0.0;
	        geomatrix[5] = 1.0;
	        
	        auto AdjustMatrixOrientation = [&](double dx, double dy) {
				geomatrix[0] += (geomatrix[1] * dx + geomatrix[2] * dy);
				geomatrix[3] += (geomatrix[4] * dx + geomatrix[5] * dy);
				if(dx!=0){
					geomatrix[1] = -geomatrix[1];
					geomatrix[4] = -geomatrix[4];
				}
				if(dy!=0){
					geomatrix[2] = -geomatrix[2];
					geomatrix[5] = -geomatrix[5];
				}
	        };
	        
			auto NormalizeOrientation = [&] {
				if(!pixel_is_area) {
					geomatrix[0] -= (geomatrix[1] * 0.5 + geomatrix[2] * 0.5);
					geomatrix[3] -= (geomatrix[4] * 0.5 + geomatrix[5] * 0.5);
				}
				if(page.orientation>=5){
					Swap(geomatrix[1],geomatrix[2]);
					Swap(geomatrix[4],geomatrix[5]);
				}
				switch(page.orientation){
					case 2:
						AdjustMatrixOrientation(page.width, 0);
						break;
					case 3:
						AdjustMatrixOrientation(page.width, page.height);
						break;
					case 4:
						AdjustMatrixOrientation(0, page.height);
						break;
					case 6:
						AdjustMatrixOrientation(page.height, 0);
						break;
					case 7:
						AdjustMatrixOrientation(page.height, page.width);
						break;
					case 8:
						AdjustMatrixOrientation(0, page.width);
						break;
				}
				// TIFF orientations:
				// 1 = FLIP_MIRROR_VERT,
				// 2 = FLIP_ROTATE_180,
				// 3 = FLIP_MIRROR_HORZ,
				// 4 = FLIP_NONE,
				// 5 = FLIP_ROTATE_CLOCKWISE,
				// 6 = FLIP_TRANSPOSE,
				// 7 = FLIP_ROTATE_ANTICLOCKWISE,
				// 8 = FLIP_TRANSVERSE,
			};
	    
	        double  *data;
	        auto FinishMatrix = [&] {
				ValueArray va;
				for(int i = 0; i < 6; i++)
					va << geomatrix[i];
				attr.GetAdd("geo_matrix") = va;
	        };
	        
			if(TIFFGetField(tiff, TIFFTAG_GEOTRANSMATRIX, &count, &data) && count == 16) {
				geomatrix[0] = data[3];
				geomatrix[1] = data[0];
				geomatrix[2] = data[1];
				geomatrix[3] = data[7];
				geomatrix[4] = data[4];
				geomatrix[5] = data[5];

				NormalizeOrientation();
				FinishMatrix();
			}
			else
			if(TIFFGetField(tiff, TIFFTAG_GEOTIEPOINTS, &count, &data) && count >= 6) {
				if(count>=18){ // 3 tiepoints needed for Xform2D::Map()
					for(int t=0; t<count; t+=6){
						if(page.orientation>=5) Swap(data[t + 0], data[t + 1]);
						switch(page.orientation){
							case 1:
								break;
							case 2:
								data[t + 0] = page.width - data[t + 0];
								break;
							case 3:
								data[t + 0] = page.width - data[t + 0];
								data[t + 1] = page.height - data[t + 1];
								break;
							case 4:
								data[t + 1] = page.height - data[t + 1];
								break;
							case 5:
								break;
							case 6:
								data[t + 0] = page.height - data[t + 0];
								break;
							case 7:
								data[t + 0] = page.height - data[t + 0];
								data[t + 1] = page.width - data[t + 1];
								break;
							case 8:
								data[t + 1] = page.width - data[t + 1];
								break;
						}
					}

					Xform2D xf = Xform2D::Map(Pointf(data[0],data[1]),Pointf(data[6+0],data[6+1]),Pointf(data[12+0],data[12+1]),
												Pointf(data[3],data[4]),Pointf(data[6+3],data[6+4]),Pointf(data[12+3],data[12+4]));

					geomatrix[0] = xf.t.x;
					geomatrix[1] = xf.x.x;
					geomatrix[2] = xf.y.x;
					geomatrix[3] = xf.t.y;
					geomatrix[4] = xf.x.y;
					geomatrix[5] = xf.y.y;

					if(!pixel_is_area) {
						geomatrix[0] -= (geomatrix[1] * 0.5 + geomatrix[2] * 0.5);
						geomatrix[3] -= (geomatrix[4] * 0.5 + geomatrix[5] * 0.5);
					}
					FinishMatrix();
				}
				else{
					double x = data[0];
					double y = data[1];
					double e = data[3];
					double n = data[4];

					if(TIFFGetField(tiff, TIFFTAG_GEOPIXELSCALE, &count, &data) && count >= 2 && data[0] && data[1]) {
						double dx = data[0];
						double dy = -abs(data[1]);

						geomatrix[0] = e - x * dx;
						geomatrix[1] = dx;
						geomatrix[2] = 0;
						geomatrix[3] = n - y * dy;
						geomatrix[4] = 0;
						geomatrix[5] = dy;

						NormalizeOrientation();
						FinishMatrix();
					}
				}
			}

			if(TIFFGetField(tiff, 42113, &count, &data) && // TIFFTAG_GDAL_NODATA = 42113
				count >= 1){
					attr.GetAdd("nodata") = String((const char*)data, count);
				}

		}
	}
	return SeekPage(0);
}

char * _loadTIFF(const char *filename, unsigned *width, unsigned *height, unsigned *numcolors, unsigned *numbits, std::map<std::string,std::string> &info, std::string params="", char ** icc_m=NULL, unsigned  *icclength=0)
{
	char *img, *buf;
	FILE * infile;
	uint32_t w, h;
	uint16_t c, b;

	TIFFSetErrorHandler(0);

	TIFF* tif = TIFFOpen(filename, "r");
	if (tif) {

		size_t npixels;
		uint32_t* raster;

		uint32_t imagelength, imagewidth;
		uint16_t config, nsamples, sampleformat, uval;

		unsigned len;
		char * buffer;

		TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
		TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
		TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &c);
		TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &b);
		TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);

		if (config != PLANARCONFIG_CONTIG) return NULL;

		char *infobuf;
		//if (TIFFGetField(tif, TIFFTAG_ARTIST, &infobuf)) info["Artist"]=infobuf; 
		//if (TIFFGetField(tif, TIFFTAG_MAKE, &infobuf))  info["Make"]=infobuf;
		//if (TIFFGetField(tif, TIFFTAG_MODEL, &infobuf))  info["Model"]=infobuf;
		//if (TIFFGetField(tif, TIFFTAG_SOFTWARE, &infobuf))  info["Software"]=infobuf; 
		//if (TIFFGetField(tif, TIFFTAG_COPYRIGHT, &infobuf))  info["Copyright"]=infobuf; 
		//if (TIFFGetField(tif, TIFFTAG_LENSINFO, &infobuf))  info["LensInfo"]=infobuf; 
		//if (TIFFGetField(tif, TIFFTAG_IMAGEDESCRIPTION, &infobuf))  info["ImageDescription"]=infobuf; 
		//if (TIFFGetField(tif, TIFFTAG_DATETIME, &infobuf)) info["DateTime"]=infobuf;
		//if (TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleformat)) {
		//	if (sampleformat == SAMPLEFORMAT_UINT) info["SampleFormat"]="uint";
		//	if (sampleformat == SAMPLEFORMAT_INT) info["SampleFormat"]="int";
		//	if (sampleformat == SAMPLEFORMAT_IEEEFP) info["SampleFormat"]="float";
		//	if (sampleformat == SAMPLEFORMAT_VOID) info["SampleFormat"]="void";
		//}
		//if (TIFFGetField(tif, TIFFTAG_ORIENTATION, &uval)) info["Orientation"]=tostr(uval);
/*
		if (TIFFGetField(tif, TIFFTAG_ICCPROFILE, &len, &buffer)) {
			*icc_m = new char[len];
			memcpy(*icc_m, buffer, len);
			*icclength = len;
		}
*/
//		else {
			*icc_m = NULL;
			*icclength = 0;
//		}

		img = new char[w*h*c*(b/8)];
		buf = (char *) _TIFFmalloc(TIFFScanlineSize(tif));
		int stride = TIFFScanlineSize(tif);

		char * dst = (char *) img;
		for (unsigned y = 0; y < h; y++){
			TIFFReadScanline(tif, buf, y, 0);
			memcpy(dst,buf,stride);
			dst += stride;
		}

/*
		uint32_t read_dir_offset; uint32_t count;
		float fval;
		uint16_t * sval;
		if (TIFFGetField(tif, TIFFTAG_EXIFIFD, &read_dir_offset )) {
			if (TIFFReadEXIFDirectory(tif, read_dir_offset)) {
				if (TIFFGetField( tif, EXIFTAG_FNUMBER, &fval)) info["FNumber"] = tostr(fval);
				if (TIFFGetField( tif, EXIFTAG_EXPOSURETIME, &fval)) info["ExposureTime"] = tostr(fval);
				if (TIFFGetField( tif, EXIFTAG_FOCALLENGTH, &fval)) info["FocalLength"] = tostr(fval);
				if (TIFFGetField( tif, EXIFTAG_ISOSPEEDRATINGS, &count, &sval)) info["ISOSpeedRatings"] = tostr(*sval);
			}
			else printf("TIFFReadEXIFDirectory failed\n");
		}
*/

		*width = w;
		*height = h;
		*numcolors = c;
		*numbits = b;
		if (buf) _TIFFfree(buf);
		TIFFClose(tif);
		return img;
	}
	else return NULL;
}

CImage* CImageIOTiff::ReadImage(CNcbiIstream& istr)
{
    TIFF*            tiff             = NULL;
    uint32*          raster           = NULL;
    TIFFErrorHandler old_err_handler  = NULL;
    TIFFErrorHandler old_warn_handler = NULL;
    CRef<CImage> image;
    try {

        old_err_handler  = TIFFSetErrorHandler(&s_TiffReadErrorHandler);
        old_warn_handler = TIFFSetWarningHandler(&s_TiffWarningHandler);

        // open our file
        tiff = TIFFClientOpen("", "rm",
                              reinterpret_cast<thandle_t>(&istr),
                              s_TIFFReadHandler,
                              s_TIFFDummyIOHandler,
                              s_TIFFSeekHandler,
                              s_TIFFCloseHandler,
                              s_TIFFSizeHandler,
                              s_TIFFMapFileHandler,
                              s_TIFFUnmapFileHandler);
        if ( !tiff ) {
            NCBI_THROW(CImageException, eReadError,
                       "CImageIOTiff::ReadImage(): error opening file ");
        }

        // extract the size parameters
        size_t width  = 0;
        size_t height = 0;
        size_t depth  = 0;
        TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH,  &width);
        TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &depth);

        if (depth != 1  &&  depth != 3  &&  depth != 4) {
            string msg("CImageIOTiff::ReadImage(): unhandled image depth: ");
            msg += NStr::NumericToString(depth);
            NCBI_THROW(CImageException, eReadError, msg);
        }

        // NB: we treat single plane images as RGB
        if (depth == 1) {
            depth = 3;
        }

        // allocate a temporary buffer for the image
        raster = (uint32*)_TIFFmalloc(tsize_t(width * height * sizeof(uint32)));
        if ( !TIFFReadRGBAImage(tiff, (uint32)width, (uint32)height, raster, 1) ) {
            _TIFFfree(raster);

            NCBI_THROW(CImageException, eReadError,
                       "CImageIOTiff::ReadImage(): error reading file");
        }

        // now we need to copy this data and pack it appropriately
        // according to the TIFFRGBAImage man page, TIFFRGBAImage and
        // TIFFReadRGBAImage create the image in *RASTER* format - i.e.,
        // with (0, 0) being the lower-left corner, not the upper-left
        // corner as everyone else in the world expects
        image = new CImage(width, height, depth);
        unsigned char* data = image->SetData();
        for (size_t j = 0;  j < height;  ++j) {
            // implicit inversion
            size_t from_idx_base = j * width;
            size_t to_idx_base   = (height - j - 1) * width;

            size_t i;
            switch (depth) {
            case 3:
                for (i = 0;  i < width;  ++i) {
                    size_t from_idx = from_idx_base + i;
                    size_t to_idx   = to_idx_base + i;

                    // TIFFReadRGBAImage() returns data in ABGR image,
                    // packed as a 32-bit value, so we need to pick this
                    // apart here
                    uint32 pixel = raster[from_idx];
                    data[3 * to_idx + 0] = TIFFGetR(pixel);
                    data[3 * to_idx + 1] = TIFFGetG(pixel);
                    data[3 * to_idx + 2] = TIFFGetB(pixel);
                }
                break;

            case 4:
                for (i = 0;  i < width;  ++i) {
                    size_t from_idx = from_idx_base + i;
                    size_t to_idx   = to_idx_base + i;

                    // TIFFReadRGBAImage() returns data in ABGR image,
                    // packed as a 32-bit value, so we need to pick this
                    // apart here
                    uint32 pixel = raster[from_idx];
                    data[4 * to_idx + 0] = TIFFGetR(pixel);
                    data[4 * to_idx + 1] = TIFFGetG(pixel);
                    data[4 * to_idx + 2] = TIFFGetB(pixel);
                    data[4 * to_idx + 3] = (unsigned char)TIFFGetA(pixel);
                }
                break;
            }
        }

        // clean-up
        _TIFFfree(raster);
        TIFFClose(tiff);
    }
    catch (...) {
        if (raster) {
            _TIFFfree(raster);
            raster = NULL;
        }

        if (tiff) {
            TIFFClose(tiff);
            tiff = NULL;
        }

        TIFFSetErrorHandler(old_err_handler);
        TIFFSetWarningHandler(old_warn_handler);

        // throw to a higher level
        throw;
    }

    TIFFSetErrorHandler(old_err_handler);
    TIFFSetWarningHandler(old_warn_handler);
    return image.Release();
}

bool CImageIOTiff::ReadImageInfo(CNcbiIstream& istr,
                                size_t* width, size_t* height, size_t* depth)
{
    TIFF*            tiff             = NULL;
    TIFFErrorHandler old_err_handler  = NULL;
    TIFFErrorHandler old_warn_handler = NULL;

    bool success = false;
    try {

        old_err_handler  = TIFFSetErrorHandler(&s_TiffReadErrorHandler);
        old_warn_handler = TIFFSetWarningHandler(&s_TiffWarningHandler);

        // open our file
        tiff = TIFFClientOpen("", "rm",
                              reinterpret_cast<thandle_t>(&istr),
                              s_TIFFReadHandler,
                              s_TIFFDummyIOHandler,
                              s_TIFFSeekHandler,
                              s_TIFFCloseHandler,
                              s_TIFFSizeHandler,
                              s_TIFFMapFileHandler,
                              s_TIFFUnmapFileHandler);
        if ( !tiff ) {
            NCBI_THROW(CImageException, eReadError,
                       "CImageIOTiff::ReadImageInfo(): error opening file ");
        }

        // extract the size parameters
        if (width) {
            TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, width);
        }
        if (height) {
            TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, height);
        }
        if (depth) {
            TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, depth);
        }
        TIFFClose(tiff);

        success = true;
    }
    catch (...) {
    }

    TIFFSetErrorHandler(old_err_handler);
    TIFFSetWarningHandler(old_warn_handler);

    return success;
}

int ImageIO::loadTIFF(const Glib::ustring &fname)
{

    static MyMutex thumbMutex;
    MyMutex::MyLock lock(thumbMutex);

    if(!options.serializeTiffRead) {
        lock.release();
    }

    IMFILE *src = fopen(fname.c_str());
    TIFF *in = TIFFClientOpen(
        fname.c_str(), "r", static_cast<thandle_t>(src),
        tiff_Read, tiff_Write, tiff_Seek, tiff_Close, tiff_Size,
        tiff_Map, tiff_Unmap);

    if (in == nullptr) {
        return IMIO_CANNOTREADFILE;
    }

    if (pl) {
        pl->setProgressStr ("PROGRESSBAR_LOADTIFF");
        pl->setProgress (0.0);
    }

    int width, height;
    TIFFGetField(in, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(in, TIFFTAG_IMAGELENGTH, &height);

    uint16 bitspersample, samplesperpixel;
    int hasTag = TIFFGetField(in, TIFFTAG_BITSPERSAMPLE, &bitspersample);
    hasTag &= TIFFGetField(in, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel);

    if (!hasTag) {
        // These are needed
        TIFFClose(in);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    uint16 config;
    TIFFGetField(in, TIFFTAG_PLANARCONFIG, &config);

    if (config != PLANARCONFIG_CONTIG) {
        TIFFClose(in);
        return IMIO_VARIANTNOTSUPPORTED;
    }

    if (sampleFormat & (IIOSF_LOGLUV24 | IIOSF_LOGLUV32)) {
        TIFFSetField(in, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_FLOAT);
    }

    /*
     * We could use the min/max values set in TIFFTAG_SMINSAMPLEVALUE and
     * TIFFTAG_SMAXSAMPLEVALUE, but for now, we normalize the image to the
     * effective minimum and maximum values
     */
    if (options.rtSettings.verbose) {
        printf("Information of \"%s\":\n", fname.c_str());
        uint16 tiffDefaultScale, tiffBaselineExposure, tiffLinearResponseLimit;
        if (TIFFGetField(in, TIFFTAG_DEFAULTSCALE, &tiffDefaultScale)) {
            printf("   DefaultScale: %d\n", tiffDefaultScale);
        }
        else
            printf("   No DefaultScale value!\n");
        if (TIFFGetField(in, TIFFTAG_BASELINEEXPOSURE, &tiffBaselineExposure)) {
            printf("   BaselineExposure: %d\n", tiffBaselineExposure);
        }
        else
            printf("   No BaselineExposure value!\n");
        if (TIFFGetField(in, TIFFTAG_LINEARRESPONSELIMIT, &tiffLinearResponseLimit)) {
            printf("   LinearResponseLimit: %d\n", tiffLinearResponseLimit);
        }
        else
            printf("   No LinearResponseLimit value!\n");

        // agriggio 2020-11-06: this causes a segfault when compiled with -O3 and PROC_TARGET_NUMBER=2
        // on gcc 7.5.0, Ubuntu 18.04. Need to understand why...
        // 
        // uint16 tiffMinValue, tiffMaxValue;
        // if (TIFFGetField(in, TIFFTAG_SMINSAMPLEVALUE, &tiffMinValue)) {
        //     printf("   MinValue: %d\n", tiffMinValue);
        // }
        // else
        //     printf("   No minimum value!\n");
        // if (TIFFGetField(in, TIFFTAG_SMAXSAMPLEVALUE, &tiffMaxValue)) {
        //     printf("   MaxValue: %d\n\n", tiffMaxValue);
        // }
        // else
        //     printf("   No maximum value!\n\n");
        // printf("   Those values are not taken into account, the image data are normalized to a [0;1] range\n\n");
    }

    char* profdata;
    deleteLoadedProfileData();
    loadedProfileDataJpg = false;

    if (TIFFGetField(in, TIFFTAG_ICCPROFILE, &loadedProfileLength, &profdata)) {
        embProfile = cmsOpenProfileFromMem (profdata, loadedProfileLength);
        loadedProfileData = new char [loadedProfileLength];
        memcpy (loadedProfileData, profdata, loadedProfileLength);
    } else {
        embProfile = nullptr;
    }

    allocate (width, height);

    unsigned char* linebuffer = new unsigned char[TIFFScanlineSize(in) * (samplesperpixel == 1 ? 3 : 1)];

    for (int row = 0; row < height; row++) {
        if (TIFFReadScanline(in, linebuffer, row, 0) < 0) {
            TIFFClose(in);
            delete [] linebuffer;
            return IMIO_READERROR;
        }

        if (samplesperpixel > 3) {
            for (int i = 0; i < width; i++) {
                memmove(linebuffer + i * 3 * bitspersample / 8, linebuffer + i * samplesperpixel * bitspersample / 8, 3 * bitspersample / 8);
            }
        }
        else if (samplesperpixel == 1) {
            const size_t bytes = bitspersample / 8;
            for (int i = width - 1; i >= 0; --i) {
                const unsigned char* const src = linebuffer + i * bytes;
                unsigned char* const dest = linebuffer + i * 3 * bytes;
                memcpy(dest + 2 * bytes, src, bytes);
                memcpy(dest + 1 * bytes, src, bytes);
                memcpy(dest + 0 * bytes, src, bytes);
            }
        }

        setScanline (row, linebuffer, bitspersample);

        if (pl && !(row % 100)) {
            pl->setProgress ((double)(row + 1) / height);
        }
    }

    TIFFClose(in);
    delete [] linebuffer;

    if (pl) {
        pl->setProgressStr ("PROGRESSBAR_READY");
        pl->setProgress (1.0);
    }

    return IMIO_SUCCESS;
}

int GeoImage::TiffOpen(string path)
{
    TIFFSetErrorHandler(myTiffErrHandler);

    GeoImage::TiffClose();

    // Check for a valid file name (this avoids Linux crash):
    //
    struct stat statbuf;
    if (stat(path.c_str(), &statbuf) < 0) {
        SetErrMsg("Invalid tiff file: %s\n", path.c_str());
        return -1;
    }

    // Not using memory-mapped IO (m) is reputed to help plug
    // leaks (but doesn't do any good on windows for me)
    //
    _tif = XTIFFOpen(path.c_str(), "rm");
    if (!_tif) {
        SetErrMsg("Unable to open tiff file: %s\n", path.c_str());
        return -1;
    }

    char emsg[1000];
    int  ok = TIFFRGBAImageOK(_tif, emsg);
    if (!ok) {
        MyBase::SetErrMsg("Unable to process tiff file:\n %s\nError message: %s", path.c_str(), emsg);
        return (-1);
    }

    // Check compression.  Some compressions, e.g. jpeg, cause crash on Linux
    //
#ifdef VAPOR3_0_0_ALPHA
    short compr = 1;
    ok = TIFFGetField(_tif, TIFFTAG_COMPRESSION, &compr);
    if (ok) {
        if (compr != COMPRESSION_NONE && compr != COMPRESSION_LZW && compr != COMPRESSION_JPEG && compr != COMPRESSION_CCITTRLE) {
            MyBase::SetErrMsg("Unsupported Tiff compression");
            return (-1);
        }
    }
#endif

    return (0);
}

int GeoImage::TiffGetImageDimensions(int dirnum, size_t &width, size_t &height) const
{
    VAssert(_tif != NULL);
    width = 0;
    height = 0;

    bool ok = (bool)TIFFSetDirectory(_tif, dirnum);
    if (!ok) return (-1);

    uint32_t w;
    ok = (bool)TIFFGetField(_tif, TIFFTAG_IMAGEWIDTH, &w);
    if (!ok) return (-1);

    uint32_t h;
    ok = (bool)TIFFGetField(_tif, TIFFTAG_IMAGELENGTH, &h);
    if (!ok) return (-1);

    width = (size_t)w;
    height = (size_t)h;

    return (0);
}

int GeoImage::TiffReadImage(int dirnum, unsigned char *texture) const
{
    VAssert(_tif != NULL);

    uint32_t *texuint32_t = (uint32_t *)texture;

    bool ok = (bool)TIFFSetDirectory(_tif, dirnum);
    if (!ok) return (-1);

    size_t w, h;
    int    rc = GeoImage::TiffGetImageDimensions(dirnum, w, h);
    if (rc < 0) return (-1);

    // Check if this is a 2-component 8-bit image.  These are read
    // by scanline since TIFFReadRGBAImage
    // apparently does not know how to get the alpha channel
    //
    short nsamples, nbitspersample;
    ok = TIFFGetField(_tif, TIFFTAG_SAMPLESPERPIXEL, &nsamples);
    if (!ok) return (-1);

    ok = TIFFGetField(_tif, TIFFTAG_BITSPERSAMPLE, &nbitspersample);
    if (!ok) return (-1);

    if (nsamples == 2 && nbitspersample == 8) {
        tdata_t buf;
        uint32_t  row;
        short   config;
        short   photometric;

        TIFFGetField(_tif, TIFFTAG_PLANARCONFIG, &config);
        if (!ok) return (-1);

        TIFFGetField(_tif, TIFFTAG_PHOTOMETRIC, &photometric);
        if (!ok) return (-1);

        buf = _TIFFmalloc(TIFFScanlineSize(_tif));
        VAssert(buf != NULL);

        unsigned char *charArray = (unsigned char *)buf;
        int            scanlength = TIFFScanlineSize(_tif) / 2;

        if (config == PLANARCONFIG_CONTIG) {
            for (row = 0; row < h; row++) {
                int revrow = h - row - 1;    // reverse, go bottom up
                int rc = TIFFReadScanline(_tif, buf, row);
                if (rc < 0) {
                    MyBase::SetErrMsg("Error reading tiff file:\n %s\n", _path.c_str());
                    _TIFFfree(buf);
                    return (-1);
                }
                for (int k = 0; k < scanlength; k++) {
                    unsigned char greyval = charArray[2 * k];
                    // If white is zero, reverse it:
                    if (!photometric) greyval = 255 - greyval;
                    unsigned char alphaval = charArray[2 * k + 1];
                    texuint32_t[revrow * w + k] = alphaval << 24 | greyval << 16 | greyval << 8 | greyval;
                }
            }
        } else if (config == PLANARCONFIG_SEPARATE) {
            uint16_t s;

            // Note: following loop (adapted from tiff docs) has not
            // been tested.  Are there tiff
            // examples with separate alpha channel?
            //
            for (s = 0; s < nsamples; s++) {
                for (row = 0; row < h; row++) {
                    int rc = TIFFReadScanline(_tif, buf, row, s);
                    if (rc < 0) {
                        MyBase::SetErrMsg("Error reading tiff file:\n %s\n", _path.c_str());
                        _TIFFfree(buf);
                        return (-1);
                    }
                    int revrow = h - row - 1;    // reverse, go bottom up
                    if (!(s % 2)) {              // color
                        for (int k = 0; k < h; k++) {
                            unsigned char greyval = charArray[k];
                            // If white is zero, reverse it:
                            if (!photometric) greyval = 255 - greyval;
                            texuint32_t[revrow * w + k] = greyval << 16 | greyval << 8 | greyval;
                        }
                    } else {    // alpha
                        for (int k = 0; k < h; k++) {
                            unsigned char alphaval = charArray[k];
                            texuint32_t[revrow * w + k] = alphaval << 24 | (texuint32_t[revrow * w + k] & 0xffffff);
                        }
                    }
                }
            }
        }
        _TIFFfree(buf);
        return (0);

    } else {
        // Read pixels, whether or not we are georeferenced:

        ok = TIFFReadRGBAImage(_tif, w, h, texuint32_t, 0);
        if (!ok) {
            MyBase::SetErrMsg("Error reading tiff file:\n %s\n", _path.c_str());
            return -1;
        }

        return (0);
    }
}

void GeoImageGeoTiff::_initTimeVector(TIFF *tif, const vector<double> &times)
{
    _times = times;
    _tiffTimes.clear();

    // Check if the first time step (tiff "directory" has a time stamp.
    // If it does all directories must have a time stamp. If no time
    // stamp is present then the time stamp is taken from the data itself.
    //
    TIFFSetDirectory(tif, 0);

    char *timePtr = NULL;
    bool  hasTime = TIFFGetField(tif, TIFFTAG_DATETIME, &timePtr);

    // build a list of the times in the tiff
    //
    UDUnits udunits;
    udunits.Initialize();

    do {
        int dircount = 0;

        if (hasTime) {
            double tifftime = 0.0;
            bool   ok = _getTiffTime(tif, &udunits, tifftime);
            if (!ok) {
                SetDiagMsg("Failed to read time stamp from TIFF image");
                break;
            }
            _tiffTimes.push_back(tifftime);
        } else {
            if (dircount < _times.size()) _tiffTimes.push_back(_times[dircount]);
        }

        dircount++;
    } while (TIFFReadDirectory(tif));

    return;
}

bool GeoImageGeoTiff::_getTiffTime(TIFF *tif, UDUnits *udunits, double &tifftime) const
{
    tifftime = 0.0;

    char *timePtr = NULL;
    bool  hasTime = (bool)TIFFGetField(tif, TIFFTAG_DATETIME, &timePtr);

    if (!hasTime) return (false);

    // determine seconds from the time stamp in the tiff
    // convert tifftags to use WRF style date/time strings
    //
    int         year, mon, mday, hour, min, sec;
    const char *format = "%4d:%2d:%2d %2d:%2d:%2d";
    int         rc = sscanf(timePtr, format, &year, &mon, &mday, &hour, &min, &sec);

    // For backwords compatibility check WRF-style format
    //
    if (rc != 6) {
        format = "%4d-%2d-%2d_%2d:%2d:%2d";
        rc = sscanf(timePtr, format, &year, &mon, &mday, &hour, &min, &sec);

        if (rc != 6) return (false);
    }

    tifftime = udunits->EncodeTime(year, mon, mday, hour, min, sec);
    return (true);
}

static void CopyGeoTIFF(TIFF *in, TIFF *out)
{
    GTIF *  gtif = (GTIF *)0; /* GeoKey-level descriptor */
    double *d_list = NULL;
    int16_t   d_list_count;

    /* read definition from source file. */
    gtif = GTIFNew(in);
    if (!gtif) return;

    if (TIFFGetField(in, GTIFF_TIEPOINTS, &d_list_count, &d_list)) TIFFSetField(out, GTIFF_TIEPOINTS, d_list_count, d_list);
    if (TIFFGetField(in, GTIFF_PIXELSCALE, &d_list_count, &d_list)) TIFFSetField(out, GTIFF_PIXELSCALE, d_list_count, d_list);
    if (TIFFGetField(in, GTIFF_TRANSMATRIX, &d_list_count, &d_list)) TIFFSetField(out, GTIFF_TRANSMATRIX, d_list_count, d_list);

    /* Here we violate the GTIF abstraction to retarget on another file.
       We should just have a function for copying tags from one GTIF object
       to another. */
    gtif->gt_tif = out;
    gtif->gt_flags |= FLAG_FILE_MODIFIED;

    /* Install keys and tags */
    GTIFWriteKeys(gtif);
    GTIFFree(gtif);
    return;
}

static void cpOtherTags(TIFF *in, TIFF *out)
{
    struct cpTag *p = tags;
    for (int i = 0; i < NTAGS; i++, p++) {
        switch (p->type) {
        case TIFF_SHORT:
            if (p->count == 1) {
                uint16_t shortv;
                CopyField(p->tag, shortv);
            } else if (p->count == 2) {
                uint16_t shortv1, shortv2;
                CopyField2(p->tag, shortv1, shortv2);
            } else if (p->count == (uint16_t)-1) {
                uint16_t  shortv1;
                uint16_t *shortav;
                CopyField2(p->tag, shortv1, shortav);
            }
            break;
        case TIFF_LONG: {
            uint32_t longv;
            CopyField(p->tag, longv);
        } break;
        case TIFF_RATIONAL:
            if (p->count == 1) {
                float floatv;
                // CopyField(tag, v) replaced by following
                // Workaround a tiff lib bug:  TIFFGetField gets a very small float
                if (TIFFGetField(in, p->tag, &floatv) && floatv >= .00001) TIFFSetField(out, p->tag, floatv);
            } else if (p->count == (uint16_t)-1) {
                float *floatav;
                CopyField(p->tag, floatav);
            }
            break;
        case TIFF_ASCII: {
            char *stringv;
            CopyField(p->tag, stringv);
        } break;
        case TIFF_DOUBLE:
            if (p->count == 1) {
                double doublev;
                CopyField(p->tag, doublev);
            } else if (p->count == (uint16_t)-1) {
                double *doubleav;
                CopyField(p->tag, doubleav);
            }
            break;
        default: break;
        }
    }
}

static int tiffcp(TIFF *in, TIFF *out)
{
    uint16_t   samplesperpixel, shortv;
    uint16_t   bitspersample = 0;
    copyFunc cf;
    uint32_t   w, l;

    CopyField(TIFFTAG_IMAGEWIDTH, w);
    CopyField(TIFFTAG_IMAGELENGTH, l);

    currentImageWidth = w;
    currentImageHeight = l;
    if (convert_8_to_4) {
        TIFFSetField(out, TIFFTAG_BITSPERSAMPLE, 4);
    } else {
        CopyField(TIFFTAG_BITSPERSAMPLE, bitspersample);
    }
    if (compression != (uint16_t)-1)
        TIFFSetField(out, TIFFTAG_COMPRESSION, compression);
    else
        CopyField(TIFFTAG_COMPRESSION, compression);
    if (compression == COMPRESSION_JPEG && jpegcolormode == JPEGCOLORMODE_RGB)
        TIFFSetField(out, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_YCBCR);
    else
        CopyField(TIFFTAG_PHOTOMETRIC, shortv);
    if (fillorder != 0)
        TIFFSetField(out, TIFFTAG_FILLORDER, fillorder);
    else
        CopyField(TIFFTAG_FILLORDER, shortv);
    CopyField(TIFFTAG_SAMPLESPERPIXEL, samplesperpixel);
    /*
     * Choose tiles/strip for the output image according to
     * the command line arguments (-tiles, -strips) and the
     * structure of the input image.
     */
    if (outtiled == -1) outtiled = TIFFIsTiled(in);
    if (outtiled) {
        /*
         * Setup output file's tile width&height.  If either
         * is not specified, use either the value from the
         * input image or, if nothing is defined, use the
         * library default.
         */
        if (tilewidth == (uint32_t)-1) TIFFGetField(in, TIFFTAG_TILEWIDTH, &tilewidth);
        if (tilelength == (uint32_t)-1) TIFFGetField(in, TIFFTAG_TILELENGTH, &tilelength);
        TIFFDefaultTileSize(out, &tilewidth, &tilelength);
        TIFFSetField(out, TIFFTAG_TILEWIDTH, tilewidth);
        TIFFSetField(out, TIFFTAG_TILELENGTH, tilelength);
    } else {
        /*
         * RowsPerStrip is left unspecified: use either the
         * value from the input image or, if nothing is defined,
         * use the library default.
         */
        if (rowsperstrip == (uint32_t)-1) TIFFGetField(in, TIFFTAG_ROWSPERSTRIP, &rowsperstrip);
        rowsperstrip = TIFFDefaultStripSize(out, rowsperstrip);
        TIFFSetField(out, TIFFTAG_ROWSPERSTRIP, rowsperstrip);
    }
    if (config != (uint16_t)-1)
        TIFFSetField(out, TIFFTAG_PLANARCONFIG, config);
    else
        CopyField(TIFFTAG_PLANARCONFIG, config);
    if (g3opts != (uint32_t)-1)
        TIFFSetField(out, TIFFTAG_GROUP3OPTIONS, g3opts);
    else
        CopyField(TIFFTAG_GROUP3OPTIONS, g3opts);
    if (samplesperpixel <= 4) {
        uint16_t *tr, *tg, *tb, *ta;
        CopyField4(TIFFTAG_TRANSFERFUNCTION, tr, tg, tb, ta);
    }
    {
        uint16_t *red, *green, *blue;
        if (TIFFGetField(in, TIFFTAG_COLORMAP, &red, &green, &blue)) {
            CheckAndCorrectColormap(in, 1 << bitspersample, red, green, blue);
            TIFFSetField(out, TIFFTAG_COLORMAP, red, green, blue);
        }
    }
    /* SMinSampleValue & SMaxSampleValue */
    switch (compression) {
    case COMPRESSION_JPEG:
        TIFFSetField(out, TIFFTAG_JPEGQUALITY, quality);
        TIFFSetField(out, TIFFTAG_JPEGCOLORMODE, jpegcolormode);
        break;
    case COMPRESSION_LZW:
    case COMPRESSION_DEFLATE:
        if (predictor != (uint16_t)-1)
            TIFFSetField(out, TIFFTAG_PREDICTOR, predictor);
        else
            CopyField(TIFFTAG_PREDICTOR, predictor);
        break;
    }
    cpOtherTags(in, out);

    if (geofile || proj4_string || timeFile)
        InstallGeoTIFF(out);
    else
        CopyGeoTIFF(in, out);

    if (worldfile) ApplyWorldFile(worldfile, out);

    cf = pickCopyFunc(in, out, bitspersample, samplesperpixel);
    return (cf ? (*cf)(in, out, l, w, samplesperpixel) : FALSE);
}

DECLAREreadFunc(readContigTilesIntoBuffer)
{
    unsigned char *tilebuf = (unsigned char *)_TIFFmalloc(TIFFTileSize(in));
    uint32_t         imagew = TIFFScanlineSize(in);
    uint32_t         tilew = TIFFTileRowSize(in);
    int            iskew = imagew - tilew;
    unsigned char *bufp = buf;
    uint32_t         tw, tl;
    uint32_t         row;

    (void)spp;
    if (tilebuf == 0) return;
    (void)TIFFGetField(in, TIFFTAG_TILEWIDTH, &tw);
    (void)TIFFGetField(in, TIFFTAG_TILELENGTH, &tl);
    for (row = 0; row < imagelength; row += tl) {
        uint32_t nrow = (row + tl > imagelength) ? imagelength - row : tl;
        uint32_t colb = 0;
        uint32_t col;

        for (col = 0; col < imagewidth; col += tw) {
            if (TIFFReadTile(in, tilebuf, col, row, 0, 0) < 0 && !ignore) goto done;
            if (colb + tilew > imagew) {
                uint32_t width = imagew - colb;
                uint32_t oskew = tilew - width;
                cpStripToTile(bufp + colb, tilebuf, nrow, width, oskew + iskew, oskew);
            } else
                cpStripToTile(bufp + colb, tilebuf, nrow, tilew, iskew, 0);
            colb += tilew;
        }
        bufp += imagew * nrow;
    }
done:
    _TIFFfree(tilebuf);
}

DECLAREreadFunc(readSeparateTilesIntoBuffer)
{
    uint32_t         imagew = TIFFScanlineSize(in);
    uint32_t         tilew = TIFFTileRowSize(in);
    int            iskew = imagew - tilew;
    unsigned char *tilebuf = (unsigned char *)_TIFFmalloc(TIFFTileSize(in));
    unsigned char *bufp = buf;
    uint32_t         tw, tl;
    uint32_t         row;

    if (tilebuf == 0) return;
    (void)TIFFGetField(in, TIFFTAG_TILEWIDTH, &tw);
    (void)TIFFGetField(in, TIFFTAG_TILELENGTH, &tl);
    for (row = 0; row < imagelength; row += tl) {
        uint32_t nrow = (row + tl > imagelength) ? imagelength - row : tl;
        uint32_t colb = 0;
        uint32_t col;

        for (col = 0; col < imagewidth; col += tw) {
            tsample_t s;

            for (s = 0; s < spp; s++) {
                if (TIFFReadTile(in, tilebuf, col, row, 0, s) < 0 && !ignore) goto done;
                /*
                 * Tile is clipped horizontally.  Calculate
                 * visible portion and skewing factors.
                 */
                if (colb + tilew > imagew) {
                    uint32_t width = imagew - colb;
                    int    oskew = tilew - width;
                    cpSeparateBufToContigBuf(bufp + colb + s, tilebuf, nrow, width, oskew + iskew, oskew, spp);
                } else
                    cpSeparateBufToContigBuf(bufp + colb + s, tilebuf, nrow, tw, iskew, 0, spp);
            }
            colb += tilew;
        }
        bufp += imagew * nrow;
    }
done:
    _TIFFfree(tilebuf);
}

DECLAREwriteFunc(writeBufferToContigTiles)
{
    uint32_t         imagew = TIFFScanlineSize(out);
    uint32_t         tilew = TIFFTileRowSize(out);
    int            iskew = imagew - tilew;
    unsigned char *obuf = (unsigned char *)_TIFFmalloc(TIFFTileSize(out));
    unsigned char *bufp = buf;
    uint32_t         tl, tw;
    uint32_t         row;

    (void)spp;
    if (obuf == NULL) return (FALSE);
    (void)TIFFGetField(out, TIFFTAG_TILELENGTH, &tl);
    (void)TIFFGetField(out, TIFFTAG_TILEWIDTH, &tw);
    for (row = 0; row < imagelength; row += tilelength) {
        uint32_t nrow = (row + tl > imagelength) ? imagelength - row : tl;
        uint32_t colb = 0;
        uint32_t col;

        for (col = 0; col < imagewidth; col += tw) {
            /*
             * Tile is clipped horizontally.  Calculate
             * visible portion and skewing factors.
             */
            if (colb + tilew > imagew) {
                uint32_t width = imagew - colb;
                int    oskew = tilew - width;
                cpStripToTile(obuf, bufp + colb, nrow, width, oskew, oskew + iskew);
            } else
                cpStripToTile(obuf, bufp + colb, nrow, tilew, 0, iskew);
            if (TIFFWriteTile(out, obuf, col, row, 0, 0) < 0) {
                _TIFFfree(obuf);
                return (FALSE);
            }
            colb += tilew;
        }
        bufp += nrow * imagew;
    }
    _TIFFfree(obuf);
    return (TRUE);
}

DECLAREwriteFunc(writeBufferToSeparateTiles)
{
    uint32_t         imagew = TIFFScanlineSize(out);
    tsize_t        tilew = TIFFTileRowSize(out);
    int            iskew = imagew - tilew;
    unsigned char *obuf = (unsigned char *)_TIFFmalloc(TIFFTileSize(out));
    unsigned char *bufp = buf;
    uint32_t         tl, tw;
    uint32_t         row;

    if (obuf == NULL) return (FALSE);
    (void)TIFFGetField(out, TIFFTAG_TILELENGTH, &tl);
    (void)TIFFGetField(out, TIFFTAG_TILEWIDTH, &tw);
    for (row = 0; row < imagelength; row += tl) {
        uint32_t nrow = (row + tl > imagelength) ? imagelength - row : tl;
        uint32_t colb = 0;
        uint32_t col;

        for (col = 0; col < imagewidth; col += tw) {
            tsample_t s;
            for (s = 0; s < spp; s++) {
                /*
                 * Tile is clipped horizontally.  Calculate
                 * visible portion and skewing factors.
                 */
                if (colb + tilew > imagew) {
                    uint32_t width = imagew - colb;
                    int    oskew = tilew - width;

                    cpContigBufToSeparateBuf(obuf, bufp + colb + s, nrow, width, oskew / spp, oskew + imagew, spp);
                } else
                    cpContigBufToSeparateBuf(obuf, bufp + colb + s, nrow, tilewidth, 0, iskew, spp);
                if (TIFFWriteTile(out, obuf, col, row, 0, s) < 0) {
                    _TIFFfree(obuf);
                    return (FALSE);
                }
            }
            colb += tilew;
        }
        bufp += nrow * imagew;
    }
    _TIFFfree(obuf);
    return (TRUE);
}

static copyFunc pickCopyFunc(TIFF *in, TIFF *out, uint16_t bitspersample, uint16_t samplesperpixel)
{
    uint16_t shortv;
    uint32_t w, l, tw, tl;
    int    bychunk;

    (void)TIFFGetField(in, TIFFTAG_PLANARCONFIG, &shortv);
    if (shortv != config && bitspersample != 8 && samplesperpixel > 1) {
        fprintf(stderr, "%s: Can not handle different planar configuration w/ bits/sample != 8\n", TIFFFileName(in));
        return (NULL);
    }
    TIFFGetField(in, TIFFTAG_IMAGEWIDTH, &w);
    TIFFGetField(in, TIFFTAG_IMAGELENGTH, &l);
    if (TIFFIsTiled(out)) {
        if (!TIFFGetField(in, TIFFTAG_TILEWIDTH, &tw)) tw = w;
        if (!TIFFGetField(in, TIFFTAG_TILELENGTH, &tl)) tl = l;
        bychunk = (tw == tilewidth && tl == tilelength);
    } else if (TIFFIsTiled(in)) {
        TIFFGetField(in, TIFFTAG_TILEWIDTH, &tw);
        TIFFGetField(in, TIFFTAG_TILELENGTH, &tl);
        bychunk = (tw == w && tl == rowsperstrip);
    } else {
        uint32_t irps = (uint32_t)-1L;
        TIFFGetField(in, TIFFTAG_ROWSPERSTRIP, &irps);
        bychunk = (rowsperstrip == irps);
    }
#define T                   1
#define F                   0
#define pack(a, b, c, d, e) ((long)(((a) << 11) | ((b) << 3) | ((c) << 2) | ((d) << 1) | (e)))
    switch (pack(shortv, config, TIFFIsTiled(in), TIFFIsTiled(out), bychunk)) {
        /* Strips -> Tiles */
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, F, T, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, F, T, T): return cpContigStrips2ContigTiles;
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, F, T, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, F, T, T): return cpContigStrips2SeparateTiles;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, F, T, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, F, T, T): return cpSeparateStrips2ContigTiles;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, F, T, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, F, T, T):
        return cpSeparateStrips2SeparateTiles;
        /* Tiles -> Tiles */
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, T, T, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, T, T, T): return cpContigTiles2ContigTiles;
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, T, T, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, T, T, T): return cpContigTiles2SeparateTiles;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, T, T, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, T, T, T): return cpSeparateTiles2ContigTiles;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, T, T, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, T, T, T):
        return cpSeparateTiles2SeparateTiles;
        /* Tiles -> Strips */
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, T, F, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, T, F, T): return cpContigTiles2ContigStrips;
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, T, F, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, T, F, T): return cpContigTiles2SeparateStrips;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, T, F, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, T, F, T): return cpSeparateTiles2ContigStrips;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, T, F, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, T, F, T):
        return cpSeparateTiles2SeparateStrips;
        /* Strips -> Strips */
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, F, F, F):
        if (convert_8_to_4)
            return cpContig2ContigByRow_8_to_4;
        else
            return cpContig2ContigByRow;

    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_CONTIG, F, F, T):
        if (convert_8_to_4)
            return cpContig2ContigByRow_8_to_4;
        else
            return cpDecodedStrips;
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, F, F, F):
    case pack(PLANARCONFIG_CONTIG, PLANARCONFIG_SEPARATE, F, F, T): return cpContig2SeparateByRow;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, F, F, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_CONTIG, F, F, T): return cpSeparate2ContigByRow;
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, F, F, F):
    case pack(PLANARCONFIG_SEPARATE, PLANARCONFIG_SEPARATE, F, F, T): return cpSeparate2SeparateByRow;
    }
#undef pack
#undef F
#undef T
    fprintf(stderr, "tiffcp: %s: Don't know how to copy/convert image.\n", TIFFFileName(in));
    return (NULL);
}

ImagePyramid::ImagePyramid(TIFF *fileHandle, std::vector<ImagePyramidLevel> levels, int channels, bool isOMETIFF) {
    m_isOMETIFF = isOMETIFF;
    if(channels <= 0 || channels > 4)
        throw Exception("Nr of channels must be between 1 and 4 in ImagePyramid when importing from TIFF");
    m_tiffHandle = fileHandle;
    m_levels = levels;
    m_channels = channels;
    for(int i = 0; i < m_levels.size(); ++i) {
        m_levels[i].tilesX = std::ceil((float)m_levels[i].width / m_levels[i].tileWidth);
        m_levels[i].tilesY = std::ceil((float)m_levels[i].height / m_levels[i].tileHeight);
    }
    TIFFSetDirectory(fileHandle, 0);
    // Get data type
    uint16_t bitsPerSample;
    TIFFGetField(fileHandle, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
    uint16_t sampleFormat;
    TIFFGetField(fileHandle, TIFFTAG_SAMPLEFORMAT, &sampleFormat);
    if(sampleFormat == SAMPLEFORMAT_IEEEFP) {
        if(bitsPerSample == 32) {
            m_dataType = TYPE_FLOAT;
        } else {
            throw Exception("Unsupported TIFF data type, float with " + std::to_string(bitsPerSample) + " bits");
        }
    } else if(sampleFormat == SAMPLEFORMAT_UINT) {
        std::map<uint16_t, DataType> bitsPerSampleMap = {
                {8, TYPE_UINT8},
                {16, TYPE_UINT16},
                };
        if(bitsPerSampleMap.count(bitsPerSample) > 0) {
            m_dataType = bitsPerSampleMap[bitsPerSample];
        } else {
            throw Exception("Unsupported TIFF data type, unsigned integer with " + std::to_string(bitsPerSample) + " bits");
        }
    } else if(sampleFormat == SAMPLEFORMAT_INT) {
        std::map<uint16_t, DataType> bitsPerSampleMap = {
                {8, TYPE_INT8},
                {16, TYPE_INT16},
                };
        if(bitsPerSampleMap.count(bitsPerSample) > 0) {
            m_dataType = bitsPerSampleMap[bitsPerSample];
        } else {
            throw Exception("Unsupported TIFF data type, signed integer with " + std::to_string(bitsPerSample) + " bits");
        }
    } else {
        throw Exception("Unsupported TIFF data type: " + std::to_string(sampleFormat) + " " + std::to_string(bitsPerSample) + " bits");
        //m_dataType = TYPE_UINT8;
    }
    // Get compression
    uint16_t compressionTag;
    TIFFGetField(fileHandle, TIFFTAG_COMPRESSION, &compressionTag);
    ImageCompression compression = ImageCompression::UNSPECIFIED;
    switch(compressionTag) {
        case COMPRESSION_NONE:
            compression = ImageCompression::RAW;
            break;
        case COMPRESSION_JPEG:
            compression = ImageCompression::JPEG;
            {
                int quality = -1;
                int res = TIFFGetField(m_tiffHandle, TIFFTAG_JPEGQUALITY, &quality);
                if(res != 1)
                    throw Exception("Unable to get JPEG quality from TIFF");
                m_compressionQuality = quality;
            }
            break;
        case COMPRESSION_JXL:
            compression = ImageCompression::JPEGXL;
            break;
        case COMPRESSION_LZW:
            compression = ImageCompression::LZW;
            break;
        case COMPRESSION_JP2000:
            throw Exception("JPEG 2000 TIFF not supported yet");
            break;
        case COMPRESSION_ADOBE_DEFLATE:
        case COMPRESSION_DEFLATE:
            compression = ImageCompression::DEFLATE;
            break;
        case 34666:
            compression = ImageCompression::NEURAL_NETWORK;
            break;
        default:
            reportWarning() << "Unrecognized compression by FAST in TIFF: " << compressionTag << reportEnd();
    }
    m_compressionFormat = compression;
    // Get spacing from TIFF
    float spacingX;
    float spacingY;
    int resX = TIFFGetField(fileHandle, TIFFTAG_XRESOLUTION, &spacingX);
    int resY = TIFFGetField(fileHandle, TIFFTAG_YRESOLUTION, &spacingY);
    if(resX == 1 && resY == 1) {
        // Convert from cm
        spacingX = 1.0f/(spacingX/10.0f);
        spacingY = 1.0f/(spacingY/10.0f);
        reportInfo() << "Spacing from TIFF was " << spacingX << " " << spacingY << reportEnd();
        m_spacing = Vector3f(spacingX, spacingY, 1.0f);
    }
    mBoundingBox = DataBoundingBox(Vector3f(getFullWidth(), getFullHeight(), 0));
    m_initialized = true;
    m_pyramidFullyInitialized = true;
    m_counter += 1;
}

void TIFFImagePyramidImporter::execute() {

    reportInfo() << "Reading WSI with TIFF" << reportEnd();
    TIFFSetErrorHandler([](const char* module, const char* fmt, va_list ap) {
        auto str = make_uninitialized_unique<char[]>(512);
        sprintf(str.get(), fmt, ap);
        Reporter::warning() << "TIFF: " << module << ": " << str.get() << Reporter::end();
    });
    TIFFSetWarningHandler([](const char* module, const char* fmt, va_list ap) {
        auto str = make_uninitialized_unique<char[]>(512);
        sprintf(str.get(), fmt, ap);
        std::string strstr = str.get();
        if(strstr.find("tag ignored") == std::string::npos) // ignore warnings about incorrect tag
            Reporter::warning() << "TIFF: " << module << ": " << str.get() << Reporter::end();
    });
    TIFF* tiff = TIFFOpen(m_filename.c_str(), "rm");
    if(tiff == nullptr) {
        throw Exception("Failed to open file " + m_filename);
    }

    std::vector<ImagePyramidLevel> levelList;
    uint16_t channels;

    bool isOMETiff = false;
    TIFFSetDirectory(tiff, 0);
    // Read description to check if image is OME-TIFF
    char * description;
    int result = TIFFGetField(tiff, TIFFTAG_IMAGEDESCRIPTION, &description);
    if(result == 1) { // Must check if tag exists
        std::string str = description;
        if(str.find("xml") != std::string::npos && str.find("OME") != std::string::npos) {
            reportInfo() << "TIFF file seems to be an OME-TIFF, reading it as such.." << reportEnd();
            isOMETiff = true;
        }
    }

    float magnification = 0.0f;
    if(isOMETiff) {
        TIFFSetDirectory(tiff, 0);

        // read data for level 0
        uint width, height;
        TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
        uint tileWidth, tileHeight;
        TIFFGetField(tiff, TIFFTAG_TILEWIDTH, &tileWidth);
        TIFFGetField(tiff, TIFFTAG_TILELENGTH, &tileHeight);
        TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &channels);
        reportInfo() << "Level " << 0 <<  " has size " << width << " " << height <<
        " and tile size: " << tileWidth << " " << tileHeight << " channels " << channels << reportEnd();

        ImagePyramidLevel levelData;
        levelData.width = width;
        levelData.height = height;
        levelData.tileWidth = tileWidth;
        levelData.tileHeight = tileHeight;
        levelData.offset = 0;
        levelList.push_back(levelData);

        // Read SubIFD offsets
        uint16_t count;
        uint64_t* values; // If big tiff, this is 64. This is deleted by TIFF
        TIFFGetField(tiff, TIFFTAG_SUBIFD, &count, &values);
        std::vector<uint64_t> offsets; // Have to copy the offsets for some reason
        for(int i = 0; i < count; ++i)
            offsets.push_back(values[i]);

        reportInfo() << "Found " << count << " subdirectories (SubIFD) in OME-TIFF file" << reportEnd();
        for(int i = 0; i < count; ++i) {
            TIFFSetSubDirectory(tiff, offsets[i]);
            uint width, height;
            TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width);
            TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
            uint tileWidth, tileHeight;
            TIFFGetField(tiff, TIFFTAG_TILEWIDTH, &tileWidth);
            TIFFGetField(tiff, TIFFTAG_TILELENGTH, &tileHeight);
            reportInfo() << "Level " << (i+1) <<  " has size " << width << " " << height <<
                         " and tile size: " << tileWidth << " " << tileHeight << " channels " << channels << reportEnd();

            ImagePyramidLevel levelData;
            levelData.width = width;
            levelData.height = height;
            levelData.tileWidth = tileWidth;
            levelData.tileHeight = tileHeight;
            levelData.offset = offsets[i];
            levelList.push_back(levelData);
        }

        // Try to get magnification from description using regex
        std::regex pattern(R"(NominalMagnification="([-+]?[0-9]*\.?[0-9]+))");
        std::smatch match;
        std::string str = description;
        magnification = 0.0f;
        while(std::regex_search(str, match, pattern)) {
            if(std::stof(match[1]) > magnification) { // Get largest magnification
                magnification = std::stof(match[1]);
            }
            str = match.suffix();
        }
        if(magnification == 0) {
            reportInfo() << "Unable to get magnification from OME-TIFF XML data" << reportEnd();
        } else {
            reportInfo() << "Got magnification " << magnification << " from OME-TIFF XML data" << reportEnd();
        }
    } else {
        for(int level = 0; level < TIFFNumberOfDirectories(tiff); ++level) {
            TIFFSetDirectory(tiff, level);
            uint width, height;
            TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width);
            TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height);
            uint tileWidth, tileHeight;
            TIFFGetField(tiff, TIFFTAG_TILEWIDTH, &tileWidth);
            TIFFGetField(tiff, TIFFTAG_TILELENGTH, &tileHeight);
            TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &channels);
            reportInfo() << "Level " << level <<  " has size " << width << " " << height <<
            " and tile size: " << tileWidth << " " << tileHeight << " channels " << channels << reportEnd();

            ImagePyramidLevel levelData;
            levelData.width = width;
            levelData.height = height;
            levelData.tileWidth = tileWidth;
            levelData.tileHeight = tileHeight;
            levelList.push_back(levelData);
        }
    }
    auto image = ImagePyramid::create(tiff, levelList, (int)channels, isOMETiff);
    if(magnification > 0)
        image->setMagnification(magnification);
    addOutputData(0, image);
}

void GetMatrices(const char* fileName, Mat44f& camera, Mat44f& screen,
                     bool doTranspose)
    {
        TIFF* tif = TIFFOpen(fileName, "r");
        if (tif == NULL)
        {
            TWK_EXC_THROW_WHAT(Exception, "Could not open file");
        }

        float* ptr = NULL;
        int rslt = TIFFGetField(tif, TIFFTAG_CAMERA_MATRIX, &ptr);
        if (ptr == NULL)
        {
            TWK_EXC_THROW_WHAT(Exception, "Could not get camera matrix");
        }
        else
        {
            memcpy((void*)(&(camera[0][0])), (const void*)ptr,
                   16 * sizeof(float));
        }

        rslt = TIFFGetField(tif, TIFFTAG_SCREEN_MATRIX, &ptr);
        if (ptr == NULL)
        {
            TWK_EXC_THROW_WHAT(Exception, "Could not get screen matrix");
        }
        else
        {
            memcpy((void*)(&(screen[0][0])), (const void*)ptr,
                   16 * sizeof(float));
        }

        TIFFClose(tif);

        if (doTranspose)
        {
            camera.transpose();
            screen.transpose();
        }
    }

static void readAllTags(TIFF* tif, FrameBuffer& img)
    {
        //
        //  ICC Profile
        //
        //  I have to look for this before EXIFTAG_COLORSPACE or else I get
        //  crashing
        //

        uint32 Len;
        void* Buffer;
        bool set_profile = false;

        if (TIFFGetField(tif, TIFFTAG_ICCPROFILE, &Len, &Buffer))
        {
            img.setPrimaryColorSpace(ColorSpace::ICCProfile());
            img.setTransferFunction(ColorSpace::ICCProfile());
            img.setICCprofile(Buffer, Len);

            set_profile = true;

            //_TIFFfree(Buffer); // this is a bad idea apparently (causes a
            // crash)
        }

        //
        //  Read EXIF tags if present
        //

        uint32 exif_offset = 0;

        if (TIFFGetField(tif, TIFFTAG_EXIFIFD, &exif_offset))
        {
            tdir_t directory = TIFFCurrentDirectory(tif);

            if (TIFFReadEXIFDirectory(tif, exif_offset))
            {
                unsigned short v_short = 0;

                //
                // Special handling of color space tag
                //

                if (TIFFGetField(tif, EXIFTAG_COLORSPACE, &v_short))
                {
                    if (v_short == 1)
                    {
                        img.attribute<string>("EXIF/ColorSpace") = "1 (sRGB)";

                        if (!set_profile)
                        {
                            img.setPrimaryColorSpace(ColorSpace::Rec709());
                            img.setTransferFunction(ColorSpace::sRGB());
                        }
                    }
                    else if (v_short == 2)
                    {
                        //
                        //  NOTE: this might be wrong.
                        //  See: http://www.cpanforum.com/threads/2784
                        //

                        img.attribute<string>("EXIF/ColorSpace") =
                            "2 (Adobe RGB)";

                        if (!set_profile)
                        {
                            img.setPrimaryColorSpace(ColorSpace::ICCProfile());
                            img.attribute<string>(
                                ColorSpace::ICCProfileDescription()) =
                                string("Adobe RGB");
                        }
                    }
                    else
                    {
                        ostringstream str;
                        str << v_short << " (Uncalibrated)";
                        img.attribute<string>("EXIF/ColorSpace") = str.str();
                        img.setPrimaryColorSpace(ColorSpace::Generic());
                    }
                }
            }
        }

        //
        //  Now the rest of the tags
        //

        for (int fi = 0, nfi = tif->tif_nfields; fi < nfi; fi++)
        {
            const TIFFField* const fip = tif->tif_fields[fi];

            if (fip->field_tag != TIFFTAG_ICCPROFILE
                && // exclude tags we handle seperately
                fip->field_tag != EXIFTAG_COLORSPACE &&

                fip->field_tag != TIFFTAG_XRESOLUTION
                && fip->field_tag != TIFFTAG_YRESOLUTION
                && fip->field_tag != TIFFTAG_SOFTWARE
                && fip->field_tag != EXIFTAG_PIXELXDIMENSION
                && fip->field_tag != EXIFTAG_PIXELYDIMENSION
                && fip->field_tag != TIFFTAG_RESOLUTIONUNIT
                && fip->field_tag != TIFFTAG_PLANARCONFIG &&

                fip->field_tag != TIFFTAG_SUBIFD &&

                // There are some variable length tags that require more than
                // one argument. This is an attempt to filter those out.
                // However, this may be filtering EXIF tags ... its hard to tell
                // For example TIFFTAG_COLORMAP,  TIFFTAG_HALFTONEHINTS,
                // TIFFTAG_PAGENUMBER, and TIFFTAG_SUBIFD, ...
                // see tif_dir.c _TIFFVGetField for multiple uses of va_arg()
                // by a tag type.

                (fip->field_readcount != TIFF_VARIABLE
                 || fip->field_type == TIFF_ASCII)
                &&

                // fip->field_tag != TIFFTAG_COLORMAP &&
                // fip->field_tag != TIFFTAG_HALFTONEHINTS &&
                fip->field_tag != TIFFTAG_PAGENUMBER &&
                // fip->field_tag != TIFFTAG_SUBIFD &&
                fip->field_tag != TIFFTAG_YCBCRSUBSAMPLING &&
                // fip->field_tag != TIFFTAG_TRANSFERFUNCTION &&

                fip->field_tag != TIFFTAG_PHOTOMETRIC
                && fip->field_tag != TIFFTAG_IMAGEWIDTH
                && fip->field_tag != TIFFTAG_IMAGELENGTH
                && fip->field_tag != TIFFTAG_BITSPERSAMPLE
                && fip->field_tag != TIFFTAG_SAMPLESPERPIXEL)
            {
                unsigned char ch;
                char* text;
                float f;
                float* fp;
                unsigned int ui;
                unsigned short us;

                bool exif_tag = false;

                if (exif_offset)
                {
                    //
                    //  If we read EXIF, we have to check tags against known
                    //  EXIF
                    //

                    for (unsigned int tag = exifTags[0].tag, i = 0;
                         (tag = exifTags[i].tag) && !exif_tag; i++)
                    {
                        exif_tag = (tag == fip->field_tag);
                    }
                }

                string aname = (exif_tag ? string("EXIF/") : string("TIFF/"))
                               + fip->field_name;

                switch (fip->field_type)
                {
                case TIFF_ASCII:
                    if (fip->field_passcount)
                    {
                        if (TIFFGetField(tif, fip->field_tag, &Len, &text))
                        {
                            img.attribute<string>(aname) = text;
                        }
                    }
                    else if (TIFFGetField(tif, fip->field_tag, &text))
                    {
                        img.attribute<string>(aname) = text;
                    }
                    break;

                case TIFF_SHORT:
                    if (TIFFGetField(tif, fip->field_tag, &us))
                    {
                        img.attribute<int>(aname) = us;
                    }
                    break;

                case TIFF_LONG:
                    // sometimes this is LONG (which is supposed to be wrong)
                    if (aname == "TIFF/RichTIFFIPTC")
                    {
                        void* array = 0;

                        if (TIFFGetField(tif, fip->field_tag, &ui, &array))
                        {
                            img.attribute<string>(aname) = "";
                        }
                    }
                    else if (TIFFGetField(tif, fip->field_tag, &ui))
                    {
                        img.attribute<int>(aname) = ui;
                    }
                    break;

                case TIFF_RATIONAL:
                case TIFF_SRATIONAL:
                case TIFF_FLOAT:
                    if (TIFFGetField(tif, fip->field_tag, &f))
                    {
                        img.attribute<float>(aname) = f;
                    }
                    break;

                case TIFF_BYTE:
                    // could be BYTE
                    if (aname == "TIFF/RichTIFFIPTC")
                    {
                        char* array = 0;

                        if (TIFFGetField(tif, fip->field_tag, &ui, &array))
                        {
                            img.attribute<string>(aname) = array;
                        }
                    }
                    break;

                case TIFF_UNDEFINED: // means char, apparently
                    // could be undefined
                    if (aname == "TIFF/RichTIFFIPTC")
                    {
                        char* array = 0;

                        if (TIFFGetField(tif, fip->field_tag, &ui, &array))
                        {
                            img.attribute<string>(aname) = array;
                        }
                    }
                    // if(TIFFGetField(tif, fip->field_tag, &ch))
                    //{
                    // img.attribute<int>(aname) = ch;
                    //}
                    break;

                default:
                    break;
                }
            }
        }
    }

static void readContiguousScanlineImage3Planar(TIFF* tif, int w, int h,
                                                   FrameBuffer& img)
    {
        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
        unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        tsize_t rowsize = 0;
        unsigned char* buf = 0;
        if (sampleFormat == SAMPLEFORMAT_INT)
        {
            rowsize = TIFFScanlineSize(tif);
            buf = (unsigned char*)_TIFFmalloc(rowsize);
        }

        for (int y = 0; y < h; ++y)
        {
            int flipY = flip ? h - y - 1 : y;

            if (sampleFormat == SAMPLEFORMAT_INT)
            {
                if (TIFFReadScanline(tif, buf, y) == -1)
                {
                    // Incomplete image read, just stop and return what we did
                    // get
                    break;
                }

                float* fbBuf = img.scanline<float>(flipY);

                storeIntAsNormalizedFloatSamples(bitsPerSample, rowsize, buf,
                                                 fbBuf);

                memcpy(fbBuf, buf, rowsize);
            }
            else
            {
                if (TIFFReadScanline(tif, img.scanline<unsigned char>(flipY), y)
                    == -1)
                {
                    // Incomplete image read, just stop and return what we did
                    // get
                    break;
                }
            }
        }

        if (buf)
            _TIFFfree(buf);

        if (flop)
        {
            img.setOrientation(FrameBuffer::BOTTOMRIGHT);
        }
    }

