                storeIntAsNormalizedFloatSamples(bitsPerSample, rowsize, buf,
                                                 fbBuf);

                memcpy(fbBuf, buf, rowsize);
            }
            else
            {
                if (TIFFReadScanline(tif, img.scanline<unsigned char>(flipY), y)
                    == -1)
                {
                    // Incomplete image read, just stop and return what we did
                    // get
