static void readContiguousScanlineImageOverflow(TIFF* tif, int w, int h,
                                                    FrameBuffer& img)
    {
        //
        //  This function is used when we have an interleaved TIFF with more
        //  than 4 channels. The TwkFrameBuffer doesn't like more than 4
        //  interleaved channels, so we'll decode the scanline and copy only the
        //  first 4 samples from each pixel.
        //

        unsigned short samplesPerPixel = DEFAULT_TIFFTAG_SAMPLESPERPIXEL_VALUE;
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);

        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
        unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        if (unsigned char* buf =
                (unsigned char*)_TIFFmalloc(TIFFScanlineSize(tif)))
        {
            //
            //  Because we use TIFF a lot, and tiff files are stored by default
            //  in TOPLEFT orientation, we'll "correct" it to make it bottom
            //  right. For scanline images this is not a huge hit -- we just
            //  read the image backwards. For orther orientations we'll set the
            //  FB image orientation
            //

            for (int y = 0; y < h; ++y)
            {
                int flipY = flip ? h - y - 1 : y;

                if (TIFFReadScanline(tif, buf, y) == -1)
                {
                    // Incomplete image read, just stop and return what we did
                    // get
                    break;
                }

                if (sampleFormat == SAMPLEFORMAT_INT)
                {
                    switch (bitsPerSample)
                    {
                    case 8:
                        copyAndNormalizeScanlineSamples(
                            (unsigned char*)buf, img.scanline<float>(flipY), w,
                            4, samplesPerPixel - 4,
                            SAMPLEFORMAT_INT8_NORM_VALUE);
                        break;
                    case 16:
                        copyAndNormalizeScanlineSamples(
                            (unsigned short*)buf, img.scanline<float>(flipY), w,
                            4, samplesPerPixel - 4,
                            SAMPLEFORMAT_INT16_NORM_VALUE);
                        break;
                    case 32:
                        copyAndNormalizeScanlineSamples(
                            (float*)buf, img.scanline<float>(flipY), w, 4,
                            samplesPerPixel - 4, SAMPLEFORMAT_INT32_NORM_VALUE);
                        break;
                    }
                }
                else
                {
                    switch (bitsPerSample)
                    {
                    case 1:
                    case 8:
                        copyScanlineSamples((unsigned char*)buf,
                                            img.scanline<unsigned char>(flipY),
                                            w, 4, samplesPerPixel - 4);
                        break;
                    case 16:
                        copyScanlineSamples((unsigned short*)buf,
                                            img.scanline<unsigned short>(flipY),
                                            w, 4, samplesPerPixel - 4);
                        break;
                    case 32:
                        copyScanlineSamples((float*)buf,
                                            img.scanline<float>(flipY), w, 4,
                                            samplesPerPixel - 4);
                        break;
                    }
                }
            }

            _TIFFfree(buf);
        }

        if (flop)
        {
            img.setOrientation(FrameBuffer::BOTTOMRIGHT);
        }
    }

static void readContiguousScanlineImageAsPlanar(TIFF* tif, int w, int h,
                                                    FrameBuffer& img)
    {
        //
        //  This function is used when we have an interleaved TIFF with more
        //  than 4 channels. In this case the FrameBuffer should be planar and
        //  we'll have to copy the interleaved scanline to the planes.
        //

        assert(img.isPlanar());

        unsigned short samplesPerPixel = DEFAULT_TIFFTAG_SAMPLESPERPIXEL_VALUE;
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);

        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
        unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        if (unsigned char* buf =
                (unsigned char*)_TIFFmalloc(TIFFScanlineSize(tif)))
        {
            //
            //  Because we use TIFF a lot, and tiff files are stored by default
            //  in TOPLEFT orientation, we'll "correct" it to make it bottom
            //  right. For scanline images this is not a huge hit -- we just
            //  read the image backwards. For orther orientations we'll set the
            //  FB image orientation
            //

            for (int y = 0; y < h; ++y)
            {
                int flipY = flip ? h - y - 1 : y;

                if (TIFFReadScanline(tif, buf, y) == -1)
                {
                    // Incomplete image read, just stop and return what we did
                    // get
                    break;
                }

                FrameBuffer* fbuf = &img;

                for (int c = 0;
                     c < img.planeSize() && c < samplesPerPixel && fbuf; c++)
                {

                    if (sampleFormat == SAMPLEFORMAT_INT)
                    {
                        switch (bitsPerSample)
                        {
                        case 8:
                            copyAndNormalizeScanlineSamples(
                                (unsigned char*)buf + c,
                                &fbuf->pixel<float>(0, flipY), w, 1,
                                samplesPerPixel - 1,
                                SAMPLEFORMAT_INT8_NORM_VALUE);
                            break;
                        case 16:
                            copyAndNormalizeScanlineSamples(
                                (unsigned short*)buf + c,
                                &fbuf->pixel<float>(0, flipY), w, 1,
                                samplesPerPixel - 1,
                                SAMPLEFORMAT_INT16_NORM_VALUE);
                            break;
                        case 32:
                            copyAndNormalizeScanlineSamples(
                                (float*)buf + c, &fbuf->pixel<float>(0, flipY),
                                w, 1, samplesPerPixel - 1,
                                SAMPLEFORMAT_INT32_NORM_VALUE);
                            break;
                        }
                    }
                    else
                    {
                        switch (bitsPerSample)
                        {
                        case 1:
                        case 8:
                            copyScanlineSamples(
                                (unsigned char*)buf + c,
                                &fbuf->pixel<unsigned char>(0, flipY), w, 1,
                                samplesPerPixel - 1);
                            break;
                        case 16:
                            copyScanlineSamples(
                                (unsigned short*)buf + c,
                                &fbuf->pixel<unsigned short>(0, flipY), w, 1,
                                samplesPerPixel - 1);
                            break;
                        case 32:
                            copyScanlineSamples((float*)buf + c,
                                                &fbuf->pixel<float>(0, flipY),
                                                w, 1, samplesPerPixel - 1);
                            break;
                        }
                    }

                    fbuf = fbuf->nextPlane();
                }
            }

            _TIFFfree(buf);
        }

        if (flop)
        {
            img.setOrientation(FrameBuffer::BOTTOMRIGHT);
        }
    }

static void readPlanarScanlineImage(TIFF* tif, int w, int h, int planes,
                                        FrameBuffer& img)
    {
        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
        unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        //
        //  Because we use TIFF a lot, and tiff files are stored by default
        //  in TOPLEFT orientation, we'll "correct" it to make it bottom
        //  right. For scanline images this is not a huge hit -- we just
        //  read the image backwards. For orther orientations we'll set the
        //  FB image orientation
        //

        FrameBuffer* fb = &img;

        tsize_t rowsize = 0;
        unsigned char* buf = 0;
        if (sampleFormat == SAMPLEFORMAT_INT)
        {
            rowsize = TIFFScanlineSize(tif);
            buf = (unsigned char*)_TIFFmalloc(rowsize);
        }

        for (int p = 0; p < planes && fb; ++p)
        {
            for (int y = 0; y < h; ++y)
            {
                int flipY = flip ? h - y - 1 : y;

                if (sampleFormat == SAMPLEFORMAT_INT)
                {
                    if (TIFFReadScanline(tif, buf, y, p) == -1)
                    {
                        // Incomplete image read, just stop and return what we
                        // did get
                        break;
                    }

                    float* fbBuf = &fb->pixel<float>(0, flipY);

                    storeIntAsNormalizedFloatSamples(bitsPerSample, rowsize,
                                                     buf, fbBuf);

                    memcpy(fbBuf, buf, rowsize);
                }
                else
                {
                    if (TIFFReadScanline(
                            tif, &fb->pixel<unsigned char>(0, flipY), y, p)
                        == -1)
                    {
                        // Incomplete image read, just stop and return what we
                        // did get
                        break;
                    }
                }
            }

            fb = fb->nextPlane();
        }

        if (buf)
            _TIFFfree(buf);

        if (flop)
        {
            img.setOrientation(FrameBuffer::BOTTOMRIGHT);
        }
    }

static void readContiguousTiledImage(TIFF* tif, int w, int h,
                                         FrameBuffer& img)
    {
        tsize_t rowsize = TIFFTileRowSize(tif);

        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        if (flip)
        {
            img.setOrientation(flop ? FrameBuffer::TOPRIGHT
                                    : FrameBuffer::TOPLEFT);
        }
        else
        {
            img.setOrientation(flop ? FrameBuffer::BOTTOMRIGHT
                                    : FrameBuffer::NATURAL);
        }

        if (unsigned char* buf = (unsigned char*)_TIFFmalloc(TIFFTileSize(tif)))
        {
            uint32 tw, th, w, h;

            TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
            TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
            TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tw);
            TIFFGetField(tif, TIFFTAG_TILELENGTH, &th);

            unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
            unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
            TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
            TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

            // These checks are to handle the case where the
            // tile is larger than the image size.
            //
            uint32 copy_rowsize = ((w < tw) ? (w * rowsize / tw) : rowsize);

            for (uint32 row = 0; row < h; row += th)
            {
                for (uint32 col = 0; col < w; col += tw)
                {
                    if (TIFFReadTile(tif, buf, col, row, 0, 0) < 0)
                    {
                        _TIFFfree(buf);
                        return; // just return on partially read image
                    }
                    else
                    {
                        //
                        //  Copy the tile row over to the fb
                        //

                        for (int y = 0; y < th && row + y < h; y++)
                        {
                            if (sampleFormat == SAMPLEFORMAT_INT)
                            {
                                storeIntAsNormalizedFloatSamples(
                                    bitsPerSample, copy_rowsize,
                                    buf + y * rowsize,
                                    &img.pixel<float>(col, row + y));
                            }
                            else
                            {
                                unsigned char* s =
                                    &img.pixel<unsigned char>(col, row + y);
                                memcpy(s, buf + y * rowsize, copy_rowsize);
                            }
                        }
                    }
                }
            }

            _TIFFfree(buf);
        }
    }

static void readPlanarTiledImage(TIFF* tif, int w, int h, FrameBuffer& img)
    {
        tsize_t rowsize = TIFFTileRowSize(tif);
        unsigned short orient = ORIENTATION_TOPLEFT;
        bool flip = false;
        bool flop = false;
        FrameBuffer::Orientation o;

        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        flip = orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
        flop = orient == ORIENTATION_TOPRIGHT || orient == ORIENTATION_BOTRIGHT;

        if (flip)
        {
            o = flop ? FrameBuffer::TOPRIGHT : FrameBuffer::TOPLEFT;
        }
        else
        {
            o = flop ? FrameBuffer::BOTTOMRIGHT : FrameBuffer::NATURAL;
        }

        if (unsigned char* buf = (unsigned char*)_TIFFmalloc(TIFFTileSize(tif)))
        {
            uint32 tw, th, w, h;
            unsigned short d = DEFAULT_TIFFTAG_SAMPLESPERPIXEL_VALUE;
            unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
            unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;

            TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w);
            TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h);
            TIFFGetField(tif, TIFFTAG_TILEWIDTH, &tw);
            TIFFGetField(tif, TIFFTAG_TILELENGTH, &th);
            TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &d);
            TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
            TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

            FrameBuffer* fb = &img;

            // These checks are to handle the case where the
            // tile is larger than the image size.
            //
            uint32 copy_rowsize = ((w < tw) ? (w * rowsize / tw) : rowsize);

            for (uint32 plane = 0; plane < d && fb; plane++)
            {
                fb->setOrientation(o);

                for (uint32 row = 0; row < h; row += th)
                {
                    for (uint32 col = 0; col < w; col += tw)
                    {
                        if (TIFFReadTile(tif, buf, col, row, 0, plane) < 0)
                        {
                            _TIFFfree(buf);
                            return; // just return on partially read image
                        }
                        else
                        {
                            //
                            //  Copy the tile row over to the fb
                            //

                            for (int y = 0; y < th && row + y < h; y++)
                            {
                                if (sampleFormat == SAMPLEFORMAT_INT)
                                {
                                    storeIntAsNormalizedFloatSamples(
                                        bitsPerSample, copy_rowsize,
                                        buf + y * rowsize,
                                        &fb->pixel<float>(col, row + y));
                                }
                                else
                                {
                                    unsigned char* s =
                                        &fb->pixel<unsigned char>(col, row + y);
                                    memcpy(s, buf + y * rowsize, copy_rowsize);
                                }
                            }
                        }
                    }
                }

                fb = fb->nextPlane();
            }

            _TIFFfree(buf);
        }
    }

void IOtiff::getImageInfo(const std::string& filename, FBInfo& fbi) const
    {
#ifdef _MSC_VER
        TIFF* tif = TIFFOpenW(UNICODE_C_STR(filename.c_str()), "r");
#else
        TIFF* tif = TIFFOpen(UNICODE_C_STR(filename.c_str()), "r");
#endif

        if (!tif)
        {
            TWK_THROW_STREAM(Exception,
                             "TIFF cannot open \"" << filename << "\"");
        }

        unsigned short colorspace;
        unsigned short orient = ORIENTATION_TOPLEFT;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &fbi.width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &fbi.height);
        TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &colorspace);
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);

        unsigned short samplesPerPixel = DEFAULT_TIFFTAG_SAMPLESPERPIXEL_VALUE;
        TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);
        fbi.numChannels = samplesPerPixel;

        unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);

        unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;
        TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);

        uint16 config;
        TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);

        float x_rez, y_rez;
        if (TIFFGetField(tif, TIFFTAG_XRESOLUTION, &x_rez)
            && TIFFGetField(tif, TIFFTAG_YRESOLUTION, &y_rez))
        {
            fbi.pixelAspect = x_rez != 0.0 ? y_rez / x_rez : 1.0;
            unsigned short unit = 0;

            if (TIFFGetField(tif, TIFFTAG_RESOLUTIONUNIT, &unit))
            {
                ostringstream str;

                switch (unit)
                {
                case 1:
                    str << "None";
                    break;
                case 2:
                    str << "Inch";
                    break;
                case 3:
                    str << "Centimeter";
                    break;
                default:
                    str << "Unknown (" << unit << ")";
                    break;
                }

                fbi.proxy.newAttribute("TIFF/ResolutionUnit", str.str());
            }

            fbi.proxy.setPixelAspectRatio(x_rez != 0.0 ? y_rez / x_rez : 1.0);
            fbi.proxy.newAttribute("TIFF/XResolution", x_rez);
            fbi.proxy.newAttribute("TIFF/YResolution", y_rez);
        }

        readAllTags(tif, fbi.proxy);

        switch (bitsPerSample)
        {
        case 1:
            fbi.dataType = FrameBuffer::BIT;
            break;
        case 8:
            if (sampleFormat == SAMPLEFORMAT_INT)
            {
                // We promote tif signed int types to
                // a float type framebuffer, normalized
                // by the unsigned 8 bit value.
                fbi.dataType = FrameBuffer::FLOAT;
            }
            else
            {
                fbi.dataType = FrameBuffer::UCHAR;
            }
            break;
        case 16:
            if (sampleFormat == SAMPLEFORMAT_INT)
            {
                // We promote tif signed int types to
                // a float type framebuffer, normalized
                // by the unsigned 16 bit value.
                fbi.dataType = FrameBuffer::FLOAT;
            }
            else
            {
                fbi.dataType = FrameBuffer::USHORT;
            }
            break;
        case 32:
            if (sampleFormat == SAMPLEFORMAT_INT)
            {
                // We promote tif signed int types to
                // a float type framebuffer, normalized
                // by the unsigned 16 bit value for this case.
                fbi.dataType = FrameBuffer::FLOAT;
            }
            else if (sampleFormat == SAMPLEFORMAT_UINT)
            {
                fbi.dataType = FrameBuffer::UINT;
            }
            else
            {
                fbi.dataType = FrameBuffer::FLOAT;
            }
            break;
        default:
            TIFFClose(tif);
            TWK_THROW_STREAM(Exception, "Sorry, unsupported bit depth.");
        }

        bool readAsRGBA = false;

        if ((colorspace != PHOTOMETRIC_RGB
             && colorspace != PHOTOMETRIC_MINISBLACK
             && colorspace != PHOTOMETRIC_MINISWHITE)
            || (m_addAlphaTo3Channel && samplesPerPixel == 3)
            || (config == PLANARCONFIG_CONTIG && samplesPerPixel > 4))
        {
            readAsRGBA = true;
        }

        if (readAsRGBA)
        {
            // TIFFReadRGBAImage(tif, width, height, p, 0);
            fbi.orientation = FrameBuffer::NATURAL;
        }
        else if (TIFFIsTiled(tif))
        {
            // readContiguousTiledImage(tif, width, height, fb);
            //  or readPlanarTiledImage(tif, width, height, fb);
            bool flip = false;
            bool flop = false;
            flip =
                orient == ORIENTATION_TOPLEFT || orient == ORIENTATION_TOPRIGHT;
            flop = orient == ORIENTATION_TOPRIGHT
                   || orient == ORIENTATION_BOTRIGHT;

            if (flip)
                fbi.orientation =
                    (flop ? FrameBuffer::TOPRIGHT : FrameBuffer::TOPLEFT);
            else
                fbi.orientation =
                    (flop ? FrameBuffer::BOTTOMRIGHT : FrameBuffer::NATURAL);
        }
        else
        {
            bool flip = false;
            bool flop = false;
            flop = orient == ORIENTATION_TOPRIGHT
                   || orient == ORIENTATION_BOTRIGHT;
            fbi.orientation =
                flop ? FrameBuffer::BOTTOMRIGHT : FrameBuffer::NATURAL;
        }

        TIFFClose(tif);
    }

void IOtiff::readImage(FrameBuffer& fb, const std::string& filename,
                           const ReadRequest& request) const
    {
        TIFF* tif = NULL;
        StreamData* stream = NULL;

        try
        {
            if (m_iotype == StandardIO)
            {
#ifdef _MSC_VER
                tif = TIFFOpenW(UNICODE_C_STR(filename.c_str()), "r");
#else
                tif = TIFFOpen(UNICODE_C_STR(filename.c_str()), "r");
#endif
            }
            else
            {
                stream = new StreamData(
                    filename, (FileStream::Type)((unsigned int)m_iotype - 1),
                    m_iosize, m_iomaxAsync);

                tif = TIFFClientOpen(filename.c_str(), "r", (thandle_t)stream,
                                     readproc, writeproc, seekproc, closeproc,
                                     sizeproc, NULL, NULL);
            }

            if (!tif)
            {
                TWK_THROW_STREAM(Exception,
                                 "TIFF: cannot open \"" << filename << "\"");
            }

            int width;
            int height;
            unsigned short bitsPerSample = DEFAULT_TIFFTAG_BITSPERSAMPLE_VALUE;
            FrameBuffer::DataType dataType = FrameBuffer::UCHAR;
            unsigned short samplesPerPixel =
                DEFAULT_TIFFTAG_SAMPLESPERPIXEL_VALUE;
            uint16 config;
            bool istexture = false;
            bool isshadow = false;
            char* texformat = 0;
            char* datetime = 0;
            unsigned short colorspace = 2;
            float x_rez, y_rez;
            uint16 numExtra = 0, *extraSamples = 0;
            unsigned short sampleFormat = DEFAULT_TIFFTAG_SAMPLEFORMAT_VALUE;

            TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &colorspace);
            TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
            TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
            TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
            TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);
            TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sampleFormat);
            TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &config);
            TIFFGetField(tif, TIFFTAG_EXTRASAMPLES, &numExtra, &extraSamples);

#if 0
        if (TIFFGetField(tif, TIFFTAG_PIXAR_TEXTUREFORMAT, &texformat) == 1)
        {
            istexture = true;
            isshadow = !strcmp(texformat, "Shadow");
        }
#endif

            FrameBuffer* img = NULL;
            bool readAsRGBA = false;

            if ((colorspace != PHOTOMETRIC_RGB
                 && colorspace != PHOTOMETRIC_MINISBLACK
                 && colorspace != PHOTOMETRIC_MINISWHITE)
                || (m_addAlphaTo3Channel && samplesPerPixel == 3)
                || (config == PLANARCONFIG_CONTIG && samplesPerPixel > 4))
            {
                readAsRGBA = true;
                samplesPerPixel = 4;
            }

            switch (bitsPerSample)
            {
            case 1:
                dataType = FrameBuffer::UCHAR;
                break;

            case 8:
                if (sampleFormat == SAMPLEFORMAT_INT)
                {
                    // We promote tif signed int types to
                    // a float type framebuffer, normalized
                    // by the unsigned 8 bit value.
                    dataType = FrameBuffer::FLOAT;
                }
                else
                {
                    dataType = FrameBuffer::UCHAR;
                }
                break;

            case 16:
                if (sampleFormat == SAMPLEFORMAT_INT)
                {
                    // We promote tif signed int types to
                    // a float type framebuffer, normalized
                    // by the unsigned 16 bit value.
                    dataType = FrameBuffer::FLOAT;
                }
                else
                {
                    dataType = FrameBuffer::USHORT;
                }
                break;

            case 32:
                if (sampleFormat == SAMPLEFORMAT_INT)
                {
                    // We promote tif signed int types to
                    // a float type framebuffer, normalized
                    // by the unsigned 16 bit value for this case.
                    dataType = FrameBuffer::FLOAT;
                }
                else if (sampleFormat == SAMPLEFORMAT_UINT)
                {
                    dataType = FrameBuffer::UINT;
                }
                else
                {
                    dataType = FrameBuffer::FLOAT;
                }
                break;

            default:
                TWK_THROW_STREAM(UnsupportedException,
                                 "TIFF: Unsupported bit depth ("
                                     << bitsPerSample << ") trying to read "
                                     << filename);
            }

            if (config == PLANARCONFIG_SEPARATE)
            {
                const char* chanNames[] = {"R", "G", "B", "A", "Z",
                                           "X", "Y", "P", "D", "Q"};

                StringVector planeNames;

                for (int i = 0; i < samplesPerPixel && i < 10; i++)
                {
                    planeNames.push_back(string(chanNames[i]));
                }

                fb.restructurePlanar(width, height, planeNames, dataType,
                                     FrameBuffer::NATURAL);
            }
            else
            {
                fb.restructure(
                    width, height, 0, min((int)samplesPerPixel, 4),
                    dataType); // interleaved, we can only do 4 channels
            }

            string message = "Reading TIFF " + stl_ext::basename(filename);

            if (readAsRGBA)
            {
                uint32* p = fb.begin<uint32>();
                const uint32* e = fb.end<uint32>();

                TIFFReadRGBAImage(tif, width, height, p, 0);

#ifdef __BIG_ENDIAN__
                for (; p < e; p++)
                {
                    const uint32 i = *p;
                    *p = (uint32(TIFFGetR(i)) << 24)
                         | (uint32(TIFFGetG(i)) << 16)
                         | (uint32(TIFFGetB(i)) << 8) | uint32(TIFFGetA(i));
                }
#endif
            }
            else if (TIFFIsTiled(tif))
            {
                if (config == PLANARCONFIG_CONTIG)
                {
                    readContiguousTiledImage(tif, width, height, fb);
                    fb.newAttribute("TIFF/PlanarConfig",
                                    string("Tiled Contiguous"));
                }
                else
                {
                    readPlanarTiledImage(tif, width, height, fb);
                    fb.newAttribute("TIFF/PlanarConfig",
                                    string("Tiled Separate"));
                }
            }
            else
            {
                if (config == PLANARCONFIG_CONTIG)
                {
                    if (samplesPerPixel == 1
                        || (dataType != FrameBuffer::USHORT
                            && samplesPerPixel <= 4))
                    {
                        readContiguousScanlineImage(tif, width, height, fb);
                    }
                    else
                    {
                        const char* chanNames[] = {"R", "G", "B", "A", "Z",
                                                   "X", "Y", "P", "D", "Q"};

                        if (!fb.isPlanar())
                        {
                            StringVector planeNames;

                            for (int i = 0; i < samplesPerPixel && i < 10; i++)
                            {
                                planeNames.push_back(string(chanNames[i]));
                            }

                            fb.restructurePlanar(width, height, planeNames,
                                                 dataType,
                                                 FrameBuffer::NATURAL);
                        }
                        readContiguousScanlineImageAsPlanar(tif, width, height,
                                                            fb);
                    }

                    fb.newAttribute("TIFF/PlanarConfig", string("Contiguous"));
                }
                else
                {
                    readPlanarScanlineImage(tif, width, height, samplesPerPixel,
                                            fb);
                    fb.newAttribute("TIFF/PlanarConfig", string("Separate"));
                }
            }

            //
            //  Here we have a workaround for Maya writing the resolution tags
            //  wrong. Someday this should be replaced with a rule in our fancy
            //  rules system.
            //

            char* software = NULL;
            bool isMaya = false;
            unsigned int compression;

            if (TIFFGetField(tif, TIFFTAG_COMPRESSION, &compression))
            {
                string compressionName = tagToName(compression, compTags);
                fb.newAttribute("TIFF/Compression", compressionName);
            }

            if (TIFFGetField(tif, TIFFTAG_SOFTWARE, &software))
            {
                fb.newAttribute("TIFF/Software", string(software));

                if (!strncmp(software, "Maya", 4))
                {
                    isMaya = true;
                }
            }

            if (TIFFGetField(tif, TIFFTAG_XRESOLUTION, &x_rez)
                && TIFFGetField(tif, TIFFTAG_YRESOLUTION, &y_rez))
            {
                unsigned short unit = 0;

                if (TIFFGetField(tif, TIFFTAG_RESOLUTIONUNIT, &unit))
                {
                    ostringstream str;

                    switch (unit)
                    {
                    case 1:
                        str << "None";
                        break;
                    case 2:
                        str << "Inch";
                        break;
                    case 3:
                        str << "Centimeter";
                        break;
                    default:
                        str << "Unknown (" << unit << ")";
                        break;
                    }

                    fb.newAttribute("TIFF/ResolutionUnit", str.str());
                }

                if (!isMaya)
                {
                    fb.setPixelAspectRatio(x_rez != 0.0 ? y_rez / x_rez : 1.0);
                }

                fb.newAttribute("TIFF/XResolution", x_rez);
                fb.newAttribute("TIFF/YResolution", y_rez);
            }

            readAllTags(tif, fb);

            TIFFClose(tif);

#if 0
        if (isshadow)
        {
            TwkFB::normalize(&fb, true, true);
        }
#endif

            if (numExtra)
            {
                switch (extraSamples[0])
                {
                case EXTRASAMPLE_UNASSALPHA:
                    fb.attribute<string>("AlphaType") = "Unpremultipled";
                    break;
                case EXTRASAMPLE_ASSOCALPHA:
                    fb.attribute<string>("AlphaType") = "Premultiplied";
                    break;
                default:
                    break;
                }
            }

            //
            //  Most cards can't handle RGB or RGBA 16bit int textures.
            //
            if (fb.dataType() == FrameBuffer::USHORT && fb.numChannels() != 1
                && !fb.isPlanar())
            {
                //  cerr << "converting tiff to planar" << endl;
                FrameBufferVector fbv = split(&fb);
                fbv[0]->setIdentifier(fb.identifier());
                fb.copyAttributesTo(fbv[0]);
                for (int j = 1; j < fbv.size(); ++j)
                    fbv[0]->appendPlane(fbv[j]);
                fb.copyFrom(fbv[0]);
                delete fbv[0];
            }
        }
        catch (...)
        {
            delete stream;
            stream = 0;
            throw;
        }

        delete stream;
    }

static void readTIFF(ImageType::ImageStruct* im,
                         const Mu::String& imgFileName, Thread& thread)
    {
        Process* process = thread.process();
        MuLangContext* context = (MuLangContext*)process->context();

        //
        // Suppress annoying messages about unknown tags, etc...
        //

        TIFFSetErrorHandler(0);
        TIFFSetWarningHandler(0);

        TIFF* tif = TIFFOpen(imgFileName.c_str(), "r");

        if (!tif)
        {
            const Mu::MuLangContext* context =
                static_cast<const Mu::MuLangContext*>(thread.context());
            ExceptionType::Exception* e =
                new ExceptionType::Exception(context->exceptionType());
            e->string() = "failed to open image \"";
            e->string() += imgFileName;
            e->string() += "\"";
            thread.setException(e);
            throw ProgramException(e);
        }

        uint16* sampleinfo;
        uint16 extrasamples;
        uint16 bps;

        int width, height;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bps);

        im->width = width;
        im->height = height;

        TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &extrasamples,
                              &sampleinfo);

        unsigned short orient = ORIENTATION_TOPLEFT;
        TIFFGetField(tif, TIFFTAG_ORIENTATION, &orient);
        bool flip =
            orient == ORIENTATION_BOTLEFT || orient == ORIENTATION_BOTRIGHT;
        bool flop =
            orient == ORIENTATION_BOTRIGHT || orient == ORIENTATION_TOPRIGHT;
        flop = false;

        bool hasAlpha = extrasamples == 1 ? true : false;

        DynamicArrayType* dataType = static_cast<DynamicArrayType*>(
            context->arrayType(context->vec4fType(), 1, 0));
        im->data = new DynamicArray(dataType, 1);
        im->data->resize(width * height);

        vector<char> buf(TIFFScanlineSize(tif));

        int y0 = 0;
        int y1 = height;
        int inc = 1;

        switch (bps)
        {
        case 32:
            for (int y = 0; y < height; y++)
            {
                int oy = flip ? (height - 1 - y) : y;

                if (hasAlpha)
                {
                    TIFFReadScanline(tif, (float*)im->row(oy), y);
                }
                else
                {
                    TIFFReadScanline(tif, &buf[0], y);
                    Vector4f* dstPixel = im->row(oy);
                    Vector3f* srcPixel = reinterpret_cast<Vector3f*>(&buf[0]);

                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel)[0] = (*srcPixel)[0];
                        (*dstPixel)[1] = (*srcPixel)[1];
                        (*dstPixel)[2] = (*srcPixel)[2];
                        (*dstPixel)[3] = 1.0f;
                    }
                }

                if (flop)
                    reverse(im->row(oy), im->row(oy) + width);
            }
            break;

        case 16:
            for (int y = 0; y < height; y++)
            {
                int oy = flip ? (height - 1 - y) : y;
                TIFFReadScanline(tif, &buf[0], y);
                Vector4f* dstPixel = im->row(oy);

                if (hasAlpha)
                {
                    Vector4s* srcPixel = reinterpret_cast<Vector4s*>(&buf[0]);

                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel)[0] = (*srcPixel)[0] / 65535.0f;
                        (*dstPixel)[1] = (*srcPixel)[1] / 65535.0f;
                        (*dstPixel)[2] = (*srcPixel)[2] / 65535.0f;
                        (*dstPixel)[3] = (*srcPixel)[3] / 65535.0f;
                    }
                }
                else
                {
                    Vector3s* srcPixel = reinterpret_cast<Vector3s*>(&buf[0]);

                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel)[0] = (*srcPixel)[0] / 65535.0f;
                        (*dstPixel)[1] = (*srcPixel)[1] / 65535.0f;
                        (*dstPixel)[2] = (*srcPixel)[2] / 65535.0f;
                        (*dstPixel)[3] = 1.0f;
                    }
                }

                if (flop)
                    reverse(im->row(oy), im->row(oy) + width);
            }
            break;

        case 8:

            for (int y = 0; y < height; y++)
            {
                int oy = flip ? (height - 1 - y) : y;
                TIFFReadScanline(tif, &buf[0], y);
                Vector4f* dstPixel = im->row(oy);

                if (hasAlpha)
                {
                    Vector4c* srcPixel = reinterpret_cast<Vector4c*>(&buf[0]);

                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel)[0] = (*srcPixel)[0] / 255.0f;
                        (*dstPixel)[1] = (*srcPixel)[1] / 255.0f;
                        (*dstPixel)[2] = (*srcPixel)[2] / 255.0f;
                        (*dstPixel)[3] = (*srcPixel)[3] / 255.0f;
                    }
                }
                else
                {
                    Vector3c* srcPixel = reinterpret_cast<Vector3c*>(&buf[0]);

                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel)[0] = (*srcPixel)[0] / 255.0f;
                        (*dstPixel)[1] = (*srcPixel)[1] / 255.0f;
                        (*dstPixel)[2] = (*srcPixel)[2] / 255.0f;
                        (*dstPixel)[3] = 1.0f;
                    }
                }

                if (flop)
                    reverse(im->row(oy), im->row(oy) + width);
            }
            break;

        default:

            TIFFClose(tif);
            {
                width = 0;
                const Mu::MuLangContext* context =
                    static_cast<const Mu::MuLangContext*>(thread.context());
                ExceptionType::Exception* e =
                    new ExceptionType::Exception(context->exceptionType());
                e->string() = "Unsupported bit depth in image file \"";
                e->string() += imgFileName;
                e->string() += "\"";
                thread.setException(e);
                throw ProgramException(e);
            }
        }

        TIFFClose(tif);
    }

Img4f* TiffIff::read(const char* imgFileName, bool showWarnings)
    {
        if (!showWarnings)
        {
            // Suppress annoying messages about unknown tags, etc...
            TIFFSetErrorHandler(0);
            TIFFSetWarningHandler(0);
        }

        TIFF* tif = TIFFOpen(imgFileName, "r");
        if (tif == NULL)
        {
            IffExc exc("Could not open specified image file for reading");
            throw(exc);
        }

        int width;
        int height;
        uint16 bitsPerSample;
        uint16* sampleinfo;
        uint16 extrasamples;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);

        TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &extrasamples,
                              &sampleinfo);

        bool hasAlpha = false;
        if (extrasamples == 1)
        {
            hasAlpha = true;
        }

        Img4f* ret = new Img4f(width, height);
        tdata_t buf = _TIFFmalloc(TIFFScanlineSize(tif));

        switch (bitsPerSample)
        {
        case 32:
            for (int y = 0; y < height; ++y)
            {
                if (hasAlpha)
                {
                    TIFFReadScanline(tif, &(ret->pixel(0, height - y - 1)), y);
                }
                else
                {
                    TIFFReadScanline(tif, buf, y);
                    Col4f* dstPixel = (*ret)[height - y - 1];
                    Col3f* srcPixel = (Col3f*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = srcPixel->x;
                        (*dstPixel).y = srcPixel->y;
                        (*dstPixel).z = srcPixel->z;
                        (*dstPixel).w = 1.0f;
                    }
                }
            }
            break;
        case 16:
            for (int y = 0; y < height; ++y)
            {
                TIFFReadScanline(tif, buf, y);

                Col4f* dstPixel = (*ret)[height - y - 1];
                if (hasAlpha)
                {
                    Col4us* srcPixel = (Col4us*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (float)srcPixel->x / 65535.0f;
                        (*dstPixel).y = (float)srcPixel->y / 65535.0f;
                        (*dstPixel).z = (float)srcPixel->z / 65535.0f;
                        (*dstPixel).w = (float)srcPixel->w / 65535.0f;
                    }
                }
                else
                {
                    Col3us* srcPixel = (Col3us*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (float)srcPixel->x / 65535.0f;
                        (*dstPixel).y = (float)srcPixel->y / 65535.0f;
                        (*dstPixel).z = (float)srcPixel->z / 65535.0f;
                        (*dstPixel).w = 1.0f;
                    }
                }
            }
            break;
        case 8:
            for (int y = 0; y < height; ++y)
            {
                TIFFReadScanline(tif, buf, y);

                Col4f* dstPixel = (*ret)[height - y - 1];

                if (hasAlpha)
                {
                    Col4uc* srcPixel = (Col4uc*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (float)srcPixel->x / 255.0f;
                        (*dstPixel).y = (float)srcPixel->y / 255.0f;
                        (*dstPixel).z = (float)srcPixel->z / 255.0f;
                        (*dstPixel).w = (float)srcPixel->w / 255.0f;
                    }
                }
                else
                {
                    Col3uc* srcPixel = (Col3uc*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (float)srcPixel->x / 255.0f;
                        (*dstPixel).y = (float)srcPixel->y / 255.0f;
                        (*dstPixel).z = (float)srcPixel->z / 255.0f;
                        (*dstPixel).w = 1.0f;
                    }
                }
            }
            break;
        default:
            IffExc exc("Sorry, unsupported TIFF data type.");
            throw(exc);
        } // End switch( bitsPerSample )

        _TIFFfree(buf);

        TIFFClose(tif);

        return ret;
    }

Img4uc* TiffIff::read4uc(const char* imgFileName, bool showWarnings)
    {
        if (!showWarnings)
        {
            // Suppress annoying messages about unknown tags, etc...
            TIFFSetErrorHandler(0);
            TIFFSetWarningHandler(0);
        }

        TIFF* tif = TIFFOpen(imgFileName, "r");
        if (tif == NULL)
        {
            IffExc exc("Could not open specified image file for reading");
            throw(exc);
        }

        int width;
        int height;
        uint16 bitsPerSample;
        uint16* sampleinfo;
        uint16 extrasamples;

        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height);
        TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);

        TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES, &extrasamples,
                              &sampleinfo);

        bool hasAlpha = false;
        if (extrasamples == 1)
        {
            hasAlpha = true;
        }

        Img4uc* ret = new Img4uc(width, height);
        tdata_t buf = _TIFFmalloc(TIFFScanlineSize(tif));

        switch (bitsPerSample)
        {
        case 32:
            for (int y = 0; y < height; ++y)
            {
                TIFFReadScanline(tif, buf, y);

                Col4uc* dstPixel = (*ret)[height - y - 1];
                if (hasAlpha)
                {
                    Col4f* srcPixel = (Col4f*)buf;
                    (*dstPixel).x = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).x * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).y = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).y * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).z = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).z * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).w = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).w * 255.0f, 0.0f, 255.0f));
                }
                else
                {
                    Col3f* srcPixel = (Col3f*)buf;
                    (*dstPixel).x = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).x * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).y = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).y * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).z = (unsigned char)(TwkMath::clamp(
                        (*srcPixel).z * 255.0f, 0.0f, 255.0f));
                    (*dstPixel).w = 255;
                }
            }
            break;
        case 16:
            for (int y = 0; y < height; ++y)
            {
                TIFFReadScanline(tif, buf, y);

                Col4uc* dstPixel = (*ret)[height - y - 1];
                if (hasAlpha)
                {
                    Col4us* srcPixel = (Col4us*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).x / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).y = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).y / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).z = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).z / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).w = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).w / 255.0f, 0.0f, 255.0f));
                    }
                }
                else
                {
                    Col3us* srcPixel = (Col3us*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).x / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).y = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).y / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).z = (unsigned char)(TwkMath::clamp(
                            (*srcPixel).z / 255.0f, 0.0f, 255.0f));
                        (*dstPixel).w = 255;
                    }
                }
            }
            break;
        case 8:
            for (int y = 0; y < height; ++y)
            {
                TIFFReadScanline(tif, buf, y);

                Col4uc* dstPixel = (*ret)[height - y - 1];

                if (hasAlpha)
                {
                    TIFFReadScanline(tif, &(ret->pixel(0, height - y - 1)), y);
                }
                else
                {
                    Col3uc* srcPixel = (Col3uc*)buf;
                    for (int x = 0; x < width; ++x, ++dstPixel, ++srcPixel)
                    {
                        (*dstPixel).x = srcPixel->x;
                        (*dstPixel).y = srcPixel->y;
                        (*dstPixel).z = srcPixel->z;
                        (*dstPixel).w = 255;
                    }
                }
            }
            break;
        default:
            IffExc exc("Sorry, unsupported TIFF data type.");
            throw(exc);
        } // End switch( bitsPerSample )

        _TIFFfree(buf);

        TIFFClose(tif);

        return ret;
    }

cv::Mat load_tiff(std::string file,bool bgr2rgb)
    {
        TIFF *in = TIFFOpen(file.c_str(),"r");
        if(!in)
            throw std::runtime_error("Failed to open tiff file " + file);
        try {
            uint32_t width,height;
            uint16_t depth,spp,format = SAMPLEFORMAT_UINT;
            TIFFGetField(in,TIFFTAG_IMAGEWIDTH,&width);
            TIFFGetField(in,TIFFTAG_IMAGELENGTH,&height);
            TIFFGetField(in,TIFFTAG_SAMPLESPERPIXEL,&spp);
            TIFFGetField(in,TIFFTAG_BITSPERSAMPLE,&depth);
            TIFFGetField(in,TIFFTAG_SAMPLEFORMAT,&format);
            if(spp <= 0 || spp > 4)
                throw std::runtime_error("Invalid format for " + file);
            int type = -1;
            switch(depth) {
            case 8:
                switch(format) {
                case SAMPLEFORMAT_INT: type = CV_MAKETYPE(CV_8S,spp);  break;
                case SAMPLEFORMAT_UINT: type = CV_MAKETYPE(CV_8U,spp);  break;
                }
                break;
            case 16:
                switch(format) {
                case SAMPLEFORMAT_INT: type = CV_MAKETYPE(CV_16S,spp);  break;
                case SAMPLEFORMAT_UINT: type = CV_MAKETYPE(CV_16U,spp);  break;
                }
                break;
            case 32:
                switch(format) {
                case SAMPLEFORMAT_INT: type = CV_MAKETYPE(CV_32S,spp);  break;
                case SAMPLEFORMAT_IEEEFP: type = CV_MAKETYPE(CV_32F,spp);  break;
                }
                break;
            case 64:
                switch(format) {
                case SAMPLEFORMAT_IEEEFP: type = CV_MAKETYPE(CV_64F,spp);  break;
                }
                break;
            }
            if(type == -1)
                throw std::runtime_error("Unsupported tiff format " + file + ": spp=" + std::to_string(spp) + " depth=" + std::to_string(depth) + " format=" + std::to_string(format));
            cv::Mat res(height,width,type);
            if(TIFFScanlineSize(in)!=int(res.step[0])) {
                throw std::runtime_error("Internal error in stride/scanline size in " + file);
            }
            for(unsigned i=0;i<height;i++) {
                if(TIFFReadScanline(in,(char*)res.data + res.step[0]*i,i,0)<0)
                    throw std::runtime_error("Failed to read " + file);
            }
            TIFFClose(in);
            if(bgr2rgb) {
                cv::Mat tmp;
                if(res.channels() == 3) {
                    cv::cvtColor(res,tmp,cv::COLOR_RGB2BGR);
                    res = tmp;
                }
                else if(res.channels() == 4) {
                    cv::cvtColor(res,tmp,cv::COLOR_RGBA2BGRA);
                    res = tmp;
                }
            }
            return res;
        }
        catch(...) {
            TIFFClose(in);
            throw;
        }
    }

static osg::ImageSequence* tiffLoad(std::istream& fin, const osgDB::Options* options)
{
    TIFFSetErrorHandler(tiffError);
    TIFFSetWarningHandler(tiffWarn);
    TIFF* in = TIFFClientOpen("inputstream", "r", (thandle_t)&fin,
                              tiffStreamReadProc, tiffStreamWriteProc,
                              tiffStreamSeekProc, tiffStreamCloseProc,
                              tiffStreamSizeProc, tiffStreamMapProc, tiffStreamUnmapProc);
    if (in == NULL) { OSG_WARN << "[ReaderWriterTiff] Unable to open stream" << std::endl; return NULL; }

    uint16_t photometric = 0;
    if (TIFFGetField(in, TIFFTAG_PHOTOMETRIC, &photometric) == 1)
    {
        if (photometric != PHOTOMETRIC_RGB && photometric != PHOTOMETRIC_PALETTE &&
            photometric != PHOTOMETRIC_MINISWHITE && photometric != PHOTOMETRIC_MINISBLACK)
        {
            OSG_WARN << "[ReaderWriterTiff] Photometric type " << photometric << " not handled" << std::endl;
            TIFFClose(in); return NULL;
        }
    }
    else
    {
        OSG_WARN << "[ReaderWriterTiff] Unable to get photometric type" << std::endl;
        TIFFClose(in); return NULL;
    }

    uint16_t samplesperpixel = 0;
    if (TIFFGetField(in, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel) == 1)
    {
        if (samplesperpixel != 1 && samplesperpixel != 2 &&
            samplesperpixel != 3 && samplesperpixel != 4)
        {
            OSG_WARN << "[ReaderWriterTiff] Bad samples per pixel: " << samplesperpixel << std::endl;
            TIFFClose(in); return NULL;
        }
    }
    else
    {
        OSG_WARN << "[ReaderWriterTiff] Unable to get samples per pixel" << std::endl;
        TIFFClose(in); return NULL;
    }

    uint16_t bitspersample = 0;
    if (TIFFGetField(in, TIFFTAG_BITSPERSAMPLE, &bitspersample) == 1)
    {
        if (bitspersample != 8 && bitspersample != 16 && bitspersample != 32)
        {
            OSG_WARN << "[ReaderWriterTiff] Can only handle 8, 16 and 32 bit samples" << std::endl;
            TIFFClose(in); return NULL;
        }
    }
    else
    {
        OSG_WARN << "[ReaderWriterTiff] Unable to get bits per sample" << std::endl;
        TIFFClose(in); return NULL;
    }

    uint32_t w = 0, h = 0, d = 1; uint16_t config = 0, dataType = 0;
    if (TIFFGetField(in, TIFFTAG_IMAGEWIDTH, &w) != 1 || TIFFGetField(in, TIFFTAG_IMAGELENGTH, &h) != 1 ||
        TIFFGetField(in, TIFFTAG_PLANARCONFIG, &config) != 1)
    {
        OSG_WARN << "[ReaderWriterTiff] Unable to get width / height / depth parameters" << std::endl;
        TIFFClose(in); return NULL;
    }

    // if it has a palette, data returned is 3 byte rgb
    int format = (photometric == PHOTOMETRIC_PALETTE) ? 3 : (samplesperpixel * bitspersample / 8);
    int bytespersample = bitspersample / 8;
    int bytesperpixel = bytespersample * samplesperpixel;
    TIFFGetField(in, TIFFTAG_DATATYPE, &dataType);
    TIFFGetField(in, TIFFTAG_IMAGEDEPTH, &d);

    osg::ref_ptr<osg::ImageSequence> seq = new osg::ImageSequence;
    if (d > 1)
    {
        // TODO...
        OSG_WARN << "[ReaderWriterTiff] Unsupported dimension" << std::endl;
    }
    else
    {
        int dirCount = 0, imgSize = w * h * format;
        do
        {
            unsigned char* inBuffer = NULL; bool hasError = false;
            unsigned char* buffer = new unsigned char[imgSize];
            memset(buffer, 0, imgSize); dirCount++;

            unsigned char* currPtr = buffer + (h - 1) * w * format;
            uint16_t *red = NULL, *green = NULL, *blue = NULL;
            size_t rowSize = TIFFScanlineSize(in);
            switch (PACK(photometric, config))
            {
            case PACK(PHOTOMETRIC_MINISWHITE, PLANARCONFIG_CONTIG):
            case PACK(PHOTOMETRIC_MINISBLACK, PLANARCONFIG_CONTIG):
            case PACK(PHOTOMETRIC_MINISWHITE, PLANARCONFIG_SEPARATE):
            case PACK(PHOTOMETRIC_MINISBLACK, PLANARCONFIG_SEPARATE):
                inBuffer = new unsigned char[rowSize];
                for (uint32_t row = 0; row < h; row++)
                {
                    if (TIFFReadScanline(in, inBuffer, row, 0) < 0) { hasError = true; break; }
                    invertRow(currPtr, inBuffer, samplesperpixel * w,
                              photometric == PHOTOMETRIC_MINISWHITE, bitspersample);
                    currPtr -= format * w;
                }
                break;

            case PACK(PHOTOMETRIC_PALETTE, PLANARCONFIG_CONTIG):
            case PACK(PHOTOMETRIC_PALETTE, PLANARCONFIG_SEPARATE):
                if (TIFFGetField(in, TIFFTAG_COLORMAP, &red, &green, &blue) != 1)
                { hasError = true; break; }
                else if (!hasError && bitspersample != 32 && checkColormap(1<<bitspersample, red, green, blue) == 16)
                {
                    for (int i = (1 << bitspersample) - 1; i >= 0; --i)
                    { red[i] = CVT(red[i]); green[i] = CVT(green[i]); blue[i] = CVT(blue[i]); }
                }

                inBuffer = new unsigned char[rowSize];
                for (uint32_t row = 0; row < h; row++)
                {
                    if (TIFFReadScanline(in, inBuffer, row, 0) < 0) { hasError = true; break; }
                    remapRow(currPtr, inBuffer, w, red, green, blue); currPtr -= format * w;
                }
                break;

            case PACK(PHOTOMETRIC_RGB, PLANARCONFIG_CONTIG):
                inBuffer = new unsigned char[rowSize];
                for (uint32_t row = 0; row < h; row++)
                {
                    if (TIFFReadScanline(in, inBuffer, row, 0) < 0) { hasError = true; break; }
                    memcpy(currPtr, inBuffer, format * w); currPtr -= format * w;
                }
                break;

            case PACK(PHOTOMETRIC_RGB, PLANARCONFIG_SEPARATE):
                inBuffer = new unsigned char[format * rowSize];
                for (uint32_t row = 0; !hasError && row < h; row++)
                {
                    for (int s = 0; s < format; s++)
                    {
                        if (TIFFReadScanline(in, (tdata_t)(inBuffer + s * rowSize), (uint32_t)row, (tsample_t)s) < 0)
                        { hasError = true; break; }
                    }

                    if (!hasError)
                    {
                        unsigned char *inBuffer2 = inBuffer + rowSize, *inBuffer3 = inBuffer + 2 * rowSize,
                                      *inBuffer4 = inBuffer + 3 * rowSize;
                        if (format == 4) interleaveRow(currPtr, inBuffer, inBuffer2, inBuffer3, inBuffer4, w, format, bitspersample);
                        else if (format == 3) interleaveRow(currPtr, inBuffer, inBuffer2, inBuffer3, w, format, bitspersample);
                        currPtr -= format * w;
                    }
                }
                break;
            default:
                OSG_WARN << "[ReaderWriterTiff] Unsupported Packing: " << photometric << ", " << config << std::endl;
                hasError = true; break;
            }

            if (inBuffer) delete[] inBuffer;
            if (hasError)
            {
                OSG_WARN << "[ReaderWriterTiff] Failed to read with packing: " << photometric << ", " << config << std::endl;
                if (buffer) delete[] buffer; continue;
            }

            int numComponents = (photometric == PHOTOMETRIC_PALETTE) ? format : samplesperpixel;
            unsigned int pixelFormat =
                (numComponents) == 1 ? GL_LUMINANCE :
                (numComponents) == 2 ? GL_LUMINANCE_ALPHA :
                (numComponents) == 3 ? GL_RGB :
                (numComponents) == 4 ? GL_RGBA : (GLenum)-1;
            unsigned int dataType =
                (bitspersample == 8) ? GL_UNSIGNED_BYTE :
                (bitspersample == 16) ? GL_UNSIGNED_SHORT :
                (bitspersample == 32) ? GL_FLOAT : (GLenum)-1;
            unsigned int internalFormat = computeInternalFormat(pixelFormat, dataType);
            if (internalFormat <= 0)
            {
                OSG_WARN << "[ReaderWriterTiff] Unsupported image format" << std::endl;
                continue;
            }

            osg::Image* image = new osg::Image;
            image->setImage(w, h, d, internalFormat, pixelFormat, dataType,
                            buffer, osg::Image::USE_NEW_DELETE);
            seq->addImage(image);
        } while (TIFFReadDirectory(in));
    }
    TIFFClose(in);
    return seq.release();
}

void PdfImage::loadFromTiffHandle(void* handle, unsigned imageIndex)
{
    TIFF* hInTiffHandle = (TIFF*)handle;

    int32_t row, width, height;
    uint16_t samplesPerPixel, bitsPerSample;
    uint16_t* sampleInfo;
    uint16_t extraSamples;
    uint16_t planarConfig, photoMetric, orientation;
    int32_t resolutionUnit;

    // Set the page/image index in the tiff context
    TIFFSetDirectory(hInTiffHandle, (uint16_t)imageIndex);

    TIFFGetField(hInTiffHandle, TIFFTAG_IMAGEWIDTH, &width);
    TIFFGetField(hInTiffHandle, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_BITSPERSAMPLE, &bitsPerSample);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_PLANARCONFIG, &planarConfig);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_PHOTOMETRIC, &photoMetric);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_EXTRASAMPLES, &extraSamples, &sampleInfo);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_ORIENTATION, &orientation);

    resolutionUnit = 0;
    float resX;
    float resY;
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_XRESOLUTION, &resX);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_YRESOLUTION, &resY);
    TIFFGetFieldDefaulted(hInTiffHandle, TIFFTAG_RESOLUTIONUNIT, &resolutionUnit);

    int colorChannels = samplesPerPixel - extraSamples;

    int bitsPixel = bitsPerSample * samplesPerPixel;

    // TODO: implement special cases
    if (TIFFIsTiled(hInTiffHandle))
        PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);

    if (planarConfig != PLANARCONFIG_CONTIG && colorChannels != 1)
        PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);

    PdfImageInfo info;
    info.Width = width;
    info.Height = height;
    info.BitsPerComponent = (unsigned char)bitsPerSample;
    switch (photoMetric)
    {
        case PHOTOMETRIC_MINISBLACK:
        {
            if (bitsPixel == 1)
            {
                info.DecodeArray.push_back(0);
                info.DecodeArray.push_back(1);
                info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceGrayInstace();
            }
            else if (bitsPixel == 8 || bitsPixel == 16)
            {
                info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceGrayInstace();
            }
            else
            {
                PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);
            }
            break;
        }
        case PHOTOMETRIC_MINISWHITE:
        {
            if (bitsPixel == 1)
            {
                info.DecodeArray.push_back(1);
                info.DecodeArray.push_back(0);
                info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceGrayInstace();
            }
            else if (bitsPixel == 8 || bitsPixel == 16)
            {
                info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceGrayInstace();
            }
            else
            {
                PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);
            }
            break;
        }
        case PHOTOMETRIC_RGB:
        {
            if (bitsPixel != 24)
                PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);

            info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceRGBInstace();
            break;
        }
        case PHOTOMETRIC_SEPARATED:
        {
            if (bitsPixel != 32)
                PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);

            info.ColorSpace = PdfColorSpaceFilterFactory::GetDeviceCMYKInstace();
            break;
        }
        case PHOTOMETRIC_PALETTE:
        {
            unsigned numColors = (1 << bitsPixel);
            info.DecodeArray.push_back(0);
            info.DecodeArray.push_back(numColors - 1);

            uint16_t* rgbRed;
            uint16_t* rgbGreen;
            uint16_t* rgbBlue;
            TIFFGetField(hInTiffHandle, TIFFTAG_COLORMAP, &rgbRed, &rgbGreen, &rgbBlue);

            charbuff data(numColors * 3);

            for (unsigned clr = 0; clr < numColors; clr++)
            {
                data[3 * clr + 0] = (char)(rgbRed[clr] / 257);
                data[3 * clr + 1] = (char)(rgbGreen[clr] / 257);
                data[3 * clr + 2] = (char)(rgbBlue[clr] / 257);
            }

            // Create a colorspace object
            auto& idxObj = this->GetDocument().GetObjects().CreateDictionaryObject();
            idxObj.GetOrCreateStream().SetData(data);

            // Add the colorspace to our image
            info.ColorSpace = PdfColorSpaceInitializer(std::make_shared<PdfColorSpaceFilterIndexed>(
                PdfColorSpaceInitializer(PdfColorSpaceFilterFactory::GetDeviceRGBInstace()), numColors, std::move(data)));
            break;
        }

        default:
        {
            PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);
            break;
        }
    }

    size_t scanlineSize = TIFFScanlineSize(hInTiffHandle);
    size_t bufferSize = scanlineSize * height;
    charbuff buffer(bufferSize);
    for (row = 0; row < height; row++)
    {
        if (TIFFReadScanline(hInTiffHandle,
            &buffer[row * scanlineSize],
            row) == (-1))
        {
            PODOFO_RAISE_ERROR(PdfErrorCode::UnsupportedImageFormat);
        }
    }

    SpanStreamDevice input(buffer);
    SetDataRaw(input, info);
}

void tiffDecode(PointerRange<const char> inBuffer, ImageImpl *impl)
	{
		TIFF *t = nullptr;
		try
		{
			BufferIStream stream(inBuffer);
			t = TIFFStreamOpen("MemTIFF", &stream);
			if (!t)
				CAGE_THROW_ERROR(Exception, "failed to initialize tiff decoding");
			{
				uint16 planarconfig = 0;
				TIFFGetField(t, TIFFTAG_PLANARCONFIG, &planarconfig);
				if (planarconfig != PLANARCONFIG_CONTIG)
					CAGE_THROW_ERROR(Exception, "unsupported planar configuration in tiff decoding");
			}
			TIFFGetField(t, TIFFTAG_IMAGEWIDTH, &impl->width);
			TIFFGetField(t, TIFFTAG_IMAGELENGTH, &impl->height);
			{
				uint16 ch = 0;
				TIFFGetField(t, TIFFTAG_SAMPLESPERPIXEL, &ch);
				impl->channels = ch;
			}
			{
				uint16 bpp = 0;
				TIFFGetField(t, TIFFTAG_BITSPERSAMPLE, &bpp);
				uint16 sampleformat = 0;
				TIFFGetField(t, TIFFTAG_SAMPLEFORMAT, &sampleformat);
				if (sampleformat == 0)
					sampleformat = SAMPLEFORMAT_UINT;
				if (bpp == 8 && sampleformat == SAMPLEFORMAT_UINT)
					impl->format = ImageFormatEnum::U8;
				else if (bpp == 16 && sampleformat == SAMPLEFORMAT_UINT)
					impl->format = ImageFormatEnum::U16;
				else if (bpp == 32 && sampleformat == SAMPLEFORMAT_IEEEFP)
					impl->format = ImageFormatEnum::Float;
				else
					CAGE_THROW_ERROR(Exception, "unsupported format in tiff decoding");
			}
			const uint32 stride = numeric_cast<uint32>(TIFFScanlineSize(t));
			CAGE_ASSERT(stride == impl->width * impl->channels * formatBytes(impl->format));
			impl->mem.resize(impl->height * stride);
			for (uint32 row = 0; row < impl->height; row++)
			{
				char *dst = impl->mem.data() + row * stride;
				if (TIFFReadScanline(t, dst, row) < 0)
					CAGE_THROW_ERROR(Exception, "failed scanline reading in tiff decoding");
			}
			TIFFClose(t);

			// color config
			// todo deduce it from the file
		}
		catch (...)
		{
			if (t)
				TIFFClose(t);
			throw;
		}
	}

void texture_t::load_tiff(int index, bool allow_diff_width_height, bool allow_two_byte_grayscale) {

#ifdef ENABLE_TIFF
	TIFF* tif = TIFFOpen(append_texture_dir(name).c_str(), "r"); // first try texture directory
	if (tif == NULL) {tif = TIFFOpen(name.c_str(), "r");} // not found, try current directory

	if (tif == NULL) {
		cerr << "Error opening tiff file " << name << " for read." << endl;
		exit(1);
	}
	uint32_t w(0), h(0);
	uint16_t bit_depth(0), config(0);
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH,    &w);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH,   &h);
	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bit_depth);
	TIFFGetField(tif, TIFFTAG_PLANARCONFIG,  &config);
	set_image_size(w, h, allow_diff_width_height);

	if (allow_two_byte_grayscale && (ncolors == 0 || ncolors == 1) && bit_depth == 16) { // 16-bit grayscale
		set_16_bit_grayscale();
		tmsize_t const sl_size(TIFFScanlineSize(tif));
		assert(sl_size == 2*width);
        tdata_t buf = _TIFFmalloc(sl_size);
		assert(buf);
		alloc();
		assert(config == PLANARCONFIG_CONTIG); // no support for PLANARCONFIG_SEPARATE, but could be added later

		for (int row = 0; row < height; row++) {
			TIFFReadScanline(tif, buf, row);

			for (int i = 0; i < sl_size; ++i) { // x-values
				data[sl_size*(height - row - 1) + i] = ((unsigned char const *)buf)[i]; // assumes little endian byte ordering, no swap required, may need to check this?
			}
		}
        _TIFFfree(buf);
	}
	else {
		if (ncolors == 0) {ncolors = 4;} // 3?
		uint32_t *raster = (uint32_t *)_TIFFmalloc(num_pixels() * sizeof(uint32_t));
		assert(raster != NULL);

		if (!TIFFReadRGBAImage(tif, width, height, raster, 0)) {
			cerr << "Error reading data from tiff file " << name << "." << endl;
			exit(1);
		}
		alloc();

		for (int y = 0; y < height; ++y) {
			for (int x = 0; x < width; ++x) {
				unsigned const ix(y*width + x);
				unsigned char const *d((unsigned char const *)(raster + ix));
				for (int i = 0; i < ncolors; ++i) {data[ncolors*ix+i] = d[i];} // not correct for lum+alpha textures?
			}
		}
		_TIFFfree(raster);
	}
	TIFFClose(tif);
#else
	cerr << "Error loading texture image file " << name << ": tiff support has not been enabled." << endl;
	exit(1);
#endif
}

void Tiffload::Open() throw(invalid_argument, runtime_error)
{
	// have we already opened the image?
	if (buffer != NULL) {
		return;
	}

  TIFF* image;
  if ((image = TIFFOpen(filename.c_str(), "r")) == NULL) {
    throw invalid_argument("Could not open image!");
  }

  TIFFGetField(image, TIFFTAG_BITSPERSAMPLE, &depth);
  depth = (2 << (depth - 1)) - 1;

/*  uint16 spp;
  if ((TIFFGetField(image, TIFFTAG_SAMPLESPERPIXEL, &spp) == 0) || (spp != 1)) {
    throw invalid_argument("Either undefined or unsupported number of samples per pixel");
  }*/

  tsize_t stripSize = TIFFStripSize(image);
  int stripMax = TIFFNumberOfStrips(image);
  unsigned long imageOffset = 0;

  unsigned long bufferSize = stripMax * stripSize;
  try {
    buffer = new unsigned char[bufferSize];
  } catch (bad_alloc&) {
    throw runtime_error("Failure to allocate storage for tiff image");
  }

  for (int stripCount = 0; stripCount < stripMax; ++stripCount) {
    long result = TIFFReadEncodedStrip(image, stripCount, buffer + imageOffset, stripSize);
	if (result == -1) {
	  throw invalid_argument("Read error for tiff image");
	}
    imageOffset += result;
  }

  // find the number of rows and number of columns
  if (TIFFGetField(image, TIFFTAG_IMAGEWIDTH, &cols) == 0) {
    throw invalid_argument("Tiff image does not define its width");
  }
  if (TIFFGetField(image, TIFFTAG_IMAGELENGTH, &rows) == 0) {
    throw invalid_argument("Tiff image does not define its length");
  }

  TIFFClose(image);
}

std::unique_ptr<VistaTexture> TextureLoader::loadFromFile(std::string const& sFileName) {

  std::string suffix = sFileName.substr(sFileName.rfind('.'));

  if (suffix == ".tga") {
    // load with vista
    logger().debug("Loading Texture '{}' with Vista.", sFileName);
    return std::unique_ptr<VistaTexture>(VistaOGLUtils::LoadTextureFromTga(sFileName));
  }

  std::unique_ptr<VistaTexture> result = std::make_unique<VistaTexture>(GL_TEXTURE_2D);

  if (suffix == ".tiff" || suffix == ".tif") {
    // load with tifflib
    logger().debug("Loading Texture '{}' with libtiff.", sFileName);

    auto* data = TIFFOpen(sFileName.c_str(), "r");
    if (!data) {
      logger().error("Failed to load '{}' with libtiff!", sFileName);
      return nullptr;
    }

    uint32 width{};
    uint32 height{};
    TIFFGetField(data, TIFFTAG_IMAGELENGTH, &height);
    TIFFGetField(data, TIFFTAG_IMAGEWIDTH, &width);

    uint16 bpp{};
    TIFFGetField(data, TIFFTAG_BITSPERSAMPLE, &bpp);

    int16 channels{};
    TIFFGetField(data, TIFFTAG_SAMPLESPERPIXEL, &channels);

    GLenum ePixelFormat = GL_RGBA;

    if (channels == 1) {
      ePixelFormat = GL_RED;
    } else if (channels == 2) {
      ePixelFormat = GL_RG;
    } else if (channels == 3) {
      ePixelFormat = GL_RGB;
    }

    if (bpp != 8 && bpp != 32) {
      logger().error(
          "Failed to load '{}' with libtiff: Only 8 or 32 bit per sample are supported right now!",
          sFileName);
      return nullptr;
    }

    if (bpp == 32) {

      std::vector<float> pixels(width * height * channels);

      for (unsigned y = 0; y < height; y++) {
        TIFFReadScanline(data, &pixels[width * channels * y], y);
      }

      result->Bind();
      glTexImage2D(
          GL_TEXTURE_2D, 0, GL_RGB32F, width, height, 0, ePixelFormat, GL_FLOAT, pixels.data());
      glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);

    } else {

      std::vector<char> pixels(width * height * channels);

      for (unsigned y = 0; y < height; y++) {
        TIFFReadScanline(data, &pixels[width * channels * y], y);
      }

      result->UploadTexture(width, height, pixels.data(), true, ePixelFormat);
    }

    TIFFClose(data);

  } else if (suffix == ".hdr") {

    // load with stb image
    logger().debug("Loading HDR Texture '{}' with stbi.", sFileName);

    int width{};
    int height{};
    int bpp{};
    int channels = 4;

    float* pixels = stbi_loadf(sFileName.c_str(), &width, &height, &bpp, channels);

    if (!pixels) {
      logger().error("Failed to load '{}' with stbi!", sFileName);
      return nullptr;
    }

    result->Bind();
    gluBuild2DMipmaps(GL_TEXTURE_2D, GL_RGBA32F, width, height, GL_RGBA, GL_FLOAT, pixels);

    stbi_image_free(pixels);

  } else {
    // load with stb image
    logger().debug("Loading Texture '{}' with stbi.", sFileName);

    int width{};
    int height{};
    int bpp{};
    int channels = 4;

    unsigned char* pixels = stbi_load(sFileName.c_str(), &width, &height, &bpp, channels);

    if (!pixels) {
      logger().error("Failed to load '{}' with stbi!", sFileName);
      return nullptr;
    }

    result->UploadTexture(width, height, pixels);

    stbi_image_free(pixels);
  }

  return result;
}

std::tuple<GLuint, glm::ivec2> read2DTexture(std::string const& path) {
  auto* data = TIFFOpen(path.c_str(), "r");

  if (!data) {
    logger().error("Failed to open TIFF file '{}'", path);
    return {0u, {0, 0}};
  }

  uint32_t width{};
  uint32_t height{};

  TIFFGetField(data, TIFFTAG_IMAGELENGTH, &height);
  TIFFGetField(data, TIFFTAG_IMAGEWIDTH, &width);

  std::vector<float> pixels(width * height * 3);

  for (unsigned y = 0; y < height; y++) {
    TIFFReadScanline(data, &pixels[width * 3 * y], y);
  }

  TIFFClose(data);

  GLuint texture;
  glGenTextures(1, &texture);
  glActiveTexture(GL_TEXTURE0);
  glBindTexture(GL_TEXTURE_2D, texture);
  glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);
  glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);
  glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_EDGE);
  glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_EDGE);
  glBindBuffer(GL_PIXEL_UNPACK_BUFFER, 0);
  glTexImage2D(GL_TEXTURE_2D, 0, GL_RGB32F, width, height, 0, GL_RGB, GL_FLOAT, pixels.data());

  return {texture, {width, height}};
}

std::tuple<GLuint, glm::ivec3> read3DTexture(std::string const& path) {
  auto* data = TIFFOpen(path.c_str(), "r");

  if (!data) {
    logger().error("Failed to open TIFF file '{}'", path);
    return {0u, {0, 0, 0}};
  }

  uint32_t width{};
  uint32_t height{};
  uint32_t depth{};

  TIFFGetField(data, TIFFTAG_IMAGELENGTH, &height);
  TIFFGetField(data, TIFFTAG_IMAGEWIDTH, &width);

  do {
    depth++;
  } while (TIFFReadDirectory(data));

  std::vector<float> pixels(width * height * depth * 3);

  for (unsigned z = 0; z < depth; z++) {
    TIFFSetDirectory(data, z);
    for (unsigned y = 0; y < height; y++) {
      TIFFReadScanline(data, &pixels[width * 3 * y + (3 * width * height * z)], y);
    }
  }

  TIFFClose(data);

  GLuint texture;
  glGenTextures(1, &texture);
  glActiveTexture(GL_TEXTURE0);
  glBindTexture(GL_TEXTURE_3D, texture);
  glTexParameteri(GL_TEXTURE_3D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);
  glTexParameteri(GL_TEXTURE_3D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);
  glTexParameteri(GL_TEXTURE_3D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_EDGE);
  glTexParameteri(GL_TEXTURE_3D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_EDGE);
  glTexParameteri(GL_TEXTURE_3D, GL_TEXTURE_WRAP_R, GL_CLAMP_TO_EDGE);
  glBindBuffer(GL_PIXEL_UNPACK_BUFFER, 0);
  glTexImage3D(
      GL_TEXTURE_3D, 0, GL_RGB32F, width, height, depth, 0, GL_RGB, GL_FLOAT, pixels.data());

  return {texture, {width, height, depth}};
}

RGBATexture read2DTexture(std::string const& path, uint32_t layer) {
  RGBATexture texture;

  auto* data = TIFFOpen(path.c_str(), "r");

  if (!data) {
    std::cerr << "Failed to open TIFF file '" << path << "'" << std::endl;
    return texture;
  }

  TIFFGetField(data, TIFFTAG_IMAGELENGTH, &texture.height);
  TIFFGetField(data, TIFFTAG_IMAGEWIDTH, &texture.width);

  std::vector<float> rgbData(texture.width * texture.height * 3);

  TIFFSetDirectory(data, layer);
  for (unsigned y = 0; y < texture.height; y++) {
    TIFFReadScanline(data, &rgbData[texture.width * 3 * y], y);
  }

  TIFFClose(data);

  texture.data = addAlphaChannel(rgbData);

  return texture;
}

bool loadImpl(TileSourceWebMapService* source, BaseTileData* tile, TileId const& tileId, int x,
    int y, CopyPixels which) {
  std::optional<std::string> cacheFile;

  // First we download the tile data to a local cache file. This will return quickly if the file is
  // already downloaded but will take some time if it needs to be fetched from the server.
  try {
    cacheFile = source->loadData(tileId, x, y);
  } catch (std::exception const& e) {
    // This is not critical, the planet will just not refine any further.
    logger().debug("Tile loading failed: {}", e.what());
    return false;
  }

  // Data is not available. That's most likely due to our server being offline.
  if (!cacheFile) {
    return false;
  }

  T* tileData = tile->getTypedPtr<T>();

  // Now the cache file is available, try to load it with libtiff if it's elevation data.
  if (tile->getDataType() == TileDataType::eElevation) {
    TIFFSetWarningHandler(nullptr);
    auto* data = TIFFOpen(cacheFile->c_str(), "r");
    if (!data) {

      // This is also not critical. Something went wrong - we will just remove the cache file and
      // will try to download it later again if it's requested once more.
      logger().debug("Tile loading failed: Removing invalid cache file '{}'.", *cacheFile);
      boost::filesystem::remove(*cacheFile);
      return false;
    }

    uint32_t resolution = tile->getResolution();

    // The elevation data can be read. For some patches (those at the international date boundary)
    // two requests are made. For those, only half of the pixels contain valid data (above or below
    // the diagonal).
    int imagelength{};
    if (TIFFGetField(data, TIFFTAG_IMAGELENGTH, &imagelength) == 0) {
      logger().debug("TIFFGetField failed: Removing invalid cache file '{}'.", *cacheFile);
      boost::filesystem::remove(*cacheFile);
      return false;
    }
    int tiffReturn{};
    for (int y = 0; y < imagelength; y++) {
      if (which == CopyPixels::eAll) {
        tiffReturn = TIFFReadScanline(data, &tileData[resolution * y], y);
      } else if (which == CopyPixels::eAboveDiagonal) {
        std::vector<float> tmp(resolution);
        tiffReturn = TIFFReadScanline(data, tmp.data(), y);
        int offset = resolution * y;
        int count  = resolution - y - 1;

        // NOLINTNEXTLINE(cppcoreguidelines-pro-bounds-pointer-arithmetic)
        std::memcpy(tileData + offset, tmp.data(), count * sizeof(float));
      } else if (which == CopyPixels::eBelowDiagonal) {
        std::vector<float> tmp(resolution);
        tiffReturn = TIFFReadScanline(data, tmp.data(), y);
        int offset = resolution * y + (resolution - y);
        int count  = y;

        // NOLINTNEXTLINE(cppcoreguidelines-pro-bounds-pointer-arithmetic)
        std::memcpy(tileData + offset, tmp.data() + resolution - y, count * sizeof(float));
      }
    }
    if (tiffReturn == -1) {
      logger().debug("TIFFReadScanline failed: Removing invalid cache file '{}'.", *cacheFile);
      boost::filesystem::remove(*cacheFile);
      return false;
    }

    TIFFClose(data);
  } else {

    // Image tiles are loaded with stbi.
    int width{};
    int height{};
    int bpp{};
    int channels = 4;

    auto* data =
        reinterpret_cast<T*>(stbi_load(cacheFile->c_str(), &width, &height, &bpp, channels));

    if (!data) {

      // This is also not critical. Something went wrong - we will just remove the cache file and
      // will try to download it later again if it's requested once more.
      logger().debug("Tile loading failed: Removing invalid cache file '{}'.", *cacheFile);
      boost::filesystem::remove(*cacheFile);
      return false;
    }

    // The image data can be read. For some patches (those at the international date boundary)
    // two requests are made. For those, only half of the pixels contain valid data (above or below
    // the diagonal).
    if (which == CopyPixels::eAll) {
      std::memcpy(tileData, data, channels * width * height);
    } else if (which == CopyPixels::eAboveDiagonal) {
      for (int y = 0; y < height; ++y) {
        int offset = width * y;
        int count  = channels * (width - y - 1);

        // NOLINTNEXTLINE(cppcoreguidelines-pro-bounds-pointer-arithmetic)
        std::memcpy(tileData + offset, data + offset, count);
      }
    } else if (which == CopyPixels::eBelowDiagonal) {
      for (int y = 0; y < height; ++y) {
        int offset = width * y + (width - y);
        int count  = channels * y;

        // NOLINTNEXTLINE(cppcoreguidelines-pro-bounds-pointer-arithmetic)
        std::memcpy(tileData + offset, data + offset, count);
      }
    }

    stbi_image_free(data);
  }

  return true;
}

bool QTiffHandler::read(QImage *image)
{
    if (!canRead())
        return false;

    TIFF *const tiff = TIFFClientOpen("foo",
                                      "r",
                                      this,
                                      qtiffReadProc,
                                      qtiffWriteProc,
                                      qtiffSeekProc,
                                      qtiffCloseProc,
                                      qtiffSizeProc,
                                      qtiffMapProc,
                                      qtiffUnmapProc);

    if (!tiff) {
        return false;
    }
    uint32 width;
    uint32 height;
    uint16 photometric;
    if (!TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width)
        || !TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height)
        || !TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC, &photometric)) {
        TIFFClose(tiff);
        return false;
    }

    uint16 bitPerSample;
    if (!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitPerSample)) {
        TIFFClose(tiff);
        return false;
    }

    bool grayscale = photometric == PHOTOMETRIC_MINISBLACK || photometric == PHOTOMETRIC_MINISWHITE;
    if (grayscale && bitPerSample == 1) {
        if (image->size() != QSize(width, height) || image->format() != QImage::Format_Mono)
            *image = QImage(width, height, QImage::Format_Mono);
        QVector<QRgb> colortable(2);
        if (photometric == PHOTOMETRIC_MINISBLACK) {
            colortable[0] = 0xff000000;
            colortable[1] = 0xffffffff;
        } else {
            colortable[0] = 0xffffffff;
            colortable[1] = 0xff000000;
        }
        image->setColorTable(colortable);

        if (!image->isNull()) {
            for (uint32 y=0; y<height; ++y) {
                if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                    TIFFClose(tiff);
                    return false;
                }
            }
        }
    } else {
        if ((grayscale || photometric == PHOTOMETRIC_PALETTE) && bitPerSample == 8) {
            if (image->size() != QSize(width, height) || image->format() != QImage::Format_Indexed8)
                *image = QImage(width, height, QImage::Format_Indexed8);
            if (!image->isNull()) {
                const uint16 tableSize = 256;
                QVector<QRgb> qtColorTable(tableSize);
                if (grayscale) {
                    for (int i = 0; i<tableSize; ++i) {
                        const int c = (photometric == PHOTOMETRIC_MINISBLACK) ? i : (255 - i);
                        qtColorTable[i] = qRgb(c, c, c);
                    }
                } else {
                    // create the color table
                    uint16 *redTable = static_cast<uint16 *>(qMalloc(tableSize * sizeof(uint16)));
                    uint16 *greenTable = static_cast<uint16 *>(qMalloc(tableSize * sizeof(uint16)));
                    uint16 *blueTable = static_cast<uint16 *>(qMalloc(tableSize * sizeof(uint16)));
                    if (!redTable || !greenTable || !blueTable) {
                        TIFFClose(tiff);
                        return false;
                    }
                    if (!TIFFGetField(tiff, TIFFTAG_COLORMAP, &redTable, &greenTable, &blueTable)) {
                        TIFFClose(tiff);
                        return false;
                    }

                    for (int i = 0; i<tableSize ;++i) {
                        const int red = redTable[i] / 257;
                        const int green = greenTable[i] / 257;
                        const int blue = blueTable[i] / 257;
                        qtColorTable[i] = qRgb(red, green, blue);
                    }
                }

                image->setColorTable(qtColorTable);
                for (uint32 y=0; y<height; ++y) {
                    if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                        TIFFClose(tiff);
                        return false;
                    }
                }

                // free redTable, greenTable and greenTable done by libtiff
            }
        } else {
            if (image->size() != QSize(width, height) || image->format() != QImage::Format_ARGB32)
                *image = QImage(width, height, QImage::Format_ARGB32);
            if (!image->isNull()) {
                const int stopOnError = 1;
                if (TIFFReadRGBAImageOriented(tiff, width, height, reinterpret_cast<uint32 *>(image->bits()), ORIENTATION_TOPLEFT, stopOnError)) {
                    for (uint32 y=0; y<height; ++y)
                        convert32BitOrder(image->scanLine(y), width);
                } else {
                    TIFFClose(tiff);
                    return false;
                }
            }
        }
    }

    if (image->isNull()) {
        TIFFClose(tiff);
        return false;
    }

    float resX = 0;
    float resY = 0;
    uint16 resUnit = RESUNIT_NONE;
    if (TIFFGetField(tiff, TIFFTAG_RESOLUTIONUNIT, &resUnit)
        && TIFFGetField(tiff, TIFFTAG_XRESOLUTION, &resX)
        && TIFFGetField(tiff, TIFFTAG_YRESOLUTION, &resY)) {

        switch(resUnit) {
        case RESUNIT_CENTIMETER:
            image->setDotsPerMeterX(qRound(resX * 100));
            image->setDotsPerMeterY(qRound(resY * 100));
            break;
        case RESUNIT_INCH:
            image->setDotsPerMeterX(qRound(resX * (100 / 2.54)));
            image->setDotsPerMeterY(qRound(resY * (100 / 2.54)));
            break;
        default:
            // do nothing as defaults have already
            // been set within the QImage class
            break;
        }
    }

    // rotate the image if the orientation is defined in the file
    uint16 orientationTag;
    if (TIFFGetField(tiff, TIFFTAG_ORIENTATION, &orientationTag)) {
        if (image->format() == QImage::Format_ARGB32) {
            // TIFFReadRGBAImageOriented() flip the image but does not rotate them
            switch (orientationTag) {
            case 5:
                rotate_right_mirror_horizontal(image);
                break;
            case 6:
                rotate_right_mirror_vertical(image);
                break;
            case 7:
                rotate_right_mirror_horizontal(image);
                break;
            case 8:
                rotate_right_mirror_vertical(image);
                break;
            }
        } else {
            switch (orientationTag) {
            case 1: // default orientation
                break;
            case 2: // mirror horizontal
                *image = image->mirrored(true, false);
                break;
            case 3: // mirror both
                *image = image->mirrored(true, true);
                break;
            case 4: // mirror vertical
                *image = image->mirrored(false, true);
                break;
            case 5: // rotate right mirror horizontal
                {
                    QMatrix transformation;
                    transformation.rotate(90);
                    *image = image->transformed(transformation);
                    *image = image->mirrored(true, false);
                    break;
                }
            case 6: // rotate right
                {
                    QMatrix transformation;
                    transformation.rotate(90);
                    *image = image->transformed(transformation);
                    break;
                }
            case 7: // rotate right, mirror vertical
                {
                    QMatrix transformation;
                    transformation.rotate(90);
                    *image = image->transformed(transformation);
                    *image = image->mirrored(false, true);
                    break;
                }
            case 8: // rotate left
                {
                    QMatrix transformation;
                    transformation.rotate(270);
                    *image = image->transformed(transformation);
                    break;
                }
            }
        }
    }


    TIFFClose(tiff);
    return true;
}

ICCProfile *readICCProfile(TIFF *tiff)
{
    unsigned iccProfileSize = 0;
    void *iccProfileData = Q_NULLPTR;
    if(TIFFGetField(tiff, TIFFTAG_ICCPROFILE, &iccProfileSize, &iccProfileData))
    {
        LOG_DEBUG() << LOGGING_CTX << "Found ICCP metadata (TIFFTAG_ICCPROFILE)";
        return new ICCProfile(QByteArray(reinterpret_cast<const char*>(iccProfileData), static_cast<int>(iccProfileSize)));
    }

    quint16 samplesPerPixel = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel))
        return Q_NULLPTR;

    quint16 bitsPerSample = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample))
        return Q_NULLPTR;

    float *whitePoint = Q_NULLPTR, *primaryChromaticities = Q_NULLPTR;
    unsigned short *transferFunctionRed = Q_NULLPTR, *transferFunctionGreen = Q_NULLPTR, *transferFunctionBlue = Q_NULLPTR;
    if(!TIFFGetField(tiff, TIFFTAG_WHITEPOINT, &whitePoint))
        whitePoint = Q_NULLPTR;
    if(!TIFFGetField(tiff, TIFFTAG_PRIMARYCHROMATICITIES, &primaryChromaticities))
        primaryChromaticities = Q_NULLPTR;
    if(samplesPerPixel == 1)
    {
        if(!TIFFGetField(tiff, TIFFTAG_TRANSFERFUNCTION, &transferFunctionRed, &transferFunctionGreen, &transferFunctionBlue))
            transferFunctionRed = transferFunctionGreen = transferFunctionBlue = Q_NULLPTR;
        else
            transferFunctionGreen = transferFunctionBlue = transferFunctionRed;
    }
    else
    {
        if(!TIFFGetField(tiff, TIFFTAG_TRANSFERFUNCTION, &transferFunctionRed, &transferFunctionGreen, &transferFunctionBlue))
            transferFunctionRed = transferFunctionGreen = transferFunctionBlue = Q_NULLPTR;
    }

    if(whitePoint || primaryChromaticities || transferFunctionRed || transferFunctionGreen || transferFunctionBlue)
    {
        LOG_DEBUG() << LOGGING_CTX << "Found ICCP metadata (TIFFTAG_WHITEPOINT + TIFFTAG_PRIMARYCHROMATICITIES + TIFFTAG_TRANSFERFUNCTION)";

        /// @note TIFF defaults (CIE D50) does not match sRGB defaults (CIE D65)
        if(!whitePoint && !TIFFGetFieldDefaulted(tiff, TIFFTAG_WHITEPOINT, &whitePoint))
            whitePoint = Q_NULLPTR;

        const size_t transferFunctionSize = (static_cast<std::size_t>(1) << bitsPerSample);
        return new ICCProfile(whitePoint, primaryChromaticities, transferFunctionRed, transferFunctionGreen, transferFunctionBlue, transferFunctionSize);
    }

    return Q_NULLPTR;
}

void addMetaData(TIFF *tiff, const TIFFField *field, ImageMetaData *metaData, const QString &group, const QString &tag)
{
    const int readCount = TIFFFieldReadCount(field);
    if(readCount == TIFF_VARIABLE2 || readCount == TIFF_VARIABLE || readCount > 1)
    {
        quint32 actualCount = 0;
        T *data;
        if(readCount == TIFF_VARIABLE)
        {
            quint16 gotCount = 0;
            if(!TIFFGetField(tiff, TIFFFieldTag(field), &gotCount, &data))
                return;
            actualCount = gotCount;
        }
        else if(readCount == TIFF_VARIABLE2)
        {
            quint32 gotCount = 0;
            if(!TIFFGetField(tiff, TIFFFieldTag(field), &gotCount, &data))
                return;
            actualCount = gotCount;
        }
        else
        {
            if(!TIFFGetField(tiff, TIFFFieldTag(field), &data))
                return;
            actualCount = readCount;
        }
        if(TIFFFieldDataType(field) == TIFF_UNDEFINED)
        {
            const char *charData = reinterpret_cast<const char*>(data);
            const int charSize = static_cast<int>(actualCount * sizeof(T) / sizeof(char));
            metaData->addExifEntry(group, TIFFFieldTag(field), tag, QString::fromLatin1(QByteArray(charData, charSize).toHex().prepend("0x")));
        }
        else
        {
            QStringList values;
            for(quint32 i = 0; i < actualCount; i++)
                values.append(QString::fromLatin1("%1").arg(data[i]));
            metaData->addExifEntry(group, TIFFFieldTag(field), tag, QString::fromLatin1("{ %1 }").arg(values.join(QString::fromLatin1(", "))));
        }
    }
    else if(readCount == 1)
    {
        T data;
        TIFFGetField(tiff, TIFFFieldTag(field), &data);
        metaData->addExifEntry(group, TIFFFieldTag(field), tag, QString::fromLatin1("%1").arg(data));
    }
}

void addMetaData<QString>(TIFF *tiff, const TIFFField *field, ImageMetaData *metaData, const QString &group, const QString &tag)
{
    if(TIFFFieldReadCount(field) <= 1)
        return;
    char *data = Q_NULLPTR;
    if(!TIFFGetField(tiff, TIFFFieldTag(field), &data) || !data)
        return;
    metaData->addExifEntry(group, TIFFFieldTag(field), tag, QString::fromUtf8(data));
}

void readTiffTagToMetaData(TIFF *tiff, ImageMetaData *&metaData, quint32 tag, const QString &tagDescription)
{
    const TIFFField *tagField = TIFFFindField(tiff, tag, TIFF_ANY);
    if(!tagField)
        return;
    if(TIFFFieldDataType(tagField) != TIFF_IFD8)
    {
        LOG_WARNING() << LOGGING_CTX << "TIFFFieldDataType for tag (" << tagDescription << ") is not TIFF_IFD8!";
        return;
    }
    quint64 exifOffset = 0;
    if(!TIFFGetField(tiff, tag, &exifOffset))
        return;
    if(!TIFFReadEXIFDirectory(tiff, exifOffset))
        return;
    LOG_DEBUG() << LOGGING_CTX << "Found EXIF metadata (" << tagDescription << ")";
    if(!metaData)
        metaData = new ImageMetaData;
    for(int i = 0, tagListCount = TIFFGetTagListCount(tiff); i < tagListCount; i++)
    {
        ttag_t tag = TIFFGetTagListEntry(tiff, i);
        const TIFFField *field = TIFFFieldWithTag(tiff, tag);
        const QString exifName = QString::fromUtf8(TIFFFieldName(field));
        /// @note See _TIFFVGetField in tif_dir.c
        switch(TIFFFieldDataType(field))
        {
        case TIFF_BYTE:
        case TIFF_UNDEFINED:
            addMetaData<quint8>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_ASCII:
            addMetaData<QString>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SHORT:
            addMetaData<quint16>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_LONG:
            addMetaData<quint32>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SBYTE:
            addMetaData<qint8>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SSHORT:
            addMetaData<qint16>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SLONG:
            addMetaData<qint32>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SRATIONAL:
        case TIFF_RATIONAL:
        case TIFF_FLOAT:
            addMetaData<float>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_DOUBLE:
            addMetaData<double>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_LONG8:
            addMetaData<quint64>(tiff, field, metaData, tagDescription, exifName);
            break;
        case TIFF_SLONG8:
            addMetaData<qint64>(tiff, field, metaData, tagDescription, exifName);
            break;
        default:
            break;
        }
    }
}

PayloadWithMetaData<QImage> readTiffFile(const QString &filename)
{
    QFile inFile(filename);
    if(!inFile.open(QIODevice::ReadOnly))
    {
        LOG_WARNING() << LOGGING_CTX << "Can't open" << filename;
        return QImage();
    }

#if defined (TIFFLIB_VERSION) && (TIFFLIB_VERSION >= 20221213) && (QT_VERSION >= QT_VERSION_CHECK(5, 5, 0))
    TIFFOpenOptions *opts = TIFFOpenOptionsAlloc();
    TIFFOpenOptionsSetErrorHandlerExtR(opts, errorHandlerProc, Q_NULLPTR);
    TIFFOpenOptionsSetWarningHandlerExtR(opts, &warningHandlerProc, Q_NULLPTR);
    TIFF *tiff = TIFFClientOpenExt("DecoderLibTiff", "r", &inFile, readProc, writeProc, seekProc, closeProc, sizeProc, mapProc, unmapProc, opts);
    TIFFOpenOptionsFree(opts);
#else
    TIFF *tiff = TIFFClientOpen("DecoderLibTiff", "r", &inFile, readProc, writeProc, seekProc, closeProc, sizeProc, mapProc, unmapProc);
#endif
    if(!tiff)
    {
        LOG_WARNING() << LOGGING_CTX << "Can't TIFFClientOpen for" << filename;
        return QImage();
    }

    if(!TIFFSetDirectory(tiff, 0))
    {
        LOG_WARNING() << LOGGING_CTX << "Can't TIFFSetDirectory for" << filename;
        TIFFClose(tiff);
        return QImage();
    }

    quint32 width = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_IMAGEWIDTH, &width) || width <= 0)
    {
        LOG_WARNING() << LOGGING_CTX << "Can't get TIFFTAG_IMAGEWIDTH for" << filename;
        TIFFClose(tiff);
        return QImage();
    }

    quint32 height = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_IMAGELENGTH, &height) || height <= 0)
    {
        LOG_WARNING() << LOGGING_CTX << "Can't get TIFFTAG_IMAGELENGTH for" << filename;
        TIFFClose(tiff);
        return QImage();
    }

    quint16 compression = 0;
    TIFFGetFieldDefaulted(tiff, TIFFTAG_COMPRESSION, &compression);

    /// @todo LibTIFF-4.6.0: Looks like samples layout for subsampled YCbCr data
    /// for JPEG compression is differ from samples layout for other compression
    /// types. So we will enforce built-in YCbCr to RGB converter. Despite tag
    /// name, non-YCbCr colorspaces like CMYK or RGB are not affected
    if(compression == COMPRESSION_JPEG)
        TIFFSetField(tiff, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);

    quint16 samplesPerPixel = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel) || samplesPerPixel <= 0)
        samplesPerPixel = 1;

    int alphaIndex = -1;
    quint16 extrasamplesCount = 0;
    const quint16 *extrasamples = Q_NULLPTR;
    if(TIFFGetFieldDefaulted(tiff, TIFFTAG_EXTRASAMPLES, &extrasamplesCount, &extrasamples))
    {
        for(quint16 i = 0; i < extrasamplesCount && alphaIndex < 0; ++i)
        {
            if(extrasamples[i] == EXTRASAMPLE_ASSOCALPHA)
                alphaIndex = static_cast<int>(i);
        }
        for(quint16 i = 0; i < extrasamplesCount && alphaIndex < 0; ++i)
        {
            if(extrasamples[i] == EXTRASAMPLE_UNASSALPHA)
                alphaIndex = static_cast<int>(i);
        }
    }
    const quint16 primarysamplesCount = samplesPerPixel > extrasamplesCount ? samplesPerPixel - extrasamplesCount : 0;

    quint16 photometric = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_PHOTOMETRIC, &photometric))
    {
        LOG_WARNING() << LOGGING_CTX << "Can't get TIFFTAG_PHOTOMETRIC for" << filename;
        if(primarysamplesCount == 1)
        {
            photometric = PHOTOMETRIC_MINISWHITE;
            LOG_WARNING() << LOGGING_CTX << "Assuming PHOTOMETRIC_MINISWHITE";
        }
        else if(primarysamplesCount == 3)
        {
            photometric = PHOTOMETRIC_RGB;
            LOG_WARNING() << LOGGING_CTX << "Assuming PHOTOMETRIC_RGB";
        }
        else
        {
            TIFFClose(tiff);
            return QImage();
        }
    }

    /// @note Enable converting PHOTOMETRIC_LOGL and PHOTOMETRIC_LOGLUV to 8 bit
    /// RGB and grayscale values respective
    if(IsOneOf(photometric, PHOTOMETRIC_LOGL, PHOTOMETRIC_LOGLUV))
        TIFFSetField(tiff, TIFFTAG_SGILOGDATAFMT, SGILOGDATAFMT_8BIT);

    quint16 orientation = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_ORIENTATION, &orientation) || orientation <= 0)
        orientation = ORIENTATION_TOPLEFT;

    quint16 bitsPerSample = 0;
    if(!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitsPerSample) || bitsPerSample <= 0)
        bitsPerSample = 1;

    quint16 sampleFormat = 0;
    if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_SAMPLEFORMAT, &sampleFormat) || sampleFormat <= 0)
        sampleFormat = SAMPLEFORMAT_VOID;

    quint16 inkSet = 0;
    if(photometric == PHOTOMETRIC_SEPARATED)
    {
        if(!TIFFGetFieldDefaulted(tiff, TIFFTAG_INKSET, &inkSet) || inkSet <= 0)
            inkSet = INKSET_CMYK;
    }

    quint16 planarConfig = 0;
    TIFFGetField(tiff, TIFFTAG_PLANARCONFIG, &planarConfig);

    const float *ycbcrcoeffs = Q_NULLPTR;
    TIFFGetFieldDefaulted(tiff, TIFFTAG_YCBCRCOEFFICIENTS, &ycbcrcoeffs);

    quint16 subsamplinghor = 0, subsamplingver = 0;
    TIFFGetFieldDefaulted(tiff, TIFFTAG_YCBCRSUBSAMPLING, &subsamplinghor, &subsamplingver);

    quint16 ycbcrpositioning = 0;
    TIFFGetFieldDefaulted(tiff, TIFFTAG_YCBCRPOSITIONING, &ycbcrpositioning);

    const quint16 *redTable = Q_NULLPTR;
    const quint16 *greenTable = Q_NULLPTR;
    const quint16 *blueTable = Q_NULLPTR;
    TIFFGetFieldDefaulted(tiff, TIFFTAG_COLORMAP, &redTable, &greenTable, &blueTable);

    QScopedPointer<ICCProfile> iccProfile(readICCProfile(tiff));
    if((!iccProfile || !iccProfile->isValid()) && photometric == PHOTOMETRIC_SEPARATED && inkSet == INKSET_CMYK)
        iccProfile.reset(new ICCProfile(ICCProfile::defaultCmykProfileData()));

    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_IMAGEWIDTH =" << width;
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_IMAGELENGTH =" << height;
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_PHOTOMETRIC =" << photometricToString(photometric).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_INKSET =" << inkSetToString(inkSet).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_ORIENTATION =" << orientationToString(orientation).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_BITSPERSAMPLE =" << bitsPerSample;
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_SAMPLESPERPIXEL =" << samplesPerPixel;
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_SAMPLEFORMAT =" << sampleFormatToString(sampleFormat).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_EXTRASAMPLES =" << extrasamplesToString(extrasamplesCount, extrasamples).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_PLANARCONFIG =" << planarConfigToString(planarConfig).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_COMPRESSION =" << compressionToString(compression).toLatin1().data();
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_YCBCRSUBSAMPLING =" << subsamplinghor << subsamplingver;
    LOG_DEBUG() << LOGGING_CTX << "TIFFTAG_YCBCRPOSITIONING =" << ycbcrpositioningToString(ycbcrpositioning).toLatin1().data();

    const bool isSupportedInt = IsOneOf(sampleFormat, SAMPLEFORMAT_UINT, SAMPLEFORMAT_VOID, SAMPLEFORMAT_INT) && bitsPerSample <= 64;
    const bool isSupportedFp = sampleFormat == SAMPLEFORMAT_IEEEFP && IsOneOf(bitsPerSample, 16, 24, 32, 64);
    bool isReadingSupported = false;
    switch(photometric)
    {
    case PHOTOMETRIC_MINISWHITE:
    case PHOTOMETRIC_MINISBLACK:
    case PHOTOMETRIC_LOGL:
        isReadingSupported = (isSupportedInt || isSupportedFp) && primarysamplesCount >= 1;
        break;
    case PHOTOMETRIC_RGB:
    case PHOTOMETRIC_YCBCR:
    case PHOTOMETRIC_LOGLUV:
        isReadingSupported = (isSupportedInt || isSupportedFp) && primarysamplesCount >= 3;
        break;
    case PHOTOMETRIC_SEPARATED:
        isReadingSupported = (isSupportedInt || isSupportedFp) && primarysamplesCount >= 4;
        break;
    case PHOTOMETRIC_CIELAB:
    case PHOTOMETRIC_ICCLAB:
    case PHOTOMETRIC_ITULAB:
        isReadingSupported = (isSupportedInt || isSupportedFp) && primarysamplesCount >= 1;
        break;
    case PHOTOMETRIC_PALETTE:
        isReadingSupported = redTable && greenTable && blueTable && bitsPerSample <= 16 && primarysamplesCount >= 1;
        break;
    // case PHOTOMETRIC_MASK:
    // case PHOTOMETRIC_CFA:
    default:
        break;
    }

    QImage result;
    if(isReadingSupported)
    {
        Context ctx;
        memset(&ctx, 0, sizeof(ctx));
        ctx.tiff = tiff;
        ctx.width = static_cast<qint64>(width);
        ctx.height = static_cast<qint64>(height);
        ctx.compression = compression;
        ctx.photometric = compression == COMPRESSION_JPEG && photometric == PHOTOMETRIC_YCBCR ? PHOTOMETRIC_RGB : photometric;
        ctx.samplesPerPixel = static_cast<qint64>(samplesPerPixel);
        ctx.bitsPerSample = static_cast<qint64>(bitsPerSample);
        ctx.sampleFormat = sampleFormat;
        ctx.inkSet = inkSet;
        ctx.iccProfile = iccProfile.data();
        ctx.ycbcrcoeffs = ycbcrcoeffs;
        ctx.subsamplinghor = static_cast<qint64>(subsamplinghor);
        ctx.subsamplingver = static_cast<qint64>(subsamplingver);
        ctx.ycbcrpositioning = ycbcrpositioning;
        ctx.extrasamplesCount = static_cast<qint64>(extrasamplesCount);
        ctx.primarysamplesCount = static_cast<qint64>(primarysamplesCount);
        ctx.alphaIndex = alphaIndex;
        ctx.alphaPremultiplied = alphaIndex >= 0 ? extrasamples[alphaIndex] == EXTRASAMPLE_ASSOCALPHA : false;
        ctx.redTable = redTable;
        ctx.greenTable = greenTable;
        ctx.blueTable = blueTable;
        ctx.colorTablesIs16Bit = false;

        if(photometric == PHOTOMETRIC_PALETTE && redTable && greenTable && blueTable)
        {
            /// @note See buildMap and checkcmap in tif_getimage.c
            for(quint64 i = 0, count = (1ull << bitsPerSample); i < count && !ctx.colorTablesIs16Bit; ++i)
                if(redTable[i] >= 256 || greenTable[i] >= 256 || blueTable[i] >= 256)
                    ctx.colorTablesIs16Bit = true;
        }

        if(TIFFIsTiled(tiff))
            result = readTiffFileTiled(&ctx);
        else
            result = readTiffFileStriped(&ctx);
    }

    /// @todo LibTIFF-4.6.0: TIFFRGBA* has bad errors handling and reports OK
    /// sometimes even if no data was decoded. So we will disable it for all
    /// supported formats
    if(result.isNull() && !isReadingSupported)
    {
        TIFFRGBAImage img;
        char emsg[1024];
        if(!TIFFRGBAImageBegin(&img, tiff, 0, emsg))
        {
            LOG_WARNING() << LOGGING_CTX << "Can't TIFFRGBAImageBegin for" << filename;
            LOG_WARNING() << LOGGING_CTX << "Reason:" << emsg;
            TIFFClose(tiff);
            return QImage();
        }

        result = QImage(static_cast<int>(img.width), static_cast<int>(img.height),
#if (USE_RGBA_8888)
                      QImage::Format_RGBA8888);
#else
                      QImage::Format_ARGB32);
#endif
        if(result.isNull())
        {
            LOG_WARNING() << LOGGING_CTX << "Invalid image size";
            TIFFClose(tiff);
            return QImage();
        }

        img.req_orientation = img.orientation;

#if defined (TIFFLIB_VERSION) && (TIFFLIB_VERSION >= 20210416)
        typedef uint32_t TiffImageBitsType;
#else
        typedef uint32 TiffImageBitsType;
#endif

        if(!TIFFRGBAImageGet(&img, reinterpret_cast<TiffImageBitsType*>(result.bits()), img.width, img.height))
        {
            LOG_WARNING() << LOGGING_CTX << "Can't TIFFRGBAImageGet for" << filename;
            TIFFClose(tiff);
            return QImage();
        }

#if (!USE_RGBA_8888)
        QImage_rgbSwap(result);
#endif

#undef USE_RGBA_8888

        TIFFRGBAImageEnd(&img);

        if(iccProfile)
            iccProfile->applyToImage(&result);
    }

    // Some image formats can't be rendered successfully
    if(!IsOneOf(result.format(), QImage::Format_RGB32, QImage::Format_ARGB32))
        QImage_convertTo(result, result.hasAlphaChannel() ? QImage::Format_ARGB32 : QImage::Format_RGB32);

    ImageMetaData *metaData = ImageMetaData::createMetaData(filename);
    if(!metaData)
        metaData = readExifMetaData(tiff);
    if(!metaData)
        metaData = new ImageMetaData;

    metaData->addCustomOrientation(orientation);
    metaData->applyExifOrientation(&result);

    quint16 resUnit = 0;
    if(!TIFFGetField(tiff, TIFFTAG_RESOLUTIONUNIT, &resUnit))
        resUnit = RESUNIT_INCH;
    float resX = 0.0f, resY = 0.0f;
    if(TIFFGetField(tiff, TIFFTAG_XRESOLUTION, &resX) && TIFFGetField(tiff, TIFFTAG_YRESOLUTION, &resY))
    {
        if(resUnit == RESUNIT_CENTIMETER)
            metaData->addCustomDpi(static_cast<qreal>(resX * 2.54f), static_cast<qreal>(resY * 2.54f));
        else if(resUnit == RESUNIT_INCH)
            metaData->addCustomDpi(static_cast<qreal>(resX), static_cast<qreal>(resY));
    }

    TIFFClose(tiff);

    return PayloadWithMetaData<QImage>(result, metaData);
}

uint8_t isImageJIm(const char* fileName){
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) return 0;
    char* tiffDesc = NULL;
    if(TIFFGetField(tif, TIFFTAG_IMAGEDESCRIPTION, &tiffDesc)){
        if(strstr(tiffDesc, "ImageJ")){
            uint64_t* size = getImageSize(fileName);
            if(size[2] > 1){
                if(TIFFSetDirectory(tif,1)){
                    free(size);
                    return 0;
                }
            }
            free(size);
            uint16_t compressed = 1;
            TIFFGetField(tif, TIFFTAG_COMPRESSION, &compressed);
            TIFFClose(tif);
            if(compressed != 1) return 0;
            else return 1;
        }
    }
    TIFFClose(tif);
    return 0;
}

uint64_t imageJImGetZ(const char* fileName){
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) return 0;
    char* tiffDesc = NULL;
    if(TIFFGetField(tif, TIFFTAG_IMAGEDESCRIPTION, &tiffDesc)){
        if(strstr(tiffDesc, "ImageJ")){
            char* nZ = strstr(tiffDesc,"images=");
            if(nZ){
                TIFFClose(tif);
                nZ+=7;
                char* temp;
                return strtol(nZ,&temp,10);
            }
        }
    }
    TIFFClose(tif);
    return 0;
}

uint64_t* getImageSize(const char* fileName){
    TIFFSetWarningHandler(DummyHandler);
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) printf("File \"%s\" cannot be opened",fileName);

    uint64_t x = 1,y = 1,z = 1;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &x);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &y);
    z = getImageSizeZ(fileName);
    TIFFClose(tif);

    uint64_t* dims = (uint64_t*)malloc(3*sizeof(uint64_t));
    dims[0] = y;
    dims[1] = x;
    dims[2] = z;
    return dims;
}

uint64_t getDataType(const char* fileName){
    TIFFSetWarningHandler(DummyHandler);
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) printf("File \"%s\" cannot be opened",fileName);

    uint64_t bits = 1;
    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits);
    TIFFClose(tif);

    return bits;
}

uint8_t readTiffParallel2D(uint64_t x, uint64_t y, uint64_t z, const char* fileName, void* tiff, uint64_t bits, uint64_t startSlice, uint64_t stripSize, uint8_t flipXY){
    int32_t numWorkers = omp_get_max_threads();
    uint64_t stripsPerDir = (uint64_t)ceil((double)y/(double)stripSize);
    int32_t batchSize = (stripsPerDir-1)/numWorkers+1;
    uint64_t bytes = bits/8;

    int32_t w;
    uint8_t err = 0;
    uint8_t errBak = 0;
    char errString[10000];
    uint16_t compressed = 1;
    TIFF* tif = TIFFOpen(fileName, "r");
    TIFFGetField(tif, TIFFTAG_COMPRESSION, &compressed);

    // The other method won't work on specific slices of 3D images for now
    // so start slice must also be 0
    if(numWorkers > 1 || compressed > 1){
        #pragma omp parallel for
        for(w = 0; w < numWorkers; w++){

            uint8_t outCounter = 0;
            TIFF* tif = TIFFOpen(fileName, "r");
            while(!tif){
                tif = TIFFOpen(fileName, "r");
                if(outCounter == 3){
                    #pragma omp critical
                    {
                        err = 1;
                        sprintf(errString,"Thread %d: File \"%s\" cannot be opened\n",w,fileName);
                    }
                    continue;
                }
                outCounter++;
            }

            void* buffer = malloc(x*stripSize*bytes);


            uint8_t counter = 0;
            while(!TIFFSetDirectory(tif, startSlice) && counter<3){
                printf("Thread %d: File \"%s\" Directory \"%d\" failed to open. Try %d\n",w,fileName,0,counter+1);
                counter++;
                if(counter == 3){
                    #pragma omp critical
                    {
                        err = 1;
                        sprintf(errString,"Thread %d: File \"%s\" cannot be opened\n",w,fileName);
                    }
                }
            }
            for (int64_t i = (w*batchSize); i < (w+1)*batchSize; i++)
            {
                if(i*stripSize >= y || err) break;
                //loading the data into a buffer
                int64_t cBytes = TIFFReadEncodedStrip(tif, i, buffer, stripSize*x*bytes);
                if(cBytes < 0){
                    #pragma omp critical
                    {
                        //errBak = 1;
                        err = 1;
                        sprintf(errString,"Thread %d: Strip %ld cannot be read\n",w,i);
                    }
                    break;
                }
                if(!flipXY){
                    memcpy(tiff+((i*stripSize*x)*bytes),buffer,cBytes);
                    continue;
                }
                switch(bits){
                    case 8:
                        // Map Values to flip x and y for MATLAB
                        for(int64_t k = 0; k < stripSize; k++){
                            if((k+(i*stripSize)) >= y) break;
                            for(int64_t j = 0; j < x; j++){
                                ((uint8_t*)tiff)[((j*y)+(k+(i*stripSize)))] = ((uint8_t*)buffer)[j+(k*x)];
                            }
                        }
                                break;
                    case 16:
                        // Map Values to flip x and y for MATLAB
                        for(int64_t k = 0; k < stripSize; k++){
                            if((k+(i*stripSize)) >= y) break;
                            for(int64_t j = 0; j < x; j++){
                                ((uint16_t*)tiff)[((j*y)+(k+(i*stripSize)))] = ((uint16_t*)buffer)[j+(k*x)];
                            }
                        }
                                break;
                    case 32:
                        // Map Values to flip x and y for MATLAB
                        for(int64_t k = 0; k < stripSize; k++){
                            if((k+(i*stripSize)) >= y) break;
                            for(int64_t j = 0; j < x; j++){
                                ((float*)tiff)[((j*y)+(k+(i*stripSize)))] = ((float*)buffer)[j+(k*x)];
                            }
                        }
                                break;
                    case 64:
                        // Map Values to flip x and y for MATLAB
                        for(int64_t k = 0; k < stripSize; k++){
                            if((k+(i*stripSize)) >= y) break;
                            for(int64_t j = 0; j < x; j++){
                                ((double*)tiff)[((j*y)+(k+(i*stripSize)))] = ((double*)buffer)[j+(k*x)];
                            }
                        }
                                break;
                }
            }
            free(buffer);
            TIFFClose(tif);
        }
    }
    else{
        void* tiffC = NULL;
        FILE *fp = fopen(fileName, "rb");
        if(!fp){ 
			printf("File \"%s\" cannot be opened from Disk\n",fileName);
			err = 1;
			return err;
		}

        if(!tif){ 
			printf("File \"%s\" cannot be opened\n",fileName);
			err = 1;
			return err;
		}

		uint64_t offset = 0;
        uint64_t* offsets = NULL;
        TIFFGetField(tif, TIFFTAG_STRIPOFFSETS, &offsets);
        if(!offsets){ 
			printf("Could not get offsets from the tiff file\n");
       		err = 1;
			return err;
		}
		offset = offsets[0];
        uint64_t zSize = x*y*bytes;

        fseek(fp, offset, SEEK_SET);


        TIFFClose(tif);

        if(!flipXY){
            fread(tiff, 1, zSize, fp);
        }
        else{
            uint64_t size = x*y*z*(bits/8);
            tiffC = malloc(size);
            fread(tiffC, 1, zSize, fp);
        }
        fclose(fp);
        if(flipXY){   
            for(uint64_t k = 0; k < z; k++){
                for(uint64_t j = 0; j < x; j++){
                    for(uint64_t i = 0; i < y; i++){
                        switch(bits){
                            case 8:
                                ((uint8_t*)tiff)[i+(j*y)+(k*x*y)] = ((uint8_t*)tiffC)[j+(i*x)+(k*x*y)];
                                break;
                            case 16:
                                ((uint16_t*)tiff)[i+(j*y)+(k*x*y)] = ((uint16_t*)tiffC)[j+(i*x)+(k*x*y)];
                                break;
                            case 32:
                                ((float*)tiff)[i+(j*y)+(k*x*y)] = ((float*)tiffC)[j+(i*x)+(k*x*y)];
                                break;
                            case 64:
                                ((double*)tiff)[i+(j*y)+(k*x*y)] = ((double*)tiffC)[j+(i*x)+(k*x*y)];
                                break;
                        }
                    }
                }
            }
            free(tiffC);
        }
    }

    if(err) {
        if(errBak) return readTiffParallel2DBak(x, y, z, fileName, tiff, bits, startSlice, flipXY);
        else printf(errString);
    }
	return err;
}

uint8_t readTiffParallelImageJ(uint64_t x, uint64_t y, uint64_t z, const char* fileName, void* tiff, uint64_t bits, uint64_t startSlice, uint64_t stripSize, uint8_t flipXY){
    uint8_t err = 0;
    FILE *fp = fopen(fileName, "rb");
    if(!fp){ 
		printf("File \"%s\" cannot be opened from Disk\n",fileName);
		err = 1;
		return err;
	}
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif){ 
		printf("File \"%s\" cannot be opened\n",fileName);
		err = 1;
		return err;
	}
    uint64_t offset = 0;
    uint64_t* offsets = NULL;
    TIFFGetField(tif, TIFFTAG_STRIPOFFSETS, &offsets);
    if(offsets) offset = offsets[0];

    TIFFClose(tif);

    fseek(fp, offset, SEEK_SET);

    uint64_t bytes = bits/8;
    //#pragma omp parallel for
    /*
    for(uint64_t i = 0; i < z; i++){
    uint64_t cOffset = x*y*bytes*i;
    //pread(fd,tiff+cOffset,x*y*bytes,offset+cOffset);
    read(fd,tiff+cOffset,x*y*bytes);
    }*/
    uint64_t chunk = 0;
    uint64_t tBytes = x*y*z*bytes;
    uint64_t bytesRead;
    uint64_t rBytes = tBytes;

    // Can probably read more than INT_MAX now that we use fread
    if(tBytes < INT_MAX) bytesRead = fread(tiff,1,tBytes,fp);
    else{
        while(chunk < tBytes){
            rBytes = tBytes-chunk;
            if(rBytes > INT_MAX) bytesRead = fread(tiff+chunk,1,INT_MAX,fp);
            else bytesRead = fread(tiff+chunk,1,rBytes,fp);
            chunk += bytesRead;
        }
    }
    fclose(fp);
    // Swap endianess for types greater than 8 bits
    // TODO: May need to change later because we may not always need to swap
    if(bits > 8){
        #pragma omp parallel for
        for(uint64_t i = 0; i < x*y*z; i++){
            switch(bits){
                case 16:
                    //((uint16_t*)tiff)[i] = ((((uint16_t*)tiff)[i] & 0xff) >> 8) | (((uint16_t*)tiff)[i] << 8);
                    //((uint16_t*)tiff)[i] = bswap_16(((uint16_t*)tiff)[i]);
                    ((uint16_t*)tiff)[i] = ((((uint16_t*)tiff)[i] << 8) & 0xff00) | ((((uint16_t*)tiff)[i] >> 8) & 0x00ff);
                    break;
                case 32:
                    //((num & 0xff000000) >> 24) | ((num & 0x00ff0000) >> 8) | ((num & 0x0000ff00) << 8) | (num << 24)
                    //((float*)tiff)[i] = bswap_32(((float*)tiff)[i]);
                    ((uint32_t*)tiff)[i] = ((((uint32_t*)tiff)[i] << 24) & 0xff000000 ) |
                        ((((uint32_t*)tiff)[i] <<  8) & 0x00ff0000 ) |
                        ((((uint32_t*)tiff)[i] >>  8) & 0x0000ff00 ) |
                        ((((uint32_t*)tiff)[i] >> 24) & 0x000000ff );
                    break;
                case 64:
                    //((double*)tiff)[i] = bswap_64(((double*)tiff)[i]);
                    ((uint64_t*)tiff)[i] = ( (((uint64_t*)tiff)[i] << 56) & 0xff00000000000000UL ) |
                        ( (((uint64_t*)tiff)[i] << 40) & 0x00ff000000000000UL ) |
                        ( (((uint64_t*)tiff)[i] << 24) & 0x0000ff0000000000UL ) |
                        ( (((uint64_t*)tiff)[i] <<  8) & 0x000000ff00000000UL ) |
                        ( (((uint64_t*)tiff)[i] >>  8) & 0x00000000ff000000UL ) |
                        ( (((uint64_t*)tiff)[i] >> 24) & 0x0000000000ff0000UL ) |
                        ( (((uint64_t*)tiff)[i] >> 40) & 0x000000000000ff00UL ) |
                        ( (((uint64_t*)tiff)[i] >> 56) & 0x00000000000000ffUL );
                    break;
            }

        }
    }
    // Find a way to do this in-place without making a copy
    if(flipXY){
        uint64_t size = x*y*z*(bits/8);
        void* tiffC = malloc(size);
        memcpy(tiffC,tiff,size);
        #pragma omp parallel for
        for(uint64_t k = 0; k < z; k++){
            for(uint64_t j = 0; j < x; j++){
                for(uint64_t i = 0; i < y; i++){
                    switch(bits){
                        case 8:
                            ((uint8_t*)tiff)[i+(j*y)+(k*x*y)] = ((uint8_t*)tiffC)[j+(i*x)+(k*x*y)];
                            break;
                        case 16:
                            ((uint16_t*)tiff)[i+(j*y)+(k*x*y)] = ((uint16_t*)tiffC)[j+(i*x)+(k*x*y)];
                            break;
                        case 32:
                            ((float*)tiff)[i+(j*y)+(k*x*y)] = ((float*)tiffC)[j+(i*x)+(k*x*y)];
                            break;
                        case 64:
                            ((double*)tiff)[i+(j*y)+(k*x*y)] = ((double*)tiffC)[j+(i*x)+(k*x*y)];
                            break;
                    }
                }
            }
        }
        free(tiffC);
    }
	return err;
}

void* readTiffParallelWrapperHelper(const char* fileName, void* tiff, uint8_t flipXY)
{
	TIFFSetWarningHandler(DummyHandler);
	TIFF* tif = TIFFOpen(fileName, "r");
	if(!tif) return NULL;

	uint64_t x = 1,y = 1,z = 1,bits = 1, startSlice = 0;
	TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &x);
	TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &y);
    z = getImageSizeZ(fileName);

	TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits);
	uint64_t stripSize = 1;
	TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &stripSize);
	TIFFClose(tif);

	// Check if image is an imagej image with imagej metadata
	// Get the correct
	uint8_t imageJIm = 0;
	if(isImageJIm(fileName)){
		imageJIm = 1;
		uint64_t tempZ = imageJImGetZ(fileName);
		if(tempZ) z = tempZ;
	}


	if(imageJIm){
		if(bits == 8){
			if(!tiff) tiff = (uint8_t*)malloc(x*y*z*sizeof(uint8_t));
			readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize,flipXY);
			return (void*)tiff;
		}
		else if(bits == 16){
			if(!tiff) tiff = (uint16_t*)malloc(x*y*z*sizeof(uint16_t));
			readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 32){
			if(!tiff) tiff = (float*)malloc(x*y*z*sizeof(float));
			readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 64){
			if(!tiff) tiff = (double*)malloc(x*y*z*sizeof(double));
			readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else{
			return NULL;
		}
	}
	else if(z <= 1){
		if(bits == 8){
			if(!tiff) tiff = (uint8_t*)malloc(x*y*z*sizeof(uint8_t));
			readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize,flipXY);
			return (void*)tiff;
		}
		else if(bits == 16){
			if(!tiff) tiff = (uint16_t*)malloc(x*y*z*sizeof(uint16_t));
			readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 32){
			if(!tiff) tiff = (float*)malloc(x*y*z*sizeof(float));
			readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 64){
			if(!tiff) tiff = (double*)malloc(x*y*z*sizeof(double));
			readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else{
			return NULL;
		}
	}
	else{
		if(bits == 8){
			if(!tiff) tiff = (uint8_t*)malloc(x*y*z*sizeof(uint8_t));
			readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 16){
			if(!tiff) tiff = (uint16_t*)malloc(x*y*z*sizeof(uint16_t));
			readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 32){
			if(!tiff) tiff = (float*)malloc(x*y*z*sizeof(float));
			readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else if(bits == 64){
			if(!tiff) tiff = (double*)malloc(x*y*z*sizeof(double));
			readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
			return (void*)tiff;
		}
		else{
			return NULL;
		}
	}

	// Should never get here but return NULL if we do
	return NULL;
}

void mexFunction(int nlhs, mxArray *plhs[],
        int nrhs, const mxArray *prhs[])
{
    if(nrhs != 1) mexErrMsgIdAndTxt("tiff:inputError","This function requires one argument only");
    // Check if the fileName is a char array or matlab style
    char* fileName = NULL;
    if(!mxIsClass(prhs[0], "string")){
        if(!mxIsChar(prhs[0])) mexErrMsgIdAndTxt("tiff:inputError","The first argument must be a string");
        fileName = mxArrayToString(prhs[0]);
    }
    else{ 
        mxArray* mString[1];
        mxArray* mCharA[1];

        // Convert string to char array
        mString[0] = mxDuplicateArray(prhs[0]);
        mexCallMATLAB(1, mCharA, 1, mString, "char");
        fileName = mxArrayToString(mCharA[0]);
    }

    // Handle the tilde character in filenames on Linux/Mac
    #ifndef _WIN32
    if(strchr(fileName,'~')) fileName = expandTilde(fileName);
    #endif

    TIFFSetWarningHandler(DummyHandler);
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) mexErrMsgIdAndTxt("tiff:inputError","File \"%s\" cannot be opened",fileName);

    uint64_t x = 1,y = 1,z = 1;    
    if(nrhs == 1){
        TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &x);
        TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &y);
        z = getImageSizeZ(fileName);
    }
    else{
        mexErrMsgIdAndTxt("tiff:inputError","Function only accepts one input argument");       
    }

    TIFFClose(tif);
	if(isImageJIm(fileName)){
		uint64_t tempZ = imageJImGetZ(fileName);
		if(tempZ) z = tempZ;
	}

    plhs[0] = mxCreateNumericMatrix(1,3,mxDOUBLE_CLASS, mxREAL);
    double* dims = (double*)mxGetPr(plhs[0]);
    dims[0] = y;
    dims[1] = x;
    dims[2] = z;

}

void mexFunction(int nlhs, mxArray *plhs[],
                 int nrhs, const mxArray *prhs[])
{
    if(nrhs < 1 || nrhs > 2) mexErrMsgIdAndTxt("tiff:inputError","This function takes one or two arguments only");
    // Check if the fileName is a char array or matlab style
    char* fileName = NULL;
    if(!mxIsClass(prhs[0], "string")){
        if(!mxIsChar(prhs[0])) mexErrMsgIdAndTxt("tiff:inputError","The first argument must be a string");
        fileName = mxArrayToString(prhs[0]);
    }
    else{ 
        mxArray* mString[1];
        mxArray* mCharA[1];

        // Convert string to char array
        mString[0] = mxDuplicateArray(prhs[0]);
        mexCallMATLAB(1, mCharA, 1, mString, "char");
        fileName = mxArrayToString(mCharA[0]);
    }

    // Handle the tilde character in filenames on Linux/Mac
    #ifndef _WIN32
    if(strchr(fileName,'~')) fileName = expandTilde(fileName);
    #endif

    uint8_t flipXY = 1;
    //uint8_t flipXY = 0;


    //if(nrhs > 2){
    //    flipXY = (uint8_t)*(mxGetPr(prhs[2]));
    //}


    TIFFSetWarningHandler(DummyHandler);
    TIFF* tif = TIFFOpen(fileName, "r");
    if(!tif) mexErrMsgIdAndTxt("tiff:inputError","File \"%s\" cannot be opened",fileName);

    uint64_t x = 1,y = 1,z = 1,bits = 1, startSlice = 0;
    TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &x);
    TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &y);

    if(nrhs == 1){
        z = getImageSizeZ(fileName);
    }
    else{
        if(mxGetN(prhs[1]) != 2){
            mexErrMsgIdAndTxt("tiff:inputError","Input range is not 2");
        }
        else{
            startSlice = (uint64_t)*(mxGetPr(prhs[1]))-1;
            z = (uint64_t)*((mxGetPr(prhs[1])+1))-startSlice;
            if (!TIFFSetDirectory(tif,startSlice+z-1) || !TIFFSetDirectory(tif,startSlice)){
                mexErrMsgIdAndTxt("tiff:rangeOutOfBound","Range is out of bounds");
            }
        }
    }

    TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bits);
    uint64_t stripSize = 1;
    TIFFGetField(tif, TIFFTAG_ROWSPERSTRIP, &stripSize);
    TIFFClose(tif);

    uint8_t imageJIm = 0;
    if(isImageJIm(fileName)){
        imageJIm = 1;
        uint64_t tempZ = imageJImGetZ(fileName);
        if(tempZ) z = tempZ;
    }

    uint64_t dim[3];
    dim[0] = y;
    dim[1] = x;
    dim[2] = z;



    // Case for ImageJ
    uint8_t err = 0;
    if(imageJIm){
        if(bits == 8){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT8_CLASS, mxREAL);
            uint8_t* tiff = (uint8_t*)mxGetPr(plhs[0]);
            err = readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 16){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT16_CLASS, mxREAL);
            uint16_t* tiff = (uint16_t*)mxGetPr(plhs[0]);
            err = readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 32){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxSINGLE_CLASS, mxREAL);
            float* tiff = (float*)mxGetPr(plhs[0]);
            err = readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 64){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxDOUBLE_CLASS, mxREAL);
            double* tiff = (double*)mxGetPr(plhs[0]);
            err = readTiffParallelImageJ(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else{
            mexErrMsgIdAndTxt("tiff:dataTypeError","Data type not suppported");
        }
    }
    // Case for 2D
    else if(z <= 1){
        if(bits == 8){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT8_CLASS, mxREAL);
            uint8_t* tiff = (uint8_t*)mxGetPr(plhs[0]);
            err = readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 16){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT16_CLASS, mxREAL);
            uint16_t* tiff = (uint16_t*)mxGetPr(plhs[0]);
            err = readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 32){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxSINGLE_CLASS, mxREAL);
            float* tiff = (float*)mxGetPr(plhs[0]);
            err = readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 64){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxDOUBLE_CLASS, mxREAL);
            double* tiff = (double*)mxGetPr(plhs[0]);
            err = readTiffParallel2D(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else{
            mexErrMsgIdAndTxt("tiff:dataTypeError","Data type not suppported");
        }
    }
    // Case for 3D
    else{
        if(bits == 8){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT8_CLASS, mxREAL);
            uint8_t* tiff = (uint8_t*)mxGetPr(plhs[0]);
            err = readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 16){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxUINT16_CLASS, mxREAL);
            uint16_t* tiff = (uint16_t*)mxGetPr(plhs[0]);
            err = readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 32){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxSINGLE_CLASS, mxREAL);
            float* tiff = (float*)mxGetPr(plhs[0]);
            err = readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else if(bits == 64){
            plhs[0] = mxCreateNumericArray(3,(mwSize*)dim,mxDOUBLE_CLASS, mxREAL);
            double* tiff = (double*)mxGetPr(plhs[0]);
            err = readTiffParallel(x,y,z,fileName, (void*)tiff, bits, startSlice, stripSize, flipXY);
        }
        else{
            mexErrMsgIdAndTxt("tiff:dataTypeError","Data type not suppported");
        }
    }
    if(err) mexErrMsgIdAndTxt("tiff:tiffError","An Error occured within the read function");
}

bool CTiffImg::Open(const char *szFname)
{
  Close();
  m_bRead = true;

  m_hTif = TIFFOpen(szFname, "r");
  if (!m_hTif) {
    TIFFError(szFname,"Can not open input image");
    return false;
  }
  icUInt16Number nPlanar=PLANARCONFIG_CONTIG;
  icUInt16Number nOrientation=ORIENTATION_TOPLEFT;
  icUInt16Number nSampleFormat=SAMPLEFORMAT_UINT;
  icUInt16Number *nSampleInfo=NULL;

  TIFFGetField(m_hTif, TIFFTAG_IMAGEWIDTH, &m_nWidth);
  TIFFGetField(m_hTif, TIFFTAG_IMAGELENGTH, &m_nHeight);
  TIFFGetField(m_hTif, TIFFTAG_PHOTOMETRIC, &m_nPhoto);
  TIFFGetField(m_hTif, TIFFTAG_PLANARCONFIG, &m_nPlanar);
  TIFFGetField(m_hTif, TIFFTAG_SAMPLESPERPIXEL, &m_nSamples);
  TIFFGetField(m_hTif, TIFFTAG_EXTRASAMPLES, &m_nExtraSamples, &nSampleInfo);
  TIFFGetField(m_hTif, TIFFTAG_BITSPERSAMPLE, &m_nBitsPerSample);
  TIFFGetField(m_hTif, TIFFTAG_SAMPLEFORMAT, &nSampleFormat);
  TIFFGetField(m_hTif, TIFFTAG_ROWSPERSTRIP, &m_nRowsPerStrip);
  TIFFGetField(m_hTif, TIFFTAG_ORIENTATION, &nOrientation);
  TIFFGetField(m_hTif, TIFFTAG_XRESOLUTION, &m_fXRes);
  TIFFGetField(m_hTif, TIFFTAG_YRESOLUTION, &m_fYRes);
  TIFFGetField(m_hTif, TIFFTAG_COMPRESSION, &m_nCompress);

  //Validate what we expect to work with
  if ((m_nBitsPerSample==32 && nSampleFormat!=SAMPLEFORMAT_IEEEFP) ||
      (m_nBitsPerSample!=32 && nSampleFormat!=SAMPLEFORMAT_UINT) ||
       nOrientation != ORIENTATION_TOPLEFT) {
    Close();
    return false;
  }
  m_nCurStrip=(unsigned int)-1;
  m_nCurLine = 0;

  m_nStripSize = (unsigned int)TIFFStripSize(m_hTif);

  if (m_nSamples>1 && m_nPlanar==PLANARCONFIG_SEPARATE) {
    m_nStripSamples = m_nSamples;
    m_nBytesPerLine = (m_nWidth * m_nBitsPerSample * m_nSamples + 7)>>3;
    m_nBytesPerSample = m_nBitsPerSample / 8;
    m_nBytesPerStripLine = m_nWidth * m_nBytesPerSample;
    //Only support bitspersample that fits on byte boundary
    if (m_nBitsPerSample%8) {
      Close();
      return false;
    }
    m_nStripsPerSample = m_nHeight / m_nRowsPerStrip;
    //Only support separations that evenly fit into strips
    if (m_nHeight % m_nRowsPerStrip) {
      Close();
      return false;
    }
  }
  else {
    m_nStripSamples = 1;
    m_nBytesPerLine = (m_nWidth * m_nBitsPerSample * m_nSamples + 7)>>3;
  }

  m_pStripBuf = (unsigned char*)malloc(m_nStripSize*m_nStripSamples);

  if (!m_pStripBuf) {
    Close();
    return false;
  }

  return true;
}

bool CTiffImg::GetIccProfile(unsigned char *&pProfile, unsigned int &nLen)
{
  pProfile = NULL;
  nLen = 0;

  TIFFGetField(m_hTif, TIFFTAG_ICCPROFILE, &nLen, &pProfile);

  return pProfile!=NULL && nLen>0;
}

bool wxTIFFHandler::LoadFile( wxImage *image, wxInputStream& stream, bool verbose, int index )
{
    if (index == -1)
        index = 0;

    image->Destroy();

    TIFF *tif = TIFFwxOpen( stream, "image", "r" );

    if (!tif)
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Error loading image.") );
        }

        return false;
    }

    if (!TIFFSetDirectory( tif, (tdir_t)index ))
    {
        if (verbose)
        {
            wxLogError( _("Invalid TIFF image index.") );
        }

        TIFFClose( tif );

        return false;
    }

    uint32 w, h;
    uint32 *raster;

    TIFFGetField( tif, TIFFTAG_IMAGEWIDTH, &w );
    TIFFGetField( tif, TIFFTAG_IMAGELENGTH, &h );

    uint16 extraSamples;
    uint16* samplesInfo;
    TIFFGetFieldDefaulted(tif, TIFFTAG_EXTRASAMPLES,
                          &extraSamples, &samplesInfo);
    const bool hasAlpha = (extraSamples == 1 &&
                           (samplesInfo[0] == EXTRASAMPLE_ASSOCALPHA ||
                            samplesInfo[0] == EXTRASAMPLE_UNASSALPHA));

    // guard against integer overflow during multiplication which could result
    // in allocating a too small buffer and then overflowing it
    const double bytesNeeded = (double)w * (double)h * sizeof(uint32);
    if ( bytesNeeded >= wxUINT32_MAX )
    {
        if ( verbose )
        {
            wxLogError( _("TIFF: Image size is abnormally big.") );
        }

        TIFFClose(tif);

        return false;
    }

    raster = (uint32*) _TIFFmalloc( (uint32)bytesNeeded );

    if (!raster)
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Couldn't allocate memory.") );
        }

        TIFFClose( tif );

        return false;
    }

    image->Create( (int)w, (int)h );
    if (!image->Ok())
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Couldn't allocate memory.") );
        }

        _TIFFfree( raster );
        TIFFClose( tif );

        return false;
    }

    if ( hasAlpha )
        image->SetAlpha();

    if (!TIFFReadRGBAImage( tif, w, h, raster, 0 ))
    {
        if (verbose)
        {
            wxLogError( _("TIFF: Error reading image.") );
        }

        _TIFFfree( raster );
        image->Destroy();
        TIFFClose( tif );

        return false;
    }

    unsigned char *ptr = image->GetData();
    ptr += w*3*(h-1);

    unsigned char *alpha = hasAlpha ? image->GetAlpha() : NULL;
    if ( hasAlpha )
        alpha += w*(h-1);

    uint32 pos = 0;

    for (uint32 i = 0; i < h; i++)
    {
        for (uint32 j = 0; j < w; j++)
        {
            *(ptr++) = (unsigned char)TIFFGetR(raster[pos]);
            *(ptr++) = (unsigned char)TIFFGetG(raster[pos]);
            *(ptr++) = (unsigned char)TIFFGetB(raster[pos]);
            if ( hasAlpha )
                *(alpha++) = (unsigned char)TIFFGetA(raster[pos]);

            pos++;
        }

        // subtract line we just added plus one line:
        ptr -= 2*w*3;
        if ( hasAlpha )
            alpha -= 2*w;
    }

    // set the image resolution if it's available
    uint16 tiffRes;
    if ( TIFFGetField(tif, TIFFTAG_RESOLUTIONUNIT, &tiffRes) )
    {
        wxImageResolution res;
        switch ( tiffRes )
        {
            default:
                wxLogWarning(_("Unknown TIFF resolution unit %d ignored"),
                             tiffRes);
                // fall through

            case RESUNIT_NONE:
                res = wxIMAGE_RESOLUTION_NONE;
                break;

            case RESUNIT_INCH:
                res = wxIMAGE_RESOLUTION_INCHES;
                break;

            case RESUNIT_CENTIMETER:
                res = wxIMAGE_RESOLUTION_CM;
                break;
        }

        if ( res != wxIMAGE_RESOLUTION_NONE )
        {
            float xres, yres;
            if ( TIFFGetField(tif, TIFFTAG_XRESOLUTION, &xres) )
                image->SetOption(wxIMAGE_OPTION_RESOLUTIONX, wxRound(xres));

            if ( TIFFGetField(tif, TIFFTAG_YRESOLUTION, &yres) )
                image->SetOption(wxIMAGE_OPTION_RESOLUTIONY, wxRound(yres));
        }
    }


    _TIFFfree( raster );

    TIFFClose( tif );

    return true;
}

vtkImageAlgorithm* VtkRaster::loadImageFromTIFF(const std::string& fileName)
{
    TIFF* tiff = XTIFFOpen(fileName.c_str(), "r");

    if (tiff)
    {
        GTIF* geoTiff = GTIFNew(tiff);

        int version[3];
        int count(0);
        GTIFDirectoryInfo(geoTiff, version, &count);

        if (count == 0)
            WARN("VtkRaster::loadImageFromTIFF - file is not georeferenced.");

        if (geoTiff)
        {
            double x0 = 0.0;
            double y0 = 0.0;
            double cellsize = 1.0;
            int imgWidth = 0;
            int imgHeight = 0;
            int nImages = 0;
            int pntCount = 0;
            double* pnts = nullptr;

            // get actual number of images in the tiff file
            do
            {
                ++nImages;
            } while (TIFFReadDirectory(tiff));
            if (nImages > 1)
                INFO(
                    "VtkRaster::loadImageFromTIFF() - File contains {:d} "
                    "images. This method is not tested for this case.",
                    nImages);

            // get image size
            TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &imgWidth);
            TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &imgHeight);

            // get cellsize
            // Note: GeoTiff allows anisotropic pixels. This is not supported
            // here and equilateral pixels are assumed.
            if (TIFFGetField(tiff, GTIFF_PIXELSCALE, &pntCount, &pnts))
            {
                if (pnts[0] != pnts[1])
                    WARN(
                        "VtkRaster::loadImageFromTIFF(): Original raster data "
                        "has anisotrop pixel size!");
                cellsize = pnts[0];
            }

            // get upper left point / origin
            if (TIFFGetField(tiff, GTIFF_TIEPOINTS, &pntCount, &pnts))
            {
                x0 = pnts[3];
                y0 = pnts[4] -
                     (imgHeight * cellsize);  // the origin should be the lower
                                              // left corner of the img
            }

            // read pixel values
            auto* pixVal = static_cast<uint32*>(
                _TIFFmalloc(imgWidth * imgHeight * sizeof(uint32)));
            if ((imgWidth > 0) && (imgHeight > 0))
            {
                if (!TIFFReadRGBAImage(tiff, imgWidth, imgHeight, pixVal, 0))
                {
                    ERR("VtkRaster::loadImageFromTIFF(): reading GeoTIFF "
                        "file.");
                    _TIFFfree(pixVal);
                    GTIFFree(geoTiff);
                    XTIFFClose(tiff);
                    return nullptr;
                }
            }

            // check for colormap
            uint16 photometric;
            TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC, &photometric);
            // read colormap
            uint16 *cmap_red = nullptr, *cmap_green = nullptr,
                   *cmap_blue = nullptr;
            int colormap_used = TIFFGetField(tiff, TIFFTAG_COLORMAP, &cmap_red,
                                             &cmap_green, &cmap_blue);

            auto* data = new float[imgWidth * imgHeight * 4];
            auto* pxl(new int[4]);
            for (int j = 0; j < imgHeight; ++j)
            {
                int lineindex = j * imgWidth;
                for (int i = 0; i < imgWidth; ++i)
                {  // scale intensities and set nodata values to white (i.e. the
                   // background colour)
                    unsigned pxl_idx(lineindex + i);
                    unsigned pos = 4 * (pxl_idx);
                    if (photometric == 1 && colormap_used == 1)
                    {
                        int idx = TIFFGetR(pixVal[pxl_idx]);
                        data[pos] = static_cast<float>(cmap_red[idx] >> 8);
                        data[pos + 1] =
                            static_cast<float>(cmap_green[idx] >> 8);
                        data[pos + 2] = static_cast<float>(cmap_blue[idx] >> 8);
                        data[pos + 3] = 1;
                    }
                    else
                    {
                        data[pos] =
                            static_cast<float>(TIFFGetR(pixVal[pxl_idx]));
                        data[pos + 1] =
                            static_cast<float>(TIFFGetG(pixVal[pxl_idx]));
                        data[pos + 2] =
                            static_cast<float>(TIFFGetB(pixVal[pxl_idx]));
                        data[pos + 3] =
                            static_cast<float>(TIFFGetA(pixVal[pxl_idx]));
                    }
                }
            }
            delete[] pxl;

            // set transparency values according to maximum pixel value
            if (photometric == 1)
            {
                float max_val(0);
                unsigned nPixels = 4 * imgWidth * imgHeight;
                for (unsigned j = 0; j < nPixels; ++j)
                {
                    if (data[j] > max_val)
                    {
                        max_val = data[j];
                    }
                }

                for (unsigned j = 0; j < nPixels; j += 4)
                {
                    data[j + 3] = max_val;
                }
            }

            vtkImageImport* image = vtkImageImport::New();
            image->SetDataOrigin(x0, y0, 0);
            image->SetDataSpacing(cellsize, cellsize, cellsize);
            image->SetWholeExtent(0, imgWidth - 1, 0, imgHeight - 1, 0, 0);
            image->SetDataExtent(0, imgWidth - 1, 0, imgHeight - 1, 0, 0);
            image->SetDataExtentToWholeExtent();
            image->SetDataScalarTypeToFloat();
            image->SetNumberOfScalarComponents(4);
            image->SetImportVoidPointer(data, 0);
            image->Update();

            _TIFFfree(pixVal);
            GTIFFree(geoTiff);
            XTIFFClose(tiff);
            return image;
        }

        XTIFFClose(tiff);
        ERR("VtkRaster::loadImageFromTIFF() - File not recognised as "
            "GeoTIFF-Image.");
        return nullptr;
    }

    ERR("VtkRaster::loadImageFromTIFF() - File not recognised as TIFF-Image.");
    return nullptr;
}

bool TiffDecoder::readHeader()
{
    bool result = false;

    TIFF* tif = static_cast<TIFF*>(m_tif);
    if (!m_tif)
    {
        // TIFFOpen() mode flags are different to fopen().  A 'b' in mode "rb" has no effect when reading.
        // http://www.remotesensing.org/libtiff/man/TIFFOpen.3tiff.html
        tif = TIFFOpen(m_filename.c_str(), "r");
    }

    if( tif )
    {
        uint32 wdth = 0, hght = 0;
        uint16 photometric = 0;
        m_tif = tif;

        if( TIFFGetField( tif, TIFFTAG_IMAGEWIDTH, &wdth ) &&
            TIFFGetField( tif, TIFFTAG_IMAGELENGTH, &hght ) &&
            TIFFGetField( tif, TIFFTAG_PHOTOMETRIC, &photometric ))
        {
            uint16 bpp=8, ncn = photometric > 1 ? 3 : 1;
            TIFFGetField( tif, TIFFTAG_BITSPERSAMPLE, &bpp );
            TIFFGetField( tif, TIFFTAG_SAMPLESPERPIXEL, &ncn );

            m_width = wdth;
            m_height = hght;
            if((bpp == 32 && ncn == 3) || photometric == PHOTOMETRIC_LOGLUV)
            {
                m_type = CV_32FC3;
                m_hdr = true;
                return true;
            }
            m_hdr = false;

            if( bpp > 8 &&
               ((photometric != 2 && photometric != 1) ||
                (ncn != 1 && ncn != 3 && ncn != 4)))
                bpp = 8;

            int wanted_channels = normalizeChannelsNumber(ncn);
            switch(bpp)
            {
                case 8:
                    m_type = CV_MAKETYPE(CV_8U, photometric > 1 ? wanted_channels : 1);
                    break;
                case 16:
                    m_type = CV_MAKETYPE(CV_16U, photometric > 1 ? wanted_channels : 1);
                    break;

                case 32:
                    m_type = CV_MAKETYPE(CV_32F, photometric > 1 ? 3 : 1);
                    break;
                case 64:
                    m_type = CV_MAKETYPE(CV_64F, photometric > 1 ? 3 : 1);
                    break;

                default:
                    result = false;
            }
            result = true;
        }
    }

    if( !result )
        close();

    return result;
}

rvoe<RasterImage> do_tiff_load(TIFF *tif) {
    RawPixelImage result;
    std::optional<std::string> icc;

    uint32_t w{}, h{};
    uint16_t bitspersample{}, samplesperpixel{}, photometric{}, planarconf{};
    uint32_t icc_count{};
    void *icc_data{};

    if(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &w) != 1) {
        RETERR(UnsupportedTIFF);
    }
    if(TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &h) != 1) {
        RETERR(UnsupportedTIFF);
    }

    if(TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitspersample) != 1) {
        RETERR(UnsupportedTIFF);
    }
    if(bitspersample != 8) {
        RETERR(UnsupportedTIFF);
    }

    if(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samplesperpixel) != 1) {
        RETERR(UnsupportedTIFF);
    }

    if(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric) != 1) {
        RETERR(UnsupportedTIFF);
    }

    /*
     * Note that the output variable is an array, because there can
     * be more than 1 extra channel.
    if(TIFFGetField(tif, TIFFTAG_EXTRASAMPLES, &extrasamples) == 1) {
        if(extrasamples != 0) {
            fprintf(stderr, "TIFFs with an alpha channel not supported yet.");
            RETERR(UnsupportedTIFF);
        }
    }
    */

    if(TIFFGetField(tif, TIFFTAG_PLANARCONFIG, &planarconf) != 1) {
        RETERR(UnsupportedTIFF);
    }
    // Maybe fail for this?

    if(TIFFGetField(tif, TIFFTAG_ICCPROFILE, &icc_count, &icc_data) == 1) {
        result.icc_profile = std::vector<std::byte>((const std::byte *)icc_data,
                                                    (const std::byte *)icc_data + icc_count);
    }

    result.md.pixel_depth = bitspersample;
    result.md.alpha_depth = bitspersample;

    const auto scanlinesize = TIFFScanlineSize64(tif);
    std::vector<std::byte> line(scanlinesize, std::byte(0));
    result.pixels.reserve(scanlinesize * h);
    for(uint32_t row = 0; row < h; ++row) {
        if(TIFFReadScanline(tif, line.data(), row) != 1) {
            fprintf(stderr, "TIFF decoding failed.\n");
            RETERR(FileReadError);
        }
        result.pixels.insert(result.pixels.end(), line.cbegin(), line.cend());
    }
    result.md.w = w;
    result.md.h = h;
    result.md.pixel_depth = 8;
    result.md.alpha_depth = 8;

    switch(photometric) {
    case PHOTOMETRIC_SEPARATED:
        if(samplesperpixel == 5) {
            separate_tif_alpha(result, 4);
        } else if(samplesperpixel != 4) {
            RETERR(UnsupportedTIFF);
        }
        result.md.cs = CAPY_IMAGE_CS_CMYK;
        break;

    case PHOTOMETRIC_RGB:
        if(samplesperpixel == 4) {
            separate_tif_alpha(result, 3);
        } else if(samplesperpixel != 3) {
            RETERR(UnsupportedTIFF);
        }
        result.md.cs = CAPY_IMAGE_CS_RGB;
        break;

    case PHOTOMETRIC_MINISBLACK:
        if(samplesperpixel == 2) {
            separate_tif_alpha(result, 1);
        } else if(samplesperpixel != 1) {
            RETERR(UnsupportedTIFF);
        }
        result.md.cs = CAPY_IMAGE_CS_GRAY;
        break;

    default:
        RETERR(UnsupportedTIFF);
    }
    return RasterImage(std::move(result));
}

void
TiffPlugin::setImageFiles(QStringList files)
{
  QProgressDialog progress("Enumerating files - may take some time...",
			   0,
			   0, 100,
			   0);
  progress.setMinimumDuration(0);

  QStringList flist;
  flist.clear();

  for(uint i=0; i<files.size(); i++)
    {
      progress.setValue(100*(float)i/(float)files.size());
      qApp->processEvents();

      QFileInfo fileInfo(m_fileName[0], files[i]);
      QString imgfl = fileInfo.absoluteFilePath();

      m_imageList.append(imgfl);
    }

  progress.setValue(100);
  qApp->processEvents();

  m_depth = m_imageList.size();

  TIFF *image;
  image = TIFFOpen((char*)m_imageList[0].toUtf8().data(), "r");

  // -- get number of images(directories) within the file
  m_dirCount = 0;
  if (image)
    {
      do {
	m_dirCount ++;
      } while (TIFFReadDirectory(image));
      if (m_dirCount > 1)
	QMessageBox::information(0, "3D Tiff",
				 QString("Number of images : %1").arg(m_dirCount));
      if (m_dirCount > 1)
	m_depth = m_dirCount;
    }

  TIFFGetField(image, TIFFTAG_IMAGEWIDTH, &m_width);
  TIFFGetField(image, TIFFTAG_IMAGELENGTH, &m_height);

  uint16 bitPerSample;
  TIFFGetField(image, TIFFTAG_BITSPERSAMPLE, &bitPerSample);

  TIFFClose(image);

  if (bitPerSample == 1)
    {
      QMessageBox::critical(0, "Image Format Error", "Cannot handle this format : bits per voxel = 1");
      m_imageList.clear();
      return;
    }

  if (bitPerSample == 8)
    {
      m_voxelType = _UChar;

      QStringList vtypes;
      vtypes << "UChar";
      vtypes << "Char";
      QString option = QInputDialog::getItem(0,
					     "Select Voxel Type",
					     "Voxel Type (found 8 bits per voxel)",
					     vtypes,
					     0,
					     false);
      int idx = vtypes.indexOf(option);
      if (idx == 1)
	m_voxelType = _Char;
    }
  else if (bitPerSample == 16)
    {
      m_voxelType = _UShort;

      QStringList vtypes;
      vtypes << "UShort";
      vtypes << "Short";
      QString option = QInputDialog::getItem(0,
					     "Select Voxel Type",
					     "Voxel Type (found 16 bits per voxel)",
					     vtypes,
					     0,
					     false);
      int idx = vtypes.indexOf(option);
      if (idx == 1)
	m_voxelType = _Short;
    }
  else if (bitPerSample == 32)
    {
      m_voxelType = _Float;

      QStringList vtypes;
      vtypes << "Float";
      vtypes << "Int";
      QString option = QInputDialog::getItem(0,
					     "Select Voxel Type",
					     "Voxel Type (found 32 bits per voxel)",
					     vtypes,
					     0,
					     false);
      int idx = vtypes.indexOf(option);
      if (idx == 1)
	m_voxelType = _Int;
    }

  m_headerBytes = 0;

  m_bytesPerVoxel = 1;
  if (m_voxelType == _UChar) m_bytesPerVoxel = 1;
  else if (m_voxelType == _Char) m_bytesPerVoxel = 1;
  else if (m_voxelType == _UShort) m_bytesPerVoxel = 2;
  else if (m_voxelType == _Short) m_bytesPerVoxel = 2;
  else if (m_voxelType == _Int) m_bytesPerVoxel = 4;
  else if (m_voxelType == _Float) m_bytesPerVoxel = 4;

  if (m_voxelType == _UChar ||
      m_voxelType == _Char ||
      m_voxelType == _UShort ||
      m_voxelType == _Short)
    findMinMaxandGenerateHistogram();
  else
    {
      //QMessageBox::information(0, "Error",
      //		       "Currently accepting only 1- and 2-byte images");
      findMinMax();
      generateHistogram();
    }
}

int main(int argc, char** argv)
{
	TIFF* image;
	uint32 width, height;
	int r1, c1, t1, imagesize;
	int nsamples;
	unsigned char* scanline = NULL;

	uint16 BitsPerSample;           // normally 8 for grayscale image
	uint16 SamplesPerPixel;         // normally 1 for grayscale image
	uint16 i;

	// Open the TIFF image
	if ((image = TIFFOpen(argv[1], "r")) == NULL) {
		fprintf(stderr, "Could not open incoming image\n");
		exit(42);
	}

	// Find the width and height of the image
	TIFFGetField(image, TIFFTAG_IMAGEWIDTH, &width);
	TIFFGetField(image, TIFFTAG_IMAGELENGTH, &height);
	TIFFGetField(image, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);
	TIFFGetField(image, TIFFTAG_SAMPLESPERPIXEL, &SamplesPerPixel);
	imagesize = height * width + 1;	//get image size

	//allocate memory for reading tif image
	scanline = (unsigned char*)_TIFFmalloc(SamplesPerPixel * width);
	if (scanline == NULL) {
		fprintf(stderr, "Could not allocate memory!\n");
		exit(0);
	}

	fprintf(stderr, "W=%i H=%i BitsPerSample=%i SamplesPerPixel=%i\n", width, height, BitsPerSample, SamplesPerPixel);
	for (r1 = 0; r1 < height; r1++)
	{
		TIFFReadScanline(image, scanline, r1, 0);
		for (c1 = 0; c1 < width; c1++)
		{
			t1 = c1 * SamplesPerPixel;

			for (i = 0; i < SamplesPerPixel; i++)
				printf("%u \t", *(scanline + t1 + i));
			printf("\n");
		}
	}

	_TIFFfree(scanline); //free allocate memory

	TIFFClose(image);

}

bool loadImageLibTiff(const std::string& path, Image& img)
{
    TIFFSetWarningHandler(nullptr);
    TIFFSetWarningHandlerExt(nullptr);

    TIFF* tif = TIFFOpen(path.c_str(), "r");

    if (!tif) return false;

    uint32_t width, height;
    uint16_t bitspersample = 0, sample_format = SAMPLEFORMAT_UINT, samples = 1;

    // Get the image width and height
    if (!TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width))
    {
        std::cerr << "The file '" << path << "' has no 'TIFFTAG_IMAGEWIDTH'. The file is probably corrupted\n";
        return false;
    }
    if (!TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height))
    {
        std::cerr << "The file '" << path << "' has no 'TIFFTAG_IMAGELENGTH'. The file is probably corrupted\n";
        return false;
    }
    if (!TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitspersample))
    {
        std::cerr << "The file '" << path << "' has no 'TIFFTAG_BITSPERSAMPLE'. The file is probably corrupted\n";
        return false;
    }

    // These tags might actually be not present. In these cases the default values are used.
    if (!TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sample_format))
    {
        sample_format = SAMPLEFORMAT_UINT;
    }
    if (!TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samples))
    {
        samples = 1;
    }


    ImageType type = ImageType::UC1;
    if (sample_format == SAMPLEFORMAT_IEEEFP)
    {
        type = ImageType::F1;
    }
    else if (sample_format == SAMPLEFORMAT_UINT)
    {
        type = (bitspersample == 8) ? ImageType::UC1 : (bitspersample == 16) ? ImageType::US1 : ImageType::UI1;
    }
    else if (sample_format == SAMPLEFORMAT_INT)
    {
        type = (bitspersample == 8) ? ImageType::C1 : (bitspersample == 16) ? ImageType::S1 : ImageType::I1;
    }


    if (samples != 1)
    {
        std::cout << "Image '" << path << "' has more than one channel. Only loading first channel\n";
        samples = 1;
    }

    type = (ImageType)((int)type + samples - 1);
    img.create(height, width, type);

    // size_t buffer_size = TIFFScanlineSize(tif);
    // SAIGA_ASSERT(buffer_size == img.pitchBytes);

    for (uint32_t row = 0; row < height; ++row)
    {
        TIFFReadScanline(tif, img.rowPtr(row), row);
    }


    TIFFClose(tif);

    return true;
}

bool loadImageFromMemoryLibTiff(const void* data, size_t size, Image& img)
{
    TIFFSetWarningHandler(nullptr);
    TIFFSetWarningHandlerExt(nullptr);


    imemstream strm((const char*)data, size);
    // TIFF* tif = TIFFOpen(path.c_str(), "r");
    TIFF* tif = TIFFStreamOpen("balbla", &strm);

    if (!tif) return false;

    uint32_t width, height;
    uint16_t bitspersample = 0, sample_format = SAMPLEFORMAT_UINT, samples = 1;

    // Get the image width and height
    if (!TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &width))
    {
        return false;
    }
    if (!TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &height))
    {
        return false;
    }
    if (!TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bitspersample))
    {
        return false;
    }

    // These tags might actually be not present. In these cases the default values are used.
    if (!TIFFGetField(tif, TIFFTAG_SAMPLEFORMAT, &sample_format))
    {
        sample_format = SAMPLEFORMAT_UINT;
    }
    if (!TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &samples))
    {
        samples = 1;
    }


    ImageType type = ImageType::UC1;
    if (sample_format == SAMPLEFORMAT_IEEEFP)
    {
        type = ImageType::F1;
    }
    else if (sample_format == SAMPLEFORMAT_UINT)
    {
        type = (bitspersample == 8) ? ImageType::UC1 : (bitspersample == 16) ? ImageType::US1 : ImageType::UI1;
    }
    else if (sample_format == SAMPLEFORMAT_INT)
    {
        type = (bitspersample == 8) ? ImageType::C1 : (bitspersample == 16) ? ImageType::S1 : ImageType::I1;
    }


    if (samples != 1)
    {
        samples = 1;
    }

    type = (ImageType)((int)type + samples - 1);
    img.create(height, width, type);

    // size_t buffer_size = TIFFScanlineSize(tif);
    // SAIGA_ASSERT(buffer_size == img.pitchBytes);

    for (uint32_t row = 0; row < height; ++row)
    {
        TIFFReadScanline(tif, img.rowPtr(row), row);
    }


    TIFFClose(tif);

    return true;
}

bool TiffDecoder::readHeader()
{
    bool result = false;

    TIFF* tif = static_cast<TIFF*>(m_tif.get());
    if (!tif)
    {
        // TIFFOpen() mode flags are different to fopen().  A 'b' in mode "rb" has no effect when reading.
        // http://www.remotesensing.org/libtiff/man/TIFFOpen.3tiff.html
        if ( !m_buf.empty() )
        {
            m_buf_pos = 0;
            TiffDecoderBufHelper* buf_helper = new TiffDecoderBufHelper(this->m_buf, this->m_buf_pos);
            tif = TIFFClientOpen( "", "r", reinterpret_cast<thandle_t>(buf_helper), &TiffDecoderBufHelper::read,
                                  &TiffDecoderBufHelper::write, &TiffDecoderBufHelper::seek,
                                  &TiffDecoderBufHelper::close, &TiffDecoderBufHelper::size,
                                  &TiffDecoderBufHelper::map, /*unmap=*/0 );
            if (!tif)
                delete buf_helper;
        }
        else
        {
            tif = TIFFOpen(m_filename.c_str(), "r");
        }
        if (tif)
            m_tif.reset(tif, cv_tiffCloseHandle);
        else
            m_tif.release();
    }

    if (tif)
    {
        uint32 wdth = 0, hght = 0;
        uint16 photometric = 0;

        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGEWIDTH, &wdth));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_IMAGELENGTH, &hght));
        CV_TIFF_CHECK_CALL(TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &photometric));

        {
            bool isGrayScale = photometric == PHOTOMETRIC_MINISWHITE || photometric == PHOTOMETRIC_MINISBLACK;
            uint16 bpp = 8, ncn = isGrayScale ? 1 : 3;
            if (0 == TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp))
            {
                // TIFF bi-level images don't require TIFFTAG_BITSPERSAMPLE tag
                bpp = 1;
            }
            CV_TIFF_CHECK_CALL_DEBUG(TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &ncn));

            m_width = wdth;
            m_height = hght;
            if (ncn == 3 && photometric == PHOTOMETRIC_LOGLUV)
            {
                m_type = CV_32FC3;
                m_hdr = true;
                return true;
            }
            m_hdr = false;

            if( bpp > 8 &&
               ((photometric > 2) ||
                (ncn != 1 && ncn != 3 && ncn != 4)))
                bpp = 8;

            int wanted_channels = normalizeChannelsNumber(ncn);
            switch(bpp)
            {
                case 1:
                    m_type = CV_MAKETYPE(CV_8U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 8:
                    m_type = CV_MAKETYPE(CV_8U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 16:
                    m_type = CV_MAKETYPE(CV_16U, !isGrayScale ? wanted_channels : 1);
                    result = true;
                    break;
                case 32:
                    m_type = CV_MAKETYPE(CV_32F, wanted_channels);
                    result = true;
                    break;
                case 64:
                    m_type = CV_MAKETYPE(CV_64F, wanted_channels);
                    result = true;
                    break;
            default:
                CV_Error(cv::Error::StsError, "Invalid bitsperpixel value read from TIFF header! Must be 1, 8, 16, 32 or 64.");
            }
        }
    }

    if( !result )
        close();

    return result;
}

void Image::LoadTiffFile(const void* pData, size_t Size, IDataBlob* pDstPixels, ImageDesc& Desc)
{
    TIFFClientOpenWrapper TiffClientOpenWrpr{pData, Size};

    auto TiffFile = TIFFClientOpen("", "rm", &TiffClientOpenWrpr,
                                   TIFFClientOpenWrapper::TIFFReadProc,
                                   TIFFClientOpenWrapper::TIFFWriteProc,
                                   TIFFClientOpenWrapper::TIFFSeekProc,
                                   TIFFClientOpenWrapper::TIFFCloseProc,
                                   TIFFClientOpenWrapper::TIFFSizeProc,
                                   TIFFClientOpenWrapper::TIFFMapFileProc,
                                   TIFFClientOpenWrapper::TIFFUnmapFileProc);

    TIFFGetField(TiffFile, TIFFTAG_IMAGEWIDTH, &Desc.Width);
    TIFFGetField(TiffFile, TIFFTAG_IMAGELENGTH, &Desc.Height);

    Uint16 SamplesPerPixel = 0;
    // SamplesPerPixel is usually 1 for bilevel, grayscale, and palette-color images.
    // SamplesPerPixel is usually 3 for RGB images. If this value is higher, ExtraSamples
    // should give an indication of the meaning of the additional channels.
    TIFFGetField(TiffFile, TIFFTAG_SAMPLESPERPIXEL, &SamplesPerPixel);
    Desc.NumComponents = SamplesPerPixel;

    Uint16 BitsPerSample = 0;
    TIFFGetField(TiffFile, TIFFTAG_BITSPERSAMPLE, &BitsPerSample);

    Uint16 SampleFormat = 0;
    TIFFGetField(TiffFile, TIFFTAG_SAMPLEFORMAT, &SampleFormat);
    if (SampleFormat == 0)
        SampleFormat = SAMPLEFORMAT_UINT;

    switch (SampleFormat)
    {
        case SAMPLEFORMAT_UINT:
            switch (BitsPerSample)
            {
                case 8: Desc.ComponentType = VT_UINT8; break;
                case 16: Desc.ComponentType = VT_UINT16; break;
                case 32: Desc.ComponentType = VT_UINT32; break;
                default: LOG_ERROR_AND_THROW(BitsPerSample, " is not a valid UINT component bit depth. Only 8, 16 and 32 are allowed");
            }
            break;

        case SAMPLEFORMAT_INT:
            switch (BitsPerSample)
            {
                case 8: Desc.ComponentType = VT_INT8; break;
                case 16: Desc.ComponentType = VT_INT16; break;
                case 32: Desc.ComponentType = VT_INT32; break;
                default: LOG_ERROR_AND_THROW(BitsPerSample, " is not a valid INT component bit depth. Only 8, 16 and 32 are allowed");
            }
            break;

        case SAMPLEFORMAT_IEEEFP:
            switch (BitsPerSample)
            {
                case 16: Desc.ComponentType = VT_FLOAT16; break;
                case 32: Desc.ComponentType = VT_FLOAT32; break;
                default: LOG_ERROR_AND_THROW(BitsPerSample, " is not a valid FLOAT component bit depth. Only 16 and 32 are allowed");
            }
            break;

        case SAMPLEFORMAT_VOID:
            LOG_ERROR_AND_THROW("Untyped tif images are not supported");
            break;

        case SAMPLEFORMAT_COMPLEXINT:
            LOG_ERROR_AND_THROW("Complex int tif images are not supported");
            break;

        case SAMPLEFORMAT_COMPLEXIEEEFP:
            LOG_ERROR_AND_THROW("Complex floating point tif images are not supported");
            break;

        default:
            LOG_ERROR_AND_THROW("Unknown sample format: ", Uint32{SampleFormat});
    }

    if (pDstPixels != nullptr)
    {
        size_t ScanlineSize = TIFFScanlineSize(TiffFile);
        Desc.RowStride      = AlignUp(Desc.Width * Desc.NumComponents * (BitsPerSample / 8), 4u);
        pDstPixels->Resize(size_t{Desc.Height} * size_t{Desc.RowStride});

        Uint16 PlanarConfig = 0;
        TIFFGetField(TiffFile, TIFFTAG_PLANARCONFIG, &PlanarConfig);
        if (PlanarConfig == PLANARCONFIG_CONTIG || Desc.NumComponents == 1)
        {
            VERIFY_EXPR(Desc.RowStride >= ScanlineSize);
            Uint8* pDataPtr = pDstPixels->GetDataPtr<Uint8>();
            for (Uint32 row = 0; row < Desc.Height; row++, pDataPtr += Desc.RowStride)
            {
                TIFFReadScanline(TiffFile, pDataPtr, row);
            }
        }
        else if (PlanarConfig == PLANARCONFIG_SEPARATE)
        {
            std::vector<Uint8> ScanlineData(ScanlineSize);
            for (Uint32 row = 0; row < Desc.Height; ++row)
            {
                for (Uint16 comp = 0; comp < Desc.NumComponents; ++comp)
                {
                    Uint8* const pDstRow = pDstPixels->GetDataPtr<Uint8>() + Desc.RowStride * row + comp;

                    TIFFReadScanline(TiffFile, ScanlineData.data(), row, comp);

                    auto CopyComponet = [Width = Desc.Width, NumComp = Desc.NumComponents](const auto* pSrc, auto* pDst) {
                        for (Uint32 x = 0; x < Width; ++x)
                        {
                            pDst[x * NumComp] = pSrc[x];
                        }
                    };

                    switch (BitsPerSample)
                    {
                        case 8:
                            CopyComponet(reinterpret_cast<const Uint8*>(ScanlineData.data()), reinterpret_cast<Uint8*>(pDstRow));
                            break;

                        case 16:
                            CopyComponet(reinterpret_cast<const Uint16*>(ScanlineData.data()), reinterpret_cast<Uint16*>(pDstRow));
                            break;

                        case 32:
                            CopyComponet(reinterpret_cast<const Uint32*>(ScanlineData.data()), reinterpret_cast<Uint32*>(pDstRow));
                            break;

                        default:
                            UNEXPECTED("Unexpected component bit depth (", BitsPerSample, ").");
                    }
                }
            }
        }
        else
        {
            UNEXPECTED("Unexpected planar configuration (", PlanarConfig, ").");
        }
    }

    TIFFClose(TiffFile);
}

void GTiffDataset::FillEmptyTiles()

{
    toff_t *panByteCounts = NULL;
    int    nBlockCount, iBlock;

    if (!SetDirectory())
        return;

/* -------------------------------------------------------------------- */
/*      How many blocks are there in this file?                         */
/* -------------------------------------------------------------------- */
    if( nPlanarConfig == PLANARCONFIG_SEPARATE )
        nBlockCount = nBlocksPerBand * nBands;
    else
        nBlockCount = nBlocksPerBand;

/* -------------------------------------------------------------------- */
/*      Fetch block maps.                                               */
/* -------------------------------------------------------------------- */
    if( TIFFIsTiled( hTIFF ) )
        TIFFGetField( hTIFF, TIFFTAG_TILEBYTECOUNTS, &panByteCounts );
    else
        TIFFGetField( hTIFF, TIFFTAG_STRIPBYTECOUNTS, &panByteCounts );

    if (panByteCounts == NULL)
    {
        /* Got here with libtiff 3.9.3 and tiff_write_8 test */
        CPLError(CE_Failure, CPLE_AppDefined, "FillEmptyTiles() failed because panByteCounts == NULL");
        return;
    }

/* -------------------------------------------------------------------- */
/*      Prepare a blank data buffer to write for uninitialized blocks.  */
/* -------------------------------------------------------------------- */
    int nBlockBytes;

    if( TIFFIsTiled( hTIFF ) )
        nBlockBytes = TIFFTileSize(hTIFF);
    else
        nBlockBytes = TIFFStripSize(hTIFF);

    GByte *pabyData = (GByte *) VSICalloc(nBlockBytes,1);
    if (pabyData == NULL)
    {
        CPLError(CE_Failure, CPLE_OutOfMemory,
                 "Cannot allocate %d bytes", nBlockBytes);
        return;
    }

/* -------------------------------------------------------------------- */
/*      Check all blocks, writing out data for uninitialized blocks.    */
/* -------------------------------------------------------------------- */
    for( iBlock = 0; iBlock < nBlockCount; iBlock++ )
    {
        if( panByteCounts[iBlock] == 0 )
        {
            if( WriteEncodedTileOrStrip( iBlock, pabyData, FALSE ) != CE_None )
                break;
        }
    }

    CPLFree( pabyData );
}

CPLErr GTiffDataset::CreateOverviewsFromSrcOverviews(GDALDataset* poSrcDS)
{
    CPLAssert(poSrcDS->GetRasterCount() != 0);
    CPLAssert(nOverviewCount == 0);

/* -------------------------------------------------------------------- */
/*      Move to the directory for this dataset.                         */
/* -------------------------------------------------------------------- */
    if (!SetDirectory())
        return CE_Failure;
    FlushDirectory();

    int nOvBitsPerSample = nBitsPerSample;

/* -------------------------------------------------------------------- */
/*      Do we have a palette?  If so, create a TIFF compatible version. */
/* -------------------------------------------------------------------- */
    std::vector<unsigned short> anTRed, anTGreen, anTBlue;
    unsigned short      *panRed=NULL, *panGreen=NULL, *panBlue=NULL;

    if( nPhotometric == PHOTOMETRIC_PALETTE && poColorTable != NULL )
    {
        int nColors;

        if( nOvBitsPerSample == 8 )
            nColors = 256;
        else if( nOvBitsPerSample < 8 )
            nColors = 1 << nOvBitsPerSample;
        else
            nColors = 65536;

        anTRed.resize(nColors,0);
        anTGreen.resize(nColors,0);
        anTBlue.resize(nColors,0);

        for( int iColor = 0; iColor < nColors; iColor++ )
        {
            if( iColor < poColorTable->GetColorEntryCount() )
            {
                GDALColorEntry  sRGB;

                poColorTable->GetColorEntryAsRGB( iColor, &sRGB );

                anTRed[iColor] = (unsigned short) (256 * sRGB.c1);
                anTGreen[iColor] = (unsigned short) (256 * sRGB.c2);
                anTBlue[iColor] = (unsigned short) (256 * sRGB.c3);
            }
            else
            {
                anTRed[iColor] = anTGreen[iColor] = anTBlue[iColor] = 0;
            }
        }

        panRed = &(anTRed[0]);
        panGreen = &(anTGreen[0]);
        panBlue = &(anTBlue[0]);
    }

/* -------------------------------------------------------------------- */
/*      Do we need some metadata for the overviews?                     */
/* -------------------------------------------------------------------- */
    CPLString osMetadata;

    GTIFFBuildOverviewMetadata( "NONE", this, osMetadata );

/* -------------------------------------------------------------------- */
/*      Fetch extra sample tag                                          */
/* -------------------------------------------------------------------- */
    uint16 *panExtraSampleValues = NULL;
    uint16 nExtraSamples = 0;

    if( TIFFGetField( hTIFF, TIFFTAG_EXTRASAMPLES, &nExtraSamples, &panExtraSampleValues) )
    {
        uint16* panExtraSampleValuesNew = (uint16*) CPLMalloc(nExtraSamples * sizeof(uint16));
        memcpy(panExtraSampleValuesNew, panExtraSampleValues, nExtraSamples * sizeof(uint16));
        panExtraSampleValues = panExtraSampleValuesNew;
    }
    else
    {
        panExtraSampleValues = NULL;
        nExtraSamples = 0;
    }

/* -------------------------------------------------------------------- */
/*      Fetch predictor tag                                             */
/* -------------------------------------------------------------------- */
    uint16 nPredictor = PREDICTOR_NONE;
    if ( nCompression == COMPRESSION_LZW ||
         nCompression == COMPRESSION_ADOBE_DEFLATE )
        TIFFGetField( hTIFF, TIFFTAG_PREDICTOR, &nPredictor );
    int nOvrBlockXSize, nOvrBlockYSize;
    GTIFFGetOverviewBlockSize(&nOvrBlockXSize, &nOvrBlockYSize);

    int nSrcOverviews = poSrcDS->GetRasterBand(1)->GetOverviewCount();
    int i;
    CPLErr eErr = CE_None;

    for(i=0;i<nSrcOverviews && eErr == CE_None;i++)
    {
        GDALRasterBand* poOvrBand = poSrcDS->GetRasterBand(1)->GetOverview(i);

        int         nOXSize = poOvrBand->GetXSize(), nOYSize = poOvrBand->GetYSize();

        toff_t nOverviewOffset =
                GTIFFWriteDirectory(hTIFF, FILETYPE_REDUCEDIMAGE,
                                    nOXSize, nOYSize,
                                    nOvBitsPerSample, nPlanarConfig,
                                    nSamplesPerPixel, nOvrBlockXSize, nOvrBlockYSize, TRUE,
                                    nCompression, nPhotometric, nSampleFormat,
                                    nPredictor,
                                    panRed, panGreen, panBlue,
                                    nExtraSamples, panExtraSampleValues,
                                    osMetadata );

        if( nOverviewOffset == 0 )
            eErr = CE_Failure;
        else
            eErr = RegisterNewOverviewDataset(nOverviewOffset);
    }

    CPLFree(panExtraSampleValues);
    panExtraSampleValues = NULL;

/* -------------------------------------------------------------------- */
/*      Create overviews for the mask.                                  */
/* -------------------------------------------------------------------- */
    if (eErr == CE_None)
        eErr = CreateInternalMaskOverviews(nOvrBlockXSize, nOvrBlockYSize);

    return eErr;
}

CPLErr GTiffDataset::IBuildOverviews( 
    const char * pszResampling, 
    int nOverviews, int * panOverviewList,
    int nBands, int * panBandList,
    GDALProgressFunc pfnProgress, void * pProgressData )

{
    CPLErr       eErr = CE_None;
    int          i;
    GTiffDataset *poODS;

/* -------------------------------------------------------------------- */
/*      If RRD or external OVR overviews requested, then invoke         */
/*      generic handling.                                               */
/* -------------------------------------------------------------------- */
    if( CSLTestBoolean(CPLGetConfigOption( "USE_RRD", "NO" )) 
        || CSLTestBoolean(CPLGetConfigOption( "TIFF_USE_OVR", "NO" )) )
    {
        return GDALDataset::IBuildOverviews( 
            pszResampling, nOverviews, panOverviewList, 
            nBands, panBandList, pfnProgress, pProgressData );
    }

/* -------------------------------------------------------------------- */
/*      If we don't have read access, then create the overviews         */
/*      externally.                                                     */
/* -------------------------------------------------------------------- */
    if( GetAccess() != GA_Update )
    {
        CPLDebug( "GTiff",
                  "File open for read-only accessing, "
                  "creating overviews externally." );

        return GDALDataset::IBuildOverviews( 
            pszResampling, nOverviews, panOverviewList, 
            nBands, panBandList, pfnProgress, pProgressData );
    }

/* -------------------------------------------------------------------- */
/*      Our TIFF overview support currently only works safely if all    */
/*      bands are handled at the same time.                             */
/* -------------------------------------------------------------------- */
    if( nBands != GetRasterCount() )
    {
        CPLError( CE_Failure, CPLE_NotSupported,
                  "Generation of overviews in TIFF currently only"
                  " supported when operating on all bands.\n" 
                  "Operation failed.\n" );
        return CE_Failure;
    }

/* -------------------------------------------------------------------- */
/*      Initialize progress counter.                                    */
/* -------------------------------------------------------------------- */
    if( !pfnProgress( 0.0, NULL, pProgressData ) )
    {
        CPLError( CE_Failure, CPLE_UserInterrupt, "User terminated" );
        return CE_Failure;
    }

/* -------------------------------------------------------------------- */
/*      If zero overviews were requested, we need to clear all          */
/*      existing overviews.                                             */
/* -------------------------------------------------------------------- */
    if( nOverviews == 0 )
    {
        if( nOverviewCount == 0 )
            return GDALDataset::IBuildOverviews( 
                pszResampling, nOverviews, panOverviewList, 
                nBands, panBandList, pfnProgress, pProgressData );
        else
            return CleanOverviews();
    }

/* -------------------------------------------------------------------- */
/*      Move to the directory for this dataset.                         */
/* -------------------------------------------------------------------- */
    if (!SetDirectory())
        return CE_Failure;
    FlushDirectory();

/* -------------------------------------------------------------------- */
/*      If we are averaging bit data to grayscale we need to create     */
/*      8bit overviews.                                                 */
/* -------------------------------------------------------------------- */
    int nOvBitsPerSample = nBitsPerSample;

    if( EQUALN(pszResampling,"AVERAGE_BIT2",12) )
        nOvBitsPerSample = 8;

/* -------------------------------------------------------------------- */
/*      Do we have a palette?  If so, create a TIFF compatible version. */
/* -------------------------------------------------------------------- */
    std::vector<unsigned short> anTRed, anTGreen, anTBlue;
    unsigned short      *panRed=NULL, *panGreen=NULL, *panBlue=NULL;

    if( nPhotometric == PHOTOMETRIC_PALETTE && poColorTable != NULL )
    {
        int nColors;

        if( nOvBitsPerSample == 8 )
            nColors = 256;
        else if( nOvBitsPerSample < 8 )
            nColors = 1 << nOvBitsPerSample;
        else
            nColors = 65536;

        anTRed.resize(nColors,0);
        anTGreen.resize(nColors,0);
        anTBlue.resize(nColors,0);

        for( int iColor = 0; iColor < nColors; iColor++ )
        {
            if( iColor < poColorTable->GetColorEntryCount() )
            {
                GDALColorEntry  sRGB;

                poColorTable->GetColorEntryAsRGB( iColor, &sRGB );

                anTRed[iColor] = (unsigned short) (256 * sRGB.c1);
                anTGreen[iColor] = (unsigned short) (256 * sRGB.c2);
                anTBlue[iColor] = (unsigned short) (256 * sRGB.c3);
            }
            else
            {
                anTRed[iColor] = anTGreen[iColor] = anTBlue[iColor] = 0;
            }
        }

        panRed = &(anTRed[0]);
        panGreen = &(anTGreen[0]);
        panBlue = &(anTBlue[0]);
    }

/* -------------------------------------------------------------------- */
/*      Do we need some metadata for the overviews?                     */
/* -------------------------------------------------------------------- */
    CPLString osMetadata;

    GTIFFBuildOverviewMetadata( pszResampling, this, osMetadata );

/* -------------------------------------------------------------------- */
/*      Fetch extra sample tag                                          */
/* -------------------------------------------------------------------- */
    uint16 *panExtraSampleValues = NULL;
    uint16 nExtraSamples = 0;

    if( TIFFGetField( hTIFF, TIFFTAG_EXTRASAMPLES, &nExtraSamples, &panExtraSampleValues) )
    {
        uint16* panExtraSampleValuesNew = (uint16*) CPLMalloc(nExtraSamples * sizeof(uint16));
        memcpy(panExtraSampleValuesNew, panExtraSampleValues, nExtraSamples * sizeof(uint16));
        panExtraSampleValues = panExtraSampleValuesNew;
    }
    else
    {
        panExtraSampleValues = NULL;
        nExtraSamples = 0;
    }

/* -------------------------------------------------------------------- */
/*      Fetch predictor tag                                             */
/* -------------------------------------------------------------------- */
    uint16 nPredictor = PREDICTOR_NONE;
    if ( nCompression == COMPRESSION_LZW ||
         nCompression == COMPRESSION_ADOBE_DEFLATE )
        TIFFGetField( hTIFF, TIFFTAG_PREDICTOR, &nPredictor );

/* -------------------------------------------------------------------- */
/*      Establish which of the overview levels we already have, and     */
/*      which are new.  We assume that band 1 of the file is            */
/*      representative.                                                 */
/* -------------------------------------------------------------------- */
    int nOvrBlockXSize, nOvrBlockYSize;
    GTIFFGetOverviewBlockSize(&nOvrBlockXSize, &nOvrBlockYSize);
    for( i = 0; i < nOverviews && eErr == CE_None; i++ )
    {
        int   j;

        for( j = 0; j < nOverviewCount && eErr == CE_None; j++ )
        {
            int    nOvFactor;

            poODS = papoOverviewDS[j];

            nOvFactor = (int) 
                (0.5 + GetRasterXSize() / (double) poODS->GetRasterXSize());

            if( nOvFactor == panOverviewList[i] 
                || nOvFactor == TIFF_OvLevelAdjust( panOverviewList[i],
                                                    GetRasterXSize() ) )
                panOverviewList[i] *= -1;
        }

        if( panOverviewList[i] > 0 )
        {
            toff_t	nOverviewOffset;
            int         nOXSize, nOYSize;

            nOXSize = (GetRasterXSize() + panOverviewList[i] - 1) 
                / panOverviewList[i];
            nOYSize = (GetRasterYSize() + panOverviewList[i] - 1)
                / panOverviewList[i];

            nOverviewOffset = 
                GTIFFWriteDirectory(hTIFF, FILETYPE_REDUCEDIMAGE,
                                    nOXSize, nOYSize, 
                                    nOvBitsPerSample, nPlanarConfig,
                                    nSamplesPerPixel, nOvrBlockXSize, nOvrBlockYSize, TRUE,
                                    nCompression, nPhotometric, nSampleFormat, 
                                    nPredictor,
                                    panRed, panGreen, panBlue,
                                    nExtraSamples, panExtraSampleValues,
                                    osMetadata );


            if( nOverviewOffset == 0 )
                eErr = CE_Failure;
            else
                eErr = RegisterNewOverviewDataset(nOverviewOffset);
        }
        else
            panOverviewList[i] *= -1;
    }

    CPLFree(panExtraSampleValues);
    panExtraSampleValues = NULL;

/* -------------------------------------------------------------------- */
/*      Create overviews for the mask.                                  */
/* -------------------------------------------------------------------- */
    if (eErr == CE_None)
        eErr = CreateInternalMaskOverviews(nOvrBlockXSize, nOvrBlockYSize);
    else
        return eErr;

/* -------------------------------------------------------------------- */
/*      Refresh overviews for the mask                                  */
/* -------------------------------------------------------------------- */
    if (poMaskDS != NULL &&
        poMaskDS->GetRasterCount() == 1)
    {
        GDALRasterBand **papoOverviewBands;
        int nMaskOverviews = 0;

        papoOverviewBands = (GDALRasterBand **) CPLCalloc(sizeof(void*),nOverviewCount);
        for( i = 0; i < nOverviewCount; i++ )
        {
            if (papoOverviewDS[i]->poMaskDS != NULL)
            {
                papoOverviewBands[nMaskOverviews ++] =
                        papoOverviewDS[i]->poMaskDS->GetRasterBand(1);
            }
        }
        eErr = GDALRegenerateOverviews( (GDALRasterBandH) 
                                        poMaskDS->GetRasterBand(1),
                                        nMaskOverviews, 
                                        (GDALRasterBandH *) papoOverviewBands,
                                        pszResampling, GDALDummyProgress, NULL);
        CPLFree(papoOverviewBands);
    }


/* -------------------------------------------------------------------- */
/*      Refresh old overviews that were listed.                         */
/* -------------------------------------------------------------------- */
    if (nCompression != COMPRESSION_NONE &&
        nPlanarConfig == PLANARCONFIG_CONTIG &&
        GDALDataTypeIsComplex(GetRasterBand( panBandList[0] )->GetRasterDataType()) == FALSE &&
        GetRasterBand( panBandList[0] )->GetColorTable() == NULL &&
        (EQUALN(pszResampling, "NEAR", 4) || EQUAL(pszResampling, "AVERAGE") || EQUAL(pszResampling, "GAUSS")))
    {
        /* In the case of pixel interleaved compressed overviews, we want to generate */
        /* the overviews for all the bands block by block, and not band after band, */
        /* in order to write the block once and not loose space in the TIFF file */

        GDALRasterBand ***papapoOverviewBands;
        GDALRasterBand  **papoBandList;

        int nNewOverviews = 0;
        int iBand;

        papapoOverviewBands = (GDALRasterBand ***) CPLCalloc(sizeof(void*),nBands);
        papoBandList = (GDALRasterBand **) CPLCalloc(sizeof(void*),nBands);
        for( iBand = 0; iBand < nBands; iBand++ )
        {
            GDALRasterBand* poBand = GetRasterBand( panBandList[iBand] );

            papoBandList[iBand] = poBand;
            papapoOverviewBands[iBand] = (GDALRasterBand **) CPLCalloc(sizeof(void*), poBand->GetOverviewCount());

            int iCurOverview = 0;
            for( i = 0; i < nOverviews; i++ )
            {
                int   j;

                for( j = 0; j < poBand->GetOverviewCount(); j++ )
                {
                    int    nOvFactor;
                    GDALRasterBand * poOverview = poBand->GetOverview( j );

                    nOvFactor = (int) 
                    (0.5 + poBand->GetXSize() / (double) poOverview->GetXSize());

                    int bHasNoData;
                    double noDataValue = poBand->GetNoDataValue(&bHasNoData);

                    if (bHasNoData)
                        poOverview->SetNoDataValue(noDataValue);

                    if( nOvFactor == panOverviewList[i] 
                        || nOvFactor == TIFF_OvLevelAdjust( panOverviewList[i],
                                                            poBand->GetXSize() ) )
                    {
                        papapoOverviewBands[iBand][iCurOverview] = poOverview;
                        iCurOverview++ ;
                        break;
                    }
                }
            }

            if (nNewOverviews == 0)
                nNewOverviews = iCurOverview;
            else if (nNewOverviews != iCurOverview)
            {
                CPLAssert(0);
                return CE_Failure;
            }
        }

        GDALRegenerateOverviewsMultiBand(nBands, papoBandList,
                                         nNewOverviews, papapoOverviewBands,
                                         pszResampling, pfnProgress, pProgressData );

        for( iBand = 0; iBand < nBands; iBand++ )
        {
            CPLFree(papapoOverviewBands[iBand]);
        }
        CPLFree(papapoOverviewBands);
        CPLFree(papoBandList);
    }
    else
    {
        GDALRasterBand **papoOverviewBands;

        papoOverviewBands = (GDALRasterBand **) 
            CPLCalloc(sizeof(void*),nOverviews);

        for( int iBand = 0; iBand < nBands && eErr == CE_None; iBand++ )
        {
            GDALRasterBand *poBand;
            int            nNewOverviews;

            poBand = GetRasterBand( panBandList[iBand] );

            nNewOverviews = 0;
            for( i = 0; i < nOverviews && poBand != NULL; i++ )
            {
                int   j;

                for( j = 0; j < poBand->GetOverviewCount(); j++ )
                {
                    int    nOvFactor;
                    GDALRasterBand * poOverview = poBand->GetOverview( j );

                    int bHasNoData;
                    double noDataValue = poBand->GetNoDataValue(&bHasNoData);

                    if (bHasNoData)
                        poOverview->SetNoDataValue(noDataValue);

                    nOvFactor = (int) 
                    (0.5 + poBand->GetXSize() / (double) poOverview->GetXSize());

                    if( nOvFactor == panOverviewList[i] 
                        || nOvFactor == TIFF_OvLevelAdjust( panOverviewList[i],
                                                            poBand->GetXSize() ) )
                    {
                        papoOverviewBands[nNewOverviews++] = poOverview;
                        break;
                    }
                }
            }

            void         *pScaledProgressData;

            pScaledProgressData = 
                GDALCreateScaledProgress( iBand / (double) nBands, 
                                        (iBand+1) / (double) nBands,
                                        pfnProgress, pProgressData );

            eErr = GDALRegenerateOverviews( (GDALRasterBandH) poBand,
                                            nNewOverviews, 
                                            (GDALRasterBandH *) papoOverviewBands,
                                            pszResampling, 
                                            GDALScaledProgress, 
                                            pScaledProgressData);

            GDALDestroyScaledProgress( pScaledProgressData );
        }

    /* -------------------------------------------------------------------- */
    /*      Cleanup                                                         */
    /* -------------------------------------------------------------------- */
        CPLFree( papoOverviewBands );
    }


    pfnProgress( 1.0, NULL, pProgressData );

    return eErr;
}

void GTiffDataset::WriteGeoTIFFInfo()

{
    bool bPixelIsPoint = false;
    int  bPointGeoIgnore = FALSE;

    if( GetMetadataItem( GDALMD_AREA_OR_POINT ) 
        && EQUAL(GetMetadataItem(GDALMD_AREA_OR_POINT),
                 GDALMD_AOP_POINT) )
    {
        bPixelIsPoint = true;
        bPointGeoIgnore = 
            CSLTestBoolean( CPLGetConfigOption("GTIFF_POINT_GEO_IGNORE",
                                               "FALSE") );
    }

/* -------------------------------------------------------------------- */
/*      If the geotransform is the default, don't bother writing it.    */
/* -------------------------------------------------------------------- */
    if( adfGeoTransform[0] != 0.0 || adfGeoTransform[1] != 1.0
        || adfGeoTransform[2] != 0.0 || adfGeoTransform[3] != 0.0
        || adfGeoTransform[4] != 0.0 || ABS(adfGeoTransform[5]) != 1.0 )
    {
        bNeedsRewrite = TRUE;

/* -------------------------------------------------------------------- */
/*      Clear old tags to ensure we don't end up with conflicting       */
/*      information. (#2625)                                            */
/* -------------------------------------------------------------------- */
#ifdef HAVE_UNSETFIELD
        TIFFUnsetField( hTIFF, TIFFTAG_GEOPIXELSCALE );
        TIFFUnsetField( hTIFF, TIFFTAG_GEOTIEPOINTS );
        TIFFUnsetField( hTIFF, TIFFTAG_GEOTRANSMATRIX );
#endif

/* -------------------------------------------------------------------- */
/*      Write the transform.  If we have a normal north-up image we     */
/*      use the tiepoint plus pixelscale otherwise we use a matrix.     */
/* -------------------------------------------------------------------- */
	if( adfGeoTransform[2] == 0.0 && adfGeoTransform[4] == 0.0 
            && adfGeoTransform[5] < 0.0 )
	{
	    double	adfPixelScale[3], adfTiePoints[6];

	    adfPixelScale[0] = adfGeoTransform[1];
	    adfPixelScale[1] = fabs(adfGeoTransform[5]);
	    adfPixelScale[2] = 0.0;

            if( !EQUAL(osProfile,"BASELINE") )
                TIFFSetField( hTIFF, TIFFTAG_GEOPIXELSCALE, 3, adfPixelScale );

	    adfTiePoints[0] = 0.0;
	    adfTiePoints[1] = 0.0;
	    adfTiePoints[2] = 0.0;
	    adfTiePoints[3] = adfGeoTransform[0];
	    adfTiePoints[4] = adfGeoTransform[3];
	    adfTiePoints[5] = 0.0;

            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                adfTiePoints[3] += adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5;
                adfTiePoints[4] += adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5;
            }

            if( !EQUAL(osProfile,"BASELINE") )
                TIFFSetField( hTIFF, TIFFTAG_GEOTIEPOINTS, 6, adfTiePoints );
	}
	else
	{
	    double	adfMatrix[16];

	    memset(adfMatrix,0,sizeof(double) * 16);

	    adfMatrix[0] = adfGeoTransform[1];
	    adfMatrix[1] = adfGeoTransform[2];
	    adfMatrix[3] = adfGeoTransform[0];
	    adfMatrix[4] = adfGeoTransform[4];
	    adfMatrix[5] = adfGeoTransform[5];
	    adfMatrix[7] = adfGeoTransform[3];
	    adfMatrix[15] = 1.0;

            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                adfMatrix[3] += adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5;
                adfMatrix[7] += adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5;
            }

            if( !EQUAL(osProfile,"BASELINE") )
                TIFFSetField( hTIFF, TIFFTAG_GEOTRANSMATRIX, 16, adfMatrix );
	}

        // Do we need a world file?
        if( CSLFetchBoolean( papszCreationOptions, "TFW", FALSE ) )
            GDALWriteWorldFile( osFilename, "tfw", adfGeoTransform );
        else if( CSLFetchBoolean( papszCreationOptions, "WORLDFILE", FALSE ) )
            GDALWriteWorldFile( osFilename, "wld", adfGeoTransform );
    }
    else if( GetGCPCount() > 0 )
    {
	double	*padfTiePoints;
	int		iGCP;
        bNeedsRewrite = TRUE;

	padfTiePoints = (double *) 
	    CPLMalloc( 6 * sizeof(double) * GetGCPCount() );

	for( iGCP = 0; iGCP < GetGCPCount(); iGCP++ )
	{

	    padfTiePoints[iGCP*6+0] = pasGCPList[iGCP].dfGCPPixel;
	    padfTiePoints[iGCP*6+1] = pasGCPList[iGCP].dfGCPLine;
	    padfTiePoints[iGCP*6+2] = 0;
	    padfTiePoints[iGCP*6+3] = pasGCPList[iGCP].dfGCPX;
	    padfTiePoints[iGCP*6+4] = pasGCPList[iGCP].dfGCPY;
	    padfTiePoints[iGCP*6+5] = pasGCPList[iGCP].dfGCPZ;

            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                padfTiePoints[iGCP*6+0] += 0.5;
                padfTiePoints[iGCP*6+1] += 0.5;
            }
	}

        if( !EQUAL(osProfile,"BASELINE") )
            TIFFSetField( hTIFF, TIFFTAG_GEOTIEPOINTS, 
                          6 * GetGCPCount(), padfTiePoints );
	CPLFree( padfTiePoints );
    }

/* -------------------------------------------------------------------- */
/*	Write out projection definition.				*/
/* -------------------------------------------------------------------- */
    if( pszProjection != NULL && !EQUAL( pszProjection, "" )
        && !EQUAL(osProfile,"BASELINE") )
    {
        GTIF	*psGTIF;

        bNeedsRewrite = TRUE;

        // If we have existing geokeys, try to wipe them
        // by writing a dummy goekey directory. (#2546)
        uint16 *panVI = NULL;
        uint16 nKeyCount;

        if( TIFFGetField( hTIFF, TIFFTAG_GEOKEYDIRECTORY, 
                          &nKeyCount, &panVI ) )
        {
            GUInt16 anGKVersionInfo[4] = { 1, 1, 0, 0 };
            double  adfDummyDoubleParams[1] = { 0.0 };
            TIFFSetField( hTIFF, TIFFTAG_GEOKEYDIRECTORY, 
                          4, anGKVersionInfo );
            TIFFSetField( hTIFF, TIFFTAG_GEODOUBLEPARAMS, 
                          1, adfDummyDoubleParams );
            TIFFSetField( hTIFF, TIFFTAG_GEOASCIIPARAMS, "" );
        }

        psGTIF = GTIFNew( hTIFF );  

        // set according to coordinate system.
        GTIFSetFromOGISDefn( psGTIF, pszProjection );

        if( bPixelIsPoint )
        {
            GTIFKeySet(psGTIF, GTRasterTypeGeoKey, TYPE_SHORT, 1,
                       RasterPixelIsPoint);
        }

        GTIFWriteKeys( psGTIF );
        GTIFFree( psGTIF );
    }
}

int  GTiffDataset::WriteMetadata( GDALDataset *poSrcDS, TIFF *hTIFF,
                                  int bSrcIsGeoTIFF,
                                  const char *pszProfile,
                                  const char *pszTIFFFilename,
                                  char **papszCreationOptions,
                                  int bExcludeRPBandIMGFileWriting)

{
/* -------------------------------------------------------------------- */
/*      Convert all the remaining metadata into a simple XML            */
/*      format.                                                         */
/* -------------------------------------------------------------------- */
    CPLXMLNode *psRoot = NULL, *psTail = NULL;

    if( bSrcIsGeoTIFF )
    {
        WriteMDMetadata( &(((GTiffDataset *)poSrcDS)->oGTiffMDMD), 
                         hTIFF, &psRoot, &psTail, 0, pszProfile );
    }
    else
    {
        char **papszMD = poSrcDS->GetMetadata();

        if( CSLCount(papszMD) > 0 )
        {
            GDALMultiDomainMetadata oMDMD;
            oMDMD.SetMetadata( papszMD );

            WriteMDMetadata( &oMDMD, hTIFF, &psRoot, &psTail, 0, pszProfile );
        }
    }

/* -------------------------------------------------------------------- */
/*      Handle RPC data written to an RPB file.                         */
/* -------------------------------------------------------------------- */
    char **papszRPCMD = poSrcDS->GetMetadata("RPC");
    if( papszRPCMD != NULL && !bExcludeRPBandIMGFileWriting )
    {
        if( EQUAL(pszProfile,"GDALGeoTIFF") )
            WriteRPCTag( hTIFF, papszRPCMD );

        if( !EQUAL(pszProfile,"GDALGeoTIFF") 
            || CSLFetchBoolean( papszCreationOptions, "RPB", FALSE ) )
        {
            GDALWriteRPBFile( pszTIFFFilename, papszRPCMD );
        }
    }

/* -------------------------------------------------------------------- */
/*      Handle metadata data written to an IMD file.                    */
/* -------------------------------------------------------------------- */
    char **papszIMDMD = poSrcDS->GetMetadata("IMD");
    if( papszIMDMD != NULL && !bExcludeRPBandIMGFileWriting)
    {
        GDALWriteIMDFile( pszTIFFFilename, papszIMDMD );
    }

/* -------------------------------------------------------------------- */
/*      We also need to address band specific metadata, and special     */
/*      "role" metadata.                                                */
/* -------------------------------------------------------------------- */
    int nBand;
    for( nBand = 1; nBand <= poSrcDS->GetRasterCount(); nBand++ )
    {
        GDALRasterBand *poBand = poSrcDS->GetRasterBand( nBand );

        if( bSrcIsGeoTIFF )
        {
            WriteMDMetadata( &(((GTiffRasterBand *)poBand)->oGTiffMDMD), 
                             hTIFF, &psRoot, &psTail, nBand, pszProfile );
        }
        else
        {
            char **papszMD = poBand->GetMetadata();

            if( CSLCount(papszMD) > 0 )
            {
                GDALMultiDomainMetadata oMDMD;
                oMDMD.SetMetadata( papszMD );

                WriteMDMetadata( &oMDMD, hTIFF, &psRoot, &psTail, nBand,
                                 pszProfile );
            }
        }

        int bSuccess;
        double dfOffset = poBand->GetOffset( &bSuccess );
        double dfScale = poBand->GetScale();

        if( bSuccess && (dfOffset != 0.0 || dfScale != 1.0) )
        {
            char szValue[128];

            sprintf( szValue, "%.18g", dfOffset );
            AppendMetadataItem( &psRoot, &psTail, "OFFSET", szValue, nBand, 
                                "offset", "" );
            sprintf( szValue, "%.18g", dfScale );
            AppendMetadataItem( &psRoot, &psTail, "SCALE", szValue, nBand, 
                                "scale", "" );
        }

        const char* pszUnitType = poBand->GetUnitType();
        if (pszUnitType != NULL && pszUnitType[0] != '\0')
            AppendMetadataItem( &psRoot, &psTail, "UNITTYPE", pszUnitType, nBand, 
                                "unittype", "" );
    }

/* -------------------------------------------------------------------- */
/*      Write out the generic XML metadata if there is any.             */
/* -------------------------------------------------------------------- */
    if( psRoot != NULL )
    {
        int bRet = TRUE;

        if( EQUAL(pszProfile,"GDALGeoTIFF") )
        {
            char *pszXML_MD = CPLSerializeXMLTree( psRoot );
            if( strlen(pszXML_MD) > 32000 )
            {
                if( bSrcIsGeoTIFF )
                    ((GTiffDataset *) poSrcDS)->PushMetadataToPam();
                else
                    bRet = FALSE;
                CPLError( CE_Warning, CPLE_AppDefined, 
                          "Lost metadata writing to GeoTIFF ... too large to fit in tag." );
            }
            else
            {
                TIFFSetField( hTIFF, TIFFTAG_GDAL_METADATA, pszXML_MD );
            }
            CPLFree( pszXML_MD );
        }
        else
        {
            if( bSrcIsGeoTIFF )
                ((GTiffDataset *) poSrcDS)->PushMetadataToPam();
            else
                bRet = FALSE;
        }

        CPLDestroyXMLNode( psRoot );

        return bRet;
    }
    else
    {
        /* If we have no more metadata but it existed before, remove the GDAL_METADATA tag */
        if( EQUAL(pszProfile,"GDALGeoTIFF") )
        {
            char* pszText = NULL;
            if( TIFFGetField( hTIFF, TIFFTAG_GDAL_METADATA, &pszText ) )
            {
#ifdef HAVE_UNSETFIELD
                TIFFUnsetField( hTIFF, TIFFTAG_GDAL_METADATA );
#else
                TIFFSetField( hTIFF, TIFFTAG_GDAL_METADATA, "" );
#endif
            }
        }
    }

    return TRUE;
}

int GTiffDataset::SetDirectory( toff_t nNewOffset )

{
    Crystalize();

    FlushBlockBuf();

    if( nNewOffset == 0 )
        nNewOffset = nDirOffset;

    if( TIFFCurrentDirOffset(hTIFF) == nNewOffset )
    {
        CPLAssert( *ppoActiveDSRef == this || *ppoActiveDSRef == NULL );
        *ppoActiveDSRef = this;
        return TRUE;
    }

    if( GetAccess() == GA_Update )
    {
        if( *ppoActiveDSRef != NULL )
            (*ppoActiveDSRef)->FlushDirectory();
    }

    if( nNewOffset == 0)
        return TRUE;

    (*ppoActiveDSRef) = this;

    int nSetDirResult = TIFFSetSubDirectory( hTIFF, nNewOffset );
    if (!nSetDirResult)
        return nSetDirResult;

/* -------------------------------------------------------------------- */
/*      YCbCr JPEG compressed images should be translated on the fly    */
/*      to RGB by libtiff/libjpeg unless specifically requested         */
/*      otherwise.                                                      */
/* -------------------------------------------------------------------- */
    if( !TIFFGetField( hTIFF, TIFFTAG_COMPRESSION, &(nCompression) ) )
        nCompression = COMPRESSION_NONE;

    if( !TIFFGetField( hTIFF, TIFFTAG_PHOTOMETRIC, &(nPhotometric) ) )
        nPhotometric = PHOTOMETRIC_MINISBLACK;

    if( nCompression == COMPRESSION_JPEG 
        && nPhotometric == PHOTOMETRIC_YCBCR 
        && CSLTestBoolean( CPLGetConfigOption("CONVERT_YCBCR_TO_RGB",
                                              "YES") ) )
    {
        int nColorMode;

        TIFFGetField( hTIFF, TIFFTAG_JPEGCOLORMODE, &nColorMode );
        if( nColorMode != JPEGCOLORMODE_RGB )
            TIFFSetField(hTIFF, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
    }

/* -------------------------------------------------------------------- */
/*      Propogate any quality settings.                                 */
/* -------------------------------------------------------------------- */
    if( GetAccess() == GA_Update )
    {
        // Now, reset zip and jpeg quality.
        if(nJpegQuality > 0 && nCompression == COMPRESSION_JPEG)
        {
            CPLDebug( "GTiff", "Propgate JPEG_QUALITY(%d) in SetDirectory()",
                      nJpegQuality );
            TIFFSetField(hTIFF, TIFFTAG_JPEGQUALITY, nJpegQuality); 
        }
        if(nZLevel > 0 && nCompression == COMPRESSION_ADOBE_DEFLATE)
            TIFFSetField(hTIFF, TIFFTAG_ZIPQUALITY, nZLevel);
        if(nLZMAPreset > 0 && nCompression == COMPRESSION_LZMA)
            TIFFSetField(hTIFF, TIFFTAG_LZMAPRESET, nLZMAPreset);
    }

    return nSetDirResult;
}

CPLErr GTiffDataset::OpenOffset( TIFF *hTIFFIn, 
                                 GTiffDataset **ppoActiveDSRef,
                                 toff_t nDirOffsetIn, 
				 int bBaseIn, GDALAccess eAccess,
                                 int bAllowRGBAInterface,
                                 int bReadGeoTransform,
                                 char** papszSiblingFiles )

{
    uint32	nXSize, nYSize;
    int		bTreatAsBitmap = FALSE;
    int         bTreatAsOdd = FALSE;

    this->eAccess = eAccess;

    hTIFF = hTIFFIn;
    this->ppoActiveDSRef = ppoActiveDSRef;

    nDirOffset = nDirOffsetIn;

    if (!SetDirectory( nDirOffsetIn ))
        return CE_Failure;

    bBase = bBaseIn;

    this->eAccess = eAccess;

/* -------------------------------------------------------------------- */
/*      Capture some information from the file that is of interest.     */
/* -------------------------------------------------------------------- */
    TIFFGetField( hTIFF, TIFFTAG_IMAGEWIDTH, &nXSize );
    TIFFGetField( hTIFF, TIFFTAG_IMAGELENGTH, &nYSize );
    nRasterXSize = nXSize;
    nRasterYSize = nYSize;

    if( !TIFFGetField(hTIFF, TIFFTAG_SAMPLESPERPIXEL, &nSamplesPerPixel ) )
        nBands = 1;
    else
        nBands = nSamplesPerPixel;

    if( !TIFFGetField(hTIFF, TIFFTAG_BITSPERSAMPLE, &(nBitsPerSample)) )
        nBitsPerSample = 1;

    if( !TIFFGetField( hTIFF, TIFFTAG_PLANARCONFIG, &(nPlanarConfig) ) )
        nPlanarConfig = PLANARCONFIG_CONTIG;

    if( !TIFFGetField( hTIFF, TIFFTAG_PHOTOMETRIC, &(nPhotometric) ) )
        nPhotometric = PHOTOMETRIC_MINISBLACK;

    if( !TIFFGetField( hTIFF, TIFFTAG_SAMPLEFORMAT, &(nSampleFormat) ) )
        nSampleFormat = SAMPLEFORMAT_UINT;

    if( !TIFFGetField( hTIFF, TIFFTAG_COMPRESSION, &(nCompression) ) )
        nCompression = COMPRESSION_NONE;

#if defined(TIFFLIB_VERSION) && TIFFLIB_VERSION > 20031007 /* 3.6.0 */
    if (nCompression != COMPRESSION_NONE &&
        !TIFFIsCODECConfigured(nCompression))
    {
        CPLError( CE_Failure, CPLE_AppDefined,
                  "Cannot open TIFF file due to missing codec." );
        return CE_Failure;
    }
#endif

/* -------------------------------------------------------------------- */
/*      YCbCr JPEG compressed images should be translated on the fly    */
/*      to RGB by libtiff/libjpeg unless specifically requested         */
/*      otherwise.                                                      */
/* -------------------------------------------------------------------- */
    if( nCompression == COMPRESSION_JPEG 
        && nPhotometric == PHOTOMETRIC_YCBCR 
        && CSLTestBoolean( CPLGetConfigOption("CONVERT_YCBCR_TO_RGB",
                                              "YES") ) )
    {
        int nColorMode;

        SetMetadataItem( "SOURCE_COLOR_SPACE", "YCbCr", "IMAGE_STRUCTURE" );
        if ( !TIFFGetField( hTIFF, TIFFTAG_JPEGCOLORMODE, &nColorMode ) ||
              nColorMode != JPEGCOLORMODE_RGB )
            TIFFSetField(hTIFF, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
    }

/* -------------------------------------------------------------------- */
/*      Get strip/tile layout.                                          */
/* -------------------------------------------------------------------- */
    if( TIFFIsTiled(hTIFF) )
    {
        TIFFGetField( hTIFF, TIFFTAG_TILEWIDTH, &(nBlockXSize) );
        TIFFGetField( hTIFF, TIFFTAG_TILELENGTH, &(nBlockYSize) );
    }
    else
    {
        if( !TIFFGetField( hTIFF, TIFFTAG_ROWSPERSTRIP,
                           &(nRowsPerStrip) ) )
        {
            CPLError( CE_Warning, CPLE_AppDefined, 
                      "RowsPerStrip not defined ... assuming all one strip." );
            nRowsPerStrip = nYSize; /* dummy value */
        }

        nBlockXSize = nRasterXSize;
        nBlockYSize = MIN(nRowsPerStrip,nYSize);
    }

    nBlocksPerBand =
        ((nYSize + nBlockYSize - 1) / nBlockYSize)
        * ((nXSize + nBlockXSize  - 1) / nBlockXSize);

/* -------------------------------------------------------------------- */
/*      Should we handle this using the GTiffBitmapBand?                */
/* -------------------------------------------------------------------- */
    if( nBitsPerSample == 1 && nBands == 1 )
    {
        bTreatAsBitmap = TRUE;

        // Lets treat large "one row" bitmaps using the scanline api.
        if( !TIFFIsTiled(hTIFF) 
            && nBlockYSize == nYSize 
            && nYSize > 2000 
            && bAllowRGBAInterface )
            bTreatAsSplitBitmap = TRUE;
    }

/* -------------------------------------------------------------------- */
/*      Should we treat this via the RGBA interface?                    */
/* -------------------------------------------------------------------- */
    if( bAllowRGBAInterface &&
        !bTreatAsBitmap && !(nBitsPerSample > 8) 
        && (nPhotometric == PHOTOMETRIC_CIELAB ||
            nPhotometric == PHOTOMETRIC_LOGL ||
            nPhotometric == PHOTOMETRIC_LOGLUV ||
            nPhotometric == PHOTOMETRIC_SEPARATED ||
            ( nPhotometric == PHOTOMETRIC_YCBCR 
              && nCompression != COMPRESSION_JPEG )) )
    {
        char	szMessage[1024];

        if( TIFFRGBAImageOK( hTIFF, szMessage ) == 1 )
        {
            const char* pszSourceColorSpace = NULL;
            switch (nPhotometric)
            {
                case PHOTOMETRIC_CIELAB:
                    pszSourceColorSpace = "CIELAB";
                    break;
                case PHOTOMETRIC_LOGL:
                    pszSourceColorSpace = "LOGL";
                    break;
                case PHOTOMETRIC_LOGLUV:
                    pszSourceColorSpace = "LOGLUV";
                    break;
                case PHOTOMETRIC_SEPARATED:
                    pszSourceColorSpace = "CMYK";
                    break;
                case PHOTOMETRIC_YCBCR:
                    pszSourceColorSpace = "YCbCr";
                    break;
            }
            if (pszSourceColorSpace)
                SetMetadataItem( "SOURCE_COLOR_SPACE", pszSourceColorSpace, "IMAGE_STRUCTURE" );
            bTreatAsRGBA = TRUE;
            nBands = 4;
        }
        else
        {
            CPLDebug( "GTiff", "TIFFRGBAImageOK says:\n%s", szMessage );
        }
    }

/* -------------------------------------------------------------------- */
/*      Should we treat this via the split interface?                   */
/* -------------------------------------------------------------------- */
    if( !TIFFIsTiled(hTIFF) 
        && nBitsPerSample == 8
        && nBlockYSize == nYSize 
        && nYSize > 2000
        && !bTreatAsRGBA 
        && CSLTestBoolean(CPLGetConfigOption("GDAL_ENABLE_TIFF_SPLIT", "YES")))
    {
        /* libtiff 3.9.2 (20091104) and older, libtiff 4.0.0beta5 (also 20091104) */
        /* and older will crash when trying to open a all-in-one-strip */
        /* YCbCr JPEG compressed TIFF (see #3259). */
#if (TIFFLIB_VERSION <= 20091104 && !defined(BIGTIFF_SUPPORT)) || \
    (TIFFLIB_VERSION <= 20091104 && defined(BIGTIFF_SUPPORT))
        if (nPhotometric == PHOTOMETRIC_YCBCR  &&
            nCompression == COMPRESSION_JPEG)
        {
            CPLDebug("GTiff", "Avoid using split band to open all-in-one-strip "
                              "YCbCr JPEG compressed TIFF because of older libtiff");
        }
        else
#endif
            bTreatAsSplit = TRUE;
    }

/* -------------------------------------------------------------------- */
/*      Should we treat this via the odd bits interface?                */
/* -------------------------------------------------------------------- */
    if ( nSampleFormat == SAMPLEFORMAT_IEEEFP )
    {
        if ( nBitsPerSample == 16 || nBitsPerSample == 24 )
            bTreatAsOdd = TRUE;
    }
    else if ( !bTreatAsRGBA && !bTreatAsBitmap
              && nBitsPerSample != 8
              && nBitsPerSample != 16
              && nBitsPerSample != 32
              && nBitsPerSample != 64 
              && nBitsPerSample != 128 )
        bTreatAsOdd = TRUE;

    int bMinIsWhite = nPhotometric == PHOTOMETRIC_MINISWHITE;

/* -------------------------------------------------------------------- */
/*      Capture the color table if there is one.                        */
/* -------------------------------------------------------------------- */
    unsigned short	*panRed, *panGreen, *panBlue;

    if( bTreatAsRGBA 
        || TIFFGetField( hTIFF, TIFFTAG_COLORMAP, 
                         &panRed, &panGreen, &panBlue) == 0 )
    {
	// Build inverted palette if we have inverted photometric.
	// Pixel values remains unchanged.  Avoid doing this for *deep*
        // data types (per #1882)
	if( nBitsPerSample <= 16 && nPhotometric == PHOTOMETRIC_MINISWHITE )
	{
	    GDALColorEntry  oEntry;
	    int		    iColor, nColorCount;

	    poColorTable = new GDALColorTable();
	    nColorCount = 1 << nBitsPerSample;

	    for ( iColor = 0; iColor < nColorCount; iColor++ )
	    {
		oEntry.c1 = oEntry.c2 = oEntry.c3 = (short) 
                    ((255 * (nColorCount - 1 - iColor)) / (nColorCount-1));
		oEntry.c4 = 255;
		poColorTable->SetColorEntry( iColor, &oEntry );
	    }

	    nPhotometric = PHOTOMETRIC_PALETTE;
	}
	else
	    poColorTable = NULL;
    }
    else
    {
        int	nColorCount, nMaxColor = 0;
        GDALColorEntry oEntry;

        poColorTable = new GDALColorTable();

        nColorCount = 1 << nBitsPerSample;

        for( int iColor = nColorCount - 1; iColor >= 0; iColor-- )
        {
            oEntry.c1 = panRed[iColor] / 256;
            oEntry.c2 = panGreen[iColor] / 256;
            oEntry.c3 = panBlue[iColor] / 256;
            oEntry.c4 = 255;

            poColorTable->SetColorEntry( iColor, &oEntry );

            nMaxColor = MAX(nMaxColor,panRed[iColor]);
            nMaxColor = MAX(nMaxColor,panGreen[iColor]);
            nMaxColor = MAX(nMaxColor,panBlue[iColor]);
        }

        // Bug 1384 - Some TIFF files are generated with color map entry
        // values in range 0-255 instead of 0-65535 - try to handle these
        // gracefully.
        if( nMaxColor > 0 && nMaxColor < 256 )
        {
            CPLDebug( "GTiff", "TIFF ColorTable seems to be improperly scaled, fixing up." );

            for( int iColor = nColorCount - 1; iColor >= 0; iColor-- )
            {
                oEntry.c1 = panRed[iColor];
                oEntry.c2 = panGreen[iColor];
                oEntry.c3 = panBlue[iColor];
                oEntry.c4 = 255;

                poColorTable->SetColorEntry( iColor, &oEntry );
            }
        }
    }

/* -------------------------------------------------------------------- */
/*      Create band information objects.                                */
/* -------------------------------------------------------------------- */
    for( int iBand = 0; iBand < nBands; iBand++ )
    {
        if( bTreatAsRGBA )
            SetBand( iBand+1, new GTiffRGBABand( this, iBand+1 ) );
        else if( bTreatAsSplitBitmap )
            SetBand( iBand+1, new GTiffSplitBitmapBand( this, iBand+1 ) );
        else if( bTreatAsSplit )
            SetBand( iBand+1, new GTiffSplitBand( this, iBand+1 ) );
        else if( bTreatAsBitmap )
            SetBand( iBand+1, new GTiffBitmapBand( this, iBand+1 ) );
        else if( bTreatAsOdd )
            SetBand( iBand+1, new GTiffOddBitsBand( this, iBand+1 ) );
        else
            SetBand( iBand+1, new GTiffRasterBand( this, iBand+1 ) );
    }

    if( GetRasterBand(1)->GetRasterDataType() == GDT_Unknown )
    {
        CPLError( CE_Failure, CPLE_NotSupported,
                  "Unsupported TIFF configuration." );
        return CE_Failure;
    }

/* -------------------------------------------------------------------- */
/*      Get the transform or gcps from the GeoTIFF file.                */
/* -------------------------------------------------------------------- */
    if( bReadGeoTransform )
    {
        char    *pszTabWKT = NULL;
        double	*padfTiePoints, *padfScale, *padfMatrix;
        uint16	nCount;
        bool    bPixelIsPoint = false;
        short nRasterType;
        GTIF	*psGTIF;
        int     bPointGeoIgnore = FALSE;

        psGTIF = GTIFNew( hTIFF ); // I wonder how expensive this is?

        if( psGTIF )
        {
            if( GTIFKeyGet(psGTIF, GTRasterTypeGeoKey, &nRasterType,
                        0, 1 ) == 1
                && nRasterType == (short) RasterPixelIsPoint )
            {
                bPixelIsPoint = true;
                bPointGeoIgnore =
                    CSLTestBoolean( CPLGetConfigOption("GTIFF_POINT_GEO_IGNORE",
                                                    "FALSE") );
            }

            GTIFFree( psGTIF );
        }

        adfGeoTransform[0] = 0.0;
        adfGeoTransform[1] = 1.0;
        adfGeoTransform[2] = 0.0;
        adfGeoTransform[3] = 0.0;
        adfGeoTransform[4] = 0.0;
        adfGeoTransform[5] = 1.0;

        if( TIFFGetField(hTIFF,TIFFTAG_GEOPIXELSCALE,&nCount,&padfScale )
            && nCount >= 2 
            && padfScale[0] != 0.0 && padfScale[1] != 0.0 )
        {
            adfGeoTransform[1] = padfScale[0];
            adfGeoTransform[5] = - ABS(padfScale[1]);

            if( TIFFGetField(hTIFF,TIFFTAG_GEOTIEPOINTS,&nCount,&padfTiePoints )
                && nCount >= 6 )
            {
                adfGeoTransform[0] =
                    padfTiePoints[3] - padfTiePoints[0] * adfGeoTransform[1];
                adfGeoTransform[3] =
                    padfTiePoints[4] - padfTiePoints[1] * adfGeoTransform[5];

                if( bPixelIsPoint && !bPointGeoIgnore )
                {
                    adfGeoTransform[0] -= (adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5);
                    adfGeoTransform[3] -= (adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5);
                }

                bGeoTransformValid = TRUE;
            }
        }

        else if( TIFFGetField(hTIFF,TIFFTAG_GEOTRANSMATRIX,&nCount,&padfMatrix ) 
                 && nCount == 16 )
        {
            adfGeoTransform[0] = padfMatrix[3];
            adfGeoTransform[1] = padfMatrix[0];
            adfGeoTransform[2] = padfMatrix[1];
            adfGeoTransform[3] = padfMatrix[7];
            adfGeoTransform[4] = padfMatrix[4];
            adfGeoTransform[5] = padfMatrix[5];

            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                adfGeoTransform[0] -= (adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5);
                adfGeoTransform[3] -= (adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5);
            }

            bGeoTransformValid = TRUE;
        }

/* -------------------------------------------------------------------- */
/*      Otherwise try looking for a .tfw, .tifw or .wld file.           */
/* -------------------------------------------------------------------- */
        else
        {
            char* pszWldFilename = NULL;

            bGeoTransformValid =
                GDALReadWorldFile2( osFilename, NULL, adfGeoTransform,
                                    papszSiblingFiles, &pszWldFilename);

            if( !bGeoTransformValid )
            {
                bGeoTransformValid =
                    GDALReadWorldFile2( osFilename, "wld", adfGeoTransform,
                                        papszSiblingFiles, &pszWldFilename);
            }

            if( !bGeoTransformValid )
            {
                int bTabFileOK =
                    GDALReadTabFile2( osFilename, adfGeoTransform,
                                      &pszTabWKT, &nGCPCount, &pasGCPList,
                                      papszSiblingFiles, &pszWldFilename );

                if( bTabFileOK && nGCPCount == 0 )
                    bGeoTransformValid = TRUE;
            }

            if (pszWldFilename)
            {
                osWldFilename = pszWldFilename;
                CPLFree(pszWldFilename);
            }
        }

/* -------------------------------------------------------------------- */
/*      Check for GCPs.  Note, we will allow there to be GCPs and a     */
/*      transform in some circumstances.                                */
/* -------------------------------------------------------------------- */
        if( TIFFGetField(hTIFF,TIFFTAG_GEOTIEPOINTS,&nCount,&padfTiePoints )
            && !bGeoTransformValid )
        {
            nGCPCount = nCount / 6;
            pasGCPList = (GDAL_GCP *) CPLCalloc(sizeof(GDAL_GCP),nGCPCount);

            for( int iGCP = 0; iGCP < nGCPCount; iGCP++ )
            {
                char	szID[32];

                sprintf( szID, "%d", iGCP+1 );
                pasGCPList[iGCP].pszId = CPLStrdup( szID );
                pasGCPList[iGCP].pszInfo = CPLStrdup("");
                pasGCPList[iGCP].dfGCPPixel = padfTiePoints[iGCP*6+0];
                pasGCPList[iGCP].dfGCPLine = padfTiePoints[iGCP*6+1];
                pasGCPList[iGCP].dfGCPX = padfTiePoints[iGCP*6+3];
                pasGCPList[iGCP].dfGCPY = padfTiePoints[iGCP*6+4];
                pasGCPList[iGCP].dfGCPZ = padfTiePoints[iGCP*6+5];

                if( bPixelIsPoint && !bPointGeoIgnore )
                {
                    pasGCPList[iGCP].dfGCPPixel -= 0.5;
                    pasGCPList[iGCP].dfGCPLine -= 0.5;
                }
            }
        }

/* -------------------------------------------------------------------- */
/*      Did we find a tab file?  If so we will use it's coordinate      */
/*      system and give it precidence.                                  */
/* -------------------------------------------------------------------- */
        if( pszTabWKT != NULL 
            && (pszProjection == NULL || pszProjection[0] == '\0') )
        {
            CPLFree( pszProjection );
            pszProjection = pszTabWKT;
            pszTabWKT = NULL;
            bLookedForProjection = TRUE;
        }

        CPLFree( pszTabWKT );
        bGeoTIFFInfoChanged = FALSE;
    }

/* -------------------------------------------------------------------- */
/*      Capture some other potentially interesting information.         */
/* -------------------------------------------------------------------- */
    char	*pszText, szWorkMDI[200];
    float   fResolution;
    uint16  nShort;

    if( TIFFGetField( hTIFF, TIFFTAG_DOCUMENTNAME, &pszText ) )
        SetMetadataItem( "TIFFTAG_DOCUMENTNAME",  pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_IMAGEDESCRIPTION, &pszText ) )
        SetMetadataItem( "TIFFTAG_IMAGEDESCRIPTION", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_SOFTWARE, &pszText ) )
        SetMetadataItem( "TIFFTAG_SOFTWARE", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_DATETIME, &pszText ) )
        SetMetadataItem( "TIFFTAG_DATETIME", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_ARTIST, &pszText ) )
        SetMetadataItem( "TIFFTAG_ARTIST", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_HOSTCOMPUTER, &pszText ) )
        SetMetadataItem( "TIFFTAG_HOSTCOMPUTER", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_COPYRIGHT, &pszText ) )
        SetMetadataItem( "TIFFTAG_COPYRIGHT", pszText );

    if( TIFFGetField( hTIFF, TIFFTAG_XRESOLUTION, &fResolution ) )
    {
        sprintf( szWorkMDI, "%.8g", fResolution );
        SetMetadataItem( "TIFFTAG_XRESOLUTION", szWorkMDI );
    }

    if( TIFFGetField( hTIFF, TIFFTAG_YRESOLUTION, &fResolution ) )
    {
        sprintf( szWorkMDI, "%.8g", fResolution );
        SetMetadataItem( "TIFFTAG_YRESOLUTION", szWorkMDI );
    }

    if( TIFFGetField( hTIFF, TIFFTAG_MINSAMPLEVALUE, &nShort ) )
    {
        sprintf( szWorkMDI, "%d", nShort );
        SetMetadataItem( "TIFFTAG_MINSAMPLEVALUE", szWorkMDI );
    }

    if( TIFFGetField( hTIFF, TIFFTAG_MAXSAMPLEVALUE, &nShort ) )
    {
        sprintf( szWorkMDI, "%d", nShort );
        SetMetadataItem( "TIFFTAG_MAXSAMPLEVALUE", szWorkMDI );
    }

    if( TIFFGetField( hTIFF, TIFFTAG_RESOLUTIONUNIT, &nShort ) )
    {
        if( nShort == RESUNIT_NONE )
            sprintf( szWorkMDI, "%d (unitless)", nShort );
        else if( nShort == RESUNIT_INCH )
            sprintf( szWorkMDI, "%d (pixels/inch)", nShort );
        else if( nShort == RESUNIT_CENTIMETER )
            sprintf( szWorkMDI, "%d (pixels/cm)", nShort );
        else
            sprintf( szWorkMDI, "%d", nShort );
        SetMetadataItem( "TIFFTAG_RESOLUTIONUNIT", szWorkMDI );
    }

    if( nCompression == COMPRESSION_NONE )
        /* no compression tag */;
    else if( nCompression == COMPRESSION_CCITTRLE )
        SetMetadataItem( "COMPRESSION", "CCITTRLE", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_CCITTFAX3 )
        SetMetadataItem( "COMPRESSION", "CCITTFAX3", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_CCITTFAX4 )
        SetMetadataItem( "COMPRESSION", "CCITTFAX4", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_LZW )
        SetMetadataItem( "COMPRESSION", "LZW", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_OJPEG )
        SetMetadataItem( "COMPRESSION", "OJPEG", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_JPEG )
    {  
        if ( nPhotometric == PHOTOMETRIC_YCBCR )
            SetMetadataItem( "COMPRESSION", "YCbCr JPEG", "IMAGE_STRUCTURE" );
        else
            SetMetadataItem( "COMPRESSION", "JPEG", "IMAGE_STRUCTURE" );
    }
    else if( nCompression == COMPRESSION_NEXT )
        SetMetadataItem( "COMPRESSION", "NEXT", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_CCITTRLEW )
        SetMetadataItem( "COMPRESSION", "CCITTRLEW", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_PACKBITS )
        SetMetadataItem( "COMPRESSION", "PACKBITS", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_THUNDERSCAN )
        SetMetadataItem( "COMPRESSION", "THUNDERSCAN", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_PIXARFILM )
        SetMetadataItem( "COMPRESSION", "PIXARFILM", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_PIXARLOG )
        SetMetadataItem( "COMPRESSION", "PIXARLOG", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_DEFLATE )
        SetMetadataItem( "COMPRESSION", "DEFLATE", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_ADOBE_DEFLATE )
        SetMetadataItem( "COMPRESSION", "DEFLATE", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_DCS )
        SetMetadataItem( "COMPRESSION", "DCS", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_JBIG )
        SetMetadataItem( "COMPRESSION", "JBIG", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_SGILOG )
        SetMetadataItem( "COMPRESSION", "SGILOG", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_SGILOG24 )
        SetMetadataItem( "COMPRESSION", "SGILOG24", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_JP2000 )
        SetMetadataItem( "COMPRESSION", "JP2000", "IMAGE_STRUCTURE" );
    else if( nCompression == COMPRESSION_LZMA )
        SetMetadataItem( "COMPRESSION", "LZMA", "IMAGE_STRUCTURE" );

    else
    {
        CPLString oComp;
        SetMetadataItem( "COMPRESSION", 
                         (const char *) oComp.Printf( "%d", nCompression));
    }

    if( nPlanarConfig == PLANARCONFIG_CONTIG && nBands != 1 )
        SetMetadataItem( "INTERLEAVE", "PIXEL", "IMAGE_STRUCTURE" );
    else
        SetMetadataItem( "INTERLEAVE", "BAND", "IMAGE_STRUCTURE" );

    if(  (GetRasterBand(1)->GetRasterDataType() == GDT_Byte   && nBitsPerSample != 8 ) ||
         (GetRasterBand(1)->GetRasterDataType() == GDT_UInt16 && nBitsPerSample != 16) ||
         (GetRasterBand(1)->GetRasterDataType() == GDT_UInt32 && nBitsPerSample != 32) )
    {
        for (int i = 0; i < nBands; ++i)
            GetRasterBand(i+1)->SetMetadataItem( "NBITS", 
                                                 CPLString().Printf( "%d", (int)nBitsPerSample ),
                                                 "IMAGE_STRUCTURE" );
    }

    if( bMinIsWhite )
        SetMetadataItem( "MINISWHITE", "YES", "IMAGE_STRUCTURE" );

    if( TIFFGetField( hTIFF, TIFFTAG_GDAL_METADATA, &pszText ) )
    {
        CPLXMLNode *psRoot = CPLParseXMLString( pszText );
        CPLXMLNode *psItem = NULL;

        if( psRoot != NULL && psRoot->eType == CXT_Element
            && EQUAL(psRoot->pszValue,"GDALMetadata") )
            psItem = psRoot->psChild;

        for( ; psItem != NULL; psItem = psItem->psNext )
        {
            const char *pszKey, *pszValue, *pszRole, *pszDomain; 
            char *pszUnescapedValue;
            int nBand, bIsXML = FALSE;

            if( psItem->eType != CXT_Element
                || !EQUAL(psItem->pszValue,"Item") )
                continue;

            pszKey = CPLGetXMLValue( psItem, "name", NULL );
            pszValue = CPLGetXMLValue( psItem, NULL, NULL );
            nBand = atoi(CPLGetXMLValue( psItem, "sample", "-1" )) + 1;
            pszRole = CPLGetXMLValue( psItem, "role", "" );
            pszDomain = CPLGetXMLValue( psItem, "domain", "" );

            if( pszKey == NULL || pszValue == NULL )
                continue;

            if( EQUALN(pszDomain,"xml:",4) )
                bIsXML = TRUE;

            pszUnescapedValue = CPLUnescapeString( pszValue, NULL, 
                                                   CPLES_XML );
            if( nBand == 0 )
            {
                if( bIsXML )
                {
                    char *apszMD[2] = { pszUnescapedValue, NULL };
                    SetMetadata( apszMD, pszDomain );
                }
                else
                    SetMetadataItem( pszKey, pszUnescapedValue, pszDomain );
            }
            else
            {
                GDALRasterBand *poBand = GetRasterBand(nBand);
                if( poBand != NULL )
                {
                    if( EQUAL(pszRole,"scale") )
                        poBand->SetScale( CPLAtofM(pszUnescapedValue) );
                    else if( EQUAL(pszRole,"offset") )
                        poBand->SetOffset( CPLAtofM(pszUnescapedValue) );
                    else if( EQUAL(pszRole,"unittype") )
                        poBand->SetUnitType( pszUnescapedValue );
                    else
                    {
                        if( bIsXML )
                        {
                            char *apszMD[2] = { pszUnescapedValue, NULL };
                            poBand->SetMetadata( apszMD, pszDomain );
                        }
                        else
                            poBand->SetMetadataItem(pszKey,pszUnescapedValue,
                                                    pszDomain );
                    }
                }
            }
            CPLFree( pszUnescapedValue );
        }

        CPLDestroyXMLNode( psRoot );
    }

    bMetadataChanged = FALSE;

/* -------------------------------------------------------------------- */
/*      Check for NODATA                                                */
/* -------------------------------------------------------------------- */
    if( TIFFGetField( hTIFF, TIFFTAG_GDAL_NODATA, &pszText ) )
    {
        bNoDataSet = TRUE;
        dfNoDataValue = CPLAtofM( pszText );
    }

/* -------------------------------------------------------------------- */
/*      If this is a "base" raster, we should scan for any              */
/*      associated overviews, internal mask bands and subdatasets.      */
/* -------------------------------------------------------------------- */
    if( bBase )
    {
        char **papszSubdatasets = NULL;
        int  iDirIndex = 0;

        FlushDirectory();  
        while( !TIFFLastDirectory( hTIFF ) 
               && (iDirIndex == 0 || TIFFReadDirectory( hTIFF ) != 0) )
        {
            toff_t	nThisDir = TIFFCurrentDirOffset(hTIFF);
            uint32	nSubType = 0;

            *ppoActiveDSRef = NULL; // our directory no longer matches this ds

            iDirIndex++;

            if( !TIFFGetField(hTIFF, TIFFTAG_SUBFILETYPE, &nSubType) )
                nSubType = 0;

            /* Embedded overview of the main image */
            if ((nSubType & FILETYPE_REDUCEDIMAGE) != 0 &&
                (nSubType & FILETYPE_MASK) == 0 &&
                iDirIndex != 1 )
            {
                GTiffDataset	*poODS;

                poODS = new GTiffDataset();
                if( poODS->OpenOffset( hTIFF, ppoActiveDSRef, nThisDir, FALSE, 
                                       eAccess ) != CE_None 
                    || poODS->GetRasterCount() != GetRasterCount() )
                {
                    delete poODS;
                }
                else
                {
                    CPLDebug( "GTiff", "Opened %dx%d overview.\n", 
                              poODS->GetRasterXSize(), poODS->GetRasterYSize());
                    nOverviewCount++;
                    papoOverviewDS = (GTiffDataset **)
                        CPLRealloc(papoOverviewDS, 
                                   nOverviewCount * (sizeof(void*)));
                    papoOverviewDS[nOverviewCount-1] = poODS;
                    poODS->poBaseDS = this;
                }
            }

            /* Embedded mask of the main image */
            else if ((nSubType & FILETYPE_MASK) != 0 &&
                     (nSubType & FILETYPE_REDUCEDIMAGE) == 0 &&
                     poMaskDS == NULL )
            {
                poMaskDS = new GTiffDataset();

                /* The TIFF6 specification - page 37 - only allows 1 SamplesPerPixel and 1 BitsPerSample
                   Here we support either 1 or 8 bit per sample
                   and we support either 1 sample per pixel or as many samples as in the main image
                   We don't check the value of the PhotometricInterpretation tag, which should be
                   set to "Transparency mask" (4) according to the specification (page 36)
                   ... But the TIFF6 specification allows image masks to have a higher resolution than
                   the main image, what we don't support here
                */

                if( poMaskDS->OpenOffset( hTIFF, ppoActiveDSRef, nThisDir, 
                                          FALSE, eAccess ) != CE_None 
                    || poMaskDS->GetRasterCount() == 0
                    || !(poMaskDS->GetRasterCount() == 1 || poMaskDS->GetRasterCount() == GetRasterCount())
                    || poMaskDS->GetRasterXSize() != GetRasterXSize()
                    || poMaskDS->GetRasterYSize() != GetRasterYSize()
                    || poMaskDS->GetRasterBand(1)->GetRasterDataType() != GDT_Byte)
                {
                    delete poMaskDS;
                    poMaskDS = NULL;
                }
                else
                {
                    CPLDebug( "GTiff", "Opened band mask.\n");
                    poMaskDS->poBaseDS = this;

                    poMaskDS->bPromoteTo8Bits = CSLTestBoolean(CPLGetConfigOption("GDAL_TIFF_INTERNAL_MASK_TO_8BIT", "YES"));
                }
            }

            /* Embedded mask of an overview */
            /* The TIFF6 specification allows the combination of the FILETYPE_xxxx masks */
            else if ((nSubType & FILETYPE_REDUCEDIMAGE) != 0 &&
                     (nSubType & FILETYPE_MASK) != 0)
            {
                GTiffDataset* poDS = new GTiffDataset();
                if( poDS->OpenOffset( hTIFF, ppoActiveDSRef, nThisDir, FALSE, 
                                      eAccess ) != CE_None
                    || poDS->GetRasterCount() == 0
                    || poDS->GetRasterBand(1)->GetRasterDataType() != GDT_Byte)
                {
                    delete poDS;
                }
                else
                {
                    int i;
                    for(i=0;i<nOverviewCount;i++)
                    {
                        if (((GTiffDataset*)papoOverviewDS[i])->poMaskDS == NULL &&
                            poDS->GetRasterXSize() == papoOverviewDS[i]->GetRasterXSize() &&
                            poDS->GetRasterYSize() == papoOverviewDS[i]->GetRasterYSize() &&
                            (poDS->GetRasterCount() == 1 || poDS->GetRasterCount() == GetRasterCount()))
                        {
                            CPLDebug( "GTiff", "Opened band mask for %dx%d overview.\n",
                                      poDS->GetRasterXSize(), poDS->GetRasterYSize());
                            ((GTiffDataset*)papoOverviewDS[i])->poMaskDS = poDS;
                            poDS->bPromoteTo8Bits = CSLTestBoolean(CPLGetConfigOption("GDAL_TIFF_INTERNAL_MASK_TO_8BIT", "YES"));
                            poDS->poBaseDS = this;
                            break;
                        }
                    }
                    if (i == nOverviewCount)
                    {
                        delete poDS;
                    }
                }
            }
            else if( nSubType == 0 ) {
                CPLString osName, osDesc;
                uint32	nXSize, nYSize;
                uint16  nSPP;

                TIFFGetField( hTIFF, TIFFTAG_IMAGEWIDTH, &nXSize );
                TIFFGetField( hTIFF, TIFFTAG_IMAGELENGTH, &nYSize );
                if( !TIFFGetField(hTIFF, TIFFTAG_SAMPLESPERPIXEL, &nSPP ) )
                    nSPP = 1;

                osName.Printf( "SUBDATASET_%d_NAME=GTIFF_DIR:%d:%s", 
                               iDirIndex, iDirIndex, osFilename.c_str() );
                osDesc.Printf( "SUBDATASET_%d_DESC=Page %d (%dP x %dL x %dB)", 
                               iDirIndex, iDirIndex, 
                               (int)nXSize, (int)nYSize, nSPP );

                papszSubdatasets = 
                    CSLAddString( papszSubdatasets, osName );
                papszSubdatasets = 
                    CSLAddString( papszSubdatasets, osDesc );
            }

            // Make sure we are stepping from the expected directory regardless
            // of churn done processing the above.
            if( TIFFCurrentDirOffset(hTIFF) != nThisDir )
                TIFFSetSubDirectory( hTIFF, nThisDir );
            *ppoActiveDSRef = NULL;
        }

        /* If we have a mask for the main image, loop over the overviews, and if they */
        /* have a mask, let's set this mask as an overview of the main mask... */
        if (poMaskDS != NULL)
        {
            int i;
            for(i=0;i<nOverviewCount;i++)
            {
                if (((GTiffDataset*)papoOverviewDS[i])->poMaskDS != NULL)
                {
                    poMaskDS->nOverviewCount++;
                    poMaskDS->papoOverviewDS = (GTiffDataset **)
                        CPLRealloc(poMaskDS->papoOverviewDS, 
                                poMaskDS->nOverviewCount * (sizeof(void*)));
                    poMaskDS->papoOverviewDS[poMaskDS->nOverviewCount-1] =
                            ((GTiffDataset*)papoOverviewDS[i])->poMaskDS;
                }
            }
        }

/* -------------------------------------------------------------------- */
/*      Only keep track of subdatasets if we have more than one         */
/*      subdataset (pair).                                              */
/* -------------------------------------------------------------------- */
        if( CSLCount(papszSubdatasets) > 2 )
        {
            oGTiffMDMD.SetMetadata( papszSubdatasets, "SUBDATASETS" );
        }
        CSLDestroy( papszSubdatasets );
    }

    return( CE_None );
}

GDALDataset *GTiffDataset::Create( const char * pszFilename,
                                   int nXSize, int nYSize, int nBands,
                                   GDALDataType eType,
                                   char **papszParmList )

{
    GTiffDataset *	poDS;
    TIFF		*hTIFF;

/* -------------------------------------------------------------------- */
/*      Create the underlying TIFF file.                                */
/* -------------------------------------------------------------------- */
    hTIFF = CreateLL( pszFilename, nXSize, nYSize, nBands, 
                      eType, 0, papszParmList );

    if( hTIFF == NULL )
        return NULL;

/* -------------------------------------------------------------------- */
/*      Create the new GTiffDataset object.                             */
/* -------------------------------------------------------------------- */
    poDS = new GTiffDataset();
    poDS->hTIFF = hTIFF;
    poDS->poActiveDS = poDS;
    poDS->ppoActiveDSRef = &(poDS->poActiveDS);

    poDS->nRasterXSize = nXSize;
    poDS->nRasterYSize = nYSize;
    poDS->eAccess = GA_Update;
    poDS->bCrystalized = FALSE;
    poDS->nSamplesPerPixel = (uint16) nBands;
    poDS->osFilename = pszFilename;

    /* Avoid premature crystalization that will cause directory re-writting */
    /* if GetProjectionRef() or GetGeoTransform() are called on the newly created GeoTIFF */
    poDS->bLookedForProjection = TRUE;

    TIFFGetField( hTIFF, TIFFTAG_SAMPLEFORMAT, &(poDS->nSampleFormat) );
    TIFFGetField( hTIFF, TIFFTAG_PLANARCONFIG, &(poDS->nPlanarConfig) );
    TIFFGetField( hTIFF, TIFFTAG_PHOTOMETRIC, &(poDS->nPhotometric) );
    TIFFGetField( hTIFF, TIFFTAG_BITSPERSAMPLE, &(poDS->nBitsPerSample) );
    TIFFGetField( hTIFF, TIFFTAG_COMPRESSION, &(poDS->nCompression) );

    if( TIFFIsTiled(hTIFF) )
    {
        TIFFGetField( hTIFF, TIFFTAG_TILEWIDTH, &(poDS->nBlockXSize) );
        TIFFGetField( hTIFF, TIFFTAG_TILELENGTH, &(poDS->nBlockYSize) );
    }
    else
    {
        if( !TIFFGetField( hTIFF, TIFFTAG_ROWSPERSTRIP,
                           &(poDS->nRowsPerStrip) ) )
            poDS->nRowsPerStrip = 1; /* dummy value */

        poDS->nBlockXSize = nXSize;
        poDS->nBlockYSize = MIN((int)poDS->nRowsPerStrip,nYSize);
    }

    poDS->nBlocksPerBand =
        ((nYSize + poDS->nBlockYSize - 1) / poDS->nBlockYSize)
        * ((nXSize + poDS->nBlockXSize - 1) / poDS->nBlockXSize);

    if( CSLFetchNameValue( papszParmList, "PROFILE" ) != NULL )
        poDS->osProfile = CSLFetchNameValue( papszParmList, "PROFILE" );

/* -------------------------------------------------------------------- */
/*      YCbCr JPEG compressed images should be translated on the fly    */
/*      to RGB by libtiff/libjpeg unless specifically requested         */
/*      otherwise.                                                      */
/* -------------------------------------------------------------------- */
    if( poDS->nCompression == COMPRESSION_JPEG 
        && poDS->nPhotometric == PHOTOMETRIC_YCBCR 
        && CSLTestBoolean( CPLGetConfigOption("CONVERT_YCBCR_TO_RGB",
                                              "YES") ) )
    {
        int nColorMode;

        poDS->SetMetadataItem( "SOURCE_COLOR_SPACE", "YCbCr", "IMAGE_STRUCTURE" );
        if ( !TIFFGetField( hTIFF, TIFFTAG_JPEGCOLORMODE, &nColorMode ) ||
             nColorMode != JPEGCOLORMODE_RGB )
            TIFFSetField(hTIFF, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB);
    }

/* -------------------------------------------------------------------- */
/*      Read palette back as a color table if it has one.               */
/* -------------------------------------------------------------------- */
    unsigned short	*panRed, *panGreen, *panBlue;

    if( poDS->nPhotometric == PHOTOMETRIC_PALETTE
        && TIFFGetField( hTIFF, TIFFTAG_COLORMAP, 
                         &panRed, &panGreen, &panBlue) )
    {
        int	nColorCount;
        GDALColorEntry oEntry;

        poDS->poColorTable = new GDALColorTable();

        nColorCount = 1 << poDS->nBitsPerSample;

        for( int iColor = nColorCount - 1; iColor >= 0; iColor-- )
        {
            oEntry.c1 = panRed[iColor] / 256;
            oEntry.c2 = panGreen[iColor] / 256;
            oEntry.c3 = panBlue[iColor] / 256;
            oEntry.c4 = 255;

            poDS->poColorTable->SetColorEntry( iColor, &oEntry );
        }
    }

/* -------------------------------------------------------------------- */
/*      Do we want to ensure all blocks get written out on close to     */
/*      avoid sparse files?                                             */
/* -------------------------------------------------------------------- */
    if( !CSLFetchBoolean( papszParmList, "SPARSE_OK", FALSE ) )
        poDS->bFillEmptyTiles = TRUE;

/* -------------------------------------------------------------------- */
/*      Preserve creation options for consulting later (for instance    */
/*      to decide if a TFW file should be written).                     */
/* -------------------------------------------------------------------- */
    poDS->papszCreationOptions = CSLDuplicate( papszParmList );

    poDS->nZLevel = GTiffGetZLevel(papszParmList);
    poDS->nLZMAPreset = GTiffGetLZMAPreset(papszParmList);
    poDS->nJpegQuality = GTiffGetJpegQuality(papszParmList);

/* -------------------------------------------------------------------- */
/*      If we are writing jpeg compression we need to write some        */
/*      imagery to force the jpegtables to get created.  This is,       */
/*      likely only needed with libtiff >= 3.9.3 (#3633)                */
/* -------------------------------------------------------------------- */
    if( poDS->nCompression == COMPRESSION_JPEG
        && strstr(TIFFLIB_VERSION_STR, "Version 3.9") != NULL )
    {
        CPLDebug( "GDAL",
                  "Writing zero block to force creation of JPEG tables." );
        if( TIFFIsTiled( hTIFF ) )
        {
            int cc = TIFFTileSize( hTIFF );
            unsigned char *pabyZeros = (unsigned char *) CPLCalloc(cc,1);
            TIFFWriteEncodedTile(hTIFF, 0, pabyZeros, cc);
            CPLFree( pabyZeros );
        }
        else
        {
            int cc = TIFFStripSize( hTIFF );
            unsigned char *pabyZeros = (unsigned char *) CPLCalloc(cc,1);
            TIFFWriteEncodedStrip(hTIFF, 0, pabyZeros, cc);
            CPLFree( pabyZeros );
        }
        poDS->bDontReloadFirstBlock = TRUE;
    }

/* -------------------------------------------------------------------- */
/*      Create band information objects.                                */
/* -------------------------------------------------------------------- */
    int		iBand;

    for( iBand = 0; iBand < nBands; iBand++ )
    {
        if( poDS->nBitsPerSample == 8 ||
            poDS->nBitsPerSample == 16 ||
            poDS->nBitsPerSample == 32 ||
            poDS->nBitsPerSample == 64 ||
            poDS->nBitsPerSample == 128)
            poDS->SetBand( iBand+1, new GTiffRasterBand( poDS, iBand+1 ) );
        else
        {
            poDS->SetBand( iBand+1, new GTiffOddBitsBand( poDS, iBand+1 ) );
            poDS->GetRasterBand( iBand+1 )->
                SetMetadataItem( "NBITS", 
                                 CPLString().Printf("%d",poDS->nBitsPerSample),
                                 "IMAGE_STRUCTURE" );
        }
    }

    poDS->oOvManager.Initialize( poDS, pszFilename );

    return( poDS );
}

GDALDataset *
GTiffDataset::CreateCopy( const char * pszFilename, GDALDataset *poSrcDS, 
                          int bStrict, char ** papszOptions, 
                          GDALProgressFunc pfnProgress, void * pProgressData )

{
    TIFF	*hTIFF;
    int		nXSize = poSrcDS->GetRasterXSize();
    int		nYSize = poSrcDS->GetRasterYSize();
    int		nBands = poSrcDS->GetRasterCount();
    int         iBand;
    CPLErr      eErr = CE_None;
    uint16	nPlanarConfig;
    uint16	nBitsPerSample;
    GDALRasterBand *poPBand;

    if( poSrcDS->GetRasterCount() == 0 )
    {
        CPLError( CE_Failure, CPLE_AppDefined,
                  "Unable to export GeoTIFF files with zero bands." );
        return NULL;
    }

    poPBand = poSrcDS->GetRasterBand(1);
    GDALDataType eType = poPBand->GetRasterDataType();

/* -------------------------------------------------------------------- */
/*      Check, whether all bands in input dataset has the same type.    */
/* -------------------------------------------------------------------- */
    for ( iBand = 2; iBand <= nBands; iBand++ )
    {
        if ( eType != poSrcDS->GetRasterBand(iBand)->GetRasterDataType() )
        {
            if ( bStrict )
            {
                CPLError( CE_Failure, CPLE_AppDefined,
                          "Unable to export GeoTIFF file with different datatypes per\n"
                          "different bands. All bands should have the same types in TIFF." );
                return NULL;
            }
            else
            {
                CPLError( CE_Warning, CPLE_AppDefined,
                          "Unable to export GeoTIFF file with different datatypes per\n"
                          "different bands. All bands should have the same types in TIFF." );
            }
        }
    }

    if( !pfnProgress( 0.0, NULL, pProgressData ) )
        return NULL;

/* -------------------------------------------------------------------- */
/*      Capture the profile.                                            */
/* -------------------------------------------------------------------- */
    const char          *pszProfile;
    int                 bGeoTIFF;

    pszProfile = CSLFetchNameValue(papszOptions,"PROFILE");
    if( pszProfile == NULL )
        pszProfile = "GDALGeoTIFF";

    if( !EQUAL(pszProfile,"BASELINE") 
        && !EQUAL(pszProfile,"GeoTIFF") 
        && !EQUAL(pszProfile,"GDALGeoTIFF") )
    {
        CPLError( CE_Failure, CPLE_AppDefined, 
                  "PROFILE=%s not supported in GTIFF driver.", 
                  pszProfile );
        return NULL;
    }

    if( EQUAL(pszProfile,"BASELINE") )
        bGeoTIFF = FALSE;
    else
        bGeoTIFF = TRUE;

/* -------------------------------------------------------------------- */
/*      Special handling for NBITS.  Copy from band metadata if found.  */
/* -------------------------------------------------------------------- */
    char     **papszCreateOptions = CSLDuplicate( papszOptions );

    if( poPBand->GetMetadataItem( "NBITS", "IMAGE_STRUCTURE" ) != NULL 
        && atoi(poPBand->GetMetadataItem( "NBITS", "IMAGE_STRUCTURE" )) > 0
        && CSLFetchNameValue( papszCreateOptions, "NBITS") == NULL )
    {
        papszCreateOptions = 
            CSLSetNameValue( papszCreateOptions, "NBITS",
                             poPBand->GetMetadataItem( "NBITS", 
                                                       "IMAGE_STRUCTURE" ) );
    }

    if( CSLFetchNameValue( papszOptions, "PIXELTYPE" ) == NULL
        && eType == GDT_Byte
        && poPBand->GetMetadataItem( "PIXELTYPE", "IMAGE_STRUCTURE" ) )
    {
        papszCreateOptions = 
            CSLSetNameValue( papszCreateOptions, "PIXELTYPE", 
                             poPBand->GetMetadataItem( 
                                 "PIXELTYPE", "IMAGE_STRUCTURE" ) );
    }

    int nSrcOverviews = poSrcDS->GetRasterBand(1)->GetOverviewCount();
    double dfExtraSpaceForOverviews = 0;
    if (nSrcOverviews != 0 &&
        CSLFetchBoolean(papszOptions, "COPY_SRC_OVERVIEWS", FALSE))
    {
        int i;
        for(i=0;i<nSrcOverviews;i++)
        {
            dfExtraSpaceForOverviews += ((double)poSrcDS->GetRasterBand(1)->GetOverview(i)->GetXSize()) *
                                        poSrcDS->GetRasterBand(1)->GetOverview(i)->GetYSize();
        }
        dfExtraSpaceForOverviews *= nBands * (GDALGetDataTypeSize(eType) / 8);
    }

/* -------------------------------------------------------------------- */
/*      Create the file.                                                */
/* -------------------------------------------------------------------- */
    hTIFF = CreateLL( pszFilename, nXSize, nYSize, nBands, 
                      eType, dfExtraSpaceForOverviews, papszCreateOptions );

    CSLDestroy( papszCreateOptions );

    if( hTIFF == NULL )
        return NULL;

    TIFFGetField( hTIFF, TIFFTAG_PLANARCONFIG, &nPlanarConfig );
    TIFFGetField(hTIFF, TIFFTAG_BITSPERSAMPLE, &nBitsPerSample );

/* -------------------------------------------------------------------- */
/*      Are we really producing an RGBA image?  If so, set the          */
/*      associated alpha information.                                   */
/* -------------------------------------------------------------------- */
    int bForcePhotometric = 
        CSLFetchNameValue(papszOptions,"PHOTOMETRIC") != NULL;

    if( nBands == 4 && !bForcePhotometric
        && poSrcDS->GetRasterBand(4)->GetColorInterpretation()==GCI_AlphaBand)
    {
        uint16 v[1];

        v[0] = EXTRASAMPLE_ASSOCALPHA;
	TIFFSetField(hTIFF, TIFFTAG_EXTRASAMPLES, 1, v);
        TIFFSetField( hTIFF, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_RGB );
    }

/* -------------------------------------------------------------------- */
/*      If the output is jpeg compressed, and the input is RGB make     */
/*      sure we note that.                                              */
/* -------------------------------------------------------------------- */
    uint16      nCompression;

    if( !TIFFGetField( hTIFF, TIFFTAG_COMPRESSION, &(nCompression) ) )
        nCompression = COMPRESSION_NONE;

    if( nCompression == COMPRESSION_JPEG )
    {
        if( nBands >= 3 
            && (poSrcDS->GetRasterBand(1)->GetColorInterpretation() 
                == GCI_YCbCr_YBand)
            && (poSrcDS->GetRasterBand(2)->GetColorInterpretation() 
                == GCI_YCbCr_CbBand)
            && (poSrcDS->GetRasterBand(3)->GetColorInterpretation() 
                == GCI_YCbCr_CrBand) )
        {
            /* do nothing ... */
        }
        else
        {
            /* we assume RGB if it isn't explicitly YCbCr */
            CPLDebug( "GTiff", "Setting JPEGCOLORMODE_RGB" );
            TIFFSetField( hTIFF, TIFFTAG_JPEGCOLORMODE, JPEGCOLORMODE_RGB );
        }
    }

/* -------------------------------------------------------------------- */
/*      Does the source image consist of one band, with a palette?      */
/*      If so, copy over.                                               */
/* -------------------------------------------------------------------- */
    if( (nBands == 1 || nBands == 2) && poSrcDS->GetRasterBand(1)->GetColorTable() != NULL 
        && eType == GDT_Byte )
    {
        unsigned short	anTRed[256], anTGreen[256], anTBlue[256];
        GDALColorTable *poCT;

        poCT = poSrcDS->GetRasterBand(1)->GetColorTable();

        for( int iColor = 0; iColor < 256; iColor++ )
        {
            if( iColor < poCT->GetColorEntryCount() )
            {
                GDALColorEntry  sRGB;

                poCT->GetColorEntryAsRGB( iColor, &sRGB );

                anTRed[iColor] = (unsigned short) (257 * sRGB.c1);
                anTGreen[iColor] = (unsigned short) (257 * sRGB.c2);
                anTBlue[iColor] = (unsigned short) (257 * sRGB.c3);
            }
            else
            {
                anTRed[iColor] = anTGreen[iColor] = anTBlue[iColor] = 0;
            }
        }

        if( !bForcePhotometric )
            TIFFSetField( hTIFF, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_PALETTE );
        TIFFSetField( hTIFF, TIFFTAG_COLORMAP, anTRed, anTGreen, anTBlue );
    }
    else if( (nBands == 1 || nBands == 2) 
             && poSrcDS->GetRasterBand(1)->GetColorTable() != NULL 
             && eType == GDT_UInt16 )
    {
        unsigned short	*panTRed, *panTGreen, *panTBlue;
        GDALColorTable *poCT;

        panTRed   = (unsigned short *) CPLMalloc(65536*sizeof(unsigned short));
        panTGreen = (unsigned short *) CPLMalloc(65536*sizeof(unsigned short));
        panTBlue  = (unsigned short *) CPLMalloc(65536*sizeof(unsigned short));

        poCT = poSrcDS->GetRasterBand(1)->GetColorTable();

        for( int iColor = 0; iColor < 65536; iColor++ )
        {
            if( iColor < poCT->GetColorEntryCount() )
            {
                GDALColorEntry  sRGB;

                poCT->GetColorEntryAsRGB( iColor, &sRGB );

                panTRed[iColor] = (unsigned short) (256 * sRGB.c1);
                panTGreen[iColor] = (unsigned short) (256 * sRGB.c2);
                panTBlue[iColor] = (unsigned short) (256 * sRGB.c3);
            }
            else
            {
                panTRed[iColor] = panTGreen[iColor] = panTBlue[iColor] = 0;
            }
        }

        if( !bForcePhotometric )
            TIFFSetField( hTIFF, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_PALETTE );
        TIFFSetField( hTIFF, TIFFTAG_COLORMAP, panTRed, panTGreen, panTBlue );

        CPLFree( panTRed );
        CPLFree( panTGreen );
        CPLFree( panTBlue );
    }
    else if( poSrcDS->GetRasterBand(1)->GetColorTable() != NULL )
        CPLError( CE_Warning, CPLE_AppDefined,
                  "Unable to export color table to GeoTIFF file.  Color tables\n"
                  "can only be written to 1 band or 2 bands Byte or UInt16 GeoTIFF files." );

    if( nBands == 2
        && poSrcDS->GetRasterBand(1)->GetColorTable() != NULL 
        && (eType == GDT_Byte || eType == GDT_UInt16) )
    {
        uint16 v[1] = { EXTRASAMPLE_UNASSALPHA };

        TIFFSetField(hTIFF, TIFFTAG_EXTRASAMPLES, 1, v );
    }

/* -------------------------------------------------------------------- */
/*      Transfer some TIFF specific metadata, if available.             */
/*      The return value will tell us if we need to try again later with*/
/*      PAM because the profile doesn't allow to write some metadata    */
/*      as TIFF tag                                                     */
/* -------------------------------------------------------------------- */
    int bHasWrittenMDInGeotiffTAG =
            GTiffDataset::WriteMetadata( poSrcDS, hTIFF, FALSE, pszProfile,
                                 pszFilename, papszOptions );

/* -------------------------------------------------------------------- */
/* 	Write NoData value, if exist.                                   */
/* -------------------------------------------------------------------- */
    if( EQUAL(pszProfile,"GDALGeoTIFF") )
    {
        int		bSuccess;
        double	dfNoData;

        dfNoData = poSrcDS->GetRasterBand(1)->GetNoDataValue( &bSuccess );
        if ( bSuccess )
            GTiffDataset::WriteNoDataValue( hTIFF, dfNoData );
    }

/* -------------------------------------------------------------------- */
/*      Are we addressing PixelIsPoint mode?                            */
/* -------------------------------------------------------------------- */
    bool bPixelIsPoint = false;
    int  bPointGeoIgnore = FALSE;

    if( poSrcDS->GetMetadataItem( GDALMD_AREA_OR_POINT ) 
        && EQUAL(poSrcDS->GetMetadataItem(GDALMD_AREA_OR_POINT),
                 GDALMD_AOP_POINT) )
    {
        bPixelIsPoint = true;
        bPointGeoIgnore = 
            CSLTestBoolean( CPLGetConfigOption("GTIFF_POINT_GEO_IGNORE",
                                               "FALSE") );
    }

/* -------------------------------------------------------------------- */
/*      Write affine transform if it is meaningful.                     */
/* -------------------------------------------------------------------- */
    const char *pszProjection = NULL;
    double      adfGeoTransform[6];

    if( poSrcDS->GetGeoTransform( adfGeoTransform ) == CE_None
        && (adfGeoTransform[0] != 0.0 || adfGeoTransform[1] != 1.0
            || adfGeoTransform[2] != 0.0 || adfGeoTransform[3] != 0.0
            || adfGeoTransform[4] != 0.0 || adfGeoTransform[5] != 1.0 ))
    {
        if( bGeoTIFF )
        {
            if( adfGeoTransform[2] == 0.0 && adfGeoTransform[4] == 0.0 
                && adfGeoTransform[5] < 0.0 )
            {

                double	adfPixelScale[3], adfTiePoints[6];

                adfPixelScale[0] = adfGeoTransform[1];
                adfPixelScale[1] = fabs(adfGeoTransform[5]);
                adfPixelScale[2] = 0.0;

                TIFFSetField( hTIFF, TIFFTAG_GEOPIXELSCALE, 3, adfPixelScale );

                adfTiePoints[0] = 0.0;
                adfTiePoints[1] = 0.0;
                adfTiePoints[2] = 0.0;
                adfTiePoints[3] = adfGeoTransform[0];
                adfTiePoints[4] = adfGeoTransform[3];
                adfTiePoints[5] = 0.0;

                if( bPixelIsPoint && !bPointGeoIgnore )
                {
                    adfTiePoints[3] += adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5;
                    adfTiePoints[4] += adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5;
                }

                TIFFSetField( hTIFF, TIFFTAG_GEOTIEPOINTS, 6, adfTiePoints );
            }
            else
            {
                double	adfMatrix[16];

                memset(adfMatrix,0,sizeof(double) * 16);

                adfMatrix[0] = adfGeoTransform[1];
                adfMatrix[1] = adfGeoTransform[2];
                adfMatrix[3] = adfGeoTransform[0];
                adfMatrix[4] = adfGeoTransform[4];
                adfMatrix[5] = adfGeoTransform[5];
                adfMatrix[7] = adfGeoTransform[3];
                adfMatrix[15] = 1.0;

                if( bPixelIsPoint && !bPointGeoIgnore )
                {
                    adfMatrix[3] += adfGeoTransform[1] * 0.5 + adfGeoTransform[2] * 0.5;
                    adfMatrix[7] += adfGeoTransform[4] * 0.5 + adfGeoTransform[5] * 0.5;
                }

                TIFFSetField( hTIFF, TIFFTAG_GEOTRANSMATRIX, 16, adfMatrix );
            }

            pszProjection = poSrcDS->GetProjectionRef();
        }

/* -------------------------------------------------------------------- */
/*      Do we need a TFW file?                                          */
/* -------------------------------------------------------------------- */
        if( CSLFetchBoolean( papszOptions, "TFW", FALSE ) )
            GDALWriteWorldFile( pszFilename, "tfw", adfGeoTransform );
        else if( CSLFetchBoolean( papszOptions, "WORLDFILE", FALSE ) )
            GDALWriteWorldFile( pszFilename, "wld", adfGeoTransform );
    }

/* -------------------------------------------------------------------- */
/*      Otherwise write tiepoints if they are available.                */
/* -------------------------------------------------------------------- */
    else if( poSrcDS->GetGCPCount() > 0 && bGeoTIFF )
    {
        const GDAL_GCP *pasGCPs = poSrcDS->GetGCPs();
        double	*padfTiePoints;

        padfTiePoints = (double *) 
            CPLMalloc(6*sizeof(double)*poSrcDS->GetGCPCount());

        for( int iGCP = 0; iGCP < poSrcDS->GetGCPCount(); iGCP++ )
        {

            padfTiePoints[iGCP*6+0] = pasGCPs[iGCP].dfGCPPixel;
            padfTiePoints[iGCP*6+1] = pasGCPs[iGCP].dfGCPLine;
            padfTiePoints[iGCP*6+2] = 0;
            padfTiePoints[iGCP*6+3] = pasGCPs[iGCP].dfGCPX;
            padfTiePoints[iGCP*6+4] = pasGCPs[iGCP].dfGCPY;
            padfTiePoints[iGCP*6+5] = pasGCPs[iGCP].dfGCPZ;

            if( bPixelIsPoint && !bPointGeoIgnore )
            {
                padfTiePoints[iGCP*6+0] += 0.5;
                padfTiePoints[iGCP*6+1] += 0.5;
            }
        }

        TIFFSetField( hTIFF, TIFFTAG_GEOTIEPOINTS, 
                      6*poSrcDS->GetGCPCount(), padfTiePoints );
        CPLFree( padfTiePoints );

        pszProjection = poSrcDS->GetGCPProjection();

        if( CSLFetchBoolean( papszOptions, "TFW", FALSE ) 
            || CSLFetchBoolean( papszOptions, "WORLDFILE", FALSE ) )
        {
            CPLError(CE_Warning, CPLE_AppDefined,
                     "TFW=ON or WORLDFILE=ON creation options are ignored when GCPs are available");
        }
    }

    else
        pszProjection = poSrcDS->GetProjectionRef();

/* -------------------------------------------------------------------- */
/*      Write the projection information, if possible.                  */
/* -------------------------------------------------------------------- */
    if( pszProjection != NULL && strlen(pszProjection) > 0 && bGeoTIFF )
    {
        GTIF	*psGTIF;

        psGTIF = GTIFNew( hTIFF );
        GTIFSetFromOGISDefn( psGTIF, pszProjection );

        if( poSrcDS->GetMetadataItem( GDALMD_AREA_OR_POINT ) 
            && EQUAL(poSrcDS->GetMetadataItem(GDALMD_AREA_OR_POINT),
                     GDALMD_AOP_POINT) )
        {
            GTIFKeySet(psGTIF, GTRasterTypeGeoKey, TYPE_SHORT, 1,
                       RasterPixelIsPoint);
        }

        GTIFWriteKeys( psGTIF );
        GTIFFree( psGTIF );
    }

/* -------------------------------------------------------------------- */
/*      If we are writing jpeg compression we need to write some        */
/*      imagery to force the jpegtables to get created.  This is,       */
/*      likely only needed with libtiff >= 3.9.3 (#3633)                */
/* -------------------------------------------------------------------- */
    int bDontReloadFirstBlock = FALSE;
    if( nCompression == COMPRESSION_JPEG
        && strstr(TIFFLIB_VERSION_STR, "Version 3.9") != NULL )
    {
        CPLDebug( "GDAL", 
                  "Writing zero block to force creation of JPEG tables." );
        if( TIFFIsTiled( hTIFF ) )
        {
            int cc = TIFFTileSize( hTIFF );
            unsigned char *pabyZeros = (unsigned char *) CPLCalloc(cc,1);
            TIFFWriteEncodedTile(hTIFF, 0, pabyZeros, cc);
            CPLFree( pabyZeros );
        }
        else
        {
            int cc = TIFFStripSize( hTIFF );
            unsigned char *pabyZeros = (unsigned char *) CPLCalloc(cc,1);
            TIFFWriteEncodedStrip(hTIFF, 0, pabyZeros, cc);
            CPLFree( pabyZeros );
        }
        bDontReloadFirstBlock = TRUE;
    }

/* -------------------------------------------------------------------- */
/*      Cleanup                                                         */
/* -------------------------------------------------------------------- */

    TIFFWriteCheck( hTIFF, TIFFIsTiled(hTIFF), "GTiffCreateCopy()");
    TIFFWriteDirectory( hTIFF );
    TIFFFlush( hTIFF );
    XTIFFClose( hTIFF );
    hTIFF = NULL;

    if( eErr != CE_None )
    {
        VSIUnlink( pszFilename );
        return NULL;
    }

/* -------------------------------------------------------------------- */
/*      Re-open as a dataset and copy over missing metadata using       */
/*      PAM facilities.                                                 */
/* -------------------------------------------------------------------- */
    GTiffDataset *poDS;
    CPLString osFileName("GTIFF_RAW:");

    osFileName += pszFilename;

    poDS = (GTiffDataset *) GDALOpen( osFileName, GA_Update );
    if( poDS == NULL )
        poDS = (GTiffDataset *) GDALOpen( osFileName, GA_ReadOnly );

    if ( poDS == NULL )
    {
        VSIUnlink( pszFilename );
        return NULL;
    }

    poDS->osProfile = pszProfile;
    poDS->CloneInfo( poSrcDS, GCIF_PAM_DEFAULT & ~GCIF_MASK );
    poDS->papszCreationOptions = CSLDuplicate( papszOptions );
    poDS->bDontReloadFirstBlock = bDontReloadFirstBlock;

/* -------------------------------------------------------------------- */
/*      CloneInfo() doesn't merge metadata, it just replaces it totally */
/*      So we have to merge it                                          */
/* -------------------------------------------------------------------- */

    char **papszSRC_MD = poSrcDS->GetMetadata();
    char **papszDST_MD = CSLDuplicate(poDS->GetMetadata());

    papszDST_MD = CSLMerge( papszDST_MD, papszSRC_MD );

    poDS->SetMetadata( papszDST_MD );
    CSLDestroy( papszDST_MD );

    /* Depending on the PHOTOMETRIC tag, the TIFF file may not have */
    /* the same band count as the source. Will fail later in GDALDatasetCopyWholeRaster anyway... */
    for( int nBand = 1;
         nBand <= MIN(poDS->GetRasterCount(), poSrcDS->GetRasterCount()) ;
         nBand++ )
    {
        char **papszSRC_MD = poSrcDS->GetRasterBand(nBand)->GetMetadata();
        char **papszDST_MD = CSLDuplicate(poDS->GetRasterBand(nBand)->GetMetadata());

        papszDST_MD = CSLMerge( papszDST_MD, papszSRC_MD );

        poDS->GetRasterBand(nBand)->SetMetadata( papszDST_MD );
        CSLDestroy( papszDST_MD );
    }

    hTIFF = (TIFF*) poDS->GetInternalHandle(NULL);

/* -------------------------------------------------------------------- */
/*      Handle forcing xml:ESRI data to be written to PAM.              */
/* -------------------------------------------------------------------- */
    if( CSLTestBoolean(CPLGetConfigOption( "ESRI_XML_PAM", "NO" )) )
    {
        char **papszESRIMD = poSrcDS->GetMetadata("xml:ESRI");
        if( papszESRIMD )
        {
            poDS->SetMetadata( papszESRIMD, "xml:ESRI");
        }
    }

/* -------------------------------------------------------------------- */
/*      Second chance : now that we have a PAM dataset, it is possible  */
/*      to write metadata that we couldn't be writen as TIFF tag        */
/* -------------------------------------------------------------------- */
    if (!bHasWrittenMDInGeotiffTAG)
        GTiffDataset::WriteMetadata( poDS, hTIFF, TRUE, pszProfile,
                                     pszFilename, papszOptions, TRUE /* don't write RPC and IMD file again */);

    /* To avoid unnecessary directory rewriting */
    poDS->bMetadataChanged = FALSE;
    poDS->bGeoTIFFInfoChanged = FALSE;

    /* We must re-set the compression level at this point, since it has */
    /* been lost a few lines above when closing the newly create TIFF file */
    /* The TIFFTAG_ZIPQUALITY & TIFFTAG_JPEGQUALITY are not store in the TIFF file. */
    /* They are just TIFF session parameters */

    poDS->nZLevel = GTiffGetZLevel(papszOptions);
    poDS->nLZMAPreset = GTiffGetLZMAPreset(papszOptions);
    poDS->nJpegQuality = GTiffGetJpegQuality(papszOptions);

    if (nCompression == COMPRESSION_ADOBE_DEFLATE)
    {
        if (poDS->nZLevel != -1)
        {
            TIFFSetField( hTIFF, TIFFTAG_ZIPQUALITY, poDS->nZLevel );
        }
    }
    else if( nCompression == COMPRESSION_JPEG)
    {
        if (poDS->nJpegQuality != -1)
        {
            TIFFSetField( hTIFF, TIFFTAG_JPEGQUALITY, poDS->nJpegQuality );
        }
    }
    else if( nCompression == COMPRESSION_LZMA)
    {
        if (poDS->nLZMAPreset != -1)
        {
            TIFFSetField( hTIFF, TIFFTAG_LZMAPRESET, poDS->nLZMAPreset );
        }
    }

    /* Precreate (internal) mask, so that the IBuildOverviews() below */
    /* has a chance to create also the overviews of the mask */
    int nMaskFlags = poSrcDS->GetRasterBand(1)->GetMaskFlags();
    if( eErr == CE_None
        && !(nMaskFlags & (GMF_ALL_VALID|GMF_ALPHA|GMF_NODATA) )
        && (nMaskFlags & GMF_PER_DATASET) )
    {
        eErr = poDS->CreateMaskBand( nMaskFlags );
    }

/* -------------------------------------------------------------------- */
/*      Create and then copy existing overviews if requested            */
/*  We do it such that all the IFDs are at the beginning of the file,   */
/*  and that the imagery data for the smallest overview is written      */
/*  first, that way the file is more usable when embedded in a          */
/*  compressed stream.                                                  */
/* -------------------------------------------------------------------- */

    /* For scaled progress due to overview copying */
    double dfTotalPixels = ((double)nXSize) * nYSize;
    double dfCurPixels = 0;

    if (eErr == CE_None &&
        nSrcOverviews != 0 &&
        CSLFetchBoolean(papszOptions, "COPY_SRC_OVERVIEWS", FALSE))
    {
        eErr = poDS->CreateOverviewsFromSrcOverviews(poSrcDS);

        if (poDS->nOverviewCount != nSrcOverviews)
        {
            CPLError(CE_Failure, CPLE_AppDefined,
                     "Did only manage to instanciate %d overview levels, whereas source contains %d",
                     poDS->nOverviewCount, nSrcOverviews);
            eErr = CE_Failure;
        }

        int i;
        for(i=0;i<nSrcOverviews;i++)
        {
            GDALRasterBand* poOvrBand = poSrcDS->GetRasterBand(1)->GetOverview(i);
            dfTotalPixels += ((double)poOvrBand->GetXSize()) *
                                      poOvrBand->GetYSize();
        }

        char* papszCopyWholeRasterOptions[2] = { NULL, NULL };
        if (nCompression != COMPRESSION_NONE)
            papszCopyWholeRasterOptions[0] = (char*) "COMPRESSED=YES";
        /* Now copy the imagery */
        for(i=0;eErr == CE_None && i<nSrcOverviews;i++)
        {
            /* Begin with the smallest overview */
            int iOvrLevel = nSrcOverviews-1-i;

            /* Create a fake dataset with the source overview level so that */
            /* GDALDatasetCopyWholeRaster can cope with it */
            GDALDataset* poSrcOvrDS = new GDALOverviewDS(poSrcDS, iOvrLevel);

            GDALRasterBand* poOvrBand =
                    poSrcDS->GetRasterBand(1)->GetOverview(iOvrLevel);
            double dfNextCurPixels = dfCurPixels +
                    ((double)poOvrBand->GetXSize()) * poOvrBand->GetYSize();

            void* pScaledData = GDALCreateScaledProgress( dfCurPixels / dfTotalPixels,
                                      dfNextCurPixels / dfTotalPixels,
                                      pfnProgress, pProgressData);

            eErr = GDALDatasetCopyWholeRaster( (GDALDatasetH) poSrcOvrDS,
                                                (GDALDatasetH) poDS->papoOverviewDS[iOvrLevel],
                                                papszCopyWholeRasterOptions,
                                                GDALScaledProgress, pScaledData );

            dfCurPixels = dfNextCurPixels;
            GDALDestroyScaledProgress(pScaledData);

            delete poSrcOvrDS;
            poDS->papoOverviewDS[iOvrLevel]->FlushCache();

            /* Copy mask of the overview */
            if (eErr == CE_None && poDS->poMaskDS != NULL)
            {
                eErr = GDALRasterBandCopyWholeRaster( poOvrBand->GetMaskBand(),
                                                    poDS->papoOverviewDS[iOvrLevel]->poMaskDS->GetRasterBand(1),
                                                    papszCopyWholeRasterOptions,
                                                    GDALDummyProgress, NULL);
                poDS->papoOverviewDS[iOvrLevel]->poMaskDS->FlushCache();
            }
        }
    }

/* -------------------------------------------------------------------- */
/*      Copy actual imagery.                                            */
/* -------------------------------------------------------------------- */
    void* pScaledData = GDALCreateScaledProgress( dfCurPixels / dfTotalPixels,
                                                  1.0,
                                                  pfnProgress, pProgressData);

    if (poDS->bTreatAsSplit || poDS->bTreatAsSplitBitmap)
    {
        /* For split bands, we use TIFFWriteScanline() interface */
        CPLAssert(poDS->nBitsPerSample == 8 || poDS->nBitsPerSample == 1);

        if (poDS->nPlanarConfig == PLANARCONFIG_CONTIG && poDS->nBands > 1)
        {
            int j;
            GByte* pabyScanline = (GByte *) CPLMalloc(TIFFScanlineSize(hTIFF));
            for(j=0;j<nYSize && eErr == CE_None;j++)
            {
                eErr = poSrcDS->RasterIO(GF_Read, 0, j, nXSize, 1,
                                         pabyScanline, nXSize, 1,
                                         GDT_Byte, nBands, NULL, poDS->nBands, 0, 1);
                if (eErr == CE_None &&
                    TIFFWriteScanline( hTIFF, pabyScanline, j, 0) == -1)
                {
                    CPLError( CE_Failure, CPLE_AppDefined,
                              "TIFFWriteScanline() failed." );
                    eErr = CE_Failure;
                }
                if( !GDALScaledProgress( (j+1) * 1.0 / nYSize, NULL, pScaledData ) )
                    eErr = CE_Failure;
            }
            CPLFree(pabyScanline);
        }
        else
        {
            int iBand, j;
            GByte* pabyScanline = (GByte *) CPLMalloc(TIFFScanlineSize(hTIFF));
            eErr = CE_None;
            for(iBand=1;iBand<=nBands && eErr == CE_None;iBand++)
            {
                for(j=0;j<nYSize && eErr == CE_None;j++)
                {
                    eErr = poSrcDS->GetRasterBand(iBand)->RasterIO(
                                                    GF_Read, 0, j, nXSize, 1,
                                                    pabyScanline, nXSize, 1,
                                                    GDT_Byte, 0, 0);
                    if (poDS->bTreatAsSplitBitmap)
                    {
                        for(int i=0;i<nXSize;i++)
                        {
                            GByte byVal = pabyScanline[i];
                            if ((i & 0x7) == 0)
                                pabyScanline[i >> 3] = 0;
                            if (byVal)
                                pabyScanline[i >> 3] |= (0x80 >> (i & 0x7));
                        }
                    }
                    if (eErr == CE_None &&
                        TIFFWriteScanline( hTIFF, pabyScanline, j, (uint16) (iBand-1)) == -1)
                    {
                        CPLError( CE_Failure, CPLE_AppDefined,
                                  "TIFFWriteScanline() failed." );
                        eErr = CE_Failure;
                    }
                    if( !GDALScaledProgress( (j+1 + (iBand - 1) * nYSize) * 1.0 /
                                      (nBands * nYSize), NULL, pScaledData ) )
                        eErr = CE_Failure;
                }
            }
            CPLFree(pabyScanline);
        }

        /* Necessary to be able to read the file without re-opening */
#if defined(HAVE_TIFFGETSIZEPROC)
        TIFFSizeProc pfnSizeProc = TIFFGetSizeProc( hTIFF );

        TIFFFlushData( hTIFF );

        toff_t nNewDirOffset = pfnSizeProc( TIFFClientdata( hTIFF ) );
        if( (nNewDirOffset % 2) == 1 )
            nNewDirOffset++;
#endif

        TIFFFlush( hTIFF );

#if defined(HAVE_TIFFGETSIZEPROC)
        if( poDS->nDirOffset != TIFFCurrentDirOffset( hTIFF ) )
        {
            poDS->nDirOffset = nNewDirOffset;
            CPLDebug( "GTiff", "directory moved during flush." );
        }
#endif
    }
    else if (eErr == CE_None)
    {
        char* papszCopyWholeRasterOptions[2] = { NULL, NULL };
        if (nCompression != COMPRESSION_NONE)
            papszCopyWholeRasterOptions[0] = (char*) "COMPRESSED=YES";
        eErr = GDALDatasetCopyWholeRaster( (GDALDatasetH) poSrcDS, 
                                            (GDALDatasetH) poDS,
                                            papszCopyWholeRasterOptions,
                                            GDALScaledProgress, pScaledData );
    }

    GDALDestroyScaledProgress(pScaledData);

    if (eErr == CE_None)
    {
        if (poDS->poMaskDS)
        {
            const char* papszOptions[2] = { "COMPRESSED=YES", NULL };
            eErr = GDALRasterBandCopyWholeRaster(
                                    poSrcDS->GetRasterBand(1)->GetMaskBand(),
                                    poDS->GetRasterBand(1)->GetMaskBand(),
                                    (char**)papszOptions,
                                    GDALDummyProgress, NULL);
        }
        else
            eErr = GDALDriver::DefaultCopyMasks( poSrcDS, poDS, bStrict );
    }

    if( eErr == CE_Failure )
    {
        delete poDS;
        poDS = NULL;

        if (CSLTestBoolean(CPLGetConfigOption("GTIFF_DELETE_ON_ERROR", "YES")))
            VSIUnlink( pszFilename ); // should really delete more carefully.
    }

    return poDS;
}

CPLErr GTiffDataset::CreateMaskBand(int nFlags)
{
    if (poMaskDS != NULL)
    {
        CPLError(CE_Failure, CPLE_AppDefined,
                 "This TIFF dataset has already an internal mask band");
        return CE_Failure;
    }
    else if (CSLTestBoolean(CPLGetConfigOption("GDAL_TIFF_INTERNAL_MASK", "NO")))
    {
        toff_t  nOffset;
        int     bIsTiled;
        int     bIsOverview = FALSE;
        uint32	nSubType;
        int     nCompression;

        if (nFlags != GMF_PER_DATASET)
        {
            CPLError(CE_Failure, CPLE_AppDefined,
                     "The only flag value supported for internal mask is GMF_PER_DATASET");
            return CE_Failure;
        }

        if( strstr(GDALGetMetadataItem(GDALGetDriverByName( "GTiff" ),
                                       GDAL_DMD_CREATIONOPTIONLIST, NULL ),
                   "<Value>DEFLATE</Value>") != NULL )
            nCompression = COMPRESSION_ADOBE_DEFLATE;
        else
            nCompression = COMPRESSION_PACKBITS;

    /* -------------------------------------------------------------------- */
    /*      If we don't have read access, then create the mask externally.  */
    /* -------------------------------------------------------------------- */
        if( GetAccess() != GA_Update )
        {
            CPLError( CE_Warning, CPLE_AppDefined,
                    "File open for read-only accessing, "
                    "creating mask externally." );

            return GDALPamDataset::CreateMaskBand(nFlags);
        }

        if (poBaseDS)
        {
            if (!poBaseDS->SetDirectory())
                return CE_Failure;
        }
        if (!SetDirectory())
            return CE_Failure;

        if( TIFFGetField(hTIFF, TIFFTAG_SUBFILETYPE, &nSubType))
        {
            bIsOverview = (nSubType & FILETYPE_REDUCEDIMAGE) != 0;

            if ((nSubType & FILETYPE_MASK) != 0)
            {
                CPLError( CE_Failure, CPLE_AppDefined,
                          "Cannot create a mask on a TIFF mask IFD !" );
                return CE_Failure;
            }
        }

        bIsTiled = TIFFIsTiled(hTIFF);

        FlushDirectory();

        nOffset = GTIFFWriteDirectory(hTIFF,
                                      (bIsOverview) ? FILETYPE_REDUCEDIMAGE | FILETYPE_MASK : FILETYPE_MASK,
                                      nRasterXSize, nRasterYSize,
                                      1, PLANARCONFIG_CONTIG, 1,
                                      nBlockXSize, nBlockYSize,
                                      bIsTiled, nCompression, 
                                      PHOTOMETRIC_MASK, PREDICTOR_NONE,
                                      SAMPLEFORMAT_UINT, NULL, NULL, NULL, 0, NULL, "");
        if (nOffset == 0)
            return CE_Failure;

        poMaskDS = new GTiffDataset();
        poMaskDS->bPromoteTo8Bits = CSLTestBoolean(CPLGetConfigOption("GDAL_TIFF_INTERNAL_MASK_TO_8BIT", "YES"));
        if( poMaskDS->OpenOffset( hTIFF, ppoActiveDSRef, nOffset, 
                                  FALSE, GA_Update ) != CE_None)
        {
            delete poMaskDS;
            poMaskDS = NULL;
            return CE_Failure;
        }

        return CE_None;
    }
    else
    {
        return GDALPamDataset::CreateMaskBand(nFlags);
    }
}

bool CTiffContext::LoadFrameInfo(int32_t frame,
                                 int32_t* width,
                                 int32_t* height,
                                 int32_t* comps,
                                 int32_t* bpc,
                                 CFX_DIBAttribute* pAttribute) {
  if (!TIFFSetDirectory(m_tif_ctx.get(), (uint16_t)frame))
    return false;

  uint32_t tif_width = 0;
  uint32_t tif_height = 0;
  uint16_t tif_comps = 0;
  uint16_t tif_bpc = 0;
  uint32_t tif_rps = 0;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_IMAGEWIDTH, &tif_width);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_IMAGELENGTH, &tif_height);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_SAMPLESPERPIXEL, &tif_comps);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_BITSPERSAMPLE, &tif_bpc);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_ROWSPERSTRIP, &tif_rps);

  uint16_t tif_resunit = 0;
  if (TIFFGetField(m_tif_ctx.get(), TIFFTAG_RESOLUTIONUNIT, &tif_resunit)) {
    pAttribute->m_wDPIUnit =
        static_cast<CFX_DIBAttribute::ResUnit>(tif_resunit - 1);
  } else {
    pAttribute->m_wDPIUnit = CFX_DIBAttribute::kResUnitInch;
  }

  float tif_xdpi = 0.0f;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_XRESOLUTION, &tif_xdpi);
  if (tif_xdpi)
    pAttribute->m_nXDPI = static_cast<int32_t>(tif_xdpi + 0.5f);

  float tif_ydpi = 0.0f;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_YRESOLUTION, &tif_ydpi);
  if (tif_ydpi)
    pAttribute->m_nYDPI = static_cast<int32_t>(tif_ydpi + 0.5f);

  FX_SAFE_INT32 checked_width = tif_width;
  FX_SAFE_INT32 checked_height = tif_height;
  if (!checked_width.IsValid() || !checked_height.IsValid())
    return false;

  *width = checked_width.ValueOrDie();
  *height = checked_height.ValueOrDie();
  *comps = tif_comps;
  *bpc = tif_bpc;
  if (tif_rps > tif_height) {
    tif_rps = tif_height;
    TIFFSetField(m_tif_ctx.get(), TIFFTAG_ROWSPERSTRIP, tif_rps);
  }
  return true;
}

void CTiffContext::SetPalette(const RetainPtr<CFX_DIBitmap>& pDIBitmap,
                              uint16_t bps) {
  uint16_t* red_orig = nullptr;
  uint16_t* green_orig = nullptr;
  uint16_t* blue_orig = nullptr;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_COLORMAP, &red_orig, &green_orig,
               &blue_orig);
#define CVT(x) ((uint16_t)((x) >> 8))
  UNSAFE_TODO({
    for (int32_t i = pdfium::checked_cast<int32_t>((1L << bps) - 1); i >= 0;
         i--) {
      red_orig[i] = CVT(red_orig[i]);
      green_orig[i] = CVT(green_orig[i]);
      blue_orig[i] = CVT(blue_orig[i]);
    }
    int32_t len = 1 << bps;
    for (int32_t index = 0; index < len; index++) {
      uint32_t r = red_orig[index] & 0xFF;
      uint32_t g = green_orig[index] & 0xFF;
      uint32_t b = blue_orig[index] & 0xFF;
      uint32_t color = (uint32_t)b | ((uint32_t)g << 8) | ((uint32_t)r << 16) |
                       (((uint32_t)0xffL) << 24);
      pDIBitmap->SetPaletteArgb(index, color);
    }
  });
#undef CVT
}

bool CTiffContext::Decode(const RetainPtr<CFX_DIBitmap>& pDIBitmap) {
  uint32_t img_width = pDIBitmap->GetWidth();
  uint32_t img_height = pDIBitmap->GetHeight();
  uint32_t width = 0;
  uint32_t height = 0;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_IMAGEWIDTH, &width);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_IMAGELENGTH, &height);
  if (img_width != width || img_height != height)
    return false;

  if (pDIBitmap->GetBPP() == 32) {
    uint16_t rotation = ORIENTATION_TOPLEFT;
    TIFFGetField(m_tif_ctx.get(), TIFFTAG_ORIENTATION, &rotation);
    uint32_t* data = const_cast<uint32_t*>(
        reinterpret_cast<const uint32_t*>(pDIBitmap->GetBuffer().data()));
    if (TIFFReadRGBAImageOriented(m_tif_ctx.get(), img_width, img_height, data,
                                  rotation, 1)) {
      for (uint32_t row = 0; row < img_height; row++) {
        uint8_t* row_buf = pDIBitmap->GetWritableScanline(row).data();
        TiffBGRA2RGBA(row_buf, img_width, 4);
      }
      return true;
    }
  }
  uint16_t spp = 0;
  uint16_t bps = 0;
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_SAMPLESPERPIXEL, &spp);
  TIFFGetField(m_tif_ctx.get(), TIFFTAG_BITSPERSAMPLE, &bps);
  FX_SAFE_UINT32 safe_bpp = bps;
  safe_bpp *= spp;
  if (!safe_bpp.IsValid())
    return false;
  uint32_t bpp = safe_bpp.ValueOrDie();
  if (bpp == 1)
    return Decode1bppRGB(pDIBitmap, height, width, bps, spp);
  if (bpp <= 8)
    return Decode8bppRGB(pDIBitmap, height, width, bps, spp);
  if (bpp <= 24)
    return Decode24bppRGB(pDIBitmap, height, width, bps, spp);
  return false;
}

char *loadTiff3D2Metadata ( char * filename, unsigned int &sz0, unsigned int  &sz1, unsigned int  &sz2, unsigned int  &sz3, int &datatype, int &b_swap, void * &fhandle, int &header_len ) {

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_START(TiffLoadMetadata)
    #endif

	uint32 XSIZE;
	uint32 YSIZE;
	uint16 bpp;
	uint16 spp;
	uint16 Cpage;
	uint16 Npages;
    TIFF *input;
    int check;

	input=TIFFOpen(filename,"r");
	if (!input)
    {
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Cannot open the file %s",finName).c_str());
		return ((char *) "Cannot open the file.");
    }

	check=TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &XSIZE);
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Image length of %s undefined\n", finName).c_str());
		return ((char *) "Image width of undefined.");
	}		
    
	check=TIFFGetField(input, TIFFTAG_IMAGELENGTH, &YSIZE);
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Image length of %s undefined\n", finName).c_str());
		return ((char *) "Image length of undefined.");
	}		
    
	check=TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp); 
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Undefined bits per sample in %s \n", finName).c_str());
		return ((char *) "Undefined bits per sample.");
	}

	check=TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp);
	if (!check)
	{
		TIFFClose(input);
		//throw iim::IOException(strprintf("in IOManager::readTiffMultipage(...): Undefined bits per sample in %s \n", finName).c_str());
		return ((char *) "Undefined samples per pixel.");
	}

	// Onofri
	check=TIFFGetField(input, TIFFTAG_PAGENUMBER, &Cpage, &Npages);
	if (!check || Npages==0) { // the tag has not been read correctly
		// Add warning?
		Npages = 0;
		do {
			Npages++;
		} while ( TIFFReadDirectory(input) );
	}

	sz0 = XSIZE;
	sz1 = YSIZE;
	sz2 = Npages;
	sz3 = spp;
	datatype = bpp/8;

	//b_swap = 0;
	b_swap=TIFFIsByteSwapped(input);
	fhandle = (void *) input;
	header_len = -1;

	// the file must non be closed (it is responsibility of the caller)
	//TIFFClose(input);

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_STOP(TiffLoadMetadata, itm::IO, itm::strprintf("successfully loaded metadata from file \"%s\"", filename))
    #endif

	return ((char *) 0);
}

char *appendSlice2Tiff3DFile ( char *filename, int slice, unsigned char *img, unsigned int img_width, unsigned int img_height ) {
    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_START(TiffAppendData)
    #endif

	TIFF *output;
	uint16 spp, bpp, NPages, pg0;

	output=TIFFOpen(filename,"r");
	TIFFGetField(output, TIFFTAG_BITSPERSAMPLE, &bpp); 
	TIFFGetField(output, TIFFTAG_SAMPLESPERPIXEL, &spp);
	TIFFGetField(output, TIFFTAG_PAGENUMBER, &pg0, &NPages);
	TIFFClose(output);
	// since we are 
	output = (slice==0)? TIFFOpen(filename,"w") : TIFFOpen(filename,"a");

	TIFFSetDirectory(output,slice); // WARNING: slice must be the first page after the last, otherwise the file can be corrupted

	TIFFSetField(output, TIFFTAG_IMAGEWIDTH, img_width);
	TIFFSetField(output, TIFFTAG_IMAGELENGTH, img_height);
	TIFFSetField(output, TIFFTAG_BITSPERSAMPLE, bpp); 
	TIFFSetField(output, TIFFTAG_SAMPLESPERPIXEL, spp);
	TIFFSetField(output, TIFFTAG_ROWSPERSTRIP, img_height);
	TIFFSetField(output, TIFFTAG_COMPRESSION, COMPRESSION_LZW);
	//TIFFSetField(output, TIFFTAG_COMPRESSION, COMPRESSION_NONE);
	TIFFSetField(output, TIFFTAG_PLANARCONFIG,PLANARCONFIG_CONTIG);
	TIFFSetField(output, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_MINISBLACK);	
	//TIFFSetField(output, TIFFTAG_PHOTOMETRIC, PHOTOMETRIC_RGB);	
	// We are writing single page of the multipage file 
	TIFFSetField(output, TIFFTAG_SUBFILETYPE, FILETYPE_PAGE);
	TIFFSetField(output, TIFFTAG_PAGENUMBER, (uint16)slice, NPages); 

	TIFFWriteEncodedStrip(output, 0, img, img_width * img_height * spp * (bpp/8));
	//img +=  img_width * img_height;

	TIFFWriteDirectory(output);

	TIFFClose(output);

    // 2015-01-30. Alessandro. @ADDED performance (time) measurement in all most time-consuming methods.
    #ifdef _VAA3D_TERAFLY_PLUGIN_MODE
    TERAFLY_TIME_STOP(TiffAppendData, itm::IO, itm::strprintf("appended slice %d x %d to 3D tiff \"%s\"", img_width, img_height, filename))
    #endif

	return (char *) 0;
}

char *readTiff3DFile2Buffer ( void *fhandler, unsigned char *img, unsigned int img_width, unsigned int img_height, unsigned int first, unsigned int last, int b_swap ) {
	uint32 rps;
    uint16 spp, bpp, photo, comp, planar_config;
    int check, StripsPerImage,LastStripSize;

    TIFF *input = (TIFF *) fhandler;

	check=TIFFGetField(input, TIFFTAG_ROWSPERSTRIP, &rps);
	if (!check)
	{
		return ((char *) "Image length of undefined.");
	}	
	//rps=600;
    
	check=TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp); 
	if (!check)
	{
		return ((char *) "Undefined bits per sample.");
	}

	check=TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp);
	if (!check)
	{
		return ((char *) "Undefined samples per pixel.");
	}

	check=TIFFGetField(input, TIFFTAG_PHOTOMETRIC, &photo);
	if (!check)
	{
		return ((char *) "Cannot determine photometric interpretation.");
	}

	check=TIFFGetField(input, TIFFTAG_COMPRESSION, &comp);
	if (!check)
	{
		return ((char *) "Cannot determine compression technique.");
	}

	check=TIFFGetField(input, TIFFTAG_PLANARCONFIG, &planar_config);
	if (!check)
	{
		return ((char *) "Cannot determine planar configuration.");
	}


	StripsPerImage =  (img_height + rps - 1) / rps;
	LastStripSize = img_height % rps;
	if (LastStripSize==0)
		LastStripSize=rps;

	check=TIFFSetDirectory(input, first);
	if (!check)
	{
		return ((char *) "Cannot open the requested first strip.");
	}

	unsigned char *buf = img;
	int page=0;
	do{

		for (int i=0; i < StripsPerImage-1; i++){
			if (comp==1) {
				TIFFReadRawStrip(input, i, buf, spp * rps * img_width * (bpp/8));
				buf = buf + spp * rps * img_width * (bpp/8);
			}
			else{
				TIFFReadEncodedStrip(input, i, buf, spp * rps * img_width * (bpp/8));
				buf = buf + spp * rps * img_width * (bpp/8);
			}
		}

		if (comp==1) {
			TIFFReadRawStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
		}
		else{
			TIFFReadEncodedStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
		}
		buf = buf + spp * LastStripSize * img_width * (bpp/8);

		page++;
	
	}while ( page < static_cast<int>(last-first+1) && TIFFReadDirectory(input));//while (TIFFReadDirectory(input));

	// input file is assumedo ti be already open and it is provided as an handler; the file should be closed by caller
	//TIFFClose(input);  

	if ( page < static_cast<int>(last-first+1) ){
		return ((char *) "Cannot read all the pages.");
	}

	// swap the data bytes if necessary 	
	if (b_swap)
	{
		int i;
		size_t total = img_width * img_height * spp * (last-first+1);
		if (bpp/8 == 2)
		{
			for (i=0;i<total; i++)
			{
				swap2bytes((void *)(img+2*i));
			}
		}
		else if (bpp/8 == 4)
		{
			for (i=0;i<total; i++)
			{
				swap4bytes((void *)(img+4*i));
			}
		}
	}

	return (char *) 0;
}

void 
	iomanager::tiff3D::readMetadata(
	std::string img_path,		// (INPUT)	image filepath
	int & img_width,			// (OUTPUT) image width  (in pixels)
	int & img_height,			// (OUTPUT) image height (in pixels)
	int & img_depth,			// (OUTPUT) image depth (in pixels)
	int & img_bytes_x_chan,	// (OUTPUT) number of bytes per channel
	int & img_chans,			// (OUTPUT) number of channels
	const std::string & params)	// (INPUT)	additional parameters <param1=val, param2=val, ...> 
throw (iom::exception)
{
	/**/iom::debug(iom::LEV3, iom::strprintf("img_path = %s, img_width = %d, img_height = %d, img_depth = %d, img_bytes_x_chan = %d, img_chans = %d, params = \"%s\"",
		img_path.c_str(), img_width, img_height, img_depth,	img_bytes_x_chan, img_chans, params.c_str()).c_str(), __iom__current__function__);

	// disable warning handler to avoid messages on unrecognized tags
	TIFFSetWarningHandler(0);

	TIFF* input=TIFFOpen(img_path.c_str(),"r");
	if (!input)
		throw iom::exception(iom::strprintf("unable to open image \"%s\". Possible unsupported format or it isn't an image.\nSupported format is 3DTIFF", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &img_width))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_IMAGEWIDTH' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_IMAGELENGTH, &img_height))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_IMAGELENGTH' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &img_bytes_x_chan))
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_BITSPERSAMPLE' from image \"%s\". ", img_path.c_str()), __iom__current__function__);
	img_bytes_x_chan /= 8;

	if (!TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &img_chans)) 
		throw iom::exception(iom::strprintf("unable to determine 'TIFFTAG_SAMPLESPERPIXEL' from image \"%s\". ", img_path.c_str()), __iom__current__function__);

	// Onofri
	uint16 cpage;  // Current page. We do not actually need it.
	uint16 npages; // Number of pages. 
	int PNcheck=TIFFGetField(input, TIFFTAG_PAGENUMBER, &cpage, &npages);
	if (!PNcheck || npages==0) { // the tag has not been read correctly
		iom::warning(iom::strprintf("unable to determine 'TIFFTAG_PAGENUMBER' from image file \"%s\". ", img_path.c_str()).c_str(),__iom__current__function__);
		img_depth = 0;
		do {
			img_depth++;
		} while (TIFFReadDirectory(input));
	}
	else
		img_depth = npages;

	TIFFClose(input);
}

