	int PNcheck=TIFFGetField(input, TIFFTAG_PAGENUMBER, &cpage, &npages);
	if (!PNcheck || npages==0) { // the tag has not been read correctly
		iom::warning(iom::strprintf("unable to determine 'TIFFTAG_PAGENUMBER' from image file \"%s\". ", img_path.c_str()).c_str(),__iom__current__function__);
		img_depth = 0;
		do {
			img_depth++;
		} while (TIFFReadDirectory(input));
	}
	else
		img_depth = npages;

	TIFFClose(input);
	
	// check the exception after closing the file
	if ( img_depth > 1 ) 
		throw iom::exception(iom::strprintf("image \"%s\" has more than one page.\nSupported format is 2DTIFF", img_path.c_str()), __iom__current__function__);
}

unsigned char *readtiff(char *myfile, int *width, int *height, int *depth, int *channels )
//================================================================================

{
    TIFF* tif = TIFFOpen(myfile, "r");
    if (tif)
    {
		*depth = 0;
		do
		{
			(*depth)++;
			uint16 compression, cc, bpp         ;

			//check if we are reading a compressed image. If so
			//return with an error
			TIFFGetField(tif, TIFFTAG_COMPRESSION, &compression);

			if ( compression != COMPRESSION_NONE )
			{
				cerr<<"File is not an Uncompressed TIFF image !\n";
			}

			//check if the image has 8 bits-per-pixel in each channel
			TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp);

			if ( bpp != 8 )
			{
				cerr<<"File does not have 8 bits per channel !\n";
			}


			//read the dimensions of the TIFF image
			TIFFGetField(tif, TIFFTAG_IMAGEWIDTH , width);//width
			TIFFGetField(tif, TIFFTAG_IMAGELENGTH, height);//height
			//determine what type of image is it (color or grayscale)
			TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &cc);

			//get the photometric if a grayscale image is
			//being read
			if (cc == 1)
			{
				*channels = 1;
			}
			else
			{
				*channels = 3;
			}
		}while(TIFFReadDirectory(tif));

		TIFFClose(tif);
	}
	else
    {
        cerr<<"Unable to open file for reading !\n";
    }


	tif = TIFFOpen(myfile,"r");
	if(tif)
	{
		unsigned char *img = new unsigned char[(*channels)*(*width)*(*height)*(*depth)];
		int layer = 0;
		do
		{			
			uint16 p, compression, cc, bpp;

			//check if we are reading a compressed image. If so
			//return with an error
			TIFFGetField(tif, TIFFTAG_COMPRESSION, &compression);

			if ( compression != COMPRESSION_NONE )
			{
				cerr<<"File is not an Uncompressed TIFF image !\n";
			}

			//check if the image has 8 bits-per-pixel in each channel
			TIFFGetField(tif, TIFFTAG_BITSPERSAMPLE, &bpp);

			if ( bpp != 8 )
			{
				cerr<<"File does not have 8 bits per channel !\n";
			}


			//read the dimensions of the TIFF image
			TIFFGetField(tif, TIFFTAG_IMAGEWIDTH , width);//width
			TIFFGetField(tif, TIFFTAG_IMAGELENGTH, height);//height
			//determine what type of image is it (color or grayscale)
			TIFFGetField(tif, TIFFTAG_SAMPLESPERPIXEL, &cc);

			//get the photometric if a grayscale image is
			//being read
			if (cc == 1)
			{
				*channels = 1;
				TIFFGetField(tif, TIFFTAG_PHOTOMETRIC, &p);
			}
			else
			{
				*channels = 3;
			}

			//create the matrix to hold the image data

			//create a dummy array
			int linebytes = (*channels)*(*width);
			unsigned char *buf = new unsigned char[linebytes];

			//start reading the data from the TIFF file in scanlines
			for ( int row = 0; row < *height; row++ )
			{
				if(TIFFReadScanline(tif, buf, row, 0) != 1)
				{
					cerr<<"Error reading TIFF scanline !\n";
				}
				memcpy(&img[layer*(*channels)*(*width)*(*height)+row*linebytes], buf, linebytes);

			}

			delete(buf);

			layer++;
		} while(TIFFReadDirectory(tif));

        TIFFClose(tif);

		return img;
    }

	return NULL;
}

bool QTiffHandlerPrivate::openForRead(QIODevice *device)
{
    if (tiff)
        return true;

    if (!canRead(device))
        return false;

    tiff = TIFFClientOpen("foo",
                          "r",
                          device,
                          qtiffReadProc,
                          qtiffWriteProc,
                          qtiffSeekProc,
                          qtiffCloseProc,
                          qtiffSizeProc,
                          qtiffMapProc,
                          qtiffUnmapProc);

    if (!tiff) {
        return false;
    }
    uint32 width;
    uint32 height;
    if (!TIFFGetField(tiff, TIFFTAG_IMAGEWIDTH, &width)
        || !TIFFGetField(tiff, TIFFTAG_IMAGELENGTH, &height)
        || !TIFFGetField(tiff, TIFFTAG_PHOTOMETRIC, &photometric)) {
        close();
        return false;
    }
    size = QSize(width, height);

    uint16 orientationTag;
    if (TIFFGetField(tiff, TIFFTAG_ORIENTATION, &orientationTag))
        transformation = exif2Qt(orientationTag);

    // BitsPerSample defaults to 1 according to the TIFF spec.
    uint16 bitPerSample;
    if (!TIFFGetField(tiff, TIFFTAG_BITSPERSAMPLE, &bitPerSample))
        bitPerSample = 1;
    uint16 samplesPerPixel; // they may be e.g. grayscale with 2 samples per pixel
    if (!TIFFGetField(tiff, TIFFTAG_SAMPLESPERPIXEL, &samplesPerPixel))
        samplesPerPixel = 1;

    grayscale = photometric == PHOTOMETRIC_MINISBLACK || photometric == PHOTOMETRIC_MINISWHITE;

    if (grayscale && bitPerSample == 1 && samplesPerPixel == 1)
        format = QImage::Format_Mono;
    else if (photometric == PHOTOMETRIC_MINISBLACK && bitPerSample == 8 && samplesPerPixel == 1)
        format = QImage::Format_Grayscale8;
    else if ((grayscale || photometric == PHOTOMETRIC_PALETTE) && bitPerSample == 8 && samplesPerPixel == 1)
        format = QImage::Format_Indexed8;
    else if (samplesPerPixel < 4)
        format = QImage::Format_RGB32;
    else
        format = QImage::Format_ARGB32_Premultiplied;

    headersRead = true;
    return true;
}

bool QTiffHandler::read(QImage *image)
{
    // Open file and read headers if it hasn't already been done.
    if (!d->openForRead(device()))
        return false;

    QImage::Format format = d->format;
    if (format == QImage::Format_RGB32 &&
            (image->format() == QImage::Format_ARGB32 ||
             image->format() == QImage::Format_ARGB32_Premultiplied))
        format = image->format();

    if (image->size() != d->size || image->format() != format)
        *image = QImage(d->size, format);

    TIFF *const tiff = d->tiff;
    const uint32 width = d->size.width();
    const uint32 height = d->size.height();

    if (format == QImage::Format_Mono) {
        QVector<QRgb> colortable(2);
        if (d->photometric == PHOTOMETRIC_MINISBLACK) {
            colortable[0] = 0xff000000;
            colortable[1] = 0xffffffff;
        } else {
            colortable[0] = 0xffffffff;
            colortable[1] = 0xff000000;
        }
        image->setColorTable(colortable);

        if (!image->isNull()) {
            for (uint32 y=0; y<height; ++y) {
                if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                    d->close();
                    return false;
                }
            }
        }
    } else {
        if (format == QImage::Format_Indexed8) {
            if (!image->isNull()) {
                const uint16 tableSize = 256;
                QVector<QRgb> qtColorTable(tableSize);
                if (d->grayscale) {
                    for (int i = 0; i<tableSize; ++i) {
                        const int c = (d->photometric == PHOTOMETRIC_MINISBLACK) ? i : (255 - i);
                        qtColorTable[i] = qRgb(c, c, c);
                    }
                } else {
                    // create the color table
                    uint16 *redTable = 0;
                    uint16 *greenTable = 0;
                    uint16 *blueTable = 0;
                    if (!TIFFGetField(tiff, TIFFTAG_COLORMAP, &redTable, &greenTable, &blueTable)) {
                        d->close();
                        return false;
                    }
                    if (!redTable || !greenTable || !blueTable) {
                        d->close();
                        return false;
                    }

                    for (int i = 0; i<tableSize ;++i) {
                        const int red = redTable[i] / 257;
                        const int green = greenTable[i] / 257;
                        const int blue = blueTable[i] / 257;
                        qtColorTable[i] = qRgb(red, green, blue);
                    }
                }

                image->setColorTable(qtColorTable);
                for (uint32 y=0; y<height; ++y) {
                    if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                        d->close();
                        return false;
                    }
                }

                // free redTable, greenTable and greenTable done by libtiff
            }
        } else if (format == QImage::Format_Grayscale8) {
            if (!image->isNull()) {
                for (uint32 y = 0; y < height; ++y) {
                    if (TIFFReadScanline(tiff, image->scanLine(y), y, 0) < 0) {
                        d->close();
                        return false;
                    }
                }
            }
        } else {
            if (!image->isNull()) {
                const int stopOnError = 1;
                if (TIFFReadRGBAImageOriented(tiff, width, height, reinterpret_cast<uint32 *>(image->bits()), qt2Exif(d->transformation), stopOnError)) {
                    for (uint32 y=0; y<height; ++y)
                        convert32BitOrder(image->scanLine(y), width);
                } else {
                    d->close();
                    return false;
                }
            }
        }
    }

    if (image->isNull()) {
        d->close();
        return false;
    }

    float resX = 0;
    float resY = 0;
    uint16 resUnit;
    if (!TIFFGetField(tiff, TIFFTAG_RESOLUTIONUNIT, &resUnit))
        resUnit = RESUNIT_INCH;

    if (TIFFGetField(tiff, TIFFTAG_XRESOLUTION, &resX)
        && TIFFGetField(tiff, TIFFTAG_YRESOLUTION, &resY)) {

        switch(resUnit) {
        case RESUNIT_CENTIMETER:
            image->setDotsPerMeterX(qRound(resX * 100));
            image->setDotsPerMeterY(qRound(resY * 100));
            break;
        case RESUNIT_INCH:
            image->setDotsPerMeterX(qRound(resX * (100 / 2.54)));
            image->setDotsPerMeterY(qRound(resY * (100 / 2.54)));
            break;
        default:
            // do nothing as defaults have already
            // been set within the QImage class
            break;
        }
    }

    d->close();
    return true;
}

TiffInfo GetTiffInfo(TIFF* tiffptr)
{
    TiffInfo info = {};

    LIBTIFF_CALL(TIFFGetField(tiffptr, TIFFTAG_IMAGEWIDTH, &info.image_width));
    LIBTIFF_CALL(TIFFGetField(tiffptr, TIFFTAG_IMAGELENGTH, &info.image_height));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_SAMPLESPERPIXEL, &info.channels));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_BITSPERSAMPLE, &info.bit_depth));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_ORIENTATION, &info.orientation));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_COMPRESSION, &info.compression));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_ROWSPERSTRIP, &info.rows_per_strip));
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_FILLORDER, &info.fill_order));

    info.is_tiled = TIFFIsTiled(tiffptr);
    if (info.is_tiled) {
        LIBTIFF_CALL(TIFFGetField(tiffptr, TIFFTAG_TILEWIDTH, &info.tile_width));
        LIBTIFF_CALL(TIFFGetField(tiffptr, TIFFTAG_TILELENGTH, &info.tile_height));
    } else {
        // We will be reading data line-by-line and pretend that lines are tiles
        info.tile_width = info.image_width;
        info.tile_height = 1;
    }

    if (TIFFGetField(tiffptr, TIFFTAG_PHOTOMETRIC, &info.photometric_interpretation)) {
        info.is_palette = (info.photometric_interpretation == PHOTOMETRIC_PALETTE);
    } else {
        info.photometric_interpretation = PHOTOMETRIC_MINISBLACK;
    }

    uint16_t planar_config;
    LIBTIFF_CALL(TIFFGetFieldDefaulted(tiffptr, TIFFTAG_PLANARCONFIG, &planar_config));
    info.is_planar = (planar_config == PLANARCONFIG_SEPARATE);

    if (info.is_palette) {
        LIBTIFF_CALL(TIFFGetField(tiffptr, TIFFTAG_COLORMAP, &info.palette.red, &info.palette.green, &info.palette.blue));
        info.channels = 3; // Palette is always RGB
        info.key_bit_depth = info.bit_depth;
        info.bit_depth = 16; // Palette colors are always 16 bits
    }

    return info;
}

char *loadTiffMetaInfo(char* filename, uint32 &sz0, uint32  &sz1, uint32  &sz2, uint32  &sz3, uint16 &datatype)
{
    //
    TIFFSetWarningHandler(0);
    TIFFSetErrorHandler(0);

    //
    TIFF *input = TIFFOpen(filename,"r");
    if (!input)
    {
        return ((char *) "Cannot open the file.");
    }

    if (!TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &sz0))
    {
        TIFFClose(input);
        return ((char *) "Image width of undefined.");
    }

    if (!TIFFGetField(input, TIFFTAG_IMAGELENGTH, &sz1))
    {
        TIFFClose(input);
        return ((char *) "Image length of undefined.");
    }

    if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &datatype))
    {
        TIFFClose(input);
        return ((char *) "Undefined bits per sample.");
    }

//    if (!TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &sz3))
//    {
//        sz3 = 1;
//    }

    sz3 = 1; // hard coded here 6/8/2018

    uint16 Cpage;
    if (!TIFFGetField(input, TIFFTAG_PAGENUMBER, &Cpage, &sz2) || sz2==0)
    {
        sz2 = 1;
        while ( TIFFReadDirectory(input) )
        {
            sz2++;
        }
    }
    datatype /= 8;

    //
    TIFFClose(input);

    //
    return ((char *) 0);
}

void readTiff( stringstream *dataStreamInMemory, unsigned char *&img, unsigned int first, unsigned int last, int starti, int endi, int startj, int endj )
{
    //
    TIFF* input = TIFFStreamOpen("MemTIFF", (istream *)dataStreamInMemory);

    //
    uint32 rps;
    uint16 spp, bpp, photo, comp, planar_config;
    int StripsPerImage,LastStripSize;
    unsigned int img_width, img_height;

    //
    if (!TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &img_width))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    if (!TIFFGetField(input, TIFFTAG_IMAGELENGTH, &img_height))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &bpp))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    if (!TIFFGetField(input, TIFFTAG_SAMPLESPERPIXEL, &spp))
    {
        spp = 1;
    }

    int b_swap=TIFFIsByteSwapped(input);

    //
    if (!TIFFGetField(input, TIFFTAG_PHOTOMETRIC, &photo))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    if (!TIFFGetField(input, TIFFTAG_COMPRESSION, &comp))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    if (!TIFFGetField(input, TIFFTAG_PLANARCONFIG, &planar_config))
    {
        TIFFClose(input);
        ssclear(dataStreamInMemory);
        return;
    }

    //
    starti = (starti == -1) ? 0 : starti;
    endi   = (endi == -1) ? img_height-1 : endi;
    startj = (startj == -1) ? 0 : startj;
    endj   = (endj == -1) ? img_width-1 : endj;

    // file is internally tiled
    if (TIFFIsTiled(input))
    {
        uint32 tilewidth;
        uint32 tilelength;
        uint32 tiledepth;
        tsize_t tilenum;
        tsize_t tilesize;
        tsize_t tilenum_width;
        tsize_t tilenum_length;
        ttile_t tile;
        tdata_t data;
        unsigned char *psrc; // pointer in the tile buffer to the top left pixel of the current block to be copied
        unsigned char *pdst; // pointer in the image buffer to the top left pixel of the current block to be filled
        uint32 stride_src;
        uint32 stride_dst;
        int i; // row index in the slice of the top left pixel of the current block to be filled
        int j; // column index in the slice of the top left pixel of the current block to be filled
        uint32 width; // width of the current block to be filled (in pixels)
        uint32 len; // length of the current block to be filled (in pixels)
        int page;

        // checks
        if ( TIFFGetField(input, TIFFTAG_TILEDEPTH, &tiledepth) )
        {
            cout<<"Tiling among slices (z direction) not supported."<<endl;
            return;
        }
        if ( spp > 1 )
        {
            if ( TIFFGetField(input, TIFFTAG_PLANARCONFIG, &planar_config) )
            {
                if ( planar_config > 1 )
                {
                    cout<<"Non-interleaved multiple channels not supported with tiling."<<endl;
                    return;
                }
            }
        }

        // tiling is in x,y only
        TIFFGetField(input, TIFFTAG_TILEWIDTH, &tilewidth);
        TIFFGetField(input, TIFFTAG_TILELENGTH, &tilelength);
        tilenum = TIFFNumberOfTiles(input);
        tilesize = TIFFTileSize(input);
        tilenum_width  = (img_width % tilewidth) ? (img_width / tilewidth) + 1 : img_width / tilewidth;
        tilenum_length = (img_height % tilelength) ? (img_height / tilelength) + 1 : img_height / tilelength;

        data = new unsigned char[tilesize];
        stride_src = tilewidth * spp; // width of tile (in bytes)
        stride_dst = (endj - startj + 1) * spp; // width of subregion (in bytes)

        page = 0;
        do {

            psrc = ((unsigned char *)data) + ((starti % tilelength)*tilewidth + (startj % tilewidth)) * spp; // in the first tile skip (starti % tilelength) rows plus (startj % tilewidth) pixels
            pdst = img; // the buffer has the size of the subregion
            len = tilelength - (starti % tilelength); // rows to be copied for the first row of tiles
            tile = TIFFComputeTile(input,startj,starti,0,0); // index of the first tile to be copied in the current row of tiles
            for ( i=starti; i<=endi; ) {
                width = tilewidth - (startj%tilewidth); // width of the first block to be copied/filled
                for ( j=startj; j<=endj; ) {
                    TIFFReadEncodedTile(input,tile,data,(tsize_t) -1); // read tile into tile buffer
                    copydata (psrc,stride_src,pdst,stride_dst,(width * spp),len); // copy the block
                    j += width;
                    tile++; // index of the next tile in the same row of tiles
                    psrc = ((unsigned char *)data) + ((i % tilelength)*tilewidth) * spp; // the block in the next tile begins just after (i % tilelength) rows
                    pdst += width * spp; // the block in the image buffer move forward of width pixels
                    width = (((tile%tilenum_width) + 1) * tilewidth <= (endj+1)) ? tilewidth : ((endj+1)%tilewidth); // if the next tile in the row is all within the subregion, width is tilewidth otherwise it is shorter
                }
                i += len;
                tile = TIFFComputeTile(input,startj,i,0,0); // index of the first tile to be copied in the current row of tiles
                psrc = ((unsigned char *)data) + ((i % tilelength)*tilewidth + (startj % tilewidth)) * spp; // in the first tile of the next row of tiles skip (i % tilelength) rows plus (startj % tilewidth) pixels
                pdst = img + ((i-starti) * stride_dst); // the block in the image buffer begin after (i-starti) rows
                len = (((tile/tilenum_width) + 1) * tilelength <= (endi+1)) ? tilelength : ((endi+1)%tilelength); // if the next row of tiles is all within the subregion, len is tilelength otherwise it is shorter
            }

            page++;

        }while ( page < static_cast<int>(last-first+1) && TIFFReadDirectory(input));

        return;
    }

    //
    if (!TIFFGetField(input, TIFFTAG_ROWSPERSTRIP, &rps))
    {
        TIFFClose(input);
        dataStreamInMemory->clear();
        dataStreamInMemory->str(string());
        return;
    }

    StripsPerImage =  (img_height + rps - 1) / rps;
    LastStripSize = img_height % rps;
    if (LastStripSize==0)
        LastStripSize=rps;

    unsigned char *buf = img;
    int page=0;

    if ( starti < 0 || endi >= img_height || startj < 0 || endj >= img_width || starti >= endi || startj >= endj )
    {
        cout<<"Wrong substack indices."<<endl;
        return;
    }

    if ( starti == 0 && endi == (img_height-1) && startj == 0 && endj == (img_width-1) ) { // read whole images from files

        if (!TIFFSetDirectory(input, first))
        {
            TIFFClose(input);
            dataStreamInMemory->clear();
            dataStreamInMemory->str(string());
            return;
        }

        do{

            for (int i=0; i < StripsPerImage-1; i++){
                if (comp==1) {
                    TIFFReadRawStrip(input, i, buf, spp * rps * img_width * (bpp/8));
                    buf = buf + spp * rps * img_width * (bpp/8);
                }
                else{
                    TIFFReadEncodedStrip(input, i, buf, spp * rps * img_width * (bpp/8));
                    buf = buf + spp * rps * img_width * (bpp/8);
                }
            }

            if (comp==1) {
                TIFFReadRawStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
            }
            else{
                TIFFReadEncodedStrip(input, StripsPerImage-1, buf, spp * LastStripSize * img_width * (bpp/8));
            }
            buf = buf + spp * LastStripSize * img_width * (bpp/8);

            page++;

        }while ( page < static_cast<int>(last-first+1) && TIFFReadDirectory(input));// while (TIFFReadDirectory(input));

    }
    else { // read only a subregion of images from files

        unsigned int XSIZE = img_width;

        unsigned char *rowbuf = new unsigned char[spp * rps * XSIZE * (bpp/8)];
        unsigned char *bufptr;

        do{
            if (!TIFFSetDirectory(input, first + page))
            {
                TIFFClose(input);
                ssclear(dataStreamInMemory);
                return;
            }

            int stripIndex = (starti / rps) - 1; // the strip preceeding the first one
            for (int i=starti; i <= endi; i++) {
                if ( floor((double)i / rps) > stripIndex ) { // read a new strip
                    stripIndex = (int)floor((double)i / rps);
                    if (comp==1) {
                        TIFFReadRawStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
                    }
                    else{
                        TIFFReadEncodedStrip(input, stripIndex, rowbuf, spp * ((stripIndex < StripsPerImage) ? rps :LastStripSize) * XSIZE * (bpp/8));
                    }
                }
                bufptr = rowbuf + (i % rps) * (spp * XSIZE * (bpp/8));
                if ( bpp == 8 )
                    for (int j=0, j1=startj; j<=(endj-startj); j++, j1++) {
                        for (int c=0; c<spp; c++) {
                            buf[j * spp + c] = bufptr[j1 * spp + c];
                        }
                    }
                else
                    for (int j=0 , j1=startj; j<=(endj-startj); j++, j1++) {
                        for (int c=0; c<spp; c++) {
                            ((uint16 *)buf)[j * spp + c] = ((uint16 *)bufptr)[j1 * spp + c];
                        }
                    }
                buf = buf + spp * (endj-startj+1) * (bpp/8);
            }

            page++;

        }while ( page < static_cast<int>(last-first+1) );

        delete []rowbuf;

    }

    if ( page < static_cast<int>(last-first+1) ){
        return;
    }

    //
    TIFFClose(input);
    ssclear(dataStreamInMemory);

    // swap the data bytes if necessary
    if (b_swap)
    {
        int i;
        size_t total = img_width * img_height * spp * (last-first+1);
        if (bpp/8 == 2)
        {
            for (i=0;i<total; i++)
            {
                swap2bytes((void *)(img+2*i));
            }
        }
        else if (bpp/8 == 4)
        {
            for (i=0;i<total; i++)
            {
                swap4bytes((void *)(img+4*i));
            }
        }
    }

    //
    return;
}

char *tiffread(const char* filename, unsigned char *&p, uint32 &sz0, uint32  &sz1, uint32  &sz2, uint16 &datatype)
{
    //
    TIFF *input = TIFFOpen(filename,"r");
    if (!input)
    {
        return ((char *) "Cannot open the file.");
    }

    if (!TIFFGetField(input, TIFFTAG_IMAGEWIDTH, &sz0))
    {
        TIFFClose(input);
        return ((char *) "Image width of undefined.");
    }

    if (!TIFFGetField(input, TIFFTAG_IMAGELENGTH, &sz1))
    {
        TIFFClose(input);
        return ((char *) "Image length of undefined.");
    }

    if (!TIFFGetField(input, TIFFTAG_BITSPERSAMPLE, &datatype))
    {
        TIFFClose(input);
        return ((char *) "Undefined bits per sample.");
    }

    uint16 Cpage;
    if (!TIFFGetField(input, TIFFTAG_PAGENUMBER, &Cpage, &sz2) || sz2==0)
    {
        sz2 = 1;
        while ( TIFFReadDirectory(input) )
        {
            sz2++;
        }
    }
    datatype /= 8;

    //cout<<"test "<<sz0<<" "<<sz1<<" "<<sz2<<" "<<datatype<<endl;

    V3DLONG imgsz = (V3DLONG)sz0*(V3DLONG)sz1*(V3DLONG)sz2*(V3DLONG)datatype;

    //
    try
    {
        p = new unsigned char [imgsz];
    }
    catch(...)
    {
        return ((char*) "fail to alloc memory for loading a tiff image.");
    }

    //
    uint32 rps;
    int StripsPerImage,LastStripSize;

    //
    if (!TIFFGetField(input, TIFFTAG_ROWSPERSTRIP, &rps))
    {
        TIFFClose(input);
        return ((char *) "Undefined rowsperstrip.");
    }

    uint16 comp;
    if (!TIFFGetField(input, TIFFTAG_COMPRESSION, &comp))
    {
        TIFFClose(input);
        return ((char *) "Undefined compression.");
    }

    StripsPerImage =  (sz1 + rps - 1) / rps;
    LastStripSize = sz1 % rps;
    if (LastStripSize==0)
        LastStripSize=rps;

    if (!TIFFSetDirectory(input, 0)) // init
    {
        TIFFClose(input);
        return ((char *) "fail to setdir.");
    }

    unsigned char *buf = p;
    do{
        for (int i=0; i < StripsPerImage-1; i++)
        {
            cout<<"i "<<i<<endl;

            if (comp==1)
            {
                TIFFReadRawStrip(input, i, buf,  rps * sz0 * datatype);
                buf = buf + rps * sz0 * datatype;
            }
            else
            {
                TIFFReadEncodedStrip(input, i, buf, rps * sz0 * datatype);
                buf = buf + rps * sz0 * datatype;
            }
        }

        if (comp==1)
        {
            TIFFReadRawStrip(input, StripsPerImage-1, buf, LastStripSize * sz0 * datatype);
        }
        else
        {
            TIFFReadEncodedStrip(input, StripsPerImage-1, buf, LastStripSize * sz0 * datatype);
        }
        buf = buf + LastStripSize * sz0 * datatype;

    } while(TIFFReadDirectory(input));

    //
    TIFFClose(input);

    //
    return ((char *) 0);
}

