    vector<double> imagedi(WIDTH*HEIGHT, 0);
    vector<uint8_t> imagergb(WIDTH*HEIGHT*3, 0);
    vector<uint8_t> imagergbi(WIDTH*HEIGHT*3, 0);

    write1ChannelTestData(image8.data(), WIDTH, HEIGHT, PATTERNSIZE);
